INSERT INTO `creature_text` VALUES (108875, 3, 0, 'Тебе запрещено входить в виноградники.', 12, 0, 100, 0, 0, 0, 114598, 13290, 13290, 0, 'Надсмотрщик виноградников');
INSERT INTO `creature_text` VALUES (108875, 4, 0, 'Знай свое место, собака!', 12, 0, 100, 0, 0, 0, 114762, 10420, 31852, 0, 'Надсмотрщица виноградников to Горожанин-шал\'дорай');
INSERT INTO `creature_text` VALUES (108876, 0, 0, 'I destroyed hundreds of your kind. You will not be an exception.', 12, 0, 100, 0, 0, 60983, 96920, 0, 0, 0, 'Восставший боец авангарда to Player');
INSERT INTO `creature_text` VALUES (108876, 1, 0, 'Враг в крепости! Поднять тревогу!', 12, 0, 100, 0, 0, 60989, 123071, 0, 0, 0, 'Восставший боец авангарда to Player');
INSERT INTO `creature_text` VALUES (108876, 1, 0, 'Я долго к этому готовился!', 12, 0, 100, 0, 0, 60988, 96915, 0, 0, 0, 'Восставший боец авангарда to Player');
INSERT INTO `creature_text` VALUES (108877, 0, 0, 'У этого вина прекрасный букет. Манафрукт седьмого поколения, оттененный приятным ореховым послевкусием.', 12, 0, 100, 0, 0, 66177, 114933, 0, 0, 0, 'Сашеверелль Лебурк to Player');
INSERT INTO `creature_text` VALUES (108877, 1, 0, 'Крепкая смесь едва созревших тенеягод и манафруктов, трижды крепленая сущностью Ночного Колодца. Пей мелкими глотками.', 12, 0, 100, 0, 0, 66178, 114934, 0, 0, 0, 'Сашеверелль Лебурк to Player');
INSERT INTO `creature_text` VALUES (108877, 2, 0, 'Настойка трехлетней выдержки, в букете ягодные тона с нотами спелой дыни. Освежает, не правда ли?', 12, 0, 100, 0, 0, 66176, 114932, 0, 0, 0, 'Сашеверелль Лебурк to Player');
INSERT INTO `creature_text` VALUES (108879, 0, 0, 'Сделать взрывной огонь! Сделать бум!', 14, 0, 100, 0, 0, 0, 114626, 0, 0, 0, 'Хумонгрис to Player');
INSERT INTO `creature_text` VALUES (108879, 1, 0, 'Хочу снега! Пусть будет снег! Пусть будет снег! Пусть будет снег!', 14, 0, 100, 0, 0, 0, 114627, 0, 0, 0, 'Хумонгрис to 0');
INSERT INTO `creature_text` VALUES (108879, 1, 1, 'Хочу снега! Сделать снег!', 14, 0, 100, 0, 0, 0, 114799, 0, 0, 0, 'Хумонгрис to 0');
INSERT INTO `creature_text` VALUES (108879, 2, 0, 'Получи ледяным кулаком!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Хумонгрис to Player');
INSERT INTO `creature_text` VALUES (108880, 0, 0, 'Без паники, мы справимся... А-а-а!', 14, 0, 100, 0, 0, 0, 114812, 0, 0, 0, 'Падосен to Хумонгрис');
INSERT INTO `creature_text` VALUES (108880, 0, 0, 'Гррраргг... Нет! Нет! Только не глаза...', 14, 0, 100, 0, 0, 0, 114813, 0, 0, 0, 'Падосен to Хумонгрис');
INSERT INTO `creature_text` VALUES (108880, 0, 0, 'Только не это!', 14, 0, 100, 0, 0, 67149, 114807, 0, 0, 0, 'Падосен to Хумонгрис');
INSERT INTO `creature_text` VALUES (108880, 3, 0, 'Это называется \"Ледяная глыба\", ты, большой тупой... Проклятье!', 14, 0, 100, 0, 0, 0, 114806, 46197, 530935, 0, 'Падосен to Хумонгрис');
INSERT INTO `creature_text` VALUES (108894, 3, 0, 'Whenever you\'re ready.', 12, 0, 100, 0, 0, 0, 106227, 25558, 25558, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (108896, 0, 0, 'Give them hell, $n.', 12, 0, 0, 0, 0, 0, 114471, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108912, 3, 0, 'Рыбалка? ...Кроме шуток?!', 15, 0, 100, 0, 0, 0, 116530, 6905, 6905, 0, 'Блит to Player');
INSERT INTO `creature_text` VALUES (108912, 3, 0, 'Я на это не подписывался.', 15, 0, 100, 0, 0, 0, 116528, 7973, 7973, 0, 'Блит to Player');
INSERT INTO `creature_text` VALUES (108916, 0, 0, 'Вот твой корабль, $n. Да пребудет с тобой Свет.', 12, 0, 0, 0, 0, 0, 114472, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (108925, 3, 0, '$c... ну что за назойливое создание... Ты заплатишь за свою настырность. Весь этот город сгорит!', 12, 0, 100, 5, 0, 0, 114641, 128215, 128215, 0, 'Лорд Аддер to Player');
INSERT INTO `creature_text` VALUES (108930, 1, 0, 'Никто не смеет бросать нам вызов!', 12, 0, 100, 0, 0, 66026, 116708, 0, 0, 0, 'Ночнорожденная-посланница to Player');
INSERT INTO `creature_text` VALUES (108930, 2, 0, 'Шал\'дорай будут править миром!', 12, 0, 100, 0, 0, 66019, 116792, 0, 0, 0, 'Ночнорожденная-посланница to Player');
INSERT INTO `creature_text` VALUES (108930, 4, 0, 'Легион, на помощь!', 12, 0, 100, 0, 0, 0, 114783, 33052, 33052, 0, 'Ночнорожденная-посланница');
INSERT INTO `creature_text` VALUES (108931, 3, 0, 'Я на одну минуту. Я...', 12, 0, 100, 0, 0, 0, 115112, 14394, 14394, 0, 'Работник виноградников to Player');
INSERT INTO `creature_text` VALUES (108932, 0, 0, 'You shall be my new subject\'s first test!', 12, 0, 100, 0, 0, 0, 114618, 0, 0, 0, 'Осквернительница Ма\'вет to 0');
INSERT INTO `creature_text` VALUES (108939, 1, 0, 'I will crush your bones!', 12, 0, 100, 0, 0, 52447, 98072, 0, 0, 0, 'Рубака из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (108939, 1, 0, 'Будет сегодня пир воронью!', 12, 0, 100, 0, 0, 52443, 98074, 0, 0, 0, 'Рубака из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (108939, 1, 0, 'Твоя кровь украсит меня!', 12, 0, 100, 0, 0, 52445, 98073, 0, 0, 0, 'Рубака из клана Говорящих с костями to Player');
INSERT INTO `creature_text` VALUES (108946, 3, 0, 'Надеюсь, этот демон получил промеж глаз!', 12, 0, 100, 603, 0, 66629, 114685, 22349, 22349, 0, 'Хеминг Эрнестуэй to Player');
INSERT INTO `creature_text` VALUES (108955, 3, 0, '%s хмыкает и в смущении отпихивает козла.', 16, 0, 100, 440, 0, 0, 114673, 28940, 28940, 0, 'Максвелл Каприна to Максвелл Каприна');
INSERT INTO `creature_text` VALUES (108957, 0, 0, 'Что ж, это твой смертный приговор. Кария и Варедис разберутся с тобой.', 14, 0, 100, 0, 0, 66918, 114712, 0, 0, 0, 'Кил\'джеден to Player');
INSERT INTO `creature_text` VALUES (108960, 3, 0, '%s дергает Максвелла за лодыжку.', 16, 0, 100, 35, 0, 0, 114671, 26586, 26586, 0, 'Низинный козел to Максвелл Каприна');
INSERT INTO `creature_text` VALUES (108961, 3, 0, 'Excellent, commander. Danath awaits you on Krasus\' Landing.', 12, 0, 100, 1, 0, 63537, 114680, 90389, 90389, 0, 'Sergeant Dalton to Player');
INSERT INTO `creature_text` VALUES (108962, 0, 0, 'Расскажи мне кто об этом, я бы ни за что не поверила. Браво!', 12, 0, 100, 0, 0, 66491, 114758, 0, 0, 0, 'Верховный маг Модера to 108897');
INSERT INTO `creature_text` VALUES (108985, 1, 0, '$n! Кил\'джеден приказал схватить тебя. Ты примкнешь к Пылающему Легиону.', 14, 0, 100, 0, 0, 0, 114714, 0, 0, 216540, 'Тюремщик из армии Оскверненной Души to Player');
INSERT INTO `creature_text` VALUES (109004, 3, 0, 'Give me the strength to shatter these bonds.', 15, 0, 100, 0, 0, 0, 0, 142445, 142445, 0, 'Highlord Tirion Fordring to Player');
INSERT INTO `creature_text` VALUES (109015, 0, 0, 'Все когда-нибудь умрут.', 12, 0, 100, 0, 0, 0, 114749, 0, 0, 0, 'Лагерта to Player');
INSERT INTO `creature_text` VALUES (109021, 1, 0, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 35, 0, 55209, 96660, 0, 0, 0, 'Пылающий демон to Player');
INSERT INTO `creature_text` VALUES (109021, 3, 0, 'Мы пожрем твою душу! И тело тоже.', 12, 0, 100, 35, 0, 55210, 96656, 44759, 44759, 0, 'Пылающий демон to Player');
INSERT INTO `creature_text` VALUES (109027, 0, 0, 'Wooo-hoo! This here wikket is makin\' good speed! But with my other riddles can this one succeed?', 12, 0, 100, 0, 0, 64439, 114889, 0, 0, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109038, 0, 0, 'Опустится тьма... умрете и вы!', 14, 0, 100, 0, 0, 68638, 115758, 0, 0, 0, 'PLASMA_SPHERES');
INSERT INTO `creature_text` VALUES (109038, 1, 0, 'Эй, не опалите бутоны, пока горите сами!', 14, 0, 100, 0, 0, 68642, 115754, 0, 0, 0, 'SOLAR_COLLAPSE');
INSERT INTO `creature_text` VALUES (109040, 0, 0, 'Красота Сурамара — моя заслуга! Я овладел искусством вплетения магии в саму природу и все ее творения!', 14, 0, 100, 0, 0, 68626, 115766, 0, 0, 0, 'UNK');
INSERT INTO `creature_text` VALUES (109040, 1, 0, 'Похоже, пора заняться прополкой!', 14, 0, 100, 0, 0, 68633, 115770, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (109040, 1, 1, 'Как вы посмели вторгнуться в мои сады?', 14, 0, 100, 0, 0, 68634, 115769, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (109040, 2, 0, '|TINTERFACE\\ICONS\\Spell_Arcane_PrismaticCloak:20|t Чародей Тел\'арн begins to cast a spell |cFFF00000|Hspell:218807|h[\"Зов ночи\"]|h|r!', 41, 0, 100, 0, 0, 0, 121596, 0, 0, 0, 'CALL_OF_NIGHT');
INSERT INTO `creature_text` VALUES (109040, 3, 0, 'Ночь наступает!', 14, 0, 100, 0, 0, 68635, 115761, 0, 0, 0, 'CALL_OF_NIGHT');
INSERT INTO `creature_text` VALUES (109040, 4, 0, 'Может, до вас, тугодумов, хоть так дойдет!', 14, 0, 100, 0, 0, 68640, 115756, 0, 0, 0, 'CONTROLLED_CHAOS');
INSERT INTO `creature_text` VALUES (109041, 0, 0, 'Один вредитель уничтожит другого!', 14, 0, 100, 0, 0, 68641, 115755, 0, 0, 0, 'PARASITIC_FETTER');
INSERT INTO `creature_text` VALUES (109041, 1, 0, 'Природа кормит тех, кто ее ценит.', 14, 0, 100, 0, 0, 68637, 115759, 0, 0, 0, 'GRACE_OF_NATURE');
INSERT INTO `creature_text` VALUES (109047, 3, 0, 'Благодарю тебя.', 12, 0, 100, 1, 0, 0, 116000, 335555, 335555, 0, 'Певец из Седой чащи to Player');
INSERT INTO `creature_text` VALUES (109048, 1, 0, 'Ко мне, слуга!', 12, 0, 100, 0, 0, 0, 114785, 0, 0, 0, 'Чародей-инженер to Player');
INSERT INTO `creature_text` VALUES (109048, 4, 0, 'Нет! Еще не время…', 12, 0, 100, 0, 0, 65654, 116788, 45787, 45787, 0, 'Чародей-инженер to Player');
INSERT INTO `creature_text` VALUES (109049, 3, 0, 'There be one more test \'fore I tell you my tale. Meet at Jade Serpent\'s temple \'fore the mixin\'s go stale!', 12, 0, 100, 0, 0, 64416, 115270, 16742, 16742, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109051, 0, 0, 'Wikket got water, clear and pure, \'fore greenies from sky could dook it for sure.', 12, 0, 100, 0, 0, 64436, 114788, 0, 0, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109051, 1, 0, 'Hurry up, wikket, make your feet into dancers! For my riddles you\'ll go off and find all the answers.', 12, 0, 100, 0, 0, 64437, 114789, 0, 0, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109060, 3, 0, 'Тупой дракон! Ты за это заплатишь!', 14, 0, 100, 0, 0, 61484, 95952, 46015, 46015, 0, 'Вредитель из племени Листа Чертополоха to Player');
INSERT INTO `creature_text` VALUES (109073, 3, 0, 'Грабьте их добро!', 14, 0, 100, 0, 0, 0, 115712, 27977, 27977, 0, 'Искоренитель');
INSERT INTO `creature_text` VALUES (109073, 3, 0, 'Царапайте им пятки!', 14, 0, 100, 0, 0, 0, 115715, 2979, 2979, 0, 'Искоренитель');
INSERT INTO `creature_text` VALUES (109099, 0, 0, 'А вот и герои явились. Ты думаешь, что одолел меня, Велен?', 14, 0, 100, 1, 0, 70836, 120208, 0, 0, 0, 'Верховный генерал Ракиш to Player');
INSERT INTO `creature_text` VALUES (109099, 1, 0, 'Прежде чем я сдамся и приму правосудие Света, мне нужно завершить одно дело.', 14, 0, 100, 0, 0, 70835, 120211, 0, 0, 0, 'Верховный генерал Ракиш to Player');
INSERT INTO `creature_text` VALUES (109106, 0, 0, 'No! You no take! I kills the thief!', 14, 0, 100, 0, 0, 64388, 114874, 0, 0, 0, 'Вадис to 0');
INSERT INTO `creature_text` VALUES (109106, 1, 0, 'Noooooo! Mine...', 12, 0, 100, 0, 0, 64389, 114875, 0, 0, 0, 'Вадис to Player');
INSERT INTO `creature_text` VALUES (109108, 0, 0, 'Сдохните, черви!', 14, 0, 100, 0, 0, 68989, 119251, 0, 0, 0, 'Кария Оскверненная Душа to 0');
INSERT INTO `creature_text` VALUES (109108, 1, 0, 'Смотрите под ноги...', 14, 0, 100, 0, 0, 68988, 119252, 0, 0, 0, 'Кария Оскверненная Душа to 0');
INSERT INTO `creature_text` VALUES (109108, 14, 0, 'Владыка Кил\'джеден, спаси меня…', 14, 0, 100, 0, 0, 66961, 115462, 0, 0, 0, 'Кария Оскверненная Душа to Player');
INSERT INTO `creature_text` VALUES (109109, 0, 0, 'Очиститесь в огне!', 14, 0, 100, 0, 0, 68990, 119253, 0, 0, 0, 'Варедис Оскверненная Душа to 0');
INSERT INTO `creature_text` VALUES (109109, 7, 0, 'Кария… Нет!..', 12, 0, 100, 0, 0, 66961, 115823, 0, 0, 0, 'Варедис Оскверненная Душа to Кария Оскверненная Душа');
INSERT INTO `creature_text` VALUES (109109, 13, 0, 'Кил\'джеден, прошу, верни меня к жизни…', 14, 0, 100, 0, 0, 66962, 115463, 0, 0, 0, 'Варедис Оскверненная Душа to Player');
INSERT INTO `creature_text` VALUES (109113, 0, 0, '|TInterface\\Icons\\inv_jewelcrafting_immactaladite_purple:20|t Камнепад begins to cast a spell |cFFFF0404|Hspell:216970|h[\"Стихийный резонанс\"]|h|r. Найдите укрытие!', 42, 0, 100, 0, 0, 0, 114847, 0, 0, 0, 'Камнепад Оползающий to Player');
INSERT INTO `creature_text` VALUES (109143, 0, 0, 'Сама судьба привела тебя к нам в этот жуткий час! Я знаю, у тебя срочное дело. Но если мы не остановим вторжение демонов, мы все обречены.', 12, 0, 100, 0, 0, 70896, 115466, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 1, 0, 'Этот барьер защищает мой народ, но поглощает все мои силы и внимание. Боюсь, на захваченную демонами территорию придется отправиться тебе. Спаси выживших и разрушь все порталы, что попадутся тебе в Экзодаре.', 12, 0, 100, 0, 0, 70895, 115485, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 2, 0, 'Поторопись. Я не знаю, сколько еще смогу удерживать барьер!', 12, 0, 100, 0, 0, 70894, 115486, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 3, 0, 'Боюсь, у нас мало времени. Ракиш еще жив, и он где-то рядом. Его нужно остановить, иначе мой народ не обретет покой.', 12, 0, 100, 1, 0, 70893, 119516, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 4, 0, 'Я знаю, через что тебе пришлось пройти, чтобы мы встретились. И что ты несешь страшную весть.', 12, 0, 100, 273, 0, 70892, 119517, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 5, 0, 'В твоих руках нечто очень важное. Покажи мне.', 12, 0, 100, 25, 0, 70891, 119518, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 6, 0, 'Свет всемогущий! Прости, я… не знал.', 12, 0, 100, 0, 0, 70889, 119596, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 7, 0, '$ct $n, нужно идти. Сейчас же!', 12, 0, 100, 0, 0, 70888, 119597, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 8, 0, 'Я все объясню по пути. Дай знать, когда соберешься выдвигаться. Только поторопись!', 12, 0, 100, 0, 0, 70887, 119598, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 9, 0, 'Проклятье!', 12, 0, 100, 603, 0, 70886, 119863, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 10, 0, 'I will lead you to the heart of the ship. Stay inside my barrier while I clear our path.', 12, 0, 100, 0, 0, 70885, 120052, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 11, 0, 'Мы успели! Милостью Света и во славу Его, я исцелю О\'роса. А затем мы покончим с Ракишем.', 12, 0, 100, 0, 0, 70883, 120201, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 12, 0, 'Ты проиграл, Ракиш! О\'рос исцелен, а тебе предстоит ответить за все зло, что ты сотворил!', 14, 0, 100, 25, 0, 70884, 120202, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (109143, 13, 0, 'Мы пойдем в атаку по твоей команде. Веди нас!', 12, 0, 100, 25, 0, 0, 120285, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (109144, 0, 0, 'I made a palanquin. Frown, so the illusion will be more convincing.', 12, 0, 100, 0, 0, 67565, 114863, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (109144, 1, 0, 'Женщину, которую ты ищешь, зовут Марго. Она проявляет к нам благосклонность.', 12, 0, 100, 0, 0, 67566, 114864, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (109185, 3, 0, 'Скорее, поспеши в Чертог Света!', 12, 0, 100, 25, 0, 0, 116235, 46916, 46916, 0, 'Верховная воздаятельница to Почетный страж Ракиша');
INSERT INTO `creature_text` VALUES (109187, 3, 0, 'Put the pot on the fire\'s heart, then the wikket\'s trial can start!', 12, 0, 100, 0, 0, 64435, 115360, 11525, 11525, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109189, 0, 0, 'Капитан Теварис появляется из теней.', 16, 0, 100, 0, 0, 0, 115152, 0, 0, 0, 'Капитан Теварис to Player');
INSERT INTO `creature_text` VALUES (109189, 1, 0, 'Во имя господина!', 12, 0, 100, 0, 0, 0, 95648, 0, 0, 0, 'Капитан Теварис to Player');
INSERT INTO `creature_text` VALUES (109196, 0, 0, 'Вижу, охота прошла успешно! Да я и не сомневался.', 12, 0, 100, 1, 0, 64690, 114974, 0, 0, 0, 'Джейс Темный Ткач to Player');
INSERT INTO `creature_text` VALUES (109199, 1, 0, 'Анат\'ашар!', 12, 0, 100, 0, 0, 66155, 116709, 0, 0, 0, 'Ночнорожденный-темпомаг to Player');
INSERT INTO `creature_text` VALUES (109212, 0, 0, 'Тогда начнем. Просто следуй моим инструкциям, я все подскажу.', 12, 0, 100, 1, 0, 0, 114923, 0, 0, 0, 'Винодел Ильте to Player');
INSERT INTO `creature_text` VALUES (109212, 3, 0, 'Да, вот так! Хм, чего же еще не хватает…', 12, 0, 100, 1, 0, 0, 115075, 44209, 68534, 0, 'Винодел Ильте to Player');
INSERT INTO `creature_text` VALUES (109212, 3, 0, 'Нам нужен... Цветок! Для изысканности.', 12, 0, 100, 0, 0, 0, 114942, 53595, 53595, 0, 'Винодел Ильте to Player');
INSERT INTO `creature_text` VALUES (109212, 3, 0, 'Нам нужно что-нибудь кислое… И темное…', 12, 0, 100, 0, 0, 0, 114940, 4596, 4596, 0, 'Винодел Ильте to Player');
INSERT INTO `creature_text` VALUES (109218, 0, 0, 'Эммарель! Командир! Вы это видели?', 14, 0, 100, 603, 0, 66574, 112966, 0, 0, 0, 'Часовой Пирс to Player');
INSERT INTO `creature_text` VALUES (109218, 3, 0, 'Вон там – в небе, над Приютом! Что-то происходит!..', 12, 0, 100, 25, 0, 66575, 112972, 7438, 7438, 0, 'Часовой Пирс to Player');
INSERT INTO `creature_text` VALUES (109222, 0, 0, 'Она пригодится тебе на нелегком пути к победе.', 12, 0, 100, 0, 0, 69024, 118564, 0, 0, 0, 'Мерил Буря Скверны to Player');
INSERT INTO `creature_text` VALUES (109223, 0, 0, 'Мы подходим к месту, где выдерживается вино.', 12, 0, 100, 1, 0, 66268, 115021, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 1, 0, 'Наверняка для тебя не будет новостью тот факт, что в процессе изготовления вина сок должен забродить.', 12, 0, 100, 1, 0, 66271, 115025, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 2, 0, 'Мы ускоряем этот процесс с помощью магии. Пользуемся данной нам силой!', 12, 0, 100, 603, 0, 66269, 115022, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 3, 0, 'Взгляни налево, и ты увидишь наш сад.', 12, 0, 100, 1, 0, 66270, 115023, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 4, 0, 'Здесь мы выращиваем особые плоды. Из них получаются самые сладкие настойки.', 12, 0, 100, 1, 0, 66272, 115026, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 5, 0, 'Прямо перед тобой – наши станции розлива.', 12, 0, 100, 1, 0, 66273, 115028, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 6, 0, 'Бутылки запечатываются магией, чтобы сущность Колодца не выдыхалась.', 12, 0, 100, 1, 0, 66274, 115029, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 7, 0, 'Наше чародейское вино может храниться столетиями!', 12, 0, 100, 603, 0, 66275, 115030, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 8, 0, 'А здесь у нас распределительная станция.', 12, 0, 100, 1, 0, 66276, 115031, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 9, 0, 'Отсюда сотни бочек и бочонков чародейского вина отправляются в город.', 12, 0, 100, 1, 0, 66277, 115032, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 10, 0, 'Магистр чрезвычайно внимательно следит за…', 12, 0, 100, 1, 0, 66278, 115033, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 11, 0, 'Не бей его!', 12, 0, 100, 603, 0, 66279, 115034, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 12, 0, 'Мне очень жаль, прошу простить мою ошибку. Обещаю, что этого больше никогда не повторится.', 12, 0, 100, 20, 0, 66280, 115035, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 13, 0, 'О чем это я?', 12, 0, 100, 6, 0, 66281, 115036, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 14, 0, 'По ту сторону моста происходит важнейшая часть процесса.', 12, 0, 100, 1, 0, 66282, 115038, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 15, 0, 'Там мы добавляем в вино сущность Ночного Колодца, наделяющую напиток восстанавливающими свойствами.', 12, 0, 100, 1, 0, 66283, 115039, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 16, 0, 'Здесь я тебя оставлю. Надо подготовиться к следующему пункту программы.', 12, 0, 100, 1, 0, 66284, 115040, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109223, 17, 0, 'Встретимся на следующей станции. Мой помощник Сильверин с радостью покажет тебе процесс насыщения.', 12, 0, 100, 273, 0, 66285, 115042, 0, 0, 0, 'Марго to Player');
INSERT INTO `creature_text` VALUES (109224, 0, 0, 'Get movin\', wikket. Let the trial begin! Makin\' sweet hozen slerp is the way you can win!', 12, 0, 100, 0, 0, 64418, 115345, 0, 0, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109224, 3, 0, 'This hozen brew be just the thing! Its taste be fit for the Monkey King!', 12, 0, 100, 0, 0, 64430, 115359, 47502, 47502, 0, 'Король обезьян to Player');
INSERT INTO `creature_text` VALUES (109225, 0, 0, 'Опять грелли?! Разгромить мой дом им мало?!', 14, 0, 100, 0, 0, 0, 115706, 0, 0, 0, 'Эландр Песнь Клинка to Player');
INSERT INTO `creature_text` VALUES (109225, 1, 0, 'Это отобьет у них охоту лезть ко мне... А то стоит только поселиться в уединенном лесном домике, как можно забыть про покой.', 12, 0, 100, 0, 0, 0, 115707, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (109307, 0, 0, 'Я все еще способна на кое-какие магические приемы... если как следует настроюсь...', 12, 0, 100, 0, 0, 0, 115428, 0, 0, 0, 'Ари to Player');
INSERT INTO `creature_text` VALUES (109307, 1, 0, 'Только посмотри! Он просто бесподобен! И станет отличным помощником, $n, если вежливо попросить.', 12, 0, 100, 0, 0, 0, 115429, 0, 0, 0, 'Ари to Player');
INSERT INTO `creature_text` VALUES (109307, 2, 0, 'Используйте свою силу, чтобы убедить оба компонента объединиться!', 12, 0, 100, 0, 0, 0, 125092, 0, 0, 0, 'Ари to Player');
INSERT INTO `creature_text` VALUES (109316, 0, 0, 'Рад, что ты в порядке.', 12, 0, 100, 1, 0, 66937, 115314, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (109316, 1, 0, 'Иллидари, вы все знаете что делать. В бой!', 14, 0, 100, 25, 0, 66939, 115316, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (109316, 2, 0, 'Кария, Варедис, мы идем за вами!', 14, 0, 100, 0, 0, 66925, 115202, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (109332, 1, 0, 'I ... must ... OBEY!', 12, 0, 100, 0, 0, 0, 115192, 0, 0, 0, 'Предок из племени Кровавого Тотема to Player');
INSERT INTO `creature_text` VALUES (109332, 1, 0, 'I have no power over myself!', 12, 0, 100, 0, 0, 0, 115190, 0, 0, 0, 'Предок из племени Кровавого Тотема to Player');
INSERT INTO `creature_text` VALUES (109332, 1, 0, 'Спаси нас!', 12, 0, 100, 0, 0, 0, 115915, 0, 0, 0, 'Предок из племени Кровавого Тотема to Player');
INSERT INTO `creature_text` VALUES (109336, 1, 0, 'Я живу, чтобы служить.', 12, 0, 100, 0, 0, 55363, 120868, 0, 0, 0, 'Наполненный Скверной повелитель гнева to Player');
INSERT INTO `creature_text` VALUES (109344, 0, 0, 'Ваши души – мои!', 12, 0, 100, 1, 0, 55719, 115208, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (109344, 3, 0, 'Иллидари! Врата открыты. Все на \"Молот Скверны\"!', 12, 0, 100, 1, 0, 64697, 115200, 1339, 1339, 18950, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (109350, 4, 0, 'Another group! They can\'t keep coming forever... can they?', 12, 0, 100, 0, 0, 64403, 116303, 75651, 75651, 0, 'Жрица Летний Лепесток to Arbiter of the Inquisiiton');
INSERT INTO `creature_text` VALUES (109352, 4, 0, 'Look out! Greenies comin\' fast! Grook \'em! Make this day their last!', 12, 0, 100, 0, 0, 64441, 116166, 67916, 67916, 203856, 'Король обезьян to Arbiter of the Inquisiiton');
INSERT INTO `creature_text` VALUES (109355, 0, 0, 'Ugh... Oh, my head!', 12, 0, 100, 0, 0, 64399, 115711, 0, 0, 0, 'Жрица Летний Лепесток to Player');
INSERT INTO `creature_text` VALUES (109355, 1, 0, 'Thank you, hero. I would have been that monster\'s next meal!', 12, 0, 100, 0, 0, 64400, 116291, 0, 0, 0, 'Жрица Летний Лепесток to Player');
INSERT INTO `creature_text` VALUES (109355, 2, 0, 'Oh, Stonestep... your sacrifice will not be in vain!', 12, 0, 100, 18, 0, 64401, 115717, 0, 0, 0, 'Жрица Летний Лепесток to Player');
INSERT INTO `creature_text` VALUES (109355, 3, 0, 'I\'m coming too!', 12, 0, 100, 0, 0, 64402, 115729, 0, 0, 0, 'Жрица Летний Лепесток to Player');
INSERT INTO `creature_text` VALUES (109388, 3, 0, 'Как пожелаешь. Я встречусь с лейтенантом там же, где мы виделись в прошлый раз. Предупреди ее, чтобы ждала меня.', 12, 0, 100, 2, 0, 0, 115280, 24579, 24579, 0, 'Ночнорожденный-шпион to Player');
INSERT INTO `creature_text` VALUES (109388, 3, 0, 'Нужно немедленно сообщать о попытках украсть чародейское вино!', 12, 0, 100, 0, 0, 0, 115285, 117623, 117623, 0, 'Ночнорожденный-шпион to Player');
INSERT INTO `creature_text` VALUES (109391, 0, 0, 'I AM RENEWED! Come, little ones. Let us put an end to this invasion!', 14, 0, 100, 0, 0, 64387, 115662, 0, 0, 0, 'Юй-лун to 1137');
INSERT INTO `creature_text` VALUES (109391, 1, 0, 'Heroes, your fight is with Korithis. I\'ll protect you from his winged monstrosity.', 12, 0, 100, 0, 0, 64386, 115890, 0, 0, 0, 'Юй-лун to Yigthregor');
INSERT INTO `creature_text` VALUES (109391, 2, 0, 'You have learned well how to fight these creatures, but this will be only one of many battles.', 12, 0, 100, 0, 0, 64377, 115905, 0, 0, 0, 'Юй-лун to Lord Korithis');
INSERT INTO `creature_text` VALUES (109391, 3, 0, 'This is Fu Zan. Take it. May it shield you in the dark times ahead.', 12, 0, 100, 0, 0, 64378, 115906, 0, 0, 0, 'Юй-лун to Lord Korithis');
INSERT INTO `creature_text` VALUES (109393, 0, 0, 'Спасибо тебе, мой герой!', 12, 0, 100, 4, 0, 0, 22649, 0, 0, 0, 'Писарь Юй-лун to Player');
INSERT INTO `creature_text` VALUES (109393, 0, 1, 'О, спасибо, спасибо тебе!', 12, 0, 100, 4, 0, 0, 22645, 0, 0, 0, 'Писарь Юй-лун to Player');
INSERT INTO `creature_text` VALUES (109397, 0, 0, 'Such delicious knowledge!', 14, 0, 100, 0, 0, 64391, 115312, 0, 0, 0, 'Белфиар to 1137');
INSERT INTO `creature_text` VALUES (109397, 1, 0, 'Another mind to feed upon!', 12, 0, 100, 0, 0, 64392, 115323, 0, 0, 0, 'Белфиар to 0');
INSERT INTO `creature_text` VALUES (109403, 0, 0, 'Во имя Саргераса!', 12, 0, 100, 0, 0, 56973, 109464, 0, 0, 0, 'Нискарская вестница погибели to Player');
INSERT INTO `creature_text` VALUES (109403, 0, 1, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 56973, 129829, 0, 0, 0, 'Нискарская вестница погибели to Player');
INSERT INTO `creature_text` VALUES (109407, 3, 0, 'Список предателей? Его надо срочно передать лейтенанту Пиет.', 12, 0, 100, 1, 0, 0, 115279, 15675, 15675, 0, 'Ночнорожденная-сержант to Player');
INSERT INTO `creature_text` VALUES (109408, 3, 0, 'Тупица, за тобой следили! К оружию!', 12, 0, 100, 22, 0, 65778, 115282, 9893, 9893, 0, 'Лейтенант Пиет to Ночнорожденный-шпион');
INSERT INTO `creature_text` VALUES (109409, 0, 0, 'Such potency... I am saved! Thank you!', 12, 0, 100, 0, 0, 0, 115296, 0, 0, 0, 'Жалкий ночнорожденный to Player');
INSERT INTO `creature_text` VALUES (109409, 0, 1, 'So generous! Thank you, I owe you my life!', 12, 0, 100, 0, 0, 66151, 115295, 0, 0, 0, 'Жалкая ночнорожденная to Player');
INSERT INTO `creature_text` VALUES (109417, 0, 0, 'Мы заберем все души...', 12, 0, 100, 0, 0, 0, 115416, 0, 0, 0, 'Преследователь душ to Player');
INSERT INTO `creature_text` VALUES (109419, 0, 0, 'Твоя смерть уже близко.', 14, 0, 100, 0, 0, 0, 115420, 0, 0, 0, 'Надзиратель to Player');
INSERT INTO `creature_text` VALUES (109422, 0, 0, 'Ничтожество!', 14, 0, 100, 1, 0, 66972, 134193, 0, 0, 0, 'Кил\'джеден to Player');
INSERT INTO `creature_text` VALUES (109422, 1, 0, 'Так тому и быть, иллидари. Тебе не спастись, как и всему твоему миру.', 14, 0, 100, 0, 0, 66964, 115465, 0, 0, 0, 'Кил\'джеден to 1145');
INSERT INTO `creature_text` VALUES (109427, 0, 0, 'Презренным... презренная смерть!', 12, 0, 100, 0, 0, 68360, 118754, 0, 0, 0, 'Джарвокс to Player');
INSERT INTO `creature_text` VALUES (109433, 0, 0, 'Сквернотопырь визжит от боли, когда вы вонзаете в него оружие!', 16, 0, 100, 552, 0, 0, 118560, 0, 0, 0, 'Нискарский небесный ужас to Player');
INSERT INTO `creature_text` VALUES (109441, 3, 0, 'Fare well, little one.', 12, 0, 100, 0, 0, 64383, 115367, 18604, 18604, 0, 'Yu\'lon to Player');
INSERT INTO `creature_text` VALUES (109442, 0, 0, 'It\'s good to see you back again. Vanthir procured more Arcwine for you to distribute.', 12, 0, 0, 0, 0, 0, 115370, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (109448, 1, 0, 'Не сдерживай гнев!', 12, 0, 100, 0, 0, 67109, 117277, 0, 0, 0, 'Заклинатель земли');
INSERT INTO `creature_text` VALUES (109448, 1, 0, 'Стихии сокрушат тебя!', 12, 0, 100, 0, 0, 67110, 117278, 0, 0, 0, 'Заклинатель земли');
INSERT INTO `creature_text` VALUES (109448, 4, 0, 'Вот для чего мы столько тренировались!', 12, 0, 100, 0, 0, 67108, 117276, 13006, 13006, 0, 'Заклинатель земли');
INSERT INTO `creature_text` VALUES (109448, 4, 0, 'Нас не остановить.', 12, 0, 100, 0, 0, 67112, 117353, 3819, 3819, 0, 'Заклинатель земли to Воин-аристократ из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (109448, 4, 0, 'Наши враги падут!', 12, 0, 100, 0, 0, 67111, 138236, 6823, 6823, 0, 'Заклинатель земли to Вонючий алхимик');
INSERT INTO `creature_text` VALUES (109452, 1, 0, 'I will swallow your bones!', 12, 0, 100, 0, 0, 52543, 98257, 0, 0, 0, 'Стражница смерти из лагеря Дредвейка to Player');
INSERT INTO `creature_text` VALUES (109455, 0, 0, 'Служители Земли с тобой.', 12, 0, 100, 0, 0, 67106, 117273, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (109455, 1, 0, 'Похоже, тебе нужна помощь.', 12, 0, 100, 0, 0, 67105, 117272, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (109461, 3, 0, 'Забирай чародейское вино, а мне тут больше делать нечего.', 12, 0, 100, 1, 0, 0, 115481, 143772, 143772, 0, 'Грузчик из виноградников to Страж Великого магистра');
INSERT INTO `creature_text` VALUES (109461, 3, 0, 'Это не стоит того, чтобы умирать!', 12, 0, 100, 430, 0, 0, 115421, 75647, 75647, 0, 'Грузчик из виноградников to Страж Великого магистра');
INSERT INTO `creature_text` VALUES (109466, 0, 0, 'Before you stands a true champion who has won many great victories in the name of the Alliance.', 12, 0, 100, 396, 0, 66544, 117275, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (109466, 1, 0, 'The ancient weapon they wield is a symbol of hope to those we have sworn to protect.', 12, 0, 100, 396, 0, 66545, 117283, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (109466, 2, 0, 'We have gathered here to recognize your valor in battle. Turn and approach your peers.', 12, 0, 100, 396, 0, 66543, 115531, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (109466, 3, 0, 'For the Alliance!', 12, 0, 100, 71, 0, 66547, 146212, 0, 0, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (109471, 0, 0, 'Before you stands the Horde\'s greatest champion, hero of countless victories against our unrelenting foe.', 12, 0, 100, 396, 0, 66550, 118524, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (109471, 1, 0, 'Turn and face your peers, $p, so that they may bear witness to your ruthlessness and cunning.', 12, 0, 100, 396, 0, 66549, 118523, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (109471, 2, 0, 'The ancient weapon they wield inspires fear in the hearts of those who would threaten our way of life.', 12, 0, 100, 396, 0, 66551, 118525, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (109471, 3, 0, 'For the Horde!', 12, 0, 100, 396, 0, 66553, 143151, 0, 0, 0, 'Леди Сильвана Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (109473, 3, 0, 'Кто ты?', 12, 0, 100, 0, 0, 66005, 118520, 1590, 1590, 0, 'Срывательница покровов из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (109473, 3, 0, 'Сознайся во всем! Ты укрываешь изменников, предавших Сурамар!', 12, 0, 100, 0, 0, 0, 124223, 289499, 289499, 0, 'Срывательница покровов из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (109498, 0, 0, 'Нельзя было давать охотнику стрелять первым...', 12, 0, 100, 0, 0, 0, 121569, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (109500, 0, 0, 'Как они меня углядели?', 12, 0, 100, 0, 0, 0, 121571, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (109501, 0, 0, 'И умирая... я проклинаю нашего танка!', 12, 0, 100, 0, 0, 0, 121568, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (109503, 0, 0, 'Нет! Ходир! Что они с тобой сделали?!', 14, 0, 100, 53, 0, 68392, 118585, 0, 0, 0, 'Торим to 0');
INSERT INTO `creature_text` VALUES (109507, 0, 0, 'Слава валарьярам!', 12, 0, 100, 0, 0, 68106, 115628, 0, 0, 0, 'Рагнвальд Драконорожденный to 0');
INSERT INTO `creature_text` VALUES (109507, 0, 1, 'Убьем всех!', 12, 0, 100, 0, 0, 68109, 115631, 0, 0, 0, 'Рагнвальд Драконорожденный to 0');
INSERT INTO `creature_text` VALUES (109507, 0, 3, 'Достойный враг!', 12, 0, 100, 0, 0, 68107, 115629, 0, 0, 0, 'Рагнвальд Драконорожденный to 0');
INSERT INTO `creature_text` VALUES (109507, 0, 4, 'Вперед, к славе!', 12, 0, 100, 0, 0, 68108, 115630, 0, 0, 0, 'Рагнвальд Драконорожденный to 0');
INSERT INTO `creature_text` VALUES (109508, 0, 0, 'Бей захватчиков!', 12, 0, 100, 0, 0, 68092, 115625, 0, 0, 0, 'Финна Бьорнсдоттир to 0');
INSERT INTO `creature_text` VALUES (109508, 0, 1, 'За Одина!', 12, 0, 100, 0, 0, 68093, 115626, 0, 0, 0, 'Финна Бьорнсдоттир to 0');
INSERT INTO `creature_text` VALUES (109508, 0, 2, 'Почувствуй мой гнев!', 12, 0, 100, 0, 0, 68094, 115627, 0, 0, 0, 'Финна Бьорнсдоттир to 0');
