INSERT INTO `creature_text` VALUES (109825, 0, 0, 'Проклятые твари! Вы уже украли мою жизнь! Что еще вы хотите у меня отнять?', 14, 0, 100, 0, 0, 0, 115934, 0, 0, 0, 'Ария Сердце Скорби');
INSERT INTO `creature_text` VALUES (109825, 1, 0, 'Проклятие не властно над тобой. И наконец-то ждет меня покой. А ты запомни: хуже зла, мой друг, бездействие \"незлых\" людей вокруг.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Ария Сердце Скорби to Player');
INSERT INTO `creature_text` VALUES (109826, 3, 0, 'Голод невыносим...', 12, 0, 100, 409, 0, 0, 115888, 1837, 1837, 0, 'Алчущая помраченная');
INSERT INTO `creature_text` VALUES (109826, 3, 0, 'Неужели этот голод никогда не перестанет мучить меня?', 12, 0, 100, 409, 0, 0, 115885, 1337, 1337, 0, 'Алчущая помраченная');
INSERT INTO `creature_text` VALUES (109826, 3, 0, 'Так... холодно...', 12, 0, 100, 409, 0, 0, 115884, 1451, 1451, 0, 'Алчущий помраченный');
INSERT INTO `creature_text` VALUES (109826, 3, 0, 'Эй? Есть тут кто?', 12, 0, 100, 409, 0, 0, 115887, 1371, 1371, 0, 'Алчущий помраченный');
INSERT INTO `creature_text` VALUES (109839, 1, 0, 'Наша сила не знает равных!', 12, 0, 100, 0, 0, 67141, 117311, 0, 0, 0, 'Друид-медведь');
INSERT INTO `creature_text` VALUES (109839, 3, 0, 'I will tear you to shreds!', 12, 0, 100, 0, 0, 67140, 117310, 18464, 18464, 0, 'Друид-медведь');
INSERT INTO `creature_text` VALUES (109839, 4, 0, 'Враги природы падут.', 12, 0, 100, 0, 0, 67142, 117312, 31869, 31869, 0, 'Друид-медведь to Присягнувший погибели надзиратель');
INSERT INTO `creature_text` VALUES (109839, 4, 0, 'С нами сила природы.', 12, 0, 100, 0, 0, 67143, 117313, 10247, 10247, 0, 'Друид-медведь to Шаман приливов из племени Соленого Глаза');
INSERT INTO `creature_text` VALUES (109839, 4, 0, 'Стражи природы беспощадны.', 12, 0, 100, 0, 0, 67144, 117314, 10965, 10965, 0, 'Друид-медведь to Колдунья из армии Ракиша');
INSERT INTO `creature_text` VALUES (109933, 0, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 56983, 105127, 0, 0, 0, 'Arbiter of the Inquisiiton to Юй-лун');
INSERT INTO `creature_text` VALUES (109942, 0, 0, '%s submits to you.', 16, 0, 100, 0, 0, 0, 119281, 0, 0, 0, 'Storm\'s Reach Greatstag to Player');
INSERT INTO `creature_text` VALUES (109954, 1, 0, 'Мои иллюзии уничтожат тебя!', 12, 0, 100, 0, 0, 0, 116340, 0, 0, 0, 'Магистр Федрис to Player');
INSERT INTO `creature_text` VALUES (109954, 4, 0, 'Иллюзия... рассеялась.', 12, 0, 100, 0, 0, 0, 116341, 8932, 8932, 0, 'Магистр Федрис to Player');
INSERT INTO `creature_text` VALUES (109955, 0, 0, 'Where are your comrades, Jergosh? All dead? How... inconvenient.', 12, 0, 100, 1, 0, 66441, 116382, 0, 0, 0, 'Нагаз to Player');
INSERT INTO `creature_text` VALUES (109955, 1, 0, 'Hmmph... I saw Tyranis go off to the right before I was forced to retreat.', 12, 0, 100, 0, 0, 66444, 116387, 0, 0, 0, 'Нагаз to Player');
INSERT INTO `creature_text` VALUES (109955, 2, 0, 'Found Tyranis too late, did you? Too bad...', 12, 0, 100, 0, 0, 66445, 116456, 0, 0, 0, 'Нагаз to Player');
INSERT INTO `creature_text` VALUES (109955, 3, 0, 'Anyway, I\'ve... found something. Follow me! I need your help.', 12, 0, 100, 0, 0, 66446, 116457, 0, 0, 0, 'Нагаз to Player');
INSERT INTO `creature_text` VALUES (109955, 4, 0, 'I\'ll have that book now, Jergosh... and you\'ll be having a little accident! Heh!', 12, 0, 100, 0, 0, 66447, 116458, 0, 0, 0, 'Нагаз to Player');
INSERT INTO `creature_text` VALUES (109955, 5, 0, 'I should be... the one... at Gul\'dan\'s side...', 12, 0, 100, 0, 0, 66448, 116459, 0, 0, 0, 'Нагаз to 1860');
INSERT INTO `creature_text` VALUES (109956, 0, 0, 'Ah, you are Jergosh, yes? I assume by your arrival that the Book of Medivh is in your possession. Your timing could have been better.', 12, 0, 100, 1, 0, 66440, 116381, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 1, 0, 'It matters not. Gul\'dan knows the Eye is here, but Tol Barad is huge and overrun. I believe an important document, a manifest of prisoners, is located in this block.', 12, 0, 100, 1, 0, 66442, 116383, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 2, 0, 'In his wisdom, Nagaz here sent his only remaining disciple to scout below. I suggest you go see what happened to him while we search elsewhere.', 12, 0, 100, 1, 0, 66443, 116385, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 3, 0, 'Came up empty? No Nagaz either, I see. Not to worry, we all know how the lower echelons of our group scramble for position. Enough nonsense. What we seek is here!', 12, 0, 100, 0, 0, 66449, 116484, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 4, 0, 'There! The unquiet dead bar our way. Get that spirit out of my way, will you?', 12, 0, 100, 0, 0, 66451, 116487, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 5, 0, 'Hmm... I see. This entry speaks of a void terror, I\'m sure. It must have consumed the Eye in the Twisting Nether. Its purpose here is irrelevant. Let us get to Baradin Hold. Quickly, Jergosh!', 12, 0, 100, 0, 0, 66452, 116489, 0, 0, 0, 'Athrikus Narassin to Armond Thaco');
INSERT INTO `creature_text` VALUES (109956, 6, 0, 'It is here the most dangerous prisoners were kept. Do not wander, orc. No telling what may lurk in the shadows...', 12, 0, 100, 0, 0, 66456, 116967, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 7, 0, 'To think the Horde and Alliance once waged war to control this prison. As usual, their small-minded efforts were misguided and futile.', 12, 0, 100, 0, 0, 66457, 116968, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 8, 0, 'In your brief life span, I\'m sure you have never faced a void terror, Jergosh. Avoid its eye attacks and stay out of my way. I am not here to protect you!', 12, 0, 100, 0, 0, 66458, 116969, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (109956, 9, 0, 'The beast falls! I am victorious once again.', 12, 0, 100, 0, 0, 66453, 116972, 0, 0, 0, 'Athrikus Narassin to Occul\'tharon');
INSERT INTO `creature_text` VALUES (109956, 10, 0, 'I\'ll retrieve the Eye. No doubt your crude attempts at its removal would cause irreparable damage.', 12, 0, 100, 0, 0, 66454, 116973, 0, 0, 0, 'Athrikus Narassin to Occul\'tharon');
INSERT INTO `creature_text` VALUES (109956, 11, 0, 'I have it. A pity you didn\'t bring along the Book of Medivh. It\'s almost as if you don\'t trust me! Make haste to the Tomb of Sargeras. Do not disappoint Gul\'dan.', 12, 0, 100, 0, 0, 66455, 115699, 0, 0, 0, 'Athrikus Narassin to Occul\'tharon');
INSERT INTO `creature_text` VALUES (109964, 0, 0, 'What are you...  NOooooooo...', 12, 0, 100, 0, 0, 66463, 116395, 0, 0, 0, 'Tyranis Malem to Player');
INSERT INTO `creature_text` VALUES (109966, 3, 0, 'Show yourself, cretin!', 12, 0, 100, 396, 0, 0, 114102, 426381, 426381, 0, 'Подозрительный городской стражник to Player');
INSERT INTO `creature_text` VALUES (109967, 0, 0, '%s submits to you.', 16, 0, 100, 0, 0, 0, 119281, 0, 0, 0, 'Storm\'s Reach Warbear to Player');
INSERT INTO `creature_text` VALUES (109969, 0, 0, 'Не сдавайся, валарьяр!', 15, 0, 100, 0, 0, 64457, 116386, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (109969, 3, 0, 'Отлично, валарьяр.', 15, 0, 100, 0, 0, 64456, 116384, 85, 85, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (110024, 1, 0, 'Легион завоюет этот мир!', 12, 0, 100, 0, 0, 0, 116483, 0, 0, 0, 'Мал\'дрет Осквернитель to Player');
INSERT INTO `creature_text` VALUES (110037, 0, 0, 'Они ушли… портал закрыт!', 12, 0, 100, 0, 0, 68112, 117637, 0, 0, 0, 'Торим to 110344');
INSERT INTO `creature_text` VALUES (110037, 1, 0, 'Ты отважно сражаешься. Зря я сомневался в выборе отца. Если хочешь помочь найти Ходира, армия Ульдуара поддержит тебя.', 12, 0, 100, 0, 0, 68402, 117638, 0, 0, 0, 'Торим to 110344');
INSERT INTO `creature_text` VALUES (110038, 0, 0, 'Сейчас как угощу свинцом!', 14, 0, 100, 0, 0, 69843, 120720, 0, 0, 0, 'Проныра Шестихват');
INSERT INTO `creature_text` VALUES (110038, 1, 0, '|TInterface\\Icons\\Ability_Hunter_RapidRegeneration:20|tПроныра Шестихват начинает |cFFFF0404|Hspell:219848|h[пистолетный обстрел]|h|r! На землю!', 41, 0, 100, 0, 0, 69843, 120720, 0, 0, 0, 'Проныра Шестихват');
INSERT INTO `creature_text` VALUES (110039, 0, 0, 'Опять ты? Проныра, в атаку!', 14, 0, 100, 0, 0, 69856, 120707, 0, 0, 0, 'Рекс Шестихват');
INSERT INTO `creature_text` VALUES (110039, 1, 0, 'Перегрузка системы? Что за?! А-а-а!', 14, 0, 100, 0, 0, 69855, 120708, 0, 0, 0, 'Рекс Шестихват');
INSERT INTO `creature_text` VALUES (110039, 2, 0, '|TInterface\\Icons\\ability_siege_engineer_overload:20|tКрошшер Рекса выходит из под контроля. |cFFFF0404|Hspell:219614|h[Взбешенность!]|h|r Прыгайте на дирижабль!', 41, 0, 100, 0, 0, 69855, 117563, 0, 0, 0, 'Рекс Шестихват');
INSERT INTO `creature_text` VALUES (110039, 3, 0, 'Похороните золото со мной…', 12, 0, 100, 0, 0, 69854, 120709, 0, 0, 0, 'Рекс Шестихват to Player');
INSERT INTO `creature_text` VALUES (110045, 3, 0, 'Назад! Не подходи к жемчужине!', 12, 0, 100, 1, 0, 0, 116415, 47212, 47212, 0, 'Байрон Холунго to Player');
INSERT INTO `creature_text` VALUES (110045, 3, 0, 'Осторожнее! Не трогай эту жемчужину!', 12, 0, 100, 1, 0, 0, 116436, 5136, 5136, 0, 'Байрон Холунго to Player');
INSERT INTO `creature_text` VALUES (110056, 0, 0, 'Мы победим!', 12, 0, 100, 0, 0, 67148, 117318, 0, 0, 0, 'Милуна to Player');
INSERT INTO `creature_text` VALUES (110056, 1, 0, 'Природа на нашей стороне!', 12, 0, 100, 0, 0, 67150, 117320, 0, 0, 0, 'Милуна to Player');
INSERT INTO `creature_text` VALUES (110056, 1, 0, 'Что-то случилось?', 12, 0, 100, 0, 0, 67149, 127518, 0, 0, 0, 'Милуна to Player');
INSERT INTO `creature_text` VALUES (110064, 0, 0, 'Чернокнижники атакуют сверху!', 14, 0, 100, 0, 0, 0, 118621, 0, 0, 0, 'Финна Бьорнсдоттир to Player');
INSERT INTO `creature_text` VALUES (110073, 1, 0, 'В моей жемчужине я вижу твою судьбу, |3-6($r)!', 12, 0, 100, 0, 0, 0, 116453, 0, 0, 0, 'Урсаадж to Player');
INSERT INTO `creature_text` VALUES (110073, 1, 0, 'Вглядись в мою жемчужину, сухопутная крыса!', 12, 0, 100, 0, 0, 0, 116452, 0, 0, 0, 'Урсаадж to Player');
INSERT INTO `creature_text` VALUES (110096, 0, 0, 'Природа дает мне силу!', 12, 0, 100, 0, 0, 67153, 117323, 0, 0, 0, 'Хранитель Ремул to Player');
INSERT INTO `creature_text` VALUES (110164, 0, 0, 'Ну и местечко. Поищи Око, а я тебя прикрою.', 12, 0, 100, 0, 0, 69484, 116507, 0, 0, 0, 'Валира Сангвинар to Player');
INSERT INTO `creature_text` VALUES (110164, 1, 0, 'Пока ты будешь в крепости Черной Ладьи, я должна уладить одно важное дело. Встретимся в Оплоте Теней.', 12, 0, 100, 0, 0, 995, 117009, 0, 0, 0, 'Валира Сангвинар to Player');
INSERT INTO `creature_text` VALUES (110344, 0, 0, 'Никчемные отродья. Вы будете молить о смерти!', 12, 0, 100, 0, 0, 68617, 117538, 0, 0, 0, 'Леди Ран\'зара to Player');
INSERT INTO `creature_text` VALUES (110344, 1, 0, 'Хватит! Мне это надоело.', 12, 0, 100, 0, 0, 68609, 117058, 0, 0, 0, 'Леди Ран\'зара to 0');
INSERT INTO `creature_text` VALUES (110349, 0, 0, 'Ваш мир с-станет наш-шим!', 12, 0, 100, 0, 0, 0, 116903, 0, 0, 0, 'Королева стаи Морвейнисс to Рагнвальд Драконорожденный');
INSERT INTO `creature_text` VALUES (110354, 0, 0, 'Я вырву то, что по праву мое, из трупа твоего слуги!', 12, 0, 100, 0, 0, 66216, 116532, 0, 0, 0, 'Корин to 0');
INSERT INTO `creature_text` VALUES (110354, 1, 0, 'Ты мне не ровня! Я лучше!', 12, 0, 100, 0, 0, 66217, 116533, 0, 0, 0, 'Корин to 0');
INSERT INTO `creature_text` VALUES (110354, 2, 0, 'I will get what is rightfully mine!', 12, 0, 100, 0, 0, 66220, 116534, 0, 0, 0, 'Корин to 0');
INSERT INTO `creature_text` VALUES (110354, 3, 0, 'Меня подставили…', 12, 0, 100, 0, 0, 66221, 116535, 0, 0, 0, 'Корин to Дополненная иллюзия');
INSERT INTO `creature_text` VALUES (110365, 1, 0, 'Вызываешь меня на бой? Не смеши!', 12, 0, 100, 0, 0, 66332, 116536, 0, 0, 0, 'Рувен');
INSERT INTO `creature_text` VALUES (110365, 2, 0, 'Мне… обещали…', 12, 0, 100, 0, 0, 66336, 116540, 0, 0, 0, 'Рувен to Player');
INSERT INTO `creature_text` VALUES (110365, 3, 0, 'Легион ценит вашу жертву. Гордитесь этим.', 12, 0, 100, 0, 0, 67605, 118734, 233029, 233029, 0, 'Рувен');
INSERT INTO `creature_text` VALUES (110365, 4, 0, 'На колени пред мощью шал\'дорай!', 12, 0, 100, 0, 0, 66334, 116538, 50693, 50693, 0, 'Рувен');
INSERT INTO `creature_text` VALUES (110378, 0, 0, 'Ой, блестяшка!', 14, 0, 100, 0, 0, 0, 120813, 0, 0, 0, 'Драгон Зиморожденный to 0');
INSERT INTO `creature_text` VALUES (110378, 0, 1, 'А вот это мне!', 14, 0, 100, 0, 0, 0, 120814, 0, 0, 0, 'Драгон Зиморожденный to 0');
INSERT INTO `creature_text` VALUES (110378, 1, 0, 'Пф! Какое уродство.', 14, 0, 100, 0, 0, 0, 120817, 0, 0, 0, 'Драгон Зиморожденный to Player');
INSERT INTO `creature_text` VALUES (110378, 1, 1, 'Жуть, ну и космы.', 14, 0, 100, 0, 0, 0, 120816, 0, 0, 0, 'Драгон Зиморожденный to Player');
INSERT INTO `creature_text` VALUES (110378, 1, 2, 'Фу... воняет.', 14, 0, 100, 0, 0, 0, 120815, 0, 0, 0, 'Драгон Зиморожденный to Player');
INSERT INTO `creature_text` VALUES (110398, 0, 0, 'Да, я добыл семя плеточника из-под корней Шаладрассила.', 12, 0, 100, 0, 0, 66745, 111956, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (110398, 1, 0, 'Возьми это семя и посади его в землю прямо здесь.', 12, 0, 100, 1, 0, 66741, 111957, 0, 0, 0, 'Наралекс to Player');
INSERT INTO `creature_text` VALUES (110399, 0, 0, 'Ко мне, братья! Пылающий Легион атакует!', 12, 0, 100, 0, 0, 0, 116717, 0, 0, 0, 'Сеста Звездочет to Player');
INSERT INTO `creature_text` VALUES (110399, 1, 0, 'Не может быть! Ты вовсе не демон!', 12, 0, 100, 0, 0, 0, 116716, 0, 0, 0, 'Сеста Звездочет to Player');
INSERT INTO `creature_text` VALUES (110400, 0, 0, 'Как ты смеешь нарушать мой покой, демон?!', 12, 0, 100, 0, 0, 0, 116714, 0, 0, 0, 'Мондрель Ночная Погибель to Player');
INSERT INTO `creature_text` VALUES (110400, 1, 0, 'Скажи Валире, мы наблюдаем за ней.', 12, 0, 100, 0, 0, 0, 116715, 0, 0, 0, 'Мондрель Ночная Погибель to Player');
INSERT INTO `creature_text` VALUES (110401, 0, 0, 'Умри, $gскверноподданный предатель:скверноподданная предательница;!', 12, 0, 100, 0, 0, 0, 116718, 0, 0, 0, 'Т\'рель Лунный Клинок to Player');
INSERT INTO `creature_text` VALUES (110401, 1, 0, 'I found peace again.', 12, 0, 100, 0, 0, 0, 116719, 0, 0, 0, 'Т\'рель Лунный Клинок to Player');
INSERT INTO `creature_text` VALUES (110428, 3, 0, 'Враг у меня на прицеле!', 12, 0, 100, 11, 0, 67212, 117390, 350, 350, 0, 'Хеминг Эрнестуэй');
INSERT INTO `creature_text` VALUES (110428, 3, 0, 'Точный выстрел!', 12, 0, 100, 11, 0, 67211, 117389, 503, 503, 0, 'Хеминг Эрнестуэй');
INSERT INTO `creature_text` VALUES (110428, 3, 0, 'Этого я убил!', 12, 0, 100, 11, 0, 67213, 117392, 296, 296, 0, 'Хеминг Эрнестуэй');
INSERT INTO `creature_text` VALUES (110435, 4, 0, ' bur ', 12, 0, 100, 0, 0, 0, 0, 7738, 7738, 0, 'Раб из племени Мелководного Плавника to Надсмотрщик Древнего берега');
INSERT INTO `creature_text` VALUES (110436, 1, 0, 'Тебя поглотят мутные воды, мягкокож-ж-жий!', 12, 0, 100, 0, 0, 0, 104399, 0, 0, 0, 'Воин Древнего берега to Player');
INSERT INTO `creature_text` VALUES (110438, 1, 0, 'Твоя смерть будет... взрывной.', 12, 0, 100, 0, 0, 0, 117602, 0, 0, 0, 'Осадный мастер Эдрин to Player');
INSERT INTO `creature_text` VALUES (110438, 4, 0, 'Араш-фолас…', 12, 0, 100, 0, 0, 65811, 116791, 33811, 33811, 0, 'Осадный мастер Эдрин to Player');
INSERT INTO `creature_text` VALUES (110438, 4, 0, 'Бесполезно!', 12, 0, 100, 0, 0, 65813, 116793, 38763, 38763, 0, 'Осадный мастер Эдрин to Player');
INSERT INTO `creature_text` VALUES (110443, 3, 0, 'Меландр, ты снова подвел меня, но ты можешь исправиться. Избавься от этих чужаков! Я возвращаюсь в Цитадель Ночи.', 12, 0, 100, 0, 0, 67613, 117104, 178156, 178156, 0, 'Элисанда to Player');
INSERT INTO `creature_text` VALUES (110443, 3, 0, 'Случайных? Тебя не удивляет, что каждое найденное тело украшает сумеречная лилия, любимый цветок Талисры?', 12, 0, 100, 0, 0, 67610, 121608, 157391, 157391, 0, 'Элисанда to Player');
INSERT INTO `creature_text` VALUES (110443, 3, 0, 'Ты хочешь, чтобы я осталась чесать языками с никчемной знатью, когда враг вот-вот нанесет удар? Глянь, бестолочь! Мы не одни.', 12, 0, 100, 0, 0, 67614, 117106, 117937, 117937, 0, 'Элисанда to Player');
INSERT INTO `creature_text` VALUES (110456, 1, 0, 'I am a real hunter!', 12, 0, 100, 0, 0, 67202, 117377, 0, 0, 0, 'Незримый стрелок');
INSERT INTO `creature_text` VALUES (110456, 1, 0, 'Этот – мой!', 12, 0, 100, 0, 0, 67204, 117408, 0, 0, 0, 'Незримый стрелок');
INSERT INTO `creature_text` VALUES (110456, 4, 0, 'Лежи и не вставай.', 12, 0, 100, 0, 0, 67207, 117384, 13338, 13338, 0, 'Незримый стрелок to Заразная личинка');
INSERT INTO `creature_text` VALUES (110457, 0, 0, 'I am the Light in the flesh!', 12, 0, 100, 0, 0, 68485, 117527, 0, 0, 0, 'Сол to Player');
INSERT INTO `creature_text` VALUES (110462, 3, 0, 'Теперь мне чуждо их имя. И их принципы.', 12, 0, 100, 1, 0, 68298, 119546, 8317, 8317, 0, 'Мариэлла Уорд to Player');
INSERT INTO `creature_text` VALUES (110473, 0, 0, 'Снегокрыл, в бой!', 12, 0, 100, 0, 0, 67192, 117365, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (110473, 3, 0, 'Никто не скроется от Незримого пути!', 12, 0, 100, 0, 0, 67190, 117363, 209, 209, 220582, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (110474, 1, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 56983, 105127, 0, 0, 0, 'Колдунья из армии Ракиша to 78116');
INSERT INTO `creature_text` VALUES (110482, 0, 0, 'Пусть наш извечный враг трепещет перед мощью, которую ты на него обрушишь!', 12, 0, 100, 4, 0, 69019, 118568, 0, 0, 0, 'Кайн Ярость Солнца to Player');
INSERT INTO `creature_text` VALUES (110486, 3, 0, 'Вас ждет кое-что похуже смерти, дренеи!', 14, 0, 100, 0, 0, 70852, 118768, 352917, 625879, 0, 'Охотник Хак\'рот');
INSERT INTO `creature_text` VALUES (110488, 0, 0, 'Да! Нас ждет великая победа! С этим оружием ты поставишь Легион на колени! Даже Кил\'джеден сбежит, поджав хвост!', 12, 0, 100, 0, 0, 68751, 118566, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (110495, 0, 0, 'Сквозь твое оружие течет сама кровь Азерота! Пусть мудрость направляет твою руку!', 12, 0, 100, 0, 0, 68747, 117166, 0, 0, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (110498, 0, 0, 'Пока ты с нами и вера наша сильна, Пылающий Легион не захватит Азерот!', 12, 0, 100, 0, 0, 68740, 117173, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (110505, 0, 0, 'Пусть твое оружие бьет без промаха, $ct! Слава тебе!', 12, 0, 100, 0, 0, 68727, 117181, 0, 0, 0, 'Эммарель Стражница Тени to Player');
INSERT INTO `creature_text` VALUES (110533, 0, 0, 'Ah, Jergosh, at last! Bring me the Book!', 14, 0, 100, 0, 0, 66424, 117564, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (110533, 1, 0, 'You have both done well to acquire these artifacts. Your positions in the Shadow Council are all but assured.', 12, 0, 100, 1, 0, 66425, 117565, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (110533, 2, 0, 'With the Book of Medivh, the Eye of Dalaran, and the Scepter of Sargeras, we will rip open the gates to all the demon worlds! We will bring the full power of the Legion to bear on this pitiful planet.', 14, 0, 100, 1, 0, 66426, 117566, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (110533, 3, 0, 'I will go through the portal and act as a stabilizing anchor on Argus. You must focus the ritual energy through the scepter, widening the breach and creating a myriad of portals.', 14, 0, 100, 1, 0, 66427, 117567, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (110533, 4, 0, '<grim chuckle>Ahh, I only need ONE of you for the ritual. \"Discuss\" the matter quickly, and be ready...', 14, 0, 100, 1, 0, 66429, 117576, 0, 0, 0, 'Гул\'дан to Player');
INSERT INTO `creature_text` VALUES (110538, 3, 0, 'Нужно понять, почему погиб прежний аркан\'дор. Ответ может крыться в его останках.', 12, 0, 100, 0, 0, 66383, 118555, 1318, 1318, 0, 'Странник дола Фародин to Player');
INSERT INTO `creature_text` VALUES (110548, 0, 0, 'Congratulations on your \"promotion,\" Jergosh. Now let us begin. Take up the scepter... and be careful! This power can tear worlds asunder.', 12, 0, 100, 0, 0, 66431, 117590, 0, 0, 0, 'Image of Gul\'dan to Phantomflame Infernal');
INSERT INTO `creature_text` VALUES (110548, 1, 0, 'The time has come! Begin the incantations. Jergosh, focus the scepter on the portal. Today we usher in the end of this world!', 14, 0, 100, 1, 0, 66432, 117591, 0, 0, 0, 'Image of Gul\'dan to Player');
INSERT INTO `creature_text` VALUES (110548, 2, 0, 'Jergosh, what are-- YOU! You will pay for this deception! I will see you dead!', 14, 0, 100, 0, 0, 66434, 117611, 0, 0, 0, 'Image of Gul\'dan to Player');
INSERT INTO `creature_text` VALUES (110556, 0, 0, 'Uh... great master! Which of us is to wield the scepter?', 14, 0, 100, 0, 0, 66428, 117568, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (110556, 1, 0, 'So, we are to be foes after all. Pity, you would have made an excellent servant!', 14, 0, 100, 0, 0, 66430, 117582, 0, 0, 0, 'Athrikus Narassin to Player');
INSERT INTO `creature_text` VALUES (110556, 2, 0, 'You have yet to experience my true power!', 14, 0, 100, 0, 0, 66422, 117586, 0, 0, 0, 'Athrikus Narassin to 0');
INSERT INTO `creature_text` VALUES (110556, 4, 0, 'Slain... by an lowborn... how beneath me...', 12, 0, 100, 0, 0, 66423, 117588, 0, 0, 0, 'Athrikus Narassin to Phantomflame Infernal');
INSERT INTO `creature_text` VALUES (110557, 0, 0, 'Мы немедленно отправимся в Храм света Пустоты.', 12, 0, 100, 1, 0, 0, 122653, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (110564, 0, 0, 'Легион перешел в наступление! Надо скорее оповестить союзников!', 14, 0, 100, 603, 0, 71131, 121182, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (110564, 1, 0, 'В столь тяжелые времена не стоит отвергать тех, кто хочет помочь. Приветствуем тебя, новая союзница.', 12, 0, 100, 1, 0, 68275, 119549, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (110564, 2, 0, 'Еще один жрец Тьмы со своими бреднями. Я даже не удивлен. Бездна сводит с ума любого, кто к ней приближается.', 12, 0, 100, 1, 0, 68302, 118800, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (110564, 3, 0, 'Под твоим началом нас ждет еще не одна славная победа.', 12, 0, 100, 1, 0, 69384, 120506, 0, 0, 0, 'Алонсий Фаол to Player');
INSERT INTO `creature_text` VALUES (110571, 3, 0, 'I will go to Dalaran and try to decipher the runes on these stones.', 12, 0, 100, 1, 0, 68292, 119456, 989, 989, 0, 'Делас Лунный Клык to Player');
INSERT INTO `creature_text` VALUES (110572, 3, 0, 'Ищите молодые деревья, но остерегайтесь голодных рыб!', 41, 0, 100, 0, 0, 0, 120683, 12324, 12324, 0, 'Огонек to Player');
INSERT INTO `creature_text` VALUES (110577, 3, 0, 'Голод.', 12, 0, 100, 0, 0, 0, 118678, 3404, 3404, 0, 'Орет Зловещий to Player');
INSERT INTO `creature_text` VALUES (110582, 4, 0, '|TInterface\\Icons\\Spell_Fire_LavaSpawn:20|t Сяо Лян begins to cast a spell |cFFFF0404|Hspell:224588|h[\"Разогрев\"]|h|r! Нужно сбросить его в воду!', 42, 0, 100, 0, 0, 0, 121737, 32282, 32282, 224592, 'Сяо Лян to Player');
INSERT INTO `creature_text` VALUES (110596, 0, 0, 'Тебе не грозит одиночество.', 12, 0, 100, 1, 0, 68301, 119561, 0, 0, 0, 'Калия Менетил to Player');
INSERT INTO `creature_text` VALUES (110598, 0, 0, 'Да озарит Свет твой путь.', 12, 0, 100, 1, 0, 68284, 119560, 0, 0, 0, 'Верховная жрица Ишана to Player');
INSERT INTO `creature_text` VALUES (110620, 0, 0, 'Вы не убьете моего нового любимца! Скоро вы сами станете рабами Легиона!', 14, 0, 100, 0, 0, 68403, 118634, 0, 0, 0, 'Леди Ран\'зара to Ходир');
INSERT INTO `creature_text` VALUES (110620, 1, 0, 'Саргерас... я подвела тебя...', 12, 0, 100, 0, 0, 74346, 123096, 0, 0, 0, 'Леди Ран\'зара to Player');
INSERT INTO `creature_text` VALUES (110624, 3, 0, 'В книгу добавлен новый раздел по истории твоего оружия. Почитай, когда появится свободная минутка.', 12, 0, 100, 1, 0, 0, 120026, 17830190, 17830190, 0, 'Эдрия to Player');
INSERT INTO `creature_text` VALUES (110624, 3, 0, 'Я буду добавлять новые сведения по мере нашего исследования. Тебе стоит взглянуть, когда будет время.', 12, 0, 100, 1, 0, 0, 120260, 20803, 20803, 0, 'Эдрия to Player');
INSERT INTO `creature_text` VALUES (110654, 3, 0, 'Бегите!', 12, 0, 100, 0, 0, 66336, 118743, 2046854, 2046854, 0, 'Перепуганный работник to Player');
INSERT INTO `creature_text` VALUES (110655, 3, 0, 'Шевелись!', 12, 0, 100, 0, 0, 0, 118738, 2046378, 2046378, 0, 'Контрабандист из дома Весперос to Player');
INSERT INTO `creature_text` VALUES (110656, 1, 0, 'Вам не помешать моим исследованиям!', 12, 0, 100, 0, 0, 0, 118752, 0, 0, 0, 'Чародейка Лиландра to Огненный кристалл Лиландры');
INSERT INTO `creature_text` VALUES (110679, 0, 0, 'Что они делают с моим сыном?', 12, 0, 100, 0, 0, 0, 118887, 0, 0, 0, 'Аврора to 0');
INSERT INTO `creature_text` VALUES (110679, 1, 0, 'Пока советник не назначен, не отходи от меня ни на шаг. Обещай мне!', 12, 0, 100, 0, 0, 0, 119494, 0, 0, 0, 'Аврора to Player');
INSERT INTO `creature_text` VALUES (110679, 2, 0, 'Спасибо. Передай Ли\'лет, что я ее поддержу.', 12, 0, 100, 0, 0, 0, 119496, 0, 0, 0, 'Аврора to Player');
INSERT INTO `creature_text` VALUES (110680, 0, 0, 'Они меня убить хотели!', 12, 0, 100, 603, 0, 0, 118819, 0, 0, 0, 'Айлен Астравар to Player');
INSERT INTO `creature_text` VALUES (110680, 1, 0, 'С меня хватит! Прочь с этого кошмарного корабля! Я немедленно возвращаюсь к маме.', 12, 0, 100, 603, 0, 0, 119490, 0, 0, 0, 'Айлен Астравар to Player');
INSERT INTO `creature_text` VALUES (110684, 0, 0, 'За $gВерховного жреца:Верховную жрицу;!', 12, 0, 100, 603, 0, 0, 121428, 0, 0, 0, 'Натали Селин to Player');
INSERT INTO `creature_text` VALUES (110686, 0, 0, 'Уверен – нас свела сама судьба.', 12, 0, 100, 1, 0, 69378, 120500, 0, 0, 0, 'Забра Проклинатель to Player');
INSERT INTO `creature_text` VALUES (110687, 0, 0, 'Прими мою благодарность.', 12, 0, 100, 1, 0, 68260, 140238, 0, 0, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` VALUES (110687, 1, 0, 'Мы сражаемся и побеждаем вместе.', 12, 0, 100, 0, 0, 68597, 119782, 0, 0, 0, 'Ялия Мудрый Шепот to Player');
INSERT INTO `creature_text` VALUES (110691, 0, 0, 'Тени. Они насмехаются над нами. Видят нашу слабость.', 12, 0, 100, 1, 0, 68289, 118807, 0, 0, 0, 'Гилнер Серый Мох to Player');
INSERT INTO `creature_text` VALUES (110691, 1, 0, 'Ты ведь их не слышишь? Они смеются.. предупреждают... Мы все умрем!', 12, 0, 100, 1, 0, 68290, 118814, 0, 0, 0, 'Гилнер Серый Мох to Player');
INSERT INTO `creature_text` VALUES (110691, 2, 0, 'Из Бездны доносится шепот! О судьбе, о жуткой смерти! Жгите свечи, молитесь за любимых, погибель рядом, и ее не избежать.', 12, 0, 100, 1, 0, 68291, 118815, 0, 0, 0, 'Гилнер Серый Мох to Player');
INSERT INTO `creature_text` VALUES (110695, 3, 0, 'Мы вступим в бой по твоей команде. Скажи мне, когда все будет готово.', 12, 0, 100, 25, 0, 70821, 119805, 792154, 792154, 0, 'Предсказатель Нобундо to Player');
INSERT INTO `creature_text` VALUES (110698, 0, 0, 'Кто здесь?', 12, 0, 100, 0, 0, 0, 132213, 0, 0, 0, 'Арлуэль to player');
INSERT INTO `creature_text` VALUES (110698, 1, 0, '<кашляет> Арлуин, это ты? Тут так темно…', 12, 0, 100, 0, 0, 0, 118775, 0, 0, 0, 'Арлуэль to player');
INSERT INTO `creature_text` VALUES (110698, 2, 0, 'Понимаю… он всегда впутывался в неприятности…', 12, 0, 100, 0, 0, 0, 118776, 0, 0, 0, 'Арлуэль to player');
INSERT INTO `creature_text` VALUES (110698, 3, 0, 'Спасибо… я этого не забуду.', 12, 0, 100, 0, 0, 0, 118777, 0, 0, 0, 'Арлуэль to player');
INSERT INTO `creature_text` VALUES (110708, 3, 0, 'Жуткий свет внешнего мира жжет глаза вашей крысе! Скорее обратно в Клоаку!', 42, 0, 100, 0, 0, 0, 118779, 94280, 111255, 51259, 'Крысоконь to Player');
INSERT INTO `creature_text` VALUES (110715, 1, 0, 'Все склонятся перед нашей мощью.', 12, 0, 100, 0, 0, 67993, 117394, 0, 0, 0, 'Заклинательница Кирин-Тора');
INSERT INTO `creature_text` VALUES (110715, 1, 0, 'Узрите мощь верховного мага!', 12, 0, 100, 0, 0, 67994, 117395, 0, 0, 0, 'Заклинательница Кирин-Тора');
INSERT INTO `creature_text` VALUES (110715, 4, 0, 'Ты не чета Стражам Тирисфаля!', 12, 0, 100, 0, 0, 67992, 117393, 1586, 1586, 0, 'Заклинательница Кирин-Тора');
INSERT INTO `creature_text` VALUES (110726, 1, 0, 'Да начнется охота!', 12, 0, 100, 0, 0, 0, 118832, 0, 0, 0, 'Кадрей to Player');
INSERT INTO `creature_text` VALUES (110739, 0, 0, '$ct, наше время пришло. Тирион Фордринг ждет.', 12, 0, 100, 0, 0, 68892, 119372, 0, 0, 0, 'Верховный лорд Дарион Могрейн to Леди Лиадрин');
INSERT INTO `creature_text` VALUES (110742, 1, 0, 'За Легион!', 12, 0, 100, 0, 0, 55192, 135850, 0, 0, 0, 'Страж Скверны to 78116');
INSERT INTO `creature_text` VALUES (110749, 0, 0, 'Сюда! Я здесь!', 12, 0, 100, 603, 0, 68995, 120059, 0, 0, 0, 'Апони Светлая Грива to Player');
INSERT INTO `creature_text` VALUES (110750, 1, 0, 'Этот – мой!', 12, 0, 100, 0, 0, 67902, 117408, 0, 0, 0, 'Ученик Стражей Тирисфаля');
INSERT INTO `creature_text` VALUES (110750, 4, 0, 'Узрите Миллхауса Великолепного!', 12, 0, 100, 0, 0, 67900, 117406, 6272, 6272, 0, 'Ученик Стражей Тирисфаля');
INSERT INTO `creature_text` VALUES (110750, 4, 0, 'Я буду истреблять, разрушать, уничтожать и… \"разрушать\" уже было?', 12, 0, 100, 0, 0, 67902, 117407, 15530, 15530, 0, 'Ученик Стражей Тирисфаля');
INSERT INTO `creature_text` VALUES (110751, 3, 0, 'Ох, мои кости… Сколько я так простоял? Пошли, найдем место, где мы не попадемся на глаза этим василискам.', 12, 0, 100, 1, 0, 68259, 118831, 218, 218, 220234, 'Забра Проклинатель to Player');
INSERT INTO `creature_text` VALUES (110768, 3, 0, 'Встретимся в Покое. Удачного тебе пути.', 12, 0, 100, 1, 0, 69281, 118904, 274, 274, 0, 'Проекция Кейлека to Player');
INSERT INTO `creature_text` VALUES (110770, 0, 0, 'Свет уничтожит тебя!', 12, 0, 100, 0, 0, 76887, 119201, 0, 0, 0, 'Леди Лиадрин to Player');
INSERT INTO `creature_text` VALUES (110770, 1, 0, 'Такая сила... Невозможно...', 12, 0, 100, 0, 0, 76888, 119202, 0, 0, 0, 'Леди Лиадрин to 0');
INSERT INTO `creature_text` VALUES (110773, 0, 0, 'Пойдем. Чтобы усилить Сердце, нам нужно поговорить с Талреем и его сородичами.', 12, 0, 100, 1, 0, 0, 119195, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 1, 0, 'Ну, давай же! Давай!', 12, 0, 100, 1, 0, 0, 119835, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 2, 0, 'Невероятно! Но этой силы все равно недостаточно.', 12, 0, 100, 1, 0, 0, 119167, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 3, 0, 'Рад видеть тебя в добром здравии, Сенегос.', 12, 0, 100, 1, 0, 0, 119143, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 4, 0, 'Сработало!..', 12, 0, 100, 1, 0, 0, 119165, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 5, 0, 'Только Лунные стражи владеют магией, способной сделать Сердце еще сильнее. Мы должны спасти их.', 12, 0, 100, 1, 0, 0, 119194, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 6, 0, 'Только… не задерживайся там, ладно?', 12, 0, 100, 1, 0, 0, 119185, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 8, 0, 'Удивительно! Я думал, Сердце – не более чем легенда.', 12, 0, 100, 1, 0, 0, 119156, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110773, 9, 0, 'Благодарю тебя, Сенегос. Надеюсь, наши пути еще пересекутся.', 12, 0, 100, 1, 0, 0, 119190, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (110779, 1, 0, 'Мы очистим ваш мир!', 12, 0, 100, 0, 0, 56970, 105114, 0, 0, 0, 'Колдунья из армии Ракиша to Повелитель стихий Да\'кеш');
INSERT INTO `creature_text` VALUES (110791, 0, 0, 'Какой безумец придумал наделить это существо силой Ночного Колодца? Если это чудище создано специально для защиты нижних ярусов цитадели, то оно крайне опасно.', 14, 0, 100, 0, 0, 67736, 118925, 0, 0, 0, 'Первая чародейка Талисра to Кислотный скорпид');
INSERT INTO `creature_text` VALUES (110791, 1, 0, 'Друзья, это знаменательный день. До пришествия Легиона вход в Цитадель Ночи был закрыт для чужеземцев десять тысяч лет.', 14, 0, 100, 0, 0, 67739, 118933, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (110791, 2, 0, 'Теперь только вы... только вы, чужеземцы можете спасти мой народ… от ужасной судьбы, на которую его обрекла Элисанда.', 14, 0, 100, 0, 0, 67737, 118929, 0, 0, 0, 'Первая чародейка Талисра to Торм Громила');
INSERT INTO `creature_text` VALUES (110791, 3, 0, 'Шанар далорас! Не представляю, как могло возникнуть нечто подобное, но в нем живет энергия Ночного Колодца.', 14, 0, 100, 0, 0, 67738, 118931, 0, 0, 0, 'Первая чародейка Талисра to Торм Громила');
INSERT INTO `creature_text` VALUES (110791, 4, 0, 'От этой аберрации исходят лучи силы. Должно быть, именно она помогает Элисанде поддерживать ловушку времени.', 14, 0, 100, 0, 0, 78870, 124818, 0, 0, 0, 'Первая чародейка Талисра to Торм Громила');
INSERT INTO `creature_text` VALUES (110791, 5, 0, 'Мы должны уничтожить тварь, чтобы освободить наших друзей у ворот!', 14, 0, 100, 0, 0, 78871, 124819, 0, 0, 0, 'Первая чародейка Талисра to Торм Громила');
INSERT INTO `creature_text` VALUES (110791, 6, 0, 'Дворец прямо перед вами. Но охраняется он еще сильнее, чем нижние ярусы.', 14, 0, 100, 0, 0, 67740, 118934, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (110793, 0, 0, 'Возьми демонов на себя, а я освобожу агента ШРУ.', 12, 0, 100, 1, 0, 69508, 119166, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110793, 1, 0, 'А вот и они!', 12, 0, 100, 1, 0, 69509, 119176, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110793, 2, 0, 'Потерпи еще немного, Матиас.', 12, 0, 100, 0, 0, 69512, 119197, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110793, 3, 0, 'Один есть. Остался последний.', 12, 0, 100, 0, 0, 27513, 119198, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110793, 4, 0, 'Не могу вскрыть замок. $p, попробуй ты.', 12, 0, 100, 661, 0, 69515, 119200, 0, 0, 0, 'Таоши to Таоши');
INSERT INTO `creature_text` VALUES (110793, 5, 0, 'Я выведу Матиаса через канализацию.', 12, 0, 100, 1, 0, 69517, 119220, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110793, 6, 0, 'Встретимся в Палате Теней, $n.', 12, 0, 100, 0, 0, 69518, 119222, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110794, 0, 0, 'Нужно тихо пробраться к Матиасу Шоу.', 12, 0, 100, 0, 0, 69499, 118984, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110794, 1, 0, 'Он внутри.', 12, 0, 100, 0, 0, 69504, 118989, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110794, 2, 0, 'Еще немного.', 12, 0, 100, 0, 0, 69505, 118990, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110794, 3, 0, '$p, он внизу! Спустись по лестнице!', 12, 0, 100, 0, 0, 69506, 118991, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110794, 4, 0, 'Надо скорей добраться до Матиаса.', 12, 0, 100, 0, 0, 69502, 118987, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (110799, 3, 0, 'Outland had its charms. But when I heard about this lodge, I had to come see it for myself.', 12, 0, 100, 396, 0, 0, 118998, 67308, 67308, 0, 'Гедра to Player');
INSERT INTO `creature_text` VALUES (110799, 3, 0, 'Охотники всех рас помогают друг другу... Раньше мы о таком и подумать не могли!', 12, 0, 100, 396, 0, 0, 118999, 138479, 138479, 0, 'Гедра to Player');
INSERT INTO `creature_text` VALUES (110804, 3, 0, 'Да, мама.', 12, 0, 100, 0, 0, 0, 119495, 73186, 73186, 0, 'Айлен to Player');
INSERT INTO `creature_text` VALUES (110818, 0, 0, 'I owe you.', 12, 0, 100, 1, 0, 0, 138328, 0, 0, 0, 'Охотница на демонов to Player');
INSERT INTO `creature_text` VALUES (110818, 0, 1, 'С твоей помощью мы победим в этой войне.', 12, 0, 100, 1, 0, 0, 119049, 0, 0, 0, 'Охотник на демонов to Player');
INSERT INTO `creature_text` VALUES (110818, 0, 2, 'Прими мою благодарность.', 12, 0, 100, 0, 0, 0, 140238, 0, 0, 0, 'Охотник на демонов to Player');
INSERT INTO `creature_text` VALUES (110837, 0, 0, 'Некоронованные спешат на помощь. Я так и знал.', 12, 0, 100, 1, 0, 69510, 119183, 0, 0, 0, 'Мастер Матиас Шоу to Player');
INSERT INTO `creature_text` VALUES (110837, 1, 0, 'Вытащи меня, и я помогу.', 12, 0, 100, 22, 0, 69511, 119196, 0, 0, 0, 'Мастер Матиас Шоу to Player');
INSERT INTO `creature_text` VALUES (110837, 2, 0, 'Что не так, Таоши?', 12, 0, 100, 6, 0, 69514, 119199, 0, 0, 0, 'Мастер Матиас Шоу to Player');
INSERT INTO `creature_text` VALUES (110837, 3, 0, 'Спасибо.', 12, 0, 100, 0, 0, 69517, 131567, 0, 0, 0, 'Мастер Матиас Шоу to Player');
INSERT INTO `creature_text` VALUES (110861, 0, 0, 'Довольно! Я сам с тобой разберусь.', 14, 0, 100, 53, 0, 0, 119160, 0, 0, 0, 'Страж казематов Тень to Player');
INSERT INTO `creature_text` VALUES (110861, 1, 0, '... Я подвел тебя, мой повелитель Детеррок...', 12, 0, 100, 0, 0, 69514, 119154, 0, 0, 0, 'Страж казематов Тень to Player');
INSERT INTO `creature_text` VALUES (110866, 0, 0, 'Благодарю тебя! Я готова.', 12, 0, 100, 396, 0, 69650, 120472, 0, 0, 0, 'Делас Лунный Клык to Player');
INSERT INTO `creature_text` VALUES (110866, 1, 0, 'Сестра, позволь мне сопровождать тебя. Я пошлю весть в Храм света Пустоты и запрошу подкрепление у конклава.', 12, 0, 100, 1, 0, 68353, 119267, 0, 0, 0, 'Делас Лунный Клык to Player');
INSERT INTO `creature_text` VALUES (110874, 0, 0, 'Символ дома Стеллерис... Но это невозможно! Корин был близким другом Рувена...', 12, 0, 100, 1, 0, 0, 119179, 0, 0, 0, 'Расстроенная аристократка to Player');
INSERT INTO `creature_text` VALUES (110874, 1, 0, 'Нет, это просто невозможно...', 12, 0, 100, 274, 0, 0, 119180, 0, 0, 0, 'Расстроенная аристократка to Player');
INSERT INTO `creature_text` VALUES (110875, 0, 0, 'Какой абсурд! Анекдот о моем господине? Нужно совсем ума лишиться, чтобы что-то такое придумать!', 12, 0, 100, 5, 0, 0, 119181, 0, 0, 0, 'Пристыженный аристократ to Player');
INSERT INTO `creature_text` VALUES (110875, 1, 0, '... Тот, кто смеется над покойным, несомненно виновен в его смерти!', 12, 0, 100, 1, 0, 0, 119182, 0, 0, 0, 'Пристыженный аристократ to Player');
INSERT INTO `creature_text` VALUES (110876, 0, 0, 'Конечно, НЕТ! Зачем ему так поступать? У него что, нет чести?!', 12, 0, 100, 5, 0, 66003, 119177, 0, 0, 0, 'Раздраженный слуга to Player');
INSERT INTO `creature_text` VALUES (110876, 1, 0, '... Зачем ему так поступать?..', 12, 0, 100, 1, 0, 66004, 119178, 0, 0, 0, 'Раздраженный слуга to Player');
INSERT INTO `creature_text` VALUES (110909, 1, 0, 'Легион поглотит все.', 12, 0, 100, 0, 0, 55147, 99763, 0, 0, 0, 'Кол\'роз Вечнопылающий to Player');
INSERT INTO `creature_text` VALUES (110909, 1, 0, 'Сочный мозг. Хрустящие кости.', 12, 0, 100, 0, 0, 55150, 96651, 0, 0, 0, 'Кол\'роз Вечнопылающий to Player');
INSERT INTO `creature_text` VALUES (110916, 3, 0, 'Не получилось решить задачку? Ничего страшного – активируй шкатулку снова и попробуй еще раз.', 15, 0, 100, 0, 0, 0, 119275, 24094, 24094, 0, 'Чародейка Хелис to Player');
INSERT INTO `creature_text` VALUES (110931, 1, 0, 'Идите к Фурогу, мелкие эльфячьи монстрики...', 12, 0, 100, 0, 0, 0, 119271, 0, 0, 0, 'Фурог эльфолом');
INSERT INTO `creature_text` VALUES (110931, 4, 0, 'I will break your elf contraption!', 12, 0, 100, 0, 0, 0, 119284, 9188, 9188, 0, 'Фурог эльфолом to Алчущий маны иссохший');
INSERT INTO `creature_text` VALUES (110939, 0, 0, 'Я знал, что помощь придет. Спасибо.', 12, 0, 100, 0, 0, 0, 119420, 0, 0, 0, 'Больной рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110939, 0, 1, 'Я знала, что помощь придет. Спасибо.', 12, 0, 100, 0, 0, 0, 119420, 0, 0, 0, 'Больной рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110939, 0, 2, 'I did not lose hope, $c. Thank you.', 12, 0, 100, 0, 0, 0, 119421, 0, 0, 0, 'Больной рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110939, 0, 3, 'Мои молитвы услышаны! $c, спасибо.', 12, 0, 100, 0, 0, 0, 119422, 0, 0, 0, 'Больной рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110939, 0, 4, 'Не ожидала, что меня спасет кто-то из ордена Серебряной Длани. Спасибо тебе и твоему ордену.', 12, 0, 100, 0, 0, 0, 119423, 0, 0, 0, 'Больной рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110949, 0, 0, 'Проклинаю... тебя!', 12, 0, 100, 0, 0, 61241, 121831, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 1, 0, 'Содрать плоть! Раскидать кости!', 12, 0, 100, 0, 0, 61237, 121827, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 3, 0, 'Не может быть...', 12, 0, 100, 0, 0, 61233, 121822, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 4, 0, 'Нас не остановить!', 12, 0, 100, 0, 0, 61236, 129922, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 5, 0, 'Ты не уйдешь... от проклятия!', 12, 0, 100, 0, 0, 61239, 121828, 0, 0, 0, 'Screeching Hag-Sister to 110392');
INSERT INTO `creature_text` VALUES (110949, 8, 0, 'Это... наша земля...', 12, 0, 100, 0, 0, 61243, 121836, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 9, 0, 'Умри, червь!', 12, 0, 100, 0, 0, 61240, 121830, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 13, 0, 'I will turn you to stone and dust!', 12, 0, 100, 0, 0, 61237, 121826, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110949, 14, 0, 'Ка-а-ар!', 12, 0, 100, 0, 0, 61234, 121843, 0, 0, 0, 'Screeching Hag-Sister to Player');
INSERT INTO `creature_text` VALUES (110958, 3, 0, 'Говорят, звездный авгур Этрей так умен, что его голова весит как три головы знатных эльфов. Или шесть голов незнатных!', 12, 0, 100, 1, 0, 0, 118919, 185910, 185910, 0, 'Элегантный дворянин');
INSERT INTO `creature_text` VALUES (110958, 3, 0, 'Легион меня нисколько не тревожит. Здорово, что мы на стороне победителей.', 12, 0, 100, 1, 0, 0, 118922, 508069, 508069, 0, 'Элегантная дворянка');
INSERT INTO `creature_text` VALUES (110959, 3, 0, 'Есть прогресс в деле о советнике Вандросе?', 12, 0, 100, 1, 0, 0, 118913, 237360, 237360, 0, 'Богатый дворянин');
INSERT INTO `creature_text` VALUES (110960, 3, 0, 'Говорят, его убила любовница.', 12, 0, 100, 1, 0, 0, 118914, 247455, 247455, 0, 'Надменная аристократка to Богатый дворянин');
INSERT INTO `creature_text` VALUES (110960, 3, 0, 'Даже если он виновен, пусть лучше он будет советником, чем Ли\'лет Лунарх.', 12, 0, 100, 1, 0, 0, 118909, 405281, 405281, 0, 'Надменная аристократка to Богатая дворянка');
INSERT INTO `creature_text` VALUES (110967, 0, 0, 'Вероломная гадина! Эта должность моя по праву!', 12, 0, 100, 603, 0, 66215, 116517, 0, 0, 0, 'Корин to Player');
INSERT INTO `creature_text` VALUES (110967, 1, 0, 'I am dishonored! My name has been blackened by baseless accusations!', 12, 0, 100, 603, 0, 66214, 116518, 0, 0, 0, 'Корин to Player');
INSERT INTO `creature_text` VALUES (110967, 2, 0, 'I will defend my honor in an ancient ritual ... I demand the right to Tal\'ashar!', 12, 0, 100, 603, 0, 66219, 116524, 0, 0, 0, 'Корин to Player');
INSERT INTO `creature_text` VALUES (110967, 3, 0, 'I will manage on my own. Tea, not a coward and I can stand up for my honor myself.', 12, 0, 100, 1, 0, 68148, 119382, 0, 0, 0, 'Корин to Player');
INSERT INTO `creature_text` VALUES (110967, 4, 0, 'Приготовься к смерти и поднимайся на крышу святилища.', 12, 0, 100, 603, 0, 68149, 119383, 0, 0, 0, 'Корин to Player');
INSERT INTO `creature_text` VALUES (110972, 0, 0, 'Кто бы сюда ни явился... будет иметь дело со мной!', 12, 0, 100, 0, 0, 68337, 119468, 0, 0, 0, 'Королева стаи Арамисса to Player');
INSERT INTO `creature_text` VALUES (110972, 1, 0, 'Мы столько веков искали ваш Храм света Пустоты! А он всегда был у нас под носом!', 12, 0, 100, 0, 0, 68338, 119470, 0, 0, 0, 'Королева стаи Арамисса to 0');
INSERT INTO `creature_text` VALUES (110972, 2, 0, 'Теперь мы чувствуем ослепительный Свет, пробивающийся через Круговерть!', 12, 0, 100, 0, 0, 68339, 119471, 0, 0, 0, 'Королева стаи Арамисса to 0');
INSERT INTO `creature_text` VALUES (110972, 3, 0, 'Наши армии пойдут на штурм. Храм падет... А с ним — и великий пророк Велен!', 12, 0, 100, 0, 0, 68340, 119472, 0, 0, 0, 'Королева стаи Арамисса to 0');
INSERT INTO `creature_text` VALUES (110972, 4, 0, 'Храм… падет…', 12, 0, 100, 0, 0, 68341, 119474, 0, 0, 0, 'Королева стаи Арамисса to Player');
INSERT INTO `creature_text` VALUES (110975, 0, 0, 'I will not miss!', 12, 0, 100, 0, 0, 67193, 117367, 0, 0, 0, 'Незримый следопыт');
INSERT INTO `creature_text` VALUES (110975, 1, 0, 'Да начнется охота!', 12, 0, 100, 0, 0, 67194, 118832, 0, 0, 0, 'Незримый следопыт');
INSERT INTO `creature_text` VALUES (110975, 1, 0, 'От меня никто не скроется!', 12, 0, 100, 0, 0, 67195, 117369, 0, 0, 0, 'Незримый следопыт');
INSERT INTO `creature_text` VALUES (110977, 3, 0, 'Я знала, что помощь придет. Спасибо.', 12, 0, 100, 0, 0, 0, 119420, 448209, 448209, 0, 'Раненая жрица Конклава to Player');
INSERT INTO `creature_text` VALUES (110978, 0, 0, 'I did not lose hope, $c. Thank you.', 12, 0, 100, 0, 0, 0, 119421, 0, 0, 0, 'Павший жрец Конклава to Player');
INSERT INTO `creature_text` VALUES (110978, 0, 1, 'Да пребудут с тобой честь и справедливость. Я буду сражаться за орден до конца!', 12, 0, 100, 0, 0, 0, 119426, 0, 0, 0, 'Павшая жрица Конклава to Player');
INSERT INTO `creature_text` VALUES (110978, 0, 2, 'Я знал, что помощь придет. Спасибо.', 12, 0, 100, 0, 0, 0, 119420, 0, 0, 0, 'Павший жрец Конклава to Player');
INSERT INTO `creature_text` VALUES (110979, 0, 0, 'Когда нас затащили сюда через портал, я уже думала, что нам конец. Спасибо, $c.', 12, 0, 100, 0, 0, 0, 119419, 0, 0, 0, 'Пленный рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110979, 0, 1, 'I did not lose hope, $c. Thank you.', 12, 0, 100, 0, 0, 0, 119421, 0, 0, 0, 'Пленный рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110979, 0, 2, 'I\'m so glad to see you, $n. May the Light keep you.', 12, 0, 100, 0, 0, 0, 119425, 0, 0, 0, 'Пленный рыцарь ордена Серебряной Длани to Player');
INSERT INTO `creature_text` VALUES (110990, 0, 0, '$r... Смертное создание... Ты много на себя берешь, призывая меня.', 12, 0, 100, 0, 0, 0, 119397, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110990, 1, 0, 'Повелитель Огня Рагнарос когда-то сковал меня именно такими оковами, какие ты держишь сейчас в руках.', 12, 0, 100, 0, 0, 0, 119398, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110990, 2, 0, 'Алчные смертные вроде тебя освободили меня лишь затем, чтобы украсть у меня оружие и силу.', 12, 0, 100, 0, 0, 0, 119399, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110990, 3, 0, 'Но поскольку моему возвращению содействовали Служители Земли, я склонен проявить снисхождение. Возможно, твое дело не такое уж глупое.', 12, 0, 100, 0, 0, 0, 119400, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110990, 4, 0, 'Отдай мне оковы , я обращу их силу их против Повелителя Огня и его прислужников. Ты получишь за это награду.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110990, 5, 0, '$R, дело сделано. Больше ко мне не обращайся.', 12, 0, 100, 0, 0, 0, 119402, 0, 0, 0, 'Громораан <Владыка Ветра> to player');
INSERT INTO `creature_text` VALUES (110992, 3, 0, 'I am a judge and executioner all rolled into one.', 12, 0, 100, 0, 0, 55020, 97915, 3574, 3574, 0, 'Ратак to Player');
INSERT INTO `creature_text` VALUES (110999, 1, 0, 'Зверь вырвался на свободу! Убей его!', 12, 0, 100, 0, 0, 0, 119457, 0, 0, 0, 'Скверноподданный адепт to Су\'эш');
INSERT INTO `creature_text` VALUES (111002, 3, 0, 'Шевелитесь! Сокрушите всех, кто смеет противостоять Легиону!', 14, 0, 100, 0, 0, 0, 119469, 31921, 31921, 0, 'Приносящий Погибель Синарий');
INSERT INTO `creature_text` VALUES (111013, 3, 0, 'Убей эту тварь! Убей ее!', 12, 0, 100, 0, 0, 0, 119462, 26526, 26526, 0, 'Боец из армии Оскверненной Души to Су\'эш');
INSERT INTO `creature_text` VALUES (111022, 0, 0, 'Вы едва одолели пешку и бросаете вызов самому владыке?', 14, 0, 100, 0, 0, 68561, 119751, 0, 0, 0, 'Внутренний демон to Гул\'дан');
INSERT INTO `creature_text` VALUES (111022, 1, 0, 'Встречайте тень грядущего! Тень бесконечной тьмы, что поглотит ваш мир!', 14, 0, 100, 0, 0, 68562, 119752, 0, 0, 0, 'Внутренний демон to Гул\'дан');
INSERT INTO `creature_text` VALUES (111022, 2, 0, 'Вы не готовы!', 14, 0, 100, 0, 0, 68563, 121538, 0, 0, 0, 'Внутренний демон to 0');
INSERT INTO `creature_text` VALUES (111022, 3, 0, 'Ощути силу внутреннего демона!', 14, 0, 100, 0, 0, 68566, 119755, 0, 0, 0, 'Внутренний демон to 0');
INSERT INTO `creature_text` VALUES (111022, 4, 0, 'Узрите пламя Аззинота!', 14, 0, 100, 0, 0, 68567, 121729, 0, 0, 0, 'Внутренний демон to 0');
INSERT INTO `creature_text` VALUES (111022, 5, 0, 'Слишком просто!', 14, 0, 100, 0, 0, 68568, 128279, 0, 0, 0, 'Внутренний демон to Player');
INSERT INTO `creature_text` VALUES (111022, 5, 1, 'И это вся ваша ярость?!', 14, 0, 100, 0, 0, 68572, 119762, 0, 0, 0, 'Внутренний демон to Внутренний демон');
INSERT INTO `creature_text` VALUES (111022, 6, 0, 'Вас ждет будущее, в котором мертвы все, кто вам дорог!', 14, 0, 100, 0, 0, 68565, 119757, 0, 0, 0, 'Внутренний демон to 0');
INSERT INTO `creature_text` VALUES (111022, 7, 0, 'Как... Это же невозможно...', 14, 0, 100, 0, 0, 68570, 119760, 0, 0, 0, 'Внутренний демон to Player');
INSERT INTO `creature_text` VALUES (111022, 42, 0, 'Умри!', 14, 0, 100, 0, 0, 68569, 129619, 0, 0, 0, 'Внутренний демон to Player');
INSERT INTO `creature_text` VALUES (111024, 1, 0, 'Негодяй!', 12, 0, 100, 0, 0, 0, 26397, 0, 0, 0, 'Жрец-ворон Алого Натиска');
INSERT INTO `creature_text` VALUES (111025, 1, 0, 'Прийти сюда было глупым поступком.', 12, 0, 100, 0, 0, 0, 26400, 0, 0, 0, 'Пехотинец Алого Натиска');
INSERT INTO `creature_text` VALUES (111026, 3, 0, 'Твое последнее слово, грязное отродье?', 12, 0, 100, 0, 0, 0, 26293, 14394, 14394, 0, 'Палач Алого Натиска to Player');
INSERT INTO `creature_text` VALUES (111044, 0, 0, 'Мы вооружены и готовы к бою, $gВерховный жрец:Верховная жрица;.', 12, 0, 100, 1, 0, 0, 122497, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (111044, 1, 0, 'Поможем нашим братьям в Свете, защитим Храм света Пустоты. К порталу!', 12, 0, 100, 1, 0, 0, 122498, 0, 0, 0, 'Лорд Максвелл Тиросс to Player');
INSERT INTO `creature_text` VALUES (111048, 0, 0, 'Увидимся в ШРУ. Удачи, $n.', 12, 0, 100, 1, 0, 69531, 119785, 0, 0, 0, 'Таоши to Player');
INSERT INTO `creature_text` VALUES (111049, 0, 0, 'Разделимся. Скройтесь в тенях и не останавливайтесь. Если что, прячьтесь в сене.', 12, 0, 100, 1, 0, 69532, 119786, 0, 0, 0, 'Мастер Матиас Шоу to Player');
INSERT INTO `creature_text` VALUES (111065, 1, 0, 'I\'m a little worried.', 12, 0, 100, 0, 0, 68258, 119584, 0, 0, 0, 'Смотритель зверинца to Су\'эш');
