INSERT INTO `creature_text` VALUES (111826, 4, 0, 'Спасибо за незабываемое приключение. Если захочешь еще, ты знаешь, где меня найти.', 12, 0, 100, 1, 0, 0, 119512, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111826, 5, 0, 'Спасибо, Ашильдир. Истинно так.', 12, 0, 100, 1, 0, 0, 120125, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111826, 6, 0, 'Мы с Талреем продумали детальный план штурма Цитадели Ночи.', 12, 0, 100, 1, 0, 0, 119484, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111826, 7, 0, 'Встретимся на месте. Я позабочусь о Сердце. Лунные стражи зачистят крепость к нашему приходу.', 12, 0, 100, 1, 0, 0, 119500, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111826, 8, 0, 'До встречи.', 12, 0, 100, 1, 0, 0, 120092, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111826, 9, 0, 'Должен быть способ защитить Сердце от гибельной магии Легиона. Кажется, у меня есть идея.', 12, 0, 100, 1, 0, 0, 119296, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (111863, 0, 0, 'What is this place?', 12, 0, 100, 0, 0, 67381, 101212, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 1, 0, 'This chamber... I have never seen anything like it.', 12, 0, 100, 0, 0, 67383, 117057, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 2, 0, 'So dark...', 12, 0, 100, 0, 0, 67384, 117539, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 3, 0, 'In\'alah!', 12, 0, 100, 463, 0, 67385, 117454, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 4, 0, 'Much better.', 12, 0, 100, 0, 0, 67386, 117220, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 5, 0, 'Old teleporters. Not so different from the ones in Suramar city. Seems they are inactive.', 12, 0, 100, 0, 0, 67387, 117064, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 6, 0, 'Let us explore downstairs. Watch your step.', 12, 0, 100, 0, 0, 67388, 117219, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 7, 0, 'Strange. The floor looks like... a map?', 12, 0, 100, 0, 0, 67389, 117221, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 8, 0, 'And we are at the center...', 12, 0, 100, 0, 0, 67390, 117551, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 9, 0, 'One thing is certain - leyline energy is being fed into this chamber from elsewhere.', 12, 0, 100, 0, 0, 67391, 117222, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 10, 0, 'What could have required such power? A weapon, perhaps?', 12, 0, 100, 0, 0, 67392, 117557, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 11, 0, 'And those keepers protecting it... How long their vigil must have been...', 12, 0, 100, 0, 0, 67393, 117558, 0, 0, 0, 'First Arcanist Thalyssra to Player');
INSERT INTO `creature_text` VALUES (111863, 12, 0, '<Sigh.> I have more questions now than before!', 12, 0, 100, 0, 0, 67394, 117559, 0, 0, 0, 'First Arcanist Thalyssra to Player');
