INSERT INTO `creature_text` VALUES (114958, 1, 1, 'Мятежник! Я прикончу тебя!', 12, 0, 100, 0, 0, 79245, 123443, 0, 0, 0, 'Поработительница из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (114958, 1, 2, 'Элисанда похвалит меня за твою смерть!', 12, 0, 100, 0, 0, 79245, 123444, 0, 0, 0, 'Поработительница из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (114958, 3, 0, 'Шал\'дорай… избранные…', 12, 0, 100, 0, 0, 65697, 116787, 0, 0, 0, 'Поработитель из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (114963, 0, 0, 'Отлично. Нас слишком мало, чтобы долго удерживать это место. Чем скорее мы двинемся дальше, тем лучше.', 12, 0, 100, 0, 0, 0, 123560, 0, 0, 0, 'Вериса Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (114978, 0, 0, 'О, спасибо! Этот шедевр в сто раз дороже моей жизни.', 12, 0, 100, 603, 0, 0, 123472, 0, 0, 0, 'Мастер Девлин to Player');
INSERT INTO `creature_text` VALUES (114983, 0, 0, 'Осторожно, сзади!', 12, 0, 100, 0, 0, 0, 124769, 0, 0, 0, 'Торвос to Player');
INSERT INTO `creature_text` VALUES (114983, 1, 0, 'Я за тобой! Вперед!', 12, 0, 100, 603, 0, 0, 124770, 0, 0, 0, 'Торвос to Player');
INSERT INTO `creature_text` VALUES (114984, 3, 0, 'О-отец... Беги...', 12, 0, 100, 396, 0, 0, 123479, 1359, 1359, 0, 'Валант to Player');
INSERT INTO `creature_text` VALUES (114985, 0, 0, '%s плюет на труп демона.', 16, 0, 100, 0, 0, 73427, 123473, 0, 0, 0, 'Скарлет to Player');
INSERT INTO `creature_text` VALUES (114985, 1, 0, 'Я знаю, где Шал\'Аран. Идемте, дамы. В городе теперь опасно.', 12, 0, 100, 396, 0, 0, 123476, 0, 0, 0, 'Скарлет to Player');
INSERT INTO `creature_text` VALUES (114998, 0, 0, 'Каждый час прибывают новые пленники! Освободите место!', 14, 0, 100, 0, 0, 78452, 123543, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 2, 0, 'Души не должны пропадать! Гоните их в портал!', 14, 0, 100, 0, 0, 0, 123544, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 4, 0, 'Реактор душ не должен простаивать!', 14, 0, 100, 0, 0, 0, 123542, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 5, 0, 'Следите за цепями!', 14, 0, 100, 0, 0, 0, 123540, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 7, 0, 'Ха! Смертным все не терпится умереть?', 12, 0, 100, 0, 0, 78444, 124519, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 8, 0, 'I will powder your miserable soul!', 12, 0, 100, 0, 0, 78445, 124520, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 9, 0, 'Шаза-киэл!', 12, 0, 100, 0, 0, 78446, 124521, 0, 0, 0, 'Ааргосс to Player');
INSERT INTO `creature_text` VALUES (114998, 10, 0, 'Ты – ничто! Жалкая букашка!', 12, 0, 100, 0, 0, 78447, 124522, 0, 0, 0, 'Ааргосс to 0');
INSERT INTO `creature_text` VALUES (114998, 11, 0, 'Нет... только не... реактор...', 12, 0, 100, 0, 0, 53329, 124523, 0, 0, 0, 'Ааргосс to Player');
INSERT INTO `creature_text` VALUES (115014, 0, 0, 'Свежее мяско!', 12, 0, 100, 0, 0, 53329, 96658, 0, 0, 0, 'Бес из армии Оскверненной души to Player');
INSERT INTO `creature_text` VALUES (115014, 1, 0, 'Поджарю с корочкой!', 12, 0, 100, 0, 0, 53329, 102137, 0, 0, 0, 'Бес из армии Оскверненной души to Player');
INSERT INTO `creature_text` VALUES (115018, 0, 0, 'Надеюсь, она увидит...', 12, 0, 100, 0, 0, 78550, 124501, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115018, 1, 0, 'Живо, садитесь в гондолу!', 12, 0, 100, 0, 0, 78551, 124502, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115018, 2, 0, 'Ты как всегда вовремя, Делина.', 12, 0, 100, 0, 0, 78552, 124503, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115021, 0, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Жуткий мародер to Player');
INSERT INTO `creature_text` VALUES (115021, 0, 1, 'Было ваше, стало наше! Хе-хе-хе!', 12, 0, 100, 0, 0, 53432, 137510, 0, 0, 0, 'Жуткий мародер to Player');
INSERT INTO `creature_text` VALUES (115021, 1, 1, 'Будьте вы прокляты!', 12, 0, 100, 0, 0, 53433, 137507, 0, 0, 0, 'Жуткий мародер to Player');
INSERT INTO `creature_text` VALUES (115022, 0, 0, 'Shut your jabber, drama man! The Monkey King got another plan!', 14, 0, 100, 396, 0, 77472, 123050, 0, 0, 0, 'Король обезьян to Барнс');
INSERT INTO `creature_text` VALUES (115022, 1, 0, 'Tonight we got a horkin\' show, an ookin\' tale you think you know.', 14, 0, 100, 396, 0, 77473, 123051, 0, 0, 0, 'Король обезьян to Барнс');
INSERT INTO `creature_text` VALUES (115022, 2, 0, 'That evil Crone? She\'s not the worst. Another witch was nasty first.', 14, 0, 100, 396, 0, 77474, 123052, 0, 0, 0, 'Король обезьян to Барнс');
INSERT INTO `creature_text` VALUES (115022, 3, 0, 'Mean dooker locked up all us hozen! Another path she should have chosen.', 14, 0, 100, 396, 0, 77475, 123502, 0, 0, 0, 'Король обезьян to Барнс');
INSERT INTO `creature_text` VALUES (115022, 4, 0, 'So sit and slerp, relax a jibbet! Enjoy the show that we call... Wikket!', 14, 0, 100, 396, 0, 77476, 123503, 0, 0, 0, 'Король обезьян to Барнс');
INSERT INTO `creature_text` VALUES (115031, 0, 0, 'Ты останешься здесь навечно!', 12, 0, 100, 0, 0, 52490, 98097, 0, 0, 0, 'Проклятый такелажник to Player');
INSERT INTO `creature_text` VALUES (115031, 1, 0, 'Умри в агонии!', 12, 0, 100, 0, 0, 52496, 98103, 0, 0, 0, 'Проклятый такелажник to Player');
INSERT INTO `creature_text` VALUES (115031, 2, 0, 'Тебе не победить!', 12, 0, 100, 0, 0, 51703, 94060, 0, 0, 0, 'Проклятый такелажник to Player');
INSERT INTO `creature_text` VALUES (115038, 0, 0, 'Странный холод возвещает о темном присутствии...', 16, 0, 100, 0, 0, 0, 125232, 0, 0, 0, 'Проекция Медива to Player');
INSERT INTO `creature_text` VALUES (115038, 1, 0, 'Ты сам хотел меня видеть, дракон. Меня не так легко запугать, как местных крестьян.', 14, 0, 100, 0, 0, 77686, 14295, 0, 0, 0, 'Проекция Медива to Player');
INSERT INTO `creature_text` VALUES (115038, 2, 0, 'Ты бросаешь мне вызов в моем же доме? Твоя заносчивость поразительна даже для дракона!', 14, 0, 100, 0, 0, 77687, 14297, 0, 0, 0, 'Проекция Медива to Player');
INSERT INTO `creature_text` VALUES (115038, 3, 0, 'Не знаю, о чем ты говоришь, дракон... Но меня ты своей дерзостью не запугаешь. Я покину Каражан тогда, когда сочту нужным!', 14, 0, 100, 0, 0, 77689, 14299, 0, 0, 0, 'Проекция Медива to Player');
INSERT INTO `creature_text` VALUES (115038, 4, 0, '%s begins to cast a spell огромной силы, вплетая в магию собственную сущность.', 16, 0, 100, 0, 0, 0, 14308, 0, 0, 0, 'Проекция Медива to Проекция Арканагоса');
INSERT INTO `creature_text` VALUES (115049, 3, 0, 'Делина, помоги беженцам покинуть город. А ты, $n, следуй за мной.', 12, 0, 100, 0, 0, 78557, 123713, 44283, 44283, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115058, 0, 0, 'Н-е-ет!', 14, 0, 100, 0, 0, 0, 128399, 0, 0, 0, 'Джонни Потрясный to Джонни Потрясный');
INSERT INTO `creature_text` VALUES (115067, 0, 0, 'Ты права. Забирайтесь в гондолу! Придется потесниться.', 12, 0, 100, 1, 0, 0, 124004, 0, 0, 0, 'Виктория to player ');
INSERT INTO `creature_text` VALUES (115078, 0, 0, 'Все будет хорошо. Аркан\'дор спасет его.', 12, 0, 100, 0, 0, 78582, 124539, 0, 0, 0, 'Арлуэль to Player');
INSERT INTO `creature_text` VALUES (115079, 0, 0, 'Сюда!', 14, 0, 100, 0, 0, 78625, 143887, 0, 0, 0, 'Виктория to Player');
INSERT INTO `creature_text` VALUES (115116, 3, 0, 'Получи!', 12, 0, 100, 0, 0, 0, 123575, 16152, 16152, 0, 'Делина to Иссыхающая горожанка');
INSERT INTO `creature_text` VALUES (115116, 3, 0, 'Пора выйти из тени! Чародейское вино всем – это цель нашего восстания!', 12, 0, 100, 0, 0, 0, 123625, 71601, 71601, 0, 'Делина');
INSERT INTO `creature_text` VALUES (115125, 3, 0, 'I am saved ... Thank you!', 12, 0, 100, 0, 0, 0, 123577, 13848, 13848, 0, 'Иссыхающая горожанка to Делина');
INSERT INTO `creature_text` VALUES (115125, 3, 0, 'I owe you.', 12, 0, 100, 0, 0, 0, 138328, 14695, 14695, 0, 'Иссыхающий горожанин to Делина');
INSERT INTO `creature_text` VALUES (115125, 4, 0, 'Пожалуйста, поделитесь чародейским вином, хоть немножко... У меня дети!', 12, 0, 100, 18, 0, 0, 123571, 7873, 7873, 0, 'Иссыхающий горожанин');
INSERT INTO `creature_text` VALUES (115132, 1, 0, 'Ты останешься здесь навечно!', 12, 0, 100, 0, 0, 77480, 98097, 0, 0, 0, 'Хеларьяр-ритуалист to Player');
INSERT INTO `creature_text` VALUES (115132, 2, 0, 'Я возвращаюсь в туманы...', 12, 0, 100, 0, 0, 52499, 98105, 0, 0, 0, 'Хеларьяр-ритуалист to Player');
INSERT INTO `creature_text` VALUES (115133, 1, 0, 'Ваши души теперь мои!', 14, 0, 100, 0, 0, 51703, 123587, 0, 0, 0, 'Видение Хелии to Хеларьяр-ритуалист');
INSERT INTO `creature_text` VALUES (115133, 4, 0, 'Даже не надейтесь, смертные! Один НИКОГДА не обретет свободу!', 14, 0, 100, 0, 0, 52492, 123609, 75773, 75773, 0, 'Видение Хелии to Хеларьяр-ритуалист');
INSERT INTO `creature_text` VALUES (115146, 3, 0, 'ВИХРЬ КОСТЕЙ!', 14, 0, 100, 0, 0, 77520, 123653, 240196, 360160, 0, 'Костенок');
INSERT INTO `creature_text` VALUES (115172, 2, 0, 'Господин! Я... подвел тебя.', 12, 0, 100, 0, 0, 0, 123856, 0, 0, 0, 'Тар\'зул to Player');
INSERT INTO `creature_text` VALUES (115172, 3, 0, 'Ты опаздываешь, насекомое!', 14, 0, 100, 396, 0, 0, 123730, 7640, 7640, 0, 'Тар\'зул to Player');
INSERT INTO `creature_text` VALUES (115179, 1, 0, 'Твоя жалкая маскировка бесполезна! Прощайся с жизнью!', 14, 0, 100, 15, 0, 77966, 123897, 0, 0, 0, 'Элукс\'ара Насмешка Тьмы to Player');
INSERT INTO `creature_text` VALUES (115179, 2, 0, 'Слишком... поздно...', 14, 0, 100, 0, 0, 77967, 123670, 0, 0, 0, 'Элукс\'ара Насмешка Тьмы to Player');
INSERT INTO `creature_text` VALUES (115213, 3, 0, 'Темные силы хотят использовать тебя, Медив! Останешься – наступят ужасные дни. Время на исходе!', 14, 0, 100, 0, 0, 77690, 14298, 59563, 59563, 0, 'Проекция Арканагоса to Player');
INSERT INTO `creature_text` VALUES (115213, 3, 0, 'Ты не оставляешь мне выбора. Раз не хочешь слушать слов – остановлю тебя силой!', 14, 0, 100, 0, 0, 77691, 14300, 82666, 82666, 0, 'Проекция Арканагоса to Player');
INSERT INTO `creature_text` VALUES (115216, 1, 0, 'Жалкое отребье!', 12, 0, 100, 0, 0, 65904, 116699, 0, 0, 0, 'Ветеран Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115216, 2, 0, 'Бесполезно!', 12, 0, 100, 0, 0, 65898, 116793, 0, 0, 0, 'Ветеран Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115216, 2, 0, 'Тор\'терас фалар!', 12, 0, 100, 0, 0, 65901, 116790, 0, 0, 0, 'Ветеран Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115242, 0, 0, 'А ну, за работу, огр! За что мы тебе платим?', 14, 0, 100, 0, 0, 0, 124775, 0, 0, 0, 'Старший канонир Прикс to 0');
INSERT INTO `creature_text` VALUES (115242, 0, 1, 'Вы море-то видали, крысы сухопутные?!', 14, 0, 100, 22, 0, 0, 124776, 0, 0, 0, 'Старший канонир Прикс to 0');
INSERT INTO `creature_text` VALUES (115242, 1, 0, 'Этот болван послал тебя за своим порохом! Он проиграл его в честной игре!', 12, 0, 100, 0, 0, 0, 123622, 0, 0, 0, 'Старший канонир Прикс to Player');
INSERT INTO `creature_text` VALUES (115242, 2, 0, 'Надеюсь, тебе руки оторвет...', 12, 0, 100, 0, 0, 0, 125940, 0, 0, 0, 'Старший канонир Прикс to Player');
INSERT INTO `creature_text` VALUES (115242, 3, 0, 'Хватит без дела слоняться, не то ты у меня по доске прогуляешься!', 14, 0, 100, 22, 0, 0, 124774, 0, 0, 0, 'Старший канонир Прикс to 0');
INSERT INTO `creature_text` VALUES (115243, 0, 0, 'Важные новости, Талисра.', 12, 0, 100, 0, 0, 78611, 124022, 0, 0, 0, 'Проекция Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115243, 1, 0, 'Воспользуйся порталом в мое поместье. Я хочу сообщить их лично.', 12, 0, 100, 0, 0, 78612, 124023, 0, 0, 0, 'Проекция Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115244, 0, 0, 'Слишком поздно, |3-6($c)! С помощью Радужного Средоточия я дополню свою несравненную мощь чародейской энергией всех силовых линий Азерота!', 14, 0, 100, 0, 0, 81686, 125520, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 1, 0, 'Без магии ваш мир склонится перед моими повелителями... а моя сила станет безграничной!', 14, 0, 100, 0, 0, 81687, 125521, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 2, 0, 'Ксилем активирует Радужное Средоточие!', 41, 0, 100, 0, 0, 0, 125003, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 3, 0, 'Отведай моей магии!', 14, 0, 100, 0, 0, 81688, 125522, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 4, 0, 'Нет... так не должно быть!', 14, 0, 100, 0, 0, 0, 125524, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 5, 0, 'I will not surrender to the power of Darkness!', 14, 0, 100, 0, 0, 0, 125924, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 6, 0, 'Нужно вернуть энергию силовым линиям!', 12, 0, 100, 0, 0, 0, 125525, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 7, 0, 'Защищай меня – во имя Азерота!', 14, 0, 100, 0, 0, 0, 125527, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 8, 0, 'Через Радужное Средоточие я вернул энергию силовым линиям Азерота.', 12, 0, 100, 0, 0, 0, 125957, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115244, 9, 0, 'I will open a portal to my tower. I think we have something to talk about ...', 12, 0, 100, 0, 0, 0, 125532, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115245, 1, 0, 'Приветствую. Готовьтесь к ритмичной заварушке.', 12, 0, 100, 0, 0, 0, 125849, 0, 0, 0, 'БИ7Б-0КС to Player');
INSERT INTO `creature_text` VALUES (115245, 4, 0, 'Твой уровень крутизны недостаточен.', 12, 0, 100, 0, 0, 34755, 125861, 9811, 9811, 0, 'БИ7Б-0КС');
INSERT INTO `creature_text` VALUES (115247, 0, 0, 'С возвращением, первая чародейка. Спасибо, что прибыла так скоро.', 12, 0, 100, 0, 0, 78614, 124055, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115247, 1, 0, 'О нашем друге Вантире ходят жуткие слухи.', 12, 0, 100, 0, 0, 78616, 124063, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115247, 2, 0, 'Его примером хотят запугать остальных.', 12, 0, 100, 0, 0, 78617, 124535, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115247, 3, 0, 'Не знаю. Надеюсь, еще не поздно.', 12, 0, 100, 0, 0, 78619, 124537, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115247, 4, 0, 'Береги себя, Талисра. Мне пора во дворец.', 12, 0, 100, 0, 0, 78406, 124075, 0, 0, 0, 'Ли\'лет Лунарх to Player');
INSERT INTO `creature_text` VALUES (115248, 0, 0, 'Благодарю, советница. Итак?', 12, 0, 100, 0, 0, 78615, 124056, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115248, 1, 0, 'Сколько у нас времени?', 12, 0, 100, 0, 0, 78618, 124536, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115248, 2, 0, 'Тише, тише, Силгрин. Я тоже рада тебя видеть.', 12, 0, 100, 0, 0, 78408, 124164, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115248, 3, 0, 'Очень эффективная.', 12, 0, 100, 0, 0, 78525, 124167, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115249, 1, 0, 'Ваше время прошло!', 12, 0, 100, 0, 0, 65799, 116705, 0, 0, 0, 'Ревизор из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115252, 0, 0, 'Смерть чужакам!', 12, 0, 100, 0, 0, 65909, 116708, 0, 0, 0, 'Кулак Сумеречной стражи to Рыцарь крови из Рассветной стражи');
INSERT INTO `creature_text` VALUES (115252, 0, 1, 'I will crush you, worm!', 12, 0, 100, 0, 0, 65708, 116706, 0, 0, 0, 'Кулак Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115252, 0, 5, 'Все покорятся нам!', 12, 0, 100, 0, 0, 65909, 116700, 0, 0, 0, 'Кулак Сумеречной стражи to Ночнорожденная-мятежница');
INSERT INTO `creature_text` VALUES (115252, 0, 6, 'Анат\'ашар!', 12, 0, 100, 0, 0, 65909, 116709, 0, 0, 0, 'Кулак Сумеречной стражи to Ночнорожденная-мятежница');
INSERT INTO `creature_text` VALUES (115252, 0, 7, 'Пришло время шал\'дорай!', 12, 0, 100, 0, 0, 65707, 116705, 0, 0, 0, 'Кулак Сумеречной стражи to Дарнасский часовой');
INSERT INTO `creature_text` VALUES (115252, 1, 0, 'Шал\'дорай будут править миром!', 12, 0, 100, 0, 0, 65698, 116792, 0, 0, 0, 'Кулак Сумеречной стражи to Рыцарь крови из Рассветной стражи');
INSERT INTO `creature_text` VALUES (115252, 1, 1, 'Шал\'дорай… избранные…', 12, 0, 100, 0, 0, 65697, 116787, 0, 0, 0, 'Кулак Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115253, 0, 0, 'Мы… выстоим…', 12, 0, 100, 0, 0, 65650, 116785, 0, 0, 0, 'Боевой маг из Сумеречной Стражи to Первая чародейка Талисра');
INSERT INTO `creature_text` VALUES (115253, 1, 0, 'Смерть чужакам!', 12, 0, 100, 0, 0, 65909, 116708, 0, 0, 0, 'Кулак Сумеречной стражи to Рыцарь крови из Рассветной стражи');
INSERT INTO `creature_text` VALUES (115254, 0, 0, 'Не-е-ет!', 14, 0, 100, 0, 0, 0, 123493, 0, 0, 0, 'Закаленный бурей валарьяр to Player');
INSERT INTO `creature_text` VALUES (115263, 0, 0, 'I hear Helia\'s call!', 14, 0, 100, 0, 0, 0, 123491, 0, 0, 0, 'Падший закаленный бурей воин to Player');
INSERT INTO `creature_text` VALUES (115264, 0, 0, 'Ты $gготов:готова;, $n? Кое-кто из твоих союзников не справляется.', 12, 0, 100, 25, 0, 0, 129252, 0, 0, 0, 'Илнея Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (115264, 3, 0, 'Небо становится все темнее, $n. Надеюсь, у тебя все готово к тому, что должно произойти.', 12, 0, 100, 25, 0, 0, 129247, 31492, 31492, 0, 'Илнея Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (115264, 3, 0, 'Подготовка – основа всего, $n. Все мое может стать твоим – за плату, разумеется.', 12, 0, 100, 25, 0, 0, 129254, 25241789, 25241789, 0, 'Илнея Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (115271, 0, 0, 'Начинаю процедуру самоликвидации.', 12, 0, 100, 0, 0, 0, 115099, 0, 0, 0, 'Древний страж щита to 0');
INSERT INTO `creature_text` VALUES (115273, 1, 0, 'Шал\'дорай победят!', 12, 0, 100, 0, 0, 65801, 116707, 0, 0, 0, 'Присягнувший Скверне колдун to Player');
INSERT INTO `creature_text` VALUES (115273, 3, 0, 'Кто ты?', 12, 0, 100, 0, 0, 66005, 118520, 59417, 59417, 0, 'Присягнувшая Скверне колдунья to Player');
INSERT INTO `creature_text` VALUES (115273, 3, 0, 'Это иллюзия! Что ты скрываешь?', 12, 0, 100, 0, 0, 65819, 124629, 11062, 11062, 0, 'Присягнувший Скверне колдун to Player');
INSERT INTO `creature_text` VALUES (115277, 0, 0, 'Я найду его. Надеюсь, меня не обнаружат.', 12, 0, 100, 0, 0, 78416, 123637, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 1, 0, 'В этом саду он явно провел немало приятных часов. Здесь его сущность светла и сильна.', 12, 0, 100, 0, 0, 78411, 123700, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 2, 0, 'Я его чувствую! Он точно еще жив.', 12, 0, 100, 0, 0, 78412, 123701, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 3, 0, 'Похоже, здесь он не раз обедал...', 12, 0, 100, 0, 0, 78409, 123698, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 4, 0, 'Готово. Вантир, мы найдем тебя!', 12, 0, 100, 0, 0, 78410, 123699, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 5, 0, 'Наверное, здесь он встречался с одним из поставщиков. Славное было время...', 12, 0, 100, 0, 0, 78413, 123702, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115277, 6, 0, 'Отлично. Этого достаточно.', 12, 0, 100, 0, 0, 65979, 123703, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115278, 1, 0, 'Stop bothering everyone!', 12, 0, 100, 0, 0, 77635, 123682, 0, 0, 0, 'Неупокоенный дворецкий');
INSERT INTO `creature_text` VALUES (115278, 4, 0, 'Прикажу уборщикам прибрать этот... бардак.', 12, 0, 100, 0, 0, 77632, 123685, 3351, 3351, 0, 'Неупокоенный дворецкий to Пожиратель из бездны');
INSERT INTO `creature_text` VALUES (115279, 0, 0, 'Я верю в Сумеречную Лилию.', 12, 0, 100, 0, 0, 0, 123647, 0, 0, 0, 'Мятежник-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (115279, 1, 0, 'Мы не будем служить Легиону.', 12, 0, 100, 0, 0, 0, 123652, 0, 0, 0, 'Мятежник-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (115279, 2, 0, 'Я должен скрыться, пока не пришли стражники.', 12, 0, 100, 0, 0, 0, 123649, 0, 0, 0, 'Мятежник-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (115279, 5, 0, 'Это безумие!', 12, 0, 100, 0, 0, 0, 123651, 0, 0, 0, 'Мятежница-шал\'дорай to Player');
INSERT INTO `creature_text` VALUES (115291, 0, 0, 'Я искупаюсь в твоей крови!', 12, 0, 100, 0, 0, 51704, 102890, 0, 0, 0, 'Хеларьяр - похититель душ to Player');
INSERT INTO `creature_text` VALUES (115291, 0, 1, 'I will present your corpse to Helia!', 12, 0, 100, 0, 0, 52489, 98099, 0, 0, 0, 'Хеларьяр - похититель душ to Player');
INSERT INTO `creature_text` VALUES (115326, 0, 0, 'Присоединись к син\'дорай, и вместе мы сметем авангард.', 14, 0, 100, 0, 0, 78237, 123740, 0, 0, 0, 'Леди Лиадрин to Player');
INSERT INTO `creature_text` VALUES (115338, 0, 0, 'Сумеречная стража и опомниться не успеет!', 12, 0, 100, 5, 0, 0, 123884, 0, 0, 0, 'Горожанка-мятежница to Player');
INSERT INTO `creature_text` VALUES (115338, 0, 2, 'Я на стороне первой чародейки!', 12, 0, 100, 0, 0, 0, 124027, 0, 0, 0, 'Горожанка-мятежница to Сурамарский смотритель');
INSERT INTO `creature_text` VALUES (115338, 0, 3, 'Магистру и ее головорезам несдобровать!', 12, 0, 100, 0, 0, 0, 125761, 0, 0, 0, 'Горожанка-мятежница to 0');
INSERT INTO `creature_text` VALUES (115338, 0, 5, 'За Талисру! За мятеж!', 12, 0, 100, 0, 0, 0, 124026, 0, 0, 0, 'Горожанин-мятежник to Смотритель из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115338, 0, 6, 'Силгрин передает привет.', 12, 0, 100, 0, 0, 0, 124019, 0, 0, 0, 'Горожанка-мятежница to Смотритель из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115342, 0, 0, 'Портал сработал, и нас не разорвало на части. Хорошо, а то я боялся, что еще один маяк перегрузит сеть.', 12, 0, 100, 0, 0, 78232, 123883, 0, 0, 0, 'Главный телемант Окулет to Player');
INSERT INTO `creature_text` VALUES (115366, 0, 0, 'Лучшая колдунья Элисанды мертва, так что подкреплений можно не опасаться. Следуйте за мной.', 12, 0, 100, 1, 0, 78378, 123851, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115366, 1, 0, 'I feel the Night Well. It seems that Elisanda was completely desperate if she overloaded him so much!', 12, 0, 100, 1, 0, 78379, 123852, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115367, 0, 0, 'Освободим союзников из ловушки времени, и Гул\'дан падет!', 12, 0, 100, 1, 0, 78380, 123853, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (115367, 1, 0, 'От иссохших в Цитадели будет немного пользы, $n. Собери лучших героев – и прорывайтесь с боем. Думаю, такая тактика тебе знакома! Мы пойдем следом.', 12, 0, 100, 1, 0, 78381, 123854, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (115371, 3, 0, 'Погоди-ка... Что...', 12, 0, 100, 0, 0, 78521, 124292, 14435, 14435, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (115372, 0, 0, 'Если что-то пойдет не так, иссохшие помогут. Удачи, $n.', 12, 0, 100, 1, 0, 78385, 124498, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115394, 0, 0, 'Осторожнее там. Ошибки вредны для здоровья – причем в буквальном смысле!', 12, 0, 100, 1, 0, 81376, 124787, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 1, 0, 'Может, хватит?! Ну сколько можно?', 12, 0, 100, 603, 0, 81372, 124783, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 2, 0, 'Постой-ка... ты же не из моих учеников! Бейте самозванца!', 14, 0, 100, 25, 0, 81709, 124788, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 3, 0, 'Студиозусы... в атаку!', 14, 0, 100, 22, 0, 81678, 124789, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 4, 0, 'Средоточие сделает меня сильнейшим магом всех времен! Попробуй меня остановить!', 14, 0, 100, 0, 0, 81679, 124798, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 5, 0, 'Вижу, сил тебе не занимать – но не пытайся меня остановить!', 12, 0, 100, 0, 0, 81382, 124799, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115394, 6, 0, 'Не догонишь! Студиозус Санат, защищай портал любой ценой!', 14, 0, 100, 0, 0, 81680, 124800, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (115426, 0, 0, 'Too... much... power...', 12, 0, 100, 0, 0, 0, 123913, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (115426, 1, 0, 'Father! No!', 12, 0, 100, 0, 0, 0, 123914, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (115427, 0, 0, 'Enough of this weakness, boy! Contain your magic!', 14, 0, 100, 0, 0, 0, 123912, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (115445, 0, 0, 'Наконец-то свобода! Помоги остальным, со мной все будет в порядке...', 12, 0, 100, 1, 0, 0, 123960, 0, 0, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115445, 0, 1, 'Благодарю тебя!', 12, 0, 100, 1, 0, 0, 123969, 0, 0, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115446, 0, 0, 'Благодарю тебя!', 12, 0, 100, 1, 0, 0, 123969, 0, 0, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115447, 0, 0, 'Эта клетка чуть не стала мне гробом...', 12, 0, 100, 1, 0, 0, 123970, 0, 0, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115448, 0, 0, 'Пылающий Легион за это заплатит!', 12, 0, 100, 1, 0, 0, 123971, 0, 0, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115449, 0, 0, 'Где мой топор? Мне пора убивать демонов!', 12, 0, 100, 1, 0, 0, 123972, 0, 0, 0, 'Потерпевший кораблекрушение рубака to Player');
INSERT INTO `creature_text` VALUES (115450, 0, 0, 'Смерть Пылающему Легиону!', 12, 0, 100, 1, 0, 0, 123973, 0, 0, 0, 'Потерпевший кораблекрушение рубака to Player');
INSERT INTO `creature_text` VALUES (115451, 0, 0, 'Я буду сражаться до последнего вздоха!', 12, 0, 100, 1, 0, 0, 123974, 0, 0, 0, 'Потерпевший кораблекрушение рубака to Player');
INSERT INTO `creature_text` VALUES (115452, 0, 0, 'Демоны заплатят!', 12, 0, 100, 1, 0, 0, 123975, 0, 0, 0, 'Потерпевший кораблекрушение рубака to Player');
INSERT INTO `creature_text` VALUES (115453, 3, 0, 'Свобода? Я этого не забуду.', 12, 0, 100, 1, 0, 0, 123976, 1422, 1422, 0, 'Потерпевшая кораблекрушение рубака to Player');
INSERT INTO `creature_text` VALUES (115454, 3, 0, 'I owe you my life!', 12, 0, 100, 1, 0, 0, 123978, 1172, 1172, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115455, 3, 0, 'Как приятно размять ноги! Ну, где тут демоны? Хочу теперь руки размять!', 12, 0, 100, 1, 0, 0, 123977, 1250, 1250, 0, 'Потерпевший кораблекрушение солдат to Player');
INSERT INTO `creature_text` VALUES (115465, 3, 0, 'Отлично! Никто лучше тебя не справится с этой задачей. Мы расскажем обо всех известных нам угрозах.', 12, 0, 100, 1, 0, 81394, 126526, 4739, 4739, 0, 'Верховный маг Модера to Player');
INSERT INTO `creature_text` VALUES (115488, 0, 0, 'Бомба Скверны, которую бросает %s, взрывается!', 16, 0, 100, 0, 0, 78200, 128332, 0, 0, 0, 'Наполненный силой пиромант to Наполненный силой пиромант');
INSERT INTO `creature_text` VALUES (115489, 3, 0, 'Ничего нельзя было изменить. Мы пытались, но все это было лишь частью большого плана.', 12, 0, 100, 1, 0, 78200, 124016, 10390, 10390, 0, 'Андуин Лотар to Player');
INSERT INTO `creature_text` VALUES (115491, 3, 0, 'Теперь ты сможешь поддерживать в башне порядок в мое отсутствие.', 12, 0, 100, 1, 0, 78197, 124000, 14559, 14559, 0, 'Медив to Player');
INSERT INTO `creature_text` VALUES (115491, 3, 0, 'Хм... Надо будет отключать тебя время от времени, чтобы не повредить матрицу личности.', 12, 0, 100, 25, 0, 78198, 124002, 56172, 56172, 0, 'Медив to Смотритель');
INSERT INTO `creature_text` VALUES (115494, 3, 0, 'А ну бейтесь, хорош прятаться!', 12, 0, 100, 1, 0, 78039, 124392, 42088, 42088, 0, 'Король Молгар');
INSERT INTO `creature_text` VALUES (115494, 3, 0, 'Больше костей для арены!', 12, 0, 100, 1, 0, 78034, 124388, 88031, 88031, 0, 'Король Молгар to Player');
INSERT INTO `creature_text` VALUES (115494, 3, 0, 'Да начнутся... ИГРЫ!', 12, 0, 100, 1, 0, 78044, 124398, 44000, 44000, 0, 'Король Молгар');
INSERT INTO `creature_text` VALUES (115494, 3, 0, 'Прикончи ее!', 12, 0, 100, 1, 0, 78042, 124395, 99891, 99891, 0, 'Король Молгар to Player');
INSERT INTO `creature_text` VALUES (115496, 0, 0, 'Думаю, нам стоит разделиться. Я попробую обойти магическую защиту башни, а вам предстоит более... грубый подход.', 12, 0, 100, 1, 0, 78192, 123095, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (115496, 1, 0, 'Скажу банальность, но будьте начеку. Я ощущаю присутствие чего-то... из давнего прошлого...', 12, 0, 100, 25, 0, 78190, 124010, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (115497, 0, 0, 'The demons have been purged from Karazhan! Thank you, champions. Medivh, you would be a welcome ally in our war against the Legion.', 12, 0, 100, 0, 0, 0, 124029, 0, 0, 0, 'Khadgar');
INSERT INTO `creature_text` VALUES (115497, 1, 0, 'I have made it clear before... I do not want such power!', 12, 0, 100, 0, 0, 0, 124034, 0, 0, 0, 'Khadgar');
INSERT INTO `creature_text` VALUES (115497, 2, 0, 'I... I don\'t know what to say, Medivh.', 12, 0, 100, 0, 0, 0, 124036, 0, 0, 0, 'Khadgar');
INSERT INTO `creature_text` VALUES (115497, 3, 0, 'The bonds of fate... I must consider those words. Until next time, champions.', 12, 0, 100, 0, 0, 0, 124048, 0, 0, 0, 'Khadgar');
INSERT INTO `creature_text` VALUES (115499, 0, 0, 'Идем. Нужно остановить властителя преисподней, пока он не нашел Шал\'Аран.', 12, 0, 100, 0, 0, 0, 125014, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115499, 1, 0, 'Следи за дыханием. При выпаде делай резкий выдох.', 12, 0, 100, 0, 0, 78435, 124665, 0, 0, 0, 'Силгрин to Арлуэль');
INSERT INTO `creature_text` VALUES (115499, 2, 0, 'Расслабь плечи. Ты сама сковываешь свои движения.', 12, 0, 100, 0, 0, 78434, 124664, 0, 0, 0, 'Силгрин to Арлуэль');
INSERT INTO `creature_text` VALUES (115499, 3, 0, 'Ноги нужно ставить чуть шире, так легче сохранять равновесие.', 12, 0, 100, 0, 0, 78433, 124656, 0, 0, 0, 'Силгрин to Арлуэль');
INSERT INTO `creature_text` VALUES (115499, 4, 0, 'Расслабь руку – не нужно так стискивать рукоять.', 12, 0, 100, 0, 0, 78436, 124666, 0, 0, 0, 'Силгрин to Арлуэль');
INSERT INTO `creature_text` VALUES (115501, 3, 0, 'Все есть часть великого цикла. Но теперь, наконец, его можно разорвать.', 12, 0, 100, 1, 0, 78183, 124017, 20406, 20406, 0, 'Верховный маг Кадгар to Андуин Лотар');
INSERT INTO `creature_text` VALUES (115502, 0, 0, 'Я на стороне первой чародейки!', 12, 0, 100, 0, 0, 0, 124027, 0, 0, 0, 'Ночнорожденный-мятежник to Player');
INSERT INTO `creature_text` VALUES (115502, 2, 0, 'Силгрин передает привет.', 12, 0, 100, 0, 0, 52234, 124019, 0, 0, 0, 'Ночнорожденная-мятежница to Player');
INSERT INTO `creature_text` VALUES (115507, 0, 0, 'Я на стороне первой чародейки!', 12, 0, 100, 0, 0, 0, 124027, 0, 0, 0, 'Помраченный-мятежник to Player');
INSERT INTO `creature_text` VALUES (115507, 1, 0, 'I want to learn from you.', 12, 0, 100, 0, 0, 52234, 124020, 0, 0, 0, 'Помраченная-мятежница to Player');
INSERT INTO `creature_text` VALUES (115517, 0, 0, 'О, подкрепление! Сюда, мне многое нужно вам рассказать.', 12, 0, 100, 0, 0, 0, 124156, 0, 0, 0, 'Маг Сендат to Player');
INSERT INTO `creature_text` VALUES (115518, 0, 0, 'Мои тайны... умрут со мной...', 12, 0, 100, 0, 0, 82995, 124090, 0, 0, 0, 'Главный механолог Найелл to Player');
INSERT INTO `creature_text` VALUES (115519, 0, 0, 'Мы… избранные…', 12, 0, 100, 0, 0, 0, 116777, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (115521, 0, 0, 'Хоть ты из другого лагеря, рада встрече. Идем быстрее, нужно многое обсудить!', 12, 0, 100, 0, 0, 0, 124157, 0, 0, 0, 'Часовой Лунная Тень to Player');
INSERT INTO `creature_text` VALUES (115524, 0, 0, 'Сюда! Я чувствую, как внизу концентрируется энергия, но стражи не дадут мне пройти. Расчисти путь.', 12, 0, 100, 1, 0, 78577, 124159, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (115524, 1, 0, 'Мощно! Давай-ка посмотрим поближе... Барьер и правда впечатляющий.', 12, 0, 100, 0, 0, 78574, 124173, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (115524, 2, 0, 'Может, мана-бур? Нет, не годится – он требует слишком много энергии...', 12, 0, 100, 0, 0, 78575, 124176, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (115524, 3, 0, 'Он подключен не к Ночному Колодцу, а к силовым линиям. Но они наши – так как же она... А, ну конечно, я поняла!', 12, 0, 100, 0, 0, 78576, 124178, 0, 0, 0, 'Чародейка Вальтруа to Player');
INSERT INTO `creature_text` VALUES (115526, 0, 0, 'Немыслимо! Что ты делаешь?', 14, 0, 100, 0, 0, 65817, 124549, 0, 0, 0, 'Ваятель чар Торендис to Player');
INSERT INTO `creature_text` VALUES (115526, 1, 0, 'Моя оборона... была безупречна...', 12, 0, 100, 0, 0, 78485, 124626, 0, 0, 0, 'Ваятель чар Торендис to Player');
INSERT INTO `creature_text` VALUES (115541, 0, 0, 'Хм, это яйцо зашевелилось?', 16, 0, 100, 0, 0, 0, 124079, 0, 0, 0, 'Блеклоперое яйцо to Player');
INSERT INTO `creature_text` VALUES (115541, 1, 0, 'Похоже, яйцо остыло. Держитесь поближе к нему, чтобы согреть его.', 16, 0, 100, 0, 0, 0, 124080, 0, 0, 0, 'Блеклоперое яйцо to Player');
INSERT INTO `creature_text` VALUES (115541, 2, 0, 'Вы слышите поскребывание внутри яйца.', 16, 0, 100, 0, 0, 0, 124081, 0, 0, 0, 'Блеклоперое яйцо to Player');
INSERT INTO `creature_text` VALUES (115541, 3, 0, 'Гиппогриф пытается выбраться из скорлупы.', 16, 0, 100, 0, 0, 0, 124082, 0, 0, 0, 'Блеклоперое яйцо to Player');
INSERT INTO `creature_text` VALUES (115541, 4, 0, 'Время пришло! Детеныш вылупляется из яйца!', 16, 0, 100, 0, 0, 0, 124083, 0, 0, 0, 'Блеклоперое яйцо to Player');
INSERT INTO `creature_text` VALUES (115550, 2, 0, 'Араш-фолас…', 12, 0, 100, 0, 0, 65657, 116791, 0, 0, 0, 'Мародер из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115550, 3, 0, 'Мы приближаемся. Я чую их страх!', 12, 0, 100, 0, 0, 0, 124152, 117594, 117594, 0, 'Мародер из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115557, 0, 0, 'Не молчи, Вантир...', 12, 0, 100, 0, 0, 78526, 124129, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 1, 0, 'Когда его забрали из \"Лунного полумрака\", он ужасно боялся... но не за себя.', 12, 0, 100, 0, 0, 78527, 124130, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 2, 0, 'А за тех, кого спрятал... ради кого пожертвовал собой.', 12, 0, 100, 0, 0, 78528, 124131, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 3, 0, 'Его бросили в мрачное уединенное место. Со временем в нем поселился голод.', 12, 0, 100, 0, 0, 78529, 124132, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 4, 0, 'Он становился все сильнее...', 12, 0, 100, 0, 0, 78530, 124133, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 5, 0, '...пока не вытеснил другие чувства!', 12, 0, 100, 0, 0, 78531, 124134, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 6, 0, 'Он почти иссох. Его мысли лихорадочны, спутаны... Еще чуть-чуть...', 12, 0, 100, 0, 0, 78532, 124135, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 7, 0, 'Есть! Нашла!', 12, 0, 100, 0, 0, 78533, 124136, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 8, 0, 'Терраса порядка! Видимо, его уже выставили на обозрение...', 12, 0, 100, 0, 0, 78534, 124137, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 9, 0, 'Заклинание отняло больше сил, чем я думала. Вам придется обойтись без меня. Простите.', 12, 0, 100, 0, 0, 78580, 124154, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115557, 10, 0, 'Спасибо, Силгрин. Береги себя.', 12, 0, 100, 0, 0, 78581, 124162, 0, 0, 0, 'Первая чародейка Талисра to Player');
INSERT INTO `creature_text` VALUES (115561, 1, 0, 'Все покорятся нам!', 12, 0, 100, 0, 0, 65908, 116700, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115561, 1, 0, 'Смерть чужакам!', 12, 0, 100, 0, 0, 65909, 116708, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 1, 0, 'Анат\'ашар!', 12, 0, 100, 0, 0, 65669, 116709, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 1, 0, 'На колени перед нами!', 12, 0, 100, 0, 0, 65664, 116697, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 2, 0, 'Мы… избранные…', 12, 0, 100, 0, 0, 65667, 116777, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 2, 0, 'Нет! Еще не время…', 12, 0, 100, 0, 0, 65654, 116788, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 2, 0, 'Тор\'терас фалар!', 12, 0, 100, 0, 0, 65656, 116790, 0, 0, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115562, 3, 0, 'Тебя ждет смерть!', 12, 0, 100, 0, 0, 65663, 116696, 2129, 2129, 0, 'Клинок Скверны из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115563, 3, 0, 'I am saved!', 12, 0, 100, 0, 0, 0, 124174, 38484, 38484, 0, 'Пойманная помраченная to Клинок Скверны из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115563, 3, 0, 'Быстрее, в развалины!', 12, 0, 100, 0, 0, 0, 124175, 16507, 16507, 0, 'Пойманная помраченная to Клинок Скверны из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115563, 3, 0, 'Чтобы вы иссохли, лиходеи!', 12, 0, 100, 0, 0, 65664, 124177, 8297, 8297, 0, 'Пойманная помраченная to Клинок Скверны из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115591, 3, 0, 'А у тебя талант!', 12, 0, 100, 0, 0, 0, 125852, 46397, 46397, 0, 'Танцовщица из группы поддержки to БИ7Б-0КС');
INSERT INTO `creature_text` VALUES (115594, 0, 0, 'Тебе здесь не место!', 12, 0, 100, 0, 0, 0, 134549, 0, 0, 0, 'Арбитр из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115594, 0, 1, 'Кара за вторжение сюда – смерть!', 12, 0, 100, 0, 0, 0, 124149, 0, 0, 0, 'Арбитр из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115595, 0, 0, 'И снова глупцы вторгаются на запретную землю!', 12, 0, 100, 0, 0, 0, 124150, 0, 0, 0, 'Палач из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (115600, 3, 0, 'I am saved!', 12, 0, 100, 0, 0, 0, 124174, 29008, 29008, 0, 'Перепуганная помраченная to Клинок Скверны из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115600, 3, 0, 'Быстрее, в развалины!', 12, 0, 100, 0, 0, 65664, 124175, 8295, 8295, 0, 'Перепуганный помраченный to Клинок Скверны из Сумеречной стражи');
INSERT INTO `creature_text` VALUES (115603, 0, 0, 'Вы опоздали, смертные! Я уничтожу вас и сожгу этот мир!', 14, 0, 100, 0, 0, 82865, 124267, 0, 0, 0, 'Генерал Зеонар to 0');
INSERT INTO `creature_text` VALUES (115603, 1, 0, 'I\'ll be back...', 12, 0, 100, 0, 0, 82866, 124271, 0, 0, 0, 'Генерал Зеонар to Player');
INSERT INTO `creature_text` VALUES (115604, 0, 0, 'Вантир с трудом поднимается на ноги.', 16, 0, 100, 0, 0, 78582, 125238, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115604, 1, 0, 'Наконец-то...', 12, 0, 100, 0, 0, 78596, 124540, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115604, 2, 0, 'Аран\'этиль... Кошмар закончился!', 12, 0, 100, 0, 0, 78597, 124541, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115604, 3, 0, 'Элисанда хотела запугать нас. Но мы обратили ее план в свою пользу.', 12, 0, 100, 0, 0, 78568, 124543, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115604, 4, 0, 'Лилия... вернет народу надежду.', 12, 0, 100, 0, 0, 78569, 124544, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115604, 5, 0, '<Кашляет.> Мне надо... отдохнуть. Спасибо вам. Спасибо за все.', 12, 0, 100, 0, 0, 78566, 124545, 0, 0, 0, 'Вантир to Player');
INSERT INTO `creature_text` VALUES (115607, 0, 0, 'Хотите помешать представлению? Хорошо! Два примера лучше, чем один.', 12, 0, 100, 0, 0, 78599, 124260, 0, 0, 0, 'Торамир to 0');
INSERT INTO `creature_text` VALUES (115607, 1, 0, 'Она... не ошиблась... в тебе...', 12, 0, 100, 0, 0, 78607, 124269, 0, 0, 0, 'Торамир to Player');
INSERT INTO `creature_text` VALUES (115607, 2, 0, 'Рад тебя видеть, Силгрин. Алуриэль передает привет!', 12, 0, 100, 0, 0, 78601, 124262, 0, 0, 0, 'Торамир to Player');
INSERT INTO `creature_text` VALUES (115607, 3, 0, 'Ты разочаровал ее, воин-чародей. А ведь мог бы занять мое место.', 12, 0, 100, 0, 0, 78602, 124265, 0, 0, 0, 'Торамир to Player');
INSERT INTO `creature_text` VALUES (115607, 4, 0, 'И все же ты обратил оружие против нас. Ты просто предатель!', 12, 0, 100, 0, 0, 78604, 124268, 0, 0, 0, 'Торамир to Player');
INSERT INTO `creature_text` VALUES (115607, 5, 0, 'Смирись, Силгрин. Тебе не выстоять!', 12, 0, 100, 0, 0, 78605, 124263, 0, 0, 0, 'Торамир to Player');
INSERT INTO `creature_text` VALUES (115608, 0, 0, 'Я знаю.', 12, 0, 100, 0, 0, 78608, 124270, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 1, 0, 'Поспешим. Мы должны спасти Вантира.', 12, 0, 100, 0, 0, 78609, 124272, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 2, 0, 'Тихо, Вантир. Мы тебя вытащим.', 12, 0, 100, 0, 0, 78610, 124538, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 3, 0, 'С возвращением, друг.', 12, 0, 100, 0, 0, 78598, 124542, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 4, 0, 'Мы отведем его в убежище. Сообщи Талисре о нашем успехе.', 12, 0, 100, 0, 0, 78567, 124546, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 5, 0, 'Опусти клинок, Торамир!', 12, 0, 100, 0, 0, 78600, 124261, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 6, 0, 'У меня есть совесть. Я служу народу Сурамара.', 12, 0, 100, 0, 0, 78603, 124266, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115608, 7, 0, 'Возможно. Но я не один.', 12, 0, 100, 0, 0, 78606, 124264, 0, 0, 0, 'Силгрин to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 0, 'Поджарю с корочкой!', 12, 0, 100, 0, 0, 0, 102137, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 1, 'А вот и наш обед пожаловал!', 12, 0, 100, 0, 0, 0, 96654, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 2, 'На помощь! Тут плохиши!', 12, 0, 100, 0, 0, 0, 96655, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 3, 'Свежее мяско!', 12, 0, 100, 0, 0, 0, 96658, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 4, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 0, 0, 0, 96660, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 5, 'Хватайте обед!', 12, 0, 100, 0, 0, 0, 96653, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 6, 'Эй! Что ты здесь делаешь?', 12, 0, 100, 0, 0, 0, 96657, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115620, 0, 7, 'Мы пожрем твою душу! И тело тоже.', 12, 0, 100, 0, 0, 0, 96656, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (115631, 0, 0, 'Демоны повсюду...', 12, 0, 100, 0, 0, 0, 124198, 0, 0, 0, 'Отчаявшаяся беженка to Player');
INSERT INTO `creature_text` VALUES (115631, 0, 0, 'Пожалуйста, найди, где мне спрятаться!', 12, 0, 100, 0, 0, 0, 124197, 0, 0, 0, 'Отчаявшаяся беженка to Player');
INSERT INTO `creature_text` VALUES (115631, 0, 0, 'Поспешим! Здесь небезопасно.', 12, 0, 100, 0, 0, 0, 124196, 0, 0, 0, 'Отчаявшаяся беженка to Player');
INSERT INTO `creature_text` VALUES (115631, 3, 0, 'I heard that in the homes of some citizens there are conspirators ...', 12, 0, 100, 0, 0, 0, 124199, 24, 24, 0, 'Отчаявшаяся беженка to Player');
INSERT INTO `creature_text` VALUES (115631, 3, 0, 'Мы... мы чуть не погибли!', 12, 0, 100, 0, 0, 0, 124193, 37339, 37339, 0, 'Отчаявшаяся беженка');
INSERT INTO `creature_text` VALUES (115631, 3, 0, 'Спасибо тебе.', 12, 0, 100, 0, 0, 0, 144752, 6757, 6757, 0, 'Отчаявшаяся беженка');
INSERT INTO `creature_text` VALUES (115631, 3, 0, 'Этот город охвачен безумием...', 12, 0, 100, 0, 0, 0, 124194, 90097, 90097, 0, 'Отчаявшаяся беженка');
INSERT INTO `creature_text` VALUES (115634, 3, 0, 'Используйте |TInterface\\Icons\\SPELL_SHADOW_ENSLAVEDEMON.BLP:20|t |cFFFF0000|Hspell:111771|h[Порабощение демона]|h|r, чтобы подчинить скакуна гнева!', 41, 0, 100, 0, 0, 0, 130095, 360996, 360996, 0, 'Контроллер to Player');
INSERT INTO `creature_text` VALUES (115638, 0, 0, 'Слишком поздно! Сила Левии моя! Ее знания помогут моим слугам проникнуть в ряды Кирин-Тора и уничтожить его изнутри!', 14, 0, 100, 0, 0, 81348, 126884, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 1, 0, 'Мои сайаады уже искушают слабовольных магов! Они сами сдадутся Легиону!', 14, 0, 100, 1, 0, 81349, 126885, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 2, 0, 'Но сперва ты заплатишь за то, что у меня отняли игрушку.', 14, 0, 100, 1, 0, 85036, 126886, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 3, 0, 'В атаку, крошки!', 14, 0, 100, 0, 0, 85037, 130802, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 4, 0, 'Перебейте бесов-прислужников, пока они не усилили Агату своей энергией!', 41, 0, 100, 0, 0, 0, 126793, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 5, 0, 'НЕТ! Что ты творишь?! Ты будешь страдать!', 14, 0, 100, 0, 0, 0, 126815, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 6, 0, 'Нет! Мои крошки!', 14, 0, 100, 0, 0, 0, 126888, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115638, 7, 0, 'Ты... за это... заплатишь...', 14, 0, 100, 0, 0, 0, 126889, 0, 0, 0, 'Агата');
INSERT INTO `creature_text` VALUES (115639, 0, 0, 'Мяу-мяу.', 12, 0, 100, 0, 0, 0, 125145, 0, 0, 0, 'Танцовщица to Player');
INSERT INTO `creature_text` VALUES (115654, 0, 0, 'Ну чо?', 12, 0, 100, 0, 0, 0, 125136, 0, 0, 0, 'Танцор to Player');
INSERT INTO `creature_text` VALUES (115656, 0, 0, 'Будьте вы прокляты!', 12, 0, 100, 0, 0, 53433, 137507, 0, 0, 0, 'Грязный рабочий to Player');
INSERT INTO `creature_text` VALUES (115656, 1, 1, 'Было ваше, стало наше! Хе-хе-хе!', 12, 0, 100, 0, 0, 53432, 137510, 0, 0, 0, 'Грязный рабочий to Player');
INSERT INTO `creature_text` VALUES (115656, 1, 2, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Грязный рабочий to Player');
INSERT INTO `creature_text` VALUES (115658, 0, 0, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 55376, 102157, 0, 0, 0, 'Присягнувший погибели инквизитор to Player');
INSERT INTO `creature_text` VALUES (115658, 0, 1, 'Перед смертью ты выдашь мне все секреты.', 12, 0, 100, 0, 0, 55373, 102154, 0, 0, 0, 'Присягнувший погибели инквизитор to Player');
INSERT INTO `creature_text` VALUES (115662, 0, 0, 'Ну и как тут сохранять оптимизм?', 12, 0, 100, 0, 0, 0, 125139, 0, 0, 0, 'Танцор to Player');
INSERT INTO `creature_text` VALUES (115663, 4, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 3779, 3779, 0, 'Злобоклюв - хранитель гнезда to Молодой злобоклюв');
INSERT INTO `creature_text` VALUES (115664, 4, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 12924, 12924, 0, 'Острокоготь - хранитель гнезда to Гнездо острокогтя');
INSERT INTO `creature_text` VALUES (115665, 0, 0, '%s is trying to escape in a panic!', 16, 0, 100, 0, 0, 0, 1150, 0, 0, 0, 'Снегоспин - хранитель гнезда to 0');
INSERT INTO `creature_text` VALUES (115665, 6, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 0, 0, 0, 'Снегоспин - хранитель гнезда to Гнездо снегоспина');
INSERT INTO `creature_text` VALUES (115666, 0, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 0, 0, 0, 'Кровогляд - хранитель гнезда to Гнездо кровогляда');
INSERT INTO `creature_text` VALUES (115671, 0, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 0, 0, 0, 'Злобоклюв-матриарх to Молодой злобоклюв');
INSERT INTO `creature_text` VALUES (115672, 0, 0, '%s приходит в ярость от гибели детеныша!', 16, 0, 100, 0, 0, 0, 124247, 0, 0, 0, 'Острокоготь-матриарх to Молодой острокоготь');
