INSERT INTO `creature_text` VALUES (116219, 0, 0, 'Всех смертных ждет реактор душ.', 12, 0, 100, 0, 0, 55022, 102016, 0, 0, 0, 'Нечистый хранитель душ to Player');
INSERT INTO `creature_text` VALUES (116219, 0, 1, 'Твоя душа будет моей!', 12, 0, 100, 0, 0, 55013, 97908, 0, 0, 0, 'Нечистый хранитель душ to Player');
INSERT INTO `creature_text` VALUES (116223, 0, 0, 'Мы избраны править миром!', 12, 0, 100, 0, 0, 66025, 116707, 0, 0, 0, 'Капитан Фиора to Player');
INSERT INTO `creature_text` VALUES (116225, 1, 0, 'Анат\'ашар!', 12, 0, 100, 0, 0, 65818, 116709, 0, 0, 0, 'Присягнувший Скверне истязатель to Player');
INSERT INTO `creature_text` VALUES (116256, 0, 0, 'И это, по-твоему, боевая стойка? Любой эльф в этом лагере на кусочки тебя разорвет после такой стойки.', 14, 0, 100, 25, 0, 0, 124012, 0, 0, 0, 'Виктория to 0');
INSERT INTO `creature_text` VALUES (116256, 0, 1, 'Нет-нет-нет. Я сказала, стройтесь. Построиться!', 14, 0, 100, 25, 0, 0, 124011, 0, 0, 0, 'Виктория to 0');
INSERT INTO `creature_text` VALUES (116256, 0, 2, 'Поднимите свое оружие. Поднимайте! Если слишком тяжело, вам нужно стать сильнее.', 14, 0, 100, 25, 0, 0, 124013, 0, 0, 0, 'Виктория to 0');
INSERT INTO `creature_text` VALUES (116256, 0, 3, 'А это еще что? Не делай так!', 14, 0, 100, 25, 0, 0, 124015, 0, 0, 0, 'Виктория to 0');
INSERT INTO `creature_text` VALUES (116256, 0, 4, 'Хватит лениться! Вы – ночнорожденные, и готовитесь защищать свой город и свои традиции. Будьте горды!', 14, 0, 100, 25, 0, 0, 124014, 0, 0, 0, 'Виктория to 0');
INSERT INTO `creature_text` VALUES (116281, 0, 0, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 57005, 105125, 0, 0, 0, 'Присягнувший погибели предвестник to Player');
INSERT INTO `creature_text` VALUES (116281, 1, 0, 'Во имя Саргераса!', 12, 0, 100, 0, 0, 57005, 109464, 0, 0, 0, 'Присягнувший погибели предвестник to Player');
INSERT INTO `creature_text` VALUES (116282, 0, 0, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 56980, 105124, 0, 0, 0, 'Присягнувшая погибели колдунья to Player');
INSERT INTO `creature_text` VALUES (116291, 0, 0, 'Вы все обречены!', 14, 0, 100, 0, 0, 82703, 129026, 0, 0, 0, 'Лорд Калгорат to 0');
INSERT INTO `creature_text` VALUES (116297, 0, 0, 'Глупое смертное существо! Как ты смеешь выпускать наших пленников? В таком случае ты и займешь их место!', 14, 0, 100, 0, 0, 0, 125006, 0, 0, 0, 'Владыка судеб Кро\'нак to 0');
INSERT INTO `creature_text` VALUES (116308, 0, 0, 'Разлом закрывается!', 12, 0, 100, 0, 0, 82251, 125181, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116308, 1, 0, 'Скорее, идем в Клоаку, $n. Надеюсь, еще не слишком поздно.', 12, 0, 100, 0, 0, 82263, 125013, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116308, 3, 0, 'Что происходит?!', 12, 0, 100, 0, 0, 82264, 125106, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116311, 0, 0, 'Никто не помешает возвращению культа Пустоты!', 12, 0, 100, 0, 0, 0, 125009, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116311, 1, 0, 'Тьма... поглотит... все...', 12, 0, 100, 0, 0, 0, 125010, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116321, 0, 0, 'Встретимся в Оплоте Хранителя, верховный маг $n.', 12, 0, 100, 0, 0, 0, 128550, 0, 0, 0, 'Чародейка Рианна to Player');
INSERT INTO `creature_text` VALUES (116322, 0, 0, 'Вы замолчите навеки!', 12, 0, 100, 0, 0, 0, 125074, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116322, 1, 0, 'Мои братья вас прикончат...', 12, 0, 100, 0, 0, 0, 125083, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116322, 2, 0, 'Не суйте носы, куда не следует!', 12, 0, 100, 0, 0, 0, 125070, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116322, 4, 0, 'Уходите... пока не поздно...', 12, 0, 100, 0, 0, 0, 125081, 0, 0, 0, 'Убийца из культа Пустоты to Player');
INSERT INTO `creature_text` VALUES (116323, 0, 0, 'Вы никогда не узнаете наших секретов!', 12, 0, 100, 0, 0, 0, 125016, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116323, 0, 1, 'Оставьте меня в покое!', 12, 0, 100, 5, 0, 0, 125060, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116323, 0, 2, 'Тени поглотят вас!', 12, 0, 100, 0, 0, 0, 125062, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116323, 3, 0, 'Тьма... зовет меня...', 12, 0, 100, 0, 0, 0, 125068, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116323, 4, 0, 'Один из них дал мне этот пергамент. Можете забрать его – я не желаю иметь с их культом никаких дел!', 12, 0, 100, 0, 0, 0, 125017, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116323, 5, 0, 'Я больше ничего не знаю, клянусь!', 12, 0, 100, 0, 0, 0, 125018, 0, 0, 0, 'Отребье из Клоаки to Player');
INSERT INTO `creature_text` VALUES (116351, 0, 0, 'Бедняжка Талисра... Ты так хочешь спасти свой народ, но при этом отрываешь его от того, что поддерживает в нем жизнь! Боюсь, Ли\'лет придется заплатить за твою дерзость!', 12, 0, 100, 1, 0, 78355, 124429, 0, 0, 0, 'Андарис Нарассин to Player');
INSERT INTO `creature_text` VALUES (116351, 1, 0, 'Давай, покажи, чего стоит твой мятеж! Сразись со мной в поместье Ли\'лет. Или я буду вынуждена... допросить еще кое-кого!', 12, 0, 100, 1, 0, 78356, 124430, 0, 0, 0, 'Андарис Нарассин to Player');
INSERT INTO `creature_text` VALUES (116360, 3, 0, 'Что-то тут не так...', 12, 0, 100, 0, 0, 66004, 124630, 5606, 5606, 0, 'Прорицательница из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (116361, 0, 0, 'Твой вердикт ничего не значит! Пылающий Легион уничтожит орден Серебряной Длани!', 14, 0, 100, 1, 0, 0, 125122, 0, 0, 0, 'Волзарак Искуситель to Player');
INSERT INTO `creature_text` VALUES (116361, 1, 0, 'Нас не остановить!', 12, 0, 100, 0, 0, 82688, 129922, 0, 0, 0, 'Волзарак Искуситель to Player');
INSERT INTO `creature_text` VALUES (116362, 0, 0, 'Глупцы... Вы думали, что сможете уйти живыми?', 12, 0, 100, 11, 0, 82854, 125100, 0, 0, 0, 'Эрия Рассветная Погибель to Player');
INSERT INTO `creature_text` VALUES (116362, 1, 0, 'Культ Пустоты не знает пощады!', 12, 0, 100, 53, 0, 82855, 125101, 0, 0, 0, 'Эрия Рассветная Погибель to Player');
INSERT INTO `creature_text` VALUES (116362, 2, 0, 'Повелитель... я подвела тебя...', 12, 0, 100, 0, 0, 82856, 125102, 0, 0, 0, 'Эрия Рассветная Погибель to Player');
INSERT INTO `creature_text` VALUES (116372, 0, 0, 'Clear!', 12, 0, 100, 0, 0, 78428, 124668, 0, 0, 0, 'Арлуэль to 0');
INSERT INTO `creature_text` VALUES (116383, 0, 0, 'Ты не пожалеешь о своем выборе.', 12, 0, 100, 1, 0, 0, 125119, 0, 0, 0, 'Паладин-рекрут to Player');
INSERT INTO `creature_text` VALUES (116384, 0, 0, 'Я к твоим услугам.', 12, 0, 100, 1, 0, 0, 125120, 0, 0, 0, 'Паладин-рекрут to Player');
INSERT INTO `creature_text` VALUES (116385, 0, 0, 'Я к твоим услугам.', 12, 0, 100, 1, 0, 0, 125120, 0, 0, 0, 'Паладин-рекрут to Player');
INSERT INTO `creature_text` VALUES (116386, 0, 0, 'I respect your verdict. One day I will prove myself worthy.', 12, 0, 100, 1, 0, 0, 125121, 0, 0, 0, 'Паладин-рекрут to Player');
INSERT INTO `creature_text` VALUES (116388, 0, 0, 'Ты не пожалеешь о своем выборе.', 12, 0, 100, 1, 0, 0, 125119, 0, 0, 0, 'Паладин-рекрут to Player');
INSERT INTO `creature_text` VALUES (116407, 0, 0, 'Время крушить!', 14, 0, 100, 0, 0, 81947, 128047, 0, 0, 0, 'Харджатан');
INSERT INTO `creature_text` VALUES (116407, 1, 0, 'Ко мне, малыши! Крушить!', 14, 0, 100, 0, 0, 81945, 128045, 0, 0, 0, 'Харджатан');
INSERT INTO `creature_text` VALUES (116407, 2, 0, 'Бал, ламер жита!', 14, 0, 100, 0, 0, 81944, 128044, 0, 0, 0, 'Харджатан');
INSERT INTO `creature_text` VALUES (116407, 3, 0, 'Харджатан... плохо... дрался...', 14, 0, 100, 0, 0, 81949, 128049, 0, 0, 0, 'Харджатан to Player');
INSERT INTO `creature_text` VALUES (116409, 0, 0, 'Зря ты пошел за мной, брат. Круговерть Пустоты наполняет меня своей мощью, и я стал сильнее, чем ты можешь вообразить!', 14, 0, 100, 0, 0, 82344, 126332, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 1, 0, 'Убить меня? Вряд ли, брат. Но твой клинок сможет послужить мне!', 14, 0, 100, 0, 0, 82345, 126334, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 2, 0, 'Убей незваного гостя, брат!', 14, 0, 100, 0, 0, 82346, 126336, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 3, 0, 'Опять мне приходится все делать за тебя, брат!', 14, 0, 100, 0, 0, 0, 126850, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 4, 0, 'Ты бесполезен, брат! Постой в стороне – а я все сделаю сам!', 14, 0, 100, 0, 0, 0, 126849, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 5, 0, 'Am I ... defeated ?! It\'s impossible...', 12, 0, 100, 0, 0, 0, 126450, 0, 0, 0, 'Рейст Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116409, 6, 0, '|TINTERFACE\\ICONS\\Inv_misc_shadowegg.BLP:20|t %s |cFFFF0404|Hspell:235317|h[Открывает]|h|r разломы пустоты! ', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Инквизитор Варисс');
INSERT INTO `creature_text` VALUES (116410, 0, 0, 'I won’t let you bring this power to Azeroth, Raist. Stop, or I\'ll have to kill you!', 14, 0, 100, 0, 0, 82269, 126333, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116410, 1, 0, 'Что? Что ты делаешь? Не-е-ет!', 14, 0, 100, 0, 0, 82270, 126335, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116410, 2, 0, 'Повинуюсь...', 12, 0, 100, 0, 0, 82271, 126337, 0, 0, 0, 'Карам Волшебное Копье to Player');
INSERT INTO `creature_text` VALUES (116410, 3, 0, '|TINTERFACE\\ICONS\\Ability_fixated_state_red.BLP:20|t %s вновь преследует вас! ', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Инквизитор Варисс');
INSERT INTO `creature_text` VALUES (116421, 0, 0, 'Твоя судьба предрешена!', 12, 0, 100, 0, 0, 66021, 116703, 0, 0, 0, 'Чародейка силовых линий to Player');
INSERT INTO `creature_text` VALUES (116421, 0, 1, 'Эй, ты! Что ты делаешь?', 12, 0, 100, 0, 0, 0, 125202, 0, 0, 0, 'Чародей силовых линий to Player');
INSERT INTO `creature_text` VALUES (116424, 0, 0, 'Чувствуете, смертные? Ваш мир содрогается от мощи Саргераса!', 12, 0, 100, 396, 0, 0, 129199, 0, 0, 0, 'Изверг Легиона to Player');
INSERT INTO `creature_text` VALUES (116427, 0, 0, 'Am I finally free?', 12, 0, 100, 6, 0, 0, 125295, 0, 0, 0, 'Порабощенный Скверной дух to Player');
INSERT INTO `creature_text` VALUES (116427, 0, 1, 'Твоя доброта не будет забыта.', 12, 0, 100, 1, 0, 0, 125297, 0, 0, 0, 'Порабощенный Скверной дух to Player');
INSERT INTO `creature_text` VALUES (116427, 0, 2, 'Спасибо тебе, |3-6($c).', 12, 0, 100, 1, 0, 0, 125294, 0, 0, 0, 'Порабощенный Скверной дух to Player');
INSERT INTO `creature_text` VALUES (116428, 0, 0, 'Я живу, чтобы служить.', 12, 0, 100, 0, 0, 55363, 120868, 0, 0, 0, 'Воитель пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116428, 0, 1, 'Жизнь за Легион.', 12, 0, 100, 0, 0, 55364, 98760, 0, 0, 0, 'Воитель пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116428, 0, 3, 'Сейчас ты умрешь.', 12, 0, 100, 0, 0, 55362, 98765, 0, 0, 0, 'Воитель пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116428, 0, 4, 'Мы очистим вселенную огнем!', 12, 0, 100, 0, 0, 55370, 98766, 0, 0, 0, 'Воитель пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116429, 0, 0, 'Было ваше, стало наше! Хе-хе-хе!', 12, 0, 100, 0, 0, 53432, 137510, 0, 0, 0, 'Гнусный расхититель to Player');
INSERT INTO `creature_text` VALUES (116429, 1, 0, 'ЭЙ! Эй! $gНашел:Нашла; что-нибудь хорошее?!', 12, 0, 100, 1, 0, 0, 100523, 0, 0, 0, 'Гнусный расхититель to 0');
INSERT INTO `creature_text` VALUES (116433, 0, 0, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 56980, 105124, 0, 0, 0, 'Маг пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116433, 0, 1, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 56982, 129829, 0, 0, 0, 'Маг пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116436, 0, 0, 'Как глупо! Теперь тебе не избежать смерти!', 12, 0, 100, 0, 0, 0, 125226, 0, 0, 0, 'Аринор - мастер порталов to 0');
INSERT INTO `creature_text` VALUES (116441, 0, 0, 'Добро пожаловать в Око Вечности, $p. Надеюсь, ты справишься с задачей.', 12, 0, 100, 1, 0, 81307, 126076, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 1, 0, 'Антонидас насытил фрагменты диска той самой энергией, которой мы, маги, пытаемся управлять. Но ему и его коллегам так и не удалось рассеять избыточную энергию.', 12, 0, 100, 1, 0, 81308, 125824, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 2, 0, 'Сейчас мы снимем с фрагментов защитные чары. Думаю, если направить на них энергию твоего артефакта, это вызовет ответную реакцию.', 12, 0, 100, 1, 0, 81309, 125826, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 3, 0, 'При необходимости мои ученики снабдят тебя припасами. Рекомендую воспользоваться их помощью.', 12, 0, 100, 1, 0, 81310, 125828, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 4, 0, 'Удачи тебе, $p. Да, имей в виду: если ты покинешь Око Вечности, фрагменты вернутся в исходное состояние.', 12, 0, 100, 1, 0, 81311, 125235, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 5, 0, 'Можешь приступать. Я буду наблюдать за тобой сверху.', 12, 0, 100, 1, 0, 81312, 125236, 0, 0, 0, 'Верховный маг Кейлек to 0');
INSERT INTO `creature_text` VALUES (116441, 6, 0, 'Таймеры восстановления способностей и отрицательные эффекты обнулены.', 41, 0, 100, 0, 0, 0, 126533, 0, 0, 0, 'Верховный маг Кейлек to Player');
INSERT INTO `creature_text` VALUES (116441, 10, 0, 'I was not closely acquainted with Antonidas, but I am sure that he would be proud of you. Great job, Archmage $p.', 12, 0, 100, 1, 0, 81316, 126152, 0, 0, 0, 'Верховный маг Кейлек to Player');
INSERT INTO `creature_text` VALUES (116441, 11, 0, 'Если хочешь, я перенесу тебя в Оплот Хранителя.', 12, 0, 100, 1, 0, 81317, 126153, 0, 0, 0, 'Верховный маг Кейлек to Player');
INSERT INTO `creature_text` VALUES (116448, 3, 0, 'Вот гады... На корабль!', 12, 0, 100, 0, 0, 82831, 126978, 2115, 2115, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (116466, 0, 0, 'Am I finally free?', 12, 0, 100, 6, 0, 0, 125295, 0, 0, 0, 'Порабощенный Скверной дух to Player');
