INSERT INTO `creature_text` VALUES (116478, 0, 0, 'Клянусь, ты покоришься Пылающему Легиону!', 12, 0, 100, 0, 0, 83268, 129812, 0, 0, 0, 'Хранитель клятв Зорак to Player');
INSERT INTO `creature_text` VALUES (116478, 1, 0, 'Они присягнули мне по своей воле!', 14, 0, 100, 0, 0, 83270, 129814, 0, 0, 0, 'Хранитель клятв Зорак to Player');
INSERT INTO `creature_text` VALUES (116478, 2, 0, 'Это... невозможно...', 12, 0, 100, 0, 0, 83269, 133175, 0, 0, 0, 'Хранитель клятв Зорак to Player');
INSERT INTO `creature_text` VALUES (116479, 1, 0, 'Ваш мир будет очищен!', 12, 0, 100, 0, 0, 0, 129826, 0, 0, 0, 'Эредарская надзирательница to Player');
INSERT INTO `creature_text` VALUES (116479, 1, 0, 'Легион завершит начатое.', 12, 0, 100, 0, 0, 0, 129830, 0, 0, 0, 'Эредарский надзиратель to Player');
INSERT INTO `creature_text` VALUES (116479, 1, 0, 'Трепещи перед моим могуществом!', 12, 0, 100, 0, 0, 0, 129828, 0, 0, 0, 'Эредарский надзиратель to Player');
INSERT INTO `creature_text` VALUES (116479, 3, 0, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 0, 129829, 70304, 70304, 0, 'Эредарская надзирательница to Player');
INSERT INTO `creature_text` VALUES (116484, 1, 0, 'Значит, ты выбираешь смерть!', 14, 0, 100, 0, 0, 82898, 125737, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116484, 3, 0, 'Предлагаю тебе выбор: уходи или умри!', 14, 0, 100, 0, 0, 82897, 125736, 53492, 53492, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116484, 3, 0, 'Что?! Ты хочешь меня остановить? Я многим тебе обязана, но даже ты не помешаешь мне свершить возмездие!', 14, 0, 100, 1, 0, 82896, 125735, 25313, 25313, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116484, 4, 0, '|TInterface\\Icons\\Spell_Shadow_Metamorphosis:20|t%s |cFFFF0000|Hspell:237937|h[усиливает]|h|r |3-3($n)!', 16, 0, 100, 0, 0, 82626, 129123, 25210, 25210, 0, 'Сигрин to Ярл Вельбранд');
INSERT INTO `creature_text` VALUES (116484, 4, 0, 'Отправляйся в Хельхейм!', 14, 0, 100, 53, 0, 82913, 128563, 47484, 47484, 0, 'Сигрин');
INSERT INTO `creature_text` VALUES (116484, 5, 0, 'Что же я делаю? Это неправильно!', 12, 0, 100, 1, 0, 0, 125738, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116484, 6, 0, 'Нет! Я не пойду по стопам отца... Я выстою!', 12, 0, 100, 1, 0, 0, 125742, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116484, 7, 0, 'Великая Эйир! Я смиренно прошу прощения за содеянное.', 12, 0, 100, 1, 0, 0, 125744, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116496, 4, 0, '|TInterface\\Icons\\ABILITY_WARLOCK_SHADOWFURYTGA.BLP:20|tНа земле появляется несколько |cFFFF0000|Hspell:237908|h[рун]|h|r.', 41, 0, 100, 0, 0, 0, 128432, 40187, 40187, 0, 'Руновидец Фальяр');
INSERT INTO `creature_text` VALUES (116497, 0, 0, 'Сестра... Ты помнишь, как бросила меня на верную смерть?', 12, 0, 100, 0, 0, 82274, 125700, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 1, 0, 'Как? Я принял дар, которым наградил нас отец! Дар, которым вы с матерью побрезговали!', 12, 0, 100, 1, 0, 82275, 125702, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 2, 0, 'Вижу, ты забыла, как отец приучал нас к Скверне. Недаром тебя постоянно от нее тошнило.', 12, 0, 100, 1, 0, 82276, 125704, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 3, 0, 'В нашей крови есть нечто, что делает нас стойкими к Скверне... удерживает ее внутри. Прими этот дар!', 12, 0, 100, 25, 0, 82277, 125705, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 4, 0, 'Эх, сестра... Я не хотел, чтобы мы становились врагами…', 14, 0, 100, 0, 0, 82278, 125707, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 5, 0, 'Сдавайся! Тебе не одолеть меня без помощи Скверны!', 12, 0, 100, 0, 0, 82279, 125708, 0, 0, 0, 'Торвальд to 0');
INSERT INTO `creature_text` VALUES (116497, 6, 0, 'Это... невозможно...', 12, 0, 100, 0, 0, 82280, 133175, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 7, 0, 'Думаешь, твои повелители лучше моих?..', 12, 0, 100, 0, 0, 82281, 125712, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 8, 0, 'Спроси у них, кто подослал к нам убийц той ночью...', 12, 0, 100, 0, 0, 82282, 125713, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116497, 9, 0, 'Может... тогда ты прозреешь...', 12, 0, 100, 0, 0, 82283, 125714, 0, 0, 0, 'Торвальд to Player');
INSERT INTO `creature_text` VALUES (116499, 4, 0, 'Дрекирьяры не сдаются!', 14, 0, 100, 0, 0, 82626, 128427, 26187, 26187, 0, 'Ярл Вельбранд to Сигрин');
INSERT INTO `creature_text` VALUES (116521, 0, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 56983, 105127, 0, 0, 0, 'Осквернительница пламени ужаса to Player');
INSERT INTO `creature_text` VALUES (116548, 0, 0, 'Ты погибнешь от моего клинка!', 12, 0, 100, 0, 0, 55195, 102130, 0, 0, 0, 'Страж Скверны - мародер to Player');
INSERT INTO `creature_text` VALUES (116548, 0, 1, 'За Легион!', 12, 0, 100, 0, 0, 55194, 135850, 0, 0, 0, 'Страж Скверны - мародер to Player');
INSERT INTO `creature_text` VALUES (116549, 0, 0, 'Жутковато!', 12, 0, 100, 0, 0, 0, 125342, 0, 0, 0, 'Бэк-вокалистка to 0');
INSERT INTO `creature_text` VALUES (116549, 1, 0, 'О нет. Тяжелые времена...', 12, 0, 100, 0, 0, 0, 125303, 0, 0, 0, 'Бэк-вокалистка to Player');
INSERT INTO `creature_text` VALUES (116550, 0, 0, 'I will miss the show!', 12, 0, 100, 0, 0, 0, 13918, 0, 0, 0, 'Призрачный зритель to Player');
INSERT INTO `creature_text` VALUES (116552, 0, 0, 'Жизнь за Легион.', 12, 0, 100, 0, 0, 55364, 98760, 0, 0, 0, 'Гневный боец to Player');
INSERT INTO `creature_text` VALUES (116552, 0, 1, 'Мой клинок пронзит тебя.', 12, 0, 100, 0, 0, 55149, 98763, 0, 0, 0, 'Гневный боец to Пеплоуст-воин');
INSERT INTO `creature_text` VALUES (116554, 0, 0, 'Все покорятся нам!', 12, 0, 100, 0, 0, 65908, 116700, 0, 0, 0, 'Часовой из Сумеречной Стражи to Player');
INSERT INTO `creature_text` VALUES (116554, 1, 0, 'Не может быть...', 12, 0, 100, 0, 0, 65903, 121822, 0, 0, 0, 'Часовой из Сумеречной Стражи to Player');
INSERT INTO `creature_text` VALUES (116555, 0, 0, 'Мы проиграли…', 12, 0, 100, 0, 0, 65810, 116789, 0, 0, 0, 'Яростный чародей из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (116555, 1, 0, 'Наша магия сокрушит тебя!', 12, 0, 100, 0, 0, 66022, 116704, 0, 0, 0, 'Яростный чародей из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (116556, 0, 0, 'I thought I was finished. Thanks!', 12, 0, 100, 0, 0, 0, 125329, 0, 0, 0, 'Иллидари-разведчица to Player');
INSERT INTO `creature_text` VALUES (116556, 0, 1, 'Ты как раз вовремя!', 12, 0, 100, 0, 0, 0, 125299, 0, 0, 0, 'Иллидари-разведчица to Player');
INSERT INTO `creature_text` VALUES (116556, 0, 2, 'Они все лезут и лезут...', 12, 0, 100, 0, 0, 0, 125331, 0, 0, 0, 'Иллидари-разведчица to Player');
INSERT INTO `creature_text` VALUES (116556, 0, 3, 'Мне уж показалось, что все пропало. Спасибо, $n!', 12, 0, 100, 0, 0, 0, 125330, 0, 0, 0, 'Иллидари-разведчица to Player');
INSERT INTO `creature_text` VALUES (116560, 3, 0, 'Они все лезут и лезут...', 12, 0, 100, 0, 0, 0, 125331, 228207, 228207, 0, 'Иллидари-разведчица to Player');
INSERT INTO `creature_text` VALUES (116568, 0, 0, 'Великая Эйир, я исполнила твою волю и одолела всех претендентов на трон Волнскорна.', 12, 0, 100, 1, 0, 82887, 125718, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 1, 0, 'Но прежде чем стать королевой, я бы хотела задать тебе один вопрос.', 12, 0, 100, 1, 0, 82888, 125719, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 2, 0, 'Скажи... кто приказал убить мою мать?', 12, 0, 100, 1, 0, 82889, 125721, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 3, 0, 'Да.', 12, 0, 100, 0, 0, 82890, 125723, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 4, 0, 'Что?!', 14, 0, 100, 0, 0, 82891, 125725, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 5, 0, 'Вот как?! Прав был брат – стражи титанов ничем не лучше демонов Легиона!', 14, 0, 100, 0, 0, 82892, 125728, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 6, 0, 'I will show Odin what true rage is! I will destroy the Halls of Valor with him!', 14, 0, 100, 0, 0, 82893, 125730, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 7, 0, 'I will avenge my family!', 14, 0, 100, 463, 0, 82894, 125731, 0, 0, 0, 'Сигрин to Сигрин');
INSERT INTO `creature_text` VALUES (116568, 8, 0, 'Так ты не выйдешь на честный бой? Священные кости наших предков не потерпят такого неуважения!', 14, 0, 100, 1, 0, 82935, 127736, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 9, 0, 'Довольно болтовни, Фальяр!', 12, 0, 100, 0, 0, 82875, 125688, 0, 0, 0, 'Сигрин to Руновидец Фальяр');
INSERT INTO `creature_text` VALUES (116568, 10, 0, 'Ты их недостоин, и я сокрушу тебя! Трусость будет наказана!', 14, 0, 100, 0, 0, 82936, 127737, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 12, 0, 'Теперь мой путь лежит в Хридшал. Увидимся там, |3-6($c).', 12, 0, 100, 0, 0, 82876, 125691, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 13, 0, 'Вельбранд рассчитывал, что его слуги меня остановят.', 12, 0, 100, 0, 0, 82923, 125879, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 14, 0, 'Идем, бросим вызов самому ярлу!', 12, 0, 100, 0, 0, 82924, 125880, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 15, 0, 'Jarl Velbrand! In the name of Eyir, I, the heiress of Skovald, declare my right to the throne of Volnskorn!', 14, 0, 100, 0, 0, 82877, 125692, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 16, 0, 'Служи мне!', 14, 0, 100, 0, 0, 82878, 125693, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 17, 0, 'Не будем терять время! Врект ждет!', 12, 0, 100, 0, 0, 82879, 125699, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 18, 0, 'Торвальд! Я не хотела оставлять тебя, но и не могла защитить! Как ты справился с убийцами?', 12, 0, 100, 1, 0, 82880, 125701, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 19, 0, 'О нет, Торвальд, ты поддался Скверне! Как ты мог?!', 12, 0, 100, 1, 0, 82881, 125703, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 20, 0, 'Нет! Я не буду служить Легиону!', 12, 0, 100, 1, 0, 82882, 125706, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 21, 0, 'Прости, брат, но я не позволю Легиону погубить врайкулов.', 12, 0, 100, 1, 0, 82884, 125711, 0, 0, 0, 'Сигрин to Торвальд');
INSERT INTO `creature_text` VALUES (116568, 22, 0, 'Торвальд...', 12, 0, 100, 1, 0, 82885, 125715, 0, 0, 0, 'Сигрин to Торвальд');
INSERT INTO `creature_text` VALUES (116568, 23, 0, 'Он... мертв...', 12, 0, 100, 0, 0, 82886, 125716, 0, 0, 0, 'Сигрин to Торвальд');
INSERT INTO `creature_text` VALUES (116568, 32, 0, 'Свитки говорят, что моя судьба – объединить Волнскорн...', 12, 0, 100, 0, 0, 82870, 125676, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 33, 0, 'Значит, таково мое предназначение. Я исполню свой долг и послужу Волнскорну.', 12, 0, 100, 0, 0, 82871, 125681, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 34, 0, 'Скажи, что мне нужно сделать! Я готова.', 12, 0, 100, 0, 0, 82872, 125682, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116568, 35, 0, 'Фальяр! Я Сигрин, кровь от крови Сковальда. Именем валькир вызываю тебя на бой за право властвовать над Волнскорном!', 12, 0, 100, 0, 0, 82874, 125684, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (116580, 0, 0, 'Сейчас я с тобой разберусь.', 12, 0, 100, 0, 0, 55196, 102131, 0, 0, 0, 'Страж Скверны - дозорный to Player');
INSERT INTO `creature_text` VALUES (116580, 0, 1, 'Пылающий Легион поглотит все.', 12, 0, 100, 0, 0, 55198, 102133, 0, 0, 0, 'Страж Скверны - дозорный to Player');
INSERT INTO `creature_text` VALUES (116580, 1, 0, 'Как вы посмели напасть на Мардум?', 12, 0, 100, 0, 0, 55191, 96662, 0, 0, 0, 'Страж Скверны - дозорный to Player');
INSERT INTO `creature_text` VALUES (116600, 1, 0, 'Твои кости украсят меня!', 12, 0, 100, 0, 0, 52497, 98102, 0, 0, 0, 'Проклятый морской волк to Player');
INSERT INTO `creature_text` VALUES (116600, 1, 0, 'Туманы заберут тебя!', 12, 0, 100, 0, 0, 52495, 98104, 0, 0, 0, 'Проклятый морской волк to Player');
INSERT INTO `creature_text` VALUES (116607, 0, 0, 'Мрлг! Мррррглгл грл мррргргллл!', 12, 0, 100, 0, 0, 0, 125401, 0, 0, 0, 'Мурчаль');
INSERT INTO `creature_text` VALUES (116624, 1, 0, 'Ваша хваленая природа – ничто против Скверны!', 12, 0, 100, 0, 0, 82733, 125817, 0, 0, 0, 'Мать Миренда to Player');
INSERT INTO `creature_text` VALUES (116624, 2, 0, 'Глупцы! Отвлекающий маневр удался!', 12, 0, 100, 0, 0, 82732, 128575, 0, 0, 0, 'Мать Миренда to Player');
INSERT INTO `creature_text` VALUES (116632, 4, 0, 'Твоя сила несравненна.', 12, 0, 100, 0, 0, 0, 113046, 10270, 43906, 0, 'Иллидарская карательница to Гневный боец');
INSERT INTO `creature_text` VALUES (116638, 4, 0, 'Верховная мать Злоба гневно смотрит на Кор\'вас.', 16, 0, 100, 0, 0, 0, 125404, 27935, 27935, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (116645, 0, 0, 'Тяжко пришлось... Спасибо тебе за помощь! Теперь надо спасать Джейса! Он в зале управления!', 12, 0, 100, 0, 0, 0, 125410, 0, 0, 0, 'Провидец Акалу to 975');
INSERT INTO `creature_text` VALUES (116654, 0, 0, 'Но я... должен был победить...', 12, 0, 100, 0, 0, 60137, 111067, 0, 0, 0, 'Лорд Ар\'дранат to Кор\'вас Кровавый Шип');
INSERT INTO `creature_text` VALUES (116675, 3, 0, 'Заблудившийся шерстоперый котенок замечает вас и в страхе убегает прочь.', 16, 0, 100, 0, 0, 0, 110347, 89, 89, 0, 'Непослушный шерстоперый котенок to Player');
INSERT INTO `creature_text` VALUES (116689, 0, 0, 'Посмотрим, сколько вы выдержите!', 14, 0, 100, 0, 0, 81827, 128765, 0, 0, 0, 'Атриган');
INSERT INTO `creature_text` VALUES (116689, 1, 0, 'Вопите от боли!', 14, 0, 100, 0, 0, 81825, 128762, 0, 0, 0, 'Атриган');
INSERT INTO `creature_text` VALUES (116689, 1, 1, 'I will cut you into pieces!', 14, 0, 100, 0, 0, 81824, 128761, 0, 0, 0, 'Атриган');
INSERT INTO `creature_text` VALUES (116691, 0, 0, 'I will break your mind!', 14, 0, 100, 0, 0, 81731, 128780, 0, 0, 0, 'Белак');
INSERT INTO `creature_text` VALUES (116691, 1, 0, 'Сопротивление принесет лишь боль!', 14, 0, 100, 0, 0, 81730, 128778, 0, 0, 0, 'Белак');
INSERT INTO `creature_text` VALUES (116692, 0, 0, 'Идите ко мне... несите свою скорбь!', 14, 0, 100, 0, 0, 0, 125514, 0, 0, 0, 'Древний Змей to Player');
INSERT INTO `creature_text` VALUES (116702, 0, 0, 'Роланд Абернати сухо кашляет.', 16, 0, 100, 0, 0, 82563, 125383, 0, 0, 0, 'Роланд Абернати to Player');
INSERT INTO `creature_text` VALUES (116704, 1, 0, 'Укажи мне цель.', 12, 0, 100, 0, 0, 55289, 100225, 0, 0, 0, 'Кор\'вас Кровавый Шип');
INSERT INTO `creature_text` VALUES (116704, 4, 0, 'Ха! Кровь льется рекой.', 12, 0, 100, 0, 0, 55288, 100222, 11443, 11443, 0, 'Кор\'вас Кровавый Шип');
INSERT INTO `creature_text` VALUES (116705, 1, 0, 'Смерть будет быстрой.', 12, 0, 100, 0, 0, 80978, 127408, 0, 0, 0, 'Леди С\'тэно');
INSERT INTO `creature_text` VALUES (116705, 4, 0, 'Твоя смерть порадует владыку Иллидана!', 12, 0, 100, 0, 0, 55288, 127407, 11444, 11444, 0, 'Леди С\'тэно');
INSERT INTO `creature_text` VALUES (116714, 0, 0, 'Что она натворила?! Портал перегрузился и соединился с Пустотой!', 12, 0, 100, 5, 0, 82550, 126619, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 1, 0, 'I will try to stabilize it! Hold back the demons!', 12, 0, 100, 0, 0, 82551, 126620, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 2, 0, 'Ладно, попробую стабилизировать портал. Надеюсь, ты знаешь, что делаешь, |3-6($c)!', 12, 0, 100, 0, 0, 82554, 126748, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 3, 0, 'Стой... что-то не так! Не могу... сдержать...', 12, 0, 100, 0, 0, 82557, 126753, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 4, 0, 'Ох!', 12, 0, 100, 0, 0, 82558, 126754, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 12, 0, 'Кажется, руны сработали! Энергия Скверны угасает...', 12, 0, 100, 0, 0, 82555, 126749, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 13, 0, 'Готово.', 12, 0, 100, 0, 0, 82556, 126750, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116714, 14, 0, 'Фамбар взял след. Ступай за ним в портал, и он приведет тебя к пропавшей чародейке.', 12, 0, 100, 25, 0, 82549, 126787, 0, 0, 0, 'Ритцун Хмурый Огонь to Player');
INSERT INTO `creature_text` VALUES (116721, 0, 0, 'I will destroy you!', 12, 0, 100, 0, 0, 55334, 127505, 0, 0, 0, 'Гор\'лок Мясоруб to Player');
INSERT INTO `creature_text` VALUES (116721, 0, 1, 'I am too tough for you!', 12, 0, 100, 0, 0, 55333, 102135, 0, 0, 0, 'Гор\'лок Мясоруб to Player');
INSERT INTO `creature_text` VALUES (116721, 0, 3, 'Смерть уже заждалась.', 12, 0, 100, 0, 0, 55331, 127315, 0, 0, 0, 'Гор\'лок Мясоруб to Player');
INSERT INTO `creature_text` VALUES (116727, 0, 0, 'Верховный маг, у меня есть сферы, которые могут тебе пригодиться.', 12, 0, 100, 0, 0, 0, 125875, 0, 0, 0, 'Ученица Алена to Player');
INSERT INTO `creature_text` VALUES (116733, 1, 0, '', 12, 0, 100, 6, 0, 78643, 124690, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (116734, 1, 0, '', 12, 0, 100, 603, 0, 78642, 124689, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (116736, 2, 0, '', 12, 0, 100, 1, 0, 78645, 124698, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (116737, 0, 0, 'Хочешь бросить меня тут замерзать до смерти? Ничего не выйдет. Идем!', 12, 0, 100, 0, 0, 81293, 125883, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 0, 1, 'Собираешься покорять вершину без меня? Ну уж нет!', 12, 0, 100, 0, 0, 81037, 125405, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 1, 0, 'Да... Сладкий запах смерти.', 12, 0, 100, 0, 0, 81036, 126414, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 1, 1, 'Мы проводим тебя в посмертие!', 12, 0, 100, 53, 0, 81296, 125881, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 1, 2, 'Время умирать!', 12, 0, 100, 0, 0, 81037, 126415, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 2, 0, 'Я возьму огонь. Долго же я ждала этого момента!', 12, 0, 100, 0, 0, 81298, 128129, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116737, 3, 0, 'Подождем. Славная будет битва!', 12, 0, 100, 16, 0, 81295, 127392, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (116743, 0, 0, 'Сейчас оторвемся!', 14, 0, 100, 0, 0, 34341, 125516, 0, 0, 0, 'Повелитель теней Аамин to Player');
INSERT INTO `creature_text` VALUES (116743, 1, 0, 'Сейчас вы увидите, насколько у меня взрывной темперамент!', 14, 0, 100, 0, 0, 0, 125515, 0, 0, 0, 'Повелитель теней Аамин to Player');
INSERT INTO `creature_text` VALUES (116743, 8, 0, 'Они всегда так быстро взрываются.', 12, 0, 100, 0, 0, 34755, 125545, 0, 0, 0, 'Повелитель теней Аамин to Player');
INSERT INTO `creature_text` VALUES (116765, 4, 0, 'Познайте гнев природы, демоны!', 12, 0, 100, 0, 0, 52153, 97828, 35766, 35766, 0, 'Сторожевое древо');
INSERT INTO `creature_text` VALUES (116784, 1, 0, 'Мы очистим ваш мир!', 12, 0, 100, 0, 0, 56970, 105114, 0, 0, 0, 'Скверноуст - маг порчи to Player');
INSERT INTO `creature_text` VALUES (116785, 0, 0, 'Аномальную спору можно найти на вершине этого утеса. Но пробиваться туда придется с боем. Удачи!', 12, 0, 100, 1, 0, 0, 125809, 0, 0, 0, 'Муяни to Player');
INSERT INTO `creature_text` VALUES (116789, 3, 0, 'Гру-у...', 12, 0, 100, 0, 0, 0, 125552, 643594, 763704, 0, 'Потомок Шкама to 1235');
INSERT INTO `creature_text` VALUES (116801, 0, 0, '...И тогда я сказал: \"Нельзя убить то, чего не видишь!\" И исчез! ХА-ХА-ХА! Ну и рожа была у того парня. Он чуть не уронил рожок мороженого.', 12, 0, 100, 396, 0, 0, 125503, 0, 0, 0, 'Макс Критс');
INSERT INTO `creature_text` VALUES (116801, 3, 0, '...Яд? Зачем такие хлопоты – просто подкрадись сзади!', 12, 0, 100, 396, 0, 0, 125502, 10510, 10510, 0, 'Макс Критс');
INSERT INTO `creature_text` VALUES (116801, 3, 0, 'Из прошлой команды меня никто не выгонял. Я сам от них ушел, ведь они были слабаки!', 12, 0, 100, 396, 0, 0, 125510, 60700, 60700, 0, 'Макс Критс');
INSERT INTO `creature_text` VALUES (116801, 3, 0, 'Как я могу быть лучшим бойцом, если мне нужно бежать, как только на земле появляется немного огня?', 12, 0, 100, 396, 0, 0, 125512, 84307, 84307, 0, 'Макс Критс');
INSERT INTO `creature_text` VALUES (116815, 1, 0, 'Узри силу шал\'дорай!', 12, 0, 100, 0, 0, 65706, 116690, 0, 0, 0, 'Заклинатель клинков из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (116815, 2, 0, 'Не может быть...', 12, 0, 100, 0, 0, 65692, 121822, 0, 0, 0, 'Заклинатель клинков из Сумеречной стражи to Player');
INSERT INTO `creature_text` VALUES (116823, 3, 0, 'Пусть двери Валхаллы всегда будут для тебя открыты.', 12, 0, 100, 53, 0, 30541, 126205, 11125, 11125, 0, 'Жнец душ Песни Тьмы to Жнец душ Песни Тьмы');
INSERT INTO `creature_text` VALUES (116824, 3, 0, 'Благослови тебя Один.', 12, 0, 100, 53, 0, 0, 126206, 39591, 39591, 0, 'Плененный врайкул to Жнец душ Песни Тьмы');
INSERT INTO `creature_text` VALUES (116824, 3, 0, 'Пусть двери Валхаллы всегда будут для тебя открыты.', 12, 0, 100, 53, 0, 0, 126205, 20099, 20099, 0, 'Плененный врайкул');
INSERT INTO `creature_text` VALUES (116825, 1, 0, 'Столько вопросов, так мало времени.', 12, 0, 100, 0, 0, 55377, 102158, 0, 0, 0, 'Свежеватель душ Песни Тьмы to Player');
INSERT INTO `creature_text` VALUES (116825, 1, 0, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 55376, 102157, 0, 0, 0, 'Свежеватель душ Песни Тьмы to Player');
INSERT INTO `creature_text` VALUES (116827, 0, 0, 'Тебе нужна упряжь? Ха! Ну возьми, попробуй!', 12, 0, 100, 0, 0, 82745, 125902, 0, 0, 0, 'Андйор Укротитель Драконов to Player');
INSERT INTO `creature_text` VALUES (116827, 1, 0, 'Упряжь...', 12, 0, 100, 0, 0, 82744, 125889, 0, 0, 0, 'Андйор Укротитель Драконов to Player');
INSERT INTO `creature_text` VALUES (116839, 0, 0, 'Противиться Легиону бессмысленно, верховный маг! Все сгинет!', 14, 0, 100, 0, 0, 0, 125526, 0, 0, 0, 'Верховный маг Ксилем to Player');
INSERT INTO `creature_text` VALUES (116843, 1, 0, '|TInterface\\Icons\\TRADE_ARCHAEOLOGY_SHARK JAWS:20|tВсе, кто окажется слишком близко к |cFFFF0000|Hspell:234621|h[пожирающей пасти]|h|r, будут |cFFFF0000|Hspell:232885|h[пожраны целиком]|h|r!', 42, 0, 100, 0, 0, 85239, 133077, 0, 0, 0, 'Сарукел to Player');
INSERT INTO `creature_text` VALUES (116843, 3, 0, '|TInterface\\Icons\\TRADE_ARCHAEOLOGY_SHARK JAWS:20|tОтнесите |cFFFF0000|Hspell:232913|h[оскверняющие чернила]|h|r к Сарукелу, чтобы он закрыл свою |cFFFF0000|Hspell:234621|h[пожирающую пасть]|h|r!', 41, 0, 100, 0, 0, 0, 131647, 398844, 484094, 0, 'Сарукел to Сарукел');
INSERT INTO `creature_text` VALUES (116849, 0, 0, 'Огромное тебе спасибо!', 12, 0, 100, 2, 0, 0, 125549, 0, 0, 0, 'Хмелевар Буйных Портеров to Player');
INSERT INTO `creature_text` VALUES (116849, 0, 1, 'Благодарю тебя. Я думал, здесь Легион нам не угрожает...', 12, 0, 100, 2, 0, 0, 125619, 0, 0, 0, 'Хмелевар Буйных Портеров to Player');
INSERT INTO `creature_text` VALUES (116849, 0, 2, 'Уф, было неудобно. Спасибо!', 12, 0, 100, 2, 0, 0, 125617, 0, 0, 0, 'Хмелевар Буйных Портеров to Player');
INSERT INTO `creature_text` VALUES (116849, 0, 3, 'Спасибо! Покажем этим демонам, чего мы стоим!', 12, 0, 100, 2, 0, 0, 125620, 0, 0, 0, 'Хмелевар Буйных Портеров to Player');
INSERT INTO `creature_text` VALUES (116849, 0, 9, 'Желаю тебе всех благ!', 12, 0, 100, 2, 0, 0, 125618, 0, 0, 0, 'Хмелевар Буйных Портеров to Player');
INSERT INTO `creature_text` VALUES (116867, 0, 0, 'В моем дыхании – ярость шторма! Скажи, куда нанести удар, и наши враги падут!', 12, 0, 100, 0, 0, 81406, 125572, 0, 0, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (116868, 0, 0, 'Приветствую. Тебя направил сюда Всеотец Один?', 12, 0, 100, 0, 0, 81410, 128439, 0, 0, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (116868, 1, 0, 'Дай знать, когда приготовишься, и мы поднимемся в небеса.', 12, 0, 100, 0, 0, 81409, 128435, 0, 0, 0, 'Ветир to Player');
INSERT INTO `creature_text` VALUES (116880, 0, 0, 'Банк? Как они...', 12, 0, 100, 6, 0, 82821, 125561, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (116880, 1, 0, 'Сердце Круула... плохо дело.', 12, 0, 100, 0, 0, 82809, 125615, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (116880, 2, 0, 'Никогда не поручай магам работу охотников на демонов!', 12, 0, 100, 0, 0, 82818, 125623, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (116880, 3, 0, 'Демоны могли спрятаться в банке. Будь начеку.', 12, 0, 100, 0, 0, 82808, 125613, 0, 0, 0, 'Кор\'вас Кровавый Шип to Player');
INSERT INTO `creature_text` VALUES (116882, 1, 0, 'Смертная плоть так хрупка.', 12, 0, 100, 0, 0, 55197, 102132, 0, 0, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (116882, 3, 0, 'Ты погибнешь от моего клинка!', 12, 0, 100, 0, 0, 55195, 102130, 15421, 15421, 0, 'Страж Скверны - завоеватель to Player');
INSERT INTO `creature_text` VALUES (116900, 0, 0, 'Смотри как я могу!', 12, 0, 100, 0, 0, 80948, 127431, 0, 0, 0, 'Хмелевар Альма to 0');
INSERT INTO `creature_text` VALUES (116900, 1, 0, 'А я думала, тут безопасно. До недавних пор Легион даже не знал про нас.', 12, 0, 100, 0, 0, 82069, 125641, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 2, 0, 'Как думаешь, демоны догадываются, что будет, если выпить штормовой мед?', 12, 0, 100, 0, 0, 82070, 125642, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 3, 0, 'Хмелевара нигде не видать. Может, она смогла выбраться?', 12, 0, 100, 0, 0, 82071, 125662, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 4, 0, 'Слышишь? Давай посмотрим, что происходит снаружи!', 12, 0, 100, 0, 0, 82065, 125698, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 5, 0, 'Сюда! Нам нужно открыть эту дверь.', 12, 0, 100, 0, 0, 82072, 125663, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 6, 0, 'Отлично! Тренировка мне не помешает!', 12, 0, 100, 0, 0, 80950, 127433, 0, 0, 0, 'Хмелевар Альма to 0');
INSERT INTO `creature_text` VALUES (116900, 7, 0, 'Последняя дверь!', 12, 0, 100, 25, 0, 82073, 125664, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 8, 0, 'Что это было? Что она имела в виду? Великий мастер, мы должны сейчас же вернуться в храм! Я жду тебя.', 12, 0, 100, 0, 0, 82086, 128169, 0, 0, 0, 'Хмелевар Альма to Варагол Подчинитель');
INSERT INTO `creature_text` VALUES (116900, 9, 0, 'Великий мастер $n, ты как раз вовремя! Помоги мне открыть дверь!', 12, 0, 100, 0, 0, 82062, 125602, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (116900, 10, 0, 'Идем.', 12, 0, 100, 0, 0, 82063, 140119, 0, 0, 0, 'Хмелевар Альма to 116992');
INSERT INTO `creature_text` VALUES (116909, 1, 0, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 81408, 105128, 0, 0, 0, 'Эредарская подчинительница Скверны to Player');
INSERT INTO `creature_text` VALUES (116929, 0, 0, 'Великий мастер, пора праздновать!', 12, 0, 100, 0, 0, 82436, 125575, 0, 0, 0, 'Хмелевар Бланш to Player');
INSERT INTO `creature_text` VALUES (116939, 0, 0, 'Смертным... смерть!', 14, 0, 100, 0, 0, 82212, 129548, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 1, 0, 'Хаос!', 14, 0, 100, 0, 0, 82212, 129550, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 1, 1, 'Отмечены!', 14, 0, 100, 0, 0, 82212, 129891, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 2, 0, 'Рассечь!', 14, 0, 100, 0, 0, 82212, 129552, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 2, 1, 'Пронзить!', 14, 0, 100, 0, 0, 82212, 129894, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 3, 0, '|TINTERFACE\\ICONS\\SPELL_FIRE_FELHELLFIRE:20|t Аватара начинает применять заклинание |cFFFF0404|Hspell:239132|h[\"Разлом реальностей\"]|h|r!', 41, 0, 100, 0, 0, 82212, 126582, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 4, 0, 'Пламя!', 14, 0, 100, 0, 0, 82212, 129889, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 4, 1, 'Сжечь!', 14, 0, 100, 0, 0, 82212, 129549, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 5, 0, 'Покончи... с ними...', 14, 0, 100, 0, 0, 82213, 129538, 0, 0, 0, 'Аватара Падшего');
INSERT INTO `creature_text` VALUES (116939, 6, 0, 'Все...', 14, 0, 100, 0, 0, 82203, 129566, 0, 0, 0, 'Аватара Падшего to Player');
INSERT INTO `creature_text` VALUES (116939, 7, 0, 'Смерть!', 14, 0, 100, 0, 0, 82212, 133907, 0, 0, 0, 'Аватара Падшего to Player');
INSERT INTO `creature_text` VALUES (116939, 8, 0, 'Труп!', 14, 0, 100, 0, 0, 82212, 129555, 0, 0, 0, 'Аватара Падшего to Player');
INSERT INTO `creature_text` VALUES (116944, 0, 0, 'Наконец-то мы одни.', 14, 0, 100, 0, 0, 82036, 129343, 0, 0, 0, 'Мефистрот to Грознокрыл');
INSERT INTO `creature_text` VALUES (116944, 1, 0, 'Отдайте щит, и я дарую вам быструю смерть!', 14, 0, 100, 0, 0, 80500, 127948, 0, 0, 0, 'Мефистрот to 0');
INSERT INTO `creature_text` VALUES (116944, 2, 0, 'Мы... все равно... победим...', 14, 0, 100, 0, 0, 80501, 127952, 0, 0, 0, 'Мефистрот to Player');
INSERT INTO `creature_text` VALUES (116944, 4, 0, 'Вам не совладать с мощью Легиона!', 14, 0, 100, 0, 0, 80496, 127944, 0, 0, 0, 'Мефистрот to Иллидан Ярость Бури');
INSERT INTO `creature_text` VALUES (116953, 1, 0, 'I am too tough for you!', 12, 0, 100, 0, 0, 0, 102135, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (116975, 0, 0, 'Озорной дух предстает в своем истинном облике.', 16, 0, 100, 0, 0, 0, 125584, 0, 0, 0, 'Сажекопытный холмоход to Player');
INSERT INTO `creature_text` VALUES (116991, 3, 0, 'Гагх гурл!', 12, 0, 100, 0, 0, 79771, 125590, 1331, 1331, 0, 'Мургулий to Player');
INSERT INTO `creature_text` VALUES (116991, 4, 0, 'Мургллл... Груггл ург груллуллл!', 12, 0, 100, 0, 0, 79958, 125614, 6991, 6991, 0, 'Мургулий to Player');
INSERT INTO `creature_text` VALUES (116991, 4, 0, 'Мургулий указывает на хилую рыбку и смотрит на вас. Он явно хочет, чтобы вы что-то сделали.', 16, 0, 100, 0, 0, 79958, 125616, 11095, 11095, 0, 'Мургулий to Player');
INSERT INTO `creature_text` VALUES (117006, 0, 0, 'Туман наконец-то рассеивается...', 12, 0, 100, 1, 0, 0, 127046, 0, 0, 0, 'Боец Армии погибели Легиона to Player');
INSERT INTO `creature_text` VALUES (117006, 0, 1, 'I owe you.', 12, 0, 100, 1, 0, 0, 138328, 0, 0, 0, 'Боец Армии погибели Легиона to Player');
INSERT INTO `creature_text` VALUES (117006, 0, 2, 'Боль утихла. Спасибо тебе, $n.', 12, 0, 100, 1, 0, 0, 127045, 0, 0, 0, 'Боец Армии погибели Легиона to Player');
INSERT INTO `creature_text` VALUES (117018, 1, 0, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 56982, 129829, 0, 0, 0, 'Демон-инструктор to Player');
INSERT INTO `creature_text` VALUES (117018, 1, 0, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 56981, 105125, 0, 0, 0, 'Демон-инструктор to Player');
INSERT INTO `creature_text` VALUES (117021, 0, 0, 'Ой-ой...', 12, 0, 100, 0, 0, 0, 125643, 0, 0, 0, 'Змееуст-экстрактор to Player');
INSERT INTO `creature_text` VALUES (117030, 0, 0, 'Да! Я исцелен!', 12, 0, 100, 603, 0, 82562, 125633, 0, 0, 0, 'Роланд Абернати to Player');
INSERT INTO `creature_text` VALUES (117032, 1, 0, 'Я заставлю этих существ сожрать твою душу... по кусочкам!', 12, 0, 100, 0, 0, 80559, 125629, 0, 0, 0, 'Земорас');
INSERT INTO `creature_text` VALUES (117042, 0, 0, 'Похоже, судьбе было угодно вновь свести нас вместе, |3-6($c).', 12, 0, 100, 0, 0, 83135, 125653, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (117042, 1, 0, 'Итак... Круул возвращается.', 12, 0, 100, 0, 0, 83142, 125717, 0, 0, 0, 'Пророк Велен to Player');
