INSERT INTO `creature_text` VALUES (117555, 1, 0, 'Хватайте обед!', 12, 0, 100, 0, 0, 55202, 96653, 0, 0, 0, 'Бес огня Скверны to Player');
INSERT INTO `creature_text` VALUES (117555, 3, 0, 'Мы пожрем твою душу! И тело тоже.', 12, 0, 100, 0, 0, 55205, 96656, 3562, 3562, 0, 'Бес огня Скверны to Player');
