INSERT INTO `creature_text` VALUES (117557, 1, 0, 'Смерть уже заждалась.', 12, 0, 100, 0, 0, 55332, 127315, 0, 0, 0, 'Мо\'арг-костекрушитель to Player');
INSERT INTO `creature_text` VALUES (117561, 0, 0, 'I won’t let you take his power!', 14, 0, 100, 603, 0, 82697, 127202, 0, 0, 0, 'Призывательница Тьмы Резара to Player');
INSERT INTO `creature_text` VALUES (117561, 1, 0, 'Сила Канретада принадлежит Легиону!', 14, 0, 100, 0, 0, 83292, 127200, 0, 0, 0, 'Призывательница Тьмы Резара to Player');
INSERT INTO `creature_text` VALUES (117561, 2, 0, 'Ты не помешаешь ритуалу!', 14, 0, 100, 603, 0, 83292, 127201, 0, 0, 0, 'Призывательница Тьмы Резара to Player');
INSERT INTO `creature_text` VALUES (117562, 0, 0, 'Хочешь забрать Камень Рока? Ну давай, попробуй!', 12, 0, 100, 0, 0, 82793, 126083, 0, 0, 0, 'Вестник рока Бельреш to Player');
INSERT INTO `creature_text` VALUES (117562, 1, 0, 'Ты не победишь... Легион...', 12, 0, 100, 0, 0, 82794, 126084, 0, 0, 0, 'Вестник рока Бельреш to Player');
INSERT INTO `creature_text` VALUES (117567, 1, 0, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 56984, 105128, 0, 0, 0, 'Эредарская призывательница to Player');
INSERT INTO `creature_text` VALUES (117593, 1, 0, 'Вы, паладины, такие предсказуемые. Ну-ка, посмотрим, какова истинная сила твоего клинка...', 12, 0, 100, 0, 0, 0, 127570, 0, 0, 0, 'Уртракс Осквернитель');
INSERT INTO `creature_text` VALUES (117593, 4, 0, 'Эта земля никогда не исцелится...', 12, 0, 100, 0, 0, 0, 127571, 22594, 22594, 0, 'Уртракс Осквернитель to Player');
INSERT INTO `creature_text` VALUES (117596, 4, 0, '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t Гладиатор из племени Острозубов преследует вас!', 42, 0, 100, 0, 0, 0, 129936, 10148, 10148, 0, 'Гладиатор из племени Острозубов to Player');
INSERT INTO `creature_text` VALUES (117598, 0, 0, 'Системы активны. Обнаружены гости.', 12, 0, 100, 0, 0, 81188, 126238, 0, 0, 0, 'Смотритель М4С73Р to Player');
INSERT INTO `creature_text` VALUES (117622, 0, 0, 'Мне надоели эти игры. Говори - или ты умрешь.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117622, 0, 1, 'Я все равно добьюсь своего. Говори.', 12, 0, 100, 0, 0, 0, 127678, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117622, 0, 2, 'Мне это надоело. Быстро рассказывай все, что я хочу знать, или умрешь.', 12, 0, 100, 0, 0, 0, 127682, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117622, 0, 3, 'Что ты задумал, демон?', 12, 0, 100, 0, 0, 0, 127679, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117622, 0, 4, 'Говори!', 12, 0, 100, 0, 0, 0, 127681, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117622, 0, 5, 'I will get everything I need from you, demon.', 12, 0, 100, 0, 0, 0, 127677, 0, 0, 0, 'Элитис Огненная Буря to 0');
INSERT INTO `creature_text` VALUES (117648, 0, 0, 'Это только... начало...', 12, 0, 100, 0, 0, 81074, 126278, 0, 0, 0, 'Зор\'тадуун to Player');
INSERT INTO `creature_text` VALUES (117673, 3, 0, 'Не стоило тебе мешать моему пиру, смертное создание!', 14, 0, 100, 0, 0, 0, 126190, 5655, 5655, 0, 'Владыка Кормаг to 116800');
INSERT INTO `creature_text` VALUES (117692, 0, 0, 'Консоли нужно активировать одновременно. Поможешь мне?', 12, 0, 100, 1, 0, 81187, 126237, 0, 0, 0, 'Магни Бронзобород to Player');
INSERT INTO `creature_text` VALUES (117692, 1, 0, 'I smell corruption in my brothers. Be alert, cleansing begins.', 12, 0, 100, 1, 0, 81211, 126262, 0, 0, 0, 'Магни Бронзобород to 1274');
INSERT INTO `creature_text` VALUES (117692, 2, 0, 'Готовьтесь к новой волне. Машина переполнена Светом.', 12, 0, 100, 5, 0, 81212, 126464, 0, 0, 0, 'Магни Бронзобород to 1274');
INSERT INTO `creature_text` VALUES (117694, 0, 0, 'Ничего себе, какая огроменная дверь! Но где-то должен быть рычаг. Всегда где-то есть рычаг!', 12, 0, 100, 5, 0, 81184, 126233, 0, 0, 0, 'Бранн Бронзобород to Player');
INSERT INTO `creature_text` VALUES (117694, 1, 0, 'Большие защитники, само собой, тоже оживут. Готовьтесь!', 12, 0, 100, 579, 0, 81213, 126263, 0, 0, 0, 'Бранн Бронзобород to 1274');
INSERT INTO `creature_text` VALUES (117694, 2, 0, 'Это еще не все!', 12, 0, 100, 5, 0, 81209, 126462, 0, 0, 0, 'Бранн Бронзобород to 1274');
INSERT INTO `creature_text` VALUES (117694, 3, 0, 'В этот раз хоть без лучей из глаз обошлось!', 12, 0, 100, 5, 0, 81210, 126467, 0, 0, 0, 'Бранн Бронзобород to 1274');
INSERT INTO `creature_text` VALUES (117695, 0, 0, 'Неизвестное хранилище титанов в идеальном состоянии? Надо все записать! Как думаете, что в этих пузырях?', 12, 0, 100, 5, 0, 81168, 126293, 0, 0, 0, 'Бетильда Глубокомолот to Player');
INSERT INTO `creature_text` VALUES (117695, 1, 0, 'Порождения Света... Думаю, их можно рассеять заклинанием.', 12, 0, 100, 0, 0, 81171, 126261, 0, 0, 0, 'Бетильда Глубокомолот to 1274');
INSERT INTO `creature_text` VALUES (117695, 2, 0, 'Провалиться мне, чтобы я еще раз связалась с Бронзобородами!', 12, 0, 100, 5, 0, 81172, 126463, 0, 0, 0, 'Бетильда Глубокомолот to 1274');
INSERT INTO `creature_text` VALUES (117709, 0, 0, 'Полагаю, я должна прос-следить за ремонтом \"Молота С-Скверны\". Увидимс-ся на борту.', 12, 0, 100, 0, 0, 82581, 129203, 0, 0, 0, 'Леди С\'тэно to Player');
INSERT INTO `creature_text` VALUES (117714, 0, 0, 'Приветствую, $p. Я подготавливаю все необходимое для ритуала очищения барда, но у нас нет самого главного – святой воды.', 12, 0, 100, 1, 0, 82493, 126893, 0, 0, 0, 'Лорд Грейсон Тенелом to 0');
INSERT INTO `creature_text` VALUES (117714, 1, 0, 'I sent three reliable paladins in search of her, but they never returned.', 12, 0, 100, 1, 0, 82494, 126894, 0, 0, 0, 'Лорд Грейсон Тенелом to 0');
INSERT INTO `creature_text` VALUES (117714, 2, 0, 'Видимо, придется этим заняться тебе. Возьми с собой пару моих спутников – кто знает, какие опасности могут поджидать в этом месте...', 12, 0, 100, 1, 0, 82495, 126895, 0, 0, 0, 'Лорд Грейсон Тенелом to 0');
INSERT INTO `creature_text` VALUES (117714, 3, 0, 'Спасибо за святую воду. И глубочайшая благодарность за спасение паладинов.', 12, 0, 100, 1, 0, 82496, 126906, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 4, 0, 'I will sprinkle the bard with holy water - and then it\'s up to you and your weapons.', 12, 0, 100, 1, 0, 82497, 126907, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 5, 0, 'Доспех готов! Теперь мы отправимся в склеп Ривендера и сразимся с его духом.', 12, 0, 100, 1, 0, 82498, 126908, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 6, 0, 'ВСЕ ПО КОНЯМ! ВПЕРЕД!', 14, 0, 100, 22, 0, 82499, 126909, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 7, 0, 'Паладины прикроют нас, пока мы будем разбираться с Ривендером.', 12, 0, 100, 1, 0, 82500, 126910, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 8, 0, 'А где тело? Оно должно быть здесь!', 12, 0, 100, 1, 0, 82501, 126911, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 9, 0, 'Кто-то забрал его! Не к добру все это...', 12, 0, 100, 1, 0, 82502, 126912, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 10, 0, 'Скакун покорился! Воистину, Свет благоволит нам!', 12, 0, 100, 1, 0, 82504, 126918, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 11, 0, 'Давай вернемся в Обитель Света. Там ты спокойно наденешь на коня доспех.', 12, 0, 100, 1, 0, 82505, 126919, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117714, 12, 0, 'Дай мне знать, когда можно будет отправляться. Я займусь порталом.', 12, 0, 100, 1, 0, 82506, 126920, 0, 0, 0, 'Лорд Грейсон Тенелом to Player');
INSERT INTO `creature_text` VALUES (117759, 1, 0, 'Надеюсь, ты $gготов:готова; к смерти, |3-6($r). Я этого медведя неделю не кормил, так что он тебя сожрет заживо!', 14, 0, 100, 0, 0, 0, 126325, 0, 0, 0, 'Морозец to Player');
INSERT INTO `creature_text` VALUES (117792, 1, 0, 'Кто посмел прервать мой пир?', 12, 0, 100, 0, 0, 80590, 126289, 0, 0, 0, 'Квисгор Ненасытный');
INSERT INTO `creature_text` VALUES (117795, 0, 0, 'Моя иллюзия! Ах ты, дерзкая тварь!', 14, 0, 100, 0, 0, 81346, 126820, 0, 0, 0, 'Агата to Player');
INSERT INTO `creature_text` VALUES (117795, 1, 0, 'Я найду тебя, |3-6($c)! Найду и прикончу!', 14, 0, 100, 0, 0, 81347, 126821, 0, 0, 0, 'Агата to Player');
INSERT INTO `creature_text` VALUES (117800, 0, 0, 'Думали укрыться в своей жалкой хмелеварне? Пылающий Легион видит все!', 12, 0, 100, 0, 0, 82684, 128144, 0, 0, 0, 'Ил\'рукс to Player');
INSERT INTO `creature_text` VALUES (117800, 1, 0, 'Вы опоздали, жалкие создания...', 12, 0, 100, 0, 0, 82683, 128143, 0, 0, 0, 'Ил\'рукс to Player');
INSERT INTO `creature_text` VALUES (117820, 1, 0, 'Во имя Саргераса!', 12, 0, 100, 0, 0, 56973, 109464, 0, 0, 0, 'Колдунья из армии Ракиша to Player');
INSERT INTO `creature_text` VALUES (117820, 1, 0, 'Да исполнится воля Легиона!', 12, 0, 100, 0, 0, 56967, 105111, 0, 0, 0, 'Колдунья из армии Ракиша to Player');
INSERT INTO `creature_text` VALUES (117820, 3, 0, 'Мы очистим ваш мир!', 12, 0, 100, 0, 0, 56970, 105114, 10103, 10103, 0, 'Колдунья из армии Ракиша to Player');
INSERT INTO `creature_text` VALUES (117852, 0, 0, 'Вы нашли меня лишь потому, что я сам этого хотел! Теперь я отомщу Громовому Тотему.... и тебе!', 14, 0, 100, 22, 0, 81537, 126319, 0, 0, 0, 'Тугар Кровавый Тотем to Player');
INSERT INTO `creature_text` VALUES (117853, 0, 0, 'Стена пламени! Арргх! Разберись с Тугаром. Я тебя догоню!', 14, 0, 100, 0, 0, 82326, 126312, 0, 0, 0, 'Наваррогг');
INSERT INTO `creature_text` VALUES (117860, 3, 0, 'I deserve it.', 14, 0, 100, 0, 0, 0, 126281, 7053, 7053, 0, 'Морозец to Осо');
INSERT INTO `creature_text` VALUES (117860, 3, 0, 'Осо, пасть разинь, чтобы я мог стрельнуть!', 14, 0, 100, 0, 0, 0, 126486, 7687, 40156, 0, 'Морозец to Осо');
INSERT INTO `creature_text` VALUES (117864, 0, 0, 'Седлаем коней! Нам нужно найти пропавших союзников!', 12, 0, 100, 0, 0, 0, 129599, 0, 0, 0, 'Мелен Ярость Света to Player');
INSERT INTO `creature_text` VALUES (117898, 0, 0, 'Для меня честь сопровождать тебя!', 12, 0, 100, 0, 0, 0, 129597, 0, 0, 0, 'Разеан Светопад to Player');
