INSERT INTO `creature_text` VALUES (118402, 1, 0, 'Мой \"повелитель\" приказал мне помогать тебе, так что постарайся не умереть.', 12, 0, 100, 0, 0, 0, 126758, 0, 0, 0, 'Сиайт to Player');
INSERT INTO `creature_text` VALUES (118402, 3, 0, 'Кнут, знаешь ли, мне не для красоты. Скажешь чего лишнего, нарушишь условия контракта – и я тебе продемонстрирую, какие он может творить чудеса...', 12, 0, 100, 0, 0, 0, 126774, 61165, 61165, 0, 'Сиайт to Player');
INSERT INTO `creature_text` VALUES (118402, 4, 0, 'Долго же я ждала этой минуты, казарянин! Только теперь кнут в руках у меня!', 12, 0, 100, 0, 0, 67109, 126775, 5140, 5140, 0, 'Сиайт to Player');
