INSERT INTO `creature_text` VALUES (119270, 1, 0, 'Ты погибнешь от моего клинка!', 12, 0, 100, 0, 0, 55195, 102130, 0, 0, 0, 'Защитник с берега Ярости Скверны to Player');
INSERT INTO `creature_text` VALUES (119338, 0, 0, 'I understand his grief, but the king’s place is on the throne. Especially at the height of the war with the Legion. Convince Anduin to come back!', 12, 0, 100, 0, 0, 0, 128740, 0, 0, 0, 'Генн Седогрив to player');
INSERT INTO `creature_text` VALUES (119338, 1, 0, 'Пророчеством войну не выиграть. У парня доброе сердце, но он слаб.', 12, 0, 100, 0, 0, 0, 128802, 0, 0, 0, 'Генн Седогрив to player');
INSERT INTO `creature_text` VALUES (119338, 2, 0, 'Этого недостаточно, пророк. Андуина не было на Расколотом берегу. Он не видел, как наши солдаты жертвовали собой...', 12, 0, 100, 0, 0, 0, 128807, 0, 0, 0, 'Генн Седогрив to player');
INSERT INTO `creature_text` VALUES (119338, 3, 0, 'Король должен знать цену своим приказам и отвечать за них. Если простой компас подорвал его дух, что будет, когда война придет в Штормград?', 12, 0, 100, 0, 0, 0, 128809, 0, 0, 0, 'Генн Седогрив to player');
INSERT INTO `creature_text` VALUES (119340, 0, 0, 'Как раз вовремя. Теперь у нас есть шанс достучаться до короля.', 12, 0, 100, 0, 0, 0, 128738, 0, 0, 0, 'Пророк Велен to player');
INSERT INTO `creature_text` VALUES (119340, 1, 0, 'Андуин не ест, не спит и решительно отказывается возвращаться во дворец. Я не знаю, как его утешить.', 12, 0, 100, 0, 0, 0, 128739, 0, 0, 0, 'Пророк Велен to player');
INSERT INTO `creature_text` VALUES (119340, 2, 0, 'Юный король станет великим лидером. Я это предвижу.', 12, 0, 100, 0, 0, 0, 128801, 0, 0, 0, 'Пророк Велен to player');
INSERT INTO `creature_text` VALUES (119340, 3, 0, 'Свет силен в Андуине. Он научится быть королем. Мы должны верить.', 12, 0, 100, 0, 0, 0, 128803, 0, 0, 0, 'Пророк Велен to player');
INSERT INTO `creature_text` VALUES (119357, 0, 0, 'Чтобы не привлекать внимание в городе, мне понадобится маскировка.', 12, 0, 100, 0, 0, 0, 128747, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 1, 0, 'Я знаю одно заклинание. Следуй за мной.', 12, 0, 100, 0, 0, 0, 128748, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 2, 0, 'Штормград всегда был моим домом. Я знаю все его улочки как свои пять пальцев.', 12, 0, 100, 0, 0, 0, 129139, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 3, 0, 'Все вокруг такое знакомое, а я как будто заблудился.', 12, 0, 100, 0, 0, 0, 129140, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 4, 0, 'I understand their fear. If we do not stop the demons on the Broken Shore, they will soon come to Stormwind.', 12, 0, 100, 0, 0, 0, 128767, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 5, 0, 'I do not blame his choice. My father sacrificed everything for his people. He was a great king.', 12, 0, 100, 0, 0, 0, 128784, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 6, 0, 'I have not been to Leo\'s Peace since the day my father’s memorial was brought here.', 12, 0, 100, 0, 0, 0, 129163, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 7, 0, 'Война отнимает слишком много сил. У меня даже не было времени погоревать.', 12, 0, 100, 0, 0, 0, 129164, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 8, 0, 'С тех пор как ко мне вернулся компас, я возношу молитвы Свету, чтобы узнать, обрел ли дух отца покой. Но Свет безмолвствует.', 12, 0, 100, 0, 0, 0, 128797, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 9, 0, 'Народ меня любит и уважает, но... не верит в меня. Не верит так, как верил в отца.', 12, 0, 100, 0, 0, 0, 128799, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 10, 0, 'Теперь я знаю, что думают люди, но какого мнения обо мне советники отца? Боюсь, они не доверяют мне.', 12, 0, 100, 0, 0, 0, 128800, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119357, 11, 0, 'Я должен знать. Встретимся в крепости!', 12, 0, 100, 0, 0, 0, 128927, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (119388, 0, 0, 'Велен. Я думал, ты забыл свою родину. Но ты вернулся.', 12, 0, 100, 397, 0, 86182, 129672, 0, 0, 0, 'Вождь Хатуун to 0');
INSERT INTO `creature_text` VALUES (119388, 1, 0, 'Аргус изменился, и мы изменились вместе с ним. Разве у нас был выбор?', 12, 0, 100, 397, 0, 86184, 129679, 0, 0, 0, 'Вождь Хатуун to 0');
INSERT INTO `creature_text` VALUES (119388, 2, 0, 'Ты бросил свой народ, пророк... и свою родину. Такое карается смертью.', 12, 0, 100, 397, 0, 86185, 129673, 0, 0, 0, 'Вождь Хатуун to 0');
INSERT INTO `creature_text` VALUES (119388, 3, 0, 'Но твои союзники спасли многих из нас. Быть может, ты и изменился.', 12, 0, 100, 396, 0, 88232, 135982, 0, 0, 0, 'Вождь Хатуун to 0');
INSERT INTO `creature_text` VALUES (119388, 4, 0, 'Алмазный коротышка ждет тебя в пещере, где хранится камень. Он... встревожен.', 12, 0, 100, 396, 0, 86829, 135017, 0, 0, 0, 'Вождь Хатуун to 0');
INSERT INTO `creature_text` VALUES (119397, 1, 0, 'Ваши народы... ваши миры... сгорят!', 14, 0, 100, 0, 0, 86597, 130002, 0, 0, 0, 'Аггонар');
INSERT INTO `creature_text` VALUES (119408, 0, 0, 'Не трожь мою лодку! Все орлы – к пушкам! А мартышки – к парусам!', 14, 0, 100, 0, 0, 0, 128083, 0, 0, 0, '\"Капитан\" Клутц to 1293');
INSERT INTO `creature_text` VALUES (119419, 0, 0, 'Эй, ты! Это о тебе говорил таурен!', 12, 0, 100, 5, 0, 0, 127971, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 1, 0, 'Опять пытаешься расстроить наши планы? Ха!', 12, 0, 100, 11, 0, 0, 127972, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 2, 0, 'Силенок не хватит...', 12, 0, 100, 274, 0, 0, 127973, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 3, 0, 'И еще: если с головы моего дорогого таурена упадет хоть один волос, у тебя будут СЕРЬЕЗНЫЕ проблемы...', 12, 0, 100, 25, 0, 0, 127974, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 4, 0, 'Со мной!', 14, 0, 100, 15, 0, 0, 127975, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 5, 0, 'Впрочем, ладно. Я уже получила то, за чем пришла.', 12, 0, 100, 1, 0, 0, 127976, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 6, 0, 'Таинственная особа наполняет сосуд субстанцией из котла.', 16, 0, 100, 0, 0, 0, 127981, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 7, 0, 'Молись, чтобы мы больше не встретились.', 12, 0, 100, 1, 0, 0, 127977, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 8, 0, 'У меня нет времени для пафосных фраз. Лучше я просто эффектно уйду.', 12, 0, 100, 1, 0, 0, 127978, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119419, 9, 0, 'Мощный прыжок!', 14, 0, 100, 5, 0, 0, 127979, 0, 0, 0, 'Таинственная особа to Player');
INSERT INTO `creature_text` VALUES (119435, 1, 0, 'Да исполнится воля Легиона!', 12, 0, 100, 1, 0, 56967, 105111, 0, 0, 0, 'Демоническая наставница to Player');
INSERT INTO `creature_text` VALUES (119466, 0, 0, 'I will tear your flesh and devour your soul.', 12, 0, 100, 0, 0, 55016, 97911, 0, 0, 0, 'Жуткий пленитель to Player');
