INSERT INTO `creature_text` VALUES (119490, 0, 0, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 57005, 105125, 0, 0, 0, 'Призыватель небес Дрез\'\'мал to Player');
INSERT INTO `creature_text` VALUES (119490, 0, 1, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 57004, 105124, 0, 0, 0, 'Призыватель небес Дрез\'\'мал to Player');
INSERT INTO `creature_text` VALUES (119490, 0, 2, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 57006, 129829, 0, 0, 0, 'Призыватель небес Дрез\'\'мал to Player');
INSERT INTO `creature_text` VALUES (119490, 0, 3, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 57007, 105127, 0, 0, 0, 'Призыватель небес Дрез\'\'мал to Player');
INSERT INTO `creature_text` VALUES (119490, 0, 4, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 57008, 105128, 0, 0, 0, 'Призыватель небес Дрез\'\'мал to Player');
INSERT INTO `creature_text` VALUES (119515, 0, 0, 'Это была роковая ошибка.', 12, 0, 100, 0, 0, 55149, 96650, 0, 0, 0, 'Нечистый вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (119515, 1, 0, 'Тебе здесь не место!', 12, 0, 100, 0, 0, 55148, 134549, 0, 0, 0, 'Нечистый вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (119515, 2, 0, 'Ты умрешь!', 12, 0, 100, 0, 0, 55145, 99135, 0, 0, 0, 'Нечистый вестник ужаса to Player');
INSERT INTO `creature_text` VALUES (119516, 0, 0, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 57006, 129829, 0, 0, 0, 'Безумный фанатик to Player');
INSERT INTO `creature_text` VALUES (119516, 1, 0, 'Во имя Саргераса!', 12, 0, 100, 0, 0, 56997, 109464, 0, 0, 0, 'Безумный фанатик to Player');
INSERT INTO `creature_text` VALUES (119517, 0, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 56983, 105127, 0, 0, 0, 'Безумная фанатичка to Player');
INSERT INTO `creature_text` VALUES (119533, 1, 0, 'Хаос восторжествует.', 12, 0, 100, 0, 0, 88036, 132525, 0, 0, 0, 'Штурмовой призыватель Рока to Player');
INSERT INTO `creature_text` VALUES (119535, 0, 0, 'Еще одной атаки от этих созданий мы не выдержим. Нам нужно усилить войска.', 12, 0, 100, 396, 0, 86221, 129121, 0, 0, 0, 'Иллидан Ярость Бури to 0');
INSERT INTO `creature_text` VALUES (119535, 1, 0, 'Я бы не полагался на их помощь. Рискуй жизнью, если хочешь, а я буду готовить войска к неизбежной контратаке.', 12, 0, 100, 396, 0, 86224, 132382, 0, 0, 0, 'Иллидан Ярость Бури to 0');
INSERT INTO `creature_text` VALUES (119539, 0, 0, 'Используй мои врата смерти или открой свои. Увидимся в Акерусе.', 12, 0, 100, 1, 0, 81291, 128054, 0, 0, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (119629, 0, 0, 'I will crush you, insect!', 12, 0, 100, 0, 0, 0, 130239, 0, 0, 0, 'Лорд Хел\'Нурат to 0');
INSERT INTO `creature_text` VALUES (119629, 1, 0, 'Зорот не победить...', 12, 0, 100, 0, 0, 0, 130241, 0, 0, 0, 'Лорд Хел\'Нурат to Player');
INSERT INTO `creature_text` VALUES (119630, 0, 0, 'Мурглаг!', 14, 0, 100, 0, 0, 33164, 128096, 0, 0, 0, 'Пойманный головастик to Player');
INSERT INTO `creature_text` VALUES (119630, 3, 0, 'Грулгруг грулл мург груумблг?!', 12, 0, 100, 0, 0, 53431, 128095, 53664, 53664, 0, 'Пойманный головастик');
INSERT INTO `creature_text` VALUES (119630, 3, 0, 'Гуррг мргглл?!', 12, 0, 100, 0, 0, 8475, 128092, 19133, 19133, 0, 'Пойманный головастик');
INSERT INTO `creature_text` VALUES (119644, 3, 0, 'Довольно!', 12, 0, 100, 0, 0, 0, 129681, 11940, 11940, 0, 'Сектант из культа Пустоты to Карина из Луносвета');
INSERT INTO `creature_text` VALUES (119647, 0, 0, 'Ты не превратишь моих товарищей в безмозглых марионеток!', 14, 0, 100, 0, 0, 0, 128132, 0, 0, 0, 'Хранитель святилища дрекирьяров to Player');
INSERT INTO `creature_text` VALUES (119664, 0, 0, 'Спасибо! Встретимся на месте!', 12, 0, 100, 0, 0, 82075, 125566, 0, 0, 0, 'Хмелевар Альма to Player');
INSERT INTO `creature_text` VALUES (119665, 0, 0, 'Как только наешься, немедленно отправляйся на охоту – добыча уже ждет тебя!', 12, 0, 100, 0, 0, 83030, 128343, 0, 0, 0, 'Один to Player');
INSERT INTO `creature_text` VALUES (119673, 1, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Проклятый разоритель to Player');
INSERT INTO `creature_text` VALUES (119673, 4, 0, 'Будьте вы прокляты!', 12, 0, 100, 0, 0, 53433, 137507, 6417, 6417, 0, 'Проклятый разоритель to Player');
INSERT INTO `creature_text` VALUES (119676, 0, 0, '$n! Сюда, скорее! Покончим с этим!', 12, 0, 100, 1, 0, 81471, 128156, 0, 0, 0, 'Ласан Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (119718, 1, 0, 'Надо же, обед сам пришел.', 12, 0, 100, 0, 0, 0, 102143, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119718, 2, 0, 'Тебя ждет смерть!', 12, 0, 100, 0, 0, 0, 116696, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119718, 2, 1, 'Хочешь поиграть с моими бесенятами?', 12, 0, 100, 0, 0, 0, 102148, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119718, 2, 2, 'I\'m so hungry.', 12, 0, 100, 0, 0, 0, 102142, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119718, 2, 3, 'Мои детки знают, как тебя лучше поджарить.', 12, 0, 100, 0, 0, 0, 102146, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119726, 0, 0, '$ct, поспеши на передовую, скорее!', 14, 0, 100, 0, 0, 83073, 128999, 0, 0, 0, 'Верховный маг Кадгар to Player');
INSERT INTO `creature_text` VALUES (119726, 1, 0, 'Отсупаем!', 14, 0, 0, 0, 0, 85239, 131417, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (119735, 0, 0, 'Глупые смертные! НИЧТО вас не спасет!', 14, 0, 100, 153, 0, 0, 114009, 0, 0, 0, 'Варагол Подчинитель to 0');
INSERT INTO `creature_text` VALUES (119735, 1, 0, 'Мы... ВЕЗДЕСУЩИ!', 14, 0, 100, 53, 0, 0, 114014, 0, 0, 0, 'Варагол Подчинитель to 0');
INSERT INTO `creature_text` VALUES (119735, 2, 0, 'Мое задание выполнено, монахи. Ваш орден уже пал!', 12, 0, 100, 0, 0, 82691, 128168, 0, 0, 0, 'Варагол Подчинитель to Сюэнь');
INSERT INTO `creature_text` VALUES (119750, 3, 0, 'Кто здесь?', 12, 0, 100, 1, 0, 87105, 132213, 29, 29, 0, 'Эфириал из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (119750, 3, 0, 'Что это было?', 12, 0, 100, 0, 0, 86804, 132211, 46586, 46586, 0, 'Эфириал из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (119751, 3, 0, 'Тебе не спрятаться.', 12, 0, 100, 0, 0, 88504, 132217, 12551, 12551, 0, 'Маг Бездны из Темной Стражи');
INSERT INTO `creature_text` VALUES (119771, 0, 0, 'Благодарю тебя, великий мастер!', 12, 0, 100, 2, 0, 0, 128328, 0, 0, 0, 'Отравленный Скверной посвященный to Player');
INSERT INTO `creature_text` VALUES (119771, 0, 1, 'Я был так слаб… Спасибо тебе за помощь!', 12, 0, 100, 2, 0, 0, 128325, 0, 0, 0, 'Отравленный Скверной посвященный to Player');
INSERT INTO `creature_text` VALUES (119772, 0, 0, 'Я был так слаб… Спасибо тебе за помощь!', 12, 0, 100, 2, 0, 0, 128325, 0, 0, 0, 'Отравленный Скверной посвященный to Player');
INSERT INTO `creature_text` VALUES (119772, 0, 1, 'I felt life flowing away ... I will be forever grateful to you, great master!', 12, 0, 100, 2, 0, 0, 128327, 0, 0, 0, 'Отравленный Скверной посвященный to Player');
INSERT INTO `creature_text` VALUES (119773, 0, 0, 'Благодарю тебя, великий мастер!', 12, 0, 100, 2, 0, 0, 128328, 0, 0, 0, 'Отравленная Скверной посвященная to Player');
INSERT INTO `creature_text` VALUES (119773, 0, 1, 'I felt life flowing away ... I will be forever grateful to you, great master!', 12, 0, 100, 2, 0, 0, 128331, 0, 0, 0, 'Отравленная Скверной посвященная to Player');
INSERT INTO `creature_text` VALUES (119781, 0, 0, 'Иллидари! Выпускайте зверя!', 14, 0, 100, 1, 0, 81479, 128219, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (119781, 1, 0, 'Этот сквернотопырь на днях напал на наш лагерь. Изловить взрослую особь – редкая удача, поэтому я велела не убивать его.', 12, 0, 100, 1, 0, 81480, 128220, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (119781, 2, 0, 'Чтобы приручить своевольного зверя, нужно показать ему свою силу. Думаю, он подчинится, если ты отлупишь его как следует.', 12, 0, 100, 1, 0, 81481, 128221, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (119781, 3, 0, 'Не стойте, ловите его!', 14, 0, 100, 1, 0, 81484, 128222, 0, 0, 0, 'Верховная мать Злоба to Ликшал');
INSERT INTO `creature_text` VALUES (119781, 4, 0, 'Очень жаль, но сквернокрыл удрал. Хм... Смотри, за ним тянется кровавый след. Очень хорошо!', 12, 0, 100, 1, 0, 81485, 128223, 0, 0, 0, 'Верховная мать Злоба to Ликшал');
INSERT INTO `creature_text` VALUES (119781, 5, 0, 'Демоническое зрение поможет тебе выследить зверя, но если он улетел в гнездовье, вернуть его будет непросто. Сквернотопыри... очень своевольны.', 12, 0, 100, 1, 0, 81486, 128224, 0, 0, 0, 'Верховная мать Злоба to 119916');
INSERT INTO `creature_text` VALUES (119781, 6, 0, 'Вижу, тебе удалось его приручить! Мои поздравления, $p!', 12, 0, 100, 1, 0, 81482, 128233, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (119781, 7, 0, 'Дай нам немного времени, чтобы привести зверя в презентабельный вид. Ирдрис поможет тебе вернуться на \"Молот Скверны\".', 12, 0, 100, 1, 0, 81483, 128234, 0, 0, 0, 'Верховная мать Злоба to Player');
INSERT INTO `creature_text` VALUES (119790, 3, 0, 'Видишь Всадников? Удивительно, как они держатся против этой орды.', 12, 0, 100, 25, 0, 81289, 128212, 53851, 53851, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (119790, 3, 0, 'Одна ее тень обратит Легион в бегство. Под ливнем смерти твари будут молить о пощаде!', 12, 0, 100, 0, 0, 81288, 128211, 28255, 28255, 0, 'Минерва Воронья Скорбь to Player');
INSERT INTO `creature_text` VALUES (119808, 0, 0, 'Немедленно отправляйтесь на поле боя и перебейте демонов, атакующих святилище!', 12, 0, 100, 0, 0, 82690, 128617, 0, 0, 0, 'Повелитель небес Тенистая Роща to Player');
INSERT INTO `creature_text` VALUES (119823, 0, 0, 'Я займусь порталом. Когда он откроется, активируй камень, чтобы призвать Аракс\'ата и скакуна гнева.', 12, 0, 100, 1, 0, 82546, 128225, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 1, 0, 'Наверняка демон натравит на нас своих слуг. Пока я буду поддерживать портал, отбиваться от них придется тебе.', 12, 0, 100, 1, 0, 82547, 128227, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 2, 0, 'Я готов открыть путь через Круговерь Пустоты!', 12, 0, 100, 1, 0, 82536, 127652, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 3, 0, 'Ракат варус мир\'энон!', 12, 0, 100, 0, 0, 82537, 127653, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 4, 0, 'Вэ Зорот!', 12, 0, 100, 0, 0, 82538, 127654, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 5, 0, 'Путь открыт! Скорее, активируй камень призыва!', 12, 0, 100, 0, 0, 82539, 127657, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 6, 0, 'Отлично. Теперь нужно созвать Совет. Только вместе мы сможем справиться с этим могучим созданием.', 12, 0, 100, 0, 0, 82545, 127670, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119823, 7, 0, 'Сдерживай коня, а я соберу остальных...', 12, 0, 100, 0, 0, 82535, 128842, 0, 0, 0, 'Мор\'зул Вестник Крови to Player');
INSERT INTO `creature_text` VALUES (119830, 0, 0, 'Вижу, вы передумали и решили вернуться на мою сторону. Позвольте мне развеять остатки сомнений в ваших мыслях.', 14, 0, 100, 1, 0, 82957, 128245, 0, 0, 0, 'Громораан to Player');
INSERT INTO `creature_text` VALUES (119830, 1, 0, 'I am the true Lord of the Wind, and the Heavenly Heights is my kingdom. And I won’t let anyone poison the air here.', 14, 0, 100, 1, 0, 82958, 128246, 0, 0, 0, 'Громораан to Player');
INSERT INTO `creature_text` VALUES (119830, 2, 0, 'Порознь нас разметут ветра; вместе же мы станем могучим ураганом. Надеюсь, наш конклав больше никогда не распадется.', 14, 0, 100, 1, 0, 82959, 128247, 0, 0, 0, 'Громораан to Player');
INSERT INTO `creature_text` VALUES (119830, 3, 0, 'Спасибо тебе за все, $p. Сейчас я верну тебя в Сердце Азерота.', 14, 0, 100, 403, 0, 82960, 128248, 0, 0, 0, 'Громораан to Player');
INSERT INTO `creature_text` VALUES (119830, 4, 0, 'Там тебя будет ждать дар – знак моей признательности. Он хорошо послужит тебе.', 14, 0, 100, 403, 0, 82961, 128249, 0, 0, 0, 'Громораан to Player');
INSERT INTO `creature_text` VALUES (119834, 3, 0, 'Есть тут кто-нибудь? Помогите!', 12, 0, 100, 0, 0, 0, 109685, 115095, 115095, 0, 'Самоотверженная боевая жрица Альянса to Player');
INSERT INTO `creature_text` VALUES (119839, 0, 0, 'I will destroy you myself!', 14, 0, 100, 377, 0, 82712, 130049, 0, 0, 0, 'Зоротианский скакун гнева to 119733');
INSERT INTO `creature_text` VALUES (119840, 0, 0, 'I will destroy you myself!', 12, 0, 100, 377, 0, 82712, 130049, 0, 0, 0, 'Аракс\'ат to 0');
INSERT INTO `creature_text` VALUES (119840, 1, 0, 'Смертным не завладеть скакуном гнева!', 12, 0, 100, 377, 0, 82713, 127667, 0, 0, 0, 'Аракс\'ат to 0');
INSERT INTO `creature_text` VALUES (119840, 2, 0, 'Невозможно...', 12, 0, 100, 0, 0, 82714, 135820, 0, 0, 0, 'Аракс\'ат to Player');
INSERT INTO `creature_text` VALUES (119844, 0, 0, 'Добро пожаловать на Утесы сквернокрылов, $gистребитель:истребительница; $p. Верховная мать Злоба ждет тебя на площадке к западу отсюда.', 12, 0, 100, 1, 0, 0, 128442, 0, 0, 0, 'Ирдрис Легкий Клинок to Player');
INSERT INTO `creature_text` VALUES (119850, 0, 0, 'Геомант Наваррогга пытается запечатать тоннели, чтобы преградить демонам путь. Защитите его!', 14, 0, 100, 0, 0, 81549, 128630, 0, 0, 0, 'Майла Крутогор to 1299');
INSERT INTO `creature_text` VALUES (119850, 1, 0, 'Аргх!', 14, 0, 100, 0, 0, 81550, 128631, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (119850, 2, 0, 'Герои... убейте... демона...', 14, 0, 100, 0, 0, 81551, 128632, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (119850, 3, 0, 'Отлично! Мы запечатали только два тоннеля, но и это немало.', 12, 0, 100, 1, 0, 81552, 128633, 0, 0, 0, 'Майла Крутогор to 1299');
INSERT INTO `creature_text` VALUES (119850, 4, 0, 'Берегись!', 14, 0, 100, 0, 0, 0, 143886, 0, 0, 0, 'Майла Крутогор to 1299');
INSERT INTO `creature_text` VALUES (119850, 5, 0, 'Это еще не конец!', 14, 0, 100, 0, 0, 81553, 128637, 0, 0, 0, 'Майла Крутогор to Player');
INSERT INTO `creature_text` VALUES (119864, 2, 0, 'Эредары... победят...', 12, 0, 100, 0, 0, 86686, 134485, 0, 0, 0, 'Фанатичная тень to Player');
INSERT INTO `creature_text` VALUES (119866, 1, 0, 'Прими Саргераса!', 12, 0, 100, 0, 0, 86744, 134508, 0, 0, 0, 'Фанатичная тень to Player');
INSERT INTO `creature_text` VALUES (119869, 1, 0, 'Давай поиграем, |3-6($c)!', 12, 0, 100, 0, 0, 0, 128320, 0, 0, 0, 'Ифисиль to Player');
INSERT INTO `creature_text` VALUES (119869, 2, 0, 'Это было... интересно...', 12, 0, 100, 0, 0, 0, 128323, 0, 0, 0, 'Ифисиль to Player');
INSERT INTO `creature_text` VALUES (119869, 3, 0, 'Не волнуйся, Урлия, – кажется, это именно то, что нужно. Такие просто не сдаются.', 12, 0, 100, 0, 0, 0, 128319, 18433, 18433, 0, 'Ифисиль to Player');
INSERT INTO `creature_text` VALUES (119869, 3, 0, 'О, смотри-ка – Агата нашла себе еще одну игрушку.', 12, 0, 100, 0, 0, 0, 128315, 1113, 1113, 0, 'Ифисиль to Player');
INSERT INTO `creature_text` VALUES (119872, 2, 0, 'Агата... будет... довольна...', 12, 0, 100, 0, 0, 0, 128322, 0, 0, 0, 'Урлия to Player');
INSERT INTO `creature_text` VALUES (119872, 3, 0, 'Надеюсь, поинтереснее предыдущей.', 12, 0, 100, 0, 0, 0, 128321, 9867, 9867, 0, 'Урлия to Player');
INSERT INTO `creature_text` VALUES (119892, 3, 0, 'Рад видеть, $n. Кошмар отступил, и теперь мы сможем ее выслушать. Только скажи, и мы начнем.', 12, 0, 100, 0, 0, 85236, 128653, 4787, 4787, 0, 'Магни Бронзобород to Player');
INSERT INTO `creature_text` VALUES (119895, 3, 0, 'Возобновление режима самовосстановления. Оценка внешней помощи: изъявление благодарности.', 12, 0, 100, 0, 0, 0, 128660, 76038, 76038, 0, 'Защитный голем to Защитный голем');
INSERT INTO `creature_text` VALUES (119907, 0, 0, 'Приветствую тебя! Мое оружие к твоим услугам.', 12, 0, 100, 0, 0, 0, 124025, 0, 0, 0, 'Ночнорожденная-мятежница to Player');
INSERT INTO `creature_text` VALUES (119907, 1, 0, 'I want to learn from you.', 12, 0, 100, 0, 0, 0, 124020, 0, 0, 0, 'Ночнорожденный-мятежник to Player');
INSERT INTO `creature_text` VALUES (119907, 1, 0, 'За Талисру! За мятеж!', 12, 0, 100, 0, 0, 0, 124026, 0, 0, 0, 'Ночнорожденная-мятежница to Player');
INSERT INTO `creature_text` VALUES (119907, 1, 0, 'Силгрин передает привет.', 12, 0, 100, 0, 0, 0, 124019, 0, 0, 0, 'Ночнорожденная-мятежница to Player');
INSERT INTO `creature_text` VALUES (119907, 1, 0, 'Смерть Легиону! Смерть его союзникам!', 12, 0, 100, 0, 0, 0, 124021, 0, 0, 0, 'Ночнорожденная-мятежница to Player');
INSERT INTO `creature_text` VALUES (119907, 1, 0, 'Я на стороне первой чародейки!', 12, 0, 100, 0, 0, 0, 124027, 0, 0, 0, 'Ночнорожденный-мятежник to Player');
INSERT INTO `creature_text` VALUES (119923, 1, 0, 'Собор теперь наш!', 12, 0, 100, 0, 0, 81715, 130742, 0, 0, 0, 'Врачеватель душ адского огня to Player');
INSERT INTO `creature_text` VALUES (119944, 0, 0, 'Спокойно, Авиаш. $n – наш союзник.', 12, 0, 100, 0, 0, 0, 128464, 0, 0, 0, 'Ласан Небесный Рог to Player');
INSERT INTO `creature_text` VALUES (119949, 3, 0, 'Кажется, наши союзники смогли отбить атаку... Нужно быть наготове.', 14, 0, 100, 0, 0, 0, 128615, 796368, 796368, 0, 'Повелитель небес Тенистая Роща to Player');
INSERT INTO `creature_text` VALUES (119952, 1, 0, 'Вы падете перед мощью Легиона!', 12, 0, 100, 0, 0, 81715, 130740, 0, 0, 0, 'Страж Скверны - разрушитель to Player');
INSERT INTO `creature_text` VALUES (119952, 1, 0, 'Теперь ваши реликвии вас не спасут!', 12, 0, 100, 53, 0, 0, 130743, 0, 0, 0, 'Страж Скверны - разрушитель to Player');
INSERT INTO `creature_text` VALUES (119955, 0, 0, '%s издает громкий клекот при вашем появлении.', 16, 0, 100, 0, 0, 0, 128465, 0, 0, 0, 'Авиаш to Player');
INSERT INTO `creature_text` VALUES (119978, 0, 0, '|TInterface\\Icons\\ability_fixated_state_purple:20|t%s |cFFFF0404|Hspell:238674|h[сосредотачивает внимание]|h|r на вас!', 42, 0, 100, 0, 0, 0, 130791, 0, 0, 0, 'Гремучий плеточник to Player');
INSERT INTO `creature_text` VALUES (119982, 0, 0, 'Скорее! Моим братьям и сестрам нужна помощь!', 12, 0, 100, 0, 0, 82140, 128562, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (119982, 1, 0, 'Если это отвлекающий маневр, то где же главная цель? Надо спросить Авиану!', 12, 0, 100, 0, 0, 82141, 128613, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (119982, 2, 0, 'Мои когти разорвут тебя!', 12, 0, 100, 0, 0, 80818, 126365, 0, 0, 0, 'Тизали Ворона to 0');
INSERT INTO `creature_text` VALUES (120028, 3, 0, 'Поберегись!', 14, 0, 100, 5, 0, 83023, 127855, 59282, 59282, 0, 'Маг Кирин-Тора to Player');
INSERT INTO `creature_text` VALUES (120036, 0, 0, 'Враги познают мой гнев!', 12, 0, 100, 0, 0, 80815, 126362, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120036, 0, 1, 'I will cover, supreme druid!', 12, 0, 100, 0, 0, 80826, 126374, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120036, 0, 2, 'I never mind getting my claws dirty.', 12, 0, 100, 0, 0, 80823, 126371, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120036, 0, 3, 'Мои когти разорвут тебя!', 12, 0, 100, 0, 0, 80818, 126365, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120041, 0, 0, 'Устроим врагам взбучку!', 12, 0, 100, 0, 0, 80816, 126363, 0, 0, 0, 'Тизали Ворона to 0');
INSERT INTO `creature_text` VALUES (120041, 0, 1, 'Мои когти разорвут тебя!', 12, 0, 100, 0, 0, 80818, 126365, 0, 0, 0, 'Друид-ворона to 0');
INSERT INTO `creature_text` VALUES (120041, 0, 2, 'I never mind getting my claws dirty.', 12, 0, 100, 0, 0, 80823, 126371, 0, 0, 0, 'Тизали Ворона to Завоеватель из армии Горести Скверны');
INSERT INTO `creature_text` VALUES (120041, 0, 3, 'Что, не ждали?!', 12, 0, 100, 4, 0, 82152, 128743, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120041, 1, 0, 'Ну же, скорее! Надо спасти Голдринна!', 12, 0, 100, 0, 0, 82149, 128734, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120041, 2, 0, 'Порталы должны быть где-то рядом... Гляди в оба!', 12, 0, 100, 0, 0, 82150, 128741, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120041, 3, 0, 'Представь – демон выходит из портала, и его тут же опутывает лоза! Потрясающе!', 12, 0, 100, 11, 0, 82151, 128742, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120041, 4, 0, 'Голдринн прав. Нужно выяснить, кто стоит за этими атаками! Нельзя терять время!', 12, 0, 100, 0, 0, 82155, 128835, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120054, 3, 0, 'Если эта связь продержится еще какое-то время, Азсуна будет для нас потеряна!', 14, 0, 100, 0, 0, 0, 128840, 25466, 25466, 0, 'Призрачная защитница');
INSERT INTO `creature_text` VALUES (120062, 0, 0, 'They brought back my cousin\'s body from the Broken Shore last week. I expect a lot more to follow.', 12, 0, 100, 0, 0, 0, 128757, 0, 0, 0, 'Дженни Уинслоу to player');
INSERT INTO `creature_text` VALUES (120062, 1, 0, 'Will he? How exactly will he keep an endless army of demons from invading and killing everyone?', 12, 0, 100, 0, 0, 0, 129044, 0, 0, 0, 'Дженни Уинслоу to player');
INSERT INTO `creature_text` VALUES (120064, 0, 0, 'At least we\'re safe here in Stormwind. King Wrynn will keep the city safe.', 12, 0, 100, 0, 0, 0, 128760, 0, 0, 0, 'Рой Питтс to player');
INSERT INTO `creature_text` VALUES (120065, 0, 0, 'I\'m King Anduin! I\'m gonna kill every demon I see!', 12, 0, 100, 0, 0, 0, 128779, 0, 0, 0, 'Дугги to player');
INSERT INTO `creature_text` VALUES (120065, 1, 0, 'Ага, папа тоже так говорит. Ну ладно, я король Вариан. Трепещите, демоны!', 12, 0, 100, 0, 0, 0, 128783, 0, 0, 0, 'Дугги to player');
INSERT INTO `creature_text` VALUES (120066, 0, 0, 'Будь лучше Варианом! Он был куда сильнее.', 12, 0, 100, 0, 0, 0, 128782, 0, 0, 0, 'Кэрин to player');
INSERT INTO `creature_text` VALUES (120070, 3, 0, 'I have already knocked out all the necessary information from this bastard. I know where his commander is!', 12, 0, 100, 1, 0, 82156, 128854, 12235, 12235, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120074, 1, 0, 'Нить твоей жизни подходит к концу.', 12, 0, 100, 0, 0, 55182, 103523, 0, 0, 0, 'Ползун Скверны - ткач хаоса to Player');
INSERT INTO `creature_text` VALUES (120074, 1, 0, 'Подчинись воле Саргераса.', 12, 0, 100, 0, 0, 55181, 103522, 0, 0, 0, 'Ползун Скверны - ткач хаоса to Player');
INSERT INTO `creature_text` VALUES (120078, 1, 0, 'Ты пойдешь на корм паукам!', 12, 0, 100, 0, 0, 55029, 101524, 0, 0, 0, 'Ползунья Скверны - праматерь to Player');
INSERT INTO `creature_text` VALUES (120084, 0, 0, 'Этот демон отведает моего клинка!', 12, 0, 100, 0, 0, 82157, 128867, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120084, 0, 1, 'Мои когти разорвут тебя!', 12, 0, 100, 0, 0, 80818, 126365, 0, 0, 0, 'Тизали Ворона to 0');
INSERT INTO `creature_text` VALUES (120084, 0, 2, 'Ха! С нами никому не справиться!', 12, 0, 100, 0, 0, 80824, 126372, 0, 0, 0, 'Тизали Ворона to Завоеватель из армии Горести Скверны');
INSERT INTO `creature_text` VALUES (120084, 1, 0, 'Этого должно хватить! Так, как его?.. Катаракт? Катарсис? Язык сломаешь!', 12, 0, 100, 0, 0, 83155, 129717, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120084, 2, 0, 'Встретимся в Роще Снов, верховный друид. Спасибо за компанию!', 12, 0, 100, 1, 0, 82158, 128884, 0, 0, 0, 'Тизали Ворона to Player');
INSERT INTO `creature_text` VALUES (120088, 1, 0, 'Твой огонь потушен!', 12, 0, 100, 0, 0, 0, 128895, 0, 0, 0, 'Чемпион Аквалит to Player');
INSERT INTO `creature_text` VALUES (120088, 3, 0, 'Идем, мой маленький герой, пусть прилив правосудия захлестнет наших врагов!', 12, 0, 100, 0, 0, 0, 128901, 80, 80, 0, 'Чемпион Аквалит to Player');
INSERT INTO `creature_text` VALUES (120088, 4, 0, 'Захлебнись!', 12, 0, 100, 0, 0, 0, 128896, 11341, 11341, 0, 'Чемпион Аквалит to Player');
INSERT INTO `creature_text` VALUES (120088, 4, 0, 'Прочь, мерзкая тварь!', 12, 0, 100, 0, 0, 0, 128897, 18455, 18455, 0, 'Чемпион Аквалит to Player');
INSERT INTO `creature_text` VALUES (120088, 4, 0, 'Пусть вода очистит тебя!', 12, 0, 100, 0, 0, 0, 128894, 16892, 16892, 0, 'Чемпион Аквалит to Player');
INSERT INTO `creature_text` VALUES (120093, 3, 0, 'Эти твари... Я не смогла...', 12, 0, 100, 0, 0, 0, 107754, 30504, 30504, 0, 'Разжигательница войны Ярости Земли to Player');
INSERT INTO `creature_text` VALUES (120095, 0, 0, 'I will destroy all your Ancients to the one!', 12, 0, 100, 0, 0, 82715, 128889, 0, 0, 0, 'Командующий Катит\'ракс to Player');
INSERT INTO `creature_text` VALUES (120095, 1, 0, 'Это... невозможно...', 12, 0, 100, 0, 0, 83269, 133175, 0, 0, 0, 'Командующий Катит\'ракс to Тизали Ворона');
INSERT INTO `creature_text` VALUES (120104, 0, 0, 'Я вернусь в Рощу Снов. Спасибо!', 12, 0, 100, 2, 0, 0, 128876, 0, 0, 0, 'Раненый друид to Player');
INSERT INTO `creature_text` VALUES (120104, 0, 1, 'Командира демонов надо остановить. Я в тебя верю!', 12, 0, 100, 2, 0, 0, 128875, 0, 0, 0, 'Раненый друид to Player');
INSERT INTO `creature_text` VALUES (120104, 0, 2, 'Спасибо тебе. Я опасался худшего.', 12, 0, 100, 2, 0, 0, 128874, 0, 0, 0, 'Раненый друид to Player');
INSERT INTO `creature_text` VALUES (120105, 3, 0, 'I\'m alive? Thank you friend! Many thanks!', 12, 0, 100, 0, 0, 0, 128883, 1134, 1134, 0, 'Житель Нар\'таласа to Житель Нар\'таласа');
INSERT INTO `creature_text` VALUES (120105, 3, 0, 'Спасибо тебе!', 12, 0, 100, 0, 0, 0, 136433, 953, 953, 0, 'Житель Нар\'таласа to Житель Нар\'таласа');
INSERT INTO `creature_text` VALUES (120106, 0, 0, 'I was so weak - this web was pulling the essence out of me.', 12, 0, 100, 0, 0, 0, 128879, 0, 0, 0, 'Жительница Нар\'таласа to Жительница Нар\'таласа');
INSERT INTO `creature_text` VALUES (120106, 0, 1, 'I\'m alive? Thank you friend! Many thanks!', 12, 0, 100, 0, 0, 0, 128883, 0, 0, 0, 'Жительница Нар\'таласа to Жительница Нар\'таласа');
INSERT INTO `creature_text` VALUES (120108, 0, 0, 'Древний связывающий осколок начинает излучать огромную силу.', 16, 0, 100, 0, 0, 0, 128908, 0, 0, 0, 'Археолог Карла to Player');
INSERT INTO `creature_text` VALUES (120125, 3, 0, 'Дай малышу примоститься!', 12, 0, 100, 0, 0, 0, 93337, 74, 74, 0, 'Даглоп to Player');
INSERT INTO `creature_text` VALUES (120125, 3, 0, 'Прислужник! Получай.', 12, 0, 100, 0, 0, 0, 128916, 33281, 55199, 0, 'Даглоп to Player');
INSERT INTO `creature_text` VALUES (120128, 0, 0, 'Твоя доблесть впечатляет, $p. Вижу, Громораан умеет выбирать себе защитников. Я склонюсь перед ним.', 12, 0, 100, 1, 0, 0, 128981, 0, 0, 0, 'Рохаш to Рохаш');
INSERT INTO `creature_text` VALUES (120129, 0, 0, 'Сегодня ветры принесли тебе удачу. Я изменю свое мнение о Громораане.', 12, 0, 100, 1, 0, 0, 129356, 0, 0, 0, 'Незир to Незир');
INSERT INTO `creature_text` VALUES (120130, 0, 0, 'Твое усердие удивляет меня. Надеюсь, Громораан оправдает твое доверие. Я гарантирую ему свою лояльность.', 12, 0, 100, 1, 0, 0, 129357, 0, 0, 0, 'Аншал to Аншал');
INSERT INTO `creature_text` VALUES (120136, 1, 0, 'Вам не уйти от Легиона!', 12, 0, 100, 0, 0, 0, 128937, 0, 0, 0, 'Подозрительный житель to Player');
INSERT INTO `creature_text` VALUES (120136, 1, 0, 'Вы что, не видите? Легион уже победил!', 12, 0, 100, 0, 0, 0, 128939, 0, 0, 0, 'Подозрительный житель to Player');
INSERT INTO `creature_text` VALUES (120136, 1, 0, 'За Легион!', 12, 0, 100, 0, 0, 0, 135850, 0, 0, 0, 'Подозрительный житель to Player');
INSERT INTO `creature_text` VALUES (120136, 3, 0, 'Смерть всем, кто бросил вызов Легиону!', 12, 0, 100, 0, 0, 0, 128941, 35259, 35259, 0, 'Подозрительный житель to Player');
INSERT INTO `creature_text` VALUES (120136, 4, 0, 'I ... I could not help myself.', 12, 0, 100, 0, 0, 0, 128988, 6045, 6045, 0, 'Подозрительный житель');
INSERT INTO `creature_text` VALUES (120136, 4, 0, 'I wanted to stop, but I could not ... As if I was someone\'s puppet.', 12, 0, 100, 0, 0, 0, 128990, 5887, 5887, 0, 'Подозрительный житель');
INSERT INTO `creature_text` VALUES (120136, 4, 0, 'Вода... Не давайте никому ее пить!', 12, 0, 100, 0, 0, 0, 128991, 18387, 18387, 0, 'Подозрительный житель');
INSERT INTO `creature_text` VALUES (120136, 4, 0, 'Они проникли в мой разум... завладели мыслями!', 12, 0, 100, 0, 0, 0, 128989, 16894, 16894, 0, 'Подозрительный житель');
INSERT INTO `creature_text` VALUES (120139, 0, 0, 'Король Седогрив сказал, что ты придешь. Пожалуйста, проходи.', 12, 0, 100, 0, 0, 0, 128746, 0, 0, 0, 'Королевские стражник to player');
INSERT INTO `creature_text` VALUES (120139, 1, 0, 'Не вышло? Король Седогрив не обрадуется, но не расстраивайся: эта задача вряд ли кому-то по плечу.', 12, 0, 100, 0, 0, 0, 128928, 0, 0, 0, 'Королевские стражник to player');
INSERT INTO `creature_text` VALUES (120144, 0, 1, 'Смерть всем, кто бросил вызов Легиону!', 12, 0, 100, 0, 0, 0, 128941, 0, 0, 0, 'Подозрительная жительница to Player');
INSERT INTO `creature_text` VALUES (120144, 0, 3, 'За моих повелителей! За Легион!', 12, 0, 100, 0, 0, 0, 128936, 0, 0, 0, 'Подозрительная жительница to Player');
INSERT INTO `creature_text` VALUES (120144, 0, 4, 'Вам не уйти от Легиона!', 12, 0, 100, 0, 0, 0, 128937, 0, 0, 0, 'Подозрительная жительница to Player');
INSERT INTO `creature_text` VALUES (120144, 0, 5, 'Вы что, не видите? Легион уже победил!', 12, 0, 100, 0, 0, 0, 128939, 0, 0, 0, 'Подозрительная жительница to Player');
INSERT INTO `creature_text` VALUES (120144, 1, 2, 'I wanted to stop, but I couldn’t ... As if I was someone’s puppet.', 12, 0, 100, 0, 0, 0, 128990, 0, 0, 0, 'Подозрительная жительница to 0');
INSERT INTO `creature_text` VALUES (120144, 1, 4, 'Где я? И кто ты?', 12, 0, 100, 0, 0, 0, 128993, 0, 0, 0, 'Подозрительная жительница to 0');
INSERT INTO `creature_text` VALUES (120144, 1, 5, 'Вода... Не давайте никому ее пить!', 12, 0, 100, 0, 0, 0, 128991, 0, 0, 0, 'Подозрительная жительница to 0');
INSERT INTO `creature_text` VALUES (120147, 0, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Злобный алхимик to Player');
INSERT INTO `creature_text` VALUES (120156, 0, 0, 'I ... I am free ... Thank you.', 12, 0, 100, 0, 0, 0, 129069, 0, 0, 0, 'Зараженный Скверной страж крепости to Player');
INSERT INTO `creature_text` VALUES (120157, 0, 0, 'На нас напали, мы несем тяжелые потери. Повсюду открылись порталы Легиона. Нам нужно подкрепление!', 12, 0, 100, 54, 0, 82741, 125200, 0, 0, 0, 'Вандел to Player');
INSERT INTO `creature_text` VALUES (120161, 0, 0, 'Шлем господства позволяет видеть многое.', 12, 0, 100, 0, 0, 82970, 128943, 0, 0, 0, 'Король-лич to Player');
INSERT INTO `creature_text` VALUES (120161, 1, 0, 'Мне было видение... тысячи живых мертвецов, бредущих через Нордскол в поисках великой силы.', 12, 0, 100, 0, 0, 82971, 128944, 0, 0, 0, 'Король-лич to Player');
INSERT INTO `creature_text` VALUES (120161, 2, 0, 'Но среди бескрайних ледяных просторов я не чувствую ничего.', 12, 0, 100, 0, 0, 82972, 128945, 0, 0, 0, 'Король-лич to Player');
INSERT INTO `creature_text` VALUES (120161, 3, 0, 'Отправляйся на самый север Ледяной Короны и все увидишь.', 12, 0, 100, 0, 0, 82973, 128946, 0, 0, 0, 'Король-лич to Player');
INSERT INTO `creature_text` VALUES (120162, 0, 0, 'Будь начеку и береги карманы.', 12, 0, 100, 1, 0, 0, 120221, 0, 0, 0, 'Лора Штерн to Player');
INSERT INTO `creature_text` VALUES (120162, 0, 0, 'И передай всем, чтобы с ним обходились как можно лучше. А не то мы все можем наутро проснуться мертвыми. Смекаешь, о чем я?', 12, 0, 100, 5, 0, 0, 120256, 0, 0, 0, 'Лора Штерн to Player');
INSERT INTO `creature_text` VALUES (120197, 1, 0, 'Смерть чужакам!', 12, 0, 100, 0, 0, 65909, 116708, 0, 0, 0, 'Присягнувшая Скверне - зверолов to Player');
INSERT INTO `creature_text` VALUES (120198, 3, 0, 'Да что случилось с моими курами?!', 12, 0, 100, 5, 0, 53431, 129058, 128, 128, 0, 'Жительница Браденсбрука to Player');
INSERT INTO `creature_text` VALUES (120202, 3, 0, 'А, это ты, $n. Тебя-то я и ищу. Как тебе мой новый наряд? А, да кого я обманываю? Скоро это перестанет иметь значение.', 12, 0, 100, 1, 0, 0, 129066, 1437, 1437, 0, 'Гимбл Шестеренкинс to Player');
INSERT INTO `creature_text` VALUES (120208, 1, 0, 'Эти припасы принадлежат Легиону!', 12, 0, 100, 0, 0, 0, 129102, 0, 0, 0, 'Райеш to Player');
INSERT INTO `creature_text` VALUES (120208, 2, 0, 'Легион вас уничтожит...', 12, 0, 100, 0, 0, 0, 129103, 0, 0, 0, 'Райеш to Player');
INSERT INTO `creature_text` VALUES (120219, 0, 0, 'Сюда!', 14, 0, 100, 0, 0, 82134, 143887, 0, 0, 0, 'Тизали Ворона to 1312');
INSERT INTO `creature_text` VALUES (120219, 1, 0, 'Авиана, прошу, помоги!', 14, 0, 100, 0, 0, 82135, 128409, 0, 0, 0, 'Тизали Ворона to 1312');
INSERT INTO `creature_text` VALUES (120221, 1, 0, 'Приветствую тебя!', 12, 0, 100, 0, 0, 0, 127444, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (120236, 2, 0, 'Легион выстоит...', 12, 0, 100, 0, 0, 0, 129706, 0, 0, 0, 'Сиаррикс to player');
INSERT INTO `creature_text` VALUES (120244, 0, 0, 'Ты иди вперед, а я пойду в противоположную сторону. Потом встретимся.', 12, 0, 100, 0, 0, 81644, 126085, 0, 0, 0, 'Магата Зловещий Тотем to Player');
INSERT INTO `creature_text` VALUES (120244, 1, 0, 'И как ты собираешься использовать Камень Рока, глупое дитя, если даже портал открыть не можешь?', 12, 0, 100, 463, 0, 81645, 126086, 0, 0, 0, 'Магата Зловещий Тотем to Player');
INSERT INTO `creature_text` VALUES (120245, 0, 0, 'От племени Зловещего Тотема так просто не избавиться, дитя.', 12, 0, 100, 1, 0, 81649, 127689, 0, 0, 0, 'Магата Зловещий Тотем to Player');
INSERT INTO `creature_text` VALUES (120264, 3, 0, 'Кажется, Генн и Велен говорят обо мне, но я не могу разобрать слов...', 12, 0, 100, 1, 0, 83238, 129213, 3469, 3469, 0, 'Андуин Ринн to Player');
INSERT INTO `creature_text` VALUES (120267, 0, 0, 'Тебе не совладать с этой силой!', 12, 0, 100, 0, 0, 82686, 129571, 0, 0, 0, 'Барастагакс Огненный Палач to Player');
INSERT INTO `creature_text` VALUES (120268, 0, 0, 'Кажется, Генн и Велен говорят обо мне, но я не могу разобрать слов...', 12, 0, 100, 0, 0, 0, 129213, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (120268, 1, 0, 'Спасибо. Теперь я знаю, как мне осознать тяжесть бремени отца.', 12, 0, 100, 0, 0, 0, 128819, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (120268, 2, 0, 'I will go to the Broken Shore to experience the greatness of his sacrifice.', 12, 0, 100, 0, 0, 0, 128822, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (120302, 1, 0, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 57008, 105128, 0, 0, 0, 'Эредар-чародей to Player');
INSERT INTO `creature_text` VALUES (120303, 1, 0, 'Почувствуй мою ненависть!', 12, 0, 100, 1, 0, 57008, 102138, 0, 0, 0, 'Страж гнева - клинок хаоса to Player');
INSERT INTO `creature_text` VALUES (120305, 3, 0, 'Нам нужны ответы! Мы не знаем, чего ждать.', 12, 0, 100, 1, 0, 0, 129375, 26345, 26345, 0, 'Обеспокоенная горожанка to Стражница Кирин-Тора');
INSERT INTO `creature_text` VALUES (120312, 0, 0, 'Ну и бардак тут устроил Иллидан! Готовьтесь... сейчас будем прорываться.', 14, 0, 100, 0, 0, 80487, 127932, 0, 0, 0, 'Майев Песнь Теней to Бес адского огня');
INSERT INTO `creature_text` VALUES (120312, 1, 0, 'I will continue the search for Illidan, and you will deal with these monsters.', 14, 0, 100, 0, 0, 80488, 127933, 0, 0, 0, 'Майев Песнь Теней to Майев Песнь Теней');
INSERT INTO `creature_text` VALUES (120313, 0, 0, 'Без силовых ядер у них ничего не выйдет!', 12, 0, 100, 0, 0, 0, 129400, 0, 0, 0, 'Раненая шаманка to Player');
INSERT INTO `creature_text` VALUES (120313, 0, 1, 'Эредары ничего не добьются, только разозлят стихии!', 12, 0, 100, 0, 0, 0, 129401, 0, 0, 0, 'Раненая шаманка to Player');
INSERT INTO `creature_text` VALUES (120313, 0, 2, 'Легион хочет подчинить себе стихии Азерота... Демоны зашли слишком далеко!', 12, 0, 100, 0, 0, 0, 129399, 0, 0, 0, 'Раненая шаманка to Player');
INSERT INTO `creature_text` VALUES (120321, 1, 0, 'Еще один пленник для моей тюрьмы.', 12, 0, 100, 0, 0, 55363, 97909, 0, 0, 0, 'Ужасный тюремщик душ to Player');
INSERT INTO `creature_text` VALUES (120361, 0, 0, '|TINTERFACE\\\\ICONS\\\\SPELL_FIRE_FELFLAMERING.BLP:20|t Антеон - сокрушитель Света использует заклинание |cFFFF0000|Hspell:250880|h[Бомбардировка Скверны]|h|r! Оставайтесь в области \"Барьера Света\"!', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Антеон - сокрушитель Света');
INSERT INTO `creature_text` VALUES (120361, 1, 0, '|TINTERFACE\\ICONS\\SPELL_FIRE_FELFLAMERING.BLP:20|t Антеон - сокрушитель Света использует заклинание |cFFFF0000|Hspell:250880|h[Бомбардировка Скверны]|h|r! Оставайтесь в области \"Барьера Света\"!', 41, 0, 100, 0, 0, 23504, 136540, 0, 0, 0, 'Антеон - сокрушитель Света');
