INSERT INTO `creature_text` VALUES (120368, 3, 0, 'Нас... не остановить...', 14, 0, 100, 0, 0, 82201, 129461, 1869813, 1869813, 0, 'Сонм страданий to Реактор душ');
INSERT INTO `creature_text` VALUES (120368, 3, 0, 'Тьма заберет тебя!', 14, 0, 100, 0, 0, 82190, 129443, 1772482, 1772482, 0, 'Сонм страданий to Реактор душ');
INSERT INTO `creature_text` VALUES (120392, 1, 0, 'Легион властвует в небе!', 12, 0, 100, 0, 0, 82699, 129511, 0, 0, 0, 'Хранительница Роя - надзирательница to Player');
INSERT INTO `creature_text` VALUES (120393, 1, 0, 'Все, что ты знаешь о Легионе, – лишь тень нашей истинной силы.', 12, 0, 100, 0, 0, 88036, 129539, 0, 0, 0, 'Осадный мастер Вораан');
INSERT INTO `creature_text` VALUES (120393, 1, 0, 'Твой мир обречен сгореть в пламени повелителя.', 12, 0, 100, 0, 0, 88033, 129541, 0, 0, 0, 'Осадный мастер Вораан');
INSERT INTO `creature_text` VALUES (120410, 0, 0, 'Свет отрекся от нас!', 14, 0, 100, 5, 0, 0, 120825, 0, 0, 0, 'Вестница доказанного рока');
INSERT INTO `creature_text` VALUES (120410, 3, 0, 'Взгляните в небо! Наконец-то нам будет явлена истина!', 14, 0, 100, 1, 0, 0, 129523, 1159261, 1159261, 0, 'Вестница доказанного рока');
INSERT INTO `creature_text` VALUES (120410, 3, 0, 'Ослепительный изумрудный огонь выжжет нашу порчу!', 14, 0, 100, 5, 0, 0, 120827, 5444, 5444, 0, 'Вестник доказанного рока');
