INSERT INTO `creature_text` VALUES (120411, 3, 0, 'Взгляните в небо! Наконец-то нам будет явлена истина!', 14, 0, 100, 1, 0, 0, 129523, 3932, 3932, 0, 'Вестник доказанного рока');
INSERT INTO `creature_text` VALUES (120411, 3, 0, 'Свет отрекся от нас!', 14, 0, 100, 5, 0, 0, 120825, 3476, 3476, 0, 'Вестник доказанного рока');
INSERT INTO `creature_text` VALUES (120411, 3, 0, 'Силы за гранью нашего понимания признали нас недостойными!', 14, 0, 100, 5, 0, 0, 120826, 541718, 541718, 0, 'Вестница доказанного рока');
INSERT INTO `creature_text` VALUES (120429, 3, 0, '...и во тьме восторжествует Свет.', 12, 0, 100, 1, 0, 0, 129563, 417315, 417315, 0, 'Жительница Экзодара to Player');
INSERT INTO `creature_text` VALUES (120429, 3, 0, 'Каким бы тревожным ни казалось все происходящее, сквозь тьму пробивается луч надежды.', 12, 0, 100, 0, 0, 0, 129560, 10235, 10235, 0, 'Жительница Экзодара to Player');
INSERT INTO `creature_text` VALUES (120429, 3, 0, 'Ремонт почти закончен.', 12, 0, 100, 0, 0, 0, 129562, 10054620, 10054620, 0, 'Житель Экзодара to Player');
INSERT INTO `creature_text` VALUES (120433, 3, 0, '...и во тьме восторжествует Свет.', 12, 0, 100, 1, 0, 0, 129563, 537829, 537829, 0, 'Жительница Экзодара to Player');
