INSERT INTO `creature_text` VALUES (120488, 4, 0, 'Локальные угрозы отсутствуют. Выношу благодарность.', 12, 0, 100, 0, 0, 0, 128688, 2971, 2971, 0, 'Защитный узел');
INSERT INTO `creature_text` VALUES (120529, 0, 0, 'Довольно!', 14, 0, 100, 1, 0, 86511, 129681, 0, 0, 0, 'Верховный экзарх Туралион to 0');
