INSERT INTO `creature_text` VALUES (120529, 2, 0, 'Только объединив усилия мы сможем хотя бы попытаться победить Легион.\n', 12, 0, 100, 1, 0, 86188, 129692, 0, 0, 0, 'Верховный экзарх Туралион to 0');
INSERT INTO `creature_text` VALUES (120529, 3, 0, 'Давай поговорим... нам нужно многое обсудить.', 12, 0, 100, 1, 0, 37963, 130543, 46, 46, 0, 'Верховный экзарх Туралион to Player');
INSERT INTO `creature_text` VALUES (120533, 0, 0, 'Л\'\'ура...', 12, 0, 100, 603, 0, 87898, 130106, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (120533, 1, 0, 'Пришло время взять судьбу в свои руки.', 12, 0, 100, 603, 0, 87869, 135162, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (120533, 2, 0, 'Спасибо тебе, Фария.', 12, 0, 100, 603, 0, 87924, 133000, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (120598, 0, 0, 'Жалкое создание! Как смеешь ты сюда соваться?', 12, 0, 100, 0, 0, 86165, 130499, 0, 0, 0, 'Зет\'\'тал to Player');
INSERT INTO `creature_text` VALUES (120598, 1, 0, 'Кости! Такие хрустящие, такие вкусные!', 12, 0, 100, 0, 0, 86163, 130497, 0, 0, 0, 'Зет\'тал to Player');
INSERT INTO `creature_text` VALUES (120601, 0, 0, 'Твоя плоть будет вечно питать Скверну!', 12, 0, 100, 0, 0, 86168, 130502, 0, 0, 0, 'Магралот to Player');
INSERT INTO `creature_text` VALUES (120602, 0, 0, 'I will devour your soul.', 12, 0, 100, 0, 0, 86162, 130496, 0, 0, 0, 'Кар\'\'ааз to Player');
INSERT INTO `creature_text` VALUES (120603, 1, 0, 'Мы не остановимся...', 12, 0, 100, 0, 0, 86762, 134577, 0, 0, 0, 'Фанатичная тень to Player');
INSERT INTO `creature_text` VALUES (120603, 1, 0, 'Ты поплатишься за трусость!', 12, 0, 100, 0, 0, 86754, 134564, 0, 0, 0, 'Фанатичная тень to Player');
INSERT INTO `creature_text` VALUES (120603, 3, 0, 'Ты... останешься здесь... навечно...', 12, 0, 100, 0, 0, 86764, 134489, 0, 0, 0, 'Фанатичная тень to 120693');
INSERT INTO `creature_text` VALUES (120604, 1, 0, 'Мы не остановимся...', 12, 0, 100, 0, 0, 86744, 134577, 0, 0, 0, 'Фанатичная тень to 120693');
INSERT INTO `creature_text` VALUES (120604, 1, 0, 'Прими Саргераса!', 12, 0, 100, 0, 0, 86757, 134508, 0, 0, 0, 'Фанатичная тень to Player');
INSERT INTO `creature_text` VALUES (120604, 3, 0, 'Спасайся... пока можешь.', 12, 0, 100, 0, 0, 87170, 134491, 5, 15, 0, 'Фанатичная тень to 120693');
INSERT INTO `creature_text` VALUES (120608, 0, 0, 'Нужно уходить, пока еще не поздно!', 12, 0, 100, 5, 0, 0, 133521, 0, 0, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (120608, 0, 1, 'Мы еще повоюем!', 12, 0, 100, 5, 0, 0, 133522, 0, 0, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (120608, 0, 2, 'I ... I need to get back to mine as soon as possible.', 12, 0, 100, 5, 0, 0, 133523, 0, 0, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (120608, 0, 3, 'Большое спасибо. Дальше я справлюсь.', 12, 0, 100, 5, 0, 0, 133519, 0, 0, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (120632, 3, 0, 'I\'ll just hide again and wait until it\'s over.', 12, 0, 100, 0, 0, 0, 129716, 33111, 33111, 0, 'Мурилло Сияющий Самоцвет to Player');
INSERT INTO `creature_text` VALUES (120663, 0, 0, 'Сочный мозг. Хрустящие кости.', 12, 0, 100, 0, 0, 55150, 96651, 0, 0, 0, 'Страж ужаса - призыватель огня to Player');
INSERT INTO `creature_text` VALUES (120663, 0, 1, 'Ужаснись!', 12, 0, 100, 0, 0, 55142, 96645, 0, 0, 0, 'Страж ужаса - призыватель огня to Player');
INSERT INTO `creature_text` VALUES (120676, 0, 0, 'Было ваше, стало наше! Хе-хе-хе!', 12, 0, 100, 0, 0, 53433, 137510, 0, 0, 0, 'Вонючий алхимик to Player');
INSERT INTO `creature_text` VALUES (120677, 0, 0, 'Будьте вы прокляты!', 12, 0, 100, 0, 0, 53433, 137507, 0, 0, 0, 'Проклятый разоритель to Player');
INSERT INTO `creature_text` VALUES (120683, 0, 0, 'Перед смертью ты выдашь мне все секреты.', 12, 0, 100, 0, 0, 55373, 102154, 0, 0, 0, 'Дознаватель с \"Церберакса\" to 110340');
INSERT INTO `creature_text` VALUES (120683, 0, 1, 'От меня не спрячешься.', 12, 0, 100, 0, 0, 55374, 102155, 0, 0, 0, 'Дознаватель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120683, 0, 2, 'Столько вопросов, так мало времени.', 12, 0, 100, 0, 0, 55374, 102158, 0, 0, 0, 'Дознаватель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120683, 0, 3, 'I see you.', 12, 0, 100, 0, 0, 55378, 132218, 0, 0, 0, 'Дознаватель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120684, 0, 0, 'Перед смертью ты выдашь мне все секреты.', 12, 0, 100, 0, 0, 55373, 102154, 0, 0, 0, 'Наблюдатель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120684, 0, 1, 'I will break your will.', 12, 0, 100, 0, 0, 55379, 102160, 0, 0, 0, 'Наблюдатель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120684, 0, 2, 'Мои глаза видят все.', 12, 0, 100, 0, 0, 55378, 102159, 0, 0, 0, 'Наблюдатель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120684, 0, 3, 'Это будет познавательно.', 12, 0, 100, 0, 0, 55375, 102156, 0, 0, 0, 'Наблюдатель с \"Церберакса\" to Player');
INSERT INTO `creature_text` VALUES (120704, 0, 0, 'Как ты смеешь?!', 12, 0, 100, 0, 0, 57006, 129829, 0, 0, 0, 'Страж Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120704, 0, 1, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 57005, 105125, 0, 0, 0, 'Страж Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120704, 0, 2, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 57004, 105124, 0, 0, 0, 'Страж Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120722, 0, 0, 'Великий мастер! Сюда.', 12, 0, 100, 0, 0, 82391, 128485, 0, 0, 0, 'Мастер Бу to player');
INSERT INTO `creature_text` VALUES (120722, 1, 0, 'Великий мастер, есть идея...', 12, 0, 100, 0, 0, 82401, 128495, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120723, 0, 0, 'Мы очистим ваш мир!', 12, 0, 100, 0, 0, 56994, 105114, 0, 0, 0, 'Фанатичка с Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120723, 0, 1, 'Вкуси магии Скверны!', 12, 0, 100, 0, 0, 57008, 105128, 0, 0, 0, 'Фанатичка с Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120723, 0, 2, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 57007, 105127, 0, 0, 0, 'Фанатичка с Плато аннигиляров to player');
INSERT INTO `creature_text` VALUES (120725, 0, 0, 'Только такое дело... под действием яйцевзрывателя иногда из яиц сразу вылупляются кракены. Но это же мизерная цена за удобство и технологичность?', 12, 0, 100, 0, 0, 0, 129887, 0, 0, 0, 'Зипп Свистоплюх to Player');
INSERT INTO `creature_text` VALUES (120726, 0, 0, 'Идем со мной к берегу.', 12, 0, 100, 0, 0, 0, 129897, 0, 0, 0, 'Говорящий с водой Рюли to Player');
INSERT INTO `creature_text` VALUES (120726, 1, 0, 'Сейчас я буду говорить с водой...', 12, 0, 100, 0, 0, 0, 129898, 0, 0, 0, 'Говорящий с водой Рюли to Player');
INSERT INTO `creature_text` VALUES (120726, 2, 0, 'Как интересно.', 12, 0, 100, 0, 0, 0, 129899, 0, 0, 0, 'Говорящий с водой Рюли to Player');
INSERT INTO `creature_text` VALUES (120735, 3, 0, 'Благодарю тебя за спасение!', 12, 0, 100, 0, 0, 0, 129860, 122, 122, 0, 'Пленный друид to Player');
INSERT INTO `creature_text` VALUES (120735, 3, 0, 'Демонам не удержать нас в цепях.', 12, 0, 100, 0, 0, 0, 129862, 110, 110, 0, 'Пленный друид to Player');
INSERT INTO `creature_text` VALUES (120735, 3, 0, 'Я должна вернуться в лагерь!', 12, 0, 100, 66, 0, 0, 129861, 177, 177, 0, 'Пленный друид to Player');
INSERT INTO `creature_text` VALUES (120738, 0, 0, 'Оружие к бою.', 12, 0, 100, 0, 0, 86120, 129985, 0, 0, 0, 'Верховный экзарх Туралион to Верховный экзарх Туралион');
INSERT INTO `creature_text` VALUES (120738, 1, 0, 'Держимся вместе, что бы ни случилось!', 12, 0, 100, 0, 0, 86121, 130504, 0, 0, 0, 'Верховный экзарх Туралион to Верховный экзарх Туралион');
INSERT INTO `creature_text` VALUES (120747, 0, 0, 'Спасибо за помощь, великий мастер $p. Увы, Бань Лу здесь нет. Я видел, как он ушел на юг, но дальнейшая его судьба мне неизвестна.', 12, 0, 100, 2, 0, 0, 129893, 0, 0, 0, 'Командир Шэнь Ли to Player');
INSERT INTO `creature_text` VALUES (120749, 1, 0, 'I will break your will.', 12, 0, 100, 0, 0, 55379, 102160, 0, 0, 0, 'Гнусный мучитель to Player');
INSERT INTO `creature_text` VALUES (120749, 1, 0, 'Мои глаза видят все.', 12, 0, 100, 0, 0, 55378, 102159, 0, 0, 0, 'Гнусный мучитель to Player');
INSERT INTO `creature_text` VALUES (120749, 2, 0, 'От Легиона... не убежишь...', 12, 0, 100, 0, 0, 0, 101125, 0, 0, 0, 'Гнусный мучитель to Player');
INSERT INTO `creature_text` VALUES (120749, 2, 0, 'Этот мир... сгорит...', 12, 0, 100, 0, 0, 0, 101128, 0, 0, 0, 'Гнусный мучитель to Хати');
INSERT INTO `creature_text` VALUES (120749, 3, 0, 'Это будет познавательно.', 12, 0, 100, 0, 0, 55375, 102156, 85549, 85549, 0, 'Гнусный мучитель to Player');
INSERT INTO `creature_text` VALUES (120749, 3, 0, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 55376, 102157, 6094, 6094, 0, 'Гнусный мучитель to Player');
INSERT INTO `creature_text` VALUES (120750, 1, 0, 'Свежее мяско!', 12, 0, 100, 0, 0, 55207, 96658, 0, 0, 0, 'Нестабильный плут to Player');
INSERT INTO `creature_text` VALUES (120750, 1, 0, 'Эй! Что ты здесь делаешь?', 12, 0, 100, 0, 0, 55204, 96657, 0, 0, 0, 'Нестабильный плут to Player');
INSERT INTO `creature_text` VALUES (120758, 0, 0, 'У Белого Тигра Cюэня, одного из Небожителей, был сын Бань Лу.', 12, 0, 100, 0, 0, 82387, 128481, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120758, 1, 0, 'На протяжении веков Бань Лу носил на своей спине всех великих мастеров ордена. Для него это было честью!', 12, 0, 100, 0, 0, 82388, 128482, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120758, 2, 0, 'Но после атаки Легиона на Пик Безмятежности Бань Лу исчез. Похоже, гибель стольких монахов... подкосила его.', 12, 0, 100, 1, 0, 82389, 128483, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120758, 3, 0, 'Может, если мы вернемся на Пик, то узнаем, где его искать.', 12, 0, 100, 1, 0, 82390, 128484, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120760, 0, 0, 'Эта тропа приведет тебя на плато. Будь начеку. Мы скоро встретимся.', 12, 0, 100, 0, 0, 86203, 130551, 0, 0, 0, 'Верховный экзарх Туралион to Player');
INSERT INTO `creature_text` VALUES (120764, 0, 0, 'Я владею силой, которую вы отвергли!', 12, 0, 100, 0, 0, 86873, 135148, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120764, 0, 1, 'Склонитесь или умрите!', 12, 0, 100, 0, 0, 86875, 135150, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120764, 0, 2, 'Поплатись за свою глупость!', 12, 0, 100, 0, 0, 86866, 134505, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120764, 0, 3, 'Вы недостойны называться эредарами.', 12, 0, 100, 0, 0, 86874, 135149, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120764, 1, 0, 'I am not the last. Eredar ... will rise ...', 12, 0, 100, 0, 0, 86872, 135146, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120764, 1, 1, 'Тебе некуда бежать.', 12, 0, 100, 0, 0, 86717, 134496, 0, 0, 0, 'Тень Казадуума to Player');
INSERT INTO `creature_text` VALUES (120771, 0, 0, 'Довольно игр!', 14, 0, 100, 0, 0, 82695, 128410, 0, 0, 0, 'Инфаззар Темный to 0');
INSERT INTO `creature_text` VALUES (120792, 3, 0, 'Небо – моя забота. Защищайте Эгиду!', 14, 0, 100, 0, 0, 82035, 129342, 6284, 6284, 0, 'Иллидан Ярость Бури to Грознокрыл');
INSERT INTO `creature_text` VALUES (120797, 0, 0, 'Моя скала! Умри!', 12, 0, 100, 0, 0, 0, 129909, 0, 0, 0, 'Дукия to Player');
INSERT INTO `creature_text` VALUES (120814, 0, 0, 'Великий мастер! Бань Лу нашелся?', 12, 0, 100, 6, 0, 82402, 128496, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120814, 1, 0, 'Смерть Хайта огорчила меня. С камнем на сердце я скитался по Пандарии.', 12, 0, 100, 274, 0, 82423, 128498, 0, 0, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (120814, 2, 0, 'И однажды мне явилось видение... нечто новое и интересное... ты!', 12, 0, 100, 5, 0, 82424, 128499, 0, 0, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (120814, 3, 0, 'Многие увидели твои добродетели. Мастер Бу – терпение, груммели – честь; Шадо-Пан – долг, цзинь-юй – веру.', 12, 0, 100, 1, 0, 82425, 128500, 0, 0, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (120814, 4, 0, 'В тебе сочетаются мудрость, стойкость и чистота, приличествующие великому мастеру. Осталось проверить лишь твои боевые навыки!', 12, 0, 100, 603, 0, 82426, 128501, 0, 0, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (120814, 5, 0, 'Твои навыки впечатляют, великий мастер. Для меня будет честью служить тебе, как я служил твоим предшественникам – лидерам нашего ордена.', 14, 0, 100, 377, 0, 82427, 128502, 0, 0, 0, 'Бань Лу to 0');
INSERT INTO `creature_text` VALUES (120837, 1, 0, 'Жизнь за Легион.', 12, 0, 100, 0, 0, 55364, 98760, 0, 0, 0, 'Головорез Ярости Клинка to Player');
INSERT INTO `creature_text` VALUES (120837, 1, 0, 'Имя нам – Легион!', 12, 0, 100, 0, 0, 55369, 132504, 0, 0, 0, 'Головорез Ярости Клинка to Player');
INSERT INTO `creature_text` VALUES (120837, 1, 0, 'Мы очистим вселенную огнем!', 12, 0, 100, 0, 0, 55370, 98766, 0, 0, 0, 'Головорез Ярости Клинка to Player');
INSERT INTO `creature_text` VALUES (120837, 1, 0, 'Сейчас ты умрешь.', 12, 0, 100, 0, 0, 55362, 98765, 0, 0, 0, 'Головорез Ярости Клинка to Player');
INSERT INTO `creature_text` VALUES (120842, 0, 0, 'Мудрый выбор. Вместе мы способны на многое!', 12, 0, 100, 1, 0, 80720, 127097, 0, 0, 0, 'Эльталисте to Player');
INSERT INTO `creature_text` VALUES (120844, 0, 0, 'Ты должен помочь им, Туралион. А я проберусь в тыл и отвлеку врагов. Мы скоро вернемся, обещаю.', 12, 0, 100, 1, 0, 86160, 132632, 0, 0, 0, 'Аллерия Ветрокрылая to 0');
INSERT INTO `creature_text` VALUES (120852, 1, 0, 'Во имя Саргераса!', 12, 0, 100, 25, 0, 56997, 109464, 0, 0, 0, 'Присягнувший погибели предвестник to Player');
INSERT INTO `creature_text` VALUES (120860, 0, 0, 'Ну, что тут у нас? Дай посмотреть...', 14, 0, 100, 0, 0, 82393, 128487, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120860, 1, 0, 'Следы лап! И куда же они ведут?..', 12, 0, 100, 0, 0, 82394, 128488, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120863, 0, 0, 'Подожди меня! Ноги-то уже не те, что в молодости.', 14, 0, 100, 0, 0, 82396, 128490, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120864, 0, 0, 'Ты куда? Подожди меня. Спасибо, что терпишь медлительного старика.', 14, 0, 100, 0, 0, 82398, 128492, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120864, 1, 0, 'Можешь чуть помедленнее? Ты уж извини, что я еле плетусь.', 12, 0, 100, 0, 0, 82397, 128491, 0, 0, 0, 'Мастер Бу to Player');
INSERT INTO `creature_text` VALUES (120866, 0, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 1, 0, 53431, 137511, 0, 0, 0, 'Инфернальный техник to Player');
INSERT INTO `creature_text` VALUES (120869, 0, 0, 'Легион завершит начатое.', 12, 0, 100, 0, 0, 0, 129830, 0, 0, 0, 'Эредарский пиромант to Player');
INSERT INTO `creature_text` VALUES (120869, 1, 0, 'Твоя смерть будет быстрой и страшной.', 12, 0, 100, 0, 0, 0, 129827, 0, 0, 0, 'Эредарский пиромант to Player');
INSERT INTO `creature_text` VALUES (120896, 0, 1, 'От меня не спрячешься.', 12, 0, 100, 0, 0, 55374, 102155, 0, 0, 0, 'Темный мучитель to Player');
INSERT INTO `creature_text` VALUES (120896, 0, 2, 'Это будет познавательно.', 12, 0, 100, 0, 0, 55375, 102156, 0, 0, 0, 'Темный мучитель to Player');
INSERT INTO `creature_text` VALUES (120896, 0, 3, 'Перед смертью ты выдашь мне все секреты', 12, 0, 100, 0, 0, 55373, 102154, 0, 0, 0, 'Темный мучитель to Player');
INSERT INTO `creature_text` VALUES (120896, 0, 4, 'I see you.', 12, 0, 100, 0, 0, 55372, 132218, 0, 0, 0, 'Темный мучитель to Player');
INSERT INTO `creature_text` VALUES (120913, 0, 0, '%s стреляет из лука с невероятной скоростью.', 16, 0, 100, 0, 0, 0, 130017, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120913, 1, 0, 'Вырывать тебя из лап демонов уже вошло в привычку.', 12, 0, 100, 0, 0, 86136, 130027, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120913, 2, 0, 'Демон ослаблен. Добейте его!', 12, 0, 100, 0, 0, 86321, 131669, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120913, 3, 0, 'Ты в порядке, Туралион?', 12, 0, 100, 0, 0, 86138, 130029, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120913, 4, 0, 'Аратор! Он здесь!', 12, 0, 100, 5, 0, 86139, 130662, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120913, 5, 0, 'Столько лет я каждый день думала о том, что скажу ему. Но сейчас... не могу произнести и слова.', 12, 0, 100, 1, 0, 86177, 134221, 0, 0, 0, 'Аллерия Ветрокрылая to Аггонар');
INSERT INTO `creature_text` VALUES (120927, 3, 0, 'Элуна помогает тем, кто защищает эту землю от Пылающего Легиона. Шагни же в круг лунного света и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130000, 14777, 14777, 0, 'Жрица Элуны to Player');
INSERT INTO `creature_text` VALUES (120928, 0, 0, 'Если хочешь оттянуться, через 20 минут начнется концерт \"Чумного вепря\"!', 14, 0, 100, 0, 0, 0, 130632, 0, 0, 0, 'NPC_JAY_MAGUIRE');
INSERT INTO `creature_text` VALUES (120928, 1, 0, '\"Чумной вепрь\" со своей лучшей андерграундной программой на всем восточном побережье! Звезда сегодняшнего вечера выйдет на сцену через 10 минут!', 14, 0, 100, 0, 0, 0, 130633, 0, 0, 0, 'NPC_JAY_MAGUIRE');
INSERT INTO `creature_text` VALUES (120928, 2, 0, '\"Чумной вепрь\" выйдет на сцену уже через минуту! Присоединяйтесь к нам в Котле рока на восточном берегу острова.', 14, 0, 100, 0, 0, 0, 132720, 0, 0, 0, 'NPC_JAY_MAGUIRE');
INSERT INTO `creature_text` VALUES (120928, 3, 0, 'Как настроение? Сейчас вас ждет нечто совершенно убойное! Разрешите представить... группа \"Чумной вепрь\"!', 14, 0, 100, 0, 0, 0, 130640, 0, 0, 0, 'NPC_JAY_MAGUIRE');
INSERT INTO `creature_text` VALUES (120928, 4, 0, 'Хочешь послушать отличную группу? Концерт \"Чумного вепря\" начнется через тридцать минут!', 14, 0, 100, 0, 0, 0, 130631, 0, 0, 0, 'NPC_JAY_MAGUIRE');
INSERT INTO `creature_text` VALUES (120931, 3, 0, 'Элуна помогает тем, кто защищает эту землю от Пылающего Легиона. Шагни же в круг лунного света и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130000, 2477075, 3150330, 0, 'Жрица Элуны to Player');
INSERT INTO `creature_text` VALUES (120938, 1, 0, 'Легион сулит нам такую силу, какой нет даже у леди Азшары!', 12, 0, 100, 0, 0, 0, 130088, 0, 0, 0, 'Леди Сатара to Player');
INSERT INTO `creature_text` VALUES (120938, 4, 0, 'Наги одержат верх...', 12, 0, 100, 0, 0, 0, 130089, 14381, 14381, 0, 'Леди Сатара to Player');
INSERT INTO `creature_text` VALUES (120945, 1, 0, 'Мои глаза видят все.', 12, 0, 100, 0, 0, 55378, 102159, 0, 0, 0, 'Терзающий осквернитель to Player');
INSERT INTO `creature_text` VALUES (120945, 1, 0, 'Это будет познавательно.', 12, 0, 100, 0, 0, 55375, 102156, 0, 0, 0, 'Терзающий осквернитель to Player');
INSERT INTO `creature_text` VALUES (120945, 1, 0, 'Я и мертвого могу заставить говорить.', 12, 0, 100, 0, 0, 55376, 102157, 0, 0, 0, 'Терзающий осквернитель to Player');
INSERT INTO `creature_text` VALUES (120945, 3, 0, 'Столько вопросов, так мало времени.', 12, 0, 100, 0, 0, 55375, 102158, 8433, 8433, 0, 'Терзающий осквернитель to Player');
INSERT INTO `creature_text` VALUES (120952, 1, 0, 'Моя душа в обмен на мощь Скверны!', 12, 0, 100, 0, 0, 0, 130091, 0, 0, 0, 'Наполненный Скверной защитник to Player');
INSERT INTO `creature_text` VALUES (120952, 1, 0, 'Отдай себя Легиону.', 12, 0, 100, 0, 0, 0, 130092, 0, 0, 0, 'Наполненный Скверной защитник to Player');
INSERT INTO `creature_text` VALUES (120952, 3, 0, 'I am glad to serve the Legion.', 12, 0, 100, 0, 0, 0, 130090, 7630, 7630, 0, 'Наполненный Скверной защитник to Player');
INSERT INTO `creature_text` VALUES (120953, 0, 0, 'Сюда, $p. Пригнись, не высовывайся.', 12, 0, 100, 0, 0, 86134, 130482, 0, 0, 0, 'Верховный экзарх Туралион to Player');
INSERT INTO `creature_text` VALUES (120962, 1, 0, 'Моя душа в обмен на мощь Скверны!', 12, 0, 100, 0, 0, 0, 130091, 0, 0, 0, 'Наполненная Скверной песнопевица to Player');
INSERT INTO `creature_text` VALUES (120962, 1, 0, 'Отдай себя Легиону.', 12, 0, 100, 0, 0, 0, 130092, 0, 0, 0, 'Наполненная Скверной песнопевица to Player');
INSERT INTO `creature_text` VALUES (120977, 1, 0, 'Путь открыт. Вперед, дети Аргуса, союзники, герои. \"Виндикар\" ждет вас!', 12, 0, 100, 396, 0, 23504, 135840, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (120988, 0, 0, 'WARN: PHASE 2', 41, 0, 0, 0, 0, 0, 127919, 0, 0, 0, 'PHASE_2');
INSERT INTO `creature_text` VALUES (121022, 1, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Жалкий грабитель to Player');
INSERT INTO `creature_text` VALUES (121031, 0, 0, 'Мы пожрем твою душу! И тело тоже.', 12, 0, 100, 0, 0, 55205, 96656, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (121031, 0, 1, 'Поджарю с корочкой!', 12, 0, 100, 0, 0, 55210, 102137, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (121031, 0, 2, 'Эй! Что ты здесь делаешь?', 12, 0, 100, 0, 0, 55210, 96657, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (121031, 0, 3, 'Сейчас я поджарю твои ноги!', 12, 0, 100, 0, 0, 55206, 96660, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (121031, 0, 4, 'Хватайте обед!', 12, 0, 100, 0, 0, 55202, 96653, 0, 0, 0, 'Адский бес to Player');
INSERT INTO `creature_text` VALUES (121046, 1, 0, 'Бить! Пинать! Жечь!', 12, 0, 100, 0, 0, 0, 130128, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (121093, 3, 0, 'Мать-Земля всегда с тобой, воительница! Стань в кольцо из тотемов и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130134, 4524, 4524, 0, 'Гарма Смеющийся Тотем to Player');
INSERT INTO `creature_text` VALUES (121094, 3, 0, 'Мать-Земля всегда с тобой, воительница! Стань в кольцо из тотемов и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130134, 80340, 80340, 0, 'Этчурато Смеющийся Тотем to Player');
INSERT INTO `creature_text` VALUES (121106, 3, 0, 'Мать-Земля всегда с тобой, воительница! Стань в кольцо из тотемов и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130134, 16030, 16030, 0, 'Мотега Смеющийся Тотем to Player');
INSERT INTO `creature_text` VALUES (121118, 3, 0, 'Герой, я несу тебе милость Эйир! Подойди и получи ее благословение.', 12, 0, 100, 0, 0, 0, 130193, 5736, 5736, 0, 'Жрица Эйир to Player');
INSERT INTO `creature_text` VALUES (121121, 3, 0, 'Герой, я несу тебе милость Эйир! Подойди и получи ее благословение.', 12, 0, 100, 0, 0, 19174, 130193, 2746, 616641, 0, 'Жрица Эйир to Player');
INSERT INTO `creature_text` VALUES (121124, 0, 0, 'Аннигиляция!', 12, 0, 100, 0, 0, 0, 130211, 0, 0, 0, 'Апокрон to Player');
INSERT INTO `creature_text` VALUES (121127, 3, 0, 'Герой, я несу тебе дар принца Фарондиса. Мои заклинания помогут тебе на время стать сильнее, ловче и могущественнее. Подойди и получи свой дар.', 12, 0, 100, 0, 0, 0, 130194, 5862, 5862, 0, 'Верховный магистр Эйдрис to Player');
INSERT INTO `creature_text` VALUES (121129, 3, 0, 'Герой, я несу тебе дар принца Фарондиса. Мои заклинания помогут тебе на время стать сильнее, ловче и могущественнее. Подойди и получи свой дар.', 12, 0, 100, 0, 0, 0, 130194, 4817, 4817, 0, 'Верховный магистр Алиндорис to Player');
INSERT INTO `creature_text` VALUES (121132, 3, 0, 'Герой, я несу тебе дар принца Фарондиса. Мои заклинания помогут тебе на время стать сильнее, ловче и могущественнее. Подойди и получи свой дар.', 12, 0, 100, 0, 0, 0, 130194, 779, 779, 0, 'Верховный магистр Йессе to Player');
INSERT INTO `creature_text` VALUES (121151, 0, 0, 'Спасибо тебе. Прими в качестве награды часть моей силы – и да поможет она тебе в битве с нашим общим врагом.', 12, 0, 100, 1, 0, 0, 130220, 0, 0, 0, 'Норгаббон из племени Темного Камня to Player');
INSERT INTO `creature_text` VALUES (121151, 1, 0, 'Прощай.', 12, 0, 100, 0, 0, 0, 130223, 0, 0, 0, 'Норгаббон из племени Темного Камня to Player');
INSERT INTO `creature_text` VALUES (121154, 1, 0, 'Будьте вы прокляты!', 12, 0, 100, 0, 0, 53433, 137507, 0, 0, 0, 'Усердный собиратель to Player');
INSERT INTO `creature_text` VALUES (121154, 1, 0, 'Было ваше, стало наше! Хе-хе-хе!', 12, 0, 100, 1, 0, 53432, 137510, 0, 0, 0, 'Усердный собиратель to Player');
INSERT INTO `creature_text` VALUES (121184, 0, 0, 'Эти глупцы принесли нам Приливный камень – как и предвидела королева!', 14, 0, 100, 0, 0, 81789, 128032, 0, 0, 0, 'Госпожа Сашж\'ин to Player');
INSERT INTO `creature_text` VALUES (121184, 1, 0, 'Убейте их! Верните то, что по праву наше!', 14, 0, 100, 0, 0, 81789, 128033, 0, 0, 0, 'Госпожа Сашж\'ин to Player');
INSERT INTO `creature_text` VALUES (121184, 2, 0, 'Слабаки! Как вы посмели подвести королеву?', 14, 0, 100, 0, 0, 81789, 128034, 0, 0, 0, 'Госпожа Сашж\'ин to Ведьма из клана Волнистой Чешуи');
INSERT INTO `creature_text` VALUES (121190, 1, 0, 'Никчемный болван! И что в нем нашла королева?', 14, 0, 100, 0, 0, 85239, 128039, 0, 0, 0, 'Госпожа Сашж\'ин to Харджатан');
INSERT INTO `creature_text` VALUES (121224, 0, 0, 'Идем.', 12, 0, 100, 0, 0, 0, 140119, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121224, 1, 0, 'Здесь все куда ужаснее, чем я думал.', 12, 0, 100, 0, 0, 0, 129721, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121224, 2, 0, 'Отец хотел оградить меня от войны, говорил, что я должен остаться, потому что Ринн всегда должен быть на троне Штормграда. Он боялся меня потерять.', 12, 0, 100, 0, 0, 0, 129722, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121224, 3, 0, 'Когда умерла мать, у него остался только я.', 12, 0, 100, 0, 0, 0, 129723, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121224, 4, 0, 'Что ж, идем дальше.', 12, 0, 100, 0, 0, 0, 129720, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121227, 0, 0, 'Ко мне, герои! Я дарую вам истинное зрение!', 12, 0, 100, 0, 0, 83608, 130043, 0, 0, 0, 'ILLIDAN_STAGE_4');
INSERT INTO `creature_text` VALUES (121230, 0, 0, 'Постой, пророк. Я разведала обстановку. Мак\'\'Ари оказался во власти теней.', 12, 0, 100, 0, 0, 87528, 135241, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121230, 1, 0, 'Встретимся внизу, на руинах, $n. Начнем там, где кончается тьма.', 12, 0, 100, 0, 0, 87544, 131846, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121230, 2, 0, 'Идем.', 12, 0, 100, 0, 0, 92629, 140119, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121230, 3, 0, 'Не понимаю, о чем ты?', 12, 0, 100, 0, 0, 87535, 130971, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121230, 3, 0, 'Постой, пророк. Я разведала обстановку. Мак\'Ари оказался во власти теней.', 12, 0, 100, 0, 0, 86691, 135241, 4, 4, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121237, 0, 0, 'Я жил в безопасности, ценой которой была кровь героев, таких как Тирион, Болвар... и мой отец.', 12, 0, 100, 0, 0, 0, 129728, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121237, 1, 0, 'Теперь мой черед пойти по их стопам. Отведи меня туда, где погиб отец. Я должен увидеть это место.', 12, 0, 100, 0, 0, 0, 129729, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121246, 3, 0, 'Мы не сдадимся.', 12, 0, 100, 18, 0, 86584, 135538, 95806, 95806, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (121246, 3, 0, 'Фета ви акахачи!', 12, 0, 100, 18, 0, 88169, 133641, 317, 317, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (121250, 1, 0, 'I will show you ... true power!', 12, 0, 100, 0, 0, 87101, 130872, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (121250, 1, 0, 'Все должно быть разрушено!', 12, 0, 100, 0, 0, 87109, 130874, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (121250, 1, 0, 'Даже наару не устоят перед тьмой...', 12, 0, 100, 0, 0, 87103, 130886, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (121251, 1, 0, 'Было так темно...', 12, 0, 100, 579, 0, 87107, 130892, 0, 0, 0, 'Одержимый Бездной крокул to Player');
INSERT INTO `creature_text` VALUES (121251, 1, 0, 'Да поможет мне Свет...', 12, 0, 100, 20, 0, 87103, 130879, 0, 0, 0, 'Одержимый Бездной крокул to Player');
INSERT INTO `creature_text` VALUES (121251, 1, 0, 'Нет... Нет!', 12, 0, 100, 0, 0, 87105, 130880, 0, 0, 0, 'Одержимый Бездной крокул to Player');
INSERT INTO `creature_text` VALUES (121254, 0, 0, 'Поторопись, пока Преисподняя не поглотила тебя!', 12, 0, 100, 5, 0, 88169, 133520, 0, 0, 0, 'Сломленный Тьмой раб to Player');
INSERT INTO `creature_text` VALUES (121263, 0, 0, 'Огонь! За Аргус! За наш народ!', 12, 0, 100, 0, 0, 88116, 135791, 0, 0, 0, 'Верховный механолог Ромуул to Player');
INSERT INTO `creature_text` VALUES (121263, 1, 0, 'I will stay in touch while you are in Arcatraz.', 12, 0, 100, 113, 0, 86329, 132127, 0, 0, 0, 'Верховный механолог Ромуул to Player');
INSERT INTO `creature_text` VALUES (121283, 0, 0, 'Кто же теперь позаботится о моей семье?', 12, 0, 100, 0, 0, 0, 130445, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 1, 'Пожалуйста... прекрати этот кошмар!', 12, 0, 100, 0, 0, 0, 130443, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 2, 'Наконец-то этот кошмар закончился...', 12, 0, 100, 0, 0, 0, 130428, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 3, 'Спасибо тебе за доброту. Я этого никогда не забуду.', 12, 0, 100, 0, 0, 0, 130433, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 4, 'Спасибо. Я никогда не забуду твоей доброты.', 12, 0, 100, 0, 0, 0, 130427, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 5, 'Что со мной случилось? Где мое тело?', 12, 0, 100, 0, 0, 0, 130440, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 6, 'I died? No, it cannot be ... Someone help ...', 12, 0, 100, 0, 0, 0, 130444, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 7, 'I want you to remember us. Remember the sacrifice we made ...', 12, 0, 100, 0, 0, 0, 130426, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 8, 'Я вижу его… Я вижу Свет... Спасибо.', 12, 0, 100, 0, 0, 0, 130430, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 9, 'I do not want to die ...', 12, 0, 100, 0, 0, 0, 130441, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 10, 'Где я? Никак не могу вспомнить...', 12, 0, 100, 0, 0, 0, 130442, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121283, 0, 11, 'Для меня было честью служить королю...', 12, 0, 100, 0, 0, 0, 130429, 0, 0, 0, 'Страдающая душа to player');
INSERT INTO `creature_text` VALUES (121302, 0, 0, 'Поздно! Все лучшее я уже забрал!', 12, 0, 100, 0, 0, 0, 130377, 0, 0, 0, 'Охотник за сокровищами Икс\'реегед to Player');
INSERT INTO `creature_text` VALUES (121305, 0, 0, 'Опять ты? Ты не получишь мои сокровища!', 12, 0, 100, 0, 0, 0, 130378, 0, 0, 0, 'Охотник за сокровищами Икс\'реегед to Player');
INSERT INTO `creature_text` VALUES (121312, 0, 0, 'Ха-ха! Слишком поздно, смертное существо!', 12, 0, 100, 0, 0, 0, 130379, 0, 0, 0, 'Охотник за сокровищами Икс\'реегед to Player');
INSERT INTO `creature_text` VALUES (121313, 0, 0, 'Охотник за сокровищами Икс\'реегед делает в вашу сторону неприличный жест.', 16, 0, 100, 0, 0, 0, 130400, 0, 0, 0, 'Охотник за сокровищами Икс\'реегед to Player');
INSERT INTO `creature_text` VALUES (121319, 0, 0, 'Поторопитесь! Ла\'\'заруун нужно время, чтобы отправить верховного инквизитора на Аргус!', 12, 0, 100, 0, 0, 87381, 132649, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 1, 0, 'Слишком поздно. Наша госпожа уже освободила верховного инквизитора.', 12, 0, 100, 0, 0, 87382, 132652, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 1, 1, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 87382, 105125, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 2, 0, 'Магия Зерекета все еще здесь.', 12, 0, 100, 0, 0, 87379, 132181, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 3, 0, 'Теперь он наш.', 12, 0, 100, 0, 0, 87380, 132182, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 5, 0, 'Просто сдерживайте часовых, пока верховный инквизитор не окажется на свободе!', 12, 0, 100, 0, 0, 87376, 131481, 0, 0, 0, 'Искательница Тени Скверны to Player');
INSERT INTO `creature_text` VALUES (121319, 6, 0, 'Вся сила этой тюрьмы теперь принадлежит Легиону.', 12, 0, 100, 0, 0, 87382, 132259, 0, 0, 0, 'Искательница Тени Скверны to Player');
