INSERT INTO `creature_text` VALUES (121325, 10, 0, 'Тема сегодняшнего конкурса... \"Магический стиль\"!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Настасия Видящая Суть to Player');
