INSERT INTO `creature_text` VALUES (121325, 12, 0, 'Тема сегодняшнего конкурса... \"Гербовые накидки\"!', 12, 0, 100, 0, 0, 0, 130462, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 13, 0, 'Тема сегодняшнего конкурса... \"Бытовые герои\"!', 12, 0, 100, 0, 0, 0, 130463, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 14, 0, 'Тема сегодняшнего конкурса... \"Эпический фиолетовый\"!', 12, 0, 100, 0, 0, 0, 130464, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 15, 0, 'Тема сегодняшнего конкурса... \"Защитники Света\"!', 12, 0, 100, 0, 0, 0, 130465, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 16, 0, 'Тема сегодняшнего конкурса... \"Свободный стиль\"!', 12, 0, 100, 0, 0, 0, 130466, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 17, 0, 'Слева от вас – $n, воплощение великолепия!', 12, 0, 100, 0, 0, 0, 130431, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 18, 0, 'На третьем месте у нас... $n! Поздравляю, $n!', 12, 0, 100, 0, 0, 0, 130435, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 19, 0, 'И наконец наш великий чемпион трансмогрификации. Давайте поприветствуем $n!', 12, 0, 100, 0, 0, 0, 130437, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 20, 0, 'Всем остальным хочу сказать: не сдавайтесь. Однажды и вы станете победителем.', 12, 0, 100, 0, 0, 0, 130438, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121325, 21, 0, 'На сегодня это все. Еще увидимся!', 12, 0, 100, 0, 0, 0, 130439, 0, 0, 0, 'Настасия Видящая Суть to Player');
INSERT INTO `creature_text` VALUES (121326, 1, 0, 'О, что может быть лучше пары стильных шорт?', 12, 0, 100, 0, 0, 0, 130418, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 2, 0, 'А теперь укутайтесь потеплее! Давайте посмотрим, в каких нарядах вы щеголяете на ледяных берегах Нордскола!', 12, 0, 100, 0, 0, 0, 130467, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 3, 0, 'Снимите макияж и нанесите боевой раскрас!', 12, 0, 100, 0, 0, 0, 130468, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 4, 0, 'Не нужно бояться открытых нарядов! Или того, что кто-то зальет их слюнками...', 12, 0, 100, 0, 0, 0, 130469, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 5, 0, 'Напугайте нас до последнего писка моды своими жутчайшими нарядами. О... у меня уже мурашки по коже!', 12, 0, 100, 0, 0, 0, 130471, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 6, 0, 'Давайте немного повеселимся – покажите нам самый яркий и кричащий наряд, какой только можете себе вообразить!', 12, 0, 100, 0, 0, 0, 130470, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 7, 0, 'Пусть ваш наряд будет не только стильным, но и зловещим!', 12, 0, 100, 0, 0, 0, 130472, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 8, 0, 'За Орду! За Альянс! Разверните флаги своей фракции!', 12, 0, 100, 0, 0, 0, 130473, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 9, 0, 'Наряды пандаренов просто ИСХОДЯТ гармонией. Продемонстрируйте нам свой душевный покой!', 12, 0, 100, 0, 0, 0, 130474, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 10, 0, 'Огни! Нужно больше огней! Покажите, как сверкают ваши самые чародейские наряды! И не забудьте наложить чары!', 12, 0, 100, 0, 0, 0, 130475, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 11, 0, 'Пора повеселиться и позволить себе немного сумасбродства!', 12, 0, 100, 0, 0, 0, 130476, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 12, 0, 'Вы представляете Терамор? Или Кирин-Тор? У Нелли есть для вас лишние гербовые накидки!', 12, 0, 100, 0, 0, 0, 130477, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 13, 0, 'Кем вы будете? Фермером? Шеф-поваром? Ученым? Пиратом? Ох... Не терпится увидеть, что для нас приготовили!', 12, 0, 100, 0, 0, 0, 130478, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 14, 0, 'Как вам известно, самая эпическая броня ВСЕГДА фиолетовая. Облачитесь в этот самый восхитительный из цветов радуги!', 12, 0, 100, 0, 0, 0, 130479, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 15, 0, 'Белоснежные одеяния и сияющие доспехи сейчас актуальны, как никогда!', 12, 0, 100, 0, 0, 0, 130480, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 16, 0, 'Да-да. У сегодняшнего конкурса нет конкретной темы. Дайте волю своему воображению!', 12, 0, 100, 0, 0, 0, 130481, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 17, 0, 'Осталось 30 секунд!', 41, 0, 100, 0, 0, 0, 132232, 0, 0, 0, 'Азирис Песнь Красоты to 1329');
INSERT INTO `creature_text` VALUES (121326, 18, 0, 'Осталось 10 секунд!', 41, 0, 100, 0, 0, 0, 132233, 0, 0, 0, 'Азирис Песнь Красоты to 1329');
INSERT INTO `creature_text` VALUES (121326, 19, 0, 'А справа – $n! Какое обаяние!', 12, 0, 100, 0, 0, 0, 130434, 0, 0, 0, 'Азирис Песнь Красоты to Player');
INSERT INTO `creature_text` VALUES (121326, 20, 0, 'На втором месте у нас... $n! Ты сегодня на высоте!', 12, 0, 100, 0, 0, 0, 130436, 0, 0, 0, 'Азирис Песнь Красоты to Player');
