INSERT INTO `creature_text` VALUES (121531, 2, 0, 'Ты... лишь песчинка...', 12, 0, 100, 0, 0, 86576, 134381, 0, 0, 0, 'Отмеченный Скверной разрушитель to Player');
INSERT INTO `creature_text` VALUES (121538, 0, 0, 'Загляните в свои души и найдите силу, рожденную в вере.', 12, 0, 100, 0, 0, 87610, 130582, 0, 0, 0, 'Тень Аркаана to Player');
INSERT INTO `creature_text` VALUES (121538, 1, 0, 'Не поддавайтесь отчаянию. Искушение ослабляет дух и разум.', 12, 0, 100, 0, 0, 87609, 130581, 0, 0, 0, 'Тень Аркаана to Player');
INSERT INTO `creature_text` VALUES (121538, 2, 0, 'Взгляните в небо! Вы увидите мириады сверкающих звезд среди великой тьмы.', 12, 0, 100, 0, 0, 87607, 130579, 0, 0, 0, 'Тень Аркаана to Player');
INSERT INTO `creature_text` VALUES (121541, 0, 0, 'Удачи, великий мастер!', 12, 0, 100, 0, 0, 82407, 128510, 0, 0, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (121541, 3, 0, 'Куда двинемся сегодня, великий мастер?', 12, 0, 100, 0, 0, 82429, 128507, 32921, 32921, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (121541, 3, 0, 'Рад встрече, великий мастер.', 12, 0, 100, 0, 0, 82405, 128508, 530, 530, 0, 'Бань Лу to Player');
INSERT INTO `creature_text` VALUES (121544, 1, 0, 'Многие пытались убить меня, и все они мертвы! С тобой будет то же!', 14, 0, 100, 0, 0, 57004, 134082, 0, 0, 0, 'Верховный инквизитор Раалгар');
INSERT INTO `creature_text` VALUES (121546, 0, 0, 'Что Аркаан... наделал?', 12, 0, 100, 0, 0, 87621, 130962, 0, 0, 0, 'Аркаан to Player');
INSERT INTO `creature_text` VALUES (121546, 3, 0, 'Все исчезнет! Останется только... Бездна.', 12, 0, 100, 0, 0, 87607, 132154, 35, 35, 0, 'Аркаан to Аркаан');
INSERT INTO `creature_text` VALUES (121556, 3, 0, 'Нет... нет... нет...', 12, 0, 100, 0, 0, 87100, 132100, 55, 55, 0, 'Аркаан to Player');
INSERT INTO `creature_text` VALUES (121573, 0, 0, 'Эта земля полна скорби и уныния.', 12, 0, 100, 0, 0, 0, 129786, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121575, 1, 0, 'Мы очистим ваш мир!', 12, 0, 100, 113, 0, 57007, 105114, 0, 0, 0, 'Боец гвардии to Player');
INSERT INTO `creature_text` VALUES (121578, 0, 0, 'Бери на себя батарею и уничтожь нетопырей! Я на тебя рассчитываю!', 12, 0, 100, 0, 0, 86327, 133601, 0, 0, 0, 'Верховный маг И\'\'мера to Player');
INSERT INTO `creature_text` VALUES (121597, 0, 0, 'О твоем спутнике. Что он думает о твоих решениях?', 12, 0, 100, 0, 0, 87852, 130972, 0, 0, 0, 'Странник to Player');
INSERT INTO `creature_text` VALUES (121618, 0, 0, 'Нам нужно быть поосторожнее в Штормхейме. Ты и не представляешь, сколько врагов у дочери Сковальда.', 12, 0, 100, 0, 0, 82911, 125759, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 1, 0, 'Берегись! Кто-то прячется за деревьями!', 14, 0, 100, 0, 0, 82940, 128470, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 2, 0, 'Враги уже рядом... Поспешим!', 12, 0, 100, 1, 0, 83248, 129790, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 3, 0, 'Снова засада! Оружие к бою!', 14, 0, 100, 0, 0, 82946, 128476, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 4, 0, 'Не отставай!', 12, 0, 100, 1, 0, 83246, 129788, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 5, 0, 'Идем! Здесь небезопасно...', 12, 0, 100, 1, 0, 83247, 129789, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 6, 0, 'В прошлый раз мне пришлось бежать отсюда, спасаясь от убийц, которых подослали уничтожить мою семью.', 12, 0, 100, 0, 0, 82941, 128471, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 7, 0, 'Они все еще могут охотиться за мной.', 12, 0, 100, 0, 0, 82942, 128472, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 8, 0, 'Похоже, зря я надеялась убежать от прошлого.', 12, 0, 100, 0, 0, 82943, 128473, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 9, 0, 'Никогда не забуду ту ночь... Моя мать умерла у меня на глазах... Потом убийцы накинулись на брата, а мне удалось уйти.', 12, 0, 100, 0, 0, 82944, 128474, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 10, 0, 'После той резни я осталась последней в роду.', 12, 0, 100, 0, 0, 82949, 128479, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 11, 0, 'Жалкие трусы! Хотела бы я перебить их всех, а заодно и тех, кто их подослал!', 12, 0, 100, 0, 0, 82945, 128475, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 12, 0, 'Но увы. Они все время от меня ускользают... И сейчас у нас есть дело поважнее.', 12, 0, 100, 0, 0, 82947, 128477, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 13, 0, 'I am comforted only by the fact that someday they will answer for everything to the gods.', 12, 0, 100, 0, 0, 82948, 128478, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 14, 0, 'Скольд-Ашиль! Мать рассказывала мне об этом месте и о великих девах щита! Потрясающее зрелище!', 12, 0, 100, 0, 0, 82950, 125670, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121618, 15, 0, 'Эти доспехи... Такие же были у моей матери...', 12, 0, 100, 0, 0, 82951, 125671, 0, 0, 0, 'Сигрин to Player');
INSERT INTO `creature_text` VALUES (121631, 0, 0, 'Раз за разом герои Штормграда отдавали жизни ради мира в Азероте. А я лишь наблюдал за этим издалека.', 12, 0, 100, 0, 0, 0, 129724, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121631, 1, 0, 'Мой отец – не первый из королей Штормграда, кто погиб на войне.', 12, 0, 100, 0, 0, 0, 129725, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121631, 2, 0, 'Король Баратен, мой прадед, и мой дед, король Ллейн, тоже пожертвовали жизнью ради своего народа.', 12, 0, 100, 0, 0, 0, 129726, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121631, 3, 0, 'Мои предки, короли Штормграда, сражались за нашу свободу плечом к плечу с простыми солдатами.', 12, 0, 100, 0, 0, 0, 129727, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121631, 4, 0, 'Что ж, идем дальше.', 12, 0, 100, 0, 0, 0, 129720, 0, 0, 0, 'Андуин Ринн to player');
INSERT INTO `creature_text` VALUES (121670, 1, 0, 'Тьма наступает!', 12, 0, 100, 1, 0, 86791, 130980, 0, 0, 0, 'Адепт из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121671, 1, 0, 'Прими Бездну.', 12, 0, 100, 0, 0, 86791, 130989, 0, 0, 0, 'Пожиратель энергии из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121671, 1, 0, 'Я вижу лишь тьму в твоей душе.', 12, 0, 100, 0, 0, 88418, 130990, 0, 0, 0, 'Пожиратель энергии из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121671, 2, 0, 'Повелители, я иду к вам.', 12, 0, 100, 0, 0, 87109, 136183, 0, 0, 0, 'Пожиратель энергии из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121671, 2, 0, 'Это... ничего... не изменит.', 12, 0, 100, 0, 0, 88607, 136186, 0, 0, 0, 'Пожиратель энергии из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121672, 2, 0, 'Повелители, я иду к вам.', 12, 0, 100, 0, 0, 86800, 136183, 0, 0, 0, 'Фазовый клинок из Темной Стражи to Экзоскелет озаренных');
INSERT INTO `creature_text` VALUES (121694, 0, 0, 'Люблю, когда пандарены так отвязно выделываются. Танцуем!', 12, 0, 100, 0, 0, 0, 131046, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 0, 1, 'Давайте, пандарены!', 12, 0, 100, 0, 0, 0, 131047, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 1, 0, 'Давайте, тролли! Покажите всем, как надо танцевать!', 12, 0, 100, 0, 0, 0, 130769, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 1, 1, 'Танец троллей! Эти ноги не удержит никакая обувь!', 12, 0, 100, 0, 0, 0, 130770, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 2, 0, 'Давайте, таурены! Покажите, как танцуют в Мулгоре.', 12, 0, 100, 0, 0, 0, 130772, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 2, 1, 'Смотри по сторонам! А не то тебе таурены-танцоры все ноги отдавят.', 12, 0, 100, 0, 0, 0, 130771, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 3, 0, 'Вперед, братья и сестры орки! Покажите нам свой танец!', 12, 0, 100, 0, 0, 0, 130779, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 3, 0, 'Это наш город, орки. Покажем всем наше величие!', 12, 0, 100, 0, 0, 0, 130780, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 4, 0, 'Покажите нам, как танцуют гоблины!', 12, 0, 100, 0, 0, 0, 130774, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 4, 1, 'Пусть гоблины поднимутся на сцену, чтобы все их видели!', 12, 0, 100, 0, 0, 0, 130773, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 5, 0, 'А теперь выход Отрекшихся. От такого танца того и гляди остановится сердце!', 12, 0, 100, 0, 0, 0, 130768, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 5, 1, 'Приглашаем на сцену Отрекшихся!', 12, 0, 100, 0, 0, 0, 130767, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 6, 0, 'Эльфы крови! Теперь ваш черед.', 12, 0, 100, 0, 0, 0, 130776, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 6, 1, 'Полюбуйтесь на эльфов крови и их изящные движения!', 12, 0, 100, 0, 0, 0, 130775, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 7, 0, 'Выпускайте бычка! Таурены Крутогорья, ВПЕРЕД!', 12, 0, 100, 0, 0, 0, 147264, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 7, 1, 'У пришедших с крутых гор и нрав крутой! Это таурены Крутогорья!', 12, 0, 100, 0, 0, 0, 147263, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 8, 0, 'День воюют, ночь танцуют. Поприветствуйте ночнорожденных!', 12, 0, 100, 0, 0, 0, 147265, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121694, 8, 1, 'Нет, у вас не галлюцинации... просто перед вами танцующие ночнорожденные!', 12, 0, 100, 0, 0, 0, 147266, 0, 0, 0, 'Аштар');
INSERT INTO `creature_text` VALUES (121731, 0, 0, 'I can\'t believe they took over the entire building just for a dance party! Where am I going to sell all these pants now?', 12, 0, 0, 0, 0, 0, 130783, 0, 0, 0, 'Разгневанная торговка');
INSERT INTO `creature_text` VALUES (121731, 1, 0, 'Oh. Right.', 12, 0, 0, 0, 0, 0, 130785, 0, 0, 0, 'Разгневанная торговка');
INSERT INTO `creature_text` VALUES (121739, 0, 0, 'Люблю, когда пандарены так отвязно выделываются. Танцуем!', 12, 0, 100, 0, 0, 0, 131046, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 0, 1, 'Давайте, пандарены!', 12, 0, 100, 0, 0, 0, 131047, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 1, 0, 'Это ваш город, люди. Покажите нам все свое величие!', 12, 0, 100, 0, 0, 0, 131034, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 1, 1, 'Ходят слухи, что люди не умеют танцевать. Давайте, докажите, что это не так!', 12, 0, 100, 0, 0, 0, 131035, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 2, 0, 'Приглашаем на сцену дренеев!', 12, 0, 100, 0, 0, 0, 131042, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 2, 1, 'А сейчас нас ждут танцы из других миров. Встречайте: это дренеи!', 12, 0, 100, 0, 0, 0, 131043, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 3, 0, 'Пусть гномы поднимутся на сцену, чтобы их все видели!', 12, 0, 100, 0, 0, 0, 131040, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 3, 1, 'Гномы! Ну разве они не очаровашки?', 12, 0, 100, 0, 0, 0, 131041, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 4, 0, 'Только посмотрите, как ловко дворфы работают ногами! И при этом даже эль умудряются не проливать!', 12, 0, 100, 0, 0, 0, 131036, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 4, 1, 'Пора и дворфам показать, как они умеют отрываться!', 12, 0, 100, 0, 0, 0, 131037, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 5, 0, 'Полюбуйтесь на изящных и уверенных в себе ночных эльфов! Ой, как же здесь жарко...', 12, 0, 100, 0, 0, 0, 131038, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 5, 1, 'Ночные эльфы! Покажите нам, как отрываются в Тельдрассиле!', 12, 0, 100, 0, 0, 0, 131039, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 6, 0, 'У-у-у! Давайте, воргены, покажите, какие вы необузданные!', 12, 0, 100, 0, 0, 0, 131045, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 6, 1, 'Вперед, воргены, братья и сестры мои! Покажем всем, как нужно танцевать!', 12, 0, 100, 0, 0, 0, 131044, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 7, 0, 'Озаренные дренеи прибыли, чтобы танцевать и убивать демонов. Демоны у нас закончились!', 12, 7, 100, 0, 0, 0, 147270, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 7, 1, 'Отключите диско-шар, и эта сцена останется ОЗАРЕННОЙ этими дренеями!', 12, 7, 100, 0, 0, 0, 147269, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 8, 0, 'Танцуйте так, будто вы одержимы Бездной!', 12, 0, 100, 0, 0, 0, 147268, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121739, 8, 1, 'Даже аватарам Тьмы не чуждо веселье. Эльфы Бездны, зажигайте!', 12, 0, 100, 0, 0, 0, 147267, 0, 0, 0, 'Марла');
INSERT INTO `creature_text` VALUES (121740, 0, 0, 'I can\'t believe they took over the entire building just for a dance party! Where am I going to sell all these pants now?', 12, 0, 0, 0, 0, 0, 130783, 0, 0, 0, 'Разгневанная торговка');
INSERT INTO `creature_text` VALUES (121740, 1, 0, 'Oh. Right.', 12, 0, 0, 0, 0, 0, 130785, 0, 0, 0, 'Разгневанная торговка');
INSERT INTO `creature_text` VALUES (121754, 0, 0, 'Никогда не видела, чтобы дренеи собирали столь мощную армию.', 12, 0, 100, 0, 0, 88060, 135806, 0, 0, 0, 'Вериса Ветрокрылая to 0');
INSERT INTO `creature_text` VALUES (121754, 1, 0, 'Скоро мы узнаем, ответит ли Орда на призыв Велена. У них нет чувства долга, но сражаться они умеют.', 12, 0, 100, 0, 0, 87088, 130826, 0, 0, 0, 'Вериса Ветрокрылая to 0');
INSERT INTO `creature_text` VALUES (121755, 0, 0, 'И тогда мои родители вернутся домой.', 12, 0, 100, 0, 0, 88073, 135808, 0, 0, 0, 'Аратор to 0');
INSERT INTO `creature_text` VALUES (121756, 0, 0, 'Следуйте за мной. Я провожу вас в Чертог Света. Нас уже ждут.', 12, 0, 100, 0, 0, 88061, 135805, 0, 0, 0, 'Воздаятель Борос to 0');
INSERT INTO `creature_text` VALUES (121756, 1, 0, 'Аргус – родина наших предков. Мы обязаны вырвать его из рук Легиона.', 12, 0, 100, 0, 0, 88062, 135807, 0, 0, 0, 'Воздаятель Борос to 0');
INSERT INTO `creature_text` VALUES (121761, 1, 0, 'Твоя плоть предает тебя.', 12, 0, 100, 0, 0, 87604, 132229, 0, 0, 0, 'Призыватель бездны из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121761, 2, 0, 'Наконец-то.', 12, 0, 100, 0, 0, 87614, 136180, 0, 0, 0, 'Призыватель бездны из Темной Стражи to Player');
INSERT INTO `creature_text` VALUES (121960, 0, 0, 'Кто это?', 12, 0, 100, 0, 0, 87504, 132160, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121960, 1, 0, 'Жаль, что не вышло тебя переубедить. Покойся с миром.', 12, 0, 100, 0, 0, 87509, 132158, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121960, 2, 0, 'Чувствуешь? Как будто что-то тянет нас назад...', 12, 0, 100, 0, 0, 87510, 132167, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (121975, 0, 0, 'You will burn!', 14, 0, 100, 0, 0, 86364, 133394, 0, 0, 0, 'AGGRO');
INSERT INTO `creature_text` VALUES (121975, 1, 0, 'All will die in my wake!', 14, 0, 100, 0, 0, 86363, 133424, 0, 0, 0, 'WAKE_OF_FLAME_1');
INSERT INTO `creature_text` VALUES (121975, 1, 1, 'Drown in flames!', 14, 0, 100, 0, 0, 86362, 133422, 0, 0, 0, 'WAKE_OF_FLAME_2');
INSERT INTO `creature_text` VALUES (121975, 2, 0, 'WARN: TAESHALACH_TECHNIQUE', 41, 0, 100, 0, 0, 0, 132829, 0, 0, 0, 'WARN_TAESHALACH_TECHNIQUE');
INSERT INTO `creature_text` VALUES (121975, 3, 0, 'Burn!', 14, 0, 100, 0, 0, 86361, 133419, 0, 0, 0, 'TAESHALACH_TECHNIQUE_1');
INSERT INTO `creature_text` VALUES (121975, 3, 1, 'None can stand against Taeshalach!', 14, 0, 100, 0, 0, 86360, 133418, 0, 0, 0, 'TAESHALACH_TECHNIQUE_2');
INSERT INTO `creature_text` VALUES (121975, 4, 0, 'Taeshalach\'s power is unleashed!', 14, 0, 100, 0, 0, 86358, 133433, 0, 0, 0, 'PRE_PHASE_2');
INSERT INTO `creature_text` VALUES (121975, 5, 0, 'You may have withstood the flames, but you will not withstand my fury.', 14, 0, 100, 0, 0, 86356, 133435, 0, 0, 0, 'PHASE_2');
INSERT INTO `creature_text` VALUES (121975, 6, 0, 'Stand and die!', 14, 0, 100, 0, 0, 86354, 133425, 0, 0, 0, 'FLARE_1');
INSERT INTO `creature_text` VALUES (121975, 6, 1, 'There is no escape!', 14, 0, 100, 0, 0, 86995, 135008, 0, 0, 0, 'FLARE_2');
