INSERT INTO `creature_text` VALUES (123232, 0, 0, 'Освобожденные рабы-крокулы не могли далеко уйти. Они явно где-то рядом.', 12, 0, 100, 1, 0, 88247, 135960, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 1, 0, 'Они могли бы стать ценными союзниками.', 12, 0, 100, 1, 0, 86223, 132380, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 2, 0, 'Как скажешь. Пойдем, $p. Я чувствую – они где-то неподалеку.', 12, 0, 100, 1, 0, 86225, 132383, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 3, 0, 'Не все эредары оставили Аргус, когда Архимонд и Кил\'джеден заключили союз с Темным титаном.', 12, 0, 100, 0, 0, 86226, 132384, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 4, 0, 'Многие вступили в Легион добровольно, поверив лживым обещаниям Саргераса.', 12, 0, 100, 0, 0, 86227, 132385, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 5, 0, 'Те, кто осознал истину, пытались сбежать, но оказались в ловушке этого кошмара. Даже не знаю, как им удалось выжить.', 12, 0, 100, 0, 0, 86228, 132386, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123232, 6, 0, 'Сюда! Я уверен, они где-то рядом', 12, 0, 100, 0, 0, 88249, 135973, 0, 0, 0, 'Пророк Велен to 0');
INSERT INTO `creature_text` VALUES (123247, 0, 0, 'Вот это находка. Мы найдем ей применение!', 12, 0, 100, 0, 0, 0, 132350, 0, 0, 0, 'Интендант Айкрас to player');
INSERT INTO `creature_text` VALUES (123301, 1, 0, 'I will show you ... true power!', 12, 0, 100, 0, 0, 87103, 130872, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (123301, 1, 0, 'Бездна зовет нас! Слышишь?', 12, 0, 100, 0, 0, 87102, 130873, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (123301, 2, 0, 'Подчинись! У тебя нет выбора.', 12, 0, 100, 0, 0, 87103, 130870, 0, 0, 0, 'Пораженный Тьмой крокул to Player');
INSERT INTO `creature_text` VALUES (123302, 2, 0, 'Ну вот... и все.', 12, 0, 100, 0, 0, 87103, 130888, 0, 0, 0, 'Одержимый Бездной крокул to Player');
INSERT INTO `creature_text` VALUES (123302, 3, 0, 'Нет... Нет!', 12, 0, 100, 0, 0, 87103, 130880, 12, 24, 0, 'Одержимый Бездной крокул to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 0, 'Расскажи всем. Скажи, что у Брин \"Дурные новости\" на самом деле все не так уж дурно.', 12, 0, 100, 2, 0, 0, 132461, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 1, 'Аррр! Еще пойла!', 12, 0, 100, 4, 0, 0, 132458, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 2, 'Что ж, благодарю сердечно, морской волк.', 12, 0, 100, 2, 0, 0, 132455, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 3, 'Что, без дураков? Тогда спасибо тебе!', 12, 0, 100, 2, 0, 0, 132459, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 4, 'Спасибо, дружище. Сокровище можно найти не только в сундуках.', 12, 0, 100, 2, 0, 0, 132462, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 5, 'Да я тебе сейчас... О, подожди-ка, тебе понравилось? Спасибо. Никто никогда не хвалит мои шутки.', 12, 0, 100, 2, 0, 0, 132456, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 0, 6, 'I have plucked so many seas, and in my account there are so many robberies. So this is genuine pirate humor!', 12, 0, 100, 4, 0, 0, 132457, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 0, 'Закрой свою грогохлебалку и иди уже утопись, отребья кусок!', 12, 0, 100, 14, 0, 0, 132451, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 1, 'Да? Тогда сходи купи мне бурных аплодисментов.', 12, 0, 100, 4, 0, 0, 132460, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 2, 'Черную метку тебе и всем твоим дружкам-крысам!', 12, 0, 100, 14, 0, 0, 132454, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 3, 'Иди потанцуй с палачом, а шутить оставь бывалым морякам.', 12, 0, 100, 14, 0, 0, 132452, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 4, 'Ха! Ну давай, покажи, как надо. Блесни остроумием!', 12, 0, 100, 14, 0, 0, 132446, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 5, 'Да? А у тебя рожа кривая... И у твоей мамаши тоже!', 12, 0, 100, 14, 0, 0, 132445, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 6, 'I\'ll chop you up for meat!', 12, 0, 100, 14, 0, 0, 132448, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 7, 'Иди протащи себя под килем и вылижи трюм заодно.', 12, 0, 100, 14, 0, 0, 132447, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 1, 8, 'Закрой свою пасть, или я тебя на вертел посажу, ты, жалкое подобие пирата!', 12, 0, 100, 14, 0, 0, 132449, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 2, 0, 'Так, задрайте свои люки, приятели. Эта шутка вам кормило вдребезги разнесет...', 12, 0, 100, 1, 0, 0, 132463, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 3, 1, 'Что не так с нынешними сундуками с сокровищами? Они все такие дорогие и блестящие...', 12, 0, 100, 1, 0, 0, 132486, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 4, 2, 'Типа мы хотим, чтобы их нашли! Все эти сверкающие золотые окантовки и замки с драгоценными камушками...', 12, 0, 100, 1, 0, 0, 132487, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 5, 3, 'Типа, мы все-таки храним сокровища снаружи или внутри? Так ведь?', 12, 0, 100, 1, 0, 0, 132488, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 6, 4, 'Ха, а теперь налейте мне кто-нибудь грога.', 12, 0, 100, 1, 0, 0, 132464, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 7, 0, 'Все же знают чокнутого контр-адмирала Виселинкина?', 12, 0, 100, 1, 0, 0, 132483, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 8, 0, 'Прикинь, он как-то с помощью своих гномских штуковин создал себе универсального пиратского спутника, объединив попугая и акулу!', 12, 0, 100, 1, 0, 0, 132484, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 9, 0, 'Все шло отлично... пока у попугая не выросли челюсти!', 12, 0, 100, 1, 0, 0, 132485, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 10, 0, 'Вот вам загадка...', 12, 0, 100, 1, 0, 0, 132480, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 11, 0, 'У кого шесть ног, шесть рук и шесть глаз?', 12, 0, 100, 1, 0, 0, 132481, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123394, 12, 0, 'У шести пиратов!', 12, 0, 100, 1, 0, 0, 132482, 0, 0, 0, 'Брин \"Дурные новости\" to Player');
INSERT INTO `creature_text` VALUES (123413, 0, 0, 'Хм, Привратник отключен.', 12, 0, 100, 6, 0, 87595, 132728, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123413, 1, 0, 'Этот голем должен помочь нам найти Печать Пробуждения. Нужно только его запустить.', 12, 0, 100, 1, 0, 87588, 133367, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123413, 2, 0, 'Я надеялась, что это сработает. Но Привратник не реагирует.', 12, 0, 100, 1, 0, 87590, 133420, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123413, 3, 0, 'Мне нужна твоя помощь. Пни его легонько.', 12, 0, 100, 6, 0, 87583, 133508, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123413, 4, 0, 'I cannot help you directly, but I can share useful information.', 15, 0, 100, 1, 0, 87586, 133549, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123413, 5, 0, 'Верховный маг И\'мера жестом приглашает вас следовать за ней.', 16, 0, 100, 1, 0, 0, 136098, 0, 0, 0, 'Верховный маг И\'мера to Player');
INSERT INTO `creature_text` VALUES (123418, 1, 0, 'Ты познаешь истинную боль!', 12, 0, 100, 0, 0, 23504, 132511, 0, 0, 0, 'Штурмовая ревнительница to Аратор Искупитель');
INSERT INTO `creature_text` VALUES (123421, 1, 0, 'Твоя жизнь обречена.', 12, 0, 100, 0, 0, 0, 132527, 0, 0, 0, 'Штурмовая призывательница Рока to Player');
INSERT INTO `creature_text` VALUES (123422, 0, 0, 'Азерот падет!', 12, 0, 100, 274, 0, 88036, 132508, 0, 0, 0, 'Штурмовая ревнительница to Player');
INSERT INTO `creature_text` VALUES (123464, 1, 0, 'Тебе хватило смелости явиться в его мир? Умри!', 12, 0, 100, 0, 0, 86558, 134337, 0, 0, 0, 'Сестра Диверсия to Player');
INSERT INTO `creature_text` VALUES (123464, 2, 0, 'Эта боль – ничто...', 12, 0, 100, 0, 0, 86579, 134362, 0, 0, 0, 'Сестра Диверсия to Player');
INSERT INTO `creature_text` VALUES (123474, 1, 0, 'I will not let myself be tormented!', 12, 0, 100, 0, 0, 86620, 134522, 0, 0, 0, 'Консул авгари to Player');
INSERT INTO `creature_text` VALUES (123474, 1, 0, 'Ты лишаешь нас покоя!', 12, 0, 100, 273, 0, 87869, 134521, 0, 0, 0, 'Консул авгари to Player');
INSERT INTO `creature_text` VALUES (123490, 3, 0, 'Готовьте наряды! Скоро начнется шоу \"Наденьте это немедленно!\".', 12, 0, 100, 0, 0, 0, 132553, 6503, 6503, 0, 'Искажающий пространство Сваггис to Player');
INSERT INTO `creature_text` VALUES (123494, 0, 0, 'Кирин-торский тур по тавернам обещает много выпивки и веселья! Найди Анебрию Солнечное Вино в \"Приюте фокусника\" и присоединяйся!', 12, 0, 100, 0, 0, 0, 132618, 0, 0, 0, 'Митис Литсон to Player');
INSERT INTO `creature_text` VALUES (123494, 0, 0, 'Хочешь промочить горло, $p? Зайди к Анебрии Солнечное Вино в \"Приют фокусника\" и присоединяйся к кирин-торскому туру по тавернам!', 12, 0, 100, 0, 0, 0, 132612, 0, 0, 0, 'Митис Литсон to Player');
INSERT INTO `creature_text` VALUES (123504, 1, 0, 'Зачем ты здесь?', 12, 0, 100, 0, 0, 86611, 134517, 0, 0, 0, 'Пробудительница авгари to Player');
INSERT INTO `creature_text` VALUES (123504, 1, 0, 'Ты насмехаешься над моими страданиями?', 12, 0, 100, 0, 0, 86614, 134519, 0, 0, 0, 'Пробудитель авгари to Player');
INSERT INTO `creature_text` VALUES (123514, 0, 0, 'Думаешь, ты свободная душа? Все мы служим своим повелителям.', 12, 0, 100, 0, 0, 0, 136343, 0, 0, 0, 'Горрексий to 0');
INSERT INTO `creature_text` VALUES (123514, 1, 0, 'Из Скверны... в Скверну...', 12, 0, 100, 0, 0, 0, 136342, 0, 0, 0, 'Горрексий to 0');
INSERT INTO `creature_text` VALUES (123520, 0, 0, 'I will not hold back. Let\'s get started.', 12, 0, 100, 2, 0, 87571, 132869, 0, 0, 0, 'Верховный консул Велара to Player');
INSERT INTO `creature_text` VALUES (123520, 1, 0, 'Помни, твои результаты влияют на репутацию всех нас.', 12, 0, 100, 2, 0, 87568, 135396, 0, 0, 0, 'Верховный консул Велара to Player');
INSERT INTO `creature_text` VALUES (123520, 2, 0, 'Я в тебе разочарована.', 12, 0, 100, 0, 0, 87569, 135397, 0, 0, 0, 'Верховный консул Велара to Player');
INSERT INTO `creature_text` VALUES (123520, 3, 0, 'Хватит. Я вижу твое мастерство.', 12, 0, 100, 0, 0, 87572, 132873, 0, 0, 0, 'Верховный консул Велара to Player');
INSERT INTO `creature_text` VALUES (123521, 0, 0, 'Перед тобой Джарасум, великий визирь Консерватория!', 12, 0, 100, 2, 0, 87181, 132868, 0, 0, 0, 'Великий визирь Джарасум to Player');
INSERT INTO `creature_text` VALUES (123521, 1, 0, 'Тебя погубит твоя самоуверенность!', 12, 0, 100, 2, 0, 87180, 135394, 0, 0, 0, 'Великий визирь Джарасум to Player');
INSERT INTO `creature_text` VALUES (123521, 2, 0, 'Не впечатляет.', 12, 0, 100, 0, 0, 87179, 135395, 0, 0, 0, 'Великий визирь Джарасум to Player');
INSERT INTO `creature_text` VALUES (123521, 3, 0, 'Хм. Похоже, бой окончен.', 12, 0, 100, 0, 0, 87182, 132872, 0, 0, 0, 'Великий визирь Джарасум to Player');
INSERT INTO `creature_text` VALUES (123522, 0, 0, 'Дурацкая затея, но давай, попробуй.', 12, 0, 100, 2, 0, 87759, 132870, 0, 0, 0, 'Верховный пробудитель Ааргон to Player');
INSERT INTO `creature_text` VALUES (123522, 1, 0, 'Ты даже не стараешься.', 12, 0, 100, 0, 0, 88097, 135392, 0, 0, 0, 'Верховный пробудитель Ааргон to Player');
INSERT INTO `creature_text` VALUES (123522, 2, 0, 'Продолжай в том же духе и протянешь еще немного.', 12, 0, 100, 0, 0, 87757, 135393, 0, 0, 0, 'Верховный пробудитель Ааргон to Player');
INSERT INTO `creature_text` VALUES (123522, 3, 0, 'Наверное, это можно назвать победой.', 12, 0, 100, 0, 0, 87760, 132871, 0, 0, 0, 'Верховный пробудитель Ааргон to Player');
INSERT INTO `creature_text` VALUES (123565, 0, 0, 'Да, да, несите сюда побрякушки!', 12, 0, 100, 0, 0, 53431, 137511, 0, 0, 0, 'Работник Легиона to Player');
INSERT INTO `creature_text` VALUES (123658, 1, 0, 'Как ты смеешь?!', 12, 0, 100, 1, 0, 57007, 129829, 0, 0, 0, 'Капитан гвардии to Player');
INSERT INTO `creature_text` VALUES (123659, 0, 0, 'Моя родина – Аргус, величайший из миров Великой Тьмы.', 12, 0, 100, 0, 0, 57007, 105127, 0, 0, 0, 'Гвардеец-страж to Player');
INSERT INTO `creature_text` VALUES (123659, 0, 0, 'Твоя смерть будет быстрой и мучительной!', 12, 0, 100, 0, 0, 57004, 105124, 0, 0, 0, 'Гвардеец-страж to Player');
INSERT INTO `creature_text` VALUES (123659, 1, 0, 'Познай истинную мощь!', 12, 0, 100, 0, 0, 57005, 105125, 0, 0, 0, 'Гвардеец-страж to Player');
INSERT INTO `creature_text` VALUES (123668, 0, 0, 'Аркенон порос, пророк. Армия Света с тобой.', 12, 0, 100, 66, 0, 87650, 132715, 0, 0, 0, 'Капитан Фария to Player');
INSERT INTO `creature_text` VALUES (123669, 0, 0, 'Верховный воздаятель Сорвос выражает согласие.', 16, 0, 100, 66, 0, 0, 133814, 0, 0, 0, 'Верховный воздаятель Сорвос to Player');
INSERT INTO `creature_text` VALUES (123670, 0, 0, 'Бараат Стрелок отдает вам честь.', 16, 0, 100, 66, 0, 0, 133807, 0, 0, 0, 'Бараат Стрелок to Player');
INSERT INTO `creature_text` VALUES (123671, 0, 0, 'Мастер клинка Телаамон коротко кивает.', 16, 0, 100, 66, 0, 0, 133863, 0, 0, 0, 'Мастер клинка Телаамон to Player');
INSERT INTO `creature_text` VALUES (123671, 3, 0, 'Мастер клинка Телаамон приветствует вас.', 16, 0, 100, 66, 0, 87734, 134024, 7, 7, 0, 'Мастер клинка Телаамон to Player');
INSERT INTO `creature_text` VALUES (123689, 0, 0, 'Пламя Аргуса возродит меня! Я... не... перестану сражаться.', 12, 0, 100, 0, 0, 86604, 132805, 0, 0, 0, 'Талестра Злобная to Player');
INSERT INTO `creature_text` VALUES (123743, 0, 0, 'Сюда, герои!', 14, 0, 100, 0, 0, 87562, 133381, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (123743, 1, 0, 'За мной!', 14, 0, 100, 0, 0, 87563, 141951, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (123743, 2, 0, 'Выясните, что они задумали... И остановите их.', 14, 0, 100, 1, 0, 87561, 135194, 0, 0, 0, 'Аллерия Ветрокрылая to Player');
INSERT INTO `creature_text` VALUES (123794, 0, 0, 'В матче побеждает $n!', 16, 0, 100, 0, 0, 86778, 132813, 0, 0, 0, 'Волшебная коробка для карт to Player');
INSERT INTO `creature_text` VALUES (124015, 1, 0, 'Моя смерть ничего не изменит.', 12, 0, 100, 0, 0, 57007, 134483, 0, 0, 0, 'Интендант Кл\'азз to Player');
INSERT INTO `creature_text` VALUES (124070, 0, 0, 'Приступим.', 12, 0, 100, 0, 0, 87995, 133313, 0, 0, 0, 'Дозорный Кворам to Player');
INSERT INTO `creature_text` VALUES (124077, 0, 0, 'Испытание очень простое: дойди до противоположного края этого строения.', 12, 0, 100, 0, 0, 87979, 132936, 0, 0, 0, 'Проекция Привратника to Player');
INSERT INTO `creature_text` VALUES (124077, 1, 0, 'Попробуй не умереть. Удачи!', 12, 0, 100, 0, 0, 87980, 132950, 0, 0, 0, 'Проекция Привратника to Player');
INSERT INTO `creature_text` VALUES (124077, 2, 0, 'Старайся уклоняться от лучей. Они весьма болезненны.', 12, 0, 100, 0, 0, 87981, 132953, 0, 0, 0, 'Проекция Привратника to Player');
INSERT INTO `creature_text` VALUES (124077, 3, 0, 'Осторожно: падать больно.', 12, 0, 100, 0, 0, 87982, 132962, 0, 0, 0, 'Проекция Привратника to Player');
INSERT INTO `creature_text` VALUES (124077, 4, 0, 'У тебя получилось. Чудно.', 12, 0, 100, 0, 0, 87978, 133015, 0, 0, 0, 'Проекция Привратника to Player');
INSERT INTO `creature_text` VALUES (124077, 5, 0, 'Ладно, испытание окончено. Но только потому, что мне негде его продолжить.', 12, 0, 100, 0, 0, 87983, 132973, 0, 0, 0, 'Проекция Привратника to Player');
