INSERT INTO `creature_text` VALUES (128725, 1, 0, 'Пойдем. Я хочу все увидеть сам.', 12, 0, 100, 397, 0, 87948, 130824, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (128735, 0, 0, 'Венец Познания – залог нашей победы.', 12, 0, 100, 0, 0, 87949, 131753, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (128735, 1, 0, 'Пойдем. Тьма не остановит нас.', 12, 0, 100, 0, 0, 87900, 135240, 0, 0, 0, 'Пророк Велен to Player');
INSERT INTO `creature_text` VALUES (128781, 0, 0, 'Впечатляет. Легион не ожидает такой мощи.', 12, 0, 100, 0, 0, 86599, 133225, 0, 0, 0, 'леди лиадрин to 0');
INSERT INTO `creature_text` VALUES (130030, 0, 0, 'Посмотрела бы я на их жмотские рожи, когда все крошшеры разлетятся на куски.', 12, 0, 100, 1, 0, 0, 146637, 0, 0, 0, 'Келси Стализвон to Player');
INSERT INTO `creature_text` VALUES (130184, 0, 0, 'Cannons, begin firing on my mark!', 14, 0, 100, 0, 0, 88738, 134884, 0, 0, 0, 'SAY_FUSILLADE');
INSERT INTO `creature_text` VALUES (130185, 0, 0, 'I have calculated your odds of survival--zero!', 12, 0, 100, 0, 0, 86919, 134892, 0, 0, 0, 'ENTROPIC_MINE_1');
INSERT INTO `creature_text` VALUES (130185, 0, 1, 'I hope you enjoy my inventions.', 12, 0, 100, 0, 0, 86917, 134888, 0, 0, 0, 'ENTROPIC_MINE_2');
