INSERT INTO `creature_text` VALUES (130186, 0, 1, 'Leave no one alive!', 12, 0, 100, 0, 0, 86984, 134886, 0, 0, 0, 'SUMMON_REINFORCEMENTS_2');
INSERT INTO `creature_text` VALUES (130186, 0, 2, 'Legion, show these weaklings why we are the dominant force in the cosmos!', 12, 0, 100, 0, 0, 86985, 134887, 0, 0, 0, 'SUMMON_REINFORCEMENTS_3');
INSERT INTO `creature_text` VALUES (130241, 3, 0, '  whut  hon d ri       ri   hon ', 12, 14, 100, 1, 0, 0, 0, 1138325, 1138325, 0, 'Зандаларский архонт');
INSERT INTO `creature_text` VALUES (130245, 3, 0, '   M  Du  sk  deh   s qua wo nyam ', 12, 14, 100, 1, 0, 0, 0, 1113153, 1113153, 0, 'Зандаларский берсерк');
INSERT INTO `creature_text` VALUES (130532, 0, 0, 'Неплохой отряд. Конечно, еще разбойник-другой не помешал бы... но и вы сгодитесь.', 12, 0, 100, 1, 0, 94736, 143496, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 1, 0, 'Герои, на острове у побережья Фераласа обнаружены залежи азерита. Нельзя допустить, чтобы до них добралась Орда.', 12, 0, 100, 1, 0, 94733, 143483, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 2, 0, 'Наш корабль находится прямо над центром острова. Соберите как можно больше азерита. В нем – залог нашей победы!', 12, 0, 100, 1, 0, 94734, 143484, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 3, 0, 'Герои, под нами богатые залежи азерита. Спускайтесь и соберите его столько, сколько сможете. И не щадите мародеров из Орды!', 12, 0, 100, 1, 0, 94737, 143486, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 4, 0, 'Прекрасно! Еще немного азерита, и победа у нас в кармане!', 12, 0, 100, 0, 0, 8173, 143491, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 4, 1, 'Неплохо, герои, но нам нужно больше азерита!', 12, 0, 100, 0, 0, 94722, 143489, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 5, 0, 'А вот и припасы! Собирайте их!', 12, 0, 100, 0, 0, 94739, 143493, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 5, 1, 'Герои, мы сбросили вам припасы. Не дайте Орде до них добраться!', 12, 0, 100, 0, 0, 94738, 143488, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 6, 0, 'Азерит снова выходит на поверхность! Захватите отмеченные точки!', 12, 0, 100, 0, 0, 8173, 143492, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 7, 0, 'Блестяще, герои! Король Ринн будет счастлив узнать о вашей победе. За Альянс!', 12, 0, 100, 0, 0, 8173, 143494, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 7, 1, 'Благодаря вам Орда не получит азерит. Превосходно!', 12, 0, 100, 0, 0, 94723, 143490, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 8, 0, 'Проклятье! Орда нас обошла, но клянусь Покоем Льва, в следующий раз мы победим!', 12, 0, 100, 0, 0, 94731, 143495, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 9, 0, 'Ордынцы думают, что обставили нас. Преподайте им урок!', 12, 0, 100, 0, 0, 94728, 143498, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 9, 1, 'Сражайтесь за короля! Сражайтесь за Альянс!', 12, 0, 100, 0, 0, 94729, 143499, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130532, 9, 2, 'Гоните Орду с острова! Он принадлежит нам!', 12, 0, 100, 0, 0, 94730, 143500, 0, 0, 0, 'Мастер Матиас Шоу');
INSERT INTO `creature_text` VALUES (130773, 1, 0, 'Как же приятно снова взять в руки палицу!', 12, 0, 100, 0, 0, 93821, 141900, 8500, 8500, 0, 'Бейн Кровавое Копыто to Player');
INSERT INTO `creature_text` VALUES (131328, 3, 0, 'Пусть звезды укажут тебе путь, $p. Я слышала много занимательных вещей о предгорьях Хилсбрада. Пожалуй, я отправлюсь туда.', 12, 0, 100, 1, 0, 0, 143284, 1375, 1375, 0, 'Мелитье Валуран to Player');
INSERT INTO `creature_text` VALUES (131773, 0, 0, 'Дайте-ка я взгляну на вас. Печальное зрелище... Ни одного темного следопыта. Впрочем, и вы сойдете.', 12, 0, 100, 0, 0, 94758, 143504, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 1, 0, 'Бойцы Орды! Мы нашли залежи азерита на каком-то чертовом острове у побережья Фераласа. Захватите его во имя Темной Госпожи!', 12, 0, 100, 0, 0, 79286, 143501, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 2, 0, 'Ваша задача предельно проста. Соберите весь азерит до последней крупицы, чтобы он не достался врагу. Надеюсь, вопросов нет?', 12, 0, 100, 0, 0, 94756, 143502, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 3, 0, 'Там внизу азерит! Соберите его для вождя. Если Альянс будет путаться под ногами, усейте остров трупами. За дело!', 12, 0, 100, 0, 0, 94759, 143505, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 4, 0, 'Для начала неплохо, но нам нужно больше азерита!', 12, 0, 100, 0, 0, 94744, 143508, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 4, 1, 'Мы почти у цели. Не мешкайте! Нам нужен азерит!', 12, 0, 100, 0, 0, 94746, 143510, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 5, 0, 'Берите припасы, но не забывайте, за чем вас послала королева банши!', 12, 0, 100, 0, 0, 94761, 143507, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 5, 1, 'Припасы на подходе. Сделайте так, чтобы они достались вам, а не Альянсу!', 12, 0, 100, 0, 0, 94760, 143506, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 6, 0, 'Обнаружены новые точки выброса азерита на острове! Я отметил их на карте.', 12, 0, 100, 0, 0, 94747, 143511, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 6, 1, 'Здесь даже больше азерита, чем мы думали! Я отметил нужные точки на карте!', 12, 0, 100, 0, 0, 94748, 143512, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 7, 0, 'Ха! Альянс остался ни с чем! Славная работа!', 12, 0, 100, 0, 0, 94745, 143509, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 7, 1, 'Изящной эту победу не назовешь, но вы все же показали Альянсу, где их место. За Орду!', 12, 0, 100, 0, 0, 94754, 143517, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 8, 0, 'Вы проиграли ИМ? Какой позор! Отправляйтесь в Подгород на переподготовку. Снова придется вас всему учить...', 12, 0, 100, 0, 0, 94753, 143518, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131773, 9, 0, 'Убейте всех! Вы же не хотите разочаровать вождя?', 12, 0, 100, 0, 0, 94751, 143515, 0, 0, 0, 'Натанос Гнилостень');
INSERT INTO `creature_text` VALUES (131907, 1, 0, 'Мы – предвестники разрушения!', 12, 0, 100, 0, 0, 0, 147252, 0, 0, 0, 'Сумеречная ритуалистка to Player');
INSERT INTO `creature_text` VALUES (131908, 1, 0, 'Все склонятся перед нашим повелителем!', 12, 0, 100, 0, 0, 0, 147257, 0, 0, 0, 'Сумеречный выживший to Player');
INSERT INTO `creature_text` VALUES (131957, 0, 0, 'Видишь меня? Больше не увидишь!', 12, 0, 100, 0, 0, 0, 146379, 0, 0, 0, 'Агент ШРУ to Player');
INSERT INTO `creature_text` VALUES (131957, 0, 1, 'Отродье Орды! Умри!', 12, 0, 100, 0, 0, 0, 146380, 0, 0, 0, 'Агент ШРУ to Player');
INSERT INTO `creature_text` VALUES (131957, 0, 2, '$gМерзкий:Мерзкая; |3-6($r), ты у меня сейчас кровью истечешь!', 12, 0, 100, 0, 0, 0, 146375, 0, 0, 0, 'Агент ШРУ to Player');
INSERT INTO `creature_text` VALUES (131957, 0, 3, 'Ты заплатишь за свои преступления против Альянса!', 12, 0, 100, 0, 0, 0, 146376, 0, 0, 0, 'Агент ШРУ to Player');
INSERT INTO `creature_text` VALUES (131963, 0, 0, 'Нам приказано получить образцы этой таинственной руды любым возможным способом.', 12, 0, 100, 1, 0, 0, 146636, 0, 0, 0, 'Нолан Быстрый to Player');
INSERT INTO `creature_text` VALUES (131963, 1, 0, 'Мы не можем допустить возрождения Сумеречного Молота. Скитальца Бездны нужно устранить!', 12, 0, 100, 1, 0, 0, 146641, 0, 0, 0, 'Нолан Быстрый to Player');
INSERT INTO `creature_text` VALUES (131963, 2, 0, 'Мы взяли под контроль ситуацию в Силитусе. ШРУ позаботится об остальном. Спасибо, $n.', 12, 0, 100, 1, 0, 0, 147310, 0, 0, 0, 'Нолан Быстрый to Player');
INSERT INTO `creature_text` VALUES (132040, 0, 0, 'Не бойся замарать руки, вскрывая черепа силитидам!', 12, 0, 100, 1, 0, 0, 146851, 0, 0, 0, 'Пиксни Ржавобамс to Player');
INSERT INTO `creature_text` VALUES (132040, 1, 0, 'Эти исследования помогут нам понять природу силы гигантского клинка!', 12, 0, 100, 1, 0, 0, 146854, 0, 0, 0, 'Пиксни Ржавобамс to Player');
INSERT INTO `creature_text` VALUES (132045, 0, 0, 'Убей этих негодных шпионов ШРУ, пока они не успели все испоганить!', 12, 0, 100, 1, 0, 0, 146857, 0, 0, 0, 'Гизмакс Жирострел to Player');
INSERT INTO `creature_text` VALUES (132055, 1, 0, 'I\'ll teach you a lesson!', 12, 0, 100, 0, 0, 0, 146368, 0, 0, 0, 'Геолог картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132055, 1, 0, 'Драться со мной? Это ты зря!', 12, 0, 100, 0, 0, 0, 146370, 0, 0, 0, 'Геолог картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132055, 1, 0, 'Не связывайся с картелем!', 12, 0, 100, 0, 0, 0, 146369, 0, 0, 0, 'Геолог картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132055, 1, 0, 'Прочь отсюда!', 12, 0, 100, 0, 0, 0, 146371, 0, 0, 0, 'Геолог картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132062, 1, 0, 'Драться со мной? Это ты зря!', 12, 0, 100, 0, 0, 0, 146370, 0, 0, 0, 'Работник картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132062, 1, 0, 'Прочь отсюда!', 12, 0, 100, 0, 0, 0, 146371, 0, 0, 261779, 'Работник картеля Трюмных Вод to Player');
INSERT INTO `creature_text` VALUES (132090, 2, 0, 'Он вернется.', 12, 0, 100, 0, 0, 0, 107820, 0, 0, 260467, 'Тень Мрака to Эния Небесный Рог');
INSERT INTO `creature_text` VALUES (132147, 0, 0, 'Преподай урок этим тунеядцам-геологам!', 12, 0, 100, 1, 0, 0, 146861, 0, 0, 0, 'Грол Боевой Клинок to Player');
INSERT INTO `creature_text` VALUES (132147, 1, 0, 'Мы не можем допустить, чтобы этот безумец собрал остатки культа под своим началом. Принеси мне его голову!', 12, 0, 100, 1, 0, 0, 146863, 0, 0, 0, 'Грол Боевой Клинок to Player');
INSERT INTO `creature_text` VALUES (132147, 2, 0, 'Мы взяли под контроль ситуацию в Силитусе. Я позабочусь об остальном. Ступай с честью, $n.', 12, 0, 100, 1, 0, 0, 147304, 0, 0, 0, 'Грол Боевой Клинок to Player');
INSERT INTO `creature_text` VALUES (132190, 3, 0, 'Waldir Nu waldir ', 12, 7, 100, 0, 0, 0, 0, 9079, 497066, 0, 'Рабочий Альянса to Player');
INSERT INTO `creature_text` VALUES (132192, 0, 0, 'Я в жизни не видел ничего подобного! Что это может быть?', 12, 0, 100, 1, 0, 0, 147309, 0, 0, 0, 'Харг Каменная Мантия to Player');
INSERT INTO `creature_text` VALUES (132199, 1, 0, 'Сейчас твоя голова расстанется с телом! ХА-ХА-ХА!', 12, 0, 100, 0, 0, 0, 111131, 0, 0, 0, 'Сумеречный землелом to Player');
INSERT INTO `creature_text` VALUES (132199, 3, 0, 'Наступил конец света... а у меня душа поет! ХА-ХА-ХА!', 12, 0, 100, 53, 0, 0, 111124, 20185, 20185, 0, 'Сумеречный землелом to Player');
INSERT INTO `creature_text` VALUES (132203, 1, 0, 'Тебя настигнет скорая расплата!', 12, 0, 100, 0, 0, 0, 146373, 0, 0, 0, 'Мишиэль Скиталец Бездны to Player');
INSERT INTO `creature_text` VALUES (132212, 0, 0, 'Да благословит тебя Мать-Земля!', 12, 0, 100, 5, 0, 0, 144756, 906, 906, 260619, 'Заступник Крутогорья to Player');
INSERT INTO `creature_text` VALUES (132216, 0, 0, 'Голоса... Они сводят меня с ума!', 12, 0, 100, 5, 0, 0, 144753, 1391, 1391, 0, 'Заступница Крутогорья to Player');
INSERT INTO `creature_text` VALUES (132216, 3, 0, 'Я должна... Мой разум... Нужно уйти отсюда!', 12, 0, 100, 6, 0, 0, 144754, 1468, 1468, 0, 'Заступница Крутогорья to Player');
INSERT INTO `creature_text` VALUES (132591, 1, 0, 'Огмот съест твою душу! ХА-ХА-ХА!', 12, 0, 100, 0, 0, 0, 146374, 0, 0, 0, 'Огмот Безумный to Player');
INSERT INTO `creature_text` VALUES (132606, 0, 0, 'К каким дверям хранит ключи биология силитидов? Наука должна знать!', 12, 0, 100, 1, 0, 0, 146638, 0, 0, 0, 'Тамми Шестевертка to Player');
INSERT INTO `creature_text` VALUES (132606, 1, 0, 'Спасибо тебе! Теперь мы на один шаг ближе к пониманию того, как клинок влияет на Силитус.', 12, 0, 100, 1, 0, 0, 147308, 0, 0, 0, 'Тамми Шестевертка to Player');
INSERT INTO `creature_text` VALUES (132969, 0, 0, 'Кто меня звал?', 12, 0, 100, 6, 0, 0, 146070, 0, 0, 0, 'Кэти Штемпельпупс to Player');
INSERT INTO `creature_text` VALUES (132999, 0, 0, 'О, кто это здесь у нас?', 12, 0, 100, 0, 0, 0, 146117, 0, 0, 0, 'Повелитель Нексуса Ашаал');
INSERT INTO `creature_text` VALUES (132999, 1, 0, 'Интересно, как тебе стало известно, что мы здесь?', 12, 0, 100, 0, 0, 0, 146118, 0, 0, 0, 'Повелитель Нексуса Ашаал');
INSERT INTO `creature_text` VALUES (132999, 2, 0, 'Вы, смертные, все одинаковы. Сражаетесь, сами не зная, за что.', 12, 0, 100, 0, 0, 0, 146119, 0, 0, 0, 'Повелитель Нексуса Ашаал');
INSERT INTO `creature_text` VALUES (132999, 3, 0, 'Как жаль. Из тебя $gполучился бы хороший прислужник:получилась бы хорошая прислужница;.', 12, 0, 100, 0, 0, 0, 146120, 0, 0, 0, 'Повелитель Нексуса Ашаал to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 0, 'У меня вообще-то был перерыв!', 12, 0, 100, 0, 0, 0, 146352, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 1, 'Хорошо! Хорошо! Я уже работаю!', 12, 0, 100, 0, 0, 0, 146351, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 2, 'Да я только глаза прикрыл!', 12, 0, 100, 0, 0, 0, 146355, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 3, 'Работа не ждет? Вас понял!', 12, 0, 100, 0, 0, 0, 146353, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 4, 'Не нужно распускать руки! Я уже возвращаюсь к работе!', 12, 0, 100, 0, 0, 0, 146354, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133019, 0, 5, 'Да я только глаза прикрыла!', 12, 0, 100, 0, 0, 0, 146355, 0, 0, 0, 'Ленивый геолог to Player');
INSERT INTO `creature_text` VALUES (133059, 3, 0, 'Открыть огонь!', 12, 0, 100, 25, 0, 79286, 146233, 252406, 565000, 0, 'Генерал Харрис');
INSERT INTO `creature_text` VALUES (168137, 0, 0, 'Погоди... Ты не из тех чернокнижников, что были здесь раньше. Зачем ты здесь?', 12, 0, 100, 0, 0, 0, 69488, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 1, 0, 'Прошло уже много месяцев с тех пор, как двое чернокнижников проникли в храм. И я до сих пор не знаю, что им было нужно здесь...', 12, 0, 100, 0, 0, 0, 69498, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 2, 0, 'Следуй за мной. Они побывали и в Святилище Потерянных Душ, и на верхнем этаже храма.', 12, 0, 100, 0, 0, 0, 69499, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 3, 0, 'I decided that this warlock can be trusted. He can freely move around the temple.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 4, 0, 'Пройдет еще много лет, прежде чем мы сможем устранить ущерб, нанесенный этому святому месту Магтериодном, Иллиданом и орками Скверны.', 12, 0, 100, 0, 0, 0, 69501, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 5, 0, 'Здесь находится Святилище Потерянных Душ. В нем пребывают души тысяч моих погибших братьев.', 12, 0, 100, 0, 0, 0, 69502, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 6, 0, 'Будь $gосторожен:осторожна;. Многие из ловушек все еще действуют. Может быть, какое-то из твоих демонических заклинаний поможет тебе разведать обстановку, прежде чем двигаться вперед.', 12, 0, 100, 0, 0, 0, 69503, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 7, 0, '$n! Мои воины оказались в ловушке там, внизу. Времени мало. Помоги мне спасти их!', 14, 0, 100, 0, 0, 0, 69538, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (168137, 8, 0, 'Глупые чернокнижники! Я знал, что вы предпримете еще одну попытку разграбить наш храм!..', 14, 0, 100, 0, 0, 0, 69487, 0, 0, 0, 'Акама to Player');
INSERT INTO `creature_text` VALUES (170166, 0, 0, 'НЕТ!', 12, 0, 100, 0, 0, 0, 135514, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (170166, 1, 0, 'I did only what you yourself asked me before, Canretad. It’s your own fault.', 12, 0, 100, 0, 0, 0, 72496, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (170166, 2, 0, 'Канретад настолько погрузился в энергию Скверны, что она полностью подчинила его. Но лишь глоток его силы не должен повредить. Испей ее.', 12, 0, 100, 0, 0, 0, 72498, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (170166, 3, 0, 'А теперь иди и используй полученные знания. Если когда-нибудь тебе захочется избавиться от энергии Скверны или вновь ощутить силу Канретада, ищи меня в Алтаре Проклятия в Долине Призрачной Луны. Там я буду присматривать за своим пленником.', 12, 0, 100, 0, 0, 0, 72499, 0, 0, 0, 'to Player');
INSERT INTO `creature_text` VALUES (193100, 0, 0, 'My mind... my flesh... I\'m... rotting...!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200131, 0, 0, 'Test', 14, 0, 100, 0, 0, 0, 27372, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200131, 1, 0, 'Testik lol', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200132, 0, 0, 'test1', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200132, 1, 0, 'test2', 41, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200300, 0, 0, 'Your world is slowly dying mortals! You can\'t stop me.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200300, 1, 0, 'Ah-ha-ha you are so naive and funny!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200300, 2, 0, 'I have lost my power ...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200301, 0, 0, 'Darkness will cover you!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200301, 1, 0, 'No one can defeat me!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200301, 2, 0, 'I have fallen, but I\'m not your only problem ... Astaroth will destroy you!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200302, 0, 0, 'Hey! Here!', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200303, 0, 0, 'How dare you disturb me? You\'ll pay for this insolence.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200303, 1, 0, 'Mortals are too fragile ...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200303, 2, 0, 'No-o-oo! I have not finished my quest! I can not die now! Lord Sargeras, I let you down ...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200303, 3, 0, 'Swords and armor? Ha! What can they do against my magic?', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200306, 0, 0, 'I do not feel you have the strength that is needed to control that power.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200306, 1, 0, 'You are not prepared! Still not prepared ...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200306, 2, 0, 'Maybe I was wrong ...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 0, 0, 'Ты $gзахватил:захватила; с собой парашют? Приятного полета!', 14, 0, 100, 11, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 1, 0, 'Что-то совсем плохо ты выглядишь, $gдруг:подруга;. $gЗамерз:Замерзла;? Я помогу тебе!', 12, 0, 100, 11, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 2, 0, 'Зачем тебе я? Когда можно неплохо провести время в какой-нибудь забегаловке.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 3, 0, 'Во времена чумы, когда люди потеряли надежду. Мой предок, великий алхимик создал артефакт, который должен был вернуть людям веру.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 4, 0, 'Но так получилось, что он ошибся в расчетах и артефакт работал только в определенное время. Он давал заряд энергии обитателям Азерота на целый год, поэтому и назвали это духом нового года. Новым воплощением жизни Азерота. Но теперь этого не случится. Артефакт был похищен!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 5, 0, 'Артефакт просто так не спрятать и не уничтожить. Я бы смог создать приспособление, которое вычислит его местоположение но, к сожалению, у меня нет материалов для создания, сей аппарата. ', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 6, 0, '$c, тебе придется раздобыть материалы и принести их мне.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 7, 0, 'Совсем не ожидал от тебя такого, Дедушка Зима.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 8, 0, 'Мой предок доверил тебе артефакт, чтобы ты помог ему воплотить его мечту, нести счастье и праздничное настроение из года в год.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 9, 0, 'А ты решил забрать его у всех жителей Азерота, уничтожив артефакт.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 10, 0, 'На самом деле мне все равно, но эти ребята настойчиво попросили меня им помочь.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 11, 0, 'Я знаю, что тебя кто-то контролирует.  Жалкая магия… я развею ее!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 12, 0, 'Готово! Теперь уничтожьте оболочку и контроль развеется!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200400, 13, 0, 'Интересно. На это стоит взглянуть.', 12, 0, 100, 1, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200402, 0, 0, 'Я вижу, что у тебя есть кое-какая интересная мне вещица, а так же то, что тебе нужна информация. Я думаю, мы сможем договориться.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200403, 0, 0, 'Добро пожаловать в мое временное убежище и вашу могилу! Вы опоздали, артефакта здесь уже нет, а скоро не будет и вас…', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200403, 1, 0, 'Я думаю, даже самые глупые из вас уже догадались о том, что я не тот, за кого себя выдаю. Ни к чему этот маскарад.', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (200403, 2, 0, 'Я выполнил план. Заманил вас, героев, на ложный след, теперь уничтожению артефакта никто не помешает! Вас погубит тот, на кого вы надеетесь...', 14, 0, 100, 0, 0, 0, 0, 0, 0, 0, '');
