INSERT INTO `quest_offer_reward` VALUES (7810, 0, 0, 0, 0, 0, 0, 0, 0, 'Arr Me Hearty! Gaining one Trinket be not enough to make ye master of the Arena! Now tis the time to truly prove your steel.', 0);
INSERT INTO `quest_offer_reward` VALUES (7811, 0, 0, 0, 0, 0, 0, 0, 0, 'Fantastic - these will go right to use, I assure you. Thank you again for your assistance! We\'d certainly be lost without you.$b$bWith all the donations you\'ve made, I would certainly process any additional runecloth donations you care to make in the future. Just be sure to check in with me, and I\'ll personally make sure that your good deeds won\'t go unrecognized!', 0);
