INSERT INTO `quest_offer_reward` VALUES (7840, 0, 0, 0, 0, 0, 0, 0, 0, '<Lard takes the huge haunch of meat from you and stuffs it in his face.>$B$BDis hit da spot! Tanks little one.$B$BLard make you special picnic basket for help Lard.', 0);
INSERT INTO `quest_offer_reward` VALUES (7841, 0, 0, 0, 0, 0, 0, 0, 0, 'It be lookin\' like the Revantusk are forgin\' new alliances as well, mon! You done good.', 0);
INSERT INTO `quest_offer_reward` VALUES (7842, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, mon, this gonna be good. One more step to go and Wildhammer see that they can\'t mess with troll!', 0);
INSERT INTO `quest_offer_reward` VALUES (7843, 0, 0, 0, 0, 0, 0, 0, 0, 'I wish I coulda been there to see the look on their faces, mon. Are they packing up their bags? Leavin\' town?$B$BYou done good, $N. Real good. Otho reward you with the big mojo.', 0);
INSERT INTO `quest_offer_reward` VALUES (7844, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $C. Zul\'jin himself could not have done a better job!', 0);
INSERT INTO `quest_offer_reward` VALUES (7845, 0, 0, 0, 0, 0, 0, 0, 0, 'You must act quickly, $N. Surely the guards have been alerted to your presence.', 0);
INSERT INTO `quest_offer_reward` VALUES (7846, 0, 0, 0, 0, 0, 0, 0, 0, 'Freedom at last! Thank you, $N. Revantusk will know your deeds!', 0);
INSERT INTO `quest_offer_reward` VALUES (7847, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done the Revantusk a great service, $N! We owe you a great debt of gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (7848, 0, 0, 0, 0, 0, 0, 0, 0, 'I am now able to transport you to the Molten Core. Ask and it shall be done.', 0);
INSERT INTO `quest_offer_reward` VALUES (7849, 0, 0, 0, 0, 0, 0, 0, 0, 'My dearest brother, how I will miss you...$B$BThank you for your assistance, $N. You are truly a hero in my eyes.', 0);
INSERT INTO `quest_offer_reward` VALUES (7850, 0, 0, 0, 0, 0, 0, 0, 0, 'The dark energy that compels the Vilebranch has been lifted - for now. We must remain ever vigilant in our battle against evil, as even if the Vile Priestess is destroyed, another soon takes her place.', 0);
INSERT INTO `quest_offer_reward` VALUES (7861, 0, 0, 0, 0, 0, 0, 0, 0, 'A crippling blow to the Vilebranch, indeed. You shall be richly rewarded, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (7862, 0, 0, 0, 0, 0, 0, 0, 0, 'You are indeed a worthy candidate for the new position, $N. Accept this trinket as a token of our appreciation for your hard work. I will be reviewing all applications next year.', 0);
INSERT INTO `quest_offer_reward` VALUES (7863, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve certainly begun to prove yourself to us, $N. As a reward, please take this care package! Inside it you\'ll find rations and bandages that are usable only inside Warsong Gulch. $B$BAs you continue to prove yourself inside the Gulch, you\'ll find that these items will become available to you for purchase. Rations become available when you are Friendly with us, and bandages when you are Honored.', 0);
