INSERT INTO `quest_offer_reward` VALUES (8635, 0, 0, 0, 0, 0, 0, 0, 0, 'The battles of ages past are seeds from which legends grow. Yours is a time of heroes, young one. May your legend take root, thrive.$B$BI bid you well, $N, and offer you this token...', 0);
