INSERT INTO `quest_offer_reward` VALUES (8921, 0, 0, 0, 0, 0, 0, 0, 0, 'These are precisely the materials I needed. Quite timely too, $N!$B$BIn mere moments I will have your distiller ready to go! In the meantime, take this as a reward for aiding me in my project.', 0);
INSERT INTO `quest_offer_reward` VALUES (8922, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Just as I was running low!$B$BYou say Deliana sent you?', 0);
INSERT INTO `quest_offer_reward` VALUES (8923, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Just as I was running low!$B$BYou say Mokvar sent you?', 0);
INSERT INTO `quest_offer_reward` VALUES (8924, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, these will do great!$B$BBy tapping into the otherworldly energies contained within these substances we\'ll be able to reach out to those whose souls haven\'t quite left this world yet!', 0);
INSERT INTO `quest_offer_reward` VALUES (8925, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done it! This will certainly provide enough energy to power up the extra-dimensional ghost revealer!', 0);
INSERT INTO `quest_offer_reward` VALUES (8926, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve kept your end of the bargain, I shall keep mine.$B$BJust remember that I\'m holding on to the best pieces until your work is finished.', 0);
INSERT INTO `quest_offer_reward` VALUES (8927, 0, 0, 0, 0, 0, 0, 0, 0, 'Our agreement remains in place. Just remember, this is but a taste of what awaits you. Free me of this cursed fate and I will reward you with items of truly great power.', 0);
INSERT INTO `quest_offer_reward` VALUES (8928, 0, 0, 0, 0, 0, 0, 0, 0, 'You did it! This rod will do perfectly. Now to put it all together...', 0);
