INSERT INTO `quest_offer_reward` VALUES (8945, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for rescuing me... I feared for my life. Please accept this as a small token of my gratitude.$B$BAnthion is... no... he can\'t be...', 0);
INSERT INTO `quest_offer_reward` VALUES (8946, 0, 0, 0, 0, 0, 0, 0, 0, 'That locket... you found her! I can rest in peace at long last.$B$BI shall now answer any questions you might have, $N. But make haste, my time in this world is not long.', 0);
INSERT INTO `quest_offer_reward` VALUES (8947, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. I shall transmute these into something more suitable to our needs.', 0);
INSERT INTO `quest_offer_reward` VALUES (8948, 0, 0, 0, 0, 0, 0, 0, 0, 'Anthion sent you, you say? I thought he was dead. I guess I\'ve heard stranger things in my life.$B$BVery well, then.  What is it you want me to do with this banner?', 0);
INSERT INTO `quest_offer_reward` VALUES (8949, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmmm... strangely enough I don\'t feel any better about my brother\'s death at the hands of those brutes.$B$BWell, you held your end of the bargain. Let\'s see what I can do for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (8950, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, these will do just fine. After I\'m done, no force in the world will be able to stop your victim from accepting your challenge.', 0);
INSERT INTO `quest_offer_reward` VALUES (8951, 0, 0, 0, 0, 0, 0, 0, 0, 'I can\'t believe our lives are all but forfeit all because of a stupid medallion! And you\'re sure Anthion mentioned Bodley?$B$BWell, you\'ve done your job so let\'s get your reward out of the way.', 0);
