INSERT INTO `quest_offer_reward` VALUES (9050, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B , you got everything just as i asked and now i can make a Redemption Wristguards.', 0);
INSERT INTO `quest_offer_reward` VALUES (9051, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $N.  You are brave and wise.$B$BTell me, how did the devilsaur react to the toxin?', 0);
INSERT INTO `quest_offer_reward` VALUES (9052, 0, 0, 0, 0, 0, 0, 0, 0, 'The ingredients at last!$B$BWith the Un\'Goro soil I have collected, the toxin is easily concocted...', 0);
INSERT INTO `quest_offer_reward` VALUES (9053, 0, 0, 0, 0, 0, 0, 0, 0, 'At last, the toxin can be created in full. I dare not require you to inflict it upon the creature again, as I have asked too much already.$B$BYou have my thanks and respect. May you be well in your travels, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (9054, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B , you got everything just as i asked and now i can make a Cryptstalker Tunic.', 0);
