INSERT INTO `quest_offer_reward` VALUES (9062, 0, 0, 0, 0, 0, 0, 0, 0, 'The book is completely soaked. Instructor Antheol\'s not going to like this at all.$B$BHere, take this money. I have an idea.', 0);
INSERT INTO `quest_offer_reward` VALUES (9063, 0, 0, 0, 0, 0, 0, 0, 0, 'You have traveled far to meet with me, $C, and not a moment too soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (9064, 0, 0, 0, 0, 0, 0, 0, 0, 'You say those two incompetent fools bribed you to lie to me? You\'ve done very well in coming to me, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (9066, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $N. Don\'t worry, they\'re not stuck in that form permanently.  Their apprenticeship, however, won\'t be restored until they earn it back.', 0);
INSERT INTO `quest_offer_reward` VALUES (9067, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re quite the energetic young $gman:woman;, aren\'t you?$B$BThis all looks very adequate. You certainly deserve compensation for gathering up all of this for me, and something a little extra I think.$B$BOh, I almost forgot, here\'s an invitation to the party. And, $c, next time that you drop in make sure to dress up in something a little more... festive.', 0);
INSERT INTO `quest_offer_reward` VALUES (9068, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B , you got everything just as i asked and now i can make a Earthshatter Tunic.', 0);
