INSERT INTO `quest_offer_reward` VALUES (9075, 0, 0, 0, 0, 0, 0, 0, 0, 'The wristguards are ready, $N.$B$B<Rimblat bows.>', 0);
INSERT INTO `quest_offer_reward` VALUES (9076, 0, 0, 0, 0, 0, 0, 0, 0, 'You defeated him after all!  Undoubtedly my men softened him up for you.$B$BI jest, $C.  You\'ve done well.  You\'ll make a name for yourself if you keep your nose clean.', 0);
INSERT INTO `quest_offer_reward` VALUES (9077, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Breastplate.', 0);
