INSERT INTO `quest_offer_reward` VALUES (9079, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B,you got everything just as i asked and now i can make a Bonescythe Helmet.', 0);
INSERT INTO `quest_offer_reward` VALUES (9080, 0, 0, 0, 0, 0, 0, 0, 0, '<Rohan wipes the sweat from his brow.>$B$BThese shoulders took some work! Don\'t go out and get yourself killed like a scrub, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (9081, 0, 0, 0, 0, 0, 0, 0, 0, 'They go on your feet. Say, you haven\'t been nosing around in Tyr\'s Hand again, have you?', 0);
INSERT INTO `quest_offer_reward` VALUES (9082, 0, 0, 0, 0, 0, 0, 0, 0, 'These gauntlets could probably substitute as weapons if absolutely necessary. Wear them with pride!', 0);
INSERT INTO `quest_offer_reward` VALUES (9083, 0, 0, 0, 0, 0, 0, 0, 0, 'The belt is ready, $N. Have you ever seen such a high-quality item sold for so little?', 0);
INSERT INTO `quest_offer_reward` VALUES (9084, 0, 0, 0, 0, 0, 0, 0, 0, 'The bracers are ready, $N.$B$B<Rohan bows.>', 0);
INSERT INTO `quest_offer_reward` VALUES (9085, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $n. With the shadows destroyed, our hope of victory against the Lich King survives.', 0);
