INSERT INTO `quest_offer_reward` VALUES (9111, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B, you got everything just as i asked and now i can make a Robe of Faith.', 0);
INSERT INTO `quest_offer_reward` VALUES (9112, 0, 0, 0, 0, 0, 0, 0, 0, 'Exellent $N !$B, you got everything just as i asked and now i can make a Leggings of Faith.', 0);
