INSERT INTO `quest_offer_reward` VALUES (11788, 0, 0, 0, 0, 0, 0, 0, 0, 'Wow, you survived!$B$BI guess all that\'s left to do now is to deal with Gearmaster Mechazod.', 0);
INSERT INTO `quest_offer_reward` VALUES (11789, 0, 0, 0, 0, 0, 0, 0, 0, 'I thank you, $N.  This man\'s recovery will be slow, but we won\'t lose him.$B$BWhat do you say?  You found cultists inside the Stormbreaker?  You\'d best notify someone... and bring some evidence.', 0);
INSERT INTO `quest_offer_reward` VALUES (11790, 0, 0, 0, 0, 0, 0, 0, 0, 'Cultists? Below deck? I ought to have my whole crew flogged!$B$BListen... why don\'t we have Admiral Cantlebree take care of this... situation?', 0);
INSERT INTO `quest_offer_reward` VALUES (11791, 66, 0, 0, 0, 0, 0, 0, 0, 'Thank you for your concern, $N.  We were made aware of the situation not too long ago.$B$BCounselor Talbot assures me that this was an isolated incident and not a cause for military concern.$B$BOur civilian authorities will make sure everyone involved is properly prosecuted.', 0);
INSERT INTO `quest_offer_reward` VALUES (11792, 1, 0, 0, 0, 0, 0, 0, 0, 'Be discreet, $N.  We cannot look at this evidence in front of prying eyes.', 0);
INSERT INTO `quest_offer_reward` VALUES (11793, 2, 0, 0, 0, 0, 0, 0, 0, 'Speak quickly, $c.  What information do you have on the cult?', 0);
INSERT INTO `quest_offer_reward` VALUES (11794, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done well, $N.  However, I can\'t shake the feeling that this is not the last we\'ve seen of these death cultists.$B$BDarkness has a way of hiding even in plain sight.', 0);
INSERT INTO `quest_offer_reward` VALUES (11795, 1, 1, 0, 0, 0, 0, 0, 0, 'Thank you once again, $N. With your help, I may yet be able to complete this mission, disastrous as it\'s been, and return to Fizzcrank to make my report and properly honor the dead.', 0);
INSERT INTO `quest_offer_reward` VALUES (11796, 1, 1, 0, 0, 0, 0, 0, 0, 'Once again, you have my thanks, $N. Without you, I would not have been able to recover anything useful from this disaster.', 0);
INSERT INTO `quest_offer_reward` VALUES (11797, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice job, $N.  Show the lads how it\'s done.  The only good creeper\'s a dead creeper!', 0);
INSERT INTO `quest_offer_reward` VALUES (11798, 4, 1, 2, 0, 0, 0, 0, 0, '<The old gnome breathes a heavy sigh of relief.>$B$BIt is done then... you have saved us! If you hadn\'t stopped him here, then who knows how far his madness would have spread?$B$BWith the threats from the blue dragons and the Nexus War, not to mention the Lich King, the last thing we need is a mad creation of the Titans running loose!$B$BThank you, $N!', 0);
