INSERT INTO `quest_offer_reward` VALUES (13476, 0, 0, 0, 0, 0, 0, 0, 0, 'Your trials have not been easy, but your courage did not escape you and your strength did not fail. Part with past tokens of victory, $c. None shall need them as a reminder of your worth. Stand up straight, $n. Today you will walk with great pride for you are a hero!', 0);
INSERT INTO `quest_offer_reward` VALUES (13478, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re not one to disappoint, $n! What you\'ve learned goes beyond petty medals and commendations. What you\'ve learned is what can only come with experience and the hardening of one\'s temper in the field of battle.$b$bYou\'ve done well, $c. You\'re a true hero of the Alliance!', 0);
INSERT INTO `quest_offer_reward` VALUES (13479, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, excellent, thank you! I may need more of a sample to get truly conclusive results, but you\'ve done well today.', 0);
INSERT INTO `quest_offer_reward` VALUES (13480, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, excellent, thank you! I may need more of a sample to get truly conclusive results, but you\'ve done well today.', 0);
INSERT INTO `quest_offer_reward` VALUES (13481, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well. We thought Father Kamaros lost, for certain. Your bravery will serve as an example to all those who oppose the Scourge.', 0);
INSERT INTO `quest_offer_reward` VALUES (13482, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank the Light. I\'d thought Father Kamaros dead, or worse. Through your bravery and compassion, you have restored a great ally to the Argent Crusade. You have my thanks and those of the crusade.', 0);
INSERT INTO `quest_offer_reward` VALUES (13483, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes! Yes, I think I have just the task for you...', 0);
INSERT INTO `quest_offer_reward` VALUES (13484, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes! Yes, I think I have just the task for you...', 0);
INSERT INTO `quest_offer_reward` VALUES (13485, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Borean Tundra flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13486, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Sholazar Basin flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13487, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Dragonblight flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13488, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Howling Fjord flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13489, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Grizzly Hills flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13490, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Storm Peaks flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13491, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Crystalsong Forest flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13492, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Zul\'Drak flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13493, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Borean Tundra flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13494, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Sholazar Basin flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13495, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Dragonblight flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13496, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Howling Fjord flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13497, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Grizzly Hills flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13498, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Storm Peaks flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13499, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Crystalsong Forest flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13500, 0, 0, 0, 0, 0, 0, 0, 0, 'Honor the Zul\'Drak flame.', 0);
INSERT INTO `quest_offer_reward` VALUES (13501, 0, 0, 0, 0, 0, 0, 0, 0, 'Candy buckets like this are located in inns throughout the realms. Go ahead... take some!', 0);
INSERT INTO `quest_offer_reward` VALUES (13502, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! And here, please... keep the basket!$B$BRemember, you can always return with more Noblegarden Chocolates to trade for additional items.', 0);
INSERT INTO `quest_offer_reward` VALUES (13503, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! And here, please... keep the basket!$B$BRemember, you can always return with more Noblegarden Chocolates to trade for additional items.', 0);
INSERT INTO `quest_offer_reward` VALUES (13504, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work. Thank you, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (13505, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect! Thank you so much. I would hate to lose more records of our culture to war - far too much has been sacrificed already.', 0);
INSERT INTO `quest_offer_reward` VALUES (13506, 0, 0, 0, 0, 0, 0, 0, 0, '\"Another?\" They have more than one. I will not leave any of my people to be tortured, we must act quickly.', 0);
INSERT INTO `quest_offer_reward` VALUES (13507, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. One more threat we can worry a bit less about. We\'ll continue the offensive while they are still off-guard.', 0);
INSERT INTO `quest_offer_reward` VALUES (13508, 0, 0, 0, 0, 0, 0, 0, 0, '<Alanndarian looks very pale, though shallow breaths betray the life that remains in her.>$B$BYou\'re here... to save me? A bit later... than I hoped.', 0);
INSERT INTO `quest_offer_reward` VALUES (13509, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. That should slow them down a bit.', 0);
INSERT INTO `quest_offer_reward` VALUES (13510, 0, 0, 0, 0, 0, 0, 0, 0, 'Some sort of shield you say? We might have a way around that. Balthule had an idea for breaking whatever protective enchantments their shaman had, so it might work on this too. Thank you for saving Aynasha and bringing me this information - it may well make the difference in our battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (13511, 0, 0, 0, 0, 0, 0, 0, 0, 'Alanndarian is dead then. That saddens me greatly, but it is not entirely unexpected. I expect no mercy from these savages, and will show them none.', 0);
INSERT INTO `quest_offer_reward` VALUES (13512, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve shown a great deal of talent for one so young. Good work. It seems the final battle is before us.', 0);
INSERT INTO `quest_offer_reward` VALUES (13513, 0, 0, 0, 0, 0, 0, 0, 0, 'These will do perfectly. I will prepare them for Sandrya - you are sure to be dispatched to meet with her shortly.', 0);
INSERT INTO `quest_offer_reward` VALUES (13514, 0, 0, 0, 0, 0, 0, 0, 0, 'You performed excellently up there! We are fortunate to have you with us.$B$BI haven\'t yet received word regarding their leader. If you haven\'t spoken with Sandrya at the cave in the back of the vale, be sure to do so before you depart.', 0);
INSERT INTO `quest_offer_reward` VALUES (13515, 0, 0, 0, 0, 0, 0, 0, 0, 'We owe a great debt to you for all that you\'ve done to see this battle through.$B$BNot often can we claim victories so decisively, but it seems there\'s far more to this plot than just the Shatterspear. If you haven\'t already checked his body for information, make sure to do so before you leave.', 0);
INSERT INTO `quest_offer_reward` VALUES (13518, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $c. Some of them surely would not have made it without you.$B$BWe\'ll be taking the wounded to the main building once we have them all safely back in town. Perhaps you can meet us there later?', 0);
INSERT INTO `quest_offer_reward` VALUES (13519, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done well, $n. The Twilight\'s Hammer is a dangerous cult. It would be foolish to underestimate the danger they represent.', 0);
INSERT INTO `quest_offer_reward` VALUES (13520, 0, 0, 0, 0, 0, 0, 0, 0, 'This will do perfectly. Thank you so much!$B$BI suspect they will want to thank you personally should they recover, so perhaps you can stop by later on.', 0);
INSERT INTO `quest_offer_reward` VALUES (13521, 0, 0, 0, 0, 0, 0, 0, 0, '<The machine crackles loudly, and then Wizbang\'s static-ridden voice becomes audible.>$B$BLovely! I hope you didn\'t kill too many of them. We\'re not trying to wipe out the population, we just need to get to the source of this corruption quickly! I\'m proceeding with the analysis now.', 0);
INSERT INTO `quest_offer_reward` VALUES (13522, 0, 0, 0, 0, 0, 0, 0, 0, 'Your efforts are very much appreciated. Since the Cataclysm, the elementals have been out of control. We have little hope of taking back Auberdine without extinguishing their presence first.', 0);
INSERT INTO `quest_offer_reward` VALUES (13523, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve succeeded... it hasn\'t all been in vain...$B$BMy time approaches. Go now, $n, and may Elune protect you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13525, 0, 0, 0, 0, 0, 0, 0, 0, 'Why have you come here, outsider?$B$B<You tell the elder that you were sent by Foriel Broadleaf at the Grove of the Ancients. He seems to relax slightly.>$B$BThe elf is right. The forces of Felwood have seeped into Darkshore and sickened my people.', 0);
INSERT INTO `quest_offer_reward` VALUES (13526, 0, 0, 0, 0, 0, 0, 0, 0, 'These are poor, weak stalks, but they are all we have. The corruption spreads quickly, even among the plants of the forest.', 0);
INSERT INTO `quest_offer_reward` VALUES (13527, 0, 0, 0, 0, 0, 0, 0, 0, 'This definitely looks like the stuff they were eating. Let me take a closer peek...', 0);
INSERT INTO `quest_offer_reward` VALUES (13528, 0, 0, 0, 0, 0, 0, 0, 0, '<Static precedes Wizbang\'s distorted voice.>$B$BThe results are coming in now. We should have some information on how the bears contracted this in just a moment.', 0);
INSERT INTO `quest_offer_reward` VALUES (13529, 0, 0, 0, 0, 0, 0, 0, 0, 'It is done then. Thank you. I only hope that the wildlife here will see more peace with his passing.', 0);
INSERT INTO `quest_offer_reward` VALUES (13537, 0, 0, 0, 0, 0, 0, 0, 0, 'It looks like fishing\'s your thing. My belly appreciates the gift.$B$BI\'m thinking you would get more use out of this pole than I would. Have at it.', 0);
INSERT INTO `quest_offer_reward` VALUES (13538, 0, 0, 0, 0, 0, 0, 0, 0, 'I heard the explosion from here! Excellent job - we\'ve already begun rerouting the power into the Titan relic to super-charge our own forces.', 0);
INSERT INTO `quest_offer_reward` VALUES (13539, 0, 0, 0, 0, 0, 0, 0, 0, 'Now in order to kill the enemy, our men must be roused to anger; that there may be advantage from defeating the enemy, they must have their rewards. I hope that these are sufficient for your service to the Horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (13542, 0, 0, 0, 0, 0, 0, 0, 0, 'I cannot thank you enough, $N. Now we\'ll have a fighting chance to retake the ruins as soon as we are in better health.', 0);
INSERT INTO `quest_offer_reward` VALUES (13543, 0, 0, 0, 0, 0, 0, 0, 0, 'Without those three coaxing them, perhaps the flow of elementals into the Ruins of Auberdine will be stemmed.$B$BThank you, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13544, 0, 0, 0, 0, 0, 0, 0, 0, '<Elder Brolg accepts the blessed bundle from you and looks over it.>$B$BThis is exactly what we needed. The bear\'s spirit favors us and there may be hope for my brothers after all.', 0);
INSERT INTO `quest_offer_reward` VALUES (13545, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well. My people have begun to emerge from their madness, but they will fall back into it if we do not deal with the source of the corruption.', 0);
INSERT INTO `quest_offer_reward` VALUES (13546, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. You are a hero to my people. Without you, we would be doomed to madness and death. May the bear spirit always watch over you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13547, 0, 0, 0, 0, 0, 0, 0, 0, 'So much death and pain. Can there be any doubt that there is more yet to come?$B$BAll I wish to do is move onward into the Goddess\'s embrace as do these others.$B$BBut first, let us see if we can keep even more misery and destruction from being spread.', 0);
INSERT INTO `quest_offer_reward` VALUES (13548, 0, 0, 0, 0, 0, 0, 0, 0, 'Candy buckets like this are located in inns throughout the realms. Go ahead... take some!', 0);
INSERT INTO `quest_offer_reward` VALUES (13549, 0, 0, 0, 0, 0, 0, 0, 0, 'Very fine specimens indeed, $N! These are sure to provide many hours of curious speculation amongst my colleagues back home.', 0);
INSERT INTO `quest_offer_reward` VALUES (13554, 0, 0, 0, 0, 0, 0, 0, 0, 'This should be plenty! Thanks so much! I\'ll get working on a cure for this nastiness immediately.', 0);
INSERT INTO `quest_offer_reward` VALUES (13556, 0, 0, 0, 0, 0, 0, 0, 0, 'One, two, three,... hehe, that be a good number o\' them, mon! Now we be safe for quite some time!', 0);
INSERT INTO `quest_offer_reward` VALUES (13557, 0, 0, 0, 0, 0, 0, 0, 0, 'I am pleased to hear that you were able to free some of the remaining wildlife. With a bit of good fortune, we will find the cure for the corruption that the others have faced before any more of them die.', 0);
INSERT INTO `quest_offer_reward` VALUES (13558, 0, 0, 0, 0, 0, 0, 0, 0, 'Yalda? The spirits of others from the Ruins of Auberdine?$B$B<The druid ponders what you\'ve conveyed about your adventure in the ruins.>$B$BI remember them all. They will be dearly missed, but they have moved on into the Goddess\'s embrace and that is for the best.$B$BAnde\'thoras-ethil, may your troubles be diminished, $N!', 0);
INSERT INTO `quest_offer_reward` VALUES (13559, 0, 0, 0, 0, 0, 0, 0, 0, 'The Sons of Hodir accept your tribute, small one.  You continue to prove your worth as an ally in more ways than one.', 0);
INSERT INTO `quest_offer_reward` VALUES (13560, 0, 0, 0, 0, 0, 0, 0, 0, 'The decoys worked then? Great to hear it! Don\'t you worry yourself one bit about the treasures. Me and Gary will take care of the rest this time around.$B$BTruth be told, if something went wrong again and I lost any more of you adventurer types, I\'m pretty sure they\'d kick me out of town.', 0);
INSERT INTO `quest_offer_reward` VALUES (13561, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks. Though they may not be able to show it, you have the thanks of the Highborne spirits as well.$B$BFare safely, traveler.', 0);
INSERT INTO `quest_offer_reward` VALUES (13562, 0, 0, 0, 0, 0, 0, 0, 0, 'You have our thanks. It is time that we leave the past behind us, and start looking to our future.', 0);
INSERT INTO `quest_offer_reward` VALUES (13563, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. Perhaps it would have been better... if I had done it myself. But even after these thousands of years, I could not bear to raise my hand against my beloved.$B$BPlease, I would be alone with my grief...', 0);
INSERT INTO `quest_offer_reward` VALUES (13564, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, traveler. You seem to have an interest in my friend here. You\'re here on behalf of his friend Volcor then I imagine? If Volcor didn\'t come himself, he must be injured as well. I hope he recovers quickly.$B$BGimclaw is too injured to be moved far and he\'s going to need some immediate treatment. I\'m afraid I can\'t let you take him back just yet, but perhaps you can help me while I tend to him.', 0);
INSERT INTO `quest_offer_reward` VALUES (13565, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, ally. We shall keep a more vigilant eye should they return.', 0);
INSERT INTO `quest_offer_reward` VALUES (13566, 0, 0, 0, 0, 0, 0, 0, 0, 'These will do perfectly. These are the final ingredients for an incense I\'ve been working on. I will explain in just a moment, let me mix these in first.', 0);
INSERT INTO `quest_offer_reward` VALUES (13567, 0, 0, 0, 0, 0, 0, 0, 0, 'I am the caretaker for my kind, leading not through force, but through wisdom and swift action. Wisdom is what carries us into the deeper forest when danger beckons and swiftness is what leaves our enemies basking in our absence.$B$BIn this wisdom, I see through to your true intentions, and I know that you are deserving of my blessing, the blessing of my swiftness. It can be yours, but know that the other spirits will not welcome you once you proceed - consider this carefully before accepting my boon.', 0);
INSERT INTO `quest_offer_reward` VALUES (13568, 0, 0, 0, 0, 0, 0, 0, 0, 'I share these forests willingly, accepting night elf kind into my home, and on occasion calling them friend. Though they have been tainted greatly in recent memory, I can see in you the same strength, honor, and spirit that first endeared them to me.$B$BIf you place value in the ferocity necessary to overcome all odds, then I am willing to call you my champion. Know now that if you side with my kind, the other spirits will not accept you, so consider your choice carefully.', 0);
INSERT INTO `quest_offer_reward` VALUES (13569, 0, 0, 0, 0, 0, 0, 0, 0, 'So it is done. You have received the blessing of a great animal spirit, and I shall now complete the ritual for you. Please stand still.', 0);
INSERT INTO `quest_offer_reward` VALUES (13570, 0, 0, 0, 0, 0, 0, 0, 0, 'I just want to offer my sincerest thanks for all that you\'ve done for us. In my grief, I\'m afraid that I didn\'t extend the gratitude that you truly deserve. We may not have made it here without you.$B$BKnow that you will always have friends here.$B$BAnd you needn\'t worry about my loneliness. Grimclaw has taken to keeping me company, and I have to say he\'s a more comforting companion than I would have expected.$B$BTake this, and go with all our blessings. Until we meet again, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (13571, 0, 0, 0, 0, 0, 0, 0, 0, 'Above the stale, unwashed stench, you catch a whiff of a familiar smell!  It appears as if this once-mighty adventurer has a real love for kungaloosh.$b$bPeeking through the bars, you can barely make out the recipe written on his arm....', 0);
INSERT INTO `quest_offer_reward` VALUES (13572, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. With your efforts, we might be able to save the Blackwood who remain.', 0);
INSERT INTO `quest_offer_reward` VALUES (13573, 0, 0, 0, 0, 0, 0, 0, 0, 'There is no time to waste, $c.  It is taking every ounce of my power to keep Darkshore\'s entire landmass from being shattered and swept into the ocean... you must listen to me very carefully.', 0);
INSERT INTO `quest_offer_reward` VALUES (13575, 0, 0, 0, 0, 0, 0, 0, 0, 'I have much respect for the great Malfurion Stormrage.  I cannot, however, leave my people in their time of need.  Maybe there is a way we can both help each other.', 0);
INSERT INTO `quest_offer_reward` VALUES (13576, 0, 0, 0, 0, 0, 0, 0, 0, 'The element of fire is unique in that attacking it indiscriminately can often lead it to spreading further... succeeding only in agitating its flames.$B$BWe are wiser than that, $N.  We are going to dispose of it properly.', 0);
INSERT INTO `quest_offer_reward` VALUES (13577, 0, 0, 0, 0, 0, 0, 0, 0, 'A terrible sadness surrounds the creature.  Its eyes stare beyond you as you approach it.', 0);
INSERT INTO `quest_offer_reward` VALUES (13578, 0, 0, 0, 0, 0, 0, 0, 0, 'I thank you, $R. Soon my kin will rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (13579, 0, 0, 0, 0, 0, 0, 0, 0, 'If the great Malfurion requires my presence, it is my duty to heed his call.  It is also my duty to protect these ruins, however.$B$BPerhaps you\'ll help me fulfill both my obligations, $N?', 0);
INSERT INTO `quest_offer_reward` VALUES (13580, 0, 0, 0, 0, 0, 0, 0, 0, 'We are in your debt, $N.  Perhaps now, with Malfurion\'s help, we can save the rest of Darkshore.', 0);
INSERT INTO `quest_offer_reward` VALUES (13581, 0, 0, 0, 0, 0, 0, 0, 0, 'This is fortunate news, $N.  The furbolg may seem little more than a savage race, but their ties to the land are ancient and their shamans are second to none.', 0);
INSERT INTO `quest_offer_reward` VALUES (13582, 0, 0, 0, 0, 0, 0, 0, 0, '<Aroom lets out a long low-pitched hoot.>$B$BFinally kin may rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (13583, 0, 0, 0, 0, 0, 0, 0, 0, 'The wildkin were once the guardians of the land.  It is good to hear they haven\'t all forgotten their old ways.', 0);
INSERT INTO `quest_offer_reward` VALUES (13584, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s become clear, $N.  The elements won\'t be calm until Malfurion succeeds in returning balance to the land.  Aiding him is, in a way, also fulfilling my task.', 0);
INSERT INTO `quest_offer_reward` VALUES (13585, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $N!  Selenn\'s help in holding back the vortex will be invaluable.', 0);
INSERT INTO `quest_offer_reward` VALUES (13586, 0, 0, 0, 0, 0, 0, 0, 0, 'Malfurion calls?  Yes... I can hear his voice through the dream...', 0);
INSERT INTO `quest_offer_reward` VALUES (13587, 0, 0, 0, 0, 0, 0, 0, 0, 'I thank you, $N.  You\'ve given me my freedom.  Let\'s see what we can do for your world now.', 0);
INSERT INTO `quest_offer_reward` VALUES (13588, 0, 0, 0, 0, 0, 0, 0, 0, 'We are turning the tides, $N.  We should be able to contain further damage.$B$BMuch work still remains to be done.', 0);
INSERT INTO `quest_offer_reward` VALUES (13589, 0, 0, 0, 0, 0, 0, 0, 0, 'Glynda sent you to lend a hand, did she? I\'m glad she did - I could certainly use the help.$B$BI\'m working on our initial plans to start whittling away at their ranks. Please feel free to help Tysha and Balthule in the meantime.', 0);
INSERT INTO `quest_offer_reward` VALUES (13590, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to meet you. Word has been sent to me of the efforts you have made to aid us against the trolls, and I must admit I am a bit impressed. The final fight is upon us now, I hope you are prepared.', 0);
INSERT INTO `quest_offer_reward` VALUES (13591, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, this news is disturbing indeed. I will start dispatching messengers to Ashenvale immediately and see if we can\'t get some reinforcements prepared for them.$B$BI cannot thank you enough for all the help you\'ve lent us in these trying times. Such heroic deeds deserve reward. I think I might have something that will serve you perfectly.', 0);
INSERT INTO `quest_offer_reward` VALUES (13592, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome back from your exercises, $N. Even while we practice for the competition, it\'s good to remind ourselves that the Scourge is still out there.', 0);
INSERT INTO `quest_offer_reward` VALUES (13593, 0, 0, 0, 0, 0, 0, 0, 0, 'We are proud that you have agreed to train with us and compete on our behalf.', 0);
INSERT INTO `quest_offer_reward` VALUES (13594, 0, 0, 0, 0, 0, 0, 0, 0, '<The sentinel eyes you appreciatively for a moment.>$B$BA job well done, $c. If you\'re any indication of the quality of the $r adventurers we\'re going to be seeing more of, we should have this war against the orcs and their Horde taken care of faster than expected.', 0);
INSERT INTO `quest_offer_reward` VALUES (13595, 0, 0, 0, 0, 0, 0, 0, 0, 'You have helped me to return to the ground, $N.$B$BNow my hair will cease to sprout for these creatures. Better yet, I will be regrown all the sooner.', 0);
INSERT INTO `quest_offer_reward` VALUES (13596, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $n. Now give me a moment to examine the plans you\'ve brought me.', 0);
INSERT INTO `quest_offer_reward` VALUES (13597, 0, 0, 0, 0, 0, 0, 0, 0, 'For countless ages I have walked these forests and lived in these mountains. Through snow and fire, I have watched civilizations crumble and nature press on. Now, the very earth turns against us, and it becomes very apparent that we must fight if our legacy is to continue.$B$BYour heart is true, and I would choose you as our champion. If you value the thick hide and resilience of my kind, then accept my boon. Know that the other spirits will not accept you once this choice is made, so choose wisely.', 0);
INSERT INTO `quest_offer_reward` VALUES (13598, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my thanks, young one. We should be able to speed Grimclaw\'s recovery dramatically with this salve.', 0);
INSERT INTO `quest_offer_reward` VALUES (13599, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve returned! It is good to see you again. I suppose we have you to thank for Grimclaw\'s return?$B$BI\'m afraid... I have some bad news. I\'ve been unable to purge the toxin... their conditions worsen. Gershalla died shortly after you left, and Volcor... Volcor passed just before you came back.$B$BHe lived long enough to see Grimclaw come back safely, so he died with a smile on his face. I\'m just sorry that I couldn\'t do more.$B$BFor now, I\'m doing everything I can to save Shaldyn, so please excuse me.', 0);
INSERT INTO `quest_offer_reward` VALUES (13600, 0, 0, 0, 0, 0, 0, 0, 0, '<The marshal accepts the Blade of Drak\'Mar from you.>$B$BA fine weapon, and perfectly balanced. It will serve me well in the next match.', 0);
INSERT INTO `quest_offer_reward` VALUES (13601, 0, 0, 0, 0, 0, 0, 0, 0, 'I thank you for coming to our assistance. As you can see, the refugees\' situation is dire.', 0);
INSERT INTO `quest_offer_reward` VALUES (13602, 0, 0, 0, 0, 0, 0, 0, 0, 'Very well done, $N. Your actions in the Zoram Strand against the naga are commendable.$B$BI know your feat was not easily accomplished, for the strength and guile of the naga is well known to the night elves. We know this, for we share a history with them.$B$BThe naga are cursed Highborne night elves. Ten thousand years ago, Queen Azshara made a pact of desperation with the Old Gods to save her surviving people from a watery grave.$B$BThe naga are the result of that pact.', 0);
INSERT INTO `quest_offer_reward` VALUES (13603, 0, 0, 0, 0, 0, 0, 0, 0, 'So the tales were true? This is a fine blade and I have no doubt that it will serve me well in the tournament. You have my thanks, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13604, 0, 0, 0, 0, 0, 0, 0, 0, 'The platinum disc fits perfectly inside the console.  It begins to spin as its contents are scanned by white beams of light.', 0);
INSERT INTO `quest_offer_reward` VALUES (13605, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye, you\'re the fine $g lad : lass; who saved my Hollee!$B$BWell, I\'ve got a fitting reward for you here, I\'m sure. We really can\'t thank you enough for bringing her to safety, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13606, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $N. We\'re one step closer to saving Azeroth!', 0);
INSERT INTO `quest_offer_reward` VALUES (13607, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s locked?  Figures!$B$BLet\'s see what information this junk heap can give us...', 0);
INSERT INTO `quest_offer_reward` VALUES (13609, 0, 0, 0, 0, 0, 0, 0, 0, 'Ye did it! I don\'t know how, but ye did!', 0);
INSERT INTO `quest_offer_reward` VALUES (13610, 0, 0, 0, 0, 0, 0, 0, 0, 'Ye never cease to impress, $N.$b$bYe and me are having a pint together after all this is done, ye hear me?', 0);
INSERT INTO `quest_offer_reward` VALUES (13611, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve got to hand it to you, $N. Our odds might not be the best, but I feel a lot better with you lending a hand.', 0);
INSERT INTO `quest_offer_reward` VALUES (13612, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, $C!$B$BYour efforts will help keep the enemy on their heels while our forces sweep across the land!', 0);
INSERT INTO `quest_offer_reward` VALUES (13613, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $R. I shall include your service to the Horde in my report.$B$BI should have these troops fit to fight again in no time!', 0);
INSERT INTO `quest_offer_reward` VALUES (13614, 0, 0, 0, 0, 0, 0, 0, 0, 'Stand by for verification.$b$bAccess to the Celestial Planetarium granted. Please retrieve your key.', 0);
INSERT INTO `quest_offer_reward` VALUES (13615, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $C.$B$BThis should help get us by until our shipment arrives.', 0);
INSERT INTO `quest_offer_reward` VALUES (13616, 0, 0, 0, 0, 0, 0, 0, 0, 'The blade is as fine as the stories say. It would seem there\'s little reason to complete the repairs on my customary blade. Winter\'s Edge is far superior and I will continue to carry it. You have my thanks, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13617, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello, $c.$B$BWhat\'s that? You say that Shyela sent you?$B$B<The night elf briskly rubs his nimble hands together.>$B$BVery well, let\'s get started.', 0);
INSERT INTO `quest_offer_reward` VALUES (13618, 0, 0, 0, 0, 0, 0, 0, 0, 'Listen carefully, $R....', 0);
INSERT INTO `quest_offer_reward` VALUES (13619, 0, 0, 0, 0, 0, 0, 0, 0, '<Kadrak listens intently as you share Gorat\'s last words.>$B$BIt is as I feared, then. May his rest be peaceful - only not yet!', 0);
INSERT INTO `quest_offer_reward` VALUES (13620, 0, 0, 0, 0, 0, 0, 0, 0, 'Let\'s have a look, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (13621, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $R.$B$BThe elf captain is dead and the soul of one of our beloved brethren can sleep in peace.$B$BI shall pass on word of your deeds to my superiors in Orgrimmar.', 0);
INSERT INTO `quest_offer_reward` VALUES (13622, 0, 0, 0, 0, 0, 0, 0, 0, 'Analyzing fragments... please stand by.', 0);
INSERT INTO `quest_offer_reward` VALUES (13623, 0, 0, 0, 0, 0, 0, 0, 0, 'So, Evenar was succesful. Actually, more to the point, YOU were successful.$B$BI cannot thank you enough, $c, but there is more that must be done to the leaves before they will serve as a curative for what ails the child, Relara.', 0);
INSERT INTO `quest_offer_reward` VALUES (13624, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'d received a report about you from some of my scouts out in the forest but I had no idea how capable you are!$B$BThank you, $N. With what you\'ve accomplished the tide is turned. I\'ve no doubt now that we\'ll rout them by the end of the day!', 0);
INSERT INTO `quest_offer_reward` VALUES (13625, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N. Mounted combat is often hard to pick up for those accustomed to fighting on foot. I have no doubt that you\'ll continue to improve with practice, which is more than I can say for some warriors.', 0);
INSERT INTO `quest_offer_reward` VALUES (13626, 0, 0, 0, 0, 0, 0, 0, 0, 'I do not look forward to writing the letter to the Argent Dawn embassy in Darnassus. At least we\'ll be able to send their most prized possessions back for their loved ones.$B$BI cannot thank you enough for your courage, $N. Please accept this on behalf of the post.', 0);
INSERT INTO `quest_offer_reward` VALUES (13627, 0, 0, 0, 0, 0, 0, 0, 0, 'All excellent planks, and the price is right, too! Even better, those Argent Crusade sticks-in-the-mud will never know. They\'ll get their precious coliseum and I\'ll get my early completion bonus. Now that\'s a win-win if I ever saw one.', 0);
INSERT INTO `quest_offer_reward` VALUES (13628, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome, $C. It is good to see that the road to Mor\'shan Ramparts remains clear.', 0);
INSERT INTO `quest_offer_reward` VALUES (13629, 0, 0, 0, 0, 0, 0, 0, 0, 'Object integrity confirmed. Please hold while solidified residue is removed.', 0);
INSERT INTO `quest_offer_reward` VALUES (13630, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $N.$B$BWith the moonstones of Ordil\'Aran deactivated, we should not need to worry about any further incursions of furbolg here.$B$BHowever, my scouts have returned with disturbing news from the furbolg village. There is an imminent danger massing there.$B$BI wonder, might I entice you to carry a warning to Astranaar for me?', 0);
INSERT INTO `quest_offer_reward` VALUES (13631, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re here.  Let us hope this works.  For all our sakes.', 0);
INSERT INTO `quest_offer_reward` VALUES (13632, 0, 0, 0, 0, 0, 0, 0, 0, 'We are in your debt, $c. If I know anything about the furbolg, they do not like their noses bloodied.$B$BIt won\'t be long now before they tuck tail and return to their village.', 0);
INSERT INTO `quest_offer_reward` VALUES (13633, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'re saying this knight\'s story is a sham? Most suspicious. Dishonorable at the least, and murderous at the worst. Still, we can\'t confront him until we know for certain whether he was involved in these murders.$B$BDo nothing to arouse his suspicions, $N. Discretion will serve us well as we continue to investigate.', 0);
INSERT INTO `quest_offer_reward` VALUES (13634, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'re saying this knight\'s story is a sham? Most suspicious. Dishonorable at the least, and murderous at the worst. Still, we can\'t confront him until we know for certain whether he was involved in these murders.$B$BDo nothing to arouse his suspicions, $N. Discretion will serve us well as we continue to investigate.', 0);
INSERT INTO `quest_offer_reward` VALUES (13635, 0, 0, 0, 0, 0, 0, 0, 0, 'The South Gate status report? Excellent. I\'ve been awaiting news from them for a while.$B$BWith the increasing number of troggs in the hills on all sides, I fear the outposts will be the first to feel any aggressive action.', 0);
INSERT INTO `quest_offer_reward` VALUES (13636, 0, 0, 0, 0, 0, 0, 0, 0, 'Orders! Let\'s see what we have here... hah! He wants me to deal with the infestation of rat men. We\'re already in the middle of that, so no real deviation there.$B$BCare to lend a hand?', 0);
INSERT INTO `quest_offer_reward` VALUES (13639, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmph, it\'ll take more than a Dark Iron ambush to stop the deliveries!$b$bBut Saean... I would never have suspected him to be one of their sympathizers. We\'ve been working with him for almost a year now... maybe I missed the signs.$b$bWell, I\'ll consider that later.', 0);
INSERT INTO `quest_offer_reward` VALUES (13640, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems you\'ve solved one problem only to face another, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (13641, 0, 0, 0, 0, 0, 0, 0, 0, 'This one will do nicely. All that remains is to imbue it with the power of the Light, giving it the ability to find truth amid deception.', 0);
INSERT INTO `quest_offer_reward` VALUES (13642, 0, 0, 0, 0, 0, 0, 0, 0, 'With the concoction blessed by the Light of Elune, it should be ready for delivery to Astranaar.$B$BThere is no time to waste, $N!', 0);
INSERT INTO `quest_offer_reward` VALUES (13643, 0, 0, 0, 0, 0, 0, 0, 0, 'So you say Lorien Sunblaze was done in by poison and Sir Wendell was burnt alive? Dreadful ways to die, both of them. Only Conall was killed by cold steel, and from your description, it was a coward\'s strike, not honorable combat that laid him in his grave.$B$BAll three sparred with and were scheduled to fight the Black Knight. This cannot be a coincidence. Now, we must find a way to prove it was the Black Knight\'s work.', 0);
INSERT INTO `quest_offer_reward` VALUES (13644, 0, 0, 0, 0, 0, 0, 0, 0, 'With the death of the harbinger, I can begin to cleanse the lake.$B$BYou have my deepest thanks, $N. I mourn the loss of your friend, but know that his remains will fuel the reclamation of the forest hereabouts.', 0);
INSERT INTO `quest_offer_reward` VALUES (13645, 0, 0, 0, 0, 0, 0, 0, 0, 'Teronis is dead?$B$B<The lady weeps softly for the loss of her friend and then composes herself after a moment.>$B$BWe must see to it that his sacrifice was not in vain. Are you willing to see this through, $c?$B$BVery well, allow me some time to think about how we should proceed. I will know shortly.', 0);
INSERT INTO `quest_offer_reward` VALUES (13646, 0, 0, 0, 0, 0, 0, 0, 0, 'Avana is alive?! I feared that she might be dead when we heard about the attack on Maestra\'s Post!$B$BAs you can see, we have our hands full with the Horde here as well.$B$BYou have given me the greatest gift by letting me know she is well. Any time that you wish to ride one of my nightsabers back to Maestra\'s Post, it is yours.', 0);
INSERT INTO `quest_offer_reward` VALUES (13647, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello there, $G laddy:lass;! The mountaineers sent you over, eh? I do suspect we might have just the sort of work you\'re looking for. Welcome to the Farstrider Lodge!', 0);
INSERT INTO `quest_offer_reward` VALUES (13648, 0, 0, 0, 0, 0, 0, 0, 0, 'You brought that damned spy to justice! This is good news. We can all rest a little bit easier, not worrying about that one.', 0);
INSERT INTO `quest_offer_reward` VALUES (13650, 0, 0, 0, 0, 0, 0, 0, 0, 'Woo! I\'m relieved... I was worried they\'d all be destroyed by now. Thank you for checking in on them.', 0);
INSERT INTO `quest_offer_reward` VALUES (13651, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for the oil, $C. Now prepare yourself!$B$BMy runner has returned with some desperate news....$B$BSplintertree is surrounded!', 0);
INSERT INTO `quest_offer_reward` VALUES (13653, 0, 0, 0, 0, 0, 0, 0, 0, 'Gorka...$B$BWe must always be prepared for losses. Her honorable service to the Horde will not be forgotten.$B$BAnd her sacrifice shall not be in vain!', 0);
INSERT INTO `quest_offer_reward` VALUES (13654, 0, 0, 0, 0, 0, 0, 0, 0, '<Crusader Rhydalla examines the bag\'s contents with a look of disgust on her face.>$B$BIt\'s all here, $N: the poison that killed Lorien, a collection of knives, torches, and oil. No man has cause to possess these things here and no squire would do so without orders from his knightly master. We\'ve found our murderer, $N, but who is he working for?', 0);
INSERT INTO `quest_offer_reward` VALUES (13655, 0, 0, 0, 0, 0, 0, 0, 0, 'Well look at that. You found one of our stolen documents!$B$BWe\'re much obliged for its return. Hopefully a bit of gold will suit your fancy for the finder\'s fee?', 0);
INSERT INTO `quest_offer_reward` VALUES (13656, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello, $G sonny:lass;! You seem to have found one of our stolen documents, eh?$B$BThank you kindly for bringing it back to us. We\'ll gladly lend some coin as thanks for its return.', 0);
INSERT INTO `quest_offer_reward` VALUES (13657, 0, 0, 0, 0, 0, 0, 0, 0, 'Our historical records! The thought of dark iron vermin delving into our past just made me sick to my stomach.$B$BWe appreciate you bringing this back to us. We\'d like to offer you some coin as a reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (13658, 0, 0, 0, 0, 0, 0, 0, 0, 'Ug, I can\'t believe they got their hands on our transport routes - that was just asking for trouble!!$B$BThank you for getting this back to us before they were able to do much with it! You probably saved us a lot of heartache. Please accept some coin as a reward for your efforts.', 0);
INSERT INTO `quest_offer_reward` VALUES (13659, 0, 0, 0, 0, 0, 0, 0, 0, 'Hah! Think they got a kick out of seeing how low they were on our list of threat assessments! This document was admittedly a bit out of date, but none the less amusing.$B$BOf course, we\'ll offer you some money as a reward for returning this anyway - your efforts are appreciated!', 0);
INSERT INTO `quest_offer_reward` VALUES (13660, 0, 0, 0, 0, 0, 0, 0, 0, 'Our artifact records! I was pretty well worried what the dark irons might try to steal with this information at their disposal.$B$BThank you for bringing this back to us. We\'ll gladly part with some coin to express our gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (13661, 0, 0, 0, 0, 0, 0, 0, 0, 'You located all of our documents! We can\'t thank you enough for the help... we\'d all but given up hope. It seemed sure that the dark irons would be able to act upon all of this information soon. It\'s good to know that they\'ll be left blind in their attacks for quite a while yet.$B$BTo thank you for all of your efforts on our behalf, we would like to offer you a gift. An artifact really... something we recovered from one of our digs. You have the appreciation of all of the Explorers\' League.', 0);
INSERT INTO `quest_offer_reward` VALUES (13662, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep the residue pouring in!', 0);
INSERT INTO `quest_offer_reward` VALUES (13663, 0, 0, 0, 0, 0, 0, 0, 0, '<Crusader Rhydalla\'s face pales as she reads the orders.>$B$BThis can only mean one thing, $N. The Black Knight is a member of the Cult of the Damned and has been sent by Arthas to disrupt the tournament!$B$BThe Black Knight has proven himself a fearsome combatant, and you will need to be every bit as good if you wish to face him. When you are a champion, return to me and we\'ll find a way to ensure his defeat.', 0);
INSERT INTO `quest_offer_reward` VALUES (13664, 0, 0, 0, 0, 0, 0, 0, 0, 'With the death of the Black Knight, the tournament may once again serve its purpose. The champions of the Horde and Alliance may demonstrate their skill at arms and train for the day when Arthas and his minions will no longer threaten us.$B$BYou have the thanks of the Argent Crusade, $N, and that of all who have answered our call.', 0);
INSERT INTO `quest_offer_reward` VALUES (13665, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. Training against the other valiants is one of the best ways to prepare yourself for tournament competition. I know you\'ll make Stormwind proud, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13666, 0, 0, 0, 0, 0, 0, 0, 0, 'So there is some truth to those old tales? It seems they did not lie about the quality of the blade. Well done, $N. The champion who wields it will be a lucky man, indeed.', 0);
INSERT INTO `quest_offer_reward` VALUES (13667, 0, 0, 0, 0, 0, 0, 0, 0, 'I have entered your name in the rolls, $N. As an aspirant you will train with others who wish to compete in the tournament and hone your mounted combat skills. Once you have proven yourself, you will be allowed to enter the lowest rank of competition on behalf of your capital city.', 0);
INSERT INTO `quest_offer_reward` VALUES (13668, 0, 0, 0, 0, 0, 0, 0, 0, 'I have entered your name in the rolls, $n. As an aspirant you will train with others who wish to compete in the tournament and hone your mounted combat skills. Once you have proven yourself, you will be allowed to enter the lowest rank of competition on behalf of your capital city.', 0);
INSERT INTO `quest_offer_reward` VALUES (13669, 0, 0, 0, 0, 0, 0, 0, 0, 'There\'s no doubt that this is a blade of exquisite quality and balance. Now I understand why these weapons are so coveted among the champions of the Alliance.', 0);
INSERT INTO `quest_offer_reward` VALUES (13670, 0, 0, 0, 0, 0, 0, 0, 0, 'The blade is as fine as the squire said it would be, but where did he find out about it? No matter; it will serve its new owner well. Perhaps it\'s only fair that Ironforge\'s grand champion be the one to wield it.', 0);
INSERT INTO `quest_offer_reward` VALUES (13671, 0, 0, 0, 0, 0, 0, 0, 0, 'You have shown yourself a capable combatant, $N and you will serve as an example to the rest of the aspirants who clamor to compete on behalf of the Alliance.', 0);
INSERT INTO `quest_offer_reward` VALUES (13672, 0, 0, 0, 0, 0, 0, 0, 0, 'Very well, I will make the arrangements.', 0);
INSERT INTO `quest_offer_reward` VALUES (13673, 0, 0, 0, 0, 0, 0, 0, 0, 'So there is some truth to those old tales? It seems they did not lie about the quality of the blade. Well done, $N. The champion who wields it will be a lucky man, indeed.', 0);
INSERT INTO `quest_offer_reward` VALUES (13674, 0, 0, 0, 0, 0, 0, 0, 0, 'There\'s no doubt that this is a blade of exquisite quality and balance. Now I understand why these weapons are so coveted among the champions of the Alliance.', 0);
INSERT INTO `quest_offer_reward` VALUES (13675, 0, 0, 0, 0, 0, 0, 0, 0, 'The blade is as fine as the valiant said it would be, but where did he find out about it? No matter; it will serve its new owner well. Perhaps it\'s only fair that Orgrimmar\'s grand champion be the one to wield it.', 0);
INSERT INTO `quest_offer_reward` VALUES (13676, 0, 0, 0, 0, 0, 0, 0, 0, 'You have shown yourself a capable combatant, $N and you will serve as an example to the rest of the aspirants who clamor to compete on behalf of the Horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (13677, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N. Mounted combat is often hard to pick up for those accustomed to fighting on foot. I have no doubt that you\'ll continue to improve with practice, which is more than I can say for some warriors.', 0);
INSERT INTO `quest_offer_reward` VALUES (13678, 0, 0, 0, 0, 0, 0, 0, 0, 'Very well, I will make the arrangements.', 0);
INSERT INTO `quest_offer_reward` VALUES (13679, 0, 0, 0, 0, 0, 0, 0, 0, 'Congratulations are in order, $N. You have bested your challenger and earned the right to advance to the rank of valiant.', 0);
INSERT INTO `quest_offer_reward` VALUES (13680, 0, 0, 0, 0, 0, 0, 0, 0, 'Congratulations are in order, $N. You have bested your challenger and earned the right to advance to the rank of valiant.', 0);
INSERT INTO `quest_offer_reward` VALUES (13681, 0, 0, 0, 0, 0, 0, 0, 0, 'These should do. Just don\'t go around blabbing to those crusaders about where you got them from. I\'m sure they\'ll get upset. But, hey, it\'s not like the titans have come back for them. They\'re fair game, I say. Good work, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (13682, 0, 0, 0, 0, 0, 0, 0, 0, '$N, you and your allies have spared the tournament from a devastating attack. Though our skytalon riders are brave, they  have their hands full keeping the peace on the tournament grounds and would be hard-pressed to defend the skies against the cult\'s attack. You have the thanks of the Argent Crusade and all who have gathered here to compete.', 0);
INSERT INTO `quest_offer_reward` VALUES (13683, 0, 0, 0, 0, 0, 0, 0, 0, 'We are in your debt, $c.$B$BUnable to complete their nefarious rituals, the satyr are now fighting a losing battle where they are unable to replenish their numbers.', 0);
INSERT INTO `quest_offer_reward` VALUES (13684, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome aboard then, Valiant $N. The Stormwind delegation is proud to count you among its members.', 0);
INSERT INTO `quest_offer_reward` VALUES (13685, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, valiant. Ironforge needs competitors like you to prevail in the tournament.', 0);
INSERT INTO `quest_offer_reward` VALUES (13688, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome aboard then, Valiant $n. The Gnomeregan delegation is proud to count you among its members.', 0);
INSERT INTO `quest_offer_reward` VALUES (13689, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the Darnassus tournament delegation, $n. It will be a pleasure to train and compete alongside you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13690, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome aboard then, Valiant $n. The Exodar\'s delegation is proud to count you among its members.', 0);
INSERT INTO `quest_offer_reward` VALUES (13691, 0, 0, 0, 0, 0, 0, 0, 0, 'So you want to compete for Orgrimmar? Just because you\'ve beaten up a few dummies doesn\'t mean you\'ll win, but you\'ll be training with the best from now on.', 0);
INSERT INTO `quest_offer_reward` VALUES (13693, 0, 0, 0, 0, 0, 0, 0, 0, 'You think you have what it takes to compete for Sen\'jin? I think we\'ll be finding out.', 0);
INSERT INTO `quest_offer_reward` VALUES (13694, 0, 0, 0, 0, 0, 0, 0, 0, 'On behalf of Thunder Bluff and its warriors, I welcome you, $n. Always fight honorably and bravely beneath our banner.', 0);
INSERT INTO `quest_offer_reward` VALUES (13695, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the Undercity delegation. $n. As a valiant, you\'ll train with us to prepare you for the highest level of competition.', 0);
INSERT INTO `quest_offer_reward` VALUES (13696, 0, 0, 0, 0, 0, 0, 0, 0, 'So you want to compete for Silvermoon? Very well. I\'ll add you to the rolls and you may begin working with the other valiants immediately.', 0);
INSERT INTO `quest_offer_reward` VALUES (13697, 0, 0, 0, 0, 0, 0, 0, 0, 'Impressive, valiant. I will make the arrangements for your test immediately.', 0);
INSERT INTO `quest_offer_reward` VALUES (13698, 0, 0, 0, 0, 0, 0, 0, 0, 'I saw those explosions all of the way from over here! Those filthy orcs and their goblin lackeys are going to have a hard time doing anything to despoil the forest now!$B$BWe can\'t thank you enough, $N. Will this do?', 0);
INSERT INTO `quest_offer_reward` VALUES (13699, 0, 0, 0, 0, 0, 0, 0, 0, 'You have proven yourself worthy of the title of champion, $N. You will compete at the tournament\'s highest level on behalf of Stormwind, riding in the grand melee with your weapon at the ready and our mighty pennant at your back.', 0);
INSERT INTO `quest_offer_reward` VALUES (13702, 0, 0, 0, 0, 0, 0, 0, 0, 'I welcome you as a champion of the tournament, $N. As a champion, you will be able to compete for the grand championship of the tournament. You may also choose to become a valiant with another Alliance city\'s delegation and in time, a champion for that city as well.$B$BAll champions must have the assistance of a squire, $N, and you are no exception. Allow me to appoint one of ours to accompany you in battle. He will proudly carry the banner of any city that has named you a champion.', 0);
INSERT INTO `quest_offer_reward` VALUES (13703, 0, 0, 0, 0, 0, 0, 0, 0, 'We are proud that you have decided to join us and I know you will shine on the field.', 0);
INSERT INTO `quest_offer_reward` VALUES (13704, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re proud to count you among us, $n, and we look forward to training with you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13705, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to have you on our team, $n. We will all benefit from training with you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13706, 0, 0, 0, 0, 0, 0, 0, 0, 'The warriors of Darnassus are honored to fight and train beside you.', 0);
INSERT INTO `quest_offer_reward` VALUES (13707, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome, then, $n. I look forward to watching you train with my men.', 0);
INSERT INTO `quest_offer_reward` VALUES (13708, 0, 0, 0, 0, 0, 0, 0, 0, 'I am glad that you have decided to join us and look forward to seeing you in action.', 0);
INSERT INTO `quest_offer_reward` VALUES (13709, 0, 0, 0, 0, 0, 0, 0, 0, 'I am honored that you have chosen to join our delegation. I know you will make us proud, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (13710, 0, 0, 0, 0, 0, 0, 0, 0, 'You have chosen wisely, $n. Both of us will benefit from this arrangement.', 0);
INSERT INTO `quest_offer_reward` VALUES (13711, 0, 0, 0, 0, 0, 0, 0, 0, 'It will be an honor training and competing with you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (13712, 0, 0, 0, 0, 0, 0, 0, 0, 'Gorka\'s death was not in vain, $n.$B$BSplintertree would have certainly fallen if we hadn\'t come when we did.$B$BNow we must get to work shoring up her defenses.$B$BThis isn\'t over....', 0);
INSERT INTO `quest_offer_reward` VALUES (13713, 0, 0, 0, 0, 0, 0, 0, 0, 'You have proven yourself worthy of the title of champion, $n. You will compete at the tournament\'s highest level on behalf of Ironforge, riding in the grand melee with your weapon at the ready and our mighty pennant at your back.', 0);
