INSERT INTO `quest_offer_reward` VALUES (24652, 0, 0, 0, 0, 0, 0, 0, 0, 'Good job, kid. You deserve a little somethin\'. And hey, come back tomorrow if you get the time; I might have a job for ya.', 0);
INSERT INTO `quest_offer_reward` VALUES (24653, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, these are clearly symbols of Agamaggan. But if the quilboar are still devoted to their dead god, what is the source of their vicious agression?  It is said Agamaggan was noble, and even gave his life fighting the Burning Legion...\n\nA moment, $N. We must confer with the spirits.', 0);
INSERT INTO `quest_offer_reward` VALUES (24654, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re covered in hot tar and bug juice! Don\'t worry, friend. It\'ll come off.\n\nSomeday.', 0);
INSERT INTO `quest_offer_reward` VALUES (24655, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey, thanks, kid. Give it over, let\'s take a look.$B$B<He takes the device from you and fiddles with it.>$B$BOh, that ain\'t good.', 0);
INSERT INTO `quest_offer_reward` VALUES (24656, 0, 0, 0, 0, 0, 0, 0, 0, 'You got it, huh? Good job, kid. Top notch. Let\'s see what we what we got here.$B$B<He cracks open the crate and rummages around inside.', 0);
INSERT INTO `quest_offer_reward` VALUES (24657, 0, 0, 0, 0, 0, 0, 0, 0, 'You got his ledger? Really? That\'s great, $N. There\'s probably loads of good information in here.', 0);
