INSERT INTO `quest_offer_reward` VALUES (24828, 0, 0, 0, 0, 0, 0, 0, 0, 'You continue to prove yourself, $N.  Accept this, not as a gift, but as a tool for the fight against the Scourge.', 0);
INSERT INTO `quest_offer_reward` VALUES (24829, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a true champion of our cause, $n. None can claim to be more dedicated to the struggle than you.$B$BThe enemy speaks of your name only in whispers, for they know you\'re a force to be reckoned with.$B$BAccept this humble gift as a reward for your many triumphs.', 0);
INSERT INTO `quest_offer_reward` VALUES (24830, 0, 0, 0, 0, 0, 0, 0, 0, 'You continue to prove yourself, $n. Accept this, not as a gift, but as a tool for the fight against the Scourge.', 0);
INSERT INTO `quest_offer_reward` VALUES (24831, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a true champion of our cause, $n. None can claim to be more dedicated to the struggle than you.$B$BYou are a bastion of wisdom and our allies speak of you with the greatest reverence. Many of them owe you their own lives.$B$BAccept this humble gift as a reward for your many triumphs.', 0);
INSERT INTO `quest_offer_reward` VALUES (24832, 0, 0, 0, 0, 0, 0, 0, 0, 'You continue to prove yourself, $n. Accept this, not as a gift, but as a tool for the fight against the Scourge.', 0);
INSERT INTO `quest_offer_reward` VALUES (24833, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a true champion of our cause, $n. None can claim to be more dedicated to the struggle than you.$B$BIt is rumored that the minions of the Scourge fear you more than they do the Lich King himself. You are vengeance incarnate.$B$BAccept this humble gift as a reward for your many triumphs.', 0);
