INSERT INTO `quest_offer_reward` VALUES (24835, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a true champion of our cause, $n. None can claim to be more dedicated to the struggle than you.$B$BYou are a source of inspiration to our allies and your unshakeable valor gives pause to even the most seasoned of our enemy\'s generals.$B$BAccept this humble gift as a reward for your many triumphs.', 0);
