INSERT INTO `quest_offer_reward` VALUES (25790, 5, 0, 0, 0, 0, 0, 0, 0, 'THEY WHAT?!', 19116);
INSERT INTO `quest_offer_reward` VALUES (25791, 0, 0, 0, 0, 0, 0, 0, 0, 'THEY WHAT?!', 0);
INSERT INTO `quest_offer_reward` VALUES (25792, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally, some good news from the Frostmane Front. I\'ll have the mountaineers press their attack immediately. Now, we must signal our gnomish allies at Steelgrill to start their advance. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25793, 0, 0, 0, 0, 0, 0, 0, 0, 'Seal it off? That\'s gonna take some work, friend.', 0);
INSERT INTO `quest_offer_reward` VALUES (25794, 0, 0, 0, 0, 0, 0, 0, 0, 'We must make haste!$b$bAs you can surely see, his time is not long.', 0);
INSERT INTO `quest_offer_reward` VALUES (25795, 6, 0, 0, 0, 0, 0, 0, 0, '$n! Is it true that you are the $gwestmaster:leader; the attack on the twilight dragon?$B$BTruly, you are the $gwestmaster:messenger; of the ancients!', 19342);
INSERT INTO `quest_offer_reward` VALUES (25796, 273, 0, 0, 0, 0, 0, 0, 0, 'That\'ll teach them. You\'ve softened the Splithoof up enough that we should be able to govern them now with a minimal guard presence from the Speedbarge.$B$BThat is, once we\'ve dealt with their supposed air elemental leader.', 19116);
INSERT INTO `quest_offer_reward` VALUES (25797, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'ll teach them. You\'ve softened the Splithoof up enough that we should be able to govern them now with a minimal guard presence from the Speedbarge.$B$BThat is, once we\'ve dealt with their supposed air elemental leader.', 0);
INSERT INTO `quest_offer_reward` VALUES (25798, 4, 0, 0, 0, 0, 0, 0, 0, 'Oil! Black gold! Tanaris Tea!$B$BWe\'re rich! We just have to clear out the remaining pesky centaur. I\'m sure with all the money we\'re going to make, we can relocate them to some other bluff.', 19116);
INSERT INTO `quest_offer_reward` VALUES (25799, 0, 0, 0, 0, 0, 0, 0, 0, 'Oil! Black gold! Tanaris Tea!$B$BWe\'re rich! We just have to clear out the remaining pesky centaur. I\'m sure with all the money we\'re going to make, we can relocate them to some other bluff.', 0);
INSERT INTO `quest_offer_reward` VALUES (25800, 0, 0, 0, 0, 0, 0, 0, 0, 'A hearty seafood dinner will lift everyone\'s spirits and help us get the town back in shape again. Thanks for the help, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (25801, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, well done $N.$B$BAccept this as payment for fulfillment of your contract with the Merchant Marines.', 0);
INSERT INTO `quest_offer_reward` VALUES (25802, 0, 0, 0, 0, 0, 0, 0, 0, 'This crate is covered with clawed scratches, and the marks of water damage.  Prying it open reveals soggy and rotted straw, as if something of value was stored in the crate.  You search through the straw...$B$B...and find an armload of red-colored bottles.', 0);
INSERT INTO `quest_offer_reward` VALUES (25803, 0, 0, 0, 0, 0, 0, 0, 0, 'This barrel is largely untouched.  Prying it open...$B$B...you are assaulted by the pungent odor of cheese.  It\'s a little wet, but still tasty!', 0);
INSERT INTO `quest_offer_reward` VALUES (25804, 0, 0, 0, 0, 0, 0, 0, 0, 'This barrel is halfway buried amidst the mud and grass ...', 0);
INSERT INTO `quest_offer_reward` VALUES (25805, 0, 0, 0, 0, 0, 0, 0, 0, 'The captain of the Blind Princess will be relieved to hear this precious cargo has been recovered.  You\'ve well earned this payment for services rendered.$B$BIt\'s good to see dependable folk like you, especially in trying times such as these.', 0);
INSERT INTO `quest_offer_reward` VALUES (25806, 0, 0, 0, 0, 0, 0, 0, 0, '<The Gnomecorder buzzes and whirs.>$B$BHey, this thing on?$B$B<You hear a tapping sound.>$B$BAnybody there?', 0);
INSERT INTO `quest_offer_reward` VALUES (25807, 1, 0, 0, 0, 0, 0, 0, 0, 'Я... жива.$B$BТе, кто пытались осквернить меня и похитить моих детей скоро испытают на себе остроту моих когтей, |3-6($c). Можешь не сомневаться.$B$BПродолжай продвигаться вглубь Хиджала. Я соберусь с силами и присоединюсь к другим Древним.', 19342);
INSERT INTO `quest_offer_reward` VALUES (25808, 0, 0, 0, 0, 0, 0, 0, 0, 'Nicely done, $N! You worked the mines like a pro. Now how about returning my goggles.', 0);
INSERT INTO `quest_offer_reward` VALUES (25809, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank the Light! You truly are a righteous instrument of power! The Alliance is lucky to you have, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (25810, 5, 0, 0, 0, 0, 0, 0, 0, 'Так это правда – Авиана вернулась, и ты ее $gвестник:вестница;.$B$BНам предстоит много работы. Мы находимся в самом сердце Огненных Просторов. Сетрия перенесла сюда украденные на горе Хиджал яйца, чтобы тут высидеть хищников, обученных убивать.$B$BЯ надеюсь, ты $gготов:готова; нанести ответный удар!', 19342);
INSERT INTO `quest_offer_reward` VALUES (25811, 0, 0, 0, 0, 0, 0, 0, 0, 'Here goes nothin\'!', 0);
INSERT INTO `quest_offer_reward` VALUES (25812, 0, 0, 0, 0, 0, 0, 0, 0, 'A... a $R? Here?$B$BI must be mad...', 0);
INSERT INTO `quest_offer_reward` VALUES (25813, 1, 0, 0, 0, 0, 0, 0, 0, 'Well then, let\'s see them get any kind of magical hocus pocus air support now when we take over!', 19116);
INSERT INTO `quest_offer_reward` VALUES (25814, 0, 0, 0, 0, 0, 0, 0, 0, 'Well then, let\'s see them get any kind of magical hocus pocus air support now when we take over!', 0);
INSERT INTO `quest_offer_reward` VALUES (25815, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah!  That hits the spot, and loosens the tongue.  Thanks, mate!', 0);
INSERT INTO `quest_offer_reward` VALUES (25816, 0, 0, 0, 0, 0, 0, 0, 0, 'Fitzsimmons sent you, didn\'t he? It\'s been so long...', 0);
INSERT INTO `quest_offer_reward` VALUES (25817, 0, 0, 0, 0, 0, 0, 0, 0, 'The cries of anguish have been stifled...but the source of the curse still lurks.', 0);
INSERT INTO `quest_offer_reward` VALUES (25818, 0, 0, 0, 0, 0, 0, 0, 0, 'The key is rusted but it fits the lock neatly.', 0);
INSERT INTO `quest_offer_reward` VALUES (25819, 0, 0, 0, 0, 0, 0, 0, 0, 'The Eye of Paleth? You say Fitzsimmons sent you out?$B$BBy the light...not again. Fitzsimmons is dead, lad. You\'ve not the first to come to me like this, either.', 0);
INSERT INTO `quest_offer_reward` VALUES (25820, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, bless your heart, to do such a thing for a married woman like me. You\'re too kind.', 0);
INSERT INTO `quest_offer_reward` VALUES (25821, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew that you\'d be a special snowflake when I first set my eyes upon you, $N. So special, in fact, that I\'d trust no one else here with what I\'m about to ask of you.', 0);
INSERT INTO `quest_offer_reward` VALUES (25822, 0, 0, 0, 0, 0, 0, 0, 0, 'This should be enough to supply a hundred Grimtotem! Now to present the weaponry to their chieftain.', 0);
INSERT INTO `quest_offer_reward` VALUES (25823, 0, 0, 0, 0, 0, 0, 0, 0, 'I will call for the infantry to move in and clear up whatever might be left over. If only we had ten-thousand more like you, $N!', 0);
INSERT INTO `quest_offer_reward` VALUES (25824, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, it\'s not encouraging, but it\'s something...', 0);
INSERT INTO `quest_offer_reward` VALUES (25825, 4, 0, 0, 0, 0, 0, 0, 0, 'It is oil! You\'re really proving yourself to be invaluable, $n!$B$BToo bad I\'m going to have to send you off now.', 19116);
INSERT INTO `quest_offer_reward` VALUES (25826, 0, 0, 0, 0, 0, 0, 0, 0, 'It is oil! You\'re the best, $n!$B$BToo bad I\'m going to have to send you off now.', 0);
INSERT INTO `quest_offer_reward` VALUES (25830, 0, 0, 0, 0, 0, 0, 0, 0, 'Я… все еще… жив.', 19342);
INSERT INTO `quest_offer_reward` VALUES (25832, 0, 0, 0, 0, 0, 0, 0, 0, '<Aviana\'s face remains nonchalant when you tell her about events in the incubator.>$B$BMy eggs were stolen, |3-6($c). They were taken to a dark place to be desecrated there.$B$BMy children have been kidnapped, tortured... burned in a cauldron of fire... The Twilight Hammer will pay for it.$B$BBut I have to admit, there\'s a part of me that wants to take off and see... this whole world burn down.', 19342);
INSERT INTO `quest_offer_reward` VALUES (25834, 0, 0, 0, 0, 0, 0, 0, 0, 'The name be \"Cookie,\" $g lad:lass;: greatest cook \'cross Azeroth. Now the Commander\'s told me what we be needin\' to do. To gain audience with the Grimtotem chief we\'re gonna have to cook up the finest of tauren cuisine.$B$BYe get me the ingredients and I\'ll put together an offerin\' no tauren could refuse. Not even a grim tauren or whatever them things be.', 0);
INSERT INTO `quest_offer_reward` VALUES (25835, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad that Fizzle finally sent you here. We\'re helping the tauren to retake Freewind Post and push back the Grimtotem.', 0);
INSERT INTO `quest_offer_reward` VALUES (25836, 0, 0, 0, 0, 0, 0, 0, 0, 'I am honored that you have come to help, $C. It was kind of Pozzik to send you.', 0);
INSERT INTO `quest_offer_reward` VALUES (25837, 0, 0, 0, 0, 0, 0, 0, 0, 'Stand back, $N, there\'s about to be a flavor explosion and I wouldn\'t want ya gettin\' hurt.', 0);
INSERT INTO `quest_offer_reward` VALUES (25838, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, you must be the $r that Captain Tharran said he\'d be sending. I\'ve already used the deploy-a-tron to get most of the covert ops team into position at Frostmane Retreat. They\'re just waiting on your arrival. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25839, 0, 0, 0, 0, 0, 0, 0, 0, 'Don\'t worry, the feeling of being zapped won\'t stay with you for more than a few minutes. All in all, it\'s not a bad way to get where you\'re going. That parachute system needs some work, though. Very late deployment, indeed. Sometimes, too late. Anyway, we have a mission to carry out. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25840, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent job, $n. With Battok dead, I\'ll send the signal to Captain Tharran. This should be the end of the Frostmane threat. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25841, 0, 0, 0, 0, 0, 0, 0, 0, 'Both you and the air wing performed admirably. There\'s no way that the Frostmanes will remain here now. All that remains is to begin mopping up the survivors. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25842, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, |3-6($r).$B$BТеперь я могу уйти.$B$BВсе, что когда-то наполняло меня, весь этот лес... принадлежит тебе.', 19342);
INSERT INTO `quest_offer_reward` VALUES (25843, 0, 0, 0, 0, 0, 0, 0, 0, '$R.$B$BI remember you $gSavior: Savior; my children.$B$BBthe remorse of revenge has come.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25844, 0, 0, 0, 0, 0, 0, 0, 0, '<Ton and his bodyguards scarf down the meaty offering.>$B$BThis... this is the greatest thing I have ever tasted. You may prove to be a valuable ally, but before the Grimtotem agree to work with the Alliance, you must agree to our terms. I will now give to you those terms as told to me by my leader. Take them back to your commander and see if he accepts.', 0);
INSERT INTO `quest_offer_reward` VALUES (25845, 0, 0, 0, 0, 0, 0, 0, 0, 'Weapons, eh?$B$B<Force Commander Valen sighs.>$B$BI\'m about to make an executive decision here, $N. Let\'s hope this pays off, else King Wrynn will have my head!', 0);
INSERT INTO `quest_offer_reward` VALUES (25846, 0, 0, 0, 0, 0, 0, 0, 0, 'These are fine weapons! Powerful steel! My warriors will be unstoppable armed with such weaponry. Magatha smiles down upon you, $N.$B$BThere is but one thing I ask of you, perhaps two.', 0);
INSERT INTO `quest_offer_reward` VALUES (25847, 0, 0, 0, 0, 0, 0, 0, 0, 'The fight is far from over, $N. The Horde generals still live...', 0);
INSERT INTO `quest_offer_reward` VALUES (25848, 0, 0, 0, 0, 0, 0, 0, 0, '<Grundig crushes the insignia in his massive fist.>$B$BYes, ally, you have done it! It will take Hellscream weeks to recover from this blow. Southern Stonetalon belongs to the Grimtotem... and the Alliance, of course.', 0);
INSERT INTO `quest_offer_reward` VALUES (25849, 0, 0, 0, 0, 0, 0, 0, 0, 'See? Not bad, eh? Why, I almost wish all my finds decided to dig themselves up for me. It\'d make my job a lot easier!$B$BOf course, you made it easier too, $n. Much obliged.', 0);
INSERT INTO `quest_offer_reward` VALUES (25850, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s showin\' em what for! Good on you.', 0);
INSERT INTO `quest_offer_reward` VALUES (25851, 0, 0, 0, 0, 0, 0, 0, 0, 'Outstanding work, $N! Now our forces only need to worry about the Horde as they enter Stonetalon. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25852, 0, 0, 0, 0, 0, 0, 0, 0, 'You say the Grimtotem are no longer a threat? That\'s great, but we\'ve got Horde all up our keesters!', 0);
INSERT INTO `quest_offer_reward` VALUES (25853, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s a relief! Maybe we can dig our way out of the hole we\'re still in, eh?$B$BNo, nevermind, just a bad joke.', 0);
INSERT INTO `quest_offer_reward` VALUES (25854, 0, 0, 0, 0, 0, 0, 0, 0, 'Wonderful! These should be ready to hatch within the day...$B$BNow I just need to cobble up a raptor pen before they all hatch nigh-simultaneously. What could go wrong?', 0);
INSERT INTO `quest_offer_reward` VALUES (25855, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, wonderful! I\'ll be able to start reassembling these right away. Thank you so much!', 0);
INSERT INTO `quest_offer_reward` VALUES (25856, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m much obliged, $C. This\'ll go a ways towards getting my family back on their feet.', 0);
INSERT INTO `quest_offer_reward` VALUES (25857, 0, 0, 0, 0, 0, 0, 0, 0, 'Amazing! You\'re a true hero, $n. I\'ll never forget what you\'ve done for me and my family.$B$BI know it\'s not much, but please, take this with my blessing.', 0);
INSERT INTO `quest_offer_reward` VALUES (25858, 0, 0, 0, 0, 0, 0, 0, 0, 'Reinforcements have begun to circle the waters around the ledge, preparing for the attack. They await only your lead.', 0);
INSERT INTO `quest_offer_reward` VALUES (25859, 0, 0, 0, 0, 0, 0, 0, 0, 'Not one slipped through to the bridge. Not one.$B$BIt was very boring. I\'d appreciate it if you shared a little next time.', 0);
INSERT INTO `quest_offer_reward` VALUES (25860, 0, 0, 0, 0, 0, 0, 0, 0, 'Azjentus is to be taken at his word. Lady Naz\'jar\'s Honor Guard should be sufficient to see the rest of the ritual through.', 0);
INSERT INTO `quest_offer_reward` VALUES (25861, 0, 0, 0, 0, 0, 0, 0, 0, 'My scythe is sated. Thank you, my lady.', 0);
INSERT INTO `quest_offer_reward` VALUES (25862, 0, 0, 0, 0, 0, 0, 0, 0, 'The crucible looks far too heavy to carry on your own, but more than enough naga forces remain to prepare it for transport.', 0);
INSERT INTO `quest_offer_reward` VALUES (25863, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, my Battlemaiden.$B$BThis crucible will serve as a font of power for our summoning ritual. With it, Sira\'kess will beckon our allies from the depths to bolster our already swelling forces.', 0);
INSERT INTO `quest_offer_reward` VALUES (25864, 0, 0, 0, 0, 0, 0, 0, 0, 'A job well done. Consider this reward not from me, but from Ironforge; the preservation of Khaz Modan is a matter that concerns every dwarf.', 0);
INSERT INTO `quest_offer_reward` VALUES (25865, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! The Ironforge Guard appreciates this, $n. You\'ve done our city a fine favor.$B$BBut something new has come up...', 0);
INSERT INTO `quest_offer_reward` VALUES (25866, 0, 0, 0, 0, 0, 0, 0, 0, 'I don\'t suppose they agreed to come along quietly? Didn\'t think so.', 0);
INSERT INTO `quest_offer_reward` VALUES (25867, 0, 0, 0, 0, 0, 0, 0, 0, 'That should do it! The more gnolls those fools have to chase after, the easier it\'ll be to keep them in line.', 0);
INSERT INTO `quest_offer_reward` VALUES (25868, 0, 0, 0, 0, 0, 0, 0, 0, 'A job well done! You\'ve brought one of Ironforge\'s most wanted to justice today, $n. I\'d say you\'ve earned a mighty fine reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (25869, 0, 0, 0, 0, 0, 0, 0, 0, 'Grim business, that.<Quentin coughs.>$B$BNo pun intended.', 0);
INSERT INTO `quest_offer_reward` VALUES (25870, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s a good start. Normally, I wouldn\'t wish harm on any of the Earth Mother\'s creatures.$B$BBut attacking us at our most vulnerable moment?! They deserve to die!', 0);
INSERT INTO `quest_offer_reward` VALUES (25871, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for freeing her, $n. Those Grimtotem monsters would have killed her in cold blood.', 0);
INSERT INTO `quest_offer_reward` VALUES (25872, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for freeing him, $n. Those Grimtotem would have killed him in cold blood.', 0);
INSERT INTO `quest_offer_reward` VALUES (25873, 0, 0, 0, 0, 0, 0, 0, 0, 'A traitor amongst the tauren?$B$BI\'ve never heard of such a thing. Clan rivalries and warfare are one thing, but killing your own in cold blood.$B$B<Quentin shakes his head in disbelief.>', 0);
INSERT INTO `quest_offer_reward` VALUES (25874, 0, 0, 0, 0, 0, 0, 0, 0, 'The cliffwatcher was a Grimtotem spy?! No wonder we had no warning!$B$B<Thalia seethes with barely contained rage.>$B$BI don\'t want that damned thing. Throw it into the water and let the Longhorn name never be spoken again!', 0);
INSERT INTO `quest_offer_reward` VALUES (25875, 0, 0, 0, 0, 0, 0, 0, 0, 'Get in here and hug a wall! I can\'t risk blowing my cover!', 0);
INSERT INTO `quest_offer_reward` VALUES (25876, 0, 0, 0, 0, 0, 0, 0, 0, 'OH NO! They plan to \"test\" the bomb at Thal\'darah Grove! That\'s the school for young druids located in the heart of Battlescar Valley. You\'ve got to return to Barus with this information! Master Thal\'darah must be warned!', 0);
INSERT INTO `quest_offer_reward` VALUES (25877, 0, 0, 0, 0, 0, 0, 0, 0, 'Blasted Horde! I\'ve got friends at that grove. Hopefully you know how to ride a hippogryph. We\'ve got to get you to Master Thal\'darah at the overlook immediately!', 0);
INSERT INTO `quest_offer_reward` VALUES (25878, 0, 0, 0, 0, 0, 0, 0, 0, 'I could see the explosions from here, $N! The Horde won\'t be refueling from Mirkfallon anymore, thanks to you!', 0);
INSERT INTO `quest_offer_reward` VALUES (25879, 0, 0, 0, 0, 0, 0, 0, 0, 'Slowly, but surely, we pick away at the Horde\'s numbers. They won\'t have a leg to stand on when this war really opens up.', 0);
INSERT INTO `quest_offer_reward` VALUES (25880, 0, 0, 0, 0, 0, 0, 0, 0, 'These are dire times, indeed, $N. Our outpost in Stonetalon Peak has been overrun by fiendish creatures from the depths and the Horde seeks to destroy us. We need a hero, $N; a soldier of the Alliance strong enough to guide us through these trying times.', 0);
INSERT INTO `quest_offer_reward` VALUES (25881, 1, 11, 0, 0, 0, 0, 0, 0, 'Ты $gсобрал:собрала; всех выживших? Прекрасно. А оставшаяся часть горы исцелилась?$B$BРазрази меня гром! Мы снова оказались на передовой!', 19243);
INSERT INTO `quest_offer_reward` VALUES (25882, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'re the one who\'s been helping Captain Tharran beat back the Frostmane trolls? I\'ve seen your name in the reports. It\'s good to finally meet you. You might be just the $c we need to help secure the ranch. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25883, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re confident that your assault on the naga armaments has made an impact on their effectiveness.', 0);
INSERT INTO `quest_offer_reward` VALUES (25884, 0, 0, 0, 0, 0, 0, 0, 0, 'The waters surrounding the Holding Pens are red with the blood of the naga.$B$BRendel would be proud!', 0);
INSERT INTO `quest_offer_reward` VALUES (25885, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, yes. This is the stuff.$B$BMaybe I can gradually eat less of it and avoid that side-effect.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (25886, 1, 273, 0, 0, 0, 0, 0, 0, 'Изумительно! Силы культа Сумеречного Молота, конечно же, снова попробуют взять гору, вот только в бой их больше не поведут тугие на голову капитаны.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25887, 0, 0, 0, 0, 0, 0, 0, 0, 'It is as it should be.$B$BNow the way is clear to deal these naga a crippling blow.', 0);
INSERT INTO `quest_offer_reward` VALUES (25888, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us hope for the safety of those released prisoners.$B$BIn return for your noble efforts, please accept this gift.', 0);
INSERT INTO `quest_offer_reward` VALUES (25889, 0, 0, 0, 0, 0, 0, 0, 0, 'Praise you, $N. The hero we have been searching for has finally arrived, and not a moment too soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (25890, 0, 0, 0, 0, 0, 0, 0, 0, 'Erunak trusts this task to me?  Very well, let\'s see what we can learn from the ancient one.', 0);
INSERT INTO `quest_offer_reward` VALUES (25891, 0, 0, 0, 0, 0, 0, 0, 0, 'Do you know why the Horde destroys? The Horde destroys because they cannot comprehend beauty. They fear it and that which they fear they must destroy. Why else would they destroy Thal\'darah Grove?$B$BTogether, we will make them understand. They will know that what they do comes at a great cost.', 0);
INSERT INTO `quest_offer_reward` VALUES (25892, 0, 0, 0, 0, 0, 0, 0, 0, 'You made it! After the attack at the forward post, I thought we might be the only ones left. I\'m very glad to see you.', 0);
INSERT INTO `quest_offer_reward` VALUES (25893, 0, 0, 0, 0, 0, 0, 0, 0, 'Impeccable timing. Like clockwork! Trouble comes. You come. Save the day. Clockwork!$B$BWe\'ve devised our brilliant plan. Gloriously simple, barring the hostility of local life. Simpler yet with a you available!', 0);
INSERT INTO `quest_offer_reward` VALUES (25894, 0, 0, 0, 0, 0, 0, 0, 0, 'Less gill goblins is good!$B$BI\'m glad that they\'re so primitive. I don\'t think I\'d want to be dealing with a race of aquatic tech-freaks right now. Not at all.', 0);
INSERT INTO `quest_offer_reward` VALUES (25895, 0, 0, 0, 0, 0, 0, 0, 0, 'Let me see, let me see... flare gun... balloon... no rope. Never any rope. Terribly impractical if you do ask me. Rope should be mandatory.', 0);
INSERT INTO `quest_offer_reward` VALUES (25896, 0, 0, 0, 0, 0, 0, 0, 0, 'The Kvaldir have proved a greater threat than we anticipated, but our plans are not hindered in the least. As our allies arrive, we will have a force more than sufficient to usurp the Tidehunter.$B$BAll that remains is to ensure that the ritual of summoning is completed.', 0);
INSERT INTO `quest_offer_reward` VALUES (25897, 0, 0, 0, 0, 0, 0, 0, 0, 'This is great! I love it. It should work per... ewww! Is this stuff braided with hair?$B$BDoesn\'t matter. Almost done. This thing will be ready in the twist of a rope.', 0);
INSERT INTO `quest_offer_reward` VALUES (25898, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect! There\'s no way they missed that, fighting or not.', 0);
INSERT INTO `quest_offer_reward` VALUES (25899, 6, 1, 273, 0, 0, 0, 0, 0, 'Скольких ты $gубил:убила;? Неплохо. Очень даже неплохо. Думаю, мои люди больше не найдут никаких отговорок.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25900, 0, 0, 0, 0, 0, 0, 0, 0, 'That didn\'t go well.  For some reason, the beast won\'t speak back to me.$b$b<Duarn is clearly frustrated.>$b$bIf only Erunak had not left us!  He would know what to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (25901, 1, 2, 0, 0, 0, 0, 0, 0, 'Beautiful! They\'re still sharp. The plumage is covered in blood, but it\'s no trouble. Thank you, $n. We can defend the Hijal again.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25904, 0, 0, 0, 0, 0, 0, 0, 0, 'Это древняя сила.$BПримитивная.$BПростая.$B$BМы обратим ее против них.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25905, 0, 0, 0, 0, 0, 0, 0, 0, 'The rams you rescued have made their way back into the pen. I\'ve got members of the family on a day and night watch now. Those trolls won\'t take us by surprise again! ', 0);
INSERT INTO `quest_offer_reward` VALUES (25906, 6, 5, 0, 0, 0, 0, 0, 0, 'Тебя послал бог-черепаха? Будь благословен его панцирь! Я уж думал, что останусь тут навечно!', 19243);
INSERT INTO `quest_offer_reward` VALUES (25907, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $N... although I\'m afraid I haven\'t been nearly as productive as you have.', 0);
INSERT INTO `quest_offer_reward` VALUES (25908, 0, 0, 0, 0, 0, 0, 0, 0, 'Can you sense it?  If you have an ounce of shaman\'s blood in you, you must feel the sense of relief in the air.$b$bNespirah is pleased.  Perhaps she will finally speak with us.', 0);
INSERT INTO `quest_offer_reward` VALUES (25909, 0, 0, 0, 0, 0, 0, 0, 0, '<Duarn looks at the crab and disguises a frown.>$B$BIt doesn\'t look very impressive, does it?', 0);
INSERT INTO `quest_offer_reward` VALUES (25910, 273, 1, 0, 0, 0, 0, 0, 0, 'Так вот, значит, как.$B$BОт осознания того, с какими жуткими существами вступил в союз Сумеречный Молот, у меня кровь в жилах вскипает и сворачивается.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25911, 0, 0, 0, 0, 0, 0, 0, 0, 'That is the best news I\'ve heard in what seems like an eternity. We\'ll get the survivors moving over there to the surface as soon as possible. Perhaps we can forget that this whole nightmare ever happened.$B$BThank you, $n. I\'m not sure there\'s anything I can do to repay you for everything you\'ve managed to accomplish in this ocean, but I will try to find a way.$B$BYou should consider meeting us at the ships later, when you finish saying your goodbyes to the naga.', 0);
INSERT INTO `quest_offer_reward` VALUES (25912, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve found it! Elune bless you, child! Perhaps this book will give me the strength to carry on.', 0);
INSERT INTO `quest_offer_reward` VALUES (25913, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $N. Each glaive has the name of the sentinel it belonged to imprinted upon its blade. We can now send word to their families and put their souls to rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (25914, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m afraid that the death of Harbinger Aph\'lass has not ceased the tendril attacks. You\'ve done all that you can, $N, and for that I thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (25915, 1, 273, 0, 0, 0, 0, 0, 0, 'О да… Секрет силы Немесиса находится в этих пещерах.$B$BТеперь мы знаем все, что нам нужно, $n. Пора покончить с этим.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25916, 0, 0, 0, 0, 0, 0, 0, 0, 'I had assumed that the naga were only here for those pearls. No, this makes much more sense. If they had Nespirah\'s power under their control, the amount of damage the naga could do would be catastrophic!$B$BSomething must be done.', 0);
INSERT INTO `quest_offer_reward` VALUES (25917, 0, 0, 0, 0, 0, 0, 0, 0, '$n... is that you?', 0);
INSERT INTO `quest_offer_reward` VALUES (25918, 0, 0, 0, 0, 0, 0, 0, 0, '<Admiral Dvorek admires the polearms you present him.>$B$BOh, I\'m sorry. I was just imagining what these will look like with little bits of naga stuck to the ends of them. Thank you, $n. We\'ll put these to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (25919, 0, 0, 0, 0, 0, 0, 0, 0, 'Hopefully we made some progress. I\'m not sure how we\'ll know when the creature wakes up, but something tells me that it will be pretty obvious when it does.', 0);
INSERT INTO `quest_offer_reward` VALUES (25920, 0, 0, 0, 0, 0, 0, 0, 0, 'Brilliant work!$B$B<Valoren tucks the pearls away beneath her armor.>$B$BNow I just need you and Captain Taylor to get me safely out of here. I can handle the studies from there on.', 0);
INSERT INTO `quest_offer_reward` VALUES (25921, 0, 0, 0, 0, 0, 0, 0, 0, 'No need to bring me up to speed... Nespirah has been narrating your adventures as they happened, and Captain Taylor filled me in on the rest.$B$B<Duarn closes his eyes and nods.>$B$BNespirah says that she is quite grateful, and that she has a small surprise ready for the naga.', 0);
INSERT INTO `quest_offer_reward` VALUES (25922, 0, 0, 0, 0, 0, 0, 0, 0, 'It appears that I owe you my life, yet again. Thank the Light that you found me before you and your shaman activated those bio-defenses.', 0);
INSERT INTO `quest_offer_reward` VALUES (25923, 1, 5, 4, 0, 0, 0, 0, 0, 'Захватчик пал!$B$BСумеречный Молот мечтал создать собственный пантеон из оскверненных древних, чтобы править землей, водой и воздухом. С твой помощью нам удалось с ними покончить.$B$BТортолла должен узнать об этом!', 19243);
INSERT INTO `quest_offer_reward` VALUES (25924, 0, 0, 0, 0, 0, 0, 0, 0, 'Not exactly the battle you had anticipated, I\'ll wager. But a battle nonetheless, as you shall soon see....', 0);
INSERT INTO `quest_offer_reward` VALUES (25925, 0, 0, 0, 0, 0, 0, 0, 0, 'We witnessed the detonation of the bomb. Terrible, terrible destruction... Farwatcher Glen, for better or for worse, is still intact. Unfortunately we\'ve nowhere to run.', 0);
INSERT INTO `quest_offer_reward` VALUES (25926, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahhh... yes. The swamp suffers less now.$B$BMore threats, though. More lurk. I can see them!', 0);
INSERT INTO `quest_offer_reward` VALUES (25927, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes... the flames die. There is peace now. Peace for the land.', 0);
INSERT INTO `quest_offer_reward` VALUES (25928, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gбыл:была; на Огненных Просторах?$B$BНемесис мертв?$B$BЕще одной угрозой меньше.$B$BБлагодарю тебя.', 19243);
INSERT INTO `quest_offer_reward` VALUES (25929, 0, 0, 0, 0, 0, 0, 0, 0, 'Here you are, $C. With this you should find maneuvering in the sea to be nearly effortless. $B$BNow we must get to work. There is much to be done in little time.', 0);
INSERT INTO `quest_offer_reward` VALUES (25930, 0, 0, 0, 0, 0, 0, 0, 0, '<The Gnomecorder crackles with energy.>$B$BI say, are you there, $N? It would appear that you are in the center of a dragon\'s lair.', 0);
INSERT INTO `quest_offer_reward` VALUES (25931, 0, 0, 0, 0, 0, 0, 0, 0, 'A smashing success! Perhaps only a minor victory against the brood of Deathwing, but a victory nonetheless!', 0);
INSERT INTO `quest_offer_reward` VALUES (25932, 0, 0, 0, 0, 0, 0, 0, 0, 'With your help, we\'ve managed to weaken the raiders considerably. They haven\'t suffered such losses since the raids began. Now is the time to press our advantage. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25933, 0, 0, 0, 0, 0, 0, 0, 0, 'Sergeant Flinthammer? I\'m not familiar with the name, but, aye, there is a Flinthammer among the miners. He\'s one of the ones lucky enough to make it out of the mine, but most of my men are still trapped down there. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25934, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. This will allow me to finish my last task and hopefully gain the form of the wolf!$B$B<Salsbury howls.>', 0);
INSERT INTO `quest_offer_reward` VALUES (25935, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. The dogs will be well fed and ready to defend our outpost for weeks!', 0);
INSERT INTO `quest_offer_reward` VALUES (25936, 0, 0, 0, 0, 0, 0, 0, 0, 'While you were away, I was able to revive some of the fallen warriors.$B$BWe\'ll need every hand if we are to survive here.', 0);
INSERT INTO `quest_offer_reward` VALUES (25937, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for recovering these artifacts. I will see to it that they are displayed in the Ironforge museum along with the story of those who died to recover them. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25938, 0, 0, 0, 0, 0, 0, 0, 0, 'Hold there, stranger!', 0);
INSERT INTO `quest_offer_reward` VALUES (25939, 0, 0, 0, 0, 0, 0, 0, 0, 'In sparing the land further suffering, you\'ve also helped us soothe the Greenwarden.$B$BMany thanks, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (25940, 0, 0, 0, 0, 0, 0, 0, 0, 'The forest is being devoured!', 0);
INSERT INTO `quest_offer_reward` VALUES (25941, 0, 0, 0, 0, 0, 0, 0, 0, 'I hear you, $R.$B$BI understand the urgency, but these men are in no condition to fight.', 0);
INSERT INTO `quest_offer_reward` VALUES (25942, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us hope that your work has bought us the time we need...', 0);
INSERT INTO `quest_offer_reward` VALUES (25943, 0, 0, 0, 0, 0, 0, 0, 0, 'Good grub.$B$BSoon we\'ll be back to fighting strength.', 0);
INSERT INTO `quest_offer_reward` VALUES (25944, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $R.$B$BOur chances of survival are increasing.', 0);
INSERT INTO `quest_offer_reward` VALUES (25945, 1, 25, 5, 0, 0, 0, 0, 0, 'Пока ты в Когтистых горах и в армии Кром\'гара, будешь $gодет:одета; по форме! Ясно? Ты меня хорошо слышишь?! Увижу тебя без накидки с эмблемой Адского Крика – такого пинка отвешу, что шнурками подавишься.', 20886);
INSERT INTO `quest_offer_reward` VALUES (25946, 0, 0, 0, 0, 0, 0, 0, 0, 'Blood and Thunder!$B$BThis changes everything, $C!', 0);
INSERT INTO `quest_offer_reward` VALUES (25947, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done!$b$bNow, more than ever, we are prepared to face the naga!', 0);
INSERT INTO `quest_offer_reward` VALUES (25948, 0, 0, 0, 0, 0, 0, 0, 0, 'No time to address the Vanguard.$b$bJust as well...', 0);
INSERT INTO `quest_offer_reward` VALUES (25949, 0, 0, 0, 0, 0, 0, 0, 0, 'The Earthen Ring has received my distress message and sent help - just in time it would seem.$b$bHopefully more can be sent soon. We are but few against a great many.', 0);
INSERT INTO `quest_offer_reward` VALUES (25950, 0, 0, 0, 0, 0, 0, 0, 0, 'Chicken and rice, $n! That will show those sorry and sad snakes who\'s the ol\' pitch and toss!$B$BNow it\'s bird lime for that tiddlywink. Finger and thumb or plink plonk?', 0);
INSERT INTO `quest_offer_reward` VALUES (25951, 0, 0, 0, 0, 0, 0, 0, 0, 'I could ask for no more proficient a Battlemaiden than you. You have done yourself a great honor this day.', 0);
INSERT INTO `quest_offer_reward` VALUES (25952, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $c.$B$BI will get some patrols out of our own to make sure that no more naga get within sight of this cave. We should have the area secured within the hour.', 0);
INSERT INTO `quest_offer_reward` VALUES (25953, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n, and just in time too. The Captain just started sending more men out. Strong axe arms, but I doubt they possess quite the same awareness and finesse that you do.$B$BWe were lucky to have you here when things went south. I suspect you will have a large role to play in things to come as well.', 0);
INSERT INTO `quest_offer_reward` VALUES (25954, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you so much for taking this seriously.$B$BThis is some of the first definitive proof of other members of the Vashj family. Very interesting. And I\'d never even heard of either of the other two figures you mentioned until now.$B$BSo much of our history was lost during the sundering - it\'s exciting to have a chance at reclaiming some of it.', 0);
INSERT INTO `quest_offer_reward` VALUES (25955, 0, 0, 0, 0, 0, 0, 0, 0, 'This is good intel. You have a sharp eye, $c.$B$BIt sounds like the building north of the gardens will make a suitable forward post. I will send some men immediately to start defensive preparations and we will reposition there as soon as possible.', 0);
INSERT INTO `quest_offer_reward` VALUES (25956, 0, 0, 0, 0, 0, 0, 0, 0, 'A shard of metal?$B$BThe remains you describe sound much like the sea vrykul we encountered in Northrend. We\'ll not be striking any alliances with their sort, no matter the cause. Still, this information may prove useful in the future.$B$BI will think on it further if time permits. Thank you for bringing it to my attention.', 0);
INSERT INTO `quest_offer_reward` VALUES (25957, 0, 0, 0, 0, 0, 0, 0, 0, 'A Battlemaiden? I never heard of such a thing.$B$BThe way you explain it, it sounds like the naga forces are far greater in scope than the Captain could possibly have realized. We might be able to learn more through the blade if we can find other locations where this Battlemaiden was present, but the information you gave me makes me fear for the captain and his soldiers. We must warn them.', 0);
INSERT INTO `quest_offer_reward` VALUES (25958, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see you again, $c. I almost thought we\'d lost you to the Wavespeaker\'s fool errand.$B$BDid anything come of it?', 0);
INSERT INTO `quest_offer_reward` VALUES (25959, 0, 0, 0, 0, 0, 0, 0, 0, 'More naga are amassing to the south than reported too it seems. We shall have to move quickly then.', 0);
INSERT INTO `quest_offer_reward` VALUES (25960, 0, 0, 0, 0, 0, 0, 0, 0, 'You are perhaps the most dependable $r I\'ve ever met. I wish I could have more men that showed half as much pride as you do.', 0);
INSERT INTO `quest_offer_reward` VALUES (25961, 0, 0, 0, 0, 0, 0, 0, 0, 'Brilliant! I can\'t have expected much more with the tools I gave you.$B$BI should be able to fill in the blanks and get this all written out shortly. There\'s no knowing if we will make it out of this place, but if it comes down to it, I will find a way to float this to the surface in hopes that it will one day be found.$B$BThank you again, $n. You\'re not bad.', 0);
INSERT INTO `quest_offer_reward` VALUES (25962, 0, 0, 0, 0, 0, 0, 0, 0, 'You are brave indeed! I\'ve seen you battling all manner of murloc, naga, and pointy-toothed fish, but that was nothing! The way you just sling those kelp bulbs under your arm like that is a whole new kind of fearless. BOOM! You could blow up at any second.$B$BYou\'re my kind of $r.', 0);
INSERT INTO `quest_offer_reward` VALUES (25963, 0, 0, 0, 0, 0, 0, 0, 0, 'You did great up there, soldier! You devastated their ranks, but there were far more than we accounted for, and most of my men went down out there in the masses of naga.$B$BWe\'ll need to hold this position as long as possible to buy them time. Hopefully some of them will still make it to the rendezvous.', 0);
INSERT INTO `quest_offer_reward` VALUES (25964, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you. Truly.$B$BWe need to get these men moving as soon as possible. I\'ll meet you back at the forward post soon, I\'m sure.', 0);
INSERT INTO `quest_offer_reward` VALUES (25965, 0, 0, 0, 0, 0, 0, 0, 0, 'Still so many? They must want something very important here to come in such numbers.$B$BI will be honest with you, $n, because you deserve at least that: I don\'t know how much hope there is. We will retreat shortly, but we won\'t be able to defend ourselves when they strike back. We don\'t have anywhere to run... we\'re running out of options.$B$BI\'m sorry, I should keep such thoughts to myself. Do me a favor and don\'t mention any of that to the men. I\'ll figure something out.', 0);
INSERT INTO `quest_offer_reward` VALUES (25966, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re finished! Just in time. I don\'t think we could have held them much longer.$B$BAn artifact? Could this be something we can use against the naga? I suppose that would require finding it.$B$BWe may have to attend to more pressing matters first, until an opportunity to pursue this further presents itself.', 0);
INSERT INTO `quest_offer_reward` VALUES (25967, 0, 0, 0, 0, 0, 0, 0, 0, 'You made it! After the attack at the forward post, I thought we might be the only ones left. It\'s good to see you in one piece, $c.', 0);
INSERT INTO `quest_offer_reward` VALUES (25968, 0, 0, 0, 0, 0, 0, 0, 0, 'Look at you, a regular aquatic hero! Trouble comes. You come. Save the day.  You must be proud of yourself!$B$BWe\'ve devised our brilliant plan. Gloriously simple, barring the hostility of local life. Simpler yet with a you available! ', 0);
INSERT INTO `quest_offer_reward` VALUES (25969, 0, 0, 0, 0, 0, 0, 0, 0, 'Less gilblins is good.$B$BI\'m glad that they\'re so primitive. I don\'t think I\'d want to be dealing with a race of aquatic tech-freaks right now.', 0);
INSERT INTO `quest_offer_reward` VALUES (25970, 0, 0, 0, 0, 0, 0, 0, 0, 'Let me see, let me see... flare gun... balloon... no rope. Never any rope. Terribly impractical if you do ask me. Rope should be mandatory.', 0);
INSERT INTO `quest_offer_reward` VALUES (25971, 0, 0, 0, 0, 0, 0, 0, 0, 'This is great! I love it. It should work per... ewww! Is this stuff braided with hair?$B$BDoesn\'t matter. Almost done. This thing will be ready in the twist of a rope.', 0);
INSERT INTO `quest_offer_reward` VALUES (25972, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect! There\'s no way they missed that, fighting or not.', 0);
INSERT INTO `quest_offer_reward` VALUES (25973, 0, 0, 0, 0, 0, 0, 0, 0, 'That is the best news I\'ve ever heard. We\'ll get the survivors moving over there to the surface as soon as possible. Perhaps we can forget that this whole nightmare ever happened.$B$BThank you, $n. You\'ve shown your worth down here. There will be a glorious spot in the Horde for you.$B$BYou should consider meeting us at the ships later, when you finish saying your goodbyes to the naga.', 0);
INSERT INTO `quest_offer_reward` VALUES (25974, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice, $N! That will show those naga who\'s the boss!$B$BNow it\'s time for that drink. You want rum or wine?', 0);
INSERT INTO `quest_offer_reward` VALUES (25975, 0, 0, 0, 0, 0, 0, 0, 0, 'That should be enough for now. We know where there\'s more should the need arise.', 0);
INSERT INTO `quest_offer_reward` VALUES (25976, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s a lot! And we know where there\'s more should the need arise.', 0);
INSERT INTO `quest_offer_reward` VALUES (25977, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice Uncle Bob of pokin\' their general.$B$B<The lieutenant shakes her head and grins.>', 0);
INSERT INTO `quest_offer_reward` VALUES (25978, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done it! The mountaineers are returning to camp and our medics will have their hands full. We are all in your debt, $n. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25979, 0, 0, 0, 0, 0, 0, 0, 0, 'It is never easy to countenance violence against the very elements whose powers I wield, but sometimes there is no other solution. Our duty to the people of Dun Morogh comes first. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25980, 0, 0, 0, 0, 0, 0, 0, 0, 'Stuck him right through, did you? I would have loved to have seen that!', 0);
INSERT INTO `quest_offer_reward` VALUES (25981, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, they\'re ugly, but they mostly look harmless. Smelly though.$B$BHere, let me clean the gilblin stuff out of these. I\'ve got an idea....', 0);
INSERT INTO `quest_offer_reward` VALUES (25982, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, they\'re ugly, but they mostly look harmless. Mostly. They\'re almost cute in a way.$B$BHere, let me clean the gilblin stuff out of these. I\'ve got an idea....', 0);
INSERT INTO `quest_offer_reward` VALUES (25983, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s very disturbing news, but I believe our Earthen Ring friends here have a plan.$B$BAnd now that you\'re here....', 0);
INSERT INTO `quest_offer_reward` VALUES (25984, 0, 0, 0, 0, 0, 0, 0, 0, 'If it weren\'t you saying it, I wouldn\'t believe what that troublesome goblin has to say. This is troubling indeed, but I think our Earthen Ring friends have a few ideas.$B$BAnd now that you\'re here....', 0);
INSERT INTO `quest_offer_reward` VALUES (25985, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, you were sent here by Tiala? A wonderful student, she\'ll do many great things.$B$BAlthough I sense even greater potential from you.$B$BPlease, stay a while in our shrine. There\'s much to do, but you can\'t accomplish anything with a troubled mind.', 0);
INSERT INTO `quest_offer_reward` VALUES (25986, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s a good thing the Senator didn\'t come out here on his own. It\'s dangerous, and we\'re going to need all the help we can get to get the situation under control. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25987, 0, 0, 0, 0, 0, 0, 0, 0, 'What happened? That thing wasn\'t completely dead! Maybe I should stick to mechanical stuff.$B$BIt gave you a vision? Of killing all of us?! That doesn\'t sound good.$B$BWell, in any case, that Earthen Ring fellow, Erunak Stonespeaker, wants these specimens all taken out to him. I\'ll make sure he gets them. I don\'t want them anywhere near me.', 0);
INSERT INTO `quest_offer_reward` VALUES (25988, 0, 0, 0, 0, 0, 0, 0, 0, 'My bad. My name is Fiasco for a reason. I should have known that thing wasn\'t completely dead when it wiggled a little.$B$BYou say it gave you a vision? Of killing all of us?! That doesn\'t sound good.$B$BHrm, well that Earthen Ring fellow, Erunak Stonespeaker, wants these specimens all taken out to him. I\'ll make sure they get out there. It\'s not like I want them stinking up the place around here.', 0);
INSERT INTO `quest_offer_reward` VALUES (25989, 0, 0, 0, 0, 0, 0, 0, 0, '<Duarn looks at the crab and disguises a frown.>$b$bIt doesn\'t look very impressive, does it?', 0);
INSERT INTO `quest_offer_reward` VALUES (25990, 0, 0, 0, 0, 0, 0, 0, 0, 'I had assumed that the naga were only here for those pearls.  No, this makes much more sense.  If they had Nespirah\'s power under their control, the amount of damage the naga could do would be catastrophic!$b$bSomething must be done.', 0);
INSERT INTO `quest_offer_reward` VALUES (25991, 0, 0, 0, 0, 0, 0, 0, 0, '$N... is that you?', 0);
INSERT INTO `quest_offer_reward` VALUES (25992, 0, 0, 0, 0, 0, 0, 0, 0, '<Captain Vilethorn admires the polearms you present him.>$b$bOh, I\'m sorry.  I was just imagining what these will look like with little bits of naga stuck to the ends of them.  Thank you, $N.  We\'ll put these to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (25993, 0, 0, 0, 0, 0, 0, 0, 0, 'Hopefully we made some progress.  I\'m not sure how we\'ll know when the creature wakes up, but something tells me that it will be pretty obvious when it does.', 0);
INSERT INTO `quest_offer_reward` VALUES (25994, 0, 0, 0, 0, 0, 0, 0, 0, 'Brilliant work!$b$b<Tulra tucks the pearls away beneath her armor.>$b$bNow I just need you and Legionnaire Nazgrim to get me safely out of here.  I can handle the studies from there on.', 0);
INSERT INTO `quest_offer_reward` VALUES (25995, 0, 0, 0, 0, 0, 0, 0, 0, 'No need to bring me up to speed... Nespirah has been narrating your adventures as they happened, and Legionnaire Nazgrim filled me in on the rest.$b$b<Duarn closes his eyes and nods.>$b$bNespirah says that she is quite grateful, and that she has a small surprise ready for the naga.', 0);
INSERT INTO `quest_offer_reward` VALUES (25996, 0, 0, 0, 0, 0, 0, 0, 0, 'It appears that I owe you my life, yet again.  Thank Hellscream that you found me before you and your shaman activated those bio-defenses.', 0);
INSERT INTO `quest_offer_reward` VALUES (25997, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew they weren\'t here to see the sights. This is more serious than we thought! This is no mere raid. The Dark Irons intend to cripple the defense of Ironforge itself! ', 0);
INSERT INTO `quest_offer_reward` VALUES (25998, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank goodness you\'re here, $n. The airfield\'s been attacked by the Dark Irons and I don\'t think we can hold out much longer. ', 0);
INSERT INTO `quest_offer_reward` VALUES (25999, 0, 0, 0, 0, 0, 0, 0, 0, '<Saurboz looks you over.>$B$BYou have all of your limbs intact. This is a first.', 0);
INSERT INTO `quest_offer_reward` VALUES (26000, 0, 0, 0, 0, 0, 0, 0, 0, '...$r? Here?$b$bI must be mad...', 0);
INSERT INTO `quest_offer_reward` VALUES (26001, 0, 0, 0, 0, 0, 0, 0, 0, 'Blastgineer Fuzzwhistle is definitely dead. It\'s probably for the best.$B$BIt looks like he\'s holding a note in his hand.', 0);
INSERT INTO `quest_offer_reward` VALUES (26002, 0, 0, 0, 0, 0, 0, 0, 0, 'Damn shame... He was a good goblin.$B$B<Saurboz shakes his fist.>$B$BAs for you, $n, you\'ve done well. These attack plans will help us prepare. I want you to deliver the plans to Overlord Krom\'gar personally! There\'s sure to be a promotion in it for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26003, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, brother! Now they will know that they cannot sneak up on the Horde!', 0);
INSERT INTO `quest_offer_reward` VALUES (26004, 0, 0, 0, 0, 0, 0, 0, 0, '<Overlord Krom\'gar takes the plans from you.>$B$BHmm. Perhaps this grunt is more than he would appear. Perhaps this grunt is in actuality a sergeant.$B$BLook now upon Krom\'gar Sergeant $n, soldiers. He has come to serve warning of a cowardly Alliance attack. An attack that we will CRUSH!', 0);
INSERT INTO `quest_offer_reward` VALUES (26005, 0, 0, 0, 0, 0, 0, 0, 0, 'You made it out! Good to see you, $n.$B$BIt\'s good to be out of the water for a bit, and to have some resources at our disposal again. I\'m sure many of the boys would give anything to turn around and head home after what we\'ve been though, but we still have a grim task ahead.$B$BWhat about you? Want to stick it through to the end with us? I wouldn\'t blame you if you\'ve had your fill.', 0);
INSERT INTO `quest_offer_reward` VALUES (26006, 0, 0, 0, 0, 0, 0, 0, 0, 'Looks like you make it out in one piece, $c!$B$BIt\'s good to be out of that foul pit of water for a moment, and to have some resources at our disposal again. Don\'t get too comfortable though, we still have a grim task ahead.$B$BWhat about you? Had enough honor for one day, or do you want to get back in there and make Hellscream proud?', 0);
INSERT INTO `quest_offer_reward` VALUES (26007, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, it\'s not encouraging, but it\'s something...', 0);
INSERT INTO `quest_offer_reward` VALUES (26008, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us hope for the safety of those released prisoners.$B$BIn return for your noble efforts, please accept this gift.', 0);
INSERT INTO `quest_offer_reward` VALUES (26009, 0, 0, 0, 0, 0, 0, 0, 0, 'Much more blood shall be spilled upon this soil before this war is over. Alliance blood...', 0);
INSERT INTO `quest_offer_reward` VALUES (26010, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar! Alliance pigs!$B$B<Overlord Krom\'gar spits on the ground.>$B$BEnjoy the victory for it shall be short lived. Your bravery has given us more time to prepare, nothing more.', 0);
INSERT INTO `quest_offer_reward` VALUES (26011, 0, 0, 0, 0, 0, 0, 0, 0, 'Did you find any suspicious documents at Paltrow\'s camp? Perhaps on his rotting corpse? Any insight into the Alliance\'s attack patterns would be extremely helpful in the battle for Stonetalon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26012, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for heeding Rhonin\'s call, brave adventurer.$B$BLet me share with you what we know regarding recent events, and any assistance you might offer would be greatly appreciated.', 0);
INSERT INTO `quest_offer_reward` VALUES (26013, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you again, $C, for rescuing me from Baltharus\'s clutches!$B$BThere is still much work to be done, however....', 0);
INSERT INTO `quest_offer_reward` VALUES (26014, 0, 0, 0, 0, 0, 0, 0, 0, 'Of course we haven\'t returned yet! We\'ve had our fair share of setbacks... not the least of which was the passing of my dear brother Oskar.$B$B<Humphrey sniffles loudly.>$B$BHe lost his grip and floated away, the poor lad! He thought he\'d grab some fish to bring back to the captain, but he floated off, and was eaten by one o\' those... those great-eels! Bwaaaah ha ha haaaah!', 0);
INSERT INTO `quest_offer_reward` VALUES (26015, 0, 0, 0, 0, 0, 0, 0, 0, '<Humphrey grabs the phosphora from you and attaches it to an unusual device.>$B$BThere ya go. It\'s beautiful! Oskar would be proud.', 0);
INSERT INTO `quest_offer_reward` VALUES (26016, 0, 0, 0, 0, 0, 0, 0, 0, 'Gnomish flying machines? Our anti-air cannons are still not functional! We will be sitting ducks.$B$BI require time to plan... to think...$B$BThe hot air balloon and its payload cannot remain at Krom\'gar Fortress. I must make arrangements to move the balloon to another location. Our plans to destroy Windshear Hold with it are foiled.', 0);
INSERT INTO `quest_offer_reward` VALUES (26017, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya did good, $n! Ya did real good!', 0);
INSERT INTO `quest_offer_reward` VALUES (26018, 0, 0, 0, 0, 0, 0, 0, 0, '<Humphrey listens as you recount your fights with the Coldlight murlocs.>$B$BHmm... they sound like regular ol\' murlocs... just underwater. That\'ll make Cap\'n Glovaal happy.', 0);
INSERT INTO `quest_offer_reward` VALUES (26019, 0, 0, 0, 0, 0, 0, 0, 0, 'Whaaa... an egg? An EEL egg?$B$B<Even upside-down, you can see the gears in Humphrey\'s head turning.>$B$BWe can definitely do something with this! Get ready kid, \'cause it\'s time for some good ol\'-fashioned Digsong revenge!', 0);
INSERT INTO `quest_offer_reward` VALUES (26020, 0, 0, 0, 0, 0, 0, 0, 0, 'Properly motivated? Wha... what does that mean? Listen, Sergeant, I have a built in mechanism for survival called self-interest. That is, it\'s in my best interest to NOT DIE. With that said, there\'s no fixing these guns without the right materials.', 0);
INSERT INTO `quest_offer_reward` VALUES (26021, 0, 0, 0, 0, 0, 0, 0, 0, 'Oskar\'s alive? Wait... what did ya say? Ya let him FLOAT AWAY AGAIN?!$B$BWell... ya held up your end of the bargain, and I appreciate everything ya\'ve done, $n.$B$BHere... take this. I\'ve only been hanging onto it as ballast, but it doesn\'t seem to be doing much good.', 0);
INSERT INTO `quest_offer_reward` VALUES (26022, 0, 0, 0, 0, 0, 0, 0, 0, 'This is but a prelude to the slaughter we will visit upon these lands. All will know our name.$B$BYou have done well, $n. Victory for Sylvanas!', 0);
INSERT INTO `quest_offer_reward` VALUES (26023, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes... I was once Prince of the fallen kingdom of Stromgarde. I was cut down by the one seeking Trol\'kalar.$B$BI rue my foolish life now. I serve...only Sylvanas.', 0);
INSERT INTO `quest_offer_reward` VALUES (26024, 0, 0, 0, 0, 0, 0, 0, 0, 'You took down Marez Cowl? Well done, $n. Well done indeed!$B$BMarez menaced our Stromgarde defenders, and it is rumored she was an emissary between the Syndicate and some other, darker power. We are glad to see that she\'ll no longer ply her powers against us.', 0);
INSERT INTO `quest_offer_reward` VALUES (26025, 0, 0, 0, 0, 0, 0, 0, 0, 'My restless nights will turn to peaceful slumber when I have destroyed this orb. Thank you, $n. It is a chilling reminder of the terrible power the demons held over the orcs before Hellscream released them from their curse.', 0);
INSERT INTO `quest_offer_reward` VALUES (26026, 0, 0, 0, 0, 0, 0, 0, 0, '<Clarissa claps excitedly.>$B$BMr. D you\'re gonna be ok! Hooray!$B$B<Clarissa expertly places all of the parts you collected into Large Daddy.>$B$BOh, Mr. D! I knew it!', 0);
INSERT INTO `quest_offer_reward` VALUES (26027, 0, 0, 0, 0, 0, 0, 0, 0, 'Cleared out properly? Good. I doubt we\'ll even bother to lurk in Stromgarde. That ramshackle hole needs to be razed into dust.$B$BThe land would make a decent spot for a new Undercity in its place.', 0);
INSERT INTO `quest_offer_reward` VALUES (26028, 0, 0, 0, 0, 0, 0, 0, 0, '<Overlord Krom\'gar coughs.>$B$BI am - perhaps for the first time in my life - at a loss for words.$B$BI am really not sure where Clarissa even came from...', 0);
INSERT INTO `quest_offer_reward` VALUES (26029, 0, 0, 0, 0, 0, 0, 0, 0, 'This is the fate of all who would stand in our way.', 0);
INSERT INTO `quest_offer_reward` VALUES (26030, 0, 0, 0, 0, 0, 0, 0, 0, 'I hated the Syndicate in life as much as I do in death. To see the sigil again is a small comfort.$B$BBut we have much more work to do... for Sylvanas.', 0);
INSERT INTO `quest_offer_reward` VALUES (26031, 0, 0, 0, 0, 0, 0, 0, 0, 'Thoradin was the founder of Arathor. Just as I join these fragments together, he joined the scattered tribes of humans into their first great nation.$B$BBut I\'m sure you\'re not interested in such drivel. Forgive me. Old memories are often hard to bury.', 0);
INSERT INTO `quest_offer_reward` VALUES (26032, 0, 0, 0, 0, 0, 0, 0, 0, 'Valorcall was loyal to me in life. His death will not be something I bear lightly.$B$BBut bear it I must. I am Forsaken. My old existence means nothing.$B$BThe sigils are ours; Trol\'kalar awaits.', 0);
INSERT INTO `quest_offer_reward` VALUES (26033, 0, 0, 0, 0, 0, 0, 0, 0, 'Trol\'kalar, my father\'s blade... it seems like it\'s been an eternity since I saw it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26034, 0, 0, 0, 0, 0, 0, 0, 0, '<Krasus listens intently as you relate your battle with Halion, and then his eyes go wide at the mention of Deathwing.>$B$BWe owe you a debt of gratitude, mortal, for driving the invaders from the Sanctum. But this news of the Destroyer is troubling beyond words. I have much to discuss with the other members of the Accord.$B$BTake these as a token of my thanks, as you gird yourselves for the coming storm.', 0);
INSERT INTO `quest_offer_reward` VALUES (26035, 0, 0, 0, 0, 0, 0, 0, 0, 'Not bad. Since it\'s clear you\'ve got your head on straight, I was hoping you could help us with a more pressing job.', 0);
INSERT INTO `quest_offer_reward` VALUES (26036, 0, 0, 0, 0, 0, 0, 0, 0, 'Brilliant, $n. One item down an\' three more to go, based on my list. The other three items are all in Stromgarde.$B$BTake a rest an\' get your strength back--the next phase of my plan\'ll require a little more time an\' effort. Come back to me after you\'ve gotten a warm meal an\' some rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (26037, 0, 0, 0, 0, 0, 0, 0, 0, 'You amaze me, $n. Well done again.$B$BGive me a moment to cast the proper spell an\' you\'ll be ready.', 0);
INSERT INTO `quest_offer_reward` VALUES (26038, 0, 0, 0, 0, 0, 0, 0, 0, 'Malin couldn\'t\'ve asked a worthier person to come here an\' aid us, $n. You\'ve done a grand job!$B$BHe\'ll be pleased to see what an overwhelming success our mission\'s been.', 0);
INSERT INTO `quest_offer_reward` VALUES (26039, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah! You activated these shards. Already I feel more myself!$B$BBut alas, I am still far from free...', 0);
INSERT INTO `quest_offer_reward` VALUES (26040, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, yes. This is the stuff.$B$BMaybe I can wean off of it and avoid the side-effect.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26041, 0, 0, 0, 0, 0, 0, 0, 0, '<Myzrael speaks through the stone.>$B$BYou have done it! You opened the Stones of Binding! I can feel the shackles about me loosen, and freedom draws ever closer.$B$BYou are a noble $C, $n. My rescuer!', 0);
INSERT INTO `quest_offer_reward` VALUES (26042, 0, 0, 0, 0, 0, 0, 0, 0, 'You place the rod in the keystone. And a shudder is felt beneath you...', 0);
INSERT INTO `quest_offer_reward` VALUES (26043, 0, 0, 0, 0, 0, 0, 0, 0, 'A testament to the might of the Horde!', 0);
INSERT INTO `quest_offer_reward` VALUES (26044, 0, 0, 0, 0, 0, 0, 0, 0, 'Um... Well you see, it\'s quite simple... THIS CAVE IS SWARMING WITH SOULLESS KILLING MACHINES!', 0);
INSERT INTO `quest_offer_reward` VALUES (26045, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ll be damned! You managed to get the parts and not die. How are you only a sergeant?', 0);
INSERT INTO `quest_offer_reward` VALUES (26046, 0, 0, 0, 0, 0, 0, 0, 0, 'Get the parts, kill the elementals and rescue the peons... We get all of that done and we\'re as good as promoted!', 0);
INSERT INTO `quest_offer_reward` VALUES (26047, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew it! You are a hero! If I have anything to do with it, you\'ll be promoted to legionnaire!', 0);
INSERT INTO `quest_offer_reward` VALUES (26048, 0, 0, 0, 0, 0, 0, 0, 0, 'Just in time! The flying machines are starting to mass. Attack is imminent!', 0);
INSERT INTO `quest_offer_reward` VALUES (26049, 0, 0, 0, 0, 0, 0, 0, 0, 'As the eldritch shackles are placed around the Shards, an unhuman wail is heard from far below. The scream is followed by a roar.$B$BThe Shards of Myzrael no longer invoke feelings of warmth and security as they once did. Now they pulse with an open menace... as if the thing beneath them holds ill will toward the surface.', 0);
INSERT INTO `quest_offer_reward` VALUES (26050, 0, 0, 0, 0, 0, 0, 0, 0, 'The goggles work perfectly now! Well done, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26051, 0, 0, 0, 0, 0, 0, 0, 0, 'Most superb, $n! The Captain will be very pleased! And just wait until word gets back to Fleetmaster Seahorn!', 0);
INSERT INTO `quest_offer_reward` VALUES (26052, 0, 0, 0, 0, 0, 0, 0, 0, 'So the little gnomes proved their worth after all? I knew we kept those ankle biters around for a reason!', 0);
INSERT INTO `quest_offer_reward` VALUES (26053, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. The march of the Forsaken continues.', 0);
INSERT INTO `quest_offer_reward` VALUES (26054, 0, 0, 0, 0, 0, 0, 0, 0, 'Sandfury trolls and the Cult of Twilight\'s Hammer, you say?$B$BAnd they meant to send some giant monster into the heart of the city?!$B$BWell done! Stormwind is in your debt.', 0);
INSERT INTO `quest_offer_reward` VALUES (26055, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work out there, $n.$B$BThose scum deserved what they got. While nothin\' will ever bring me ship or me crew back, the death of those naga gives me some solace.', 0);
INSERT INTO `quest_offer_reward` VALUES (26056, 0, 0, 0, 0, 0, 0, 0, 0, 'I sure am glad you\'re here!', 0);
INSERT INTO `quest_offer_reward` VALUES (26057, 0, 0, 0, 0, 0, 0, 0, 0, 'I sure am glad you\'re here!', 0);
INSERT INTO `quest_offer_reward` VALUES (26058, 0, 0, 0, 0, 0, 0, 0, 0, 'I have never seen such an act of bravery. You saved the base, sergeant - or should I say legionnaire! Lok\'tar!', 0);
INSERT INTO `quest_offer_reward` VALUES (26059, 0, 0, 0, 0, 0, 0, 0, 0, 'I see that Krom\'gar has sent his best!$B$BGood, we have much work to do. The filth that is the Alliance have entered into a treaty with the Grimtotem. Now we fend off attacks from both forces.$B$BWe are spread thin across the Greatwood Vale and will soon fall beneath the concerted effort of our enemies. There is only one hope: VOODOO!', 0);
INSERT INTO `quest_offer_reward` VALUES (26060, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Once I have everything I need I will mix it all together and unleash the voodoo! Woe unto our enemies!', 0);
INSERT INTO `quest_offer_reward` VALUES (26061, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Once I have everything I need I will mix it all together and unleash the voodoo! Woe unto our enemies!', 0);
INSERT INTO `quest_offer_reward` VALUES (26062, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally! Now I will create the charm that will hold the voodoo enchantment!', 0);
INSERT INTO `quest_offer_reward` VALUES (26063, 0, 0, 0, 0, 0, 0, 0, 0, 'Darn still not very happy, but Darn will reward you for what you have done.', 0);
INSERT INTO `quest_offer_reward` VALUES (26064, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. These eggs should get us through the week.', 0);
INSERT INTO `quest_offer_reward` VALUES (26065, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $N.$B$BThese nightmarish creatures that rise from below give me pause. I fear that our efforts will be for nothing.', 0);
INSERT INTO `quest_offer_reward` VALUES (26066, 0, 0, 0, 0, 0, 0, 0, 0, 'In times of crisis, a desperate act may be all that is needed to secure victory. Sending a swarm of kobolds at the enemy may be the edge that we need to win.', 0);
INSERT INTO `quest_offer_reward` VALUES (26067, 0, 0, 0, 0, 0, 0, 0, 0, 'Victory, at last! Though it stands to reason that without a stable supply line this victory will be short lived. Enjoy it while it lasts, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26068, 0, 0, 0, 0, 0, 0, 0, 0, '<Devo laughs.>$B$BFascinating tale, friend. It would seem that our newfound kobold allies might be somewhat useful after all!', 0);
INSERT INTO `quest_offer_reward` VALUES (26069, 0, 0, 0, 0, 0, 0, 0, 0, 'The situation is grim, $R. We need all of the help that we can get!', 0);
INSERT INTO `quest_offer_reward` VALUES (26070, 0, 0, 0, 0, 0, 0, 0, 0, 'Good enough for me.$B$BDon\'t get too complacent, $n. The hard part\'s about to come.', 0);
INSERT INTO `quest_offer_reward` VALUES (26071, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done.$B$BDon\'t get too full of yourself, $N. The hard part\'s about to come.', 0);
INSERT INTO `quest_offer_reward` VALUES (26072, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, $r, can you feel them seething with a desire to break free and destroy us?$B$BThis is good! They will be the perfect lure for their leader.', 0);
INSERT INTO `quest_offer_reward` VALUES (26073, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar ogar! You are the embodiment of what it means to be a Horde soldier, $n. If only we had more like you, this war would be over and Kalimdor would belong to the Horde - UNCONTESTED!$B$BRise now as a champion of Krom\'gar!', 0);
INSERT INTO `quest_offer_reward` VALUES (26074, 0, 0, 0, 0, 0, 0, 0, 0, 'Krom\'gar has sent his champion! Well you haven\'t a thing to worry about, Champion. We\'ll get the balloon all fueled up in no time. How about you lend a hand around here while you wait?', 0);
INSERT INTO `quest_offer_reward` VALUES (26075, 0, 0, 0, 0, 0, 0, 0, 0, 'I sure hope you didn\'t miss any. I don\'t think I can take much more of this!', 0);
INSERT INTO `quest_offer_reward` VALUES (26076, 0, 0, 0, 0, 0, 0, 0, 0, 'And they called me crazy. I knew this place was crawling with spies. Poop too.', 0);
INSERT INTO `quest_offer_reward` VALUES (26077, 0, 0, 0, 0, 0, 0, 0, 0, 'Chieftain Cliffwalker and his tauren seek to commit treason against the Horde! They claim that the ancient tree at the heart of Battlescar Valley is merely a school for druids. They are blind to the fact that desperate measures are taken during war time. My scouts have assured me that the night elves hide a weapon of great power within that tree. We\'re going to end this now!', 0);
INSERT INTO `quest_offer_reward` VALUES (26078, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! My men are working hard on getting our flying machines and steam tanks back into the fight. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26079, 0, 0, 0, 0, 0, 0, 0, 0, 'Falconcrest and Otto? Wonderful!$B$BHere is your reward, $n. I am happy to pay it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26080, 0, 0, 0, 0, 0, 0, 0, 0, 'I see. Yes, I am rememberink now... Humphrey and Oskar vere not the strongest svimmers.$B$BThe reconnaissance is helpful, and the extra fish look very tasty. If you are seeink Mister Digsong before he returns, please send him my thanks.', 0);
INSERT INTO `quest_offer_reward` VALUES (26081, 0, 0, 0, 0, 0, 0, 0, 0, 'The news of your victory satisfies me, $C... a rare sensation. The Dark Lady herself has mused that our conquest of Lordaeron may be the only way the Forsaken can find true satisfaction, and pehaps even relief.$B$BBut it matters not. Our victory is inevitable. For your loyal service to the Forsaken, I award you in the name of Sylvanas.', 0);
INSERT INTO `quest_offer_reward` VALUES (26082, 0, 0, 0, 0, 0, 0, 0, 0, 'You possess quite a talent for destruction, $n. Whatever Alliance remain after your rampage across Battlescar Valley will be dealt with by the bomb.', 0);
INSERT INTO `quest_offer_reward` VALUES (26083, 0, 0, 0, 0, 0, 0, 0, 0, 'You friends with Goutgut! Him weird ogre. Smell funny.', 0);
INSERT INTO `quest_offer_reward` VALUES (26084, 0, 0, 0, 0, 0, 0, 0, 0, 'Throm\'ka, $C. If your deeds for the undead are as great as you claim, I am honored to help you serve the Horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (26085, 0, 0, 0, 0, 0, 0, 0, 0, 'The men are finding hidden reserves of strength and courage to deal with the Dark Iron onslaught. The airfield will not fall on our watch! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26086, 0, 0, 0, 0, 0, 0, 0, 0, 'Gah!  These fish are impossible to hit!$b$b<Orako looks at you.>$b$bMy wolf got eaten by a fish, and I\'m not going back until I find her.', 0);
INSERT INTO `quest_offer_reward` VALUES (26087, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey, look at how glowy it is!$b$b<Orako looks wistfully off into the distance.>$b$bMy wolf loved things that were glowy...', 0);
INSERT INTO `quest_offer_reward` VALUES (26088, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmm... you got a lot of fish, but none of these are the ones that ate my wolf.  I think it was probably a bigger fish.', 0);
INSERT INTO `quest_offer_reward` VALUES (26089, 0, 0, 0, 0, 0, 0, 0, 0, 'I know, murlocs aren\'t fish.  I thought that killing them would make me feel better, but I still feel sad about my wolf.$b$b<Orako sighs.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26090, 0, 0, 0, 0, 0, 0, 0, 0, 'That is a big egg.$b$b<Orako sniffs it.>$b$bIt is big, and it smells like fish.  Maybe the big fish that made this egg is the big fish that ate my wolf!', 0);
INSERT INTO `quest_offer_reward` VALUES (26091, 0, 0, 0, 0, 0, 0, 0, 0, '<Orako is busy playing with Wolf.>$b$bOh, thank you, $N!  I knew Wolf was alive!$b$bHere, take one of these.  I don\'t need them anymore.', 0);
INSERT INTO `quest_offer_reward` VALUES (26092, 0, 0, 0, 0, 0, 0, 0, 0, 'This is his report?!  That idiot...$b$bI suppose we can use the food.  Thanks, $N.  You\'ve done as well as can be expected.', 0);
INSERT INTO `quest_offer_reward` VALUES (26093, 0, 0, 0, 0, 0, 0, 0, 0, 'Very good, $n. The last thing we need with the undead looming is a gang of common thieves distracting us.', 0);
INSERT INTO `quest_offer_reward` VALUES (26094, 0, 0, 0, 0, 0, 0, 0, 0, 'The momentum is starting to shift, $n, largely due to your efforts. Now, it is time to strike at the Dark Iron leadership. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26095, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. We will inform the families of these fallen soldiers that they were killed in battle.$B$BIt will be grim news to bear, but it is better than the uncertainty of a missing loved one.', 0);
INSERT INTO `quest_offer_reward` VALUES (26096, 0, 0, 0, 0, 0, 0, 0, 0, 'I will use the mushrooms as a reagent to treat the corpses. We will have one ready for you soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26097, 0, 0, 0, 0, 0, 0, 0, 0, 'Orthus is dead. He is clutching something in his hand.', 0);
INSERT INTO `quest_offer_reward` VALUES (26098, 0, 0, 0, 0, 0, 0, 0, 0, '<High Chieftain Cliffwalker takes the insignia from you.>$B$B<High Chieftain Cliffwalker cries out in anger.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26099, 0, 0, 0, 0, 0, 0, 0, 0, 'You saw with your own eyes, $n. General Grebo attacked us!$B$B$B$BNow what will we do? Surely the Warchief will have us all executed for treason.', 0);
INSERT INTO `quest_offer_reward` VALUES (26100, 0, 0, 0, 0, 0, 0, 0, 0, 'WHAT? My General has been killed by the tauren? This is treason! HIGH TREASON! They will pay for this grievous error.', 0);
INSERT INTO `quest_offer_reward` VALUES (26101, 0, 0, 0, 0, 0, 0, 0, 0, 'The traitorous tauren will now stand by as we destroy his precious grove and burn his village to the ground!', 0);
INSERT INTO `quest_offer_reward` VALUES (26102, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'ve done it! Without your help, Ironforge Airfield would surely be occupied by the enemy and the Dark Irons would have a clear road to Ironforge itself. The Council of Three Hammers must be informed of what happened here. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26103, 0, 0, 0, 0, 0, 0, 0, 0, '<Engineer Hexascrub sticks a finger into each sample and sniffs it.>$B$BYes, I think this will do.', 0);
INSERT INTO `quest_offer_reward` VALUES (26105, 0, 0, 0, 0, 0, 0, 0, 0, 'You did well, $n. We need to be especially wary of Horde attacks right now. The Pincer X2 has plenty of firepower at sea, but in the back of this cave, she is exceptionally vulnerable.', 0);
INSERT INTO `quest_offer_reward` VALUES (26106, 0, 0, 0, 0, 0, 0, 0, 0, 'Have you considered a career in science, $n? If you ever get tired of this $C nonsense, come see me! I could use an assistant with your devotion to experimentation and the scientific method.', 0);
INSERT INTO `quest_offer_reward` VALUES (26107, 0, 0, 0, 0, 0, 0, 0, 0, 'The lethargy that overcame me even after the camps fell was so strong, I refused this gift when it was first given to me.$B$BNow, I am proud to do my part in the name of the Horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (26108, 0, 0, 0, 0, 0, 0, 0, 0, 'As Tor\'gan taught me, I transfer the power and the spirit of the raptors into this orb. It will imbue our warriors with the fiery strength and intelligence of the raptor.$B$BThe guile of the raptor is yours now as well, $C. Take it with pride, for the Horde!', 0);
INSERT INTO `quest_offer_reward` VALUES (26110, 0, 0, 0, 0, 0, 0, 0, 0, 'Apothecaries yet again. You think they\'d get the message. Let\'s see what it is this time...', 0);
INSERT INTO `quest_offer_reward` VALUES (26111, 0, 0, 0, 0, 0, 0, 0, 0, 'And now we will see if we can get you into L\'ghorek to speak with it before it dies.', 0);
INSERT INTO `quest_offer_reward` VALUES (26112, 0, 0, 0, 0, 0, 0, 0, 0, 'And just what is this? This is impossible! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26113, 0, 0, 0, 0, 0, 0, 0, 0, 'Refuge Pointe sent you? Good to know they\'re still there.$B$BWe\'ve got some interesting things to check into here, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (26114, 0, 0, 0, 0, 0, 0, 0, 0, 'The plague took our friends and family, but it was the Forsaken that took Phin. They\'re just as bad as the Scourge. I\'ll destroy every one of them with my own hands if I have to.$B$BWhen you\'re prepared, come let me know. We\'ll get started right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (26115, 0, 0, 0, 0, 0, 0, 0, 0, 'Now you truly know what it means to be a member of the Horde, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26116, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done a great service for us, $n. Thank you.$B$BI hope Kin wasn\'t too much of a handful down there. She\'s very friendly, but can be just as deadly when you\'re her enemy.', 0);
INSERT INTO `quest_offer_reward` VALUES (26117, 0, 0, 0, 0, 0, 0, 0, 0, 'I admit I\'m impressed, $C. It\'s a small comfort amidst all that\'s happened here.$B$BYou have fought the undead, struck back against the Horde, and honored the memory of the victims of Southshore. On behalf of the kingdom of Stromgarde, I present you with an official commendation, and this reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (26118, 0, 0, 0, 0, 0, 0, 0, 0, 'The ambassador will trouble us no more. He is not the only Dark Iron $R whose heart still belongs to Twilight\'s Hammer. We must remember that and remain vigilant against their schemes, else my son will inherit only ruins and ashes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26120, 0, 0, 0, 0, 0, 0, 0, 0, 'The orcs will know better than to press further west now. Well done.', 0);
INSERT INTO `quest_offer_reward` VALUES (26121, 0, 0, 0, 0, 0, 0, 0, 0, 'You did well, $n. We need to be especially wary of Horde attacks right now. The Pincer X2 has plenty of firepower at sea, but in the back of this cave, she is exceptionally vulnerable.', 0);
INSERT INTO `quest_offer_reward` VALUES (26122, 0, 0, 0, 0, 0, 0, 0, 0, '<Fiasco sticks a finger into each sample and sniffs it.>$b$bYes, I think this will do.', 0);
INSERT INTO `quest_offer_reward` VALUES (26124, 0, 0, 0, 0, 0, 0, 0, 0, 'You did well, $n. We need to be especially wary of Alliance attacks right now. The Verne\'s got plenty of firepower out at sea, but in the back of this cave, she\'s far too vulnerable.', 0);
INSERT INTO `quest_offer_reward` VALUES (26125, 0, 0, 0, 0, 0, 0, 0, 0, 'You did well, $n. We need to be especially wary of Alliance attacks right now. The Verne\'s got plenty of firepower out at sea, but in the back of this cave, she\'s far too vulnerable.', 0);
INSERT INTO `quest_offer_reward` VALUES (26126, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s it.  Starting today, I\'m shifting the Verne over to bio-fuel.  I\'ll send some men up to harvest more oil.$b$bThink of all the sea creatures that we\'ve saved today, $N!', 0);
INSERT INTO `quest_offer_reward` VALUES (26127, 0, 0, 0, 0, 0, 0, 0, 0, 'Good show, lad, but we\'ve no time to celebrate. Thandol Span\'s in danger!', 0);
INSERT INTO `quest_offer_reward` VALUES (26128, 0, 0, 0, 0, 0, 0, 0, 0, 'And good riddance!$B$BThandol Span\'s safe thanks to you, $n, and hopefully Calamoth is down for good this time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26129, 0, 0, 0, 0, 0, 0, 0, 0, 'An arsenal of rebels? In my town?$B$BWhat in God\'s name was Lord Crowley up to?', 0);
INSERT INTO `quest_offer_reward` VALUES (26130, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that you have unplugged L\'ghorek, we must attempt to make contact with it. I can sense that the ancient yet lives, but only barely.', 0);
INSERT INTO `quest_offer_reward` VALUES (26131, 0, 0, 0, 0, 0, 0, 0, 0, 'You don\'t know how glad I am to see fresh faces from Ironforge. Perhaps sending Mountaineer Barleybrew wasn\'t a waste after all. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26132, 0, 0, 0, 0, 0, 0, 0, 0, 'The last thing we needed was a bunch of summoned creatures swarming our forces during the final assault.', 0);
INSERT INTO `quest_offer_reward` VALUES (26133, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s one less thing we need to think about during the final assault.', 0);
INSERT INTO `quest_offer_reward` VALUES (26134, 0, 0, 0, 0, 0, 0, 0, 0, '<Furien groans.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26135, 0, 0, 0, 0, 0, 0, 0, 0, 'Minions of the Old God? Two very potent foes have joined forces then. Their hope to defeat the Tidehunter may not be so foolish after all.$B$B$n, we can\'t let that happen. If Azshara defeats the Tidehunter and takes his power for herself, I\'m not sure any number of us could ever hope to oppose her on the seas.$B$BThe first step is clear at least - we need to hunt down the fiend that destroyed our ships. The navy was confident enough to bring in a new fleet, perhaps they have a plan.', 0);
INSERT INTO `quest_offer_reward` VALUES (26137, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, well met, $C.$B$BStormpike sent you? About the keg? Hoo boy...', 0);
INSERT INTO `quest_offer_reward` VALUES (26139, 0, 0, 0, 0, 0, 0, 0, 0, 'I suppose I owe Thargas a favor for sending such a bold hero my way.$B$BOr perhaps he just wanted to keep all the clean-up for himself. Regardless, you\'ll find much to do here for the Alliance.', 0);
INSERT INTO `quest_offer_reward` VALUES (26140, 0, 0, 0, 0, 0, 0, 0, 0, 'WISE OF... YOUR ERUNAK TO... HAVE... YOU USE... THE FACELESS\' BRAIN. I SEE... YOU NOW.$B$BI WILL DIE. THERE IS NO... STOPPING... THE INEVITABLE.$B$BBUT... YOU WILL HELP... ME DEAL WITH... THESE PARASITES FIRST.', 0);
INSERT INTO `quest_offer_reward` VALUES (26141, 0, 0, 0, 0, 0, 0, 0, 0, 'WE WILL USE... THESE... RUNESTONES TO ALLOW... YOU TO POSSESS... ONE OF... THE WATER ELEMENTALS.', 0);
INSERT INTO `quest_offer_reward` VALUES (26142, 0, 0, 0, 0, 0, 0, 0, 0, 'THEY SHALL ASCEND... NO MORE!', 0);
INSERT INTO `quest_offer_reward` VALUES (26143, 0, 0, 0, 0, 0, 0, 0, 0, 'YOU HAVE DONE... MYSELF AND... THE TIDEHUNTER GREAT... SERVICE.', 0);
INSERT INTO `quest_offer_reward` VALUES (26145, 0, 0, 0, 0, 0, 0, 0, 0, 'I am quite impressed, $n. You have done the Dwarven race a great favor by defending our lands. King Magni Bronzebeard himself would be proud!', 0);
INSERT INTO `quest_offer_reward` VALUES (26146, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n! You demonstrated uncanny courage on the battlefield. Our war against the Troggs will be won with efforts such as yours. You have served King Magni well.', 0);
INSERT INTO `quest_offer_reward` VALUES (26147, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew the second I laid eyes on you, $n, that you would make Ironforge proud. You have served our people well. I salute you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26148, 0, 0, 0, 0, 0, 0, 0, 0, 'Most excellent, $n! You have brought hope to Loch Modan. With Grawmug dead, we stand a better chance of seeing the Troggs eliminated from the land.', 0);
INSERT INTO `quest_offer_reward` VALUES (26149, 0, 0, 0, 0, 0, 0, 0, 0, 'A job well done. Now you don\'t need to worry about what would have happened to them if you hadn\'t been around.$B$BThe last one was even appreciative enough to give you a reward they\'d kept hidden on their person.', 0);
INSERT INTO `quest_offer_reward` VALUES (26150, 0, 0, 0, 0, 0, 0, 0, 0, 'William sent you? Bless his soul. I... I\'m ok... It\'s just... Well... ', 0);
INSERT INTO `quest_offer_reward` VALUES (26152, 0, 0, 0, 0, 0, 0, 0, 0, 'And justice is served! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26153, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice job, $n! Penny will be very excited for breakfast tomorrow!', 0);
INSERT INTO `quest_offer_reward` VALUES (26154, 0, 0, 0, 0, 0, 0, 0, 0, 'THEIR DEVOTIONS ARE... SILENCED.$B$BNOW THAT... YOU HAVE... THEIR POWER... YOU MUST DESTROY... THEIR LEADER.', 0);
INSERT INTO `quest_offer_reward` VALUES (26157, 1, 1, 0, 0, 0, 0, 0, 0, 'Hah!  You Nethergarde nincompoops are always two steps behind me, and I\'m nowhere near as fast as I used to be.$b$bI don\'t need Mahar\'s note to know what\'s going on.  Why do you think I\'m cooped up in the back of this cave?  What I do need, however, is a strong $r like you.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26158, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent!  You\'ve managed to complete the simplest of tasks without dying.  Now that that\'s out of the way...', 19116);
INSERT INTO `quest_offer_reward` VALUES (26159, 4, 0, 0, 0, 0, 0, 0, 0, '<Kasim eyes the blood hungrily.>$b$bMore than enough, and still warm!  Now, let\'s get this ritual started, shall we?', 19116);
INSERT INTO `quest_offer_reward` VALUES (26160, 1, 0, 0, 0, 0, 0, 0, 0, 'The blood ritual was a success; I can clearly see where we are going next.$b$bI\'m sorry, I misspoke.  What I meant to say is, I can see where YOU are going next...', 19116);
INSERT INTO `quest_offer_reward` VALUES (26161, 1, 0, 0, 0, 0, 0, 0, 0, 'I see... yes, I can work with this.$b$b<Kasim looks at the night elf\'s head.>$b$bLoramus Thalipedes, demon hunter.  I look forward to our reunion.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26162, 53, 0, 0, 0, 0, 0, 0, 0, 'You... you fool!  What have you done?', 19116);
INSERT INTO `quest_offer_reward` VALUES (26163, 5, 1, 273, 0, 0, 0, 0, 0, 'No, no, no... not Kasim again!  This is madness!$b$bPlease, remain quiet.  If my commander learns that I still work for the short one, do you know how quickly I will be removed from the watch?$b$b<Grimeo takes the rune from you, presses it to his chest, closes his eyes, and sighs.>$b$bI see you speak the truth.  Let us begin our business quickly, so that it may end even quicker.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26164, 1, 0, 0, 0, 0, 0, 0, 0, 'I hope this is what the gnome wanted.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26165, 0, 0, 0, 0, 0, 0, 0, 0, 'There is no doubt about it, you have found demon blood.$b$bKasim\'s requests aside, I am sure that my Watch Commander is thankful for your efforts.  They have been struggling to deal with these demon distractions for some time now.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26166, 396, 396, 0, 0, 0, 0, 0, 0, 'This is sufficient.  If you see Grimeo again, you may tell him that he performed satisfactorily, but no... his debt is not yet cleared.  I will get to work on fashioning the stone knife of sealing.  There is no need for questions now.  All will be revealed in time.$b$bWhile I work, I have another task for you to complete.$b$bDon\'t look so upset!  This task will be fun... it involves collecting body parts.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26167, 1, 0, 0, 0, 0, 0, 0, 0, '<Kasim examines the amulet.>$b$bHe was an archmage, I can feel it.  I will hold the amulet for you while you collect the rest.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26168, 1, 0, 0, 0, 0, 0, 0, 0, '<Kasim hefts the amulet.>$b$bA warlock... and a powerful one at that.  I will hold onto this until the time is right.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26169, 1, 0, 0, 0, 0, 0, 0, 0, '<Kasim clutches the third amulet.>$b$bHe was a powerful warrior, this one.  I will keep the amulet until we are ready to proceed.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26170, 397, 396, 0, 0, 0, 0, 0, 0, 'It would seem that we are prepared to face our demon, $n.  We have the amulet\'s blessing, we have the stone knife, and I alone know the demon\'s true name: Rakh\'likh.  Together, this may be enough to defeat the Defiler.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26171, 1, 1, 0, 0, 0, 0, 0, 0, 'Loramus, resurrected!?  And then he trapped the demon inside his own body... by the Light...$b$bI see.  So this was Kasim\'s plan all along.  I suspected correctly, but I suppose I refused to believe it.$b$bAh, one last thing!  Kasim left one more instruction for me within his rune.  I did not understand it at first, but given the events that have taken place today, it now makes much more sense:$b$bHe asked me to let you choose from one of these.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26172, 21, 0, 0, 0, 0, 0, 0, 0, 'These are perfect, $n.  I will eat well for the next several days.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26173, 1, 0, 0, 0, 0, 0, 0, 0, 'Now you\'re doing great. $B$B<Commander moves closer and whispers:>$B$B$R... in case you\'re wondering, I have another small but responsible assignment...', 19342);
INSERT INTO `quest_offer_reward` VALUES (26174, 1, 1, 0, 0, 0, 0, 0, 0, 'Тебя точно никто не видел? Вот и славно.$B$B<Пустомар слушает, как вы пересказываете ему все, что узнали из документов.>$B$BГм… все вполне безобидно. То ли они там в Орде все идиоты и действительно ничего против нас не замышляют… то ли, наоборот, хитрые бестии и скрывают всю важную информацию гораздо лучше, чем я думал. Как бы там ни было, ты молодец.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26175, 2, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Surwich, |3-6($r). Feel at home.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26176, 0, 0, 0, 0, 0, 0, 0, 0, 'Glad to hear you\'ve been clearing out the troggs. Let\'s have a look at this report, and...$B$B...Is that a beer stain?', 0);
INSERT INTO `quest_offer_reward` VALUES (26177, 0, 0, 0, 0, 0, 0, 0, 0, 'These crabs look great! I will clean these up and make sure that the King hears about your contribution. Thanks!', 0);
INSERT INTO `quest_offer_reward` VALUES (26179, 0, 0, 0, 0, 0, 0, 0, 0, 'I don\'t think there could be too much misinterpreting or ignoring of that signal, hm? The Venture Co. will know not to take the $r lightly, or to be so arrogant as to believe we would allow them to steal the natural resources around us without protest. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26180, 0, 0, 0, 0, 0, 0, 0, 0, 'My anger is somewhat lessened at the news that the villain Fizsprocket is dead. I will go through his personal effects and see if there is any additional information about what the Venture Co. has planned for the future. Thank you for your efforts, $n. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26181, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello again, $C.$B$BI felt L\'ghorek die. A shame that such a magnificent being is gone.$B$BIf we do not assist Neptulon, we will all meet the same fate.', 0);
INSERT INTO `quest_offer_reward` VALUES (26182, 0, 0, 0, 0, 0, 0, 0, 0, 'Hello again, $c.$B$BI felt L\'ghorek die. A shame that such a magnificent being is gone.$B$BIf we do not assist Neptulon, we will all meet the same fate.', 0);
INSERT INTO `quest_offer_reward` VALUES (26183, 0, 0, 0, 0, 0, 0, 0, 0, 'These will do nicely $n. I will get to work right away on the cider.', 0);
INSERT INTO `quest_offer_reward` VALUES (26184, 1, 0, 0, 0, 0, 0, 0, 0, 'You say demons worshiped a tree? <Mayor\'s sighing hard. >> That\'s what I was afraid of...', 19342);
INSERT INTO `quest_offer_reward` VALUES (26185, 396, 0, 0, 0, 0, 0, 0, 0, 'Хотя меня удивляет, что Конниспот все же решился осуществить свои планы, но лично я этому рада. Я знаю, что демоны сплотились вокруг Малдраза, и разделяю его опасения.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26186, 396, 0, 0, 0, 0, 0, 0, 0, '<Major Charlton Connispot is watching with fear the tank you\'ve collected. >$b$bVery good, $n. Now we have everything we need to interrupt Marl\'s trance and put an end to both him and his experiment.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26187, 396, 396, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Весь Сурвич у тебя в долгу.$B$BВыбери что-нибудь себе в награду. Мы не очень богаты, но тех, кто нас защищает, мы все-таки можем отблагодарить.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26188, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, thank you, $n. Using the items you brought me, I will be able to create the remedy I need to stop the spread of the infection and, eventually, cure it completely. I owe you my life in thanks. But I\'ll never forget that screeching call when Mazzranache came upon me, the blaze of color as its beak descended... Be wary on your travels. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26189, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see you back in one piece, $n! You\'re quite a piece of work.$B$BI\'ll be tellin\' Captain Stoutfist how you took care of those orcs!', 0);
INSERT INTO `quest_offer_reward` VALUES (26190, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. I will get to work scaling these for the King right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (26191, 0, 0, 0, 0, 0, 0, 0, 0, 'With every day, more warriors arrive at Azshara\'s command. Soon, Neptulon the Tidehunter will hear of our preparations, but too late to stop what we have set in motion. Our numbers will be too great and our allies too powerful. Victory is only a matter of time.$B$BIn Azshara\'s name.', 0);
INSERT INTO `quest_offer_reward` VALUES (26192, 0, 0, 0, 0, 0, 0, 0, 0, 'These sacks look like just enough, $n. Can\'t wait to see the smiles on the orphans faces.', 0);
INSERT INTO `quest_offer_reward` VALUES (26193, 0, 0, 0, 0, 0, 0, 0, 0, 'NO!$B$BThis is NOT over!', 0);
INSERT INTO `quest_offer_reward` VALUES (26194, 0, 0, 0, 0, 0, 0, 0, 0, 'They may think they\'ve won, but my axe and I have something to say about that!', 0);
INSERT INTO `quest_offer_reward` VALUES (26195, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the Greenwarden\'s grove, $C. I am glad Whelgar has told you of us.$B$BIf you are able to lend us your assistance, I will be most grateful.', 0);
INSERT INTO `quest_offer_reward` VALUES (26196, 0, 0, 0, 0, 0, 0, 0, 0, 'Supplies from Ferilon? Bless his heart.$B$BWe\'re good on food, but we\'re runnin\' out of bullets!', 0);
INSERT INTO `quest_offer_reward` VALUES (26197, 0, 0, 0, 0, 0, 0, 0, 0, '$n, isn\'t it? It\'s a pleasure to finally meet you. While you were getting cleaned up, Nevin\'s team told me about your escape from Gnomeregan.', 0);
INSERT INTO `quest_offer_reward` VALUES (26198, 0, 0, 0, 0, 0, 0, 0, 0, 'You picked that one up quickly. I can\'t wait to show you some of my other favorite tricks.', 0);
INSERT INTO `quest_offer_reward` VALUES (26199, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. I\'m sure your time in Gnomeregan afforded you ample opportunity to practice your arts. Things are somewhat safer here, but your talents will never go to waste.', 0);
INSERT INTO `quest_offer_reward` VALUES (26200, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, $n. You have a natural talent for our arts.', 0);
INSERT INTO `quest_offer_reward` VALUES (26201, 0, 0, 0, 0, 0, 0, 0, 0, 'As you continue to gain power, return to me and I will teach you new spells and techniques.', 0);
INSERT INTO `quest_offer_reward` VALUES (26202, 0, 0, 0, 0, 0, 0, 0, 0, 'So, another one of Nevin\'s \"rescued\" survivors, eh? More than likely, you saved his life from the troggs down there. Did he stumble over his words when he mentioned me? The man has a healthy respect for the demonic powers we channel.', 0);
INSERT INTO `quest_offer_reward` VALUES (26203, 0, 0, 0, 0, 0, 0, 0, 0, 'For a $r who has spent so much time inside Gnomeregan, you look positively fit and healthy, $n. That\'s good. Before we can complete what Operation: Gnomeregan began, we\'re going to need more warriors like you. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26204, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work! Keep practicing your charge and you\'ll have an instant advantage at the start of battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (26205, 0, 0, 0, 0, 0, 0, 0, 0, 'Wow, I never expected it to work on the first trial!$B$BUh, I mean, we\'ve succeeded! I wonder what other uses I we can find for this handy little guy.', 0);
INSERT INTO `quest_offer_reward` VALUES (26206, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. Nevin tells me that you managed to survive the dangers in Gnomeregan by using your wits. That\'s exactly what we need up here. Clever minds and quick blades.', 0);
INSERT INTO `quest_offer_reward` VALUES (26207, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m not surprised to see you\'re such a quick study. I look forward to showing you more of our tricks of the trade.', 0);
INSERT INTO `quest_offer_reward` VALUES (26208, 0, 0, 0, 0, 0, 0, 0, 0, 'Even now, only the S.A.F.E. teams dare to venture far into the city to search for survivors. Meanwhile, Thermaplugg has sent one of his followers, Crushcog, to distract us and buy time for him to dig in.', 0);
INSERT INTO `quest_offer_reward` VALUES (26209, 0, 0, 0, 0, 0, 0, 0, 0, 'Gnolls and murlocs? Horse poopy, $g pal:lady;! Gnolls and murlocs didn\'t kill these people. I\'ve seen what gnolls and murlocs do to people that they kill and this... isn\'t it. Too pretty. Too... perfect.$B$BFurthermore, the Furlbrow\'s had been squatting on this farm for 5 years. No, whoever wiped them out had a reason. This is murder, plain and simple and we\'re gonna get to the bottom of it...', 0);
INSERT INTO `quest_offer_reward` VALUES (26210, 0, 0, 0, 0, 0, 0, 0, 0, 'MON! This is fantastic! With my best lure and the snapjaw population down, the fish will pile up.', 0);
INSERT INTO `quest_offer_reward` VALUES (26211, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, mon! I can\'t thank you enough! Hopefully this here fishing pole will be reward enough for the job.', 0);
INSERT INTO `quest_offer_reward` VALUES (26212, 0, 0, 0, 0, 0, 0, 0, 0, '<Lard takes the huge haunch of meat from you and stuffs it in his face.>\n\nDis hit da spot! Tanks little one.\n\nLard make you special picnic basket for help Lard.', 0);
INSERT INTO `quest_offer_reward` VALUES (26213, 0, 0, 0, 0, 0, 0, 0, 0, 'Is this supposed to be some kind of joke, because I\'m not laughing. Bits and pieces of red cloth? What the hell is this supposed to mean?', 0);
INSERT INTO `quest_offer_reward` VALUES (26214, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice find, rookie. I think I have a stack of these on my desk back at the station. We\'re basically back to square one. What do we know? Someone that likes to write fiction about the past sent the Furlbrows a letter.$B$BLooks like we got ourselves a real history... mystery.', 0);
INSERT INTO `quest_offer_reward` VALUES (26215, 0, 0, 0, 0, 0, 0, 0, 0, 'Look, we ain\'t got no room for no more...$B$BHoratio sent you? Right then... um, how can I help you?$B$BHe\'s not here is he? Heh, he knows I wasn\'t hidin\' from him, right? Was just tryin\' to lay low and all that, ya know?', 0);
INSERT INTO `quest_offer_reward` VALUES (26219, 0, 0, 0, 0, 0, 0, 0, 0, 'Vell done, Private $n.$B$BThe cavern is ours, but the beast escaped. Should it raise it\'s ugly head again, ve vill make with blastink it out of the vater.', 0);
INSERT INTO `quest_offer_reward` VALUES (26220, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, $N.  These will do nicely.', 0);
INSERT INTO `quest_offer_reward` VALUES (26221, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $c.$B$BThat slippery beast got away! I\'ll be making a few upgrades. It won\'t be so lucky next time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26222, 0, 0, 0, 0, 0, 0, 0, 0, 'These are fantastic! Let\'s see what we can put together. Would you be interested in testing out the prototype once I\'m done?', 0);
INSERT INTO `quest_offer_reward` VALUES (26223, 0, 0, 0, 0, 0, 0, 0, 0, 'A job well done, $r. The Revantusk thank you for your contribution.', 0);
INSERT INTO `quest_offer_reward` VALUES (26224, 0, 0, 0, 0, 0, 0, 0, 0, 'You have proven a worthy ally to the Revantusk, $N. Well done.', 0);
INSERT INTO `quest_offer_reward` VALUES (26225, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect! I don\'t know where I\'d get this stuff if it wasn\'t for drunk humans repeatedly crashing their ships.\n\nNormally I\'d settle down and get ready to haggle with you for an hour, but you\'re in luck; I\'m too eager to crack these open. Cheers!', 0);
INSERT INTO `quest_offer_reward` VALUES (26226, 0, 0, 0, 0, 0, 0, 0, 0, 'Ugh, I never liked cooking these slimy things.  Thanks for the help $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26227, 0, 0, 0, 0, 0, 0, 0, 0, 'Let me see your hands... Aha!  You\'re not any better at collecting these wretched things than I am!', 0);
INSERT INTO `quest_offer_reward` VALUES (26228, 0, 0, 0, 0, 0, 0, 0, 0, '<Lou puts his hands to his ears and starts talking loudly.>$B$BSTOP! STOP! STOP! I don\'t wanna hear it! I don\'t wanna know and I don\'t care! That kind of information is liable to get you killed \'round these parts.$B$BI got one more bit of information for you and then we\'re done!', 0);
INSERT INTO `quest_offer_reward` VALUES (26229, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh SWEET justice! Killin\' those kobolds won\'t make me walk like a normal person and it sure won\'t get me a job, but damn does it feel good.$B$B<Jimb reaches into a crusty old sack.>$B$BHere, I think I got somethin\' for you from my kobold huntin\' days. Maybe you can put it to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (26230, 396, 0, 0, 0, 0, 0, 0, 0, 'У меня от запаха принесенной тобой снеди аж в животе заурчало! Сегодня пожрем как короли!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26232, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re dealing with an organization here, rookie. You don\'t just off the richest bum in Westfall in broad daylight and leave no witnesses. Someone with a lot of power is behind these murders. What have you learned so far?', 0);
INSERT INTO `quest_offer_reward` VALUES (26233, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you are a skilled thief as well, $N.  Don\'t worry, we will return the rice once we are done... or not.', 0);
INSERT INTO `quest_offer_reward` VALUES (26234, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you are a skilled thief as well, $n. Don\'t worry, we will return the rice once we are done... or not.', 0);
INSERT INTO `quest_offer_reward` VALUES (26235, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work $N!  I will see that these rations are returned to the troops immediately.', 0);
INSERT INTO `quest_offer_reward` VALUES (26236, 0, 0, 0, 0, 0, 0, 0, 0, 'Horatio Laine? That man is a scumbag. Dirty as my underwear.$B$BListen, friend, I don\'t often give out advice like this, but you seem like a good $g man:woman;. GET OUT OF WESTFALL. You\'re already in way over your head. All you\'re doing now is putting your neck on the chopping block.', 0);
INSERT INTO `quest_offer_reward` VALUES (26237, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $g brother:sister;. You\'ve done Salma and I a great service and I promise that it won\'t soon be forgotten. I don\'t have much money, but you\'re welcome to what I do have.', 0);
INSERT INTO `quest_offer_reward` VALUES (26238, 0, 0, 0, 0, 0, 0, 0, 0, 'That will show the Wildhammer to stay away from our lands!\n\nCrazy dwarves should\'ve stayed in the mountains...', 0);
INSERT INTO `quest_offer_reward` VALUES (26240, 0, 0, 0, 0, 0, 0, 0, 0, 'Makes all the difference, doesn\'t it? That\'ll teach those dwarves a lesson.', 0);
INSERT INTO `quest_offer_reward` VALUES (26241, 0, 0, 0, 0, 0, 0, 0, 0, 'Bless your heart, dear. These poor orphans haven\'t had a hot meal in days.$B$BIt looks like we will have enough left over to help feed many more of the homeless of Westfall.', 0);
INSERT INTO `quest_offer_reward` VALUES (26244, 0, 0, 0, 0, 0, 0, 0, 0, 'You did what was necessary, $n.  I can tell you\'re the sort of $c I\'ll get along with well.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26245, 0, 0, 0, 0, 0, 0, 0, 0, 'The clues point to an obvious conclusion.  Too obvious perhaps.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26246, 0, 0, 0, 0, 0, 0, 0, 0, 'Why would the Alliance attack the Horde just as they\'re about to deliver a fragment of the World Pillar to us?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26247, 0, 0, 0, 0, 0, 0, 0, 0, 'Oi, $n!  I was told I\'d have company soon.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26248, 0, 0, 0, 0, 0, 0, 0, 0, 'We were bound to run into the Twilight\'s Hammer sooner or later, $n.  If they\'re behind this, it\'s quite likely that the World Pillar fragment is in their possession.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26249, 0, 0, 0, 0, 0, 0, 0, 0, 'This ship is filled with rats...', 19342);
INSERT INTO `quest_offer_reward` VALUES (26250, 0, 0, 0, 0, 0, 0, 0, 0, 'Oi, this was a heavy one!  He nearly broke me gryphon\'s back!$B$BAre you ready to interrogate the ogre, $n?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26251, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n.  The Twilight\'s Hammer is an evil that we must fight anywhere we find them.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26252, 0, 0, 0, 0, 0, 0, 0, 0, 'Is that... Is that the heart of one of the watchers? I\'ve heard stories about using one of these to power up a watcher for personal use. Of course I\'m really in no shape to try such things myself.', 0);
INSERT INTO `quest_offer_reward` VALUES (26254, 0, 0, 0, 0, 0, 0, 0, 0, 'We have our answers, $n.  Let\'s report back.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26255, 0, 0, 0, 0, 0, 0, 0, 0, 'We must investigate your findings further, but we must not lose track of the fragment.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26256, 0, 0, 0, 0, 0, 0, 0, 0, 'They\'re keeping slaves here?  We ought to free them regardless, but perhaps they might also aid us in finding the forgemaster.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26257, 0, 0, 0, 0, 0, 0, 0, 0, 'It actually worked? That\'s incredible, $N. Hopefully that takes care of the harvest golem problem here in Westfall.', 0);
INSERT INTO `quest_offer_reward` VALUES (26258, 0, 0, 0, 0, 0, 0, 0, 0, 'This must be where the world pillar fragment was brought.  Let\'s have a closer look.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26259, 0, 0, 0, 0, 0, 0, 0, 0, 'There is still great power present in his blood, $n.  I can sense it even now.$B$BYet there is also much corruption.  I am not sure we can salvage the Earthwarder\'s essence from this.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26260, 0, 0, 0, 0, 0, 0, 0, 0, 'There is an entry for an upcoming meeting at the Upper Silvermarsh.$B$BThis must be where the Forgemaster is.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26261, 0, 0, 0, 0, 0, 0, 0, 0, 'Let us find this journal.  We\'re getting close to the World Pillar fragment, I can sense it.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26263, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, mon. A fine hero of the Horde you are.', 0);
INSERT INTO `quest_offer_reward` VALUES (26264, 0, 0, 0, 0, 0, 0, 0, 0, 'I can\'t wait to give this new technology a try! We\'ve lost more gnomes to radiation than we can count. It\'s time to turn that around!', 0);
INSERT INTO `quest_offer_reward` VALUES (26265, 0, 0, 0, 0, 0, 0, 0, 0, 'What a relief! Thank you for your help. I hope that gadget Engineer Grindspark was working on helps get the airfield cleaned up permanently.', 0);
INSERT INTO `quest_offer_reward` VALUES (26266, 0, 0, 0, 0, 0, 0, 0, 0, 'I smell Westfall stew! Mother sent you!?', 0);
INSERT INTO `quest_offer_reward` VALUES (26267, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to see you back... and not missing any limbs, either.', 0);
INSERT INTO `quest_offer_reward` VALUES (26268, 0, 0, 0, 0, 0, 0, 0, 0, 'They\'re still intact! There\'s a lot of memories in these crate. Let\'s see what we\'ve got in the first one...\n\nHey, banana bread! And it\'s still fresh!\n\nHere, mon, I\'ll take this and you can pick something else.', 0);
INSERT INTO `quest_offer_reward` VALUES (26269, 21, 0, 0, 0, 0, 0, 0, 0, 'Ты меня очень здорово $gвыручил:выручила;, $n! Не знаю, как тебя и благодарить. Вот увидишь, скоро книга господина Эрнестуэя появится в каждой библиотеке по всему миру!$B$BЕсли когда-нибудь захочешь почитать ее целиком, всегда найдешь тут у меня экземпляр. Спасибо еще раз, $n!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26270, 0, 0, 0, 0, 0, 0, 0, 0, 'Of course, dear. Our little girl works at Sentinel Hill and is very close with the homeless of Westfall. Perhaps she has overheard something. Before you go, let me wrap up the Westfall Stew and tell you a little bit about our dear little girl.', 0);
INSERT INTO `quest_offer_reward` VALUES (26271, 0, 0, 0, 0, 0, 0, 0, 0, 'You have a kind heart, $N. You have done a good thing, today, and that act will reflect back upon you when judgment day comes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26272, 0, 0, 0, 0, 0, 0, 0, 0, 'Anotha newblood, eh? Ya actually have an intense look about ya though. Fiery eyes. You might have the makings of a real $c. Lets not waste anymore time \'den. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26273, 0, 0, 0, 0, 0, 0, 0, 0, 'Not bad, mon. Ya have a deep power in ya spells - you may have a talent for dis. Some rough edges ta be sure, but we\'ll get dose ironed out here and I\'ll teach ya a few new tings as we go. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26274, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, mon! Dis stuff come easy to ya. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26275, 0, 0, 0, 0, 0, 0, 0, 0, 'Dese are some fine pelts, boy. We could make some hefty cloaks outta dese. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26276, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya handle yerself quite well. Ya gonna be quite powerful some day. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26277, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya tha new recruit everyone\'s talkin\' about, eh? Welcome ta tha Darkspear. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26278, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep up the good work, $C. Hellscream\'s eyes are upon you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26279, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you are good for more than simple egg-smashing duty. Good. The Horde can use a capable $R like you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26280, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. Hopefully the raids will stop for now.$B$BI have one more task for you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26281, 0, 0, 0, 0, 0, 0, 0, 0, '<Aggro\'gosh salutes you.>$B$BThe Horde thanks you, $n. Grom\'gol is protected, if only for today.', 0);
INSERT INTO `quest_offer_reward` VALUES (26283, 0, 0, 0, 0, 0, 0, 0, 0, 'Fascinating... How large did you say it was? I wonder if others could be found intact...\n\nThis sample is excellent. I\'ll be sharing it with my fellow researchers.', 0);
INSERT INTO `quest_offer_reward` VALUES (26284, 0, 0, 0, 0, 0, 0, 0, 0, 'You have me own thanks and th\' thanks of th\' men you rescued. I\'ve already had th\' boys start setting up the gear down inside the cave. It should be ready to go soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26285, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, now, that\'s more like it. We may get around to blowin\' up somethin\' after all.', 0);
INSERT INTO `quest_offer_reward` VALUES (26286, 0, 0, 0, 0, 0, 0, 0, 0, 'Helix? I\'ve heard that name before. Recently as a matter of fact, but who is this admiral?', 0);
INSERT INTO `quest_offer_reward` VALUES (26287, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N. You have provided us a temporary reprieve from the onslaught of gnolls, but there is more work to be done.', 0);
INSERT INTO `quest_offer_reward` VALUES (26288, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $N. It will take the Riverpaw weeks to recover from the blow you just dealt. Please take an item of your choosing as a reward for what you have done.', 0);
INSERT INTO `quest_offer_reward` VALUES (26289, 0, 0, 0, 0, 0, 0, 0, 0, 'Helix is the one responsible for the gnoll attacks?$B$B<Kearnen nods at the tower.>$B$BHe\'s inside. He\'s been there for days. I\'ve just been waiting for an excuse to bust him.', 0);
INSERT INTO `quest_offer_reward` VALUES (26290, 0, 0, 0, 0, 0, 0, 0, 0, 'What could possibly be going on in Moonbrook? I don\'t like this one bit, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26291, 0, 0, 0, 0, 0, 0, 0, 0, 'Who IS this Shadowy Figure? We have to get to Moonbrook.', 0);
INSERT INTO `quest_offer_reward` VALUES (26292, 0, 0, 0, 0, 0, 0, 0, 0, 'The natives are restless, $N. I don\'t know what\'s going on, but homeless activity in this area has quadrupled in the past two days. If I didn\'t know any better I\'d say they were planning something...', 0);
INSERT INTO `quest_offer_reward` VALUES (26293, 1, 6, 0, 0, 0, 0, 0, 0, 'Busy now. Go away.$B$BWait, what? Hellscream sent you? What - uh - what can I do for you? Him? Us?', 15595);
INSERT INTO `quest_offer_reward` VALUES (26294, 11, 1, 0, 0, 0, 0, 0, 0, '<Garrosh laughs.>$B$BInstilled a little discipline in them, did you? Good, good!$B$BEveryone pulls their weight in my Horde, from the smallest peon to the proudest warrior. I tolerate nothing less.$B$BOur preparations for the invasion are almost complete.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26295, 0, 0, 0, 0, 0, 0, 0, 0, 'V? The only V I know is VanCleef, but he died many years ago.', 0);
INSERT INTO `quest_offer_reward` VALUES (26296, 0, 0, 0, 0, 0, 0, 0, 0, 'I haven\'t seen a red bandana in five years. Could this shadowy figure be attempting to reform the Defias Brotherhood?', 0);
INSERT INTO `quest_offer_reward` VALUES (26297, 0, 0, 0, 0, 0, 0, 0, 0, 'I could hear the cheers from here. It seems as if we\'ve got a huge problem on our hands, $N.  We have to discover the identity of this shadowy menace before we enlist the aid of Stormwind. Perhaps there is another way...', 0);
INSERT INTO `quest_offer_reward` VALUES (26298, 0, 0, 0, 0, 0, 0, 0, 0, 'These are good tusks, $n. You prove that you have the strength to hunt the Bloodscalps.$B$BAnd that is good, because you are not done hunting them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26299, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. I will inspect these heads to ensure poor Yenniku was spared the headhunter\'s axe.', 0);
INSERT INTO `quest_offer_reward` VALUES (26300, 0, 0, 0, 0, 0, 0, 0, 0, 'You place the head of Nezzliok within the cauldron...', 0);
INSERT INTO `quest_offer_reward` VALUES (26301, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes. These will serve me well. When my spirit is freed, I will enter the Nether with some fine servants indeed!$B$BYou are a credit to your $R clan, and you have appeased me.', 0);
INSERT INTO `quest_offer_reward` VALUES (26302, 0, 0, 0, 0, 0, 0, 0, 0, 'Zanzil the Outcast??$B$BIf Yenniku is held by that troll, a vile and powerful witchdoctor, then I fear that only a faint hope remains. Faint. And tiny as the smallest insect.$B$BFortunately, I have some suspicions of my own. Suspicions which may lead to Yenniku\'s salvation.', 0);
INSERT INTO `quest_offer_reward` VALUES (26303, 0, 0, 0, 0, 0, 0, 0, 0, 'You retrieved the eye! Your might is the stuff of legends, $n!$B$BAnd in getting the Eye, you must have defeated Mai\'Zoth and sent him to the Nether. That is why the spirits would not mention him before.$B$BIt is good that he is gone from the world of flesh and bone, but now that he is in the realm of spirits...I fear he will begin a new reign.$B$BBut that is a worry for another time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26304, 0, 0, 0, 0, 0, 0, 0, 0, 'My mother\'s spirit rests well tonight, $n.$B$BAs will I.', 0);
INSERT INTO `quest_offer_reward` VALUES (26305, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done it! You freed Yenniku from the will of Zanzil and the tyranny of the Skullsplitter, and completed the mission I had thought was so hopeless. Zanzil still looms a threat in the Cape of Stranglethorn, but for now, I at least know that the soul of my chief\'s son is free.$B$BOur tribe will remember your name in songs, $n, and we will etch your tale in stone so that it will live forever.', 0);
INSERT INTO `quest_offer_reward` VALUES (26306, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N! Let the Vilebranch flee in terror from the sight of you!', 0);
INSERT INTO `quest_offer_reward` VALUES (26307, 0, 0, 0, 0, 0, 0, 0, 0, 'It won\'t be long until we\'re the only trolls left breathing on these walls.', 0);
INSERT INTO `quest_offer_reward` VALUES (26308, 0, 0, 0, 0, 0, 0, 0, 0, 'There are none left to oppose the Revantusk! Any stragglers will be quickly taken care of.\n\nYou\'ve done my people a great honor, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26309, 0, 0, 0, 0, 0, 0, 0, 0, 'First the spiders and now this. I can smell the venom in this blood... it\'s the same as the skitterlings, but stronger.\n\nThe Vilebranch and spiders... there\'s some potential here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26310, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh yes... I was wise to ask you to get these. How fascinating.\n\nCome here, my pretties. Into the bag you go.', 0);
INSERT INTO `quest_offer_reward` VALUES (26311, 6, 1, 5, 0, 0, 0, 0, 0, '<Sauranok seems uninterested in the map as you relate your story.>$B$BTwilight Assassins followed you to the bar? Here, in Orgrimmar?$B$BThe Warchief won\'t be happy about this.$B$BContinue your preparations for the invasion and I will investigate.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26312, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $c. Many Earthen Ring owe dere lives to ya dis day.', 0);
INSERT INTO `quest_offer_reward` VALUES (26313, 0, 0, 0, 0, 0, 0, 0, 0, 'Dese are in surprisingly good condition - good work, mon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26314, 0, 0, 0, 0, 0, 0, 0, 0, 'Dat will slow their assault a bit while Norsala be findin\' a way to calm\'m. We\'ve no desire ta be remainin\' enemies of Therazane the Stonemother.', 0);
INSERT INTO `quest_offer_reward` VALUES (26315, 0, 0, 0, 0, 0, 0, 0, 0, 'Yeah, mon... dat\'s about the reaction we were expectin\'.$B$BHe mentioned Diamant though? If da two don\'t get along, perhaps this Diamant be a bit less brash den Boden. At least it\'s somethin\'.$B$BLet me see if I can get da earth to tell me where we might be findin\' dis Diamant.', 0);
INSERT INTO `quest_offer_reward` VALUES (26316, 0, 0, 0, 0, 0, 0, 0, 0, 'It won\'t be tough to collapse th\' tunnel, but I\'m goin\' to need my team an\' my equipment \'afore I can get to th\' task.', 0);
INSERT INTO `quest_offer_reward` VALUES (26317, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, the little one seems to have taken a liking to you. I suppose that one raptor alone and raised among orcs is much less deadly than a whole pack of them. Keep her, $n... she may come in handy.$B$BIn fact...', 0);
INSERT INTO `quest_offer_reward` VALUES (26318, 0, 0, 0, 0, 0, 0, 0, 0, 'I could feel th\' explosion way up here! My boys did a fine job riggin\' up that blast, but it wouldn\'t have happened without your help. It\'s only fair that I split my contract payment with you for helpin\' me finish th\' job.', 0);
INSERT INTO `quest_offer_reward` VALUES (26319, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmm, yes, perhaps I could be of some assistance. When I was in Northrend I helped the people of Valgarde uncover some of the mysteries of the vrykul by delving into the spirit world. Perhaps the same could be done here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26320, 0, 0, 0, 0, 0, 0, 0, 0, 'WHAT? VanCleef had a daughter?', 0);
INSERT INTO `quest_offer_reward` VALUES (26321, 0, 0, 0, 0, 0, 0, 0, 0, 'She looks bigger already! You can see, these raptors grow quickly. You are lucky to have found such a strong companion.$B$BKeep her by your side as long as you can, $n. I\'m sure she will prove to be an asset in the future.', 0);
INSERT INTO `quest_offer_reward` VALUES (26322, 5, 1, 0, 0, 0, 0, 0, 0, 'Я немедленно пошлю в Западный Край батальон солдат! Если Братство Справедливости возродилось, то нашему королевству вновь угрожает опасность!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26323, 0, 0, 0, 0, 0, 0, 0, 0, 'What do we have here? A young raptor, and her pet $R. Hah!$B$BMore importantly, what is that skull you have there? This was no ordinary troll. He was big...$B$B$B$B... and strong, too. Let\'s see what we can do with this one.$B$BDon\'t go anywhere.', 0);
INSERT INTO `quest_offer_reward` VALUES (26324, 1, 5, 0, 0, 0, 0, 0, 0, 'What, another message from Hellscream? His patience is thinner than calf\'s hide.$B$BHave you ever worked with goblins before? Everywhere is another hidden cost! Our ships lie half-assembled and the air fleet is scattered to the winds.$B$BIf this invasion is going to happen, I\'m going to need your help.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26325, 0, 0, 0, 0, 0, 0, 0, 0, '<You drop the fetishes into the pot.>$B$BYes... yesss...$B$BMy spirit is strong again, $R, and I am ready to return to the world. Now I need a body.', 0);
INSERT INTO `quest_offer_reward` VALUES (26326, 1, 0, 0, 0, 0, 0, 0, 0, 'Hey, mon! Thank ya for comin\'.$B$BWe weren\'t ready for dis kind of attack. We been expectin\' da Twilight Cult, but not elementals... we\'ve got no idea what upset dem so much that dey would turn on us like dis.$B$BYou haven\'t been out dere killin\' them, have you?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26327, 0, 0, 0, 0, 0, 0, 0, 0, 'Well met! If you\'ve got a weapon on you, I\'ll take any help I can get clearing out these crazed fools!', 0);
INSERT INTO `quest_offer_reward` VALUES (26328, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah... you must be the $r that I was expecting.$B$BYou have come on behalf of the Earthen Ring then?', 0);
INSERT INTO `quest_offer_reward` VALUES (26329, 0, 0, 0, 0, 0, 0, 0, 0, '<The high tinker reads Jessup\'s report.>$B$BSplendid news! With the troggs taken care of, we should be able to turn our attention to Crushcog\'s troublemaking.', 0);
INSERT INTO `quest_offer_reward` VALUES (26330, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $R. Only one more step remains before I may return.', 0);
INSERT INTO `quest_offer_reward` VALUES (26331, 0, 0, 0, 0, 0, 0, 0, 0, 'That should set back Crushcog\'s plans a bit, but we can\'t rest until he is defeated and Chill Breeze Valley is secure.', 0);
INSERT INTO `quest_offer_reward` VALUES (26332, 0, 0, 0, 0, 0, 0, 0, 0, '<You throw the mojo into the pot.>$B$BExcellent work, $R. You and your raptor have done a great thing. Hakkar would be most pleased...', 0);
INSERT INTO `quest_offer_reward` VALUES (26333, 0, 0, 0, 0, 0, 0, 0, 0, 'Mekkatorque will be pleased to hear that you\'ve ruined Crushcog\'s plans to use our old weapons for his followers. Without the mechano-tanks under his command, he\'ll be weak and vulnerable.', 0);
INSERT INTO `quest_offer_reward` VALUES (26334, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad to see you\'ve come to your senses.$B$BCome and visit us in Zul\'Gurub sometime, $R. I\'ll be watching...', 0);
INSERT INTO `quest_offer_reward` VALUES (26335, 0, 0, 0, 0, 0, 0, 0, 0, '<Here, in excruciating detail, are the blueprints for the Foebreaker.>$B$B<You twist the documents every which way, trying to make sense of the incomprehensible scribbles, notes, and corrections.>', 15595);
INSERT INTO `quest_offer_reward` VALUES (26336, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahoy, $C. Any friend of Skuerto is a friend of mine.$B$BYou\'ll find plenty of work needs doin\' around here. We\'re not sittin\' in this cove twiddlin\' our thumbs by choice.', 0);
INSERT INTO `quest_offer_reward` VALUES (26337, 5, 1, 273, 0, 0, 0, 0, 0, 'So they were trying to skimp on production costs? Unacceptable!$B$BI\'m glad you knocked some sense into them. If that foreman makes good on his word, our fleet will be ship-shape in time for the invasion.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26338, 0, 0, 0, 0, 0, 0, 0, 0, 'Balance is restored - for now. We may call on you again in the future, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26339, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, yes, Kelsey told me to expect you. There\'s still much to do before we can take on Crushcog.', 0);
INSERT INTO `quest_offer_reward` VALUES (26340, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmm? Barnil\'s the name. And you are...?', 0);
INSERT INTO `quest_offer_reward` VALUES (26341, 0, 0, 0, 0, 0, 0, 0, 0, '<The shards pulse softly, and a whisper slowly echoes through your head.>$B$BHelp...', 0);
INSERT INTO `quest_offer_reward` VALUES (26342, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve given us just the opening we\'ll need to get the drop on Crushcog. Excellent work, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26343, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, normal skins. In general, their appearance is not so important - they will still go to the lining. Nonetheless, quality comes first! Quality and convenience!$B$BIf you turn out to be harsh, soften with tiger blood. Well, you understand ... every professional has little professional tricks!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26344, 0, 0, 0, 0, 0, 0, 0, 0, 'Мои сапоги... они как копченая колбаса, $n. С виду приятна, прекрасна на вкус. Ты покупаешь мою колбасу, я тебя уверяю, что она приготовлена из свежайшего мяса, ты ее ешь с удовольствием, ты доволен и счастлив.$B$BИ тебе лучше не знать, из чего она сделана на самом деле. Так оно безопаснее и спокойней.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26345, 0, 0, 0, 0, 0, 0, 0, 0, 'Great! Thanks to your help I\'ll have this order filled and sent off to Director Riddlevox on the next boat to Undermine. Ahead of schedule, even!$B$BFor all your hard work, here, I made some extras, so take a pair. It\'s on Drizzlik.$B$BAnd remember, there\'s no boot so fine as one of Drizzlik\'s Excelsior line!', 0);
INSERT INTO `quest_offer_reward` VALUES (26346, 0, 0, 0, 0, 0, 0, 0, 0, 'Say no more, $n. I followed up with an... \"old friend\" while you were gone. I should\'ve known better than to send you to those shards.$B$BWe\'ve both made a grave mistake in involving ourselves in this. Luckily, I \"persuaded\" my boy Gerrig to tell me what to do if this happened.', 0);
INSERT INTO `quest_offer_reward` VALUES (26347, 0, 0, 0, 0, 0, 0, 0, 0, 'Praise you, brave $C.  The rocks of the Westfall Coast shall be lit thanks to your hard work.  Many lives will be spared so long as the torch is kept lit.$b$bI died needlessly on this very shore.  My afterlife plight is to see that no others follow my destiny.', 0);
INSERT INTO `quest_offer_reward` VALUES (26348, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N.  You have quite a knack for combat.  Thanks to you the Coast of Westfall is a safer place.', 0);
INSERT INTO `quest_offer_reward` VALUES (26349, 0, 0, 0, 0, 0, 0, 0, 0, 'So the foul heathen, Murk-Eye is dead.  Well done, $N.  By your hands one life was laid to rest but perhaps many more were saved.  The Great Sea, wrought with danger as it is, shall be a wee bit safer this night thanks to your heroics.', 0);
INSERT INTO `quest_offer_reward` VALUES (26350, 0, 0, 0, 0, 0, 0, 0, 0, 'Mandokir is alive, yes.$B$BYou are responsible, yes.$B$BYou may be able to stop him still, yes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26351, 0, 0, 0, 0, 0, 0, 0, 0, 'You bring feathers, yes.$B$BYou can follow directions, yes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26352, 0, 0, 0, 0, 0, 0, 0, 0, 'A fuel regulator? This seems important. At least, I hope it is.$B$BWe may not have ended the Venture Company\'s operations, but we at least slowed them down. Thank you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26353, 0, 0, 0, 0, 0, 0, 0, 0, 'The footlocker slowly creaks open.  Sand and water seem to be its only contents. But wait!  A small crab scurries out with a clue to the treasure in his claws!', 0);
INSERT INTO `quest_offer_reward` VALUES (26354, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work, treasure hunter!', 0);
INSERT INTO `quest_offer_reward` VALUES (26355, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re on your way to the jackpot, treasure seeker!', 0);
INSERT INTO `quest_offer_reward` VALUES (26356, 0, 0, 0, 0, 0, 0, 0, 0, 'The hinges on the old chest are rusty but they still work.  You force the chest open and take the booty.$b$bCongratulations!', 0);
INSERT INTO `quest_offer_reward` VALUES (26357, 0, 0, 0, 0, 0, 0, 0, 0, 'Such a pretty site ta see the enemy\'s hopes burnin\'...', 0);
INSERT INTO `quest_offer_reward` VALUES (26358, 5, 15, 0, 0, 0, 0, 0, 0, '<Fleep wipes her face, plastering an enormous grease smear across her cheek.>$B$BWhat? The Captain said WHAT?$B$B<She shakes her pipe wrench threateningly.>$B$BIt\'s HIS fault this big beast of an engine is delayed.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26359, 0, 0, 0, 0, 0, 0, 0, 0, 'What did Priestess tell you?$B$BThe raptor is daughter of Ohgan, heir to Mandokir\'s seat.$B$BHah!', 0);
INSERT INTO `quest_offer_reward` VALUES (26361, 4, 0, 0, 0, 0, 0, 0, 0, 'Excellent! I should have this here engine samophlanginated within the hour.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26363, 0, 0, 0, 0, 0, 0, 0, 0, 'Dead? All of them, around the altar? And you found these spider idols on them...\n\nA mass sacrifice. They threw their lives to Shadra.', 0);
INSERT INTO `quest_offer_reward` VALUES (26364, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re finally free of Razlo Crushcog and his interference! With the defeat of Crushcog and his forces, Thermaplugg can\'t afford to send any more of his followers to the surface. I can\'t wait for the day when we defeat him for good!', 0);
INSERT INTO `quest_offer_reward` VALUES (26365, 0, 0, 0, 0, 0, 0, 0, 0, 'They say the problems in the West End are above the roof. <Parker doesn\'t even turn to you. > But we have enough trouble here too. You see those gnolls over there? Looks like they\'re up to something again, and I don\'t like it. I don\'t like it at all.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26366, 0, 0, 0, 0, 0, 0, 0, 0, 'The look of fear and confusion on his face... this is what he deserved! Let\'s make sure all the Vilebranch wear the same look.', 0);
INSERT INTO `quest_offer_reward` VALUES (26367, 0, 0, 0, 0, 0, 0, 0, 0, 'I wish I could have been there to see it, $N! I\'ll have to hear the story from Ongo\'longo himself when this is over.', 0);
INSERT INTO `quest_offer_reward` VALUES (26368, 0, 0, 0, 0, 0, 0, 0, 0, 'Mmm, yes... that old nostalgic smell.\n\nYou\'ve done well for the Forsaken, and the Horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (26369, 0, 0, 0, 0, 0, 0, 0, 0, 'The parts scattered, the keepers separated... I see.\n\nThis is a ritual to summon Shadra. They must not have gotten far. It should be destroyed...', 0);
INSERT INTO `quest_offer_reward` VALUES (26370, 1, 1, 0, 0, 0, 0, 0, 0, 'Пять лет трудов пошли коту под хвост в пять минут. Проклятое Братство!$B$BИ это еще только цветочки, $n! Нас ожидает долгая и трудная работа', 23877);
INSERT INTO `quest_offer_reward` VALUES (26371, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye... I used to be flesh and blood, just like you, $g lad:missy;. That is, until that fateful day when the murlocs attacked the lighthouse...', 0);
INSERT INTO `quest_offer_reward` VALUES (26372, 5, 0, 0, 0, 0, 0, 0, 0, 'My airship has engines? At last!$B$BNow we can rip the heavens a new one!$B$BOnce the ground and naval forces are ready, we\'re going to light up the skies over the Twilight Highlands.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26373, 0, 0, 0, 0, 0, 0, 0, 0, 'If Gnomeregan Covert Operations is recommending you, then you must have made quite an impression. Your help will be most welcome here, as we\'ve a wealth of enemies and few enough mountaineers to face them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26374, 273, 1, 66, 0, 0, 0, 0, 0, 'Thank you, $N! Most of my soldiers have already reported for duty, some a little worse for the wear.$B$BOne night in Bilgewater makes a hard orc humble.$B$BStill, fresh fighting in the Twilight Highlands should restore discipline.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26375, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a generous $r. You give back to the earth that offers you so much. You have my respect. You shall surely have the respect of the Stone Lords with time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26376, 0, 0, 0, 0, 0, 0, 0, 0, 'I have observed you, $r.$B$BYou do not fear shedding Twilight blood.$B$BIn this, I am willing to extend you some degree of trust.$B$BIt is a start.$B$BAll things in time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26377, 0, 0, 0, 0, 0, 0, 0, 0, 'Time brings respect to all.$B$BThe smallest pebble.$B$BThe largest mountain.$B$BThe most stubborn cultist.', 0);
INSERT INTO `quest_offer_reward` VALUES (26378, 1, 0, 0, 0, 0, 0, 0, 0, 'Убийства, новичок. Вот что творится в землях перед нашими глазами.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26380, 0, 0, 0, 0, 0, 0, 0, 0, 'These look promising. I\'ve heard great things about the Short and Stout Brewery, but I haven\'t had the chance to try their ale until now. While you\'re here, relax a little. If you need a spare hearthstone or wish to make Kharanos your home, just speak to me and I\'ll be happy to help. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26381, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m pleased, $N. That was quicker than I expected.', 0);
INSERT INTO `quest_offer_reward` VALUES (26382, 0, 0, 0, 0, 0, 0, 0, 0, 'Good, good. Just put them over there, I\'ve got my hands full.', 0);
INSERT INTO `quest_offer_reward` VALUES (26383, 0, 0, 0, 0, 0, 0, 0, 0, 'Did the sentinels send you? Thank Elune. We need everyone we can get.', 0);
INSERT INTO `quest_offer_reward` VALUES (26384, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, what have we here...\n\nFascinating. Quite fascinating.', 0);
INSERT INTO `quest_offer_reward` VALUES (26385, 0, 0, 0, 0, 0, 0, 0, 0, 'Our allies have come to our aid? Thank Elune. Tyrande was wise to extend open arms to your people.$B$BWe need help.  We need everyone we can get.', 0);
INSERT INTO `quest_offer_reward` VALUES (26386, 0, 0, 0, 0, 0, 0, 0, 0, '<You explain to Surkhan what has happened up to this point.>$B$BIt would seem that Jin\'do has survived, and he is rallying his brothers to once again summon Hakkar into this world. It was good of you to come to speak with me.$B$BThere is not much we can do now but to wait, and watch. Jin\'do is weak, and prime for striking, but I must wait for word from Rastakhan before issuing orders. Until then, you and your raptor will have to wait.', 0);
INSERT INTO `quest_offer_reward` VALUES (26387, 0, 0, 0, 0, 0, 0, 0, 0, 'Now we\'ve got a win-win situation. The spiders are going to either starve off, or find some dwarves and elves to replenish their larder, since my colleagues and I would make a poor meal.', 0);
INSERT INTO `quest_offer_reward` VALUES (26388, 5, 6, 274, 0, 0, 0, 0, 0, 'This is a disaster, $N!$B$BOur air fleet was decimated. And reports are coming in that our navy was attacked - unprovoked - by the Alliance in open water.$B$BAnd what of Hellscream? Is the Warchief dead?', 15595);
INSERT INTO `quest_offer_reward` VALUES (26389, 0, 0, 0, 0, 0, 0, 0, 0, 'The Blackrock orcs will surely fall now that their main force has been defeated! Only one task remains. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26390, 0, 0, 0, 0, 0, 0, 0, 0, 'With Kurtok slain we are safe once again from the orcish hordes. I will send peasants to the pass in an attempt to seal the passage so that no more orcs may come through into our territory. You have the thanks of Stormwind and of the Alliance, hero! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26391, 0, 0, 0, 0, 0, 0, 0, 0, 'Bless your heart, $n. You truly are a hero of the Alliance! Though the land is completely incinerated and there is nothing left of the vineyard but ash and debris, I still might be able to recover. Right? How bad can it be? ', 0);
INSERT INTO `quest_offer_reward` VALUES (26393, 0, 0, 0, 0, 0, 0, 0, 0, 'You have to get this note to Stormwind?  That\'s not a problem, you can take one of my gryphons! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26394, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, a note from Smith Argus?  I\'m not surprised he needs more gear.  There are so many new recruits these days. Well thank you, $n.  Here\'s some money to cover your travel costs. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26395, 0, 0, 0, 0, 0, 0, 0, 0, 'A crate for Goldshire, eh?  People sure are getting lazy. Have you been to Goldshire before?  If so, then it\'s no problem, my friend.  I have plenty of gryphons trained to fly that route! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26396, 0, 0, 0, 0, 0, 0, 0, 0, 'Great, you brought the armor!  We\'ll get this divvied to those who need it immediately. Thank you, $n.  Your efforts have been a great help to us.  And now that you\'re no stranger to gryphons, I hope you\'ll come and lend your aid to Goldshire often! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26397, 0, 0, 0, 0, 0, 0, 0, 0, 'The Grimtotem forced from Mulgore... if Baine speaks for this, you are indeed a fierce warrior.$B$BFrom this day, your strength is no longer your own. It belongs to me, and the Horde, as a weapon of war.$B$BHellscream\'s eyes are upon you, $n. Victory or death!', 0);
INSERT INTO `quest_offer_reward` VALUES (26399, 0, 0, 0, 0, 0, 0, 0, 0, '<Skeezy struggles fruitlessly to open the box.>$B$BThose ogres really know how to close a treasure chest, I\'ll tell you what. A box is useless to me, hero. We\'re going to need to find a way into this crate.', 0);
INSERT INTO `quest_offer_reward` VALUES (26400, 0, 0, 0, 0, 0, 0, 0, 0, '<The goblin peers inside.>$B$BWhaaaaaa?!?$B$BI\'m... I\'m going to have to send this to... to Flem... in the south. He\'ll know what to do with it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26401, 0, 0, 0, 0, 0, 0, 0, 0, 'How horrible... for someone so young and full of promise to fall to demons!$B$BI beg your forgiveness for sending you into danger as well, $n. I hope you don\'t cross paths with that demoness again...', 0);
INSERT INTO `quest_offer_reward` VALUES (26402, 0, 0, 0, 0, 0, 0, 0, 0, 'Telaron has already sent word of you, $n. I\'m interested in seeing if you\'re half as skilled as he claims.$B$BI am fighting a war on multiple fronts, while keeping our currently held areas properly defended, as well as overseeing the instruction of our youngest sentinels. If you\'re a decent fighter, there will be plenty for you to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (26403, 0, 0, 0, 0, 0, 0, 0, 0, 'Great! Wonderful! I can get a bundle for these!!$B$BThat Venture Company sure picked the wrong jungle to mine in, didn\'t they? We\'d all be in better shape if they moved out of here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26404, 0, 0, 0, 0, 0, 0, 0, 0, 'Skeezy sent you to the right place. Welcome to Hardwrench Hideaway!', 0);
INSERT INTO `quest_offer_reward` VALUES (26405, 0, 0, 0, 0, 0, 0, 0, 0, 'As promised, your reward. You do the troll nation proud.', 0);
INSERT INTO `quest_offer_reward` VALUES (26406, 0, 0, 0, 0, 0, 0, 0, 0, 'There it is... only one left.', 0);
INSERT INTO `quest_offer_reward` VALUES (26407, 0, 0, 0, 0, 0, 0, 0, 0, '<The Far Seer makes sure to hold the item by the string, carefully avoiding the dead ogre\'s fist.>$B$BStrange indeed. I will pass this on to my contacts in the east, near the Mosh\'Ogg\'s main camp. I have a feeling that they\'ll be interested in these strange scars.$B$BYou have my thanks, $R.', 0);
INSERT INTO `quest_offer_reward` VALUES (26408, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see a new face around here!$B$BThe forest crawls with Horde, but my assignment requires that I keep this outpost safe. I could really use someone who might be up for a good fight.', 0);
INSERT INTO `quest_offer_reward` VALUES (26409, 3, 0, 0, 0, 0, 0, 0, 0, 'Hiya! Lend a hand, would ya, $g bub : toots;?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26410, 11, 0, 0, 0, 0, 0, 0, 0, 'Good thing that I just happen to be a grand master of all things explosively alchemical.$B$BThis won\'t ignite on its own, though.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26411, 1, 0, 0, 0, 0, 0, 0, 0, 'Looks like that did it. He should come to any moment now.$B$BI told you I was a grand master of alchemy!$B$BI\'ve got some extra resin leftover. I can make it into all kinds of explosively wonderful stuff. What do you want?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26413, 2, 1, 0, 0, 0, 0, 0, 0, 'We are honored by Flint Oremantle\'s presence. I am given to understand that the Stonefather is in possession of a fragment of the World Pillar.$B$BIt is imperative that we recover all of the fragments. The instability between worlds is increasing. Soon it may become too great even for mighty Thrall to hold at bay.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26414, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work $n! I see you are quite the fisherman these days. Might just have to take you out on the boat soon and see what you can really do!', 0);
INSERT INTO `quest_offer_reward` VALUES (26416, 0, 0, 0, 0, 0, 0, 0, 0, 'Hah! That Cromula cracks me up.$B$BOh, right, Stranglethorn. You\'re really going?', 0);
INSERT INTO `quest_offer_reward` VALUES (26417, 0, 0, 0, 0, 0, 0, 0, 0, 'Glad you could make it! Let\'s get to work, shall we?', 0);
INSERT INTO `quest_offer_reward` VALUES (26418, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes... it\'s finally time. Shadra will be ours!', 0);
INSERT INTO `quest_offer_reward` VALUES (26419, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellently done. You have served the Dark Lady well today, $N.\n\nThis virulent blood will supply our armies with all manner of powerful weapons against the disgusting Alliance. We will wipe them from these lands.\n\nFor the Horde, of course... and all thanks to you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26420, 0, 0, 0, 0, 0, 0, 0, 0, 'Those look like some nice bass, $n. Let\'s fry em\' up and see how they taste!', 0);
INSERT INTO `quest_offer_reward` VALUES (26421, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 0);
INSERT INTO `quest_offer_reward` VALUES (26422, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 0);
INSERT INTO `quest_offer_reward` VALUES (26423, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 0);
INSERT INTO `quest_offer_reward` VALUES (26424, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 0);
INSERT INTO `quest_offer_reward` VALUES (26425, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to meet you, $n. You\'ve probably heard others speaking of Operation: Gnomeregan. Let me tell you a bit about what happened and why we left the dwarven city of Ironforge.', 0);
INSERT INTO `quest_offer_reward` VALUES (26426, 0, 0, 0, 0, 0, 0, 0, 0, '<The ground rumbles slightly as you approach the cave. This looks to be the entrance that Diamant and Felsen spoke of.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26427, 0, 0, 0, 0, 0, 0, 0, 0, 'All right, help me get tied to the wheel.$B$B<Moody sips from the bottle.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26428, 0, 0, 0, 0, 0, 0, 0, 0, 'You stand before me as a figure of vengeance, and I bow down before you in thanks.$B$BWith that cow\'s death, and knowing that their seed will no longer spread over the face of Azeroth, I can put my energy into something more lucrative. I hope you looted their farmstead after you finished them off.', 0);
INSERT INTO `quest_offer_reward` VALUES (26429, 0, 0, 0, 0, 0, 0, 0, 0, 'The signs of war are upon your garments, and sweat drips from your brow. Good, very good indeed.', 0);
INSERT INTO `quest_offer_reward` VALUES (26430, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Revantusk, $c. I hope fighting the Witherbark hasn\'t exhausted you; the Vilebranch are even worse.', 0);
INSERT INTO `quest_offer_reward` VALUES (26432, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome, $c. We\'re gonna make the Vilebranch regret ever crossin\' us.', 0);
INSERT INTO `quest_offer_reward` VALUES (26433, 0, 0, 0, 0, 0, 0, 0, 0, '<The goblin downs the water in one gulp.>$B$BAhh... do you know how hard it is to get fresh water in Stranglethorn? It\'s no wonder that the nagas guard their shrine so ferociously!$B$BOh, right... your artifact, and clarity. Clarity...$B$BI\'ve got it!', 0);
INSERT INTO `quest_offer_reward` VALUES (26434, 0, 0, 0, 0, 0, 0, 0, 0, '<Flem feels the hides and smiles to himself.>$B$BExcellent work, $n! These reagents will serve perfectly.', 0);
INSERT INTO `quest_offer_reward` VALUES (26435, 0, 0, 0, 0, 0, 0, 0, 0, 'Fascinating!$B$BI\'ll need some time to study this. Why don\'t you have a look around Booty Bay and come back later?', 0);
INSERT INTO `quest_offer_reward` VALUES (26436, 0, 0, 0, 0, 0, 0, 0, 0, 'Diamant says you are to be trusted. Diamant is patient and wise, so I shall allow you to prove yourself.$B$BAs it happens, I could use hands that are more... mobile.', 0);
INSERT INTO `quest_offer_reward` VALUES (26437, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahh... invigorating. Thank you, fleshling.', 0);
INSERT INTO `quest_offer_reward` VALUES (26438, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve proven useful, $r.', 0);
INSERT INTO `quest_offer_reward` VALUES (26439, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $r.', 0);
INSERT INTO `quest_offer_reward` VALUES (26440, 0, 0, 0, 0, 0, 0, 0, 0, '<The stones in Pebble\'s body bounce up and down happily. He seems very appreciative.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26441, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve made a friend, have you?$B$BAre you certain you want to feed him? He might become attached.', 0);
INSERT INTO `quest_offer_reward` VALUES (26442, 0, 0, 0, 0, 0, 0, 0, 0, 'Looks like the traps are working! Thanks $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (26443, 0, 0, 0, 0, 0, 0, 0, 0, 'By the Seven Crystals of Ata\'mal, this cannot be! They plan to attack Forest Song?!$B$BNot on my watch!$B$B<Vindicator Vedaar crumples the demon-scrawled plans in his fist.>$B$BNo. We will not allow this to happen!', 0);
INSERT INTO `quest_offer_reward` VALUES (26444, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my respect and thanks, $c. Perhaps we\'ll be able to create a new home here without having to worry about the Burning Legion yet again.', 0);
INSERT INTO `quest_offer_reward` VALUES (26445, 0, 0, 0, 0, 0, 0, 0, 0, 'Never again will the Burning Legion threaten Ashenvale! You have delivered us from a fight we very likely would have lost, $c.$B$BI give you thanks, $N, and the deepest gratitude of the Hand of Argus. It honors me to have you at our side!', 0);
INSERT INTO `quest_offer_reward` VALUES (26446, 0, 0, 0, 0, 0, 0, 0, 0, 'Good. Though it may take a great deal of your years for the seedlings to grow into ancients, in the fullness of time they will aid us in our fight against all of the enemies of Ashenvale.', 0);
INSERT INTO `quest_offer_reward` VALUES (26447, 0, 0, 0, 0, 0, 0, 0, 0, 'Dreadlords! They plan to attack us here at Splintertree Post?!$B$BNot while I still draw breath!$B$B<Valusha crumples the demon-scrawled plans in her fist.>$B$BNo. We will not allow this to happen! They are a blight upon our world!', 0);
INSERT INTO `quest_offer_reward` VALUES (26448, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s better! There\'s nothing quite like ridding our world of a demon. And they make for worthy opponents as well, unlike the weak races of the Alliance.', 0);
INSERT INTO `quest_offer_reward` VALUES (26449, 0, 0, 0, 0, 0, 0, 0, 0, 'Never again will the Burning Legion make slaves of the orcs, or any other race of the Horde!$B$BBravely fought, $C! You have the true heart of the Horde beating inside of you! You give us much honor!', 0);
INSERT INTO `quest_offer_reward` VALUES (26450, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work, $n. Now I\'ve just got to decide what to do with all of these...', 0);
INSERT INTO `quest_offer_reward` VALUES (26451, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $R! Did you feel it? The rush of the melee? The true risk of free-for-all combat?$B$BAs today is your first day here, I\'ll let you perform in one more competition for another reward. If you return in the future, you may compete again, once a day. Your rewards will, of course, improve as you become stronger.', 0);
INSERT INTO `quest_offer_reward` VALUES (26452, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $r!  I look forward to seeing you fight again soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26453, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to see another new arrival here at Forest Song.$B$BVindicator Palanaar is sending as much help as she can muster and each set of hands is appreciated.$B$BTake a look around, $c. It may not be much now, but with time and help from our night elf allies, we\'ll build a settlement worthy of calling home. There is much to be done here and any help you can provide would be welcome.', 0);
INSERT INTO `quest_offer_reward` VALUES (26454, 0, 0, 0, 0, 0, 0, 0, 0, 'Wood warped by foul demonic magic is of no use to the draenei at Forest Song. Nothing sound can be built with it, but I shall purify it and return it in good time to Architect Nemos and his people for their use.$B$BSome good will come from this tragedy, after all.', 0);
INSERT INTO `quest_offer_reward` VALUES (26455, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $N. There is certainly a latent power here, one that not even constant exposure to the corruption of the satyr can dim.$B$BDo you see how it still shines, after all these years?', 0);
INSERT INTO `quest_offer_reward` VALUES (26456, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for delivering this report, $c. From what Sentinel Melyria writes, it seems our worst fears have come to pass. With each advance we make, the Horde appears more determined to do as much damage as possible.$B$BWe can\'t afford to back down; the fate of our ancestral lands hangs in the balance. With more people like you on the frontlines, I\'m certain we could triumph.$B$BWill you join our fight? A new front has just opened in Warsong Gulch to the south.', 0);
INSERT INTO `quest_offer_reward` VALUES (26457, 0, 0, 0, 0, 0, 0, 0, 0, 'At last we have sent a message that the orcs won\'t dare ignore. Any overseer or foreman who sets foot within the camp to replace Gorthak will feel my wrath!$B$BWe must press our advantage, $N, and never rest until the Warsong Lumber Camp is a distant memory and a glade of trees stands upon that ground.$B$BNow what was this about explosives...?', 0);
INSERT INTO `quest_offer_reward` VALUES (26462, 0, 0, 0, 0, 0, 0, 0, 0, 'Not bad for a city-dwellin\' $r. Good show, aye!', 0);
INSERT INTO `quest_offer_reward` VALUES (26463, 0, 0, 0, 0, 0, 0, 0, 0, 'Teronis\' body lies broken atop the small rise next to the Keeper.$B$BDeep slashes crisscross his corpse.', 0);
INSERT INTO `quest_offer_reward` VALUES (26464, 0, 0, 0, 0, 0, 0, 0, 0, 'This is terrible, $N. Teronis is dead?!$B$BThe time for mourning will come, but for now we must focus our efforts. I will send some of the Sentinels to recover Teronis\' body and to assist the keeper.$B$BYou have the gem that Teronis was seeking? It must be delivered to Astranaar.', 0);
INSERT INTO `quest_offer_reward` VALUES (26465, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s quite a few that you have found! Thank you, $N!$B$BThe old city of Zoram holds many secrets, and these statuettes may be the key to many of them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26466, 0, 0, 0, 0, 0, 0, 0, 0, 'You found the ring! Thank you, $N.$B$BPlease take this as payment, and know that your deeds today will remain forever in my memory.', 0);
INSERT INTO `quest_offer_reward` VALUES (26467, 0, 0, 0, 0, 0, 0, 0, 0, 'Judging by the gaping wound in her side, Anilia is on the verge of death.', 0);
INSERT INTO `quest_offer_reward` VALUES (26468, 0, 0, 0, 0, 0, 0, 0, 0, 'Anilia died? No... that can\'t be! It was just yesterday that we were running and playing together in the woods.$b$bI guess those days are over...', 0);
INSERT INTO `quest_offer_reward` VALUES (26469, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, kind $r. My sister\'s spirit is gladdened by your efforts.', 0);
INSERT INTO `quest_offer_reward` VALUES (26470, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N! With the befouled elemental numbers thinned and Tideress put back into the lake, we should be able to launch a counter-offensive on Silverwind Refuge.$B$B<The sentinel frowns a bit, concern washing over her face.>$B$BJust as soon as we\'re done with defending the spire....', 0);
INSERT INTO `quest_offer_reward` VALUES (26472, 0, 0, 0, 0, 0, 0, 0, 0, 'Rest in peace, my friends.$B$B<You can see the sorrow in the druid cat\'s eyes.>$B$BI felt the anguish of the Dor\'danil Barrows lessen, and knew that you had completed your task.$B$BIt was grim work that you did, $N, for the spirits you destroyed were merely victims of the Horde\'s evil. But with their destruction, let us hope the maddened spirits will finally find peace.', 0);
INSERT INTO `quest_offer_reward` VALUES (26473, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, you have the hair! This is a good first step. There are other ingredients that we will need, but the hair of the fallen Ancient could well be the most important.$B$BI only hope that whatever the Forsaken are devising from the plant will not be turned into a weapon. We simply do not have the numbers to remove them right now, especially with Maestra\'s Post under attack!', 0);
INSERT INTO `quest_offer_reward` VALUES (26474, 0, 0, 0, 0, 0, 0, 0, 0, 'This is from Orendil?$B$BHis skill with herbs and healing is great! This cure gives me hope, when before I had little....', 0);
INSERT INTO `quest_offer_reward` VALUES (26475, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, you found it! Odd... it looks tarnished somehow. I will place the tear in a stew, and pray that it will cure her.$B$BThank you! Thank you, $N!', 0);
INSERT INTO `quest_offer_reward` VALUES (26476, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, Raene sent you? It must be serious indeed.$B$BWhat can I help you with?', 0);
INSERT INTO `quest_offer_reward` VALUES (26477, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m pleased to see you\'re back, <name>. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26478, 0, 0, 0, 0, 0, 0, 0, 0, 'You got it? Wonderful for you... and for Raene!$B$BI guess we\'ll have to see about reassembling it, won\'t we?', 0);
INSERT INTO `quest_offer_reward` VALUES (26479, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve recreated the rod Teronis had set out to find? That\'s wonderful to hear.$B$BShael\'dryn has been indispensible in her assistance. Teronis would be proud to see his quest is continuing even after his tragic passing.$B$B<Raene looks over the reconstructed rod with a look of approval and thoughts racing behind her eyes.>$B$BNow it is time for us to make use of its power to eliminate the enemies of Ashenvale!', 0);
INSERT INTO `quest_offer_reward` VALUES (26480, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, yes... you appear as one of my kind, but I care not what you truly are if you can help the furbolg.$B$BThe night elves and now the orcs have visited many atrocities upon my people.$B$BCorruption runs through our veins, corruption brought down upon us by the prideful night elves and the warlike orcs. They are the ones who did this to our people... They are the ones I blame for being cast out of my tribe.$B$BAt night I can still see the campfires burning in the darkness.', 0);
INSERT INTO `quest_offer_reward` VALUES (26481, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, my friend.$B$BNow quickly, hand me Ran\'s skull. While his essence yet taints it, we shall fashion it upon Dartol\'s Rod giving you the power to influence my brethren and deliver them from evil!', 0);
INSERT INTO `quest_offer_reward` VALUES (26482, 0, 0, 0, 0, 0, 0, 0, 0, 'So, you used the skull of the furbolg king to modify the power of Dartol\'s Rod? You then used that power to convince the captured furbolg to rise up and attack the Horde at Silverwind Refuge?$B$BI am more than pleased and impressed with your abilities, $N. The Sentinels cannot thank you enough. It should be a simple matter for us to sweep away the orcs and retake the refuge now!$B$BTeronis would be proud. You have my deepest respect.', 0);
INSERT INTO `quest_offer_reward` VALUES (26483, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that\'s what I like to hear! Good on ya.', 0);
INSERT INTO `quest_offer_reward` VALUES (26484, 6, 0, 0, 0, 0, 0, 0, 0, 'The Stonefather\'s son, Flint, sent you? No time to talk.$B$BWe must prevent a second front from opening up on the war!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26485, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, nice an\' fresh. I\'ll get those going while you fetch me some more goods then...', 0);
INSERT INTO `quest_offer_reward` VALUES (26486, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s the stuff! Wouldn\'t want any trolls or goblins gettin\' pleasure out of these, now would we!\n\nMaybe I can work a bit of this into my next brew...', 0);
INSERT INTO `quest_offer_reward` VALUES (26487, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey hey! Not bad, not bad at all! You may have a future in resource acquisition. Or hotel management.', 0);
INSERT INTO `quest_offer_reward` VALUES (26488, 0, 0, 0, 0, 0, 0, 0, 0, 'You found it! I can\'t believe it was down there all those years! You have no idea how much this means to me $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26489, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew you had it in you, boss-man. You know what they say: You can take the $R out of the islands, but you can\'t take the murderous tendencies out of $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26490, 0, 0, 0, 0, 0, 0, 0, 0, 'Look at it! That\'ll put some oomph into the mix!', 0);
INSERT INTO `quest_offer_reward` VALUES (26491, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good to see you back... and not missing any limbs, either.', 0);
INSERT INTO `quest_offer_reward` VALUES (26492, 0, 0, 0, 0, 0, 0, 0, 0, 'Looks like it\'s still good inside! Huh... is that banana bread? Still fresh!\n\nHere, lad, take your share of goods.', 0);
INSERT INTO `quest_offer_reward` VALUES (26493, 0, 0, 0, 0, 0, 0, 0, 0, 'Great! These look... disgusting. But probably pretty valuable. Let\'s get to splitting these up. Some for me, some for Jasper, some for Chabal...', 0);
INSERT INTO `quest_offer_reward` VALUES (26494, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ll file these away for now. Maybe we can experiment later.$B$B<Jasper looks over at you.>$B$BYeah, you\'ll do.', 0);
INSERT INTO `quest_offer_reward` VALUES (26495, 0, 0, 0, 0, 0, 0, 0, 0, '<The troll looks into the package.>$B$BYou have my thanks.$B$BI have been waiting for you to approach me, $R. I have identified you as the most promising $R here in this strange town. I have a task for you, if you would be willing to take it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26496, 0, 0, 0, 0, 0, 0, 0, 0, 'Well met, $N! We\'ve got a lot of trolls to kill and only so many hands to do it with!', 0);
INSERT INTO `quest_offer_reward` VALUES (26497, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye, we\'ll have these trolls cleared out yet.', 0);
INSERT INTO `quest_offer_reward` VALUES (26498, 0, 0, 0, 0, 0, 0, 0, 0, 'It won\'t be long until we\'re the only dwarves left breathing on these walls.', 0);
INSERT INTO `quest_offer_reward` VALUES (26499, 2, 1, 0, 0, 0, 0, 0, 0, 'You have my thanks, $n. You\'ve made all the difference, and the fate of these cowardly stone trogg ambushers is set in stone.$B$BNow we must get about the business of retaking Stonehearth.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26500, 1, 0, 0, 0, 0, 0, 0, 0, 'Not a bad start, though you\'ll have to destroy more of them than that if you\'re going to rescue the Stonefather.$B$BA lot more.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26501, 1, 0, 0, 0, 0, 0, 0, 0, 'This is good. We no longer need to worry about stone trogg attacks from the rear.$B$BLet\'s see what you can do for us on the front.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26502, 1, 0, 0, 0, 1000, 0, 0, 0, '<Clay grumbles to himself.>$B$BThose\'ll do. Just put them down over there.$B$B<The Earthen looks you up and down with one eye and then spits dust onto the ground.>$B$BI\'m going to have something for you to do in a moment. Maybe.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26503, 0, 0, 0, 0, 0, 0, 0, 0, 'Так я и знал! Это Изувой затеял всю эту возню, но смешней всего то, что мы регулярно убиваем гноллов с таким именем. Не знаю уж, сколько там сыновей было у самого первого Изувоя, но, видно, не меньше сотни.$B$BНу что ж, придется еще одного Изувоя убить.$B$BПотом надо будет доложить мировому судье Соломону.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26504, 0, 0, 0, 0, 0, 0, 0, 0, 'A decent job is a worthy reward!$B$BLook, don\'t waste everything in one place, |3-6($c). Better spread it around, if you know what I mean.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26505, 1, 1, 6, 5, 0, 0, 0, 0, '<Peaceful Judge Solomon wears a monocle. >$B$BOh, you\'re not Conah. What do you got there?$B$B<Peaceful Judge Solomon takes Parker\'s report. >$B$BWhat the hell\'s the matter with him... Izuva? ONE more?!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26506, 4, 1, 2, 0, 0, 0, 0, 0, 'ОТЛИЧНО!!! Прямо сейчас отправлю все это в кастрюлю, и скоро будет готов обед.$B$BСпасибо тебе,душечка!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26507, 0, 0, 0, 0, 0, 0, 0, 0, '<Pebble leaps upon the pile of petrified bats and rolls around in them, seeming to forget that you exist entirely. Perhaps for the best, if Kor\'s warnings are to be trusted.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26508, 4, 1, 6, 0, 0, 0, 0, 0, 'Спасибо, что нашли мое ожерелье, $gгосподин:госпожа:c; |3-6($c)… Вы $gтакой добрый:такая добрая;! И моя кошечка тоже спасибо говорит, да, Эфси?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26509, 0, 0, 0, 0, 0, 0, 0, 0, 'Finally the menace is laid to rest! Thank you, $n, you have done me a great service. The garden shall be in full bloom this season!', 0);
INSERT INTO `quest_offer_reward` VALUES (26510, 5, 1, 0, 0, 0, 0, 0, 0, 'Превосходно! Давай-ка сейчас тут кое-что подправим, и можно будет приниматься за дело.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26511, 1, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то кто-то проучил этих мурлоков. Надеюсь, в следующий раз, когда они решат напасть на наш город, они передумают.$B$BУж мы-то с тобой знаем, что такого больше не случится, да?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26512, 0, 0, 0, 0, 0, 0, 0, 0, '<The gnomecorder crackles and pops.>$B$BCan you hear me, $n? Is this thing on? Ah, yes, I see you there now.$B$BLet\'s get to work!', 0);
INSERT INTO `quest_offer_reward` VALUES (26513, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, citizen. Without you, we would have starved or worse, been forced to go out and get food for ourselves.', 0);
INSERT INTO `quest_offer_reward` VALUES (26514, 0, 0, 0, 0, 0, 0, 0, 0, '<The gnomecorder crackles and pops.>$B$BGood work, $n. I\'ve just been handed some important information. It would appear that our most hated enemy, the Blackrock orcs, have wised up...$B$B$B$BI\'ve transferred a few silver over to you for the gnome, erm, gnoll kills.', 0);
INSERT INTO `quest_offer_reward` VALUES (26515, 0, 0, 0, 0, 0, 0, 0, 0, 'Ain\'t that a hoot! Wish I could\'ve seen it myself.', 0);
INSERT INTO `quest_offer_reward` VALUES (26516, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'ve cleared straight through \'em! Jintha\'Alor is as good as wiped out!', 0);
INSERT INTO `quest_offer_reward` VALUES (26517, 0, 0, 0, 0, 0, 0, 0, 0, 'Dead? All of them, around the altar? And you found these spider idols on them...\n\nIt\'s Shadra. The Spider Loa! They\'re trying to tap her power!', 0);
INSERT INTO `quest_offer_reward` VALUES (26518, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s one ugly mug. Maybe he was takin\' other heads tryin\' to find a prettier one!', 0);
INSERT INTO `quest_offer_reward` VALUES (26519, 0, 0, 0, 0, 0, 0, 0, 0, '<The orb hisses as you touch it.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26520, 0, 0, 0, 0, 0, 0, 0, 0, 'You used an ettin to remove the boulder that was crushing Foreman Oslow? Incredible work, $n! Oslow will be grateful once he regains his composure.', 0);
INSERT INTO `quest_offer_reward` VALUES (26521, 0, 0, 0, 0, 0, 0, 0, 0, 'The elements dance, and fire consumes. Let the flame show \'em the cost of their wickedness!', 0);
INSERT INTO `quest_offer_reward` VALUES (26523, 0, 0, 0, 0, 0, 0, 0, 0, 'By Kurdan\'s beard... these don\'t look like any spiders I\'ve seen before. Dark magic\'s at work here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26524, 0, 0, 0, 0, 0, 0, 0, 0, 'This isn\'t just blood... it\'s venom!\n\nSpiders... venom... I hope I\'m wrong about my hunches here, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26525, 0, 0, 0, 0, 0, 0, 0, 0, 'Good riddance. May those horrid things never walk these lands again.', 0);
INSERT INTO `quest_offer_reward` VALUES (26526, 0, 0, 0, 0, 0, 0, 0, 0, 'A tablet! If I can read this... this is a ritual to summon Shadra herself into this world, and a list of the idols needed.\n\nYou\'ve done a good job slaying the keeper and getting ahold of this, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26528, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $N. We\'ll be knockin\' on this bug\'s door in no time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26529, 0, 0, 0, 0, 0, 0, 0, 0, 'There it is... only one left, lad.', 0);
INSERT INTO `quest_offer_reward` VALUES (26530, 0, 0, 0, 0, 0, 0, 0, 0, 'This is it, lad! We\'re ready!', 0);
INSERT INTO `quest_offer_reward` VALUES (26531, 0, 0, 0, 0, 0, 0, 0, 0, 'Talonaxe and the boys are right behind. Let\'s do this!', 0);
INSERT INTO `quest_offer_reward` VALUES (26532, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done a great thing for the Wildhammer today, $N. I wish Falstad could\'ve been here for that.\n\nOn behalf of the whole clan, I\'d like to repay you for keeping these lands free from that foul shadow. You\'ve earned this reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (26533, 0, 0, 0, 0, 0, 0, 0, 0, '<Jasper carefully analyzes the strange compound.>$B$BThis mixture is... I don\'t understand it. Perhaps we need to run some clinical trials.', 0);
INSERT INTO `quest_offer_reward` VALUES (26534, 0, 0, 0, 0, 0, 0, 0, 0, 'You don\'t say! Well, that certainly is interesting. In that case...$B$BForget understanding how the stuff works! I gotta get my hands on that recipe!', 0);
INSERT INTO `quest_offer_reward` VALUES (26535, 0, 0, 0, 0, 0, 0, 0, 0, 'This is fascinating! Just think of all the applications!$B$BJust so we\'re clear, I\'m not thinking about using this to make an army of slaves or anything like that. No, no... I\'m much more interested in the ogre explosions!', 0);
INSERT INTO `quest_offer_reward` VALUES (26536, 0, 0, 0, 0, 0, 0, 0, 0, 'These perch look great $n. Why don\'t you join us for a fish fry tonight?', 0);
INSERT INTO `quest_offer_reward` VALUES (26537, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s a good dent, but it\'s clear to me that we\'re going to have to handle this a slightly different way.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26538, 2, 1, 0, 0, 0, 0, 0, 0, '<Gregor bows respectfully to Zaela.>$B$BIf it is Zaela\'s wish, then you shall have your supplies. Just don\'t tell the Dragonmaw Warchief...', 15595);
INSERT INTO `quest_offer_reward` VALUES (26539, 1, 6, 0, 0, 0, 0, 0, 0, 'I\'m glad you\'re here, $c. Perhaps a seasoned veteran of the Horde\'s campaigns in Northrend and elsewhere can talk some sense into the Dragonmaw leadership...', 15595);
INSERT INTO `quest_offer_reward` VALUES (26540, 5, 274, 0, 0, 0, 0, 0, 0, 'Dragon\'s Blood!? We best not tell our red dragon allies that we\'ve used these.$B$BStill, it\'s powerful magic... We\'ll have our fighting force back in no time.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26542, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Aerie Peak, $N. On behalf of High Thane Falstad, I thank ye for comin\' to help us.', 0);
INSERT INTO `quest_offer_reward` VALUES (26543, 0, 0, 0, 0, 0, 0, 0, 0, 'Looks like you found the clams, $N.  These will do nicely.', 0);
INSERT INTO `quest_offer_reward` VALUES (26544, 0, 0, 0, 0, 0, 0, 0, 0, '<The gnomecorder buzzes and whirs.>$B$BJust put the missive in the little compartment and I\'ll read it. I am fluent in orcish.$B$B<You hear a distraught yell in the background.>$B$BIt looks like these orcs are here under the direct command of Yowler. That means Yowler must have the orc invasion plan.', 0);
INSERT INTO `quest_offer_reward` VALUES (26545, 0, 0, 0, 0, 0, 0, 0, 0, '<Magistrate Solomon takes the plans from you and begins reading.>$B$BShadowhide army!? Damn those orcs to hell! These invasion plans speak of a massive build up of orcish and gnoll forces in the east. Looks like Gath\'Ilzogg, the Blackrock general, is preparing to march his armies across Lakeshire and attack Stormwind directly!', 0);
INSERT INTO `quest_offer_reward` VALUES (26546, 0, 0, 0, 0, 0, 0, 0, 0, 'Treat them with respect, and they\'ll treat you with it as well.\n\nGive \'em guff and you\'ll be out an eye!', 0);
INSERT INTO `quest_offer_reward` VALUES (26547, 0, 0, 0, 0, 0, 0, 0, 0, 'Much appreciated, $N.', 0);
INSERT INTO `quest_offer_reward` VALUES (26548, 0, 0, 0, 0, 0, 0, 0, 0, 'Good to see you out here! Let\'s get to it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26549, 1, 5, 0, 0, 0, 0, 0, 0, 'Wake up, $N! We don\'t have much time.$B$BI rescued your gear for you.$B$BAt least now you\'ve seen the foolishness of our leader first-hand. His blood is contaminated, his rage is unquenchable -- and he\'ll destroy us all trying to defeat the Horde!', 15595);
INSERT INTO `quest_offer_reward` VALUES (26550, 0, 0, 0, 0, 0, 0, 0, 0, 'It is as I suspected. Zanzil is too haughty to interfere with the squabbles between the Bloodscalps and the Skullsplitters. No, he is playing a part in a grander scheme... Jin\'do the Hexxer is using him to help bring Hakkar back to Zul\'Gurub.$B$BIt\'s good that we found this out now, because it offers us a simple solution.', 0);
INSERT INTO `quest_offer_reward` VALUES (26555, 0, 0, 0, 0, 0, 0, 0, 0, 'I ask you to slay Zanzil the Outcast, a lowly, hermetic exile of the Gurubashi, and what do you do? You dash headfirst into the Gurubashi capital and eliminate one of their High Priests! Very nearly two! Only one word suffices to explain such a rash and foolhardy endeavor:$B$BHeroism.$B$BJin\'do the Hexxer still walks within Zul\'Gurub\'s walls, but he lost one of his legs when you crushed Jeklik. Today\'s battle is ours.', 0);
INSERT INTO `quest_offer_reward` VALUES (26556, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work $N.  These samples will help us figure out what is going on in that toxic soup over there.', 0);
INSERT INTO `quest_offer_reward` VALUES (26557, 0, 0, 0, 0, 0, 0, 0, 0, 'Outstanding!  Those sandy carp are picky eaters aren\'t they?', 0);
INSERT INTO `quest_offer_reward` VALUES (26558, 0, 0, 0, 0, 0, 0, 0, 0, 'Marnal draws close... and Shadra even closer. Prepare for bloodshed.', 0);
INSERT INTO `quest_offer_reward` VALUES (26560, 0, 0, 0, 0, 0, 0, 0, 0, 'I know where they\'re holding Krakauer and Danforth. We gotta hurry. They were prepping those two for a sacrifice!', 0);
INSERT INTO `quest_offer_reward` VALUES (26561, 0, 0, 0, 0, 0, 0, 0, 0, 'Wha... Where am I? What happened? Messner? Jorgensen?$B$BOH MY GOSH! We have to get to Danforth! It might already be too late!', 0);
INSERT INTO `quest_offer_reward` VALUES (26562, 1, 1, 0, 0, 0, 0, 0, 0, 'It\'s about time you showed up! My hands are already starting to leak.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26563, 66, 1, 1, 0, 0, 0, 0, 0, '<Colonel Trotman salutes. >$B$BI don\'t believe it, $n. You\'re $gmanaged:managed; saved the entire Bravo team from the Black Mountain Orcs? So, Kishan`s comrades are saved, we returned his things to him too - no, now he certainly won`t refuse us!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26564, 1, 0, 0, 0, 0, 0, 0, 0, 'Well then. Maybe you\'re okay after all.$B$BAlright, alright... don\'t get all bent out of shape. Just give me a few moments with these parts.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26566, 0, 0, 0, 0, 0, 0, 0, 0, 'So the high tinker himself has heard of my little project? Splendid! I\'ve almost finished my latest prototype, but maybe you can help me chase down those last few parts.', 0);
INSERT INTO `quest_offer_reward` VALUES (26567, 0, 0, 0, 0, 0, 0, 0, 0, 'Help you? Help these people? These people that would rather spit on me than crack a smile?', 0);
INSERT INTO `quest_offer_reward` VALUES (26568, 0, 0, 0, 0, 0, 0, 0, 0, '<Troteman sighs.>$B$BI\'m not giving up on him, $n, and I\'m not giving up on Redridge. We need to boost his spirits... Rouse the slumbering monster within him!', 0);
INSERT INTO `quest_offer_reward` VALUES (26569, 4, 1, 273, 0, 0, 0, 0, 0, 'Ты молодчина, $n! Они очень пригодятся для нашего следующего проекта, для Приозерского СВЕРХМОСТА, длиной во все озеро Безмолвия! Лет 20 на его постройку уйдет. Ну, плюс-минус еще пара десятилетий.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26570, 1, 1, 0, 0, 0, 0, 0, 0, 'Ты $gвыиграл:выиграла; нам еще день, а то и два. Спасибо тебе, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26571, 0, 0, 0, 0, 0, 0, 0, 0, '<The gnomecorder buzzes with energy.>$B$BCome in, $n. Do you copy? Troteman here! Great job on getting Keeshan\'s weapons back! Now there\'s one final mission you have to run. Are you up to the challenge?', 0);
INSERT INTO `quest_offer_reward` VALUES (26572, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice one, $N!  I tell ya, golden stonefish are not easy to come by so this is a nice treat.', 0);
INSERT INTO `quest_offer_reward` VALUES (26573, 1, 1, 0, 0, 0, 0, 0, 0, 'Надеюсь, что эти вещи и вся его команда, которая счастливо спаслась, поднимут ему настроение. Нам не выиграть эту битву без Кишана.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26574, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Shadebough, $n. Lady Shandris sent word of you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26575, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $r. You\'ve done all that I can ask of you. I shall impart my favor for you to the Stonemother.$B$BMay the stone beneath your feet hold you steady during your quest for the World Pillar.', 0);
INSERT INTO `quest_offer_reward` VALUES (26576, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings, $r. I have been told of your coming. If you wish to aid me, then perhaps I can be of aid to you as well.', 0);
INSERT INTO `quest_offer_reward` VALUES (26577, 0, 0, 0, 0, 0, 0, 0, 0, 'You are Diamant\'s chosen.$B$BSo small. So fragile.$B$BDo you realize that your actions shall be the basis by which your entire, \"Earthen Ring\" is judged? You truly think you can stand for so many?$B$BYou are a fool. A confident fool. We shall see if you are a capable fool as well.', 0);
INSERT INTO `quest_offer_reward` VALUES (26578, 0, 0, 0, 0, 0, 0, 0, 0, 'You have appealed to Diamant. He is patient. Trusting. Perhaps too much so. You have much to prove if you wish my favor, $R.$B$BStill, this is a start.', 0);
INSERT INTO `quest_offer_reward` VALUES (26579, 0, 0, 0, 0, 0, 0, 0, 0, 'You carry with you a certain degree of potency. I can find some respect for such strength, especially in your kind.$B$BMy concerns are not yet abated. Not for the source of the overgrowth, and not for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26580, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good to see you again, brother. I see that your efforts go well. The Stone Lords appear to be showing you some degree of trust.$B$BWe\'ve come to help. We might be of some aid in tracking down the source of this sudden growth. I highly suspect your giant friend\'s aggressions are focused in the wrong direction.', 0);
INSERT INTO `quest_offer_reward` VALUES (26581, 0, 0, 0, 0, 0, 0, 0, 0, 'You let the mist touch your skin? I hope there weren\'t any adverse affects.', 0);
INSERT INTO `quest_offer_reward` VALUES (26582, 0, 0, 0, 0, 0, 0, 0, 0, 'The stone troggs? And I am to trust you at your word?$B$BI have no love for the Verlok anyway. Suspicion is more than enough reason to lay waste to them. We shall see if your word is true.', 0);
INSERT INTO `quest_offer_reward` VALUES (26583, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $R.$B$BGorsik shall hear of your deed. Therazane shall hear of your deed.$B$BYou are quite vicious for one so small... and so soft.', 0);
INSERT INTO `quest_offer_reward` VALUES (26584, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done fleshling. Your presence complimented my brothers well.', 0);
INSERT INTO `quest_offer_reward` VALUES (26585, 0, 0, 0, 0, 0, 0, 0, 0, 'So your words prove true. You have my gratitude, $R.', 0);
INSERT INTO `quest_offer_reward` VALUES (26586, 0, 0, 0, 0, 0, 0, 0, 0, 'Keeshan? You mean JOHNNY\'S ALIVE? You bet your sweet behind I\'ll help, but first you gotta get me out of this damned cage.', 0);
INSERT INTO `quest_offer_reward` VALUES (26587, 0, 0, 0, 0, 0, 0, 0, 0, 'IT\'S PAYBACK TIME!', 0);
INSERT INTO `quest_offer_reward` VALUES (26588, 0, 0, 0, 0, 0, 0, 0, 0, '$N, you got one!  It\'s a rare day that I get to see such a strong, beautiful fish.', 0);
INSERT INTO `quest_offer_reward` VALUES (26589, 0, 0, 0, 0, 0, 0, 0, 0, 'Peace, friend. I wish we could have met under less troubling circumstances.', 0);
INSERT INTO `quest_offer_reward` VALUES (26590, 0, 0, 0, 0, 0, 0, 0, 0, '<The goblin downs the water in one gulp.>$B$BAhh... do you know how hard it is to get fresh water in Stranglethorn? It\'s no wonder that the nagas guard their shrine so ferociously!$B$BOh, right... that artifact. Clarity...$B$BI\'ve got it!', 0);
INSERT INTO `quest_offer_reward` VALUES (26591, 1, 0, 0, 0, 0, 0, 0, 0, 'If you ever get tired of your being a $c, come look me up. You seem very good with those fleshy hands of yours.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26592, 0, 0, 0, 0, 0, 0, 0, 0, '<Flem weighs the gems in his hands and nods in approval.>$B$BYes, these will do. Now to perform the analysis...$B$B<The goblin clacks the gems together for a while, then looks up at you.>$B$BOh, you\'re still here? You don\'t think I\'m a fraud or something, do you?', 0);
INSERT INTO `quest_offer_reward` VALUES (26593, 0, 0, 0, 0, 0, 0, 0, 0, 'Ack! Where\'d you get these? No, don\'t tell me. I don\'t want to know!$B$BWho sent you? Sea Wolf?', 0);
INSERT INTO `quest_offer_reward` VALUES (26594, 0, 0, 0, 0, 0, 0, 0, 0, 'Heh heh, well done! And when you showed Shaky those giblets, how hard did he shake?$B$BHah! He\'s so easy to read, when I play cards with him I can\'t help but win!$B$BThanks for your help, $n. Now it\'s your turn to get paid...', 0);
INSERT INTO `quest_offer_reward` VALUES (26595, 0, 0, 0, 0, 0, 0, 0, 0, 'Great!  I can get a good price for these, especially for what\'s inside Maury\'s foot!  Thanks much, $n!\r\n\r\nI hope those three learned their lesson: don\'t bet something you can\'t afford to lose.\r\n\r\nAnd maybe it\'s too late for them, but...they should be careful of who they gamble with. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26596, 0, 0, 0, 0, 0, 0, 0, 0, 'Yeah? Whaddya need?', 0);
INSERT INTO `quest_offer_reward` VALUES (26597, 0, 0, 0, 0, 0, 0, 0, 0, '...feel strength returning....\r\n\r\nWhere might I be?  Booty Bay?  Oh my!  I had the strangest dream...\r\n\r\n...the weakness has not completely left me.... ', 0);
INSERT INTO `quest_offer_reward` VALUES (26598, 0, 0, 0, 0, 0, 0, 0, 0, 'Witch Doctor Unbagwa like Gorilla Fangs!  For you I talk to spirits.  Make Mokk the Savage come.\r\n\r\nYou protect Witch Doctor Unbagwa from other Gorilla-beast though or no Mokk for you! ', 0);
INSERT INTO `quest_offer_reward` VALUES (26599, 0, 0, 0, 0, 0, 0, 0, 0, 'You found it!  What a glorious day for ol\' Hecklebury, this is.  Getting back my chest cools some of the fire in me.$B$BBut my revenge isn\'t complete...', 0);
INSERT INTO `quest_offer_reward` VALUES (26600, 0, 0, 0, 0, 0, 0, 0, 0, 'This wasn\'t expensive, was it?  Well, here\'s some pieces of eight to reimburse you.$b$bNow let\'s get onto this revenge, shall we?', 0);
INSERT INTO `quest_offer_reward` VALUES (26601, 0, 0, 0, 0, 0, 0, 0, 0, 'Mok\'rash... Negolash... Gorlash... they\'re all gone.$b$bI suppose I have no excuses now.  I can return to the high seas and a life of pillage and booty!  And it\'s all thanks to you.$b$bNow I just need to get myself a ship...', 0);
INSERT INTO `quest_offer_reward` VALUES (26602, 0, 0, 0, 0, 0, 0, 0, 0, 'I can\'t believe my fortune, $N!  Meeting you has turned my luck to the better, make no mistake there!$B$BIf I ever get a new ship and you\'re looking to sail the seas, you would be my honored guest.', 0);
INSERT INTO `quest_offer_reward` VALUES (26603, 0, 0, 0, 0, 0, 0, 0, 0, 'Have you come to free me?', 0);
INSERT INTO `quest_offer_reward` VALUES (26604, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s about time you returned.  I was beginning to worry that I was going to be eaten while you hunted.$b$bA cat eating a princess... wouldn\'t that be a fancy feast?', 0);
INSERT INTO `quest_offer_reward` VALUES (26605, 0, 0, 0, 0, 0, 0, 0, 0, '<Poobah surveys her belongings.>$b$bLooks like it\'s all here.  Alright, I think I\'m ready to be rescued now.', 0);
INSERT INTO `quest_offer_reward` VALUES (26606, 0, 0, 0, 0, 0, 0, 0, 0, '$N, you have saved me from a great dishonor. If it was within my power, I would offer you a place among my tribe. But for now, all I can only offer you is this token for the courage and nobility you have shown.', 0);
INSERT INTO `quest_offer_reward` VALUES (26607, 1, 1, 25, 0, 0, 0, 0, 0, '<Kishan takes a bundle from you and opens it. >$B$BMy bow... And my knife! Where are you... $B$BThe amulet of Jade... My dear Jade.>$B$BTime to hit the Orcs!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26608, 0, 0, 0, 0, 0, 0, 0, 0, 'The Fel Orcs from Outland have laid claim to the Dragonmaw? Obscene!$B$BIf Hellscream knew what was happening here, he\'d raze their city to the ground...', 15595);
INSERT INTO `quest_offer_reward` VALUES (26609, 0, 0, 0, 0, 0, 0, 0, 0, 'On top of the barrel, you discover a map with some hastily written text on it, along with some coins.', 0);
INSERT INTO `quest_offer_reward` VALUES (26610, 0, 0, 0, 0, 0, 0, 0, 0, 'Sure, I remember you, $c.  Back from scouting I see....', 0);
INSERT INTO `quest_offer_reward` VALUES (26611, 0, 0, 0, 0, 0, 0, 0, 0, 'Bloodsail Buccaneers attacking Booty Bay?  This isn\'t the first time that those blaggards have tried something.  Booty Bay is impenetrable!$b$bSounds like they might be changing their strategies up, though.  Wouldn\'t put it past Firallon to try something crazy.  Very well, let\'s see what else they\'re up to.', 0);
INSERT INTO `quest_offer_reward` VALUES (26612, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect, $n.  Now that we know their plan, it\'s just a matter of time.$b$bAnd to think the fools have dropped anchor just off the cape....', 0);
INSERT INTO `quest_offer_reward` VALUES (26613, 0, 0, 0, 0, 0, 0, 0, 0, 'Good show, $n!  And just before I need to ship out too!  Can\'t thank you enough, old friend.', 0);
INSERT INTO `quest_offer_reward` VALUES (26614, 0, 0, 0, 0, 0, 0, 0, 0, 'Shiver me timbers and timber me shivers!  Ye found me beautiful eye!  Let\'s see if she still fits.$b$b<Kshhh-thunk>$b$b$n ho!  Blimey, I can see!!', 0);
INSERT INTO `quest_offer_reward` VALUES (26616, 1, 0, 0, 0, 0, 0, 0, 0, 'Пора убивать орков.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26617, 0, 0, 0, 0, 0, 0, 0, 0, 'That coin!  Where did you get it?$b$bBruce... yes, I see.  He was one of the Blackwater Raiders once.  One of us, before Greymane closed the Gilneas ports and held his crew captive.$b$bI know that what happened in Gilneas changed the people there, but I never expected them to change their loyalties.  Especially not Bruce.$b$bIf you wouldn\'t mind, could I keep the coin?  I\'ll pay its worth in gold.', 0);
INSERT INTO `quest_offer_reward` VALUES (26618, 66, 0, 0, 0, 0, 0, 0, 0, 'Не думай, что легко $gотделался:отделалась;. В лесах таятся и куда более серьезные опасности.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26619, 6, 5, 1, 0, 0, 0, 0, 0, '$N, what are your people doing? Are you attacking the Dragonmaw or are you here to rid us of Mor\'ghor\'s corruption?$B$B<Zaela listens to the plan.>$B$BAh, I see now. I like the way you think. Warchief Mor\'ghor won\'t even know what hit him.$B$BLet\'s do this!', 15595);
INSERT INTO `quest_offer_reward` VALUES (26620, 2, 0, 0, 0, 0, 0, 0, 0, 'Ммм-ммм! Грудинка – это лучшая часть туши волка. Хотя это и не традиционный способ готовить эти котлеты.$b$bВот, держи рецепт. Он немного отличается, но когда тебе действительно потребуется еда, мясо с волчьих боков будет достать проще.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26621, 4, 1, 25, 0, 0, 0, 0, 0, 'We\'ve lost a lot of good orcs, but the fight is going our way, $N.$B$BMor\'ghor\'s regime is collapsing. All that remains is to destroy the beast himself.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26622, 5, 1, 0, 0, 0, 0, 0, 0, 'Well done, $N! You have done the Horde proud in my absence.$B$BThe Dragonmaw have joined us, with Zaela as their new leader. Blood was shed, but casualties were low, and we have their complete loyalty.$B$BOur Horde grows stronger!', 15595);
INSERT INTO `quest_offer_reward` VALUES (26623, 432, 0, 0, 0, 0, 0, 0, 0, 'О, отличные кусочки! Дай-ка я замариную их особым составом, – не подсматривай! – потом хорошенько подрумяню на сковородке…$B$BТемные пирожки с крабами – мое коронное блюдо, и рецептом я делиться не готов, но вот тебе рецепт другого кушанья, не хуже.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26624, 0, 0, 0, 0, 0, 0, 0, 0, 'Yarr, ya made a good decision, $g lad: wench;.', 0);
INSERT INTO `quest_offer_reward` VALUES (26625, 1, 0, 0, 0, 1000, 0, 0, 0, '<Gravel solemnly nods his head.>$B$BFinally, we can move forward and rescue the Stonefather.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26627, 1, 0, 0, 0, 0, 0, 0, 0, 'Welcome, wanderer... I\'d offer you something, but there\'s a rolling ball... Can you do one errand for the poor old hermit?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26628, 0, 0, 0, 0, 0, 0, 0, 0, 'We survived the attack!$B$BWe could not have done it without you, $n.$B$BWe\'ll be heading out as soon as the tide rises. If you weren\'t such a promising hero I\'d offer you a place on my crew. But I can tell you have bigger fish to fry.$B$BEven if you are a puny $R....', 0);
INSERT INTO `quest_offer_reward` VALUES (26629, 0, 0, 0, 0, 0, 0, 0, 0, 'Well blow me down! Now there\'s something ya don\'t see too often.$B$B<Yancey reaches out and shakes your hand.>$B$BWelcome aboard, $n. Plunder and booty await ye.', 0);
INSERT INTO `quest_offer_reward` VALUES (26630, 0, 0, 0, 0, 0, 0, 0, 0, 'That should fool that Bloodsail dimwit.\r\n\r\nThis was fun, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26631, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes... yes... I see... well, everything appears to be in order.$B$BSlayer of the great Seahorn, eh? That doesn\'t matter on MY ship. Only thing you\'re gonna be slaying is deck stains. Grab a mop.', 0);
INSERT INTO `quest_offer_reward` VALUES (26632, 1, 0, 0, 0, 0, 0, 0, 0, '<The catapult chief gives you an appraising look.>$B$BWord of your prowess has spread ahead of you, $r. Let\'s see if you can help us keep these fungal horrors off of the front.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26633, 0, 0, 0, 0, 0, 0, 0, 0, 'I heard the most furious mopping sounds from down here, mate. That\'s EXACTLY what your Captain likes to hear.', 0);
INSERT INTO `quest_offer_reward` VALUES (26634, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ll distribute these right away, $R. And I\'ll see what I can do to put in the good word for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26635, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $n. I\'d been hearing whispers about \"Pretty Boy\" and a \"boo-box\". I don\'t think I want to go in the boo-box.', 0);
INSERT INTO `quest_offer_reward` VALUES (26636, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice job, rookie. I\'m gonna go ahead and put the camo in the box. We should be ready soon.', 0);
INSERT INTO `quest_offer_reward` VALUES (26637, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect. Those orcs won\'t know what hit \'em - literally. I\'ll need a few minutes to get these in working order and then I\'ll put them in the Bravo Company field kit.', 0);
INSERT INTO `quest_offer_reward` VALUES (26638, 0, 0, 0, 0, 0, 0, 0, 0, 'Great work, $n. We need to move quickly before they send for reinforcements.', 0);
INSERT INTO `quest_offer_reward` VALUES (26639, 0, 0, 0, 0, 0, 0, 0, 0, 'Brubaker is dead. It looks like the orcs are attempting to send a message with this grizzly display. Upon closer examination you notice something in Brubaker\'s pouch.', 0);
INSERT INTO `quest_offer_reward` VALUES (26640, 0, 0, 0, 0, 0, 0, 0, 0, '<Keeshan reads the report.>$B$BDAMN IT! This just got complicated. We\'ve got prisoners of war to rescue before we can blow up the valley. Not to mention the orcs have black dragons on their side.$B$BAre you ready, $n?', 0);
INSERT INTO `quest_offer_reward` VALUES (26641, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ll see these are burnt to ash right away, and scattered to the winds for good measure.', 0);
INSERT INTO `quest_offer_reward` VALUES (26642, 0, 0, 0, 0, 0, 0, 0, 0, 'A fresh recruit from Orgrimmar is it?$B$B<Kargal sizes you up.>$B$BI\'ll take what I can get. Keeping the roads open has been a nightmare.', 0);
INSERT INTO `quest_offer_reward` VALUES (26643, 0, 0, 0, 0, 0, 0, 0, 0, 'A grim task for sure, $N, but it\'ll keep the spiders left alive from recovering. I thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26644, 0, 0, 0, 0, 0, 0, 0, 0, 'So ye be the one that did ol\' Seahorn? Yancey sent word, but I wanted to make sure he hadn\'t been telling his tall tales again.$B$BI\'ll make it brief. Ye did well on Keelhaul\'s crew, but I expect ye to do even better on mine. I\'ve got an entire fleet to attend to, so you\'ll be helpin\' to run the day-to-day of me ship.$B$BWelcome aboard the Crimson Veil, sailor.', 0);
INSERT INTO `quest_offer_reward` VALUES (26645, 21, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, $n. За твою помощь жителям Темнолесья ты $gзаслужил:заслужила; награду.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26646, 0, 0, 0, 0, 0, 0, 0, 0, '<The radio on the Bravo Company field kit crackles.>$B$BLooks like the P.O.W.s are free. Time to initiate Operation: End All.', 0);
INSERT INTO `quest_offer_reward` VALUES (26647, 0, 0, 0, 0, 0, 0, 0, 0, 'I like the cut of your gib, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26648, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $n. The eternal battle wages on, but we\'ve tipped the scales in favor of the pirates today. Firallon will be happy to hear the news.', 0);
INSERT INTO `quest_offer_reward` VALUES (26649, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahahaha! Blasted \'em clear off their rigging! I\'ll drink to that!$B$BYou\'ve got a bright future, kid.', 0);
INSERT INTO `quest_offer_reward` VALUES (26650, 0, 0, 0, 0, 0, 0, 0, 0, 'You see a set of plans unrolled on the table. They explain everything!$B$BThese are the details of the impending attack on Booty Bay. Battle formations, plans of attack, and something about a group called the \"Brashtide Crew\".', 0);
INSERT INTO `quest_offer_reward` VALUES (26651, 0, 0, 0, 0, 0, 0, 0, 0, 'And now, we blow the whole damned thing up.', 0);
INSERT INTO `quest_offer_reward` VALUES (26652, 0, 0, 0, 0, 0, 0, 0, 0, 'Что это? Гребень? Какая прелесть! И он спокойно скользит по моим волосам, хотя они давно сбились в ужасный слипшийся колтун.$B$BАх, мне бы в зеркало посмотреться...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26653, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh, so you need a thread of ghost hair? I\'m afraid my supplies are exhausted, but I can do more... If you bring me ghost hair.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26654, 432, 0, 0, 0, 0, 0, 0, 0, 'Ну, из этого выйдет целая катушка нити из призрачных волос, да еще и останется несколько прядей. Прими эти монеты в уплату за лишние пряди.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26655, 273, 1, 0, 0, 0, 0, 0, 0, 'Beautiful! It\'ll be perfect... Here, $n, take it as a payment for the noble deed.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26656, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. Of my main strengths, speed is not one.', 0);
INSERT INTO `quest_offer_reward` VALUES (26657, 0, 0, 0, 0, 0, 0, 0, 0, 'Not just fast it seems. You have some bite in you.$B$BWell done, $c.', 0);
INSERT INTO `quest_offer_reward` VALUES (26658, 0, 0, 0, 0, 0, 0, 0, 0, 'Glorious.$B$BNow we will deal a blow to the stone dragons that they will remember for the next hundred years.', 0);
INSERT INTO `quest_offer_reward` VALUES (26659, 0, 0, 0, 0, 0, 0, 0, 0, 'Impressive, fleshling. You are far more capable than I initially assumed.$B$BI will extend that sentiment to the Stonemother as well.', 0);
INSERT INTO `quest_offer_reward` VALUES (26660, 0, 0, 0, 0, 0, 0, 0, 0, 'Тебе нужен сок зомби, да? Хмм… это сильная штука, ее не часто спрашивают.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26661, 2, 0, 0, 0, 0, 0, 0, 0, 'Отлично, ты $gсумел:сумела; добыть цветки гнили.$b$bЯ быстро приготовлю для тебя сок зомби… предупреждаю, он очень крепкий.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26662, 0, 0, 0, 0, 0, 0, 0, 0, 'The Brashtide Crew aren\'t just pirates... they\'re worgen pirates! And they\'re joining in on the attack!$B$BRevilgaz is definitely going to want to know about this.', 0);
INSERT INTO `quest_offer_reward` VALUES (26663, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve set the pirates back several days\' work. Hopefully this will delay their attack.', 0);
INSERT INTO `quest_offer_reward` VALUES (26664, 0, 0, 0, 0, 0, 0, 0, 0, 'The fewer Bloodsail Buccaneers that Booty Bay has to fight, the better. However, it might be best to stop slaughtering your supposed allies before you draw suspicion to yourself.', 0);
INSERT INTO `quest_offer_reward` VALUES (26665, 0, 0, 0, 0, 0, 0, 0, 0, 'The Brashtide!? I thought they were dead and gone YEARS ago!$B$B<Revilgaz listens intently as you relay the information you gathered.>$B$BNo, that absolutely makes sense. And it\'s not good news either. The Brashtide are formidable foes, and if their numbers are anywhere near what they were before they disappeared, we\'re in for a nasty battle.', 0);
INSERT INTO `quest_offer_reward` VALUES (26666, 1, 0, 0, 0, 0, 0, 0, 0, 'Чем я могу помочь?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26667, 1, 0, 0, 0, 0, 0, 0, 0, 'Ничего себе… ты действительно $gотправился:отправилась; туда и $gнашел:нашла; их?$b$bЯ потрясен. Полагаю, я должен поблагодарить тебя за возвращение утраченной части моего архива.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26668, 0, 0, 0, 0, 0, 0, 0, 0, 'Did you see those orcs explode? THAT\'S HOW WE DO IT IN BRAVO COMPANY! Now to clean up the rest of these jackasses.', 0);
INSERT INTO `quest_offer_reward` VALUES (26669, 1, 0, 0, 0, 0, 0, 0, 0, 'Это все, что ты $gнашел:нашла;? Боюсь, это плохие новости…', 23877);
INSERT INTO `quest_offer_reward` VALUES (26670, 0, 0, 0, 0, 0, 0, 0, 0, 'You actually went and got it?!$B$BI don\'t know whether to call you brave or insane. But once again, my archives thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26671, 0, 0, 0, 0, 0, 0, 0, 0, 'I can\'t thank you enough... I had hoped for this to be a joyous reunion, but the more I learn, the less glad I am to have asked.$B$BBut I must know.', 0);
INSERT INTO `quest_offer_reward` VALUES (26672, 0, 0, 0, 0, 0, 0, 0, 0, 'How interesting. It\'s been quite a while since I\'ve seen such a ring...', 0);
INSERT INTO `quest_offer_reward` VALUES (26674, 0, 0, 0, 0, 0, 0, 0, 0, 'Master Harris may have been right. I would have been better to leave the past behind.$B$BI\'ve got a new life now. Whether it\'s that of a monster or a man is up to me.$B$BAs for you, I cannot thank you enough for your assistance, regardless of the outcome. Please, take this with my thanks.', 0);
INSERT INTO `quest_offer_reward` VALUES (26676, 0, 0, 0, 0, 0, 0, 0, 0, 'Тысяча благодарностей, $n. Твоя глу... то есть твоя доброта согрела сердце старика!$B$BВот, $gдружище:дорогая;, прими это в знак моей благодарности.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26677, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, thanks. These will do just the trick!', 0);
INSERT INTO `quest_offer_reward` VALUES (26678, 0, 0, 0, 0, 0, 0, 0, 0, 'This must be the doublerum. You pour a glass for Revilgaz, then consider pouring one for yourself...', 0);
INSERT INTO `quest_offer_reward` VALUES (26679, 0, 0, 0, 0, 0, 0, 0, 0, '<The Baron quickly empties his cup.>$B$BAH! Much better, $n. Now, let\'s get down to business. I believe that you and I have a town to save.', 0);
INSERT INTO `quest_offer_reward` VALUES (26680, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank the Nec-...well, thank YOU, $n! You have more than earned your reward.$B$BAha! Happy! Happy nights ahead!!', 0);
INSERT INTO `quest_offer_reward` VALUES (26681, 0, 0, 0, 0, 0, 0, 0, 0, '<Ello looks at the letter, and immediately pales.>$B$BYou fool! You\'ve doomed us all!', 0);
INSERT INTO `quest_offer_reward` VALUES (26682, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. We\'ll be able to focus on greater threats here now.', 0);
INSERT INTO `quest_offer_reward` VALUES (26683, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s right, I used to have a mirror.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26684, 2, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно! Это идеально подойдет. Я глубоко признателен!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26685, 4, 0, 0, 0, 0, 0, 0, 0, 'Ну наконец-то! Устройство для изучения звезд готово! Спасибо тебе, $n. Теперь я могу продолжить исследования...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26686, 21, 0, 0, 0, 0, 0, 0, 0, 'Darkwood residents thank you, $n. You showed; a great ally; Night watch.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26687, 0, 0, 0, 0, 0, 0, 0, 0, 'What now, soldier?$B$BReinforcements? For the Barrens? We\'ve been bleeding supplies and men there for months!$B$BDoes someone need to remind Twinbraid of the attack on Northwatch? Our losses at Honor\'s Stand? What happened to the men I sent to Fort Triumph? We\'ve nothing to spare!$B$BListen $n, if you want to help out our cause, you\'ll focus your efforts here in Dustwallow. Between the deserters and the Horde encroachment... we could use a hand.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26688, 0, 0, 0, 0, 0, 0, 0, 0, 'Твои успехи впечатляют, $n. Похоже, ты на многое $gспособен:способна;. Пожалуй, твоим талантам следует найти более достойное применение.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26689, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты отлично $gвыступил:выступила; против тенеткачей, $n. Но даже теперь, спустя годы после их первого появления и несмотря на то, что мы их убиваем, их остается слишком много.$b$bЯ положусь на мастера Карвина. Он наверняка сумеет покончить с этой проблемой.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26690, 0, 0, 0, 0, 0, 0, 0, 0, '$n, to tell you the truth, I didn\'t believe you\'d be able to get this far, but you\'re certainly |3-6($c), with $g which:c; worth counting. If you really want to join Master Carvin\'s battle, I would be happy to write you a letter of introduction.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26691, 0, 0, 0, 0, 0, 0, 0, 0, 'Прости за холодный прием, $n, ты ведь понимаешь, что я очень занятой человек. Вижу, ты $gпроизвел:произвела; впечатление на Калора. Уверяю тебя, это нелегко. Так или иначе, он рекомендует принять тебя в наши ряды.$b$bВокруг полно ненадежных людей, а нам нужна помощь тех, на кого можно положиться. Мы бьемся против демонов, нежити и их пособников. Не теряй бдительности, осторожности и не доверяй никому, кто не хочет нам помочь.$b$bДа озарит тебя Свет!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26692, 0, 0, 0, 0, 0, 0, 0, 0, 'We\'re closing in on the Blackrock high command. There\'s nowhere left for those dogs to run.', 0);
INSERT INTO `quest_offer_reward` VALUES (26693, 0, 0, 0, 0, 0, 0, 0, 0, '<Keeshan comes in over the Bravo Company field kit.>$B$BNow that the easy part is over, it\'s time for phase two.', 0);
INSERT INTO `quest_offer_reward` VALUES (26694, 0, 0, 0, 0, 0, 0, 0, 0, 'He teleported away? Mentioned Darkblaze? What the hell is going on here.$B$BLooks like it\'s time to strap in and finish this nonsense.', 0);
INSERT INTO `quest_offer_reward` VALUES (26695, 0, 0, 0, 0, 0, 0, 0, 0, 'You brought them! I\'m beginning to think the tide is turning in our favor, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26696, 0, 0, 0, 0, 0, 0, 0, 0, 'Yeah? What can I do for ya?', 0);
INSERT INTO `quest_offer_reward` VALUES (26697, 0, 0, 0, 0, 0, 0, 0, 0, 'I hope those mongrels know how to doggy paddle!$B$BActually, I take that back.', 0);
INSERT INTO `quest_offer_reward` VALUES (26698, 0, 0, 0, 0, 0, 0, 0, 0, 'I should have known these Brashtide were alive and up to no good. I\'d written them off years ago.', 0);
INSERT INTO `quest_offer_reward` VALUES (26699, 0, 0, 0, 0, 0, 0, 0, 0, 'We need to keep moving, $n. Booty Bay won\'t hold out for much longer.', 0);
INSERT INTO `quest_offer_reward` VALUES (26700, 0, 0, 0, 0, 0, 0, 0, 0, 'Attacking your former crewmen...$B$BYou really have grown into a real pirate, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (26701, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar, $R. Welcome to Brackenwall.', 0);
INSERT INTO `quest_offer_reward` VALUES (26702, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Theramore.', 0);
INSERT INTO `quest_offer_reward` VALUES (26703, 0, 0, 0, 0, 0, 0, 0, 0, '$n! You\'re awake! We found you bobbing in the harbor, shortly after the remaining attackers had turned tail. We were worried about you for a while there...$B$BI\'ve been meaning to thank you, $n. Not only did you provide us with the intelligence to prepare for this attack, but you helped fight off the Bloodsail AND the Brashtide. Your name will always be remembered here in Booty Bay.$B$BWe here in Booty Bay pride ourselves on our booty. Care to take first pick?', 0);
INSERT INTO `quest_offer_reward` VALUES (26706, 0, 0, 0, 0, 0, 0, 0, 0, 'Мы справились, $N. Эвакуация началась. Если нам удастся быстро покинуть город, Отрекшиеся останутся ни с чем.', 0);
INSERT INTO `quest_offer_reward` VALUES (26707, 0, 0, 0, 0, 0, 0, 0, 0, 'I thank you, $C... and I\'m sure the worgen that may be returned to sanity with the potion this will make would thank you too.', 0);
INSERT INTO `quest_offer_reward` VALUES (26708, 0, 0, 0, 0, 0, 0, 0, 0, 'The only thing left to deal with now are the orcs holding out at Stonewatch Keep.', 0);
INSERT INTO `quest_offer_reward` VALUES (26709, 0, 0, 0, 0, 0, 0, 0, 0, 'You returned. This is good. The Stone Lords have been asking after you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26710, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done well. Now that these rocklings are free to learn our ways, they will grow tall and strong.$B$BOne day, we will extinguish the gyreworm infestation from our realm, and these rocklings shall no longer be plagued in their youth.', 0);
INSERT INTO `quest_offer_reward` VALUES (26711, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, it\'s you, ma\'am! Sorry, I didn\'t recognize you at first with all of these commoners milling about.$B$BYou people cool your heels! $N is here!$B$BHere are your macaroons, $g sir : ma\'am;. Come back anytime if you need more.', 0);
INSERT INTO `quest_offer_reward` VALUES (26712, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, it\'s you, sir! Sorry, I didn\'t recognize you at first with all of these commoners milling about.$B$BYou people cool your heels! $n is here!$B$BHere are your macaroons, sir. Come back anytime if you need more.', 0);
INSERT INTO `quest_offer_reward` VALUES (26713, 0, 0, 0, 0, 0, 0, 0, 0, '<The Bravo Company field kit beeps loudly.>$B$BCome in, $n. Are you there? If you can hear me: Blackrock has the upper hand. I repeat. Blackrock has the upper hand. GET BRAVO COMPANY OUT OF TH...', 0);
INSERT INTO `quest_offer_reward` VALUES (26714, 0, 0, 0, 0, 0, 0, 0, 0, '<A single tear rolls down Troteman\'s left cheek.>$B$BThey died in defense of the Alliance. In defense of our kingdom! Heroes, all of them, and they will be laid to rest as such.', 0);
INSERT INTO `quest_offer_reward` VALUES (26717, 0, 0, 0, 0, 0, 0, 0, 0, 'Sounds like a close call! But he ran off without harming you further...$B$BHe might be still fighting it. Master Harris must know!', 0);
INSERT INTO `quest_offer_reward` VALUES (26719, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to our humble camp. I wouldn\'t call it pleasant, but it\'s at least somewhat private.$B$BBeg your pardon? A worgen at the farm?', 0);
INSERT INTO `quest_offer_reward` VALUES (26720, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve got him!$B$BNo time to lose, then. We\'ll prepare to administer the serum right away.$B$BJITTERS! Get ready!', 0);
INSERT INTO `quest_offer_reward` VALUES (26721, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh thank you, thank you! You\'ve saved me from having to go out in the woods with those awful things!$B$BAt least for a day, that is...', 0);
INSERT INTO `quest_offer_reward` VALUES (26722, 0, 0, 0, 0, 0, 0, 0, 0, '<In the darkest depths of this earthen tunnel, you spy the faintest gleam. The last piece is found!>', 0);
INSERT INTO `quest_offer_reward` VALUES (26723, 0, 0, 0, 0, 0, 0, 0, 0, 'His clothes and bloodstains? If it\'s not tricks, it\'s something worse...', 0);
INSERT INTO `quest_offer_reward` VALUES (26724, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s heartening to see one so recently recovered be so concerned with the plight of others. Sven spares himself suffering by not dwelling on his own misfortune.', 0);
INSERT INTO `quest_offer_reward` VALUES (26725, 0, 0, 0, 0, 0, 0, 0, 0, '<Buried in the cold dirt, you find a brilliant metal shaft. But there are holes where pieces of the artifact are clearly missing.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26726, 0, 0, 0, 0, 0, 0, 0, 0, 'They will never be forgotten, $n. What you and the rest of Bravo Company sacrificed in order to save Redridge will be a tale told for ages to come.$B$BWhile I can in no way make up for the loss of your friends, I feel that you must be rewarded for such a heroic deed. These are Keeshan\'s possessions - the only things we found when we dredged the lake for his body. Troteman has informed me that Keeshan would have wanted you to take something as the lone surviving member of Bravo Company. Remember them, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (26727, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank the light, he\'s gone...$B$BI\'ll say you\'ve made amends by helping save the lives of those here, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26728, 6, 0, 0, 0, 0, 0, 0, 0, 'Они прислали нам на подмогу тебя?$b$b<Алтея вздыхает.>$b$bНадеюсь, у тебя получится.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26729, 0, 0, 0, 0, 0, 0, 0, 0, 'These will do for my purposes. Though we can\'t activate the altar with bracers alone...', 0);
INSERT INTO `quest_offer_reward` VALUES (26730, 0, 0, 0, 0, 0, 0, 0, 0, 'We have what we need. Are you ready to activate the Altar of Naias, $n?', 0);
INSERT INTO `quest_offer_reward` VALUES (26731, 0, 0, 0, 0, 0, 0, 0, 0, 'My colleagues within the Kirin Tor will be happy to learn that the heart is ours.$B$BThank you for your assistance, $n. I couldn\'t have done it without you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26732, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично сработано, $n. Сейчас мы узнаем, что там у них за эликсир...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26733, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, just what you need. Thanks. Wait a while, and I\'ll do a quick analysis.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26734, 0, 0, 0, 0, 0, 0, 0, 0, '<Brother Nimez opens the sack so you can put the crystal down there. >$B$BI knew they\'d have this stuff. I want to send it to my friends at Fort Livingston, south. Hopefully they\'ll have more luck with that substance. $B$BThank you for everything, $n.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26735, 5, 0, 0, 0, 0, 0, 0, 0, 'Они его УБИЛИ? А я все это время думал, что они у него под железной пятой и даже пикнуть не смеют...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26736, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $n. Похоже, мои подозрения подтверждаются... Эти трое заключенных избежали безумия, которое охватило Курцена и его людей.$B$BПеред твоим прибытием Эмерина телепортировала их в Штормград – восстановиться после перенесенных мучений. Они обещали скоро вернуться.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26737, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n. Об остальных мы позаботимся сами.$B$BТеперь, когда Курцен больше не мешает, мы продолжим экспедицию в Тернистую долину, которая... ох...$B$BЛадно, ничего. Ты посмотри, что у нас тут есть в запасе – может, выберешь себе что-то подходящее.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26738, 396, 0, 0, 0, 0, 0, 0, 0, 'Yes, this kid seems to be very attached to you. You are lucky - at this age they are still calm and peaceful, and they can be easily trained. Leave the cub to yourself, $n ... It will still be useful to you.$B$BBy the way ...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26739, 396, 0, 0, 0, 0, 0, 0, 0, 'There, now we\'re growing! You see, these lizards are growing fast. You\'re lucky to have a pet. Try to keep it around for as long as you can, $n. I\'m sure you won\'t regret it later.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26740, 5, 1, 25, 0, 0, 0, 0, 0, 'Oh, I see - a faulty pot of Krazek! Excellent device, but sometimes the springs or gears fail. Let\'s see what happened to this thing... $B$BAha! The steam generator\'s rusty, the oil seal\'s flying, and the cheater\'s teeth broke off. It\'s weird how they managed to at least boil a turnip!$B$BCan I fix it? Of course! $B$BBut not for free. All that\'s free is cheese in a mousetrap...', 23877);
INSERT INTO `quest_offer_reward` VALUES (26742, 0, 0, 0, 0, 0, 0, 0, 0, '<Berrin studies the inscriptions on the totem.>$B$BI think I can read this text... Fascinating!$B$BTsul\'kalu, Mahamba, Pogeyan... what are these names?', 0);
INSERT INTO `quest_offer_reward` VALUES (26743, 0, 0, 0, 0, 0, 0, 0, 0, 'First you save me from the madman\'s jail cell, and now you bring me some keys to understanding trollish shamanism.$B$BThis is turning out to be a pretty good week!$B$BThank you again, $n. I will see to it that you have the support of the Wildhammer clan as you continue on your journeys.', 0);
INSERT INTO `quest_offer_reward` VALUES (26744, 1, 66, 0, 0, 0, 0, 0, 0, 'Честно говоря, я сообщал об этом в Штормград – жене и сыну. Они любят истории о моих делах в джунглях, а видимся мы редко. Так что мне любая помощь кстати.$B$BСпасибо тебе, $n.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26745, 71, 153, 396, 0, 0, 0, 0, 0, 'Так, что тут у нас? Молодой ящер со своим любимцем |3-4($r). Ха!$B$BЛадно, сейчас важнее понять, что это за череп. Так, не обычный тролль. Он был очень большим... $B$B<Осборн стучит по черепу.>$B$BИ очень сильным. Знаешь, я как-то хотел освоить магию троллей. Интересно, насколько она сильна? Давай опробуем ее на этом приятеле, а?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26746, 0, 0, 0, 0, 0, 0, 0, 0, 'These will provide a bit of... what\'s the troll word? Voodoo? Juju? Tiklabang?$B$BIt doesn\'t matter. If we\'re going to re-build a troll, we need troll magic, and these fetishes are full of it.', 0);
INSERT INTO `quest_offer_reward` VALUES (26747, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $R. We have a head, a body, and some juju-voodoo-mojo nonsense. Now, just a little shock of electricity to get things started, and...', 0);
INSERT INTO `quest_offer_reward` VALUES (26748, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad to see you\'ve come to your senses.$B$BCome and visit us in Zul\'Gurub sometime, $R. I\'ll be watching...', 0);
INSERT INTO `quest_offer_reward` VALUES (26749, 0, 0, 0, 0, 0, 0, 0, 0, 'Mandokir is alive, yes.$B$BYou are responsible, yes.$B$BYou may be able to stop him still, yes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26750, 0, 0, 0, 0, 0, 0, 0, 0, 'So you\'ve come.$B$BFleshlings just like you killed my daughter... my only daughter. With no provocation.$B$BNot sated at stripping me of my family, more of you flocked to Deathwing to serve him in ravaging my home.$B$BYou\'re quite arrogant to come before me asking for MY help. I\'m tempted to crush you where you stand.', 0);
INSERT INTO `quest_offer_reward` VALUES (26751, 0, 0, 0, 0, 0, 0, 0, 0, 'Balance is restored - for now. We may call on you again in the future, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26752, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya\'ve done well, brotha. Da Stonemother be listenin\' and da World Pillar is not far from our reach.', 0);
INSERT INTO `quest_offer_reward` VALUES (26753, 0, 0, 0, 0, 0, 0, 0, 0, '<In the dirt of this alcove, your eye catches the shine of brilliant metal. You\'ve found another piece of the artifact!>', 0);
INSERT INTO `quest_offer_reward` VALUES (26754, 0, 0, 0, 0, 0, 0, 0, 0, 'May the disgusting monster stay dead this time!$B$BI\'ll head down there and scatter his remains to ash myself, $n. You\'ve done a great thing; perhaps in time Duskwood can return to its former peaceful state.', 0);
INSERT INTO `quest_offer_reward` VALUES (26755, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s a great start, $n, but we\'re going to need you to fly over into the town and stir up some trouble.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26760, 0, 0, 0, 0, 0, 0, 0, 0, 'Success... Finally! I can\'t thank you enough for giving me the chance to work the cure myself.$B$BOur new friend may be in need of some help as well. You might want to speak with him.', 0);
INSERT INTO `quest_offer_reward` VALUES (26761, 1, 1, 5, 0, 0, 0, 0, 0, 'Я слышал о том, что Братство Справедливости снова активировалось. Я надеялся на новости получше, но оказалось, что Приозерье атакуют! Мы то и дело теряем жителей. Наши собственные войска рассредоточены и не могут взять ситуацию в свои руки. Нам нужен герой, который бы нам помог!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26762, 1, 0, 0, 0, 0, 0, 0, 0, 'That was a nice bit of work, $n.$B$BWith our constructs back up and running behind their main lines, it\'s only a matter of time now before we make the big push through and rescue the Stonefather.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26763, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, эти подойдут. Погоди-ка минутку.$B$B<Кразек бросает камни в котел и трясет его некоторое время.>$B$BНу, вот и готово!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26765, 5, 1, 0, 0, 0, 0, 0, 0, 'Он как новый! Отлично, теперь можно готовить ужин! Большое спасибо, $n.$B$BНадеюсь, с Кразеком все прошло гладко.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26766, 0, 0, 0, 0, 0, 0, 0, 0, 'That should be enough to lure the beast, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26768, 0, 0, 0, 0, 0, 0, 0, 0, 'Let\'s see if the snare can ground something larger than a drake.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26769, 0, 0, 0, 0, 0, 0, 0, 0, '<Nozzlepot snatches the bag of gems from you.>$B$BGazlowe calls this 10%? You tell him I\'m going to weigh these!$B$BI suppose he expects me to pay you a delivery fee, as well? Fine, fine. Have some spoils!', 0);
INSERT INTO `quest_offer_reward` VALUES (26770, 1, 0, 0, 0, 0, 0, 0, 0, 'A few less stone troggs is always a good thing. Glad to see that you\'re a decent $c after all.$B$BWe\'ll soon know the exact location where they\'re keeping my father captive. Hope you survive until then.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26771, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n.  Here\'s to hoping the trap performs as well when we try it on Abyssion.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26772, 0, 0, 0, 0, 0, 0, 0, 0, 'You bring feathers, yes.$B$BYou can follow directions, yes.', 0);
INSERT INTO `quest_offer_reward` VALUES (26773, 0, 0, 0, 0, 0, 0, 0, 0, 'What did Priestess tell you?$B$BThe raptor is daughter of Ohgan, heir to Mandokir\'s seat.$B$BHah!', 0);
INSERT INTO `quest_offer_reward` VALUES (26776, 0, 0, 0, 0, 0, 0, 0, 0, '<You explain to Ghaliri what has happened up to this point.>$B$BIt would seem that Jin\'do has survived, and he is rallying his brothers to once again summon Hakkar into this world. It was good of you to come to speak with me.$B$BThere is not much we can do now but to wait, and watch. Jin\'do is weak, and prime for striking, but I must wait for word from Rastakhan before issuing orders. Until then, you and your raptor will have to wait.', 0);
INSERT INTO `quest_offer_reward` VALUES (26777, 0, 0, 0, 0, 0, 0, 0, 0, 'You have brought peace to those who knew only misery, $n. Be proud.', 0);
INSERT INTO `quest_offer_reward` VALUES (26778, 0, 0, 0, 0, 0, 0, 0, 0, 'The cries grow softer for now. Light bless you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26779, 0, 0, 0, 0, 0, 0, 0, 0, 'As promised, your reward. You do the troll nation proud.', 0);
INSERT INTO `quest_offer_reward` VALUES (26780, 0, 0, 0, 0, 0, 0, 0, 0, 'I can see the difference already. I can\'t wait until later tonight, when I get to perform my observations again!', 0);
INSERT INTO `quest_offer_reward` VALUES (26781, 0, 0, 0, 0, 0, 0, 0, 0, 'You retrieved the eye! Your might is the stuff of legends, $n!$B$BAnd to think my brother and sister said we wouldn\'t find anything of worth here in the Vale. Hah! And I\'m just getting started!', 0);
INSERT INTO `quest_offer_reward` VALUES (26782, 0, 0, 0, 0, 0, 0, 0, 0, '<Wulfred peers into the box.>$B$BThis... this is not like what I expected. I had thought it would be a... a...$B$B<His eyes glaze over.>$B$BAhem! I\'m sorry. I\'m not sure what came over me. I\'ll be sending this south, to the Explorers\' League Digsite in the Cape. Maybe they\'ll know what to do with it...', 0);
INSERT INTO `quest_offer_reward` VALUES (26783, 0, 0, 0, 0, 0, 0, 0, 0, '<Wulfred peers into the box.>$B$BThis... this is not like what I expected. I had thought it would be a... a...$B$B<His eyes glaze over.>$B$BAhem! I\'m sorry. I\'m not sure what came over me. I\'ll be sending this south, to the Explorers\' League Digsite in the Cape. Maybe they\'ll know what to do with it...', 0);
INSERT INTO `quest_offer_reward` VALUES (26784, 4, 1, 0, 0, 0, 0, 0, 0, 'These are perfect! The restless waters of that troubled river will now do much more than move silt or stone... They\'ll move you across continents.$B$BFinish up the rest of your business here in Dragonmaw and then we should be able to proceed.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26785, 0, 0, 0, 0, 0, 0, 0, 0, 'Tobias is staying in town still?$B$BMaster Harris warned him not to pursue his brother\'s fate. We all lead new lives now, after all. But the past is hard to let go of...', 0);
INSERT INTO `quest_offer_reward` VALUES (26786, 1, 273, 0, 0, 0, 0, 0, 0, 'Your Warchief is decisive, $N. He makes things happen. I like him.$B$BLet\'s talk about this portal that he wants!', 15595);
INSERT INTO `quest_offer_reward` VALUES (26787, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you! Oh, thank you. I never thought I\'d be happy to smell those brains, but I am now that I didn\'t have to get them!$B$BHere\'s your reward as promised...', 0);
INSERT INTO `quest_offer_reward` VALUES (26788, 21, 1, 0, 0, 0, 0, 0, 0, '<Ornak allows the muck to drip from his fingertips.>$B$BNow that\'s the stuff! I\'ll start mixing and diluting this with gravel and water. Just a few handfuls of this can enhance enough cement for an entire stone tower.$B$BWe\'ve got big plans for this port, $N. You\'ll see!', 15595);
INSERT INTO `quest_offer_reward` VALUES (26791, 4, 0, 0, 0, 0, 0, 0, 0, 'They won\'t have any more of those fungal giants to throw against us!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26792, 1, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. We\'ve never had such a problem in Deepholm before the destruction of the World Pillar.$B$BYour Earthen Ring must repair the damage before it\'s too late.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26793, 0, 0, 0, 0, 0, 0, 0, 0, 'Morgan Ladimore?$B$BAhh, yes, of course. His was a long and sorrowful tale. I knew him, well, before he left for the war, but that was the last time I saw him. A noble and good man, he was, but he suffered a bad end.$B$BHere, I should have something here that can tell the tale better than my own recollections...', 0);
INSERT INTO `quest_offer_reward` VALUES (26794, 0, 0, 0, 0, 0, 0, 0, 0, 'Morgan Ladimore? Never heard of...$B$BOh, you mean Mor\'Ladim! I guess you haven\'t heard the story?$B$BWell, I\'ll give you the short version. A powerful undead calling itself Mor\'Ladim has been wandering Duskwood. From what we\'ve gathered, it appears to be the undead body of Morgan Ladimore, of whom you apparently know. He\'s been causing us all sorts of problems, attacking Watchmen on patrol and killing people.$B$B<She sizes you up.>$B$BYou seem to be capable enough, maybe you could lay him to rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (26795, 0, 0, 0, 0, 0, 0, 0, 0, 'You killed him? That\'s no small accomplishment, $n! On behalf of the people of Darkshire and the Night Watch, I thank you.$B$BAh... there is one small matter, however...', 0);
INSERT INTO `quest_offer_reward` VALUES (26796, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes? My father...$B$B<Her eyes become downcast.>$B$BI wish... there was something I could have done for him... If only I had talked to him before he...', 0);
INSERT INTO `quest_offer_reward` VALUES (26797, 0, 0, 0, 0, 0, 0, 0, 0, '<A ghostly voice sounds on the wind...>$B$BThis is...? Sarah? Could it be she\'s still alive? The weight is removed from my shoulders...$B$B$n. Take my sword, Archeus. As my soul is put to rest, I have no more need for it. It was forged to do good, and though I have proved myself unworthy to hold it, perhaps you will carry on the Light through it.$B$BLys, my love...', 0);
INSERT INTO `quest_offer_reward` VALUES (26798, 6, 1, 274, 0, 0, 0, 0, 0, '$C, you\'ve returned from Dragonmaw. The portal is functioning then? Good.$B$BBefore you return to your work there, we\'ve got some dirty business to clean up right here in Orgrimmar...', 15595);
INSERT INTO `quest_offer_reward` VALUES (26799, 0, 0, 0, 0, 0, 0, 0, 0, 'Not even the power of the val\'kyr was enough to save these creatures.  They were doomed from the beginning. I have obtained some equipment for you, $n.  It\'s not much, but it will help.  Please, take your pick. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26800, 0, 0, 0, 0, 0, 0, 0, 0, 'Great work $n.  I knew you weren\'t useless. Here, take one of these.  We deathguards have piles of these things just sitting around. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26801, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re growing in strength, $n.  I\'ve seen your type before.  You will be moving out of Deathknell and on to greater things before long.  Just make sure you don\'t lose sight of where you started. You have much to accomplish, $c. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26802, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. This armor should help you out. Hopefully, it should do you more good than the last one to wear it... ', 0);
INSERT INTO `quest_offer_reward` VALUES (26803, 0, 0, 0, 0, 0, 0, 0, 0, 'So you need to get to Razor Hill in a hurry? That\'s no problem at all. One of my wind riders will get you there very quickly and it only costs a few coins to hire one.', 0);
INSERT INTO `quest_offer_reward` VALUES (26804, 0, 0, 0, 0, 0, 0, 0, 0, 'Eitrigg sent you? The warchief must be getting anxious to read my reports. I\'d\'ve sent them long ago, but each day we discover more about the Alliance\'s plans for invading Durotar.', 0);
INSERT INTO `quest_offer_reward` VALUES (26805, 0, 0, 0, 0, 0, 0, 0, 0, 'Wulfred has a good eye. I think you\'ll do just fine here at my camp.', 0);
INSERT INTO `quest_offer_reward` VALUES (26806, 0, 0, 0, 0, 0, 0, 0, 0, 'Ready to return to Orgrimmar with those reports? I can arrange a flight for you right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (26807, 0, 0, 0, 0, 0, 0, 0, 0, '<Eitrigg examines the reports.>$B$BThe Alliance has grown bolder than we\'d thought! I\'ll talk to the warchief about sending reinforcements right away. Thank you for getting these reports to me so quickly. I hope this coin is enough to cover your travel expenses.', 0);
INSERT INTO `quest_offer_reward` VALUES (26808, 1, 0, 0, 0, 0, 0, 0, 0, '<Troll expands the bundle. >$B$BThank you very much. Say $gto his young yard friend:his young yard friend; that I\'m not upset about anything. I\'m just waiting. Waiting for you.$B$BYou are here the most promising: the most promising: r; |3-6($r) in all this strange city. I have a job for you, if you\'re interested.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26809, 1, 1, 0, 0, 0, 0, 0, 0, 'Так я и думала. Занзил слишком высокого о себе мнения, чтобы впутываться в дрязги между Кровавым Скальпом и Дробителями черепов. Нет, у него планы помасштабнее... Мастер проклятий Джин\'до хочет с его помощью вернуть Хаккара в Зул\'Гуруб.$B$BХорошо, что мы узнали об этом сейчас. Теперь ясно, как нужно действовать.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26810, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gопоздал:опоздала;. Занзил ушел из Абораза.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26811, 0, 0, 0, 0, 0, 0, 0, 0, 'You arrived successfully at Zul\'Gurub, the ruined capital of the once mighty Gurubashi Empire.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26812, 0, 0, 0, 0, 0, 0, 0, 0, 'You stopped Zanzila from resurrecting High Priestess Jacklick. Without one of the priestesses, Jin\'do will probably be difficult to summon Hakkar.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26813, 0, 0, 0, 0, 0, 0, 0, 0, 'You found yourself in the depths of Zul\'Guruba. Snake statues tell us that you\'ve reached the terrace of the high priest Venoxis, the main follower of the serpent god Hetis.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26814, 0, 0, 0, 0, 0, 0, 0, 0, 'Тебе нужно было убить Занзила Отверженного, ничтожного отшельника, изгнанного племенем Гурубаши, а ты что $gсделал:сделала;? $GВорвался:Ворвалась; в столицу Гурубаши и $gубил:убила; одного из их верховных жрецов! Даже почти двух! Такое безрассудное и наглое поведение можно назвать только одним словом:$B$BГероизм.$B$BМастер проклятий Джин\'до все еще бродит в стенах Зул\'Гуруба, но после того, как ты $gубил:убила; Джеклик, он потерял одну из своих ног. Эту битву мы выиграли.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26815, 463, 1, 0, 0, 0, 0, 0, 0, '<Dusk carefully examines the contents of the vessel. >$B$BThis mixture is just... gorgeous! Let\'s see what she does with this artifact of Titans!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26816, 1, 397, 0, 0, 0, 0, 0, 0, 'Поразительно! Только представь, сколько еще этому можно найти применений!$B$BЕще раз спасибо, $n. Если тебе нужно будет что-нибудь смешать – ЧТО УГОДНО – ты всегда можешь обратиться к Пузырю.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26817, 7, 1, 0, 0, 0, 2000, 0, 0, 'Ты не представляешь, как трудно найти пресную воду в Тернистой долине! Неудивительно, что наги так ревностно охраняют свое святилище!$B$BТак, ладно, начнем кашеварить!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26818, 273, 1, 24, 0, 0, 0, 0, 0, '<Lenzie feels the hides and smiles. >$B$BHow soft and fluffy! Buyers will be delighted!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26819, 4, 1, 0, 0, 0, 0, 0, 0, 'Ура! Мы так давно не видели акириса!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26820, 1, 4, 0, 0, 0, 0, 0, 0, 'Эти статуи прекрасны...$B$BНу и ну, а я и не знала, что эти наги такие мастера! Спасибо, $n!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26821, 5, 0, 0, 0, 0, 0, 0, 0, 'Потрясающе!$B$BМне потребуется время, чтобы это изучить. Ты, может, пойдешь прогуляешься по Пиратской Бухте и зайдешь попозже?', 23877);
INSERT INTO `quest_offer_reward` VALUES (26822, 66, 0, 0, 0, 0, 0, 0, 0, 'Кхм! А-а-а... ну...$B$BХорошая работа! Поздравляю, то есть спасибо, что всех $gперебил:перебила;.$B$BВот, держи.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26823, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Первый пункт – добыча ингредиентов – выполнен. Переходим ко второму пункту – добыть еще ингредиентов!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26824, 273, 1, 0, 0, 0, 0, 0, 0, 'Так и знал, что от алхимика никакого толку. Все равно спасибо за помощь, $n. Ты $gсделал:сделала; все, что $gмог:могла;.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26825, 0, 0, 0, 0, 0, 0, 0, 0, 'So once again, it falls upon tiny Dask to get the Explorers\' League work done! Alright... let\'s see what you\'ve got in there.', 0);
INSERT INTO `quest_offer_reward` VALUES (26826, 0, 0, 0, 0, 0, 0, 0, 0, 'So once again, it falls upon tiny Dask to get the Explorers\' League work done!  Alright... let\'s see what you\'ve got in there.', 0);
INSERT INTO `quest_offer_reward` VALUES (26827, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done what I thought not possible. Quite impressive, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (26828, 0, 0, 0, 0, 0, 0, 0, 0, 'And so you arrive, fleshling. As promised.', 0);
INSERT INTO `quest_offer_reward` VALUES (26829, 0, 0, 0, 0, 0, 0, 0, 0, 'Their blood upon the earth is a sweet thing. I shall cherish every moment, slowly extinguishing the stragglers from my realm.', 0);
INSERT INTO `quest_offer_reward` VALUES (26830, 274, 1, 273, 0, 0, 0, 0, 0, 'Sauranok, Sauranok. His own visions worked against him. Twilight\'s Hammer probably corrupted him from afar. Bah! Weak! We\'ll speak his name no more.$B$BI told him you were investigating the airship docks, $N. To flush him out. Yes, I used you as bait. You served your Horde well, and deserve a reward for outing this viper from our midst.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26831, 0, 0, 0, 0, 0, 0, 0, 0, 'It is good that you were here. I find my size to be inhibiting when it comes to dragon riding, strangely.', 0);
INSERT INTO `quest_offer_reward` VALUES (26832, 0, 0, 0, 0, 0, 0, 0, 0, 'You let Lorthuna escape? I warned you of this.$B$BStill, their forces are shattered and their resources cut off. There is little they can do to me or my allies.', 0);
INSERT INTO `quest_offer_reward` VALUES (26833, 0, 0, 0, 0, 0, 0, 0, 0, 'Gorsik has arrived. All of the pieces of the World Pillar are here, thanks to you.$B$BWhat you have done for us is immeasurable. These final moments shall be the most crucial. I hesitate to ask more of you, but I\'d like you to stay with us and see this through.$B$BThe Twilight\'s Hammer was devastated by your attacks, but not destroyed. It\'s no stretch to assume they will try to stop us from completing our ritual.', 0);
INSERT INTO `quest_offer_reward` VALUES (26834, 3, 0, 0, 0, 0, 0, 0, 0, 'Good to see you again, $n. Now we\'ll get somewhere.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26835, 1, 0, 0, 0, 0, 0, 0, 0, '$n, we have good news and a slight problem....', 19342);
INSERT INTO `quest_offer_reward` VALUES (26836, 4, 0, 0, 0, 0, 0, 0, 0, '$n! You did it! How will we ever repay you?', 19342);
INSERT INTO `quest_offer_reward` VALUES (26838, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to our camp, $C. You couldn\'t have arrived at a better time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26840, 1, 11, 0, 0, 0, 0, 0, 0, '$N! I trust Orgrimmar fares well?$B$BAs you can see, construction of Dragonmaw Port continues at a breakneck pace. If my people questioned the Horde\'s power before... the sight of this imposing edifice and the speed in which it was constructed leaves no doubt.$B$BA new day dawns for the Dragonmaw.', 15595);
INSERT INTO `quest_offer_reward` VALUES (26841, 0, 0, 0, 0, 0, 0, 0, 0, 'I will be your mentor and guide for now. It is my duty to teach you the arcane arts and how you might best fit in among our people. As you learn and grow in power, you will learn an immense variety of spells that should allow you to deal with nearly any situation. But until then, return to me as often as you like and I will teach you what I can in the ways of our kind. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26842, 0, 0, 0, 0, 0, 0, 0, 0, 'Nicely done, $glad:lass;. I could use a $c like you around here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26843, 0, 0, 0, 0, 0, 0, 0, 0, 'So I was right! Now the question is what the little freaks were plotting...', 0);
INSERT INTO `quest_offer_reward` VALUES (26844, 0, 0, 0, 0, 0, 0, 0, 0, 'Good, good. Now we can see what\'s going on down in that mine.', 0);
INSERT INTO `quest_offer_reward` VALUES (26845, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s a gnoll head. Are you trying to pull some funny business, $N?$B$BIf you\'re not, I\'ve not the faintest idea what\'s going on here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26846, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m sure that only worked because the troggs aren\'t bright to begin with. But then again, us dwarves were once creatures of the earth as well.$B$BBeing bossed around by a kobold doesn\'t sound quite dignified, and I\'m glad we didn\'t get to find out if they could pull that off.', 0);
INSERT INTO `quest_offer_reward` VALUES (26854, 0, 0, 0, 0, 0, 0, 0, 0, 'Here is the corpse of a dwarf -- mauled, frozen, and picked clean by mountain scavengers.  Clutched in its hand is a book containing the roughly scribbled notes of Mori Hildelve.  And around its frame, still in superior condition, is a Brigadier\'s Vest. This grisly scene must have been where the Siege Engine pilot met his end. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26855, 0, 0, 0, 0, 0, 0, 0, 0, 'This is grim news, for Hildelve was a good friend.  And I wish I could have been there with you when you found the beast, Mangeclaw! Thank you, $n. Your deed will be remembered by the pilots of the Ironforge Siege Brigade. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26856, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve done it, $n! You\'ve upturned the Searing Blade\'s invasion before it could even start! Take this, as a token of my thanks.', 0);
INSERT INTO `quest_offer_reward` VALUES (26857, 0, 0, 0, 0, 0, 0, 0, 0, 'That ought to get the dragon\'s attention, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26858, 0, 0, 0, 0, 0, 0, 0, 0, 'My children will sing songs of your glories, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (26860, 0, 0, 0, 0, 0, 0, 0, 0, 'Much obliged! The rump\'s part of my own secret twist on the recipe... but here\'s instructions on how to make it with any kind of bear meat.$B$BAnd here\'s a fresh batch of me own. Mind you eat them fresh!', 0);
INSERT INTO `quest_offer_reward` VALUES (26861, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent.  When we make our move against Abyssion, we should be able to do so without reinforcements coming to his aid.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26862, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $C. The Molten Elementals\' spirits are finally back to rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (26863, 0, 0, 0, 0, 0, 0, 0, 0, 'Did you see a lot of kobolds in there? The thought of their grimy hands clawing around our mine makes my stomach rumble!', 0);
INSERT INTO `quest_offer_reward` VALUES (26864, 0, 0, 0, 0, 0, 0, 0, 0, 'Gnolls and kobolds, and troggs? Oh my.$B$BI\'d laugh if I didn\'t have another twist in the story for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26865, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been expecting your arrival, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26866, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been expecting your arrival, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26867, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been expecting your arrival, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26868, 0, 0, 0, 0, 0, 0, 0, 0, 'Worked like a charm, didn\'t it? Maybe Kadrell will get off my darn back now what with savin\' the whole town and all!$B$BI suppose you\'ve done earned yourself some proper goods.', 0);
INSERT INTO `quest_offer_reward` VALUES (26869, 0, 0, 0, 0, 0, 0, 0, 0, '<This is it. This is the stone Diamant sent you for.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26870, 0, 0, 0, 0, 0, 0, 0, 0, 'I can already feel the balance returning! Quick, talk to Muyoh to help him awaken Naralex!', 0);
INSERT INTO `quest_offer_reward` VALUES (26871, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, fleshling!\r\n\r\nThe gyreworms are among our most dangerous enemies.\r\n\r\nThe other Stone Lords shall have trouble not turning their head to this accomplishment. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26872, 0, 0, 0, 0, 0, 0, 0, 0, 'Your efforts shall not go unnoticed, $n.$b$bThank you for your dedication.', 0);
INSERT INTO `quest_offer_reward` VALUES (26873, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $n. Hopefully we can use these to prevent further nightmares.', 0);
INSERT INTO `quest_offer_reward` VALUES (26875, 0, 0, 0, 0, 0, 0, 0, 0, 'The Twilight forces continue to stream in, but it looks like the Earthen Ring defenders can handle them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26876, 0, 0, 0, 0, 0, 0, 0, 0, 'Your efforts in repairing the World Pillar are invaluable, $n.  Once the Temple of Earth\'s structure is stable again, the rift between the worlds will begin to stabilize.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26878, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, $C? What brings you to this place?', 0);
INSERT INTO `quest_offer_reward` VALUES (26881, 0, 0, 0, 0, 0, 0, 0, 0, 'Ashelan was good to send you. There is indeed a wicked plot underway in this corrupted place.', 0);
INSERT INTO `quest_offer_reward` VALUES (26882, 0, 0, 0, 0, 0, 0, 0, 0, 'Kelris has eluded us for quite some time.$b$bIt seems as though whenever evil made its presence known in these parts, Kelris had played a role. For some time we considered him either dead or missing.$b$bBut this makes perfect sense. By ending his reign you have spared the lives of many innocent people.$b$bTo sacrifice someone to a servant of an Old God for one\'s personal gain is beyond reproachful!$b$bYou have done a great deed, $n. I thank you on behalf of Ashenvale.$b$bIf only Thaelrid had survived....', 0);
INSERT INTO `quest_offer_reward` VALUES (26883, 0, 0, 0, 0, 0, 0, 0, 0, 'You are no doubt of brave and noble blood, $n. We and all of the people of Ashenvale commend you for your efforts against evil.', 0);
INSERT INTO `quest_offer_reward` VALUES (26884, 0, 0, 0, 0, 0, 0, 0, 0, 'You have undoubtedly faced great peril to obtain these.$b$bYour actions have been selfless. It is for the good of all Azeroth that you help me with my research. I thank you, $n, for all that you have done.$b$bNow if you\'ll excuse me I have much work to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (26885, 0, 0, 0, 0, 0, 0, 0, 0, 'You found the manuscript! What a joyous day this is! I will study it exhaustively and learn all there is to know.$B$BI\'ll make full sentinel yet!$B$BYour service to us is more than you know, $N. I hope that this gift shows you my gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (26886, 0, 0, 0, 0, 0, 0, 0, 0, 'Wonderful! I hope you didn\'t get hit with too many cannonballs.', 0);
INSERT INTO `quest_offer_reward` VALUES (26887, 0, 0, 0, 0, 0, 0, 0, 0, 'More gold! We\'ll be sure to give some of this to Mister Noggenfogger to smoothly ensure our continued relations.$B$BAnd only a little bit of it will go back to Gnomeregan for experimental weapons development!', 0);
INSERT INTO `quest_offer_reward` VALUES (26888, 0, 0, 0, 0, 0, 0, 0, 0, 'The beast is slain. I can feel his dark presence fading away forever.', 0);
INSERT INTO `quest_offer_reward` VALUES (26889, 0, 0, 0, 0, 0, 0, 0, 0, 'You know, I\'m usually not impressed when I see a $r, but I\'ve heard about what you\'ve been doin\' for the Steamwheedle. You\'re alright, kid.', 0);
INSERT INTO `quest_offer_reward` VALUES (26890, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! These will do nicely. Here\'s a reward for your hard work.', 0);
INSERT INTO `quest_offer_reward` VALUES (26891, 0, 0, 0, 0, 0, 0, 0, 0, 'This is exactly what I need!  A fathom core is an incredible well of information that we will be able to draw much good from.  Whatever the Twilight\'s Hammer is up to in there - and believe me when I say it is no good - my comrades and I will now uncover.$B$BYou\'ve done well here today; the Earthen Ring looks upon you warmly for assisting us.  You\'ve also helped the Horde as a whole, and for that you should be proud.', 0);
INSERT INTO `quest_offer_reward` VALUES (26892, 0, 0, 0, 0, 0, 0, 0, 0, 'I can hear their cries from here! Maybe this will show them how foolish it is to wish for the return of an Old God.', 0);
INSERT INTO `quest_offer_reward` VALUES (26894, 0, 0, 0, 0, 0, 0, 0, 0, 'Did Commander Grimfang send you? I\'ve asked him to find suitable recruits to aid me here.', 0);
INSERT INTO `quest_offer_reward` VALUES (26895, 0, 0, 0, 0, 0, 0, 0, 0, 'Another proud $C, lured by the promise of fame! Of wealth! Of VIOLENCE!$B$BYes, welcome, friend. Welcome... to THUNDERDROME!', 0);
INSERT INTO `quest_offer_reward` VALUES (26896, 0, 0, 0, 0, 0, 0, 0, 0, 'Another proud $C, lured by the promise of fame! Of wealth! Of VIOLENCE!$B$BYes, welcome, friend. Welcome... to THUNDERDROME!', 0);
INSERT INTO `quest_offer_reward` VALUES (26897, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank Elune you\'re here! We could use all the help we can get. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26898, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank Elune you\'re here! We could use all the help we can get.', 0);
INSERT INTO `quest_offer_reward` VALUES (26899, 0, 0, 0, 0, 0, 0, 0, 0, '<The Flaming Eradicator appears to concentrate.>$B$BI spare your life for your help, $r. Leave now...before I change my...mind.', 0);
INSERT INTO `quest_offer_reward` VALUES (26901, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n! Это именно то, что нужно!', 23877);
INSERT INTO `quest_offer_reward` VALUES (26903, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks again, friend! I told you I\'d make it worth your while.', 0);
INSERT INTO `quest_offer_reward` VALUES (26904, 0, 0, 0, 0, 0, 0, 0, 0, 'As you continue to gain power, return to me and I will teach you new spells and techniques.', 0);
INSERT INTO `quest_offer_reward` VALUES (26905, 0, 0, 0, 0, 0, 0, 0, 0, 'Прими от меня этот дар. Сегодня ты $gзаслужил:заслужила; мое благословение.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26906, 0, 0, 0, 0, 0, 0, 0, 0, '< The spirit of Agamaghana is looking at you. You have his voice in your head.$B$BI\'ve been waiting for you. I was waiting for you to come.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26907, 0, 0, 0, 0, 0, 0, 0, 0, 'Сегодня ты $gпотрудился:потрудилась; на славу. Советники Чарлги представляли для нас немалую угрозу.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26908, 0, 0, 0, 0, 0, 0, 0, 0, 'Only a veteran like you could brave the dangers lurking here.', 19116);
INSERT INTO `quest_offer_reward` VALUES (26909, 0, 0, 0, 0, 0, 0, 0, 0, '<The shards pulse softly, and a whisper slowly echoes through your head.>$B$BHelp...', 0);
INSERT INTO `quest_offer_reward` VALUES (26910, 0, 0, 0, 0, 0, 0, 0, 0, 'So you received my note? Excellent. You\'ve proven yourself quite capable in dealing with those orcs. As you continue to grow in power, I will teach you some new techniques. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26911, 0, 0, 0, 0, 0, 0, 0, 0, 'I know, $n. I should not have sent you.$B$BForgive me... the whispers of the past are sometimes too strong.', 0);
INSERT INTO `quest_offer_reward` VALUES (26912, 0, 0, 0, 0, 0, 0, 0, 0, 'As the eldritch shackles are placed around the Shards, an unhuman wail is heard from far below. The scream is followed by a roar.$B$BThe Shards of Myzrael no longer invoke feelings of warmth and security as they once did. Now they pulse with an open menace... as if the thing beneath them holds ill will toward the surface.', 0);
INSERT INTO `quest_offer_reward` VALUES (26913, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re a quick learner, $n. Return to me as you grow stronger and I\'ll show you other combat techniques.', 0);
INSERT INTO `quest_offer_reward` VALUES (26914, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you have already mastered Immolation! I look forward to instructing you in the future. Do not forget to return for further lessons as you grow in power.', 0);
INSERT INTO `quest_offer_reward` VALUES (26915, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve caught on quickly, $n, and a quick mind will serve you well. Return to me as you grow in power and I will show you new techniques.', 0);
INSERT INTO `quest_offer_reward` VALUES (26916, 0, 0, 0, 0, 0, 0, 0, 0, 'Have you mastered the use of Arcane Missiles already? You are a quick study in the arcane arts and you have a promising future ahead. Don\'t forget to return to me for training as you grow in power. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26917, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew you would master Steady Shot quickly. I\'m looking forward to showing you other techniques as you get more experienced in the ways of the $c. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26918, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re a quick learner, $n. Return to me as you grow stronger and I\'ll teach you more about using the Light to vanquish your enemies and strengthen your allies. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26919, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'re a quick learner, $n, and the wounded are fortunate to have your aid. Return to me as you grow stronger and I\'ll teach you other spells.', 0);
INSERT INTO `quest_offer_reward` VALUES (26920, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s about time you arrived, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (26921, 0, 0, 0, 0, 0, 0, 0, 0, 'You may not act quickly, but at least you do not fail me.$B$BVery well. I will have a more important task for you, $n, when the time is right.', 0);
INSERT INTO `quest_offer_reward` VALUES (26922, 0, 0, 0, 0, 0, 0, 0, 0, 'These grenades are all explosives, nothing more. Even as a member of the Royal Apothecary Society, I recognize that some problems are not solved by throwing more plague at them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26923, 0, 0, 0, 0, 0, 0, 0, 0, 'With the behemoths out of the way, our victory against the Scourge is inevitable.$B$BOur current chances against the Alliance aren\'t quite as favorable... but that\'s no concern of yours. Not yet, anyhow.', 0);
INSERT INTO `quest_offer_reward` VALUES (26924, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellently done, $n. And now, with the leader of the Scourge forces eliminated from Andorhal, we bide our time.', 0);
INSERT INTO `quest_offer_reward` VALUES (26925, 0, 0, 0, 0, 0, 0, 0, 0, 'Araj dead... this is good. The Royal Apothecary Society has done its part. As have you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26926, 0, 0, 0, 0, 0, 0, 0, 0, 'Word arrived shortly before you did, $n. With the Scourge ejected from Andorhal, the Forsaken has cemented its foothold in Lordaeron.', 0);
INSERT INTO `quest_offer_reward` VALUES (26927, 0, 0, 0, 0, 0, 0, 0, 0, 'Murlocs? Sure, I\'ve hunted murlocs.$B$BAnd every single stinkin\' one of them deserved what they got! You hear me?', 0);
INSERT INTO `quest_offer_reward` VALUES (26928, 0, 0, 0, 0, 0, 0, 0, 0, 'Phew! Ain\'t that a stench.$B$BNow you prepare yourself, because I\'m about to wow you with what\'s left of my mental faculties.', 0);
INSERT INTO `quest_offer_reward` VALUES (26929, 0, 0, 0, 0, 0, 0, 0, 0, 'Mighty kind of you to help out an ol\' fool like me. Here\'s your reward as promised an\' all.', 0);
INSERT INTO `quest_offer_reward` VALUES (26930, 0, 0, 0, 0, 0, 0, 0, 0, 'Burning bodies? No survivors? I guessed correctly.$B$BAll the better! We\'re better off without the Scarlet taint upon Lordaeron. They epitomized all the worst things about humans.', 0);
INSERT INTO `quest_offer_reward` VALUES (26931, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s a relief to have those hunters out of the way. We\'re not done killing Alliance, however...', 0);
INSERT INTO `quest_offer_reward` VALUES (26932, 0, 0, 0, 0, 0, 0, 0, 0, 'That should help prevent disease and illness from spreading into our remaining water. Many thanks, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (26933, 0, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! I hope that they come back as zombies.', 0);
INSERT INTO `quest_offer_reward` VALUES (26934, 0, 0, 0, 0, 0, 0, 0, 0, 'Glorious! This is the last bit I need.$B$BActually, stick around, $n. Let me tell you a little bit more about our plan.', 0);
INSERT INTO `quest_offer_reward` VALUES (26935, 2, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the North Ridge, |3-6($c).', 19342);
INSERT INTO `quest_offer_reward` VALUES (26936, 0, 0, 0, 0, 0, 0, 0, 0, 'Felstone is cleared and the bombs are set. I hope you\'re ready. I recommend covering your ears.$B$B<Derrington looks down and places his finger on an ignition switch.>$B$BEVERYBODY, PREPARE FOR IGNITION! IN 3...$B$B2....$B$B1...', 0);
INSERT INTO `quest_offer_reward` VALUES (26937, 0, 0, 0, 0, 0, 0, 0, 0, 'I am able to communicate with you through the Val\'kyr, $n. You have done well.$B$BRemain on Felstone Field. We must now teach these newly-converted Forsaken how to fight.', 0);
INSERT INTO `quest_offer_reward` VALUES (26938, 0, 0, 0, 0, 0, 0, 0, 0, 'When Koltira gives his word, I will bring these val\'kyr into Andorhal. We will win. And yet another piece of Lordaeron will fall to the might of the Forsaken.', 0);
INSERT INTO `quest_offer_reward` VALUES (26939, 0, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, ты еще не совсем $gвыдохся:выдохлась;. У нас впереди еще много работы!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26940, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew you would master this lesson quickly. You are a credit to the highborn and to all our kin. Remember, as you grow in power, return to me and will instruct you further. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26941, 0, 0, 0, 0, 0, 0, 0, 0, 'Что-то ты долго $gвозился:возилась;. Иди ко мне, за колонну. А то засветишь укрытие, и хана нам обоим!', 19342);
INSERT INTO `quest_offer_reward` VALUES (26942, 0, 0, 0, 0, 0, 0, 0, 0, 'Я бы и рад пригласить тебя остаться в команде \"Г\", но тебя ведь ждут другие приключения.$B$BУверен, мы еще встретимся – тогда и поговорим на эту тему. Вот тебе награда за труды.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26943, 0, 0, 0, 0, 0, 0, 0, 0, 'Never thought I\'d see a $R like you down here. Any particular reason you\'re risking life and limb?', 0);
INSERT INTO `quest_offer_reward` VALUES (26944, 0, 0, 0, 0, 0, 0, 0, 0, 'Never thought I\'d see a $R like you down here. Any particular reason you\'re risking life and limb?', 0);
INSERT INTO `quest_offer_reward` VALUES (26945, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve proven a capable student. When it is time to resume your training, return to me and I will show you other techniques. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26946, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve learned quickly. If you are as sharp as you seem to be, you may prove to be one of my best students yet. Return to me when you are in need of further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26947, 0, 0, 0, 0, 0, 0, 0, 0, 'You are a quick study in our ways. I look forward to continuing your training in the future. Return to me when the time is right. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26948, 0, 0, 0, 0, 0, 0, 0, 0, 'You have mastered the basics of the healing ways quickly, $n. I have no doubt that your healing skills will often come in handy. Return to me when you need further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26949, 0, 0, 0, 0, 0, 0, 0, 0, 'You will find no lack of allies in need of healing. Return to me when you need further training.', 0);
INSERT INTO `quest_offer_reward` VALUES (26950, 0, 0, 0, 0, 0, 0, 0, 0, 'What do you mean Thalnos was an undead? That\'s impossible. This is a holy place, the Scarlet Crusade would never allow...$B$BTake this as a reward, and get some rest. The crypts must have gotten the best of your nerves.', 0);
INSERT INTO `quest_offer_reward` VALUES (26951, 0, 0, 0, 0, 0, 0, 0, 0, 'Thanks, $c.  The spirits can rest now.  As long as we show them respect they will bless us and our new home for a long time.  Spirits be with you, $n. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26952, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome, $C. As you can see, our camp is a joint expedition between Argent Crusade members and Cenarion Circle druids, but we welcome all who wish to help heal the land.', 0);
INSERT INTO `quest_offer_reward` VALUES (26953, 0, 0, 0, 0, 0, 0, 0, 0, 'Похоже, Зен\'Кики еще не совсем освоил превращения.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26954, 1, 6, 1, 0, 0, 0, 0, 0, 'Ну, хоть что-то. Сколько птиц уничтожил Зен\'Кики?$B$BНи одной? Ты $gсам:сама; всех $gубил:убила;?$B$BЛадно, думаю, мы сумеем приспособить его к чему-нибудь другому. Дам тебе знать, как придет время.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26955, 0, 0, 0, 0, 0, 0, 0, 0, 'By the grace of the Earthmother, he\'s done it! Thank you, $n!$B$BI admit, I had nearly given up hope on Zen\'Kiki. It is only due to your patience and diligence that he has come into his own as a druid and member of the Cenarion Circle. And that\'s without even mentioning the good you\'ve done for nature.$B$BYou have my admiration and respect.', 0);
INSERT INTO `quest_offer_reward` VALUES (26956, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за помощь, $n. Конечно, прополка растений – это не самая почетная и героическая обязанность, но даже за скромную работу полагается вознаграждение.', 23877);
INSERT INTO `quest_offer_reward` VALUES (26957, 0, 0, 0, 0, 0, 0, 0, 0, 'It would appear that I arrived before you, $n.$B$BField Agent Kaartish gave me the details about my old home. It seems my battles with the Scourge aren\'t quite over yet.', 0);
INSERT INTO `quest_offer_reward` VALUES (26958, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep that technique handy, $n. I find myself using it all the time. Return when you\'re ready for further training.', 0);
INSERT INTO `quest_offer_reward` VALUES (26959, 0, 0, 0, 0, 0, 0, 0, 0, 'I knew I could count on you! These fools will never know what hit them.', 0);
INSERT INTO `quest_offer_reward` VALUES (26960, 6, 1, 0, 0, 0, 0, 0, 0, 'Excellent, Anduin will learn a lot by shadowing you.$B$BRemember, stay within the city\'s borders. Keep him out of trouble. And above all else, guard him with your life.$B$B<King Wrynn turns his attention to his son.>$B$BAnduin, no adventures! Stay close to $n and return to the keep should you see any trouble.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26961, 0, 0, 0, 0, 0, 0, 0, 0, 'You got them!  Nicely done, $N!$B$BAnd you don\'t look too worse for wear, even with those berserk troggs about.  I\'m going to keep my eye on you...you show some promise.', 0);
INSERT INTO `quest_offer_reward` VALUES (26962, 0, 0, 0, 0, 0, 0, 0, 0, 'Herod is slain! The Armory will fall!', 0);
INSERT INTO `quest_offer_reward` VALUES (26963, 0, 0, 0, 0, 0, 0, 0, 0, 'Keep that technique handy, $n. I find myself using it all the time. Return when you\'re ready for further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26964, 0, 0, 0, 0, 0, 0, 0, 0, 'Most excellent! A new recruit has arrived. There is much to do... much to do I say.', 0);
INSERT INTO `quest_offer_reward` VALUES (26965, 0, 0, 0, 0, 0, 0, 0, 0, 'Well that was unexpected. I am not one to doubt our Warchief, but...$B$BNevermind! We have work to do. I\'m sure the Dark Lady and the Warchief will get this all worked out.', 0);
INSERT INTO `quest_offer_reward` VALUES (26966, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve proven yourself a quick learner, $n. I look forward to training you again when the time is right. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26967, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes! The Scarlet Monastery now belongs to the Forsaken! None can escape our wrath!$B$BYou\'ve completed your task with the precision of a true champion. Take this as a reward, courtesy of the Banshee Queen.', 0);
INSERT INTO `quest_offer_reward` VALUES (26968, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve mastered the use of this new spell quickly, $n. I only wish all of my students were as talented. Return to me when you find yourself in need of further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (26969, 0, 0, 0, 0, 0, 0, 0, 0, 'It is a pleasure to teach such a capable student. As long as you continue the traditons faithfully, the shamanic arts will never be lost to our people again. Return to me when you need further training.', 0);
INSERT INTO `quest_offer_reward` VALUES (26970, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ll often find your skills as a healer in demand, $n. Do not hesitate to return when you need more training.', 0);
INSERT INTO `quest_offer_reward` VALUES (26971, 0, 0, 0, 0, 0, 0, 0, 0, 'Maruut said when all dis started dat he would gladly give his life ta see our task done. He would be proud of dis success... of dis death.$B$BDa rift be closed on dis side. Deepholm should calm down a bit. It\'ll be takin\' time for Thrall to mend da wound in Azeroth, and we\'ll be needin\' to remain until he finishes.$B$BThank you for all ya\'ve done. I don\'t tink we could have been accomplishin\' dis so soon without you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26972, 0, 0, 0, 0, 0, 0, 0, 0, 'What do you mean Thalnos was an undead? That\'s impossible. The entire mission of the Scarlet Crusade is to destroy the undead! There\'s just no way...$B$BYou have muddied my thoughts. Please take this reward for your achievement and leave me to rest.', 0);
INSERT INTO `quest_offer_reward` VALUES (26973, 0, 0, 0, 0, 0, 0, 0, 0, 'Good work! I just know the Light shines favorably upon us for our work. Our cause is righteous!', 0);
INSERT INTO `quest_offer_reward` VALUES (26974, 0, 0, 0, 0, 0, 0, 0, 0, 'So he\'s really dead? That means...I, Joseph, am the new Scarlet Champion!!!$B$BTake this. I don\'t have a need for such possessions anyways! I\'m the Scarlet Champion, after all.', 0);
INSERT INTO `quest_offer_reward` VALUES (26975, 2, 1, 0, 0, 0, 0, 0, 0, '< Graves bows respectfully to the prince. >$B$BBOur Highness, please understand - my people work around the clock, but this project is huge!$B$BHowever, we will try to finish it as soon as possible. I give you my word.', 19342);
INSERT INTO `quest_offer_reward` VALUES (26976, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes! The Scarlet Monastery is ours! You\'re a real friend. Take this as a reward, and feel free to visit again once I\'ve taken the Undercity too.$B$BIt\'ll be a Scarlet Paradise!', 0);
INSERT INTO `quest_offer_reward` VALUES (26977, 1, 273, 0, 0, 0, 0, 0, 0, 'Hmmm, yes? My investigation of the Twilight\'s Hammer. Of course.$B$BYour assistance will be valuable. ', 19342);
INSERT INTO `quest_offer_reward` VALUES (26978, 0, 0, 0, 0, 0, 0, 0, 0, '<Dithers practically glimmers with excitement.>$B$BThe drums are placed? In their positions?$B$BVery well then, I will give the word to the High Executor. When the time is ready, he will be the one to press the ignition switch.', 0);
INSERT INTO `quest_offer_reward` VALUES (26979, 0, 0, 0, 0, 0, 0, 0, 0, '$n. The Dark Lady watches over you.', 0);
INSERT INTO `quest_offer_reward` VALUES (26980, 0, 0, 0, 0, 0, 0, 0, 0, 'Stoutfist sent you, eh? He\'s a good captain, runs his men well. Just wish he\'d stop starin\' at my midriff.$B$BDon\'t let me catch you doin\' it either. We\'ve work to take care of here!', 0);
INSERT INTO `quest_offer_reward` VALUES (26981, 0, 0, 0, 0, 0, 0, 0, 0, 'Well met, $C. We\'ve got raptor problems for sure... sort of.$B$BMore like the entire dig site deciding to attack us. I\'ve never seen rocks and fossils get so upset!', 0);
INSERT INTO `quest_offer_reward` VALUES (26982, 0, 0, 0, 0, 0, 0, 0, 0, 'Captain Nials sent you? I haven\'t seen her in ages! How is she? Did she get my letter?$B$BOh, you\'re not here for that. Well then, let\'s get down to business!', 0);
INSERT INTO `quest_offer_reward` VALUES (26983, 0, 0, 0, 0, 0, 0, 0, 0, 'You came here from Southshore, eh? I bet you hunger for revenge against the vile Forsaken that trashed the place. The Scarlet Crusade can help take care of that!$B$BRight after you help me realign their misguided souls, that is.', 0);
INSERT INTO `quest_offer_reward` VALUES (26984, 0, 0, 0, 0, 0, 0, 0, 0, 'You look pretty tough, $C. There are wrongs to be righted here. Let\'s get started.', 0);
INSERT INTO `quest_offer_reward` VALUES (26985, 0, 0, 0, 0, 0, 0, 0, 0, 'Wait, you were sent here by THE Shandris Feathermoon? I can barely believe my ears! To know that my cause is supported by her massive army...$B$BYes, yes, you will be perfect. You\'re the key to my future!', 0);
INSERT INTO `quest_offer_reward` VALUES (26986, 0, 0, 0, 0, 0, 0, 0, 0, 'It looks like Darthalia sent an able $C, as I requested.', 0);
INSERT INTO `quest_offer_reward` VALUES (26987, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m surprised to see Huntsman Markhor working with me for once. He\'s usually quite stubborn. He does tend to a strong hatred for magic, though.$B$BI hope you do, too, $C, because we\'re going to strip the Scarlet Crusade of theirs.', 0);
INSERT INTO `quest_offer_reward` VALUES (26988, 0, 0, 0, 0, 0, 0, 0, 0, 'Yayo\'jin is correct, you helping me here will aid her in the Hinterlands as well.$B$BAll the more reason to get started immediately.', 0);
INSERT INTO `quest_offer_reward` VALUES (26989, 0, 0, 0, 0, 0, 0, 0, 0, '$Nnub has returned! And in one piece!$B$BDefender of Silverpine! Redeemer of the Horde! $Nnub, your bravery must be rewarded!', 0);
INSERT INTO `quest_offer_reward` VALUES (26990, 0, 0, 0, 0, 0, 0, 0, 0, 'Sandfury trolls and the Cult of Twilight\'s Hammer, you say?$B$BAnd they meant to send some giant monster into the heart of the city?!$B$BWell done! Thunder Bluff is in your debt.', 0);
INSERT INTO `quest_offer_reward` VALUES (26991, 0, 0, 0, 0, 0, 0, 0, 0, 'Nazeer is a strong warrior - his hatred for our enemies rivals my own. You are welcome help to my forces.', 0);
INSERT INTO `quest_offer_reward` VALUES (26992, 0, 0, 0, 0, 0, 0, 0, 0, 'Agony abounds, friend! The land itself threatens to devour us, and yet we persist. I wonder why that is? With this doomweed and the guts and gore that T\'Veen needs, we might have something suitable to send to the Forsaken front.', 0);
INSERT INTO `quest_offer_reward` VALUES (26993, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. How about I give you a nice reward for your work, and you don\'t tell a soul that you saw me here. Deal?', 0);
INSERT INTO `quest_offer_reward` VALUES (26994, 0, 0, 0, 0, 0, 0, 0, 0, 'Very good. Yes, very good indeed.$B$BYour reward, as promised. No questions.', 0);
INSERT INTO `quest_offer_reward` VALUES (26995, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! With these guts and the ferocious doomweed we\'ll be able to test out our new strain of blight. Now to select a suitable target...', 0);
INSERT INTO `quest_offer_reward` VALUES (26996, 0, 0, 0, 0, 0, 0, 0, 0, '<Dominic hands you your reward. No words are spoken.>', 0);
INSERT INTO `quest_offer_reward` VALUES (26997, 6, 5, 0, 0, 0, 0, 0, 0, 'This doesn\'t make any sense, $n.$B$BWhy was Samuelson sending us out here on a wild goose chase?$B$BSomething doesn\'t feel right. We should poke around on our own...', 19342);
INSERT INTO `quest_offer_reward` VALUES (26998, 0, 0, 0, 0, 0, 0, 0, 0, 'Potent enough to exterminate a thousand murlocs! I think I\'ll put that on the label. You\'ve done well, $N, and great work around here gets rewarded.', 0);
INSERT INTO `quest_offer_reward` VALUES (26999, 2, 1, 0, 0, 0, 0, 0, 0, 'Этого более чем достаточно, $n. А теперь прошу меня извинить – я пойду изучать образцы.', 23877);
INSERT INTO `quest_offer_reward` VALUES (27000, 1, 1, 0, 0, 0, 0, 0, 0, 'Что ж, сдается, они и правда боятся мустангов, но все равно так просто не уйдут. Я не могу заставить тебя все время гонять на лошади вокруг лесопилки. Да ты и не станешь.$B$BОтлично! Значит, без кровопускания не обойтись.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27001, 4, 1, 0, 0, 0, 0, 0, 0, 'That\'s a completely different knee! Now my lumberjacks are back to work. You\'re good in the saddle, $n. That\'s what you look like riding that mustang - a sight!', 19342);
INSERT INTO `quest_offer_reward` VALUES (27002, 0, 0, 0, 0, 0, 0, 0, 0, '$n. Nice to meet you.$B$BI know you\'re very young: young; and you have a lot to learn, but I follow your progress. There are reasons for that. We at the Silver Vanguard always look out for promising youth. So to speak, we\'re looking for talents to enlarge our staff. And you have good potential. Right now there are no tasks for you, I just wanted to get to know you in person, see and understand what you are for |3-6($r).$B$BT So you can look around in Dolnoe Hearth and enjoy its beauty for now. We\'ll meet again, $n. And now you can go.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27003, 0, 0, 0, 0, 0, 0, 0, 0, 'Smells like someone else was in this cell recently. Gross! ', 0);
INSERT INTO `quest_offer_reward` VALUES (27004, 5, 0, 0, 0, 1000, 0, 0, 0, '<Mowi takes a look at the plans that you hand to her.>$B$BBut... but, this is just gibberish!$B$BWhat does this even say? What language is that?', 19342);
INSERT INTO `quest_offer_reward` VALUES (27005, 0, 0, 0, 0, 0, 0, 0, 0, '<The examiner takes a look at the plans that you hand to him.>$B$BThis is just gibberish!$B$BWhat does this even say? What language is that?', 0);
INSERT INTO `quest_offer_reward` VALUES (27006, 5, 0, 0, 0, 0, 0, 0, 0, 'A waygate?! The culties dug up a Titan waygate?', 19342);
INSERT INTO `quest_offer_reward` VALUES (27007, 0, 0, 0, 0, 0, 0, 0, 0, 'This is the place.  Our scouts confirmed the presence of high-ranking Twilight officers nearby as well as potential trogg hunting parties.$B$BBe on the lookout, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27008, 0, 0, 0, 0, 0, 0, 0, 0, 'A waygate?! They unearthed a Titan waygate.$B$BThis is very interesting and potentially very bad.', 0);
INSERT INTO `quest_offer_reward` VALUES (27009, 0, 0, 0, 0, 0, 0, 0, 0, 'A job well done, $N. The Quilboar here can no longer be saved, but at least they are no longer organized for attack, or slaves under Amnennar\'s tyrannic rule.$B$BLeave quickly, please. Razorfen Downs is without hope. It must be purged.', 0);
INSERT INTO `quest_offer_reward` VALUES (27010, 0, 0, 0, 0, 0, 0, 0, 0, 'We have the information we need.  Let\'s not tip our hand by engaging the enemy too soon.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27011, 396, 6, 0, 0, 0, 0, 0, 0, 'Я положу это обратно, к остальным бревнам. А Натаниэль, наверно, даже и не узнает про кражу.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27012, 1, 1, 0, 0, 0, 0, 0, 0, 'Спасибо, $n. Брошу все это в огонь, чтобы гноллы не смогли забрать оружие обратно.', 19342);
INSERT INTO `quest_offer_reward` VALUES (27013, 2, 0, 0, 0, 0, 0, 0, 0, 'Теперь у меня на душе стало спокойнее, $n. Спасибо тебе!', 19342);
INSERT INTO `quest_offer_reward` VALUES (27014, 0, 0, 0, 0, 0, 0, 0, 0, 'You have arrived at last. During your training in Camp Narache, I will guide you in the ways of the $c. You have much to learn and I look forward to counting you among my students. ', 0);
INSERT INTO `quest_offer_reward` VALUES (27015, 0, 0, 0, 0, 0, 0, 0, 0, 'We have much to cover. In time, the ways of the sunwalkers will be as plain and natural to you as the shaman\'s conversations with the elements. ', 0);
INSERT INTO `quest_offer_reward` VALUES (27016, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve killed ten already? H...hah! While you were out, I\'ve broken my previous record! To ten... hundred! Yes, ten hundred boars!$B$BA bet is a bet, I suppose... here\'s your consolation prize, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (27017, 0, 0, 0, 0, 0, 0, 0, 0, 'This... Gloria used to read from this prayer book every night! How had I forgotten? She was so devout, $n.$B$B$B$BOh...$B$BThis painting... my daughter, Andrea... she loved to... to paint...', 0);
INSERT INTO `quest_offer_reward` VALUES (27019, 0, 0, 0, 0, 0, 0, 0, 0, 'Fantastic, I thought you looked to be the heroic type!  We\'ve got our work cut out for us $N, I\'m not going to lie to you.  If we stay focused on the task at hand, we should be able to come out on top.', 0);
INSERT INTO `quest_offer_reward` VALUES (27020, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $N. Return to me when you are in need of further training.', 0);
INSERT INTO `quest_offer_reward` VALUES (27021, 0, 0, 0, 0, 0, 0, 0, 0, 'You have done well, $n. Return to me when you are in need of further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (27022, 0, 0, 0, 0, 0, 0, 0, 0, 'Belnistrasz\'s sudden departure has left behind a burning brazier.  As you stare in to the flame, the image of a great-winged red dragon fills your mind.  A more graveled version of Belnistrasz\'s voice speaks directly to you.$B$B\"Indeed mortals, you are the heroes I had hoped you would be.  Thanks to you the butchery these beasts were perpetrating is a thing of the past.  My faith in your kind has been once more renewed.  Take this as a reward befitting of a hero.\"', 0);
INSERT INTO `quest_offer_reward` VALUES (27023, 0, 0, 0, 0, 0, 0, 0, 0, 'You have made me proud, $n. You will make a fine Sunwalker. Return when you desire further training. ', 0);
INSERT INTO `quest_offer_reward` VALUES (27024, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! Without their leaders, the Scourge will pose no significant threat.', 0);
INSERT INTO `quest_offer_reward` VALUES (27025, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been informed there\'s a bear warning active for the woodlands as well! They love those stabthistle seeds.$B$BIt\'s a good thing you gathered those so they don\'t attract more bears!', 0);
INSERT INTO `quest_offer_reward` VALUES (27026, 0, 0, 0, 0, 0, 0, 0, 0, 'Okay, yes, I have already been informed that bobcats are much smaller than I was assuming. But that\'s still pretty dangerous for gnomes!$B$BYou\'ve done a good thing for the safety of this lodge, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (27027, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. I see you will have no trouble mastering our more advanced arts as you gain experience. Return to me when you need further training.', 0);
INSERT INTO `quest_offer_reward` VALUES (27028, 0, 0, 0, 0, 0, 0, 0, 0, 'Lovely specimens you\'ve got there! Here\'s my wager, lad.', 0);
INSERT INTO `quest_offer_reward` VALUES (27029, 0, 0, 0, 0, 0, 0, 0, 0, 'I have faith that this substance will prevent many future headaches. Thank you, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (27030, 0, 0, 0, 0, 0, 0, 0, 0, 'Ahh, you see? Goods of the finest quality. Good work gettin\' these.', 0);
INSERT INTO `quest_offer_reward` VALUES (27031, 0, 0, 0, 0, 0, 0, 0, 0, 'Beautiful. you\'ve got an eye for quality!$B$BGreat minds think alike! It\'s clear you\'ve got an enthusiasm for flight and fowl, so maybe you can help me further...', 0);
INSERT INTO `quest_offer_reward` VALUES (27032, 0, 0, 0, 0, 0, 0, 0, 0, '<The Skystrider sits silent, its chance to fly gone. An obvious hatch on the front is marked \"SRW\".>', 0);
INSERT INTO `quest_offer_reward` VALUES (27033, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect! Now that I\'ve perfected the wing balance and have all the parts I need, all we need is a pilot.', 0);
