INSERT INTO `quest_offer_reward` VALUES (28800, 0, 0, 0, 0, 0, 0, 0, 0, 'It appears as if this particular brazier has trapped a minor djinn, and freeing him would certainly bring a reward. Right?', 0);
INSERT INTO `quest_offer_reward` VALUES (28801, 0, 0, 0, 0, 0, 0, 0, 0, 'Using the keystone, you confirm the grave is from one of the original builders, and you learn important details of the mummification process.', 0);
