INSERT INTO `quest_offer_reward` VALUES (31340, 0, 0, 0, 0, 0, 0, 0, 0, 'What? Sheepie is... gone?$B$B<Chee Chee\'s lip begins to quiver.>', 0);
INSERT INTO `quest_offer_reward` VALUES (31341, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. It will not bring my Sheepie back, but at least this way, no more sheep will be lost.', 0);
INSERT INTO `quest_offer_reward` VALUES (31342, 2, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпобедил:победила; Ша Жестокости и $gспас:спасла; монастырь. Прими же мою благодарность.$B$BБыть может, мы неправильно судили о твоем народе, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (31354, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey there!$B$BOh, I\'m fine. I\'ve had worse spills than that after a night at the Chum Bucket.$B$BBut would you look at this thing? I spotted it peeking out of the sea floor when I went under - Jiao must have dredged it up while he was thrashing around.$B$BNot really sure what to do with it, but it looks valuable. Seeing as how you pretty much saved all our hides, I think we owe you this catch.', 0);
INSERT INTO `quest_offer_reward` VALUES (31355, 2, 0, 0, 0, 0, 0, 0, 0, 'То, что случилось с Мудрым Марисом и жрицей Пламенное Сердце, очень печально. Но все равно – благодарю тебя от всей души.', 19342);
INSERT INTO `quest_offer_reward` VALUES (31356, 2, 0, 0, 0, 0, 0, 0, 0, 'Я никогда в тебе не сомневался. Но почему-то мудрости в тебе не прибавилось.$B$B<мастер подмигивает>', 19342);
INSERT INTO `quest_offer_reward` VALUES (31357, 0, 0, 0, 0, 0, 0, 0, 0, 'Я найду, где спрятать эти реликвии.$B$BПандария перед тобой в неоплатном долгу, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (31359, 0, 0, 0, 0, 0, 0, 0, 0, 'I am most impressed with your progress, Wakener. Kovok\'s training is coming along nicely and he seems to be fond of you.$B$BI have no doubt that you are the best choice to assist me in training him.', 0);
INSERT INTO `quest_offer_reward` VALUES (31360, 0, 0, 0, 0, 0, 0, 0, 0, 'Это была поистине великая битва.$B$BХорошо, что ты на нашей стороне, $n.', 19342);
INSERT INTO `quest_offer_reward` VALUES (31362, 5, 0, 0, 0, 0, 0, 0, 0, '<Lorewalker Cho shudders, in spite of himself.>$b$bThe legends... they\'re true!', 19342);
INSERT INTO `quest_offer_reward` VALUES (31363, 1, 0, 0, 0, 0, 0, 0, 0, 'Потрясающе, $n. Очень вовремя!', 19342);
INSERT INTO `quest_offer_reward` VALUES (31364, 4, 0, 0, 0, 0, 0, 0, 0, 'I can\'t believe it! Thank you, $n!', 19342);
INSERT INTO `quest_offer_reward` VALUES (31365, 0, 0, 0, 0, 0, 0, 0, 0, 'Thirteen thanks I give to you; twelve for those you rescued and my own as well.$B$BHere\'s something else I give to you in gratitude.', 0);
INSERT INTO `quest_offer_reward` VALUES (31366, 0, 0, 0, 0, 0, 0, 0, 0, 'The wing leader lies dead and the siege is broken.$B$BYou\'ve saved the temple, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (31367, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah yes, I can see by your face that you are an eager seeker of stories. You have come to the right place.', 0);
