INSERT INTO `quest_offer_reward` VALUES (31921, 0, 0, 0, 0, 0, 0, 0, 0, 'ЗапредельеТвои силы растут. Давай посмотрим, сумеешь ли ты справиться с великим укротителем питомцев Запределья!Пусть битвы принесут тебе славу.ВарзокВот и еще один этап твоего пути завершен.Варзок', 26365);
INSERT INTO `quest_offer_reward` VALUES (31922, 0, 0, 0, 0, 0, 0, 0, 0, 'I forgot to carry the 1!', 0);
INSERT INTO `quest_offer_reward` VALUES (31923, 0, 0, 0, 0, 0, 0, 0, 0, 'Nice work!', 0);
INSERT INTO `quest_offer_reward` VALUES (31924, 0, 0, 0, 0, 0, 0, 0, 0, 'It would seem that these younglings still have a lot of work to do before they are at full strength. Thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (31925, 0, 0, 0, 0, 0, 0, 0, 0, 'Even I continue to learn from each passing day.', 0);
INSERT INTO `quest_offer_reward` VALUES (31926, 0, 0, 0, 0, 0, 0, 0, 0, '', 26365);
