INSERT INTO `quest_offer_reward` VALUES (31929, 0, 0, 0, 0, 0, 0, 0, 0, 'НордсколМало кому удавалось зайти так далеко. И еще меньше тех, кто встречался с великим укротителем боевых питомцев Нордскола! Так что приготовься.Я не проиграю!ВарзокТебя ждет самое сложное испытание.Варзок', 26365);
INSERT INTO `quest_offer_reward` VALUES (31931, 0, 0, 0, 0, 0, 0, 0, 0, 'One day, when we\'re flying high above you, you\'ll be the loser!', 0);
INSERT INTO `quest_offer_reward` VALUES (31932, 0, 0, 0, 0, 0, 0, 0, 0, 'Your risk has paid its due reward. Now begone!', 0);
INSERT INTO `quest_offer_reward` VALUES (31933, 0, 0, 0, 0, 0, 0, 0, 0, 'I dare you to continue to fight the inevitable. Dragons will once again serve our purposes, and you will fall to my hand in a pet battle. I swear on the Warchief\'s mantle that it will be so!', 0);
INSERT INTO `quest_offer_reward` VALUES (31934, 0, 0, 0, 0, 0, 0, 0, 0, 'Me like fighting you, even if me lose.', 0);
INSERT INTO `quest_offer_reward` VALUES (31935, 0, 0, 0, 0, 0, 0, 0, 0, 'I applaud your tactics, but despise your victory. Well done, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (31936, 0, 0, 0, 0, 0, 0, 0, 0, 'A sprinkler system? For the farm? Thank you, $n! We can use this instead of that old watering can.$B$BThe \"Jinyu Princess\" eh? What\'s up with that?$B$B$B$BAlright, I don\'t even want to know. I\'ll get this installed right away!', 0);
INSERT INTO `quest_offer_reward` VALUES (31937, 0, 0, 0, 0, 0, 0, 0, 0, 'Pest repellers! You\'re a genius, $n! We can finally stop using that ancient bug sprayer.$B$B$B$BAw yeah, Thunder King! That\'s what I\'m talking about!$B$BI\'ll get this installed right away - those bugs won\'t know what hit \'em!', 0);
INSERT INTO `quest_offer_reward` VALUES (31938, 0, 0, 0, 0, 0, 0, 0, 0, 'An Earth-Slasher master plow! I... I never thought I\'d get to use one of these. Do you have any idea how much time this will save us?!$B$BI heard if you push the Earth-Slasher through twenty yards of solid rock, you end up with a sandbox and a citation from the Shado-Pan for causing an earthquake scare!$B$BThis is going to be AWESOME.', 0);
INSERT INTO `quest_offer_reward` VALUES (31941, 0, 0, 0, 0, 0, 0, 0, 0, 'Well planted, $n.$B$BNow we play the waiting game...', 0);
INSERT INTO `quest_offer_reward` VALUES (31942, 0, 0, 0, 0, 0, 0, 0, 0, 'That was close. We almost missed melon time.', 0);
INSERT INTO `quest_offer_reward` VALUES (31943, 0, 0, 0, 0, 0, 0, 0, 0, 'Alright, witchberries! I can\'t wait!$B$BBut I\'m gonna have to.', 0);
INSERT INTO `quest_offer_reward` VALUES (31944, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. Your training is now complete. Feel free to return in the future to spar with the masters here to practice what you have learned.', 0);
INSERT INTO `quest_offer_reward` VALUES (31945, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, I\'ll be! I\'ve certainly seen larger scallions, but this will do quite nicely. You\'ve just saved our hides, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (31946, 0, 0, 0, 0, 0, 0, 0, 0, 'Whoa, these carrots look crunchy and juicy, $n! Mung-Mung did not know you had such a green thumb.', 0);
INSERT INTO `quest_offer_reward` VALUES (31947, 0, 0, 0, 0, 0, 0, 0, 0, 'I was expecting cabbages, but not cabbages of this quality! Oh my!$B$BA perfectly round head, large waxy leaves, a perfect gradient of emerald color...$B$B<Farmer Fung takes a bite out of the cabbage.>$B$BMmm, and sweet, crisp flavor! These is a beautiful cabbage, $n. My yaks will certainly be spoiled by this tasty treat.', 0);
INSERT INTO `quest_offer_reward` VALUES (31948, 0, 0, 0, 0, 0, 0, 0, 0, 'Found the falls, did you? Oh yes, I see you found the water. Wonderful!', 0);
INSERT INTO `quest_offer_reward` VALUES (31949, 0, 0, 0, 0, 0, 0, 0, 0, '<Nana Mudclaw scrutinizes a dark witchberry, then pops it into her mouth. Her face lights up.>$B$BLands alive! I haven\'t tasted such a sweet, juicy witchberry in years! Why, the only person who could grow berries like these was... Old Yoon, yer friend\'s grandpa.$B$B<Nana Mudclaw blushes.>$B$BOh that man was such a scamp! I still remember the summer days when we were young, eating these berries by the bushel without a care in the world.$B$BThank you for reminding me of an old friend, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (31951, 0, 0, 0, 0, 0, 0, 0, 0, 'You have emerged victorious. Continue your training and find challenges in other trainers like yourself. The journey is never truly complete.', 0);
INSERT INTO `quest_offer_reward` VALUES (31953, 0, 0, 0, 0, 0, 0, 0, 0, 'You are very strong. Will you lend your skills to my efforts?', 0);
INSERT INTO `quest_offer_reward` VALUES (31954, 0, 0, 0, 0, 0, 0, 0, 0, 'Next time you lose. I\'m gonna beatcha!', 0);
INSERT INTO `quest_offer_reward` VALUES (31955, 0, 0, 0, 0, 0, 0, 0, 0, 'Wow! I\'m not the only one that\'s been training!', 0);
INSERT INTO `quest_offer_reward` VALUES (31956, 0, 0, 0, 0, 0, 0, 0, 0, 'Fine. Victory is yours once more. Begone, I have much preparation to do before we meet again.', 0);
INSERT INTO `quest_offer_reward` VALUES (31957, 0, 0, 0, 0, 0, 0, 0, 0, 'In all my time as a grand master I\'ve never faced one as determined as you. The others were right about you. You are a force to be reckoned with.', 0);
INSERT INTO `quest_offer_reward` VALUES (31958, 0, 0, 0, 0, 0, 0, 0, 0, 'You are stronger and your skills more refined. Your journey continues.', 0);
INSERT INTO `quest_offer_reward` VALUES (31967, 0, 0, 0, 0, 0, 0, 0, 0, 'CataclysmКлянусь клинком Вождя, ты сражаешься как орк! Думаю, ты $gготов:готова; отправляться дальше.Теперь ты не можешь проиграть.ВарзокДумаешь, пришло время для следующего поединка?Варзок', 26365);
INSERT INTO `quest_offer_reward` VALUES (31970, 0, 0, 0, 0, 0, 0, 0, 0, '', 26365);
INSERT INTO `quest_offer_reward` VALUES (31971, 0, 0, 0, 0, 0, 0, 0, 0, 'By defeating me, I grow stronger. I urge you to be wary during our next encounter.', 0);
INSERT INTO `quest_offer_reward` VALUES (31972, 0, 0, 0, 0, 0, 0, 0, 0, 'Even in defeat I feel closer to my pets. Thank you for a worthy skirmish.', 0);
INSERT INTO `quest_offer_reward` VALUES (31973, 0, 0, 0, 0, 0, 0, 0, 0, 'No, no, I just messed up. How about another go?', 0);
INSERT INTO `quest_offer_reward` VALUES (31974, 0, 0, 0, 0, 0, 0, 0, 0, 'You fight with the wrath of my masters. Perhaps you and I aren\'t so different.', 0);
INSERT INTO `quest_offer_reward` VALUES (31975, 0, 0, 0, 0, 0, 0, 0, 0, 'Let\'s see...where to send you next...', 0);
