INSERT INTO `quest_offer_reward` VALUES (31978, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, it\'s not as much as I would have hoped, but it will have to do. I guess there is no sense in cryin\' over spilled brew.', 0);
