INSERT INTO `quest_offer_reward` VALUES (31998, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my gratitude, $n. You\'ve displayed great valor today, and the speed at which you completed the challenge surely will impress your friends and intimidate your foes.', 0);
INSERT INTO `quest_offer_reward` VALUES (31999, 0, 0, 0, 0, 0, 0, 0, 0, 'Мы победили в схватке с Альянсом, но мы не продвинемся дальше, пока не разберемся с этими бормочущими обезьянами в окрестностях.\r\n\r\nСледующая наша цель – хозены.', 0);
INSERT INTO `quest_offer_reward` VALUES (32000, 0, 0, 0, 0, 0, 0, 0, 0, 'You have my sincerest thanks, $n. Without fine Stormstout brews to lighten the drudgery of the day, the world would be a dull place indeed.', 0);
INSERT INTO `quest_offer_reward` VALUES (32001, 0, 0, 0, 0, 0, 0, 0, 0, 'Many blessings upon you, $n. Driving hatred from this world so quickly deserves rich reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (32002, 0, 0, 0, 0, 0, 0, 0, 0, 'Many thanks, $n. Your martial prowess is second to none, and your alacrity is sure to strike fear in the hearts of your enemies.', 0);
INSERT INTO `quest_offer_reward` VALUES (32003, 0, 0, 0, 0, 0, 0, 0, 0, 'That was a blow well struck, $n. I\'ve never seen a $C fell a giant with such speed.', 0);
INSERT INTO `quest_offer_reward` VALUES (32004, 0, 0, 0, 0, 0, 0, 0, 0, '$n, that was amazing. Ner\'onok is a master tactician, and you schooled him handily.', 0);
INSERT INTO `quest_offer_reward` VALUES (32005, 0, 0, 0, 0, 0, 0, 0, 0, '$n, you\'re a stalwart $C. Your friends and guild mates will surely notice the speed and ferocity with which you conquered the Scarlet Monastery.', 0);
INSERT INTO `quest_offer_reward` VALUES (32006, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $n. Just the thought of him burning up that beautiful library makes me shudder. Your friends will appreciate the speed with which you dispatched him.', 0);
INSERT INTO `quest_offer_reward` VALUES (32007, 0, 0, 0, 0, 0, 0, 0, 0, '$n, you\'ve done a great service. May the skeletons of the dead remain peaceful now that Gandling no longer seeks to disturb their slumber.', 0);
INSERT INTO `quest_offer_reward` VALUES (32008, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, you\'re $n. Splendid! Let\'s get to work then, shall we?', 0);
INSERT INTO `quest_offer_reward` VALUES (32009, 1, 0, 0, 0, 0, 0, 0, 0, 'Oh, you must be $n. Nice to meet you. I\'m Varzok.$b$bFrom now on, come to me.', 24015);
