INSERT INTO `quest_offer_reward` VALUES (32676, 0, 0, 0, 0, 0, 0, 0, 0, 'This so-called \"Thunder King\" is surrounded and cut off from his allies. No tyrant is strong enough to stand alone.$B$BYour efforts have not gone unnoticed. Take this, with my thanks.', 0);
INSERT INTO `quest_offer_reward` VALUES (32677, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, I know a powerful artifact when I see one. You were wise to bring this here. I will see that Aethas examines it right away - perhaps we can put it to work in our favor.$B$BIn the meantime, take this in trade.', 0);
INSERT INTO `quest_offer_reward` VALUES (32678, 0, 0, 0, 0, 0, 0, 0, 0, 'We must move quickly, $n! The others have gone ahead of us, but I will brief you in the flight over.', 0);
INSERT INTO `quest_offer_reward` VALUES (32679, 0, 0, 0, 0, 0, 0, 0, 0, 'We must move quickly, $n! The others have gone ahead of us, but I will brief you in the flight over.', 0);
INSERT INTO `quest_offer_reward` VALUES (32680, 0, 0, 0, 0, 0, 0, 0, 0, 'You have arrived just in time, $R. It is crucial we gain control of these shores before the Kirin Tor.$B$BThere is much to explain.', 0);
INSERT INTO `quest_offer_reward` VALUES (32681, 0, 0, 0, 0, 0, 0, 0, 0, 'You have arrived just in time, $n. This land needs to be controlled by the Kirin Tor for everyone\'s safety. This will not be an easy task.', 0);
