INSERT INTO `quest_offer_reward` VALUES (32708, 0, 0, 0, 0, 0, 0, 0, 0, 'Very impressive.$B$BShould you find more stones, you should continue this ruse. It is sure to be quite some time before they catch on to our ploy.', 0);
