INSERT INTO `quest_offer_reward` VALUES (32710, 0, 0, 0, 0, 0, 0, 0, 0, 'Слизь, бывшая плотью Мясо\'рока, утекает в сточные воды. Судя по всему, он либо мертв, либо получил серьезные повреждения, от которых никогда не оправится.', 20338);
INSERT INTO `quest_offer_reward` VALUES (32713, 0, 0, 0, 0, 0, 0, 0, 0, 'Скальный ужас крошил все на куски, но вам удалось увернуться от осколков и не задохнуться в облаке пыли.', 19865);
INSERT INTO `quest_offer_reward` VALUES (32718, 0, 0, 0, 0, 0, 0, 0, 0, 'May the stars smile upon you, $n.', 0);
