INSERT INTO `quest_offer_reward` VALUES (36702, 0, 0, 0, 0, 0, 0, 0, 0, 'МЯСЫШ ЧУВСТВУЕТ СЕБЯ ЛУЧШЕ!$B$BМЯСЫШ ТЕПЕРЬ ДРАТЬСЯ ЗА |3-3($N)!$B$BМЯСЫШ ТЕПЕРЬ ВСЕГДА ХОДИТЬ ЗА |3-4($N)!!!', 23911);
INSERT INTO `quest_offer_reward` VALUES (36705, 0, 0, 0, 0, 0, 0, 0, 0, 'You did it. You really did it! You\'re the new lord of the ring!', 0);
INSERT INTO `quest_offer_reward` VALUES (36706, 66, 0, 0, 0, 2000, 0, 0, 0, 'Commander! I am humbled to be able to serve you. The Lieutenant requests your presence.', 19342);
INSERT INTO `quest_offer_reward` VALUES (36707, 66, 0, 0, 0, 0, 0, 0, 0, 'Throm-Ka, commander. I am pleased to finally meet you in person.', 19342);
INSERT INTO `quest_offer_reward` VALUES (36708, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you again for taking the time to visit, commander. You have no idea how much it means to us.', 19342);
INSERT INTO `quest_offer_reward` VALUES (36709, 0, 0, 0, 0, 0, 0, 0, 0, 'So, you are the commander? Sylvanas sent me to find you.', 19342);
INSERT INTO `quest_offer_reward` VALUES (36711, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпринес:принесла; мое снаряжение! Видать, удача все-таки в кои-то веки решила повернуться ко мне лицом!$B$BАбу\'гар вернет тебе должок, уж будь $gспокоен:спокойна;!$B$BСперва только вот выловлю рыбу-гиганта...', 20574);
INSERT INTO `quest_offer_reward` VALUES (36741, 0, 0, 0, 0, 0, 0, 0, 0, 'And here\'s your potion, $n! If you don\'t have any use for it, maybe gift it to a friend who does?', 0);
INSERT INTO `quest_offer_reward` VALUES (36742, 0, 0, 0, 0, 0, 0, 0, 0, 'It is made. If you don\'t have any use for it, give it to one of our melee.', 0);
INSERT INTO `quest_offer_reward` VALUES (36754, 4, 0, 0, 0, 0, 0, 0, 0, 'Your collection of artifacts becomes more impressive.', 19243);
INSERT INTO `quest_offer_reward` VALUES (36790, 2, 378, 0, 0, 0, 0, 0, 0, 'Наши жизни слишком ценны.\nПриятно знать, что убийцы понесли заслуженную кару.\n\nСпасибо тебе, $n, ты $gоказал:оказала; нам великую честь.', 22810);
INSERT INTO `quest_offer_reward` VALUES (36791, 0, 0, 0, 0, 0, 0, 0, 0, 'Here she is! Careful with her, these potions have a reputation for mysteriously disappearing.', 0);
INSERT INTO `quest_offer_reward` VALUES (36800, 0, 0, 0, 0, 0, 0, 0, 0, 'Я готов купить у тебя всех рыб-гигантов, которых ты поймаешь, ну вроде этой. Заглядывай ко мне с уловом в любое время, и я предложу тебе самую лучшую цену!', 24015);
INSERT INTO `quest_offer_reward` VALUES (36801, 396, 0, 0, 0, 0, 0, 0, 0, 'Ah, Commander $n. Good to see you. Now our party is complete.', 19342);
INSERT INTO `quest_offer_reward` VALUES (36802, 0, 0, 0, 0, 0, 0, 0, 0, 'I will take any lunkers you like find like this off your hands. Come back and see me anytime you catch one and I will pay you top dollar!', 0);
