INSERT INTO `quest_offer_reward` VALUES (37562, 0, 0, 0, 0, 0, 0, 0, 0, '$n, your courage and dedication has made Draenor a safer place. Take these armaments, and may the Light protect you on your journey.', 0);
INSERT INTO `quest_offer_reward` VALUES (37563, 273, 0, 0, 0, 0, 0, 0, 0, 'Excellent choice. I will be sad to see them leave, but I know they will help strengthen our combined packs in the coming conflicts.$b$bI will see that they make their way to your garrison as soon as they have said farewell to their loved ones.$b$bThey should be ready the next time you need to send followers on a mission.', 19342);
INSERT INTO `quest_offer_reward` VALUES (37565, 273, 0, 0, 0, 0, 0, 0, 0, 'Итак, пленники на свободе. Спасибо тебе, $n.\n\nНу что, ты $gготов:готова; к встрече с принцем?', 21384);
INSERT INTO `quest_offer_reward` VALUES (37566, 1, 273, 0, 0, 0, 0, 0, 0, 'Теперь, когда Океаний мертв, скроги наверняка передерутся друг с другом. Во всяком случае, им будет не до того чтобы помогать нагам.\n\nЧто ж, полагаю, можно смело сказать, что операция прошла успешно, хоть меня и схватили. Нам очень повезло, что ты $gоказался рядом и пришел:оказалась рядом и пришла; нам на помощь.\n\nУвидимся, $n.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37568, 4, 0, 0, 0, 0, 0, 0, 0, 'Let me unpack that for you.$b$bI should let you know - while we managed to expedite this one, future work orders will take longer.', 19342);
INSERT INTO `quest_offer_reward` VALUES (37569, 4, 0, 0, 0, 0, 0, 0, 0, 'Минутку, сейчас распакую.$b$bСразу предупреждаю – этот заказ нам удалось выполнить сверхсрочно, но на все будущие будет уходить больше времени.', 19243);
