INSERT INTO `quest_offer_reward` VALUES (37644, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve earned this!', 0);
INSERT INTO `quest_offer_reward` VALUES (37645, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve earned this!', 0);
INSERT INTO `quest_offer_reward` VALUES (37653, 273, 0, 0, 0, 0, 0, 0, 0, 'О да. Я уже чувствую, что их душонки куда слабее плоти.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37654, 6, 661, 0, 0, 0, 0, 0, 0, 'Ты хоть знаешь, что это за корабль? Это \"Месть Королевы\". А эта королева – Сильвана Ветрокрылая, королева банши.\n\nЕсли бы нашу усадьбу не затопило, я бы прямо сейчас двинул домой в Гилнеас.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37656, 5, 0, 0, 0, 0, 0, 0, 0, 'Меня всегда одолевали сомнения насчет Цианы. Что-то с ней было не так. Похоже, она сломалась и перешла на сторону Легиона.\n\nНо это ей даром не пройдет – и этой Кордане Оскверненная Песнь тоже.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37657, 5, 274, 0, 0, 0, 0, 0, 0, 'Что? Демон-тюремщик? Забвенон? Какое жуткое имя.\n\nЧто ж, неудивительно тогда, что мы не смогли с ними договориться насчет торговли. Эх, знать бы раньше – можно было бы обойтись без этой резни!\n\nНу, что сделано, то сделано. Впредь будем умнее.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37658, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, |3-6($c). Теперь у нас есть время на то, чтобы подготовить ответный удар.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37659, 0, 0, 0, 0, 0, 0, 0, 0, '< In the middle of a soaked trunk of junk, you find the captain\'s ship\'s log. Some pages are well-preserved, and from these records you can extract interesting information about the possible whereabouts of the new Horde chief - Silvana Windfall.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37660, 5, 0, 0, 0, 0, 0, 0, 0, 'Я бы не особо доверяла тому, что сказал Арев\'наал. Демоны-вопрошатели – известные манипуляторы и дезинформаторы. Но ты же ведь не думаешь, что в наших рядах есть предатель? Или...\n\n<Пожирательница душ задумывается и хмурится.>\n\nБоюсь, что с ответами на наши вопросы о владыке Иллидане придется немного обождать.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37666, 1, 0, 0, 0, 0, 0, 0, 0, 'Я в неоплатном долгу перед тобой, $n. Мастерство и смелость Некоронованных поражают воображение.\n\nНо это была самая легкая часть работы, теперь пора совершить невозможное.', 23420);
INSERT INTO `quest_offer_reward` VALUES (37669, 0, 0, 0, 0, 0, 0, 0, 0, 'Поздравляем!', 19116);
INSERT INTO `quest_offer_reward` VALUES (37678, 1, 0, 0, 0, 0, 0, 0, 0, 'Насчет вырванных страниц и порванных переплетов можешь не переживать – я прикажу своим помощникам все прибрать.$b$bСпасибо за помощь. Можешь возвращаться к своим изысканиям.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37687, 1, 6, 0, 0, 0, 1500, 0, 0, 'Значит, здесь замешан Совет Теней? Любопытно...', 20779);
INSERT INTO `quest_offer_reward` VALUES (37688, 1, 6, 0, 0, 0, 1500, 0, 0, 'Значит, здесь замешан Совет Теней? Любопытно...', 20779);
INSERT INTO `quest_offer_reward` VALUES (37689, 5, 0, 0, 0, 0, 0, 0, 0, 'У тебя получилось! Матиас Шоу снова во главе ШРУ, и ты не $gпозволил:позволила; Орде и Альянсу вцепиться друг другу в глотки.\n\nУ нас нет ни малейших сомнений в том, что ты будешь незаменимым членом Союза Теней и Некоронованных.\n\nПоздравляю, Теневой клинок $n!', 23420);
INSERT INTO `quest_offer_reward` VALUES (37690, 333, 0, 0, 0, 0, 0, 0, 0, 'Стой, кто идет?$b$bТы кто? $gПосланник:Посланница; этого странного парящего города?', 21384);
INSERT INTO `quest_offer_reward` VALUES (37727, 396, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно! Для начала очень неплохо.$B$BОбязательно при случае намешай себе побольше этого потрясающего напитка. Я хочу, чтобы весь мир услышал о моих алхимических коктейлях!$B$BПравда, для этого тебе придется раздобыть еще яиц.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37728, 396, 0, 0, 0, 0, 0, 0, 0, 'Я изначально собирался преподнести этот коктейль одной из придворных дам. Это помогло бы мне упрочить свое положение в определенных кругах.\n\nНо я в итоге так и не решился. В конце концов, кто единственный достоин отведать мой самый последний шедевр? Только я сам!', 21384);
INSERT INTO `quest_offer_reward` VALUES (37729, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасная работа, $gюный:юная; $n. Если ты и дальше будешь демонстрировать такие успехи, выпускной экзамен станет для тебя простой формальностью.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37730, 0, 0, 0, 0, 0, 0, 0, 0, 'Она... Она отдала тебе ключ? С ума сойти!', 21384);
INSERT INTO `quest_offer_reward` VALUES (37733, 1, 1, 0, 0, 0, 0, 0, 0, 'There he is, behind me.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37736, 1, 0, 0, 0, 0, 0, 0, 0, 'У тебя шляпа и мантия разного цвета – но все равно прогресс налицо. Я тебя даже сразу и не узнала.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37756, 1, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно, командир! Зараза Бурогриба не распространится по Дренору.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37757, 0, 0, 0, 0, 0, 0, 0, 0, 'Ko\'ragh be defeated. You be as strong as dey say, commander. I be proud to be at your side.', 0);
INSERT INTO `quest_offer_reward` VALUES (37758, 0, 0, 0, 0, 0, 0, 0, 0, 'A brilliant victory, commander! Imperator Mar\'gok be defeated. You indeed be a champion of da Horde!', 0);
INSERT INTO `quest_offer_reward` VALUES (37762, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, commander. With da Heart of da Mountain defeated, you have crippled the ability of da Blackrock clan to use their most powerful furnace.', 0);
INSERT INTO `quest_offer_reward` VALUES (37763, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, commander! Not since Garrosh Hellscream commanded our forces in Northrend have I seen such decisive action.', 0);
INSERT INTO `quest_offer_reward` VALUES (37764, 0, 0, 0, 0, 0, 0, 0, 0, 'Brilliant work, commander. Without da mind of Admiral Gar\'an behind it, da Iron Horde\'s naval fleet be greatly diminished.', 0);
INSERT INTO `quest_offer_reward` VALUES (37765, 1, 66, 0, 0, 0, 0, 0, 0, 'Один из самых беспощадных орков повержен. Прими мои поздравления и мою благодарность.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37766, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Brackenspore\'s infestation will not spread to the rest of Draenor. Thank you, commander.', 0);
INSERT INTO `quest_offer_reward` VALUES (37767, 0, 0, 0, 0, 0, 0, 0, 0, 'Great work! Ko\'ragh is defeated. You truly are as powerful as they say.', 0);
INSERT INTO `quest_offer_reward` VALUES (37768, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done! A great blow has been dealt this day.', 0);
INSERT INTO `quest_offer_reward` VALUES (37769, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, commander! With the Heart of the Mountain defeated, the Blackrock will have nothing to wield but sticks and stones.', 0);
INSERT INTO `quest_offer_reward` VALUES (37770, 0, 0, 0, 0, 0, 0, 0, 0, 'With Kromog defeated, we won\'t seeing as many Iron Horde machines rolling out to the front lines. You\'ve saved a lot of lives today, commander.', 0);
INSERT INTO `quest_offer_reward` VALUES (37771, 0, 0, 0, 0, 0, 0, 0, 0, 'Great work, commander! We\'ll wrest control of the seas yet!', 0);
INSERT INTO `quest_offer_reward` VALUES (37772, 0, 0, 0, 0, 0, 0, 0, 0, 'So it\'s done, then? The foundry is in shambles. We\'ve delivered a mighty blow today, commander. Great work!', 0);
INSERT INTO `quest_offer_reward` VALUES (37773, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, this must be the crystal Eksos keeps talking about. We\'ll get top men studying this right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (37774, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect, sir. I\'m glad we won\'t have to worry about saberon spirits on top of everything else that\'s trying to murder us.', 0);
INSERT INTO `quest_offer_reward` VALUES (37775, 0, 0, 0, 0, 0, 0, 0, 0, 'Word is already spreading of your epic fight, commander. This may improve relations with the ogres in the future.', 0);
INSERT INTO `quest_offer_reward` VALUES (37776, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, commander. We hope the intel we gather from the Tanaan Channel will prove very useful in the coming months.', 0);
INSERT INTO `quest_offer_reward` VALUES (37777, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. This meat ought to keep those pesky saberon busy.', 0);
INSERT INTO `quest_offer_reward` VALUES (37778, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, commander. We will get to work on this right away!', 0);
INSERT INTO `quest_offer_reward` VALUES (37780, 66, 0, 0, 0, 0, 0, 0, 0, 'Another Gul\'dana messenger has fallen before your power, commander. Our victory today will make Gul\'dan tremble with fear.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37781, 66, 0, 0, 0, 0, 0, 0, 0, 'С Кровавым Молотом покончено, и шансы императора Мар\'гока заполучить реликвию из шлаковых шахт теперь ничтожны.$b$bОтличный день.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37782, 0, 0, 0, 0, 0, 0, 0, 0, 'Derailing da Grimrail be a mighty blow to da Iron Horde. It will take dem a great deal of time to amass more troops thanks to you.', 0);
INSERT INTO `quest_offer_reward` VALUES (37783, 0, 0, 0, 0, 0, 0, 0, 0, 'Without Skulloc and their precious docks, da Iron Horde be a wolf pack trapped within their own cave... unable to hunt.', 0);
INSERT INTO `quest_offer_reward` VALUES (37784, 0, 0, 0, 0, 0, 0, 0, 0, 'I already be feelin\' da relief of da ancestral spirits of Draenor with Ner\'zhul\'s fall. You have deprived Grommash of a powerful weapon, commander.', 0);
INSERT INTO `quest_offer_reward` VALUES (37785, 66, 0, 0, 0, 0, 0, 0, 0, 'Perhaps the Ruhmar followers will now be more respectful of the \"inferior races\".', 20886);
INSERT INTO `quest_offer_reward` VALUES (37786, 0, 0, 0, 0, 0, 0, 0, 0, 'Dese wizards\' deaths be punishment enough for their lack of foresight. We should be thankful dat we were able to stop da overgrowth before it reached our world.', 0);
INSERT INTO `quest_offer_reward` VALUES (37787, 0, 0, 0, 0, 0, 0, 0, 0, 'With Warlord Zaela\'s fall we can be focusin\' our efforts on Draenor and da heart of da Iron Horde. They have nowhere to hide now.', 0);
INSERT INTO `quest_offer_reward` VALUES (37789, 0, 0, 0, 0, 0, 0, 0, 0, 'With Warlord Zaela out of the picture we can focus our efforts on Draenor and the heart of the Iron Horde. They have nowhere to hide now.', 0);
INSERT INTO `quest_offer_reward` VALUES (37790, 0, 0, 0, 0, 0, 0, 0, 0, 'I wish we could have saved the mages\' lives, but sadly it was not to be. We should be thankful that we were able to stop the overgrowth before it reached our world.', 0);
INSERT INTO `quest_offer_reward` VALUES (37791, 0, 0, 0, 0, 0, 0, 0, 0, 'Perhaps now the Adherents of Rukhmar will respect we \'lesser races\'.', 0);
INSERT INTO `quest_offer_reward` VALUES (37792, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m no shaman, far from it, but even I can feel the relief of the ancestral spirits of Draenor with Ner\'zhul\'s fall. You have deprived Grommash of a powerful weapon, commander.', 0);
INSERT INTO `quest_offer_reward` VALUES (37793, 0, 0, 0, 0, 0, 0, 0, 0, 'Without Skulloc and their precious docks, the Iron Horde won\'t be launching any attacks for a good long time!', 0);
INSERT INTO `quest_offer_reward` VALUES (37794, 0, 0, 0, 0, 0, 0, 0, 0, 'Derailing the Grimrail is a mighty blow to the Iron Horde. It will take them a great deal of time to amass more troops thanks to you.', 0);
INSERT INTO `quest_offer_reward` VALUES (37795, 0, 0, 0, 0, 0, 0, 0, 0, 'The Bloodmaul are finished, and with them Imperator Mar\'gok\'s chances of acquiring the relic within the slag mines.$B$BThis is as fine a day as I\'ve seen in some time.', 0);
INSERT INTO `quest_offer_reward` VALUES (37796, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, commander. Gul\'dan will surely tremble at the heels of our victory this day.', 0);
INSERT INTO `quest_offer_reward` VALUES (37811, 0, 0, 0, 0, 0, 0, 0, 0, 'This matches the description of what Dr. Jones was looking for. I\'ll make sure it makes it to him. $B$BHere is your reward, $C.', 0);
INSERT INTO `quest_offer_reward` VALUES (37813, 0, 0, 0, 0, 0, 0, 0, 0, 'You always be savin\' da day, commander. Thank you.', 0);
INSERT INTO `quest_offer_reward` VALUES (37814, 0, 0, 0, 0, 0, 0, 0, 0, 'Da spirits smile upon ya, commander. Thank you for all you do.', 0);
INSERT INTO `quest_offer_reward` VALUES (37815, 0, 0, 0, 0, 0, 0, 0, 0, 'Word already be spreadin\' about your exploits. If da ogres did not fear your strength yet, dey should now.', 0);
INSERT INTO `quest_offer_reward` VALUES (37816, 0, 0, 0, 0, 0, 0, 0, 0, 'A thousand thank yous, commander. Dis spyin\' should prove valuable in da comin\' months.', 0);
INSERT INTO `quest_offer_reward` VALUES (37817, 0, 0, 0, 0, 0, 0, 0, 0, 'Dis be exactly what we need, commander. I\'ll send it to da chefs right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (37818, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent. Dis be exactly what we lookin\' for!', 0);
INSERT INTO `quest_offer_reward` VALUES (37819, 0, 0, 0, 0, 0, 0, 0, 0, 'У тебя получилось! Это были тренировочные гонки, так что время не имеет значения. Но похвалу ты $gзаслужил:заслужила;!$b$bТеперь можно перейти к Крутым гонкам!', 19702);
INSERT INTO `quest_offer_reward` VALUES (37834, 6, 274, 0, 0, 0, 0, 0, 0, 'Give her here. Be careful. Do you feel I got goosebumps. She\'s watching us, $n.$B$bThe Shadow Council in my house ... Let\'s get this over with before anything bad happens.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37835, 1, 0, 0, 0, 0, 0, 0, 0, 'Замечательно, $n! Меня беспокоит, что Совет Теней занимает такие прочные позиции в этих удаленных районах Дренора. Гул\'дан не сидел сложа руки.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37836, 6, 0, 0, 0, 0, 0, 0, 0, 'Garon is still in shock, but she\'s with us now. She wants revenge on her former lord. I\'m going to forgive her for hitting me.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37837, 5, 1, 0, 0, 0, 0, 0, 0, 'You\'re $goba:both; you did great! $b$bGul\'dan is an even slippier type than I thought. He\'s been secretly gathering troops, and now that we\'ve weakened his enemies, he intends to gain power over the rest of the Iron Horde.$b$bWe need to plan our next move carefully.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37838, 1, 66, 0, 0, 0, 0, 0, 0, '$c, I know I didn\'t do much to earn your trust. Until you freed me, my life didn\'t belong to me. $b$b Your tenacity and the wisdom of your supreme wizard helped me become free. You believed:believed in me even when I didn\'t believe in myself. I will serve you here on the Drenor until my death.  $b$b Just show me your enemies, and I will kill them immediately... Commander.', 20886);
INSERT INTO `quest_offer_reward` VALUES (37839, 1, 2, 0, 0, 0, 0, 0, 0, 'Ты $gзаслужил:заслужила; эту награду, |3-6($c). Ты $gисходил:исходила; мой мир вдоль и поперек. Ты $gвыстоял:выстояла;, когда все шансы были не на твоей стороне; ты $gвышел победителем:вышла победительницей; из противостояния с древними чудовищами, темной магией и ужасающими машинами. Не раз и не два ты $gвоодушевлял:воодушевляла; моих собратьев своей исключительной смелостью и целеустремленностью.$b$bСпасибо тебе за все, $n.', 20490);
INSERT INTO `quest_offer_reward` VALUES (37840, 0, 0, 0, 0, 0, 0, 0, 0, 'You have earned this, $C. You\'ve traveled the length and breadth of this savage world. You\'ve surmounted unimaginable odds and stood toe-to-toe against ancient beasts, dark magics, and terrifying machines. Time and again you\'ve surprised me with your patience and bravery.$B$BThank you, $n. I am proud to call you my friend.', 0);
INSERT INTO `quest_offer_reward` VALUES (37841, 1, 0, 0, 0, 0, 0, 0, 0, 'Итак, мы снова встретились.$b$bПристань у тебя теперь есть, так что можно обсудить наши дальнейшие планы...', 20886);
INSERT INTO `quest_offer_reward` VALUES (37852, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad to see this out of the claws of the Adherents of Rukhmar. $B$BI\'ll get this to Dr. Jones right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (37853, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо. Твои старания помогли притупить его боль.$b$b<Стеллагоса вздыхает.>', 21384);
INSERT INTO `quest_offer_reward` VALUES (37855, 1, 0, 0, 0, 0, 0, 0, 0, 'Не беспокойся, маленькое создание. Я по-прежнему лежу в Покое Лазурного Крыла, в своем пруду.$b$bЧтобы проецировать свой образ, много энергии мне не требуется. Да, я уже стар и осталось мне недолго, но я по-прежнему остаюсь синим драконом.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37856, 0, 0, 0, 0, 0, 0, 0, 0, 'Twelve withered slain? Very good.$b$bThat should be enough to keep them away for a few months.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37857, 1, 0, 0, 0, 0, 0, 0, 0, 'Они присасываются к силовым линиям?', 21384);
INSERT INTO `quest_offer_reward` VALUES (37858, 0, 0, 0, 0, 0, 0, 0, 0, '...so many of them...', 0);
INSERT INTO `quest_offer_reward` VALUES (37859, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь я в огромном долгу перед тобой, $n.$b$bПомочь старому дракону вроде меня – уже немалый подвиг. Но спасти моих детенышей и обеспечить выживание нашего рода?$b$bТеперь я всецело поддержу тебя в твоем предприятии.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37860, 457, 0, 0, 0, 0, 0, 0, 0, 'Whatever his motivations were, the pylons are broken now. Grandfather\'s pool should be filling with mana again.$b$bNow where did that Runas run off to?', 21384);
INSERT INTO `quest_offer_reward` VALUES (37861, 1, 0, 0, 0, 0, 0, 0, 0, 'Beautiful! Ha-ha! I feel like I\'m only 30,000 years old again! Don\'t laugh, $n, for a dragon like me, it\'s the heyday of power. Mana\'s flowing into my pool again, $n. We did it.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37862, 1, 1, 0, 0, 0, 0, 0, 0, 'No... we\'re too late!', 21384);
INSERT INTO `quest_offer_reward` VALUES (37868, 0, 0, 0, 0, 0, 0, 0, 0, 'Good job, and nice time!', 0);
INSERT INTO `quest_offer_reward` VALUES (37885, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ll make sure this gets to Dr. Jones. $B$BTo be honest, I\'m eager to get it off my hands.', 0);
INSERT INTO `quest_offer_reward` VALUES (37889, 0, 0, 0, 0, 0, 0, 0, 0, 'The landing went smoothly, but there\'s trouble ahead of us.', 20779);
INSERT INTO `quest_offer_reward` VALUES (37890, 0, 0, 0, 0, 0, 0, 0, 0, 'Что дорога свободна – это хорошо, но есть другая проблема.', 20779);
INSERT INTO `quest_offer_reward` VALUES (37891, 1, 0, 0, 0, 0, 0, 0, 0, 'С возвращением, командир. Как раз только что вернулись наши разведчики с рапортом об успехе операции.$B$BОтличная работа!', 20779);
INSERT INTO `quest_offer_reward` VALUES (37910, 0, 0, 0, 0, 0, 0, 0, 0, 'Good job, and nice time!', 0);
INSERT INTO `quest_offer_reward` VALUES (37911, 0, 0, 0, 0, 0, 0, 0, 0, 'Good job, and nice time!', 0);
INSERT INTO `quest_offer_reward` VALUES (37919, 0, 0, 0, 0, 0, 0, 0, 0, 'Who would\'ve thought that such a normal looking spear would be able to take down a full-sized Gronn? $B$BI\'m sure Dr. Jones will appreciate this in his collection.', 0);
INSERT INTO `quest_offer_reward` VALUES (37934, 0, 0, 0, 0, 0, 0, 0, 0, 'All right, now we have a supply line. I managed to find a great place for the base.', 20779);
INSERT INTO `quest_offer_reward` VALUES (37935, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Это была легкая часть – а трудная еще впереди.', 20779);
INSERT INTO `quest_offer_reward` VALUES (37940, 1, 0, 0, 0, 0, 0, 0, 0, 'Разведчики уже доложили нам о твоих успехах. $B$BЛок\'тар!', 20886);
INSERT INTO `quest_offer_reward` VALUES (37945, 0, 0, 0, 0, 0, 0, 0, 0, 'This definitely looks like no Laughing Skull mask I\'ve seen before. I\'ll take it to Jones and see what he makes of it. $B$BHe left me with this payment for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (37948, 0, 0, 0, 0, 0, 0, 0, 0, 'Word is already spreading of your epic fight, commander. This may improve relations with the ogres in the future.', 0);
INSERT INTO `quest_offer_reward` VALUES (37949, 0, 0, 0, 0, 0, 0, 0, 0, 'Word already be spreadin\' about your exploits. If da ogres did not fear your strength yet, dey should now.', 0);
INSERT INTO `quest_offer_reward` VALUES (37957, 1, 0, 0, 0, 0, 0, 0, 0, 'Stay away from him, $n. These fallen elves are all the same. Disgusting, two-faced creatures.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37959, 398, 30, 0, 0, 0, 4000, 0, 0, '<Runas feverishly rubs the gems into powder and rubs magic dust on his face, hands and back.>$b$b<It\'s a moment of inexplicable bliss. >', 21384);
INSERT INTO `quest_offer_reward` VALUES (37960, 1, 0, 0, 0, 0, 0, 0, 0, 'Я действительно чувствую себя лучше.$b$bЕще немного, $n, и эти конденсаторы меня бы совсем доконали.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37961, 0, 0, 0, 0, 0, 0, 0, 0, 'What great shape this stuff is in. Yeah, honest! I think I can work with this junk. Give me just a minute here...', 0);
INSERT INTO `quest_offer_reward` VALUES (37964, 274, 6, 0, 0, 0, 0, 0, 0, 'Здесь полно стражи. Не знаю, на чем основывается уверенность Гароны.$b$bДавай я введу тебя в курс дела...', 20886);
INSERT INTO `quest_offer_reward` VALUES (37968, 1, 0, 0, 0, 0, 0, 0, 0, 'С возвращением, командир. Как раз только что вернулись наши разведчики с рапортом об успехе операции.$B$BОтличная работа!', 20574);
INSERT INTO `quest_offer_reward` VALUES (37979, 0, 0, 0, 0, 0, 0, 0, 0, 'This is incredible! I would never be able to tell that this crystal was once broken! $B$BI\'m sure Dr. Jones will be pleased to see this, as well as the draenei if the stories behind it are true.', 0);
INSERT INTO `quest_offer_reward` VALUES (37981, 0, 0, 0, 0, 0, 0, 0, 0, 'You did it. You really did it! Here\'s your winnings, champ.$B$BMan, the Highmaul ogres are not going to like this...', 0);
INSERT INTO `quest_offer_reward` VALUES (37991, 0, 0, 0, 0, 0, 0, 0, 0, 'Приветствую, |3-6($c). Нам очень пригодилась бы твоя помощь.', 21384);
INSERT INTO `quest_offer_reward` VALUES (37992, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, mon! I be takin dis to Jones right away. $B$BHere be your reward.', 0);
INSERT INTO `quest_offer_reward` VALUES (37993, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s good we got dis and not da arakkoa.$B$BJones will be wantin dis, I\'m sure.', 0);
INSERT INTO `quest_offer_reward` VALUES (37994, 0, 0, 0, 0, 0, 0, 0, 0, 'Dis book be a bad ting, mon. I\'m glad we got it before it came into da wrong hands. $B$BI\'ll get it ta Jones for ya.', 0);
INSERT INTO `quest_offer_reward` VALUES (37995, 0, 0, 0, 0, 0, 0, 0, 0, 'Jones will be happy when he sees dis. $B$BHere\'s da payment he promised.', 0);
INSERT INTO `quest_offer_reward` VALUES (37996, 0, 0, 0, 0, 0, 0, 0, 0, 'Dis be an odd mask for a Laughing Skull, to be sure. $B$BJones left dis reward for ya.', 0);
INSERT INTO `quest_offer_reward` VALUES (37997, 0, 0, 0, 0, 0, 0, 0, 0, 'Astoundin, mon! I\'d a never been able ta tell dis was eva broken. $B$BHere\'s da reward dat Jones left, I\'ll get dis to im.', 0);
INSERT INTO `quest_offer_reward` VALUES (38001, 0, 0, 0, 0, 0, 0, 0, 0, 'У нас тут идет полномасштабная война. Что ж – по крайней мере, нет недостатка в противниках!', 20779);
INSERT INTO `quest_offer_reward` VALUES (38014, 0, 0, 0, 0, 0, 0, 0, 0, 'Итак, дело сделано. Приливный камень собран воедино.$b$bЧто же до меня... взгляни. Камень вернул целостность и мне самому. Я чувствую себя здоровым и сильным. Мне теперь все по плечу!', 21384);
INSERT INTO `quest_offer_reward` VALUES (38015, 0, 0, 0, 0, 0, 0, 0, 0, 'Placeholder.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38035, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо за то, что ты здесь.', 21414);
INSERT INTO `quest_offer_reward` VALUES (38036, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты очень вовремя! Я чуть не начала перевязывать раненых листьями.', 21414);
INSERT INTO `quest_offer_reward` VALUES (38044, 1, 0, 0, 0, 0, 0, 0, 0, 'Мне уже доложили, что охотники из клана Кровавой Глазницы отступают в Зет\'Гол. Это даст нам немалое преимущество в грядущих битвах.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38045, 1, 0, 0, 0, 0, 0, 0, 0, 'Превосходно, командир! Из Кровавой Глазницы на оборону Зет\'Гола отправились их лучшие воины.$b$bЭто даст нам определенное преимущество, пусть и ненадолго.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38046, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m already hearing reports from The Iron Front that the tide of the battle has turned.$B$BSoon, The Iron Horde will have nowhere to run!', 0);
INSERT INTO `quest_offer_reward` VALUES (38047, 1, 0, 0, 0, 0, 0, 0, 0, 'Thanks to you, we took the initiative on the Iron Front. Very soon the Gul\'dan and the Iron Horde will have nowhere to retreat.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38052, 0, 0, 0, 0, 0, 0, 0, 0, 'За корабль не переживай – в конце концов, это всего лишь металл да дерево.$B$BПока мы сражаемся, \"Небесный огонь\" будет жить.', 21414);
INSERT INTO `quest_offer_reward` VALUES (38053, 0, 0, 0, 0, 0, 0, 0, 0, 'Sorry. The battery\'s dead, just like the ghoul you took it from. You have to recharge it, but how? Oh, I have an idea...', 21414);
INSERT INTO `quest_offer_reward` VALUES (38057, 0, 0, 0, 0, 0, 0, 0, 0, '<Voin does not react to you in any way, although his eyes are open. He\'s surrounded by an electric field that produces weak humming.', 21414);
INSERT INTO `quest_offer_reward` VALUES (38058, 0, 0, 0, 0, 0, 0, 0, 0, 'Так... Батарея зарядилась полностью. Отлично.$B$BДальше нам понадобится антенна...', 21414);
INSERT INTO `quest_offer_reward` VALUES (38059, 0, 0, 0, 0, 0, 0, 0, 0, '<transmitter lights are on. You hear an anxious voice. >$B$BAnybody in contact? Can you hear me? (Light to me witness, how I hate these things.) $B$BThis is Mischka. Missing soldiers of the 7th legion have returned to camp. Everything seems to be in place. $B$BUh... I got everything. Over and out. $B$BBof this kind. $B$BEnd of story... <Communications are suddenly interrupted. >', 21414);
INSERT INTO `quest_offer_reward` VALUES (38060, 0, 0, 0, 0, 0, 0, 0, 0, 'Наш сигнал бедствия приняли. В ближайшее время нас эвакуируют на заставу Седогрива.$B$BБоюсь, что дальше тебе придется справляться уже без нас.', 21414);
INSERT INTO `quest_offer_reward` VALUES (38142, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38143, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38144, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38145, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38146, 0, 0, 0, 0, 0, 0, 0, 0, 'Grow was the first to fall victim to the insanity that engulfed our clan; soon his fate befell the rest. $B$BBut with these beads I can take the reins of the clan into my own hands, becoming his new leader. $B$BThank you, $gtraveler: traveler ;. I will do my best to revive my clan.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38147, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38148, 0, 0, 0, 0, 0, 0, 0, 0, 'Wonderful. The Archdruids have already assembled.', 0);
INSERT INTO `quest_offer_reward` VALUES (38175, 1, 1, 5, 0, 0, 0, 0, 0, 'Эй, командир. Мы сейчас охлаждаем горнила, но когда ты принесешь обломки. мы уже снова будем готовы к работе.', 20490);
