INSERT INTO `quest_offer_reward` VALUES (38203, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38206, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну что, ты с нами?', 21414);
INSERT INTO `quest_offer_reward` VALUES (38210, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m sure your head is filled with questions. Answers will come, in time.$B$BI am Havi, and my words are those of the Valarjar - the glorious keepers of the Halls of Valor. $B$BYou will need my help if it is the Aegis you seek. Oh yes, I know your goals outsider. As I know many things...', 21414);
INSERT INTO `quest_offer_reward` VALUES (38213, 0, 0, 0, 0, 0, 0, 0, 0, 'Это, судя по всему, один из фолиантов Искара. Но зачем он оставил его здесь?', 20779);
INSERT INTO `quest_offer_reward` VALUES (38223, 1, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $n.$b$bЭто было непростое дело, и я снова очень благодарен тебе за помощь. Искар окончательно обезумел, и его нужно остановить.$b$bЯ надеюсь, ты будешь $gготов:готова;, когда пробьет решающий час.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38225, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38232, 0, 0, 0, 0, 0, 0, 0, 0, 'Мм-м... Оливки... Обожаю оливки... Единственное, ради чего вообще стоило соваться в этот премерзкий мир.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38235, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38237, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная сделка, не правда ли? Ты $gубил:убила; демона, твое оружие стало еще могущественнее, а я теперь сам себе хозяин. Все в выигрыше!', 21384);
INSERT INTO `quest_offer_reward` VALUES (38241, 0, 0, 0, 0, 0, 0, 0, 0, 'So it\'s as I imagined, a battle-stone!$B$BI\'ve been able to collect similar stones here in Draenor, but never one this large! A rare find indeed.', 0);
INSERT INTO `quest_offer_reward` VALUES (38242, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah sure, I be findin\' stones like this everywhere in Draenor, but never one dis big!', 0);
INSERT INTO `quest_offer_reward` VALUES (38243, 0, 0, 0, 0, 0, 0, 0, 0, 'С тобой всегда приятно иметь дело.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38246, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38250, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome back, commander. Our scouts just returned with word of your success.$B$BWell done!', 0);
INSERT INTO `quest_offer_reward` VALUES (38252, 1, 0, 0, 0, 0, 0, 0, 0, 'С возвращением, генерал. Как раз только что вернулись наши разведчики с рапортом об успехе операции.$B$BОтличная работа!', 20886);
INSERT INTO `quest_offer_reward` VALUES (38253, 0, 0, 0, 0, 0, 0, 0, 0, '<The King is carefully examining the map of the Tanaan jungle and the adjacent areas. >$b$b$n! For this garrison, the Alliance is in great debt to you. Thanks to your courage and willingness to lead the army, we were able to wage this war here on the Drenor, not in our home world, putting our loved ones in danger.$b$bIt\'s time to get it over with. Are you ready; ready for the final campaign?', 20886);
INSERT INTO `quest_offer_reward` VALUES (38254, 1, 66, 0, 0, 0, 0, 0, 0, 'Рырк совершенно точно находится в этом поезде, но сам выбраться не сможет. Нам придется... Ой, ты $gпринес:принесла; взрывчатку? Найель снова все продумала! Давай-ка вышибем дверь вагона и попробуем убедить этого инженера отправиться с нами...', 20444);
INSERT INTO `quest_offer_reward` VALUES (38255, 6, 1, 0, 0, 0, 0, 0, 0, 'Убери свое оружие, $gнезнакомец:незнакомка;. Чернорук мертв, а наш новый \"вождь\" только что подписал мне смертный приговор. Так что я готов податься к вам в наемники.$b$bВам нужна пристань? Значит, вам нужен я.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38256, 25, 274, 0, 0, 0, 0, 0, 0, '<Solologist Ryrk looks around the port for the last time. >$b$b All this was a waste of time, |3-6($c). Anything you do not destroy will go to Gul\'dan. $b$b Nothing, my next creations will last for centuries.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38257, 0, 0, 0, 0, 0, 0, 0, 0, 'Командир! Мы тебя ждали. У нас тут полный хаос.$b$bПосле штурма мои рангари постоянно вели наблюдение за этим местом. Буквально несколько недель назад наши враги активизировались. По приказу Гул\'дана остатки Железной Орды отступают в Танаан.$b$bМои разведчики раздобыли весьма интересные сведения...', 20444);
INSERT INTO `quest_offer_reward` VALUES (38258, 6, 1, 0, 0, 0, 0, 0, 0, 'Ну что, нашелся корабел? Да? Отлично! Некоторые твои соратники с нетерпением ждут, когда смогут выйти в море.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38259, 273, 1, 0, 0, 0, 0, 0, 0, '<Parts the horizon. >$b$bThis is a great place. A quiet bay is the right place. From the wreckage of this old warship, you can build your first transport.I see you already have all the materials you need. Well, shall we get started?', 20444);
INSERT INTO `quest_offer_reward` VALUES (38267, 1, 603, 1, 0, 0, 1000, 2000, 0, 'Ah, $n, you\'re just in time. $b$bThe actor told me that the Council of Shadows had begun to teach the shadows their dark arts. Under the banners of the Council of Shadows rose even some of the higher Arakkoas!$b$bI still don\'t get it, what role does Iskar play in all this?', 20779);
INSERT INTO `quest_offer_reward` VALUES (38269, 1, 603, 1, 0, 0, 1000, 2000, 0, 'Ah, $n, you\'re just in time. $b$bThe actor told me that the Council of Shadows had begun to teach the shadows their dark arts. Under the banners of the Council of Shadows rose even some of the higher Arakkoas!$b$bI still don\'t get it, what role does Iskar play in all this?', 20779);
INSERT INTO `quest_offer_reward` VALUES (38270, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты... помнишь меня? Я прошел с тобой через портал. Меня когда-то звали... Ариок. До того, как я превратился... в это.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38271, 0, 0, 0, 0, 0, 0, 0, 0, 'Старая земля... старая кровь. Я чую Килрогга. Он был здесь.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38272, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты ступаешь по священной земле, $gиноземец:чужестранка;. Здесь вожди клана Кровавой Глазницы проводили свои ритуалы, чтобы заглянуть в будущее.$b$bЗа это ты $gдолжен:должна; поплатиться жизнью – но слишком многое поставлено на карту...', 20886);
INSERT INTO `quest_offer_reward` VALUES (38273, 0, 0, 0, 0, 0, 0, 0, 0, 'После того как множество духов было повержено, аура ненависти, излучаемая пещерой, ощутимо ослабла.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38274, 0, 0, 0, 0, 0, 0, 0, 0, 'Ха! Килрогга здесь нет... Только предостережения и дурные предзнаменования. Это твой путь, $n, а я должен убить Килрогга...', 20886);
INSERT INTO `quest_offer_reward` VALUES (38286, 0, 0, 0, 0, 0, 0, 0, 0, '<Golgannet\'s Tidal Stone is unharmed and unprotected. >', 21384);
INSERT INTO `quest_offer_reward` VALUES (38287, 0, 0, 0, 0, 0, 0, 0, 0, 'С тобой всегда приятно иметь дело.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38290, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s been a pleasure trading with you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38293, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s been a pleasure trading with you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38296, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s been a pleasure trading with you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38299, 0, 0, 0, 0, 0, 0, 0, 0, 'Not bad. Not bad at all. Stop by the next time I\'m in the neighborhood, and I might have some new pets to check out.', 0);
INSERT INTO `quest_offer_reward` VALUES (38300, 0, 0, 0, 0, 0, 0, 0, 0, 'Not bad. Not bad at all. Stop by the next time I\'m in the neighborhood, and I might have some new pets to check out.', 0);
INSERT INTO `quest_offer_reward` VALUES (38306, 0, 0, 0, 0, 0, 0, 0, 0, '<Estelle pages through the damaged tome and sighs wistfully.>$B$BOh dear, another paramour dead! Draenor has a terrible habit of eating my lovers alive.', 0);
INSERT INTO `quest_offer_reward` VALUES (38307, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38308, 0, 0, 0, 0, 0, 0, 0, 0, '<You have a quick look at the report, which shows that other SHRU spies besides the Tihoswist were active in this area. >', 21108);
INSERT INTO `quest_offer_reward` VALUES (38312, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38317, 0, 0, 0, 0, 0, 0, 0, 0, 'Наконец-то снова в бой!', 21108);
INSERT INTO `quest_offer_reward` VALUES (38318, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38322, 0, 0, 0, 0, 0, 0, 0, 0, 'Чудесно. Верховные друиды уже в сборе.', 21021);
INSERT INTO `quest_offer_reward` VALUES (38323, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38324, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, they are great.$B$BWell, let’s see if we manage to hold old Garm?', 21108);
INSERT INTO `quest_offer_reward` VALUES (38331, 0, 0, 0, 0, 0, 0, 0, 0, 'Мм-м... Какой аромат, а?', 21108);
INSERT INTO `quest_offer_reward` VALUES (38332, 0, 0, 0, 0, 0, 0, 0, 0, '<Natanos squinting at the wreckage of Silvana\'s ship. >$B$BShe\'s around here somewhere... She should be close by.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38337, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38339, 30, 0, 0, 0, 0, 0, 0, 0, 'СКОЛЬКО-СКОЛЬКО ты $gубил:убила; квалдиров?! Ну ничего себе! Ты куда храбрее, чем кажешься, $gиноземец:чужестранка;!$B$BДумаю, Хелия теперь точно отпустит отсюда нас обоих – после того, как мы обдурим ее цербера.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38342, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38344, 1, 1, 5, 0, 0, 0, 0, 0, 'Yeah, it brings back memories. You didn’t look: you looked; by the numbers?$b$bIt belonged to old Nolan, may he rest in peace. He was not the best researcher, but he was strong in hops!', 20574);
INSERT INTO `quest_offer_reward` VALUES (38345, 0, 0, 0, 0, 0, 0, 0, 0, 'These are in great condition. Nolan would be green with envy.', 0);
INSERT INTO `quest_offer_reward` VALUES (38346, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. I knew you\'d be useful, sugarplum.$B$BThere\'s something very unusual about these coins...', 0);
INSERT INTO `quest_offer_reward` VALUES (38347, 378, 0, 0, 0, 0, 0, 0, 0, 'Это было отвратительно, $gиноземец:чужестранка;. Даже в смерти я не нюхала ничего более гнусного.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38351, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you found Varsha. Now, keep him supplied with waystones and we will be well on our way to defeating the iron horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (38354, 0, 0, 0, 0, 0, 0, 0, 0, 'I see you found Apprentice Var\'nath. Now, keep him supplied with waystones and we will be well on our way to defeating the iron horde.', 0);
INSERT INTO `quest_offer_reward` VALUES (38356, 0, 0, 0, 0, 0, 0, 0, 0, 'This is the best Draenor has to offer? Lucky for you I went to engineering school. One Tune-O-Tron 5000 coming up!', 0);
INSERT INTO `quest_offer_reward` VALUES (38357, 0, 0, 0, 0, 0, 0, 0, 0, 'Королевское фармацевтическое общество выступает исключительно за гуманность.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38358, 11, 0, 0, 0, 100, 0, 0, 0, 'Славно. Поставь контейнер куда-нибудь. Только смотри не расплескай!$B$B<Аптекарь мерзко хихикает.>', 21108);
INSERT INTO `quest_offer_reward` VALUES (38360, 0, 0, 0, 0, 0, 0, 0, 0, 'То есть ей все-таки удалось скрыться...', 21108);
INSERT INTO `quest_offer_reward` VALUES (38361, 0, 0, 0, 0, 0, 0, 0, 0, 'Они только начали расплачиваться за свою дерзость.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38362, 0, 0, 0, 0, 0, 0, 0, 0, 'Посмотри внимательно на эту голову, $p. И запомни, что так будет с каждым, кто осмелится противостоять Отрекшимся.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38376, 1, 0, 0, 0, 0, 0, 0, 0, 'Я уж думал, мы тут все погибнем. Я обязан тебе жизнью, $n.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38377, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38381, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38382, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38384, 0, 0, 0, 0, 0, 0, 0, 0, 'Кенарий в беде? Отец наш, мы не подведем тебя...', 20994);
INSERT INTO `quest_offer_reward` VALUES (38394, 0, 0, 0, 0, 0, 0, 0, 0, 'Y\'know, I think we might be on to somethin\' big here.', 0);
INSERT INTO `quest_offer_reward` VALUES (38395, 0, 0, 0, 0, 0, 0, 0, 0, 'That wasn\'t so hard now was it, peaches?$B$B<Estelle holds the coins you brought up the the light and practically purrs.>$B$BYes yes, these are so very strange...', 0);
INSERT INTO `quest_offer_reward` VALUES (38396, 0, 0, 0, 0, 0, 0, 0, 0, 'Good, good! I\'ve got some awful suspicious suspicions about these here suspicious coins.', 0);
INSERT INTO `quest_offer_reward` VALUES (38397, 0, 0, 0, 0, 0, 0, 0, 0, 'Honeybiscuit, when this is all over, you and I may be remembered as stumbling on a great paranormal discovery. Or I will at least.', 0);
INSERT INTO `quest_offer_reward` VALUES (38399, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah yes, an eternium rose. I heard vendors suddenly stopped buying them... something about the resell value being too low.$B$BYou\'re in luck though, because I know a collector who still wants them. In fact, I can take this one off your hands, but you\'ll have to come back tomorrow if you have another one to sell.', 0);
INSERT INTO `quest_offer_reward` VALUES (38402, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye, I\'ve been talkin\' to the priests in the Mystic Ward and they confirmed my sucpicions. These \'misprinted\' coins are all demonic in origin.$B$BYeh done a good deed takin\' these out of circulation. Well done, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (38404, 0, 0, 0, 0, 0, 0, 0, 0, 'I am most gracious for your services. So many coins! Such a foul energy within them - do you feel it? Somebody was trying to corrupt the draenei from within.$B$BWell, don\'t you worry your pretty head any. My colleagues and I will take VERY good care of these. Thank you, lovely!', 0);
INSERT INTO `quest_offer_reward` VALUES (38405, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38406, 0, 0, 0, 0, 0, 0, 0, 0, 'This big fella must have been growing for years. To be honest, it\'s so cute I\'ll probably just let it go. Maybe you can catch it again one day!', 0);
INSERT INTO `quest_offer_reward` VALUES (38407, 273, 0, 0, 0, 0, 0, 0, 0, 'You said you found it; this note in the bottle? That\'s weird.\n\nNo, I can certainly tell you where the prince of sea giants lives with his scrogs, but are you sure it\'s worth contacting? All to free a handful of drunken matros?', 21384);
INSERT INTO `quest_offer_reward` VALUES (38408, 0, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, ты найдешь что-нибудь себе по вкусу!', 20886);
INSERT INTO `quest_offer_reward` VALUES (38410, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38412, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38413, 0, 0, 0, 0, 0, 0, 0, 0, 'За то, что ты $gсделал:сделала;, я в огромном долгу перед тобой. Мои собратья не смогли сами защитить себя от дрекирьяров и закончили бы свою жизнь у них в рабстве, если бы не ты. $B$BТы настоящий друг торигниров.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38414, 0, 0, 0, 0, 0, 0, 0, 0, 'The news of our mother\'s rescue was a great relief to me. I think she\'ll personally want to express her gratitude to you.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38415, 0, 0, 0, 0, 0, 0, 0, 0, 'I know the battle for Tanaan presses sorely on your forces commander. I am glad you came. The Sargerei and Gul\'dan have the same masters, so this new attack likely affects you as well.', 0);
INSERT INTO `quest_offer_reward` VALUES (38416, 0, 0, 0, 0, 0, 0, 0, 0, 'This is unfortunate. We cannot allow Gorefiend to aid Gul\'dan\'s machinations. He must be slain once and for all.', 0);
INSERT INTO `quest_offer_reward` VALUES (38421, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, что ты так скоро $gоткликнулся:откликнулась; на мой зов. В глубинах под этим священным местом происходит что-то ужасное.', 20338);
INSERT INTO `quest_offer_reward` VALUES (38428, 0, 0, 0, 0, 0, 0, 0, 0, '<Tentatively, the pup licks your hand. After receiving a good scratch behind the ears, the pup barks and wags its tail.>', 0);
INSERT INTO `quest_offer_reward` VALUES (38435, 0, 0, 0, 0, 0, 0, 0, 0, 'The landing went smoothly, but there\'s trouble ahead of us.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38436, 0, 0, 0, 0, 0, 0, 0, 0, 'Силы врагов здесь невелики – мы с ними легко разделаемся. Однако появилась другая проблема.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38440, 1, 0, 0, 0, 0, 0, 0, 0, 'С возвращением, командир. Как раз только что вернулись наши разведчики с рапортом об успехе операции.$B$BОтличная работа!', 20338);
INSERT INTO `quest_offer_reward` VALUES (38441, 1, 0, 0, 0, 0, 0, 0, 0, 'Разведчики уже доложили нам о твоих успехах. $B$BЛок\'тар!', 20886);
INSERT INTO `quest_offer_reward` VALUES (38443, 5, 0, 0, 0, 0, 0, 0, 0, 'Он... он умирает! Сенегос, наверное, старейший из всех еще живущих драконов. Мы не должны позволить ему угаснуть вот так.\n\nС этой магической энергией что-то не в порядке, я это чувствую...', 21384);
INSERT INTO `quest_offer_reward` VALUES (38444, 0, 0, 0, 0, 0, 0, 0, 0, 'Сюда уже направляется группа поддержки, они тут все зачистят. А нам надо двигаться вперед, пока их гонцы еще не успели вернуться к Гул\'дану.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38445, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Это была легкая часть – а трудная еще впереди.', 20444);
INSERT INTO `quest_offer_reward` VALUES (38446, 0, 0, 0, 0, 0, 0, 0, 0, 'Эта кампания обещает быть нелегкой.', 20574);
INSERT INTO `quest_offer_reward` VALUES (38449, 1, 0, 0, 0, 0, 0, 0, 0, 'С возвращением, генерал. Как раз только что вернулись наши разведчики с рапортом об успехе операции.$B$BОтличная работа!', 20886);
INSERT INTO `quest_offer_reward` VALUES (38453, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь у вас достаточно информации, чтобы вычислить убийцу. Вы составили карту.', 20886);
INSERT INTO `quest_offer_reward` VALUES (38455, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38458, 0, 0, 0, 0, 0, 0, 0, 0, 'I was too late to give you the message. I am truly sorry. These poor shaman... if only I had known sooner!$B$BGul\'dan has prepared everything for his ritual. He will use the cipher and his own fel magic to corrupt an elemental lord and use it to sunder the elemental link to the land. This will cause a massive upheaval across Draenor!', 0);
INSERT INTO `quest_offer_reward` VALUES (38459, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gполучил:получила; мое письмо!', 21108);
INSERT INTO `quest_offer_reward` VALUES (38460, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну наконец-то! Теперь у меня есть $gсвой собственный прислужник:своя собственная прислужница;!', 21384);
INSERT INTO `quest_offer_reward` VALUES (38462, 0, 0, 0, 0, 0, 0, 0, 0, 'Not a moment too soon. He has summoned something, I fear the ritual is close to complete.', 0);
INSERT INTO `quest_offer_reward` VALUES (38463, 0, 0, 0, 0, 0, 0, 0, 0, 'You have saved us all. The Cipher has become fragmented with Cyrukh\'s death. This means its guardians can hide the shards all over Draenor so the chances of piecing it together will be impossible.$B$BThis victory will echo through the halls of your warriors and spur them to greater deeds. Gul\'dan can continue plotting... he will fail...', 0);
INSERT INTO `quest_offer_reward` VALUES (38473, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (38499, 0, 0, 0, 0, 0, 0, 0, 0, 'Потрясающе...', 23420);
INSERT INTO `quest_offer_reward` VALUES (38500, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно! Возможно, новый плавень пригодится и для работы с другими материалами.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38501, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну конечно же! Они обошли проблему сварки, изготавливая свои доспехи из цельного куска металла.$b$bНасчет наплечий мне нужно было догадаться самому, но вот их кираса – это что-то удивительное.$b$bСейчас я сделаю пару набросков, чтобы у тебя тоже была возможность сделать эти доспехи.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38502, 1, 0, 0, 0, 0, 0, 0, 0, 'If my guess is right, the solution to the welding problem is in this unit.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38505, 6, 0, 0, 0, 0, 0, 0, 0, 'Oh, the memory block? Just a minute.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38506, 5, 5, 0, 0, 0, 0, 0, 0, '...I would say if we weren\'t right over their homeland. Do you have to translate that?', 23420);
INSERT INTO `quest_offer_reward` VALUES (38507, 1, 25, 0, 0, 0, 0, 0, 0, 'Кто бы мог подумать, что это нужно еще и переводить. Ты $gпринял:приняла, правильное решение.$b$bВот поэтому я и дружу с такими искателями приключений, как ты.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38513, 6, 0, 0, 0, 0, 0, 0, 0, 'И ты думаешь, что $gдостоин:достойна, приобщиться к нашей мудрости, $gиноземец:чужестранка,?', 23420);
INSERT INTO `quest_offer_reward` VALUES (38514, 1, 0, 0, 0, 0, 0, 0, 0, 'Навыки неплохие, но качество никуда не годится.$b$b<Барм ломает рукавицы пополам и бросает на пол>$b$bПравда, тебе удалось доказать, что ты $gдостоин:достойна, стать моим учеником.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38515, 1, 0, 0, 0, 0, 0, 0, 0, 'That\'s great. Let\'s make a fusion out of it right away.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38518, 4, 0, 0, 0, 0, 0, 0, 0, 'Ясно, качество их работы связано с техникой обработки молотом. Как интересно...', 23420);
INSERT INTO `quest_offer_reward` VALUES (38519, 2, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $n.$b$bСпасибо.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38522, 0, 0, 0, 0, 0, 0, 0, 0, 'Я возьму эти бруски, кое-что подправлю, и...$b$bТа-да! Накопытники для твоего верхового животного!', 23420);
INSERT INTO `quest_offer_reward` VALUES (38523, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда Мей подогнала накопытники под размер твоего животного, ты $gсам:сама, сможешь его подковывать.$b$bАх да, чтобы не забыть: вот выкройка. Удачи!', 23420);
INSERT INTO `quest_offer_reward` VALUES (38524, 6, 0, 0, 0, 0, 0, 0, 0, 'Ты $gпришел:пришла, нам помочь?', 23420);
INSERT INTO `quest_offer_reward` VALUES (38525, 1, 0, 0, 0, 0, 0, 0, 0, 'Сойдет.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38526, 1, 273, 0, 0, 0, 0, 0, 0, 'Неплохо, |3-6($c). У тебя хороший замах.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38527, 273, 1, 0, 0, 0, 0, 0, 0, 'Хорошо. Что ж, за работу!', 23420);
INSERT INTO `quest_offer_reward` VALUES (38528, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично.$b$bСиловой камень – капризный материал. Наручи улучшает сера, а вот что может оптимизировать процесс изготовления наплечья? Волшебная пыльца. А шлемам требуются необработанные самоцветы. И так далее.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38530, 1, 6, 25, 0, 0, 0, 0, 0, 'Итак, ты $gвернулся:вернулась,, $n. $gРешил:Решила, продолжить свое обучение?$b$bЧто? Камень Поднебесья?$b$b<У нескольких учеников невольно вырывается возглас изумления.>$b$bЭто совсем другой разговор.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38531, 0, 0, 0, 0, 0, 0, 0, 0, '< Frowning, the Taurian is looking at your work. >$b$bYou\'re something $gI\'ve learned:Learned... $b$bOkay, great. Trial you $gPassed:Passed', 23420);
INSERT INTO `quest_offer_reward` VALUES (38532, 0, 0, 0, 0, 0, 0, 0, 0, '<Barm looks closely at the hammer, twisting it in his hand >$b$bThat\'s the real hammer!', 23420);
INSERT INTO `quest_offer_reward` VALUES (38533, 1, 0, 0, 0, 0, 0, 0, 0, 'One could hardly expect the Taurians to agree to part with their recipes. We are not as hardheaded as these... but we don\'t give out our recipes for nothing either. However, you managed to access the Stone of Celestial Empire and find out where another great anvil is in case I or Urael need it. So you deserve this recipe as a token of our gratitude.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38534, 0, 0, 0, 0, 0, 0, 0, 0, 'The armguards you\'ve made are impressive, but you can provide the same level of protection without wasting so much raw material.$B$BHere, let me show you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38536, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent smithing.$B$BNow, allow me to show you a trick I use to conserve resources.', 0);
INSERT INTO `quest_offer_reward` VALUES (38537, 0, 0, 0, 0, 0, 0, 0, 0, 'Your anvilwork is to be commended, but the recipe you\'re using is unnecessarily wasteful.$B$BTry this instead.', 0);
INSERT INTO `quest_offer_reward` VALUES (38538, 0, 0, 0, 0, 0, 0, 0, 0, 'We don\'t tend to smith many boots up here, but even I can tell that you\'re using too much metal for the sole.$B$BHere\'s a way to cut back on your resource costs.', 0);
INSERT INTO `quest_offer_reward` VALUES (38539, 0, 0, 0, 0, 0, 0, 0, 0, 'The Illidari certainly do like to waste ore, don\'t they?$B$BYou should craft them this way instead. You\'ll thank me later.', 0);
INSERT INTO `quest_offer_reward` VALUES (38540, 0, 0, 0, 0, 0, 0, 0, 0, 'Don\'t tell Thala and Barm, but I do keep a few helm patterns around, mainly for amusement and decoration.$B$BTake a look at this one. You\'ll get similar results with much less need for raw material.', 0);
INSERT INTO `quest_offer_reward` VALUES (38541, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that you\'ve done it the easy way, try this.$B$BIt\'s more difficult, but it will save you loads of materials.', 0);
INSERT INTO `quest_offer_reward` VALUES (38542, 0, 0, 0, 0, 0, 0, 0, 0, 'The amount of ore you\'re wasting on that pattern is simply unacceptable. We should conserve wherever possible: it\'s better for the land and easier on the pocket.$B$BPlease, use this plan instead.', 0);
INSERT INTO `quest_offer_reward` VALUES (38558, 0, 0, 0, 0, 0, 0, 0, 0, '<Closer takes a close look at the crystal. >$B$BBkind of order. Brilliant as new!', 21414);
INSERT INTO `quest_offer_reward` VALUES (38559, 0, 0, 0, 0, 0, 0, 0, 0, 'Новичок? Сюда уже много лет никто не приходил...$b$bВот это Камень Поднебесья. Запомни его линии. Выясни все его свойства. Если ты действительно хочешь перейти на новый уровень мастерства, то, думаю, часто буду тебя видеть здесь, рядом с ним.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38561, 0, 0, 0, 0, 0, 0, 0, 0, 'I was too late to give you the message. I am truly sorry. These poor shamans... if only I had known sooner!$B$BGul\'dan has prepared everything for his ritual. He will use the cipher and his own fel magic to corrupt an elemental lord and use it to sunder the elemental link to the land. This will cause a massive upheaval across Draenor!', 0);
INSERT INTO `quest_offer_reward` VALUES (38562, 0, 0, 0, 0, 0, 0, 0, 0, 'Кровожад продолжает пожирать души моих предков! Этому нужно положить конец раз и навсегда. Кровожада необходимо уничтожить!', 20338);
INSERT INTO `quest_offer_reward` VALUES (38563, 2, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда мы со всем разобрались, можно заняться совершенствованием наших рецептов.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38564, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично, $n. Теперь пришла пора испытать это изделие!', 23420);
INSERT INTO `quest_offer_reward` VALUES (38565, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve eliminated many sargers, and now the payers can finally banish them from Aukindon.', 20338);
INSERT INTO `quest_offer_reward` VALUES (38566, 2, 0, 0, 0, 0, 0, 0, 0, 'Мы справились с задачей, $gВерховный лорд:Верховная леди; $n.$B$BОрден Серебряной Длани возрожден.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38567, 0, 0, 0, 0, 0, 0, 0, 0, '<The Warchief studies a map of Tanaan Jungle and its surroundings, his brow creased with worry.>$B$BAh, Commander $n! Voice of da Horde in Draenor. Ya done well here, bringin\' da fight ta our enemies. Thrall tells me ya exceed his every expectation.$B$BIt be time ta finish dis. Are ya ready?', 0);
INSERT INTO `quest_offer_reward` VALUES (38568, 5, 6, 0, 0, 0, 0, 0, 0, 'Командир! Ну наконец-то. Тут у нас полный бардак. Железная Орда оставила литейную и теперь по приказу Гул\'дана отходит в Танаан, используя эту пристань как перевалочный пункт.$b$bМы тут нашли кое-что интересное, только действовать надо быстро...', 20779);
INSERT INTO `quest_offer_reward` VALUES (38570, 1, 273, 0, 0, 0, 0, 0, 0, 'Информация подтвердилась. Солога Рырка действительно держат в этом поезде, и к нему не подобраться. А что это у тебя? Взрывчатка? Хм... Я смотрю, твои дружки-гоблины в который раз все предусмотрели.$b$bНу что, тогда взрываем дверь – и потолкуем с этим типом...', 20779);
INSERT INTO `quest_offer_reward` VALUES (38571, 6, 1, 0, 0, 0, 0, 0, 0, 'Убери свое оружие, $gнезнакомец:незнакомка;. Чернорук мертв, а наш новый \"вождь\" только что подписал мне смертный приговор. Так что я готов податься к вам в наемники.$b$bВам нужна пристань? Значит, вам нужен я.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38572, 25, 274, 0, 0, 0, 0, 0, 0, '<Solologist Ryrk looks around the port for the last time. >$b$b All this was a waste of time, |3-6($c). Anything you do not destroy will go to Gul\'dan. $b$b Nothing, my next creations will last for centuries.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38573, 6, 1, 0, 0, 0, 0, 0, 0, 'Ну что, нашелся корабел? Да? Отлично! Твои соратники ждут не дождутся, когда смогут выйти в море.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38574, 273, 1, 0, 0, 0, 0, 0, 0, '<Солог Рырк окидывает взглядом горизонт.>$b$bПодходящее место. Хорошая глубокая бухта. Этот старый корабль мы разберем и построим из него твой первый транспорт. Конечно, таскать стройматериалы вверх-вниз будет тяжело, но, если что, скалы обеспечат нам прикрытие.$b$bВ общем, думаю, все получится. Ну что, начинаем строить?', 20779);
INSERT INTO `quest_offer_reward` VALUES (38576, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что ты $gпришел:пришла;.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38577, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь ты видишь, с чем мы тут столкнулись. Мы постараемся максимально отвлечь на себя силы противника, чтобы дать твоей базе больше свободы.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38578, 0, 0, 0, 0, 0, 0, 0, 0, 'Поговорим об этом позже. Спасибо.', 20574);
INSERT INTO `quest_offer_reward` VALUES (38581, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь ты видишь, с чем мы тут столкнулись. Мы постараемся максимально отвлечь на себя силы противника, чтобы дать твоей базе больше свободы.', 20574);
INSERT INTO `quest_offer_reward` VALUES (38582, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38585, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome back, commander. Our scouts just returned with word of your success.$B$BWell done!', 0);
INSERT INTO `quest_offer_reward` VALUES (38586, 0, 0, 0, 0, 0, 0, 0, 0, 'Word of your success has already been passed on by our scouts. $B$BLok\'tar!', 0);
INSERT INTO `quest_offer_reward` VALUES (38595, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38599, 603, 1, 0, 0, 0, 500, 0, 0, 'С возвращением! Уровень сигнала просто отличный, так что все готово к вылету!$B$BКогда соберешься подняться на гору – садись в ракету и лети с ветерком! Она стоит возле Гаргаша.', 20779);
INSERT INTO `quest_offer_reward` VALUES (38603, 603, 1, 0, 0, 0, 500, 0, 0, 'С возвращением!$b$bУровень сигнала от маячков очень хороший! Мы перепрограммировали систему навигации, так что теперь все должно быть в порядке!$b$bКорабль готов к вылету и находится рядом со Стражем Небес Танном.', 20574);
INSERT INTO `quest_offer_reward` VALUES (38611, 0, 0, 0, 0, 0, 0, 0, 0, 'Эти врайкулы такие унылые! Почему Натанос всегда посылает меня на самые скучные и бестолковые задания? $B$BНадеюсь, с твоим прибытием хоть немного повеселее станет.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38612, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, that’s just what you need.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38613, 0, 0, 0, 0, 0, 0, 0, 0, 'Эти врайкулы аж остолбенели, когда ты $gпролетел:пролетела; у них над головами, так что на меня никто и внимания не обратил. $B$BНадеюсь, они не доставили тебе особых хлопот.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38614, 0, 0, 0, 0, 0, 0, 0, 0, 'Ну, размер для тебя, конечно, великоват, но ничего, сгодится. $B$BЭта горная тропа наверняка таит немало опасностей, так что лучше как следует подготовиться заранее.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38615, 0, 0, 0, 0, 0, 0, 0, 0, 'Одной проблемой меньше – уже хорошо! $B$BОднако, похоже, в нашем плане появился изъян. Наверх можно было попасть только через этот мост.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38616, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная мысль. Если уж врайкулы научились делать доспехи из драконьей чешуи, то мы-то чем хуже? $B$BДумаю, у нас все получится еще лучше, чем у них.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38617, 0, 0, 0, 0, 0, 0, 0, 0, 'Славная была драка! А я еще жаловалась, что здесь мне слишком скучно.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38618, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты хочешь пройти испытания? Боюсь, что ты $gопоздал:опоздала;.$B$BЭта битва с дрекирьярами окончательно нас вымотала. Я слишком слаб, чтобы дать тебе ту силу, которую ты ищешь...$B$BВпрочем, ты еще можешь нам помочь...', 21108);
INSERT INTO `quest_offer_reward` VALUES (38624, 0, 0, 0, 0, 0, 0, 0, 0, '<Raven shouts loudly and Javi\'s voice sounds in your head>$B$BYou\'re $gstrong: strong; - the test has made it clear. $B$BBut valor is another companion. Does it follow you?', 21108);
INSERT INTO `quest_offer_reward` VALUES (38641, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38643, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38644, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты уж извини, что я поначалу отнесся к тебе с подозрением. Из-за этих недавних нападений мы тут все на взводе.$B$BЯ очень признателен тебе и твоему другу.$B$BБлагодаря вам мы смогли взять ситуацию под контроль. Многое нужно будет отстраивать заново, но главное, что мы выжили.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38645, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо. Безвинные селяне ничем не спровоцировали эти нападения. Ты правильно $gсделал, что помог:сделала, что помогла; им.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38646, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s good! Now they\'ll know how to break up my fields! Thank you, |3-6($c). It\'s great I haven\'t learned how to shoot yet.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38647, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38649, 1, 0, 0, 0, 0, 0, 0, 0, 'Что-то не так. Я это нутром чую.', 22996);
INSERT INTO `quest_offer_reward` VALUES (38655, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38656, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38662, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38663, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38669, 274, 0, 0, 0, 1000, 0, 0, 0, 'Хорошо. Несмотря на то, что ты много лет $gпровел:провела; в стазисе, твои чувства не притупились.\n\n<Майев с осторожностью смотрит на ваше оружие.>\n\nТолько попробуй поднять руку на меня, $gохотник:охотница; на демонов, и ты узнаешь, почему именно я была стражницей, охранявшей твоего хозяина.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38671, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38672, 0, 0, 0, 0, 0, 0, 0, 0, '< Pull the lever. >', 20994);
INSERT INTO `quest_offer_reward` VALUES (38675, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38684, 0, 0, 0, 0, 0, 0, 0, 0, 'Это еще что такое? Какая мерзость! Я уничтожу их. Наверняка Малфурион поступил бы так же.', 21287);
INSERT INTO `quest_offer_reward` VALUES (38687, 0, 0, 0, 0, 0, 0, 0, 0, 'Этого не может быть!', 21287);
INSERT INTO `quest_offer_reward` VALUES (38688, 0, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда древнее семя проросло, я смогу воспользоваться своим даром друида, чтобы поддерживать с тобой связь через этот росток.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38689, 273, 0, 0, 0, 0, 0, 0, 0, 'Твое лицо вновь заиграло красками, $n.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38690, 396, 0, 0, 0, 0, 0, 0, 0, 'Мы всегда знали, что помощь иллидари потребуется, чтобы защитить этот мир. Именно поэтому мы стали теми, кем.... ЧЕМ мы стали.  ', 20994);
INSERT INTO `quest_offer_reward` VALUES (38691, 0, 0, 0, 0, 0, 0, 0, 0, 'Зачем Гребень Ворона мог понадобиться Легиону? Он же умер и был погребен десять тысяч лет назад.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38692, 1, 0, 0, 0, 0, 0, 0, 0, 'Кто-то должен был это сделать.', 22996);
INSERT INTO `quest_offer_reward` VALUES (38694, 1, 0, 0, 0, 0, 0, 0, 0, 'Окулет все предусмотрел. Хорошо, одной заботой меньше.', 22996);
INSERT INTO `quest_offer_reward` VALUES (38695, 1, 0, 0, 0, 0, 0, 0, 0, 'Apparently, someone informed the Grand Master about Vantir\'s participation in the rebellion. Such cruelty is... Alicanda... How could you?', 22996);
INSERT INTO `quest_offer_reward` VALUES (38710, 1, 0, 0, 0, 0, 0, 0, 0, 'Я надеюсь, в этой компании ты будешь чувствовать себя комфортно. Здесь собрались величайшие воины Света со всего Азерота.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38711, 0, 0, 0, 0, 0, 0, 0, 0, '<Jerod\'s looking at the ring a minute before he picks it up. Then he puts it in his pocket. >$B$BIt\'s a Mayan ring. There\'s no way she\'d break up with him willingly.$B$BSo she\'s being held in the Black Rook Fortress!', 22810);
INSERT INTO `quest_offer_reward` VALUES (38714, 0, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, $n. Вне всякого сомнения, они перевели Майев в тюрьму, что находится под башней.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38715, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. May their souls rest in peace.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38717, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Этими ключами мы сможем открыть любую камеру.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38718, 0, 0, 0, 0, 0, 0, 0, 0, 'Напавшие на деревню духи что-то говорили об их повелителе. Я боюсь, что Легион воскресил лорда Гребня Ворона, чтобы тот исполнял их волю. Какая цена была уплачена за такое возрождение, мне неведомо.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38719, 0, 0, 0, 0, 0, 0, 0, 0, 'Я в огромном долгу перед тобой за свое спасение. Жаль, что моим товарищам повезло меньше.$B$BБоюсь, что за время заключения в тюрьме мне не удалось узнать ничего полезного, но одно ясно – армия демонов растет, и они твердо намерены уничтожить этот мир.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38720, 273, 0, 0, 0, 0, 0, 0, 0, 'I see you\'ve managed to find some survivors. Great, I\'ve had a mountain falling off my shoulders.', 22996);
INSERT INTO `quest_offer_reward` VALUES (38721, 0, 0, 0, 0, 0, 0, 0, 0, 'Лорд Звездный Глаз происходит из благородного дома, но как командир он и в подметки не годился ни Гребню Ворона, ни моему брату.$B$BТеперь, когда с ним покончено, мы наконец-то можем заняться и его повелителем.$B$BНо справиться с ним будет непросто. Мы должны как следует собраться с силами, прежде чем бросать вызов самому лорду Гребню Ворона.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38723, 274, 0, 0, 0, 0, 0, 0, 0, 'Превосходно. Впитай в себя эту мощь, и ты почувствуешь, как твоя сила растет!', 20994);
INSERT INTO `quest_offer_reward` VALUES (38724, 0, 0, 0, 0, 0, 0, 0, 0, '<All Maya\'s face is bruised, her lips are cracked; the body is covered in scars. She looks exhausted, yet determined.$B$BThey\'ve been torturing me here for weeks. They can\'t even torture me!$B$BThey didn\'t hear anything from me. I watched the time, listened to their steps and their voices.$B$BWaiting for a chance to escape and take revenge on my captors$B$BAnd so you brought me $gprincess; my armor and my blade. My salvation will be followed by a bloody massacre.$B$B<Mayev is smiling. >', 21249);
INSERT INTO `quest_offer_reward` VALUES (38725, 273, 0, 0, 0, 0, 0, 0, 0, '$n. I found her.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38727, 1, 0, 0, 0, 0, 0, 0, 0, 'Не каждому дано быть лидером. А еще сложнее командовать на передовой.\n\nМы практически готовы напасть на флагман королевы стаи Тиранны.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38728, 1, 0, 0, 0, 0, 0, 0, 0, 'Теперь, когда ключ у нас, осталось сделать только одну вещь...', 20994);
INSERT INTO `quest_offer_reward` VALUES (38729, 0, 0, 0, 0, 0, 0, 0, 0, '<Since you\'re the only one using a sargerite key, the Square portal is filled with supernatural energy.\n\nIt\'s time for a triumphant return to the Black Temple, where you will help the Illidan lord throw away the invaders from Azeroth and Shattrat.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38743, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38753, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38759, 0, 0, 0, 0, 0, 0, 0, 0, 'The jailer demons sucked out their souls?\r\n\r\nIn these parts, the Burning Legion is many times stronger and more fierce than anywhere else.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38765, 274, 1, 0, 0, 0, 0, 0, 0, 'Cruel necessity, but demon hunters don\'t get used to the victims. We ourselves sacrificed everything to become who we are.\r\n\r\nThe Legion will be defeated. We are waiting for your orders, $n.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38766, 273, 0, 0, 0, 0, 0, 0, 0, 'Already, the flow of demons from below has slowed to a trickle.\n\nI can sense the doom guard\'s energy. You have become more powerful.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38777, 1, 0, 0, 0, 0, 0, 0, 0, '', 21953);
INSERT INTO `quest_offer_reward` VALUES (38778, 1, 0, 0, 0, 0, 0, 0, 0, 'Так это звуки твоих шагов донеслись до меня, когда рунные камни утратили свою власть надо мной? $B$BБлагодарю тебя, $gиноземец:чужестранка;.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38784, 1, 0, 0, 0, 0, 0, 0, 0, '', 21953);
INSERT INTO `quest_offer_reward` VALUES (38785, 1, 0, 0, 0, 0, 0, 0, 0, 'Wow! Was that in one of those rocky creatures? But it looks like you brought it; I just got the body. Next time, don\'t forget your hands and your head. There\'s a lot of meat in there.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38786, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve learned much, $n.$B$BStay a while. I have a story for you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38787, 0, 0, 0, 0, 0, 0, 0, 0, 'Echoes of Rethu\'s time in this land still remain, $n. Be on the lookout for his influence when you are out in the field.', 0);
INSERT INTO `quest_offer_reward` VALUES (38789, 0, 0, 0, 0, 0, 0, 0, 0, 'This... this cannot be! A piece of Rethu\'s own mining log!$B$BLook here, miner... it details a leystone mining technique he was working on...', 0);
INSERT INTO `quest_offer_reward` VALUES (38790, 0, 0, 0, 0, 0, 0, 0, 0, 'Rethu\'s own mining pick...$B$BWell, one of them, anyhow. Stories say he went through them almost weekly.$B$BStill, I\'ve never seen one with my own eyes. Look at the wear on the blade, miner. The angles he used... the strength with which he struck: we could learn much from studying this artifact.', 0);
INSERT INTO `quest_offer_reward` VALUES (38791, 0, 0, 0, 0, 0, 0, 0, 0, 'This... this could not be...$B$BThis is from a member my tribe, $n. Where did you find this?$B$BOne of the first stories we ever learn about Rethu involves the day he lost his horn in battle. There is no way of knowing if this belonged to him but... but...', 0);
INSERT INTO `quest_offer_reward` VALUES (38792, 0, 0, 0, 0, 0, 0, 0, 0, 'You saw a spirit while out mining in the wilds?$B$BNo, I do not believe you. One mustn\'t spend too long out mining, it fatigues the mind. You must be seeing things.$B$BStill, something seems... different about you. You\'ve learned something. Perhaps there\'s some truth to your tale after all...', 0);
INSERT INTO `quest_offer_reward` VALUES (38793, 0, 0, 0, 0, 0, 0, 0, 0, '<The tauren listens as you describe your encounter with the spirit of Rethu Ironhorn.>$B$BI would not normally believe you, but... I can sense truth in your tale. You are a lucky miner.$B$BI am pleased to know that our ancestors look upon us with pride. We have chosen an admirable path.$B$BThank you, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (38794, 0, 0, 0, 0, 0, 0, 0, 0, 'The spirit of Rethu Ironhorn? Tell me everything he said.$B$BHe said he... dug too deep? I see. The Earthmother took him.$B$BThank you for coming to me, miner. This is a tale that the rest of the tribe must hear. We are all the wiser for it.', 0);
INSERT INTO `quest_offer_reward` VALUES (38795, 1, 0, 0, 0, 0, 0, 0, 0, '', 21953);
INSERT INTO `quest_offer_reward` VALUES (38796, 1, 0, 0, 0, 0, 0, 0, 0, '', 21952);
INSERT INTO `quest_offer_reward` VALUES (38797, 1, 0, 0, 0, 0, 0, 0, 0, 'Это – часть адской твари? Смотрите-ка, у нас тут $gбоевой шахтер:боевая шахтерка;!$b$bНо, похоже, по дороге эта руда немного потускнела. В следующий раз непременно срежь раны, нанесенные мечом и магией, прежде чем добывать руду.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38798, 0, 0, 0, 0, 0, 0, 0, 0, 'It is done.$B$BStretch it out a bit.$B$BYou may experience some stray urges to burn worlds and sow chaos for the next week or so. Do your best to resist them.', 0);
INSERT INTO `quest_offer_reward` VALUES (38799, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, this is my sister\'s ring.$B$BThe fact that you found it on a demon\'s corpse suggests that she has either fallen in battle or, like my other sisters, thrown in her hat with the demons themselves.$B$BThank you for bringing this to me. We don\'t have many spare materials here on the front lines... but I can do without these.', 0);
INSERT INTO `quest_offer_reward` VALUES (38800, 0, 0, 0, 0, 0, 0, 0, 0, 'It is. This eye once belonged to my sister, Rin\'thissa.$B$BPoor fool, she cut out her own eye and replaced it with a hunk of ore. That was the tipping point... she\'s completely fel-maddened now.$B$BLearn from her mistake, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (38801, 0, 0, 0, 0, 0, 0, 0, 0, 'This was my sister\'s. Lyrelle\'s.$B$BHer fate is clear now. She no longer needed that signet ring... the girl must have traded in her own mining arm for one carved out of felslate itself.$B$BAren\'t you glad we only used three small shards?$B$BTake this to heart, $n: you can lose everything to greed if you don\'t exercise restraint. Poor Lyrelle paid that price for all of us.', 0);
INSERT INTO `quest_offer_reward` VALUES (38802, 0, 0, 0, 0, 0, 0, 0, 0, 'My sister Ondri was the first among us to mine, and the first among us to fall. Some Nathrezim dreadlord convinced her to replace her own heart - her heart! - with a chunk of felslate.$B$BI slew the dreadlord myself, but couldn\'t find the heart. This must be it.$B$BBig sister... I am sorry. Your loss will not be forgotten. We will learn from your missteps.', 0);
INSERT INTO `quest_offer_reward` VALUES (38803, 0, 0, 0, 0, 0, 0, 0, 0, 'My sister\'s soul rests now. I have you to thank for that.$B$BRin\'thissa was always a charmer. You did well to avoid her invitations, and even better to slay her and end her demonic possession. You\'ve learned much since we first met, $n.', 0);
INSERT INTO `quest_offer_reward` VALUES (38804, 0, 0, 0, 0, 0, 0, 0, 0, 'Poor Lyrelle.$B$BI do not blame you for putting her down, $n. I thank you. I hope that you would do the same to me, if I were to slip and fall down that same path.$B$BJust... try to learn something from your ordeal. Reflect on your experience. I would hate to see her sacrifice be for nothing.', 0);
INSERT INTO `quest_offer_reward` VALUES (38805, 0, 0, 0, 0, 0, 0, 0, 0, 'My sister\'s villainy is finally at an end.$B$BShe was always the wisest among us. Sad that she had to go out the way she did. I\'m certain there was much she could have taught us.', 0);
INSERT INTO `quest_offer_reward` VALUES (38806, 1, 0, 0, 0, 0, 0, 0, 0, 'Этот вонючий кусочек... это ведь инфернальная сера, да?$b$bПотрясающе.$b$bМне о ней рассказывали, но я никогда еще ее не видела.$b$bЯ должна ее изучить. А пока – вот все, что я знаю об этой редкой, редкой руде.', 24015);
INSERT INTO `quest_offer_reward` VALUES (38807, 0, 0, 0, 0, 0, 0, 0, 0, 'Fascinating...', 0);
INSERT INTO `quest_offer_reward` VALUES (38808, 1, 0, 0, 0, 0, 0, 0, 0, 'Эти сердца выглядят очень аппетитно. $B$BДремлющие короли не смогут от них отказаться.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38810, 1, 0, 0, 0, 0, 0, 0, 0, 'Yes, these mushrooms are perfect for your ritual.$B$BSay when you are $gready:ready; start and I will give you further instructions.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38811, 0, 0, 0, 0, 0, 0, 0, 0, 'Могила Ашильдир потревожена, $gиноземец:чужестранка;. $B$BТебе что-нибудь известно об этом?', 21108);
INSERT INTO `quest_offer_reward` VALUES (38813, 273, 0, 0, 0, 0, 0, 0, 0, 'Наши войска вступили в бой с Легионом.\n\nВскоре мы сами сможем сообщить об этой победе владыке Иллидану в Черном храме.', 20994);
INSERT INTO `quest_offer_reward` VALUES (38815, 0, 0, 0, 0, 0, 0, 0, 0, 'Where\'s the... where am I? $B$BI... I know you...', 21108);
INSERT INTO `quest_offer_reward` VALUES (38816, 0, 0, 0, 0, 0, 0, 0, 0, 'Это была славная битва. Королева валь\'кир отмщена.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38817, 0, 0, 0, 0, 0, 0, 0, 0, 'Коварство Говорящих с костями не знает границ. $B$BС помощью своих гнусных ритуалов они пытаются расколоть саму душу нашей королевы – но мы теперь воспользуемся этой же самой магией, чтобы вернуть ее в мир!', 21108);
INSERT INTO `quest_offer_reward` VALUES (38818, 0, 0, 0, 0, 0, 0, 0, 0, 'Я содрогаюсь от одной мысли о своей судьбе... $B$BЯ недооценила силу Фальяра и жестоко поплатилась за это.$B$BТо, что он имел власть изгнать нас сюда... это просто немыслимо.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38819, 273, 6, 0, 0, 0, 0, 0, 0, 'Приятное зрелище. Мне кажется, тебе удалось полностью дезорганизовать войска Легиона.\n\nКак считаешь, мы уже готовы напасть на флагманский корабль, чтобы завладеть саргеритовым ключом?', 20994);
INSERT INTO `quest_offer_reward` VALUES (38823, 0, 0, 0, 0, 0, 0, 0, 0, 'Эти мистики не пожалели стараний, чтобы ослабить дух Ашильдир. $B$BБудем надеяться, что теперь ее дух достаточно силен, чтобы откликнуться на призыв и пробудиться.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38833, 1, 66, 0, 0, 0, 0, 0, 0, 'There she is... demon steel. Congratulations, $n, you\'ve moved on to a new level of skill.', 23420);
INSERT INTO `quest_offer_reward` VALUES (38834, 396, 0, 0, 0, 0, 0, 0, 0, 'Как же я рад вас видеть. Мы выбили демонов из этих руин, но с большим трудом. Эти демоны куда сильнее всех тех, с которыми нам доводилось сталкиваться раньше.\n\nВаша помощь придется очень кстати. Если не разгромить Легион сейчас, он захватит Азсуну, а затем и все Расколотые острова.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38857, 2, 0, 0, 0, 0, 0, 0, 0, 'Просто великолепно, $gдобрый господин:добрая госпожа;. Даже не представить, насколько я благодарен за освобождение моих людей. Рош тебе особенно признателен.\n\n<Добряк Рош ворчит.>\n\nВозможно, мы с тобой сможем вести дела и дальше.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38862, 0, 0, 0, 0, 0, 0, 0, 0, 'Пока я не выясню, что стряслось с племенем Листа Чертополоха, нужно будет присматривать за яйцами лунокрылов. Хорошо, что хоть им уже ничто не угрожает.', 21021);
INSERT INTO `quest_offer_reward` VALUES (38872, 0, 0, 0, 0, 0, 0, 0, 0, '', 21215);
INSERT INTO `quest_offer_reward` VALUES (38873, 0, 0, 0, 0, 0, 0, 0, 0, 'Ха! Посмотрим, что Седогрив станет делать со своими пушками, когда останется без команды!', 21384);
INSERT INTO `quest_offer_reward` VALUES (38878, 0, 0, 0, 0, 0, 0, 0, 0, 'Надеюсь, ты несешь мне добрые вести, |3-6($c).$B$BРади нашего общего блага.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38882, 0, 0, 0, 0, 0, 0, 0, 0, 'It looks like we underestimated Greymane\'s tenacity - a mistake that cost the Forsaken dearly. $B$BThis Eyir cannot hide for long, however. If I must I will hunt her through the Halls of Valor! $B$BGo now, $c. I will call upon you when you are needed.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38888, 0, 0, 0, 0, 0, 0, 0, 0, 'You are indeed a miner. You have the look of it. There is ore in your blood.', 0);
INSERT INTO `quest_offer_reward` VALUES (38889, 0, 0, 0, 0, 0, 0, 0, 0, 'You squat and reach out to the bear\'s hand. The bear sniffs it and looks at you gullibly. He\'s just a baby and can\'t stand up for himself yet. Will you take him with you?', 21021);
INSERT INTO `quest_offer_reward` VALUES (38901, 1, 0, 0, 0, 0, 0, 0, 0, 'You want to mine felslate?$b$bDo you really think you have what it takes?$b$bAre you willing to make sacrifices?$b$bGood.', 22522);
INSERT INTO `quest_offer_reward` VALUES (38904, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to the Halls of Valor, hero. Normally one must die to gain entrance, but times have changed.', 21021);
INSERT INTO `quest_offer_reward` VALUES (38907, 1, 0, 0, 0, 0, 0, 0, 0, 'I know you seek the Pillar of Creation but I am sad to say it was one of many things we lost today.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38909, 0, 0, 0, 0, 0, 0, 0, 0, 'Riverbend was... destroyed? That disgusting beast Dargrul! I will not stop until I see him dead.$b$bBut we cannot do that with the army we have.$b$bWe need help from the other tribes.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38910, 11, 0, 0, 0, 0, 0, 0, 0, 'Thanks to your courage, many of our feathered friends have escaped a terrible fate. You\'re doing a great job.$b$bNow how about an enemy attack on the forehead?', 21249);
INSERT INTO `quest_offer_reward` VALUES (38911, 0, 0, 0, 0, 0, 0, 0, 0, 'Our riverlands are under attack and that girl sends an outsider to our aid?', 21108);
INSERT INTO `quest_offer_reward` VALUES (38912, 0, 0, 0, 0, 0, 0, 0, 0, '$gForeigner, you\'re alone: A stranger, you\'re alone; one of the few strangers in Krutigorje, who was lucky enough to get to these forests and stay alive. I vouched for you, and Torok ordered his men not to touch you.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38913, 11, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! And what are you, tiny creature? Have you come to battle us or join us in battle?', 21249);
INSERT INTO `quest_offer_reward` VALUES (38915, 0, 0, 0, 0, 0, 0, 0, 0, 'Это... очень много дрогбаров.', 21384);
INSERT INTO `quest_offer_reward` VALUES (38916, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, thank you for coming. Mayla is as stubborn as her father if not more so.', 21249);
INSERT INTO `quest_offer_reward` VALUES (38922, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (38923, 2, 0, 0, 0, 0, 0, 0, 0, 'ОТЛИЧНАЯ РАБОТА. ПЛОХО ТОЛЬКО, ЧТО АЛЬЯНС СНОВА СТУЧИТСЯ В НАШУ ДВЕРЬ! В БОЙ!', 20779);
INSERT INTO `quest_offer_reward` VALUES (38924, 2, 0, 0, 0, 0, 0, 0, 0, 'ОТЛИЧНАЯ РАБОТА. ПЛОХО ТОЛЬКО, ЧТО АЛЬЯНС СНОВА СТУЧИТСЯ В НАШУ ДВЕРЬ! В БОЙ!', 20779);
INSERT INTO `quest_offer_reward` VALUES (38925, 2, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Теперь Орда всерьез задумается над тем, стоит ли испытывать здесь в Ашране наше терпение.', 20490);
INSERT INTO `quest_offer_reward` VALUES (38926, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done. That will give the Horde something to think about next time they decide to test us here in Ashran.', 0);
INSERT INTO `quest_offer_reward` VALUES (38932, 0, 0, 0, 0, 0, 0, 0, 0, 'This is a blueprint for some high tech naval equipment. I should be able to reverse engineer the plans and have this equipment available for you shortly!', 0);
INSERT INTO `quest_offer_reward` VALUES (38933, 1, 0, 0, 0, 0, 0, 0, 0, 'Пришло время нанести Легиону ответный удар.', 21108);
INSERT INTO `quest_offer_reward` VALUES (38935, 0, 0, 0, 0, 0, 0, 0, 0, 'Ha ha! You were sent by Bolo? The \"Keg Mender,\" right? From the Drunken Hozen?$B$BHow is his eye healing? Is his friend still hung over?$B$BAhh... that was an enjoyable evening.', 0);
INSERT INTO `quest_offer_reward` VALUES (38936, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh, you are a friend of Cho\'s? Welcome! Welcome!$B$BI would serve you a banquet, but as you can see, we are in dire straights at the moment.$B$BSay, would you be willing to lend us a hand?', 0);
INSERT INTO `quest_offer_reward` VALUES (38944, 0, 0, 0, 0, 0, 0, 0, 0, 'Now this... this I can work with!$B$BReminds me of some old night elven festival cloth I\'ve seen.', 0);
INSERT INTO `quest_offer_reward` VALUES (38945, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s a shame. Without the ability to do any stitching, we can hardly do anything with this fabric.$B$B<Tanithria pouts.>$B$BStill, there ARE patterns we know that don\'t require stitching. Shoulderpads are easy, and belts should be a cinch.', 0);
INSERT INTO `quest_offer_reward` VALUES (38946, 0, 0, 0, 0, 0, 0, 0, 0, 'My my, look at you! Such an attractive young $C, and all bedecked in magical finery and weaponry. To what do I owe the pleasure?$B$BAha! Tailoring! It\'s your lucky day, er...$B$BI\'m sorry, what was your name? $n?$B$BIt\'s your lucky day, $n! I happen to be one of the best tailors in Suramar...', 0);
INSERT INTO `quest_offer_reward` VALUES (38947, 0, 0, 0, 0, 0, 0, 0, 0, 'Perfect. Marvelous!', 0);
INSERT INTO `quest_offer_reward` VALUES (38948, 0, 0, 0, 0, 0, 0, 0, 0, 'You, $n, are a person of great skill. I send you out for patterns, and you do so without question. Remarkable!', 0);
INSERT INTO `quest_offer_reward` VALUES (38949, 0, 0, 0, 0, 0, 0, 0, 0, 'There you have it!$B$BNo, no, don\'t thank me. I\'m just happy to have been of service.', 0);
INSERT INTO `quest_offer_reward` VALUES (38950, 0, 0, 0, 0, 0, 0, 0, 0, 'Your friend certainly has an... interesting appearance.$B$BHe taught you to sew this silk? Hmm. Maybe we should keep him around for a while.', 0);
INSERT INTO `quest_offer_reward` VALUES (38951, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmm... he may have a point.$B$BThese needles ARE quite strong, and they have a nice bounce to them. Still, I do take issue with their shape and feel. I have an idea.', 0);
INSERT INTO `quest_offer_reward` VALUES (38952, 0, 0, 0, 0, 0, 0, 0, 0, 'Look at these! Strong, light, flexible, sharp, with a refined curve and a smooth edge. You\'ve certainly put your Dalaran touch on it. It\'s the best of both worlds!$B$BEnough fussing over our tools, now. Shall we sew?', 0);
INSERT INTO `quest_offer_reward` VALUES (38953, 0, 0, 0, 0, 0, 0, 0, 0, 'Now isn\'t that just lovely. Go on, try it out yourself!', 0);
INSERT INTO `quest_offer_reward` VALUES (38954, 0, 0, 0, 0, 0, 0, 0, 0, 'Sorry about that, $n. You caught me snooping around. I suppose my curiosity got the better of me.$B$BAllow me to make it up to you.', 0);
INSERT INTO `quest_offer_reward` VALUES (38955, 0, 0, 0, 0, 0, 0, 0, 0, 'There you have it. Not the best bag in the world, but it\'s better than most.$B$BI want to apologize again for running off, $n. My hunger... err, curiosity... occasionally gets the better of me. I promise it will not happen again.', 0);
INSERT INTO `quest_offer_reward` VALUES (38956, 0, 0, 0, 0, 0, 0, 0, 0, 'My most sincere apologies, $n. Not only did I make a spectacle of myself, I fear I may have also put the good name of your tailoring shop at risk.$B$BYou have every right to be upset with me.', 0);
INSERT INTO `quest_offer_reward` VALUES (38957, 0, 0, 0, 0, 0, 0, 0, 0, 'Aha! This will be much simpler than I\'d first thought.$B$B<Lyndras takes a moment to jot some instructions down on a scrap of vellum.>$B$BHere, $n. You should have it.', 0);
INSERT INTO `quest_offer_reward` VALUES (38958, 0, 0, 0, 0, 0, 0, 0, 0, 'Marvelous!', 0);
INSERT INTO `quest_offer_reward` VALUES (38959, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent! It\'s always nice to have familiar things.$B$BThis will make instructing you much easier for me.', 0);
INSERT INTO `quest_offer_reward` VALUES (38960, 0, 0, 0, 0, 0, 0, 0, 0, 'This will be plenty.', 0);
INSERT INTO `quest_offer_reward` VALUES (38961, 0, 0, 0, 0, 0, 0, 0, 0, 'Lyndras ran off again before you could return, $n. He left a note and a pattern for you.$B$BThe note reads,$B$B\"I\'m sorry, $n, but I\'m afraid I may become a threat to the fellow tailors of this shop. I\'m off to find somewhere to try and control my urges, or fall into depravity. It has been an honor training you.$B$BSincerely,$B$BLyndras\"$B$BHow sad...', 0);
INSERT INTO `quest_offer_reward` VALUES (38962, 0, 0, 0, 0, 0, 0, 0, 0, 'Now that you can move safely around the city, it\'s time to find some new masters.', 0);
INSERT INTO `quest_offer_reward` VALUES (38963, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. Before long you may be the one teaching me a thing or two.', 0);
INSERT INTO `quest_offer_reward` VALUES (38965, 0, 0, 0, 0, 0, 0, 0, 0, '<Tanithria listens to your account of Lyndras\' fate.>$B$BI... I see. That\'s a shame.$B$BI had grown to enjoy his company, you know. If you\'ll excuse me, I need a moment to think about him.', 0);
INSERT INTO `quest_offer_reward` VALUES (38966, 0, 0, 0, 0, 0, 0, 0, 0, 'The good news is that we can now craft more efficient patterns out of silkweave.$B$BThe bad news is that we\'ve expended the remainder of Lyndras\' knowledge. Where should we turn next?', 0);
INSERT INTO `quest_offer_reward` VALUES (38967, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, a fellow tailor. Pleasure to make your acquaintance, uh... $n, was it?$B$BI trust you\'d like to come into the fold of our guild? Where did you receive your prior training?', 0);
INSERT INTO `quest_offer_reward` VALUES (38968, 0, 0, 0, 0, 0, 0, 0, 0, 'Hmm.$B$BVery well. We may continue.', 0);
INSERT INTO `quest_offer_reward` VALUES (38969, 0, 0, 0, 0, 0, 0, 0, 0, 'Your stitching is still a little rough, but you\'ve already mastered all of the basics.$B$BWell done.$B$BI don\'t say that very often. Savor it.', 0);
INSERT INTO `quest_offer_reward` VALUES (38970, 0, 0, 0, 0, 0, 0, 0, 0, 'Don\'t look at us that way, $n. Selling our services to the demons may have been morally dubious, but our skills have grown exponentially since Azayna made the deal.$B$BContinue your training with us, or don\'t. If you want to leave now, nobody\'s stopping you. If you want to learn more... we are the only game in town.', 0);
INSERT INTO `quest_offer_reward` VALUES (38971, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent work. I will get to studying this fabric right away. As promised, I will teach you what I know. Are you acquainted with our patterns for gloves?', 0);
INSERT INTO `quest_offer_reward` VALUES (38974, 0, 0, 0, 0, 0, 0, 0, 0, '<Tellumi takes the loincloth from you and gives you a sly smile.>$B$BI trust you now see looking at a situation from a functional angle than a reactionary one will earn you far more knowledge.', 0);
INSERT INTO `quest_offer_reward` VALUES (38975, 0, 0, 0, 0, 0, 0, 0, 0, 'Not quite what I\'d expected, but it was an interesting history lesson, at least. You can keep the robes, I\'ve learned all I need just by watching you work.$B$BNow, as promised, I shall show you what I know.', 0);
INSERT INTO `quest_offer_reward` VALUES (38989, 0, 0, 0, 0, 0, 0, 0, 0, 'Lok\'tar General. Your forces await your orders.', 0);
INSERT INTO `quest_offer_reward` VALUES (38990, 0, 0, 0, 0, 0, 0, 0, 0, 'Король-лич сделал мудрый выбор, доверив эти клинки тебе, $n. Ты уже не раз $gдоказал, что достоин:доказала, что достойна;, владеть ими.$b$bИ, должен признаться, в глубине души я рад, что он выбрал не меня. Я больше не хочу вверять свою судьбу магическому оружию.', 21287);
INSERT INTO `quest_offer_reward` VALUES (38992, 1, 66, 0, 0, 0, 500, 0, 0, 'Слишком долго я просидел без дела в стенах Акеруса, и Вечный голод гложет мою душу. Давно пора отправиться на поле боя и сполна утолить его.$b$bДля меня будет честью сражаться с тобой плечом к плечу, Владыка Смерти.', 21287);
INSERT INTO `quest_offer_reward` VALUES (38996, 0, 0, 0, 0, 0, 0, 0, 0, 'Greetings General. Your forces await your orders on the Planning Table.', 0);
INSERT INTO `quest_offer_reward` VALUES (39000, 274, 1, 0, 0, 0, 0, 0, 0, '<Kadgar is carefully examining the books you\'ve obtained without hiding your disgust. >$b$bIt\'s disgusting, isn\'t it? The encyclopedia of a psychotic maniac is harmless compared to a coloring book.', 20490);
INSERT INTO `quest_offer_reward` VALUES (39003, 274, 1, 5, 0, 0, 0, 0, 0, '<Cadgar changes face when you hear the story of what happened in the tower. >$b$bKordana...? Oh, no... Yes, she\'s been a little distant lately... But I would never... I thought it was just the specifics of her work. It never occurred to me...$b$bYeah, $n! Do you understand now? That\'s what Gul\'Dan is all about. He finds our weaknesses and skillfully uses them against us. So from now on, think properly who you should trust. Oh, Kordana!$b$b<Kadgar turns to the sea. >$b$b >Kordana...', 20490);
INSERT INTO `quest_offer_reward` VALUES (39004, 0, 0, 0, 0, 0, 0, 0, 0, '<Khadgar\'s face is ashen.>$B$BDraenor is saved, $n. But the Legion is more powerful than even I had imagined. And Gul\'dan, that snake - he\'s still alive. Somewhere.$B$BStay here and celebrate with the others, $n. You\'ve earned it. But I will soon return to Azeroth to deal with this, and I hope you will join me there when the time comes.', 0);
INSERT INTO `quest_offer_reward` VALUES (39015, 0, 0, 0, 0, 0, 0, 0, 0, '', 22019);
INSERT INTO `quest_offer_reward` VALUES (39018, 0, 0, 0, 0, 0, 0, 0, 0, '<The Archmage looks damp and uncomfortable.>$B$BI apologize for the conditions here, $n. I had no choice. I summoned this tower at the optimal confluence of arcane energies. My hope is to finish my work here before all my books get musty.$B$BSo then! Let\'s get to it.', 0);
INSERT INTO `quest_offer_reward` VALUES (39019, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome back, my friend. We have much work to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (39020, 0, 0, 0, 0, 0, 0, 0, 0, 'О, $n, ты $gвернулся:вернулась;. Тебя до сих пор окружает потусторонняя аура.', 20490);
INSERT INTO `quest_offer_reward` VALUES (39021, 0, 0, 0, 0, 0, 0, 0, 0, 'О, $n, ты $gвернулся:вернулась;. Тебя до сих пор окружает потусторонняя аура.', 20886);
INSERT INTO `quest_offer_reward` VALUES (39022, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome back, my friend. We have much work to do.', 0);
INSERT INTO `quest_offer_reward` VALUES (39025, 1, 0, 0, 0, 0, 0, 0, 0, 'Drogbar hasn\'t come to the surface in over a century. We\'ve almost forgotten about them, except to scare naughty children with them.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39026, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты сражаешься как истинный воитель.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39027, 0, 0, 0, 0, 0, 0, 0, 0, 'Such destruction. So many good warbraves have fallen today.$b$bIt is clear now, we will need the full might of the Highmountain tribes if we are to recover the hammer.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39032, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебя Элисандрия послала? Она славная. Хорошая подруга и настоящая защитница природы.', 20994);
INSERT INTO `quest_offer_reward` VALUES (39033, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная работа, $n. Опытный предсказатель с помощью апекситовых кристаллов может отыскивать месторождения нефти, спрятанные глубоко под землей. К сожалению, обучить этому искусству тебя не получится, но ты все равно сможешь получить свою выгоду.', 20886);
INSERT INTO `quest_offer_reward` VALUES (39034, 0, 0, 0, 0, 0, 0, 0, 0, 'Your new allies left this here, as a tribute for your efforts on their behalf.', 0);
INSERT INTO `quest_offer_reward` VALUES (39040, 0, 0, 0, 0, 0, 0, 0, 0, 'I trust your battles went well?', 0);
INSERT INTO `quest_offer_reward` VALUES (39041, 0, 0, 0, 0, 0, 0, 0, 0, 'The wind whispers your name, $n, and tales of your conquest.', 0);
INSERT INTO `quest_offer_reward` VALUES (39043, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you for clearing us a path. We should have a straight shot at Dargrul now.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39047, 0, 0, 0, 0, 0, 0, 0, 0, '', 21287);
INSERT INTO `quest_offer_reward` VALUES (39049, 273, 0, 0, 0, 0, 0, 0, 0, 'An inquisitor\'s essence... perfect. Allari was right.\n\nNow to complete my ritual and spy on the Legion\'s leadership.', 20994);
INSERT INTO `quest_offer_reward` VALUES (39050, 1, 0, 0, 0, 0, 0, 0, 0, 'Это что-то новенькое. Она выглядит как демоническая паучиха. Вот только пауков еще в Легионе не хватало...', 20994);
INSERT INTO `quest_offer_reward` VALUES (39051, 0, 0, 0, 0, 0, 0, 0, 0, 'Suramar is not far from here.', 20994);
INSERT INTO `quest_offer_reward` VALUES (39054, 0, 0, 0, 0, 0, 0, 0, 0, 'Хех! Мы еще сделаем из тебя настоящего морского волка! Пора приступать к постройке первого корабля!', 20444);
INSERT INTO `quest_offer_reward` VALUES (39055, 0, 0, 0, 0, 0, 0, 0, 0, 'Первый корабль готов! Отличная работа, $n. Я займусь подготовкой к отплытию в Танаан.', 20444);
INSERT INTO `quest_offer_reward` VALUES (39056, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты отлично справляешься, командир! Из тебя выйдет замечательный капитан.', 20574);
INSERT INTO `quest_offer_reward` VALUES (39057, 1, 21, 0, 0, 0, 0, 0, 0, 'Прекрасно, $n. В доках кипит работа.$b$b<Кадгар прихлопывает в ладоши и потирает их друг о друга. В его бледно-голубых глазах мерцает что-то зловещее.>$b$bМы им покажем.', 20886);
INSERT INTO `quest_offer_reward` VALUES (39059, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты очень вовремя, $n.', 21491);
INSERT INTO `quest_offer_reward` VALUES (39060, 0, 0, 0, 0, 0, 0, 0, 0, 'It is done. You have provided; Worgen from Greymane Outpost is a tremendous service, $n.', 21491);
INSERT INTO `quest_offer_reward` VALUES (39061, 0, 0, 0, 0, 0, 0, 0, 0, 'Прекрасно. Наконец-то части головоломки начинают складываться в цельную картину. $B$BМожет, мы пока и не знаем, что здесь делают силы Сильваны, но зато, похоже, теперь нам известно их местонахождение.', 21491);
INSERT INTO `quest_offer_reward` VALUES (39062, 0, 0, 0, 0, 0, 0, 0, 0, 'So the cavalry has arrived? $B$BGood, we must make haste if we wish to uncover Sylvanas\' plot before it comes to bear.', 21491);
INSERT INTO `quest_offer_reward` VALUES (39063, 0, 0, 0, 0, 0, 0, 0, 0, 'Val\'kira, you say? It\'s a lot sadder than I thought. If Silvana gets the chance to create a new Val\'kira, her army will be invincible. It seems her ultimate goal is the crypt. We must stop her!', 21491);
INSERT INTO `quest_offer_reward` VALUES (39067, 0, 0, 0, 0, 0, 0, 0, 0, 'Great work! We will get to work right away on the upgrade.', 0);
INSERT INTO `quest_offer_reward` VALUES (39068, 0, 0, 0, 0, 0, 0, 0, 0, 'Controlling the seas of Draenor is in our grasp!', 0);
INSERT INTO `quest_offer_reward` VALUES (39082, 0, 0, 0, 0, 0, 0, 0, 0, 'Обожаю запах свежеспиленных деревьев и смолы! Ну что, начинаем строить?', 20444);
INSERT INTO `quest_offer_reward` VALUES (39090, 2, 0, 0, 0, 0, 0, 0, 0, 'Отлично! К сожалению, наши противники настолько упрямы, что продолжают атаку.', 20886);
INSERT INTO `quest_offer_reward` VALUES (39092, 0, 0, 0, 0, 0, 0, 0, 0, 'Рад видеть тебя, $n. $B$BШтурм лагеря Дредвейка не задался, и я уж было решил, что наше дело дрянь, но тут Краули сообщила мне о твоих успехах.$B$BПора закончить наше дело!', 21491);
INSERT INTO `quest_offer_reward` VALUES (39096, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done Commander! But there are still a lot of Horde out there wreaking havoc.', 0);
INSERT INTO `quest_offer_reward` VALUES (39107, 0, 0, 0, 0, 0, 0, 0, 0, 'Хе-хе! Похоже, это действительно все! Это очень удачно – для нас обоих. Так, тебе я могу выделить вот столько...', 20574);
INSERT INTO `quest_offer_reward` VALUES (39117, 0, 0, 0, 0, 0, 0, 0, 0, '', 21232);
INSERT INTO `quest_offer_reward` VALUES (39122, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты $gочнулся:очнулась;! Мы уж думали, что тебе конец... $B$BТебе очень повезло, что мы успели вовремя вытащить вас с Седогривом. Еще немного, и яд Сильваны тебя бы доконал. Жаль, конечно, что ей удалось улизнуть, но теперь ее планы в Скольд-Ашиле пошли прахом, и это целиком и полностью твоя заслуга. $B$BВне всякого сомнения, Седогрив захочет объявить тебе личную благодарность, когда малость поправится. Ты очень $gпомог:помогла; нашему делу, $n. $B$BГилнеас в огромном долгу перед тобой.', 21491);
INSERT INTO `quest_offer_reward` VALUES (39123, 1, 0, 0, 0, 0, 0, 0, 0, 'Я смотрю, на тебе ни царапины! Видать, удалось застать свои жертвы врасплох!', 21108);
INSERT INTO `quest_offer_reward` VALUES (39124, 1, 0, 0, 0, 0, 0, 0, 0, 'Тебе удалось добыть старорога Крутогорья и остаться в живых? Таким немногие могут похвастаться. Я впечатлен.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39127, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Townlong Steppes. If you insist on wandering in to the darkest places of Pandaria then far be it from me to not put you to good use.', 0);
INSERT INTO `quest_offer_reward` VALUES (39133, 0, 0, 0, 0, 0, 0, 0, 0, 'For a long time already the earth itself did not rebel against us.', 21249);
INSERT INTO `quest_offer_reward` VALUES (39134, 0, 0, 0, 0, 0, 0, 0, 0, 'This is hardly the last Dargrool assault. He\'s still exploring the possibilities of the Hammer - like the child who was given a new toy.', 21249);
INSERT INTO `quest_offer_reward` VALUES (39142, 1, 0, 0, 0, 0, 0, 0, 0, 'Подопытный не выжил? Весьма неожиданный поворот.$B$BНу что ж, ты в любом случае $gвыполнил:выполнила; свою часть уговора. Пришла пора и мне выполнить свою.', 22996);
INSERT INTO `quest_offer_reward` VALUES (39149, 0, 0, 0, 0, 0, 0, 0, 0, 'We don\'t need any help, and we don\'t like strangers wanderin\' around on our land.', 0);
INSERT INTO `quest_offer_reward` VALUES (39153, 0, 0, 0, 0, 0, 0, 0, 0, 'Сегодня вороны отведают воргенской плоти, |3-6($c).$B$BОтличная работа.', 21384);
INSERT INTO `quest_offer_reward` VALUES (39154, 0, 0, 0, 0, 0, 0, 0, 0, 'Не $gожидал:ожидала; меня здесь встретить? Для меня тоже оказалось полной неожиданностью, что мои силы до сих пор не могут проникнуть в город этих проклятых врайкулов! $B$BПришло время сменить тактику, и твое появление здесь как раз очень кстати.', 21384);
INSERT INTO `quest_offer_reward` VALUES (39155, 0, 0, 0, 0, 0, 0, 0, 0, '$gHero: Warrior; returns, and the barrier no longer blocks our way. You have a worthy reward waiting for you. $B$But first we must penetrate the crypt!', 21384);
INSERT INTO `quest_offer_reward` VALUES (39175, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_offer_reward` VALUES (39176, 0, 0, 0, 0, 0, 0, 0, 0, '<The construct lies amidst rubble, in disrepair.>$B$B<Judging by the tools on its manipulator arms, this construct does appear to be designed for gemwork.>', 0);
INSERT INTO `quest_offer_reward` VALUES (39177, 0, 0, 0, 0, 0, 0, 0, 0, 'Are you responsible for the return of this construct, $n? It came barrelling through our camp like it was on a mission. It took three of the warders to tackle and subdue it, but for the time being, it appears content to stay here.', 0);
INSERT INTO `quest_offer_reward` VALUES (39178, 1, 0, 0, 0, 0, 0, 0, 0, 'Эти рога будут очень впечатляюще смотреться на стене Приюта!', 21108);
INSERT INTO `quest_offer_reward` VALUES (39179, 1, 0, 0, 0, 0, 0, 0, 0, 'Ты просто умница. Я теперь понимаю, почему Ритцун так любит давать тебе всякие поручения.', 22996);
INSERT INTO `quest_offer_reward` VALUES (39180, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Cenarion Refuge, $n. I hope you\'ve come to aid us in our mission. We\'ve much work ahead of us.', 0);
INSERT INTO `quest_offer_reward` VALUES (39181, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Cenarion Refuge, $n. I hope you\'ve come to aid us in our mission. We\'ve much work ahead of us.', 0);
INSERT INTO `quest_offer_reward` VALUES (39182, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad that Stonebreaker Hold sent you. The druids of the thicket have been slain! Only myself and one other have survived, and he\'s insane.$B$BI don\'t know what happened, but I intend to get to the bottom of it. Whatever it was, it happened quickly and feels unnatural.$B$BWill you help me?', 0);
INSERT INTO `quest_offer_reward` VALUES (39188, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'m glad that Stonebreaker Hold sent you. The druids of the thicket have been slain! Only myself and one other have survived, and he\'s insane.$B$BI don\'t know what happened, but I intend to get to the bottom of it. Whatever it was, it happened quickly and feels unnatural.$B$BWill you help me?', 0);
INSERT INTO `quest_offer_reward` VALUES (39191, 0, 0, 0, 0, 0, 0, 0, 0, '', 21154);
INSERT INTO `quest_offer_reward` VALUES (39192, 0, 0, 0, 0, 0, 0, 0, 0, 'Your weapon has untold potential! You will need to get more acquainted with it first and we can work on it some more.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39195, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes?', 0);
INSERT INTO `quest_offer_reward` VALUES (39196, 0, 0, 0, 0, 0, 0, 0, 0, '<Even though he endeavors to hide it, a look of relief spreads over Kroghan\'s face.>$B$BIt\'s about time you got here! Granted, you\'re not an army, but any able-bodied $R than can wield a weapon or chant a spell is welcome here. The people of Garadar are kin to all the Horde and we are proud to shed our blood in their defense.', 0);
INSERT INTO `quest_offer_reward` VALUES (39197, 0, 0, 0, 0, 0, 0, 0, 0, 'Your people have done a great service for my good friend, Ikuti. For this, you are welcome here in Telaar.', 0);
INSERT INTO `quest_offer_reward` VALUES (39198, 0, 0, 0, 0, 0, 0, 0, 0, 'Denjai seeks help? I\'d send him reinforcements, but we\'re spread thin here as it is. We\'ve got trouble of our own with the ogres up here.$B$BWhen I can, I\'ll give him what information we have on the local ogre tribes and their tactics. Perhaps if he can survive long enough, we\'ll be able to launch a two-pronged assault on the vile scum.', 0);
INSERT INTO `quest_offer_reward` VALUES (39199, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, good. We could use more able bodies.', 0);
INSERT INTO `quest_offer_reward` VALUES (39201, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey there. Looking for work? Perfect!', 0);
INSERT INTO `quest_offer_reward` VALUES (39202, 0, 0, 0, 0, 0, 0, 0, 0, 'Hey there. Looking for work? Perfect!', 0);
INSERT INTO `quest_offer_reward` VALUES (39203, 0, 0, 0, 0, 0, 0, 0, 0, 'What took you so long? Time waits only for Aspects, $R.$B$BWe have much work to do, you and I.', 0);
INSERT INTO `quest_offer_reward` VALUES (39204, 0, 0, 0, 0, 0, 0, 0, 0, 'I\'ve been expecting you, $C. We have much work to do, you and I.', 0);
INSERT INTO `quest_offer_reward` VALUES (39206, 0, 0, 0, 0, 0, 0, 0, 0, 'Who are you?$B$BTell me why I shouldn\'t split you in two where you stand!', 0);
INSERT INTO `quest_offer_reward` VALUES (39207, 0, 0, 0, 0, 0, 0, 0, 0, 'You were almost shot down, eh? Savage monsters - those Horde.$B$BPayback will come for what they\'ve done. You can count on at least that much, $C.$B$BNow, it\'s time to put you to work.', 0);
INSERT INTO `quest_offer_reward` VALUES (39208, 0, 0, 0, 0, 0, 0, 0, 0, '<Stefan reads the missive, then crumples it up and tosses it into the fire.>$B$BOld news. The Argent Crusade is - once again - late to the party. We\'ve been tracking Drakuru since Grizzly Hills. He is slated for eradication.', 0);
INSERT INTO `quest_offer_reward` VALUES (39209, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye, Hemet\'s here. He\'s at the camp, just go northwest toward the river and you\'ll soon see it. And for the love of the Light, lad, have him send someone to cut me down!', 0);
INSERT INTO `quest_offer_reward` VALUES (39210, 0, 0, 0, 0, 0, 0, 0, 0, 'Welcome to Dalaran.', 0);
INSERT INTO `quest_offer_reward` VALUES (39212, 0, 0, 0, 0, 0, 0, 0, 0, 'Aye, Hemet\'s here. He\'s at the camp, just go northwest toward the river and you\'ll soon see it. And for the love of the Light, lad, have him send someone to cut me down!', 0);
INSERT INTO `quest_offer_reward` VALUES (39214, 0, 0, 0, 0, 0, 0, 0, 0, 'It has been a while since I\'ve talked to the living. I hope you don\'t mind if I assist you. Being slain in combat has given me a calmer outlook in this emergency than other warriors.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39217, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная работа, $n! Ситуация там сейчас у нас весьма непростая.', 20886);
INSERT INTO `quest_offer_reward` VALUES (39221, 0, 0, 0, 0, 0, 0, 0, 0, 'I wonder if dat magnaron fel into da pool. Get it, mon?', 0);
INSERT INTO `quest_offer_reward` VALUES (39225, 0, 0, 0, 0, 0, 0, 0, 0, 'I wouldn\'t know dat monster started off as an orc if Saurfang hadn\'t told me.', 0);
INSERT INTO `quest_offer_reward` VALUES (39228, 0, 0, 0, 0, 0, 0, 0, 0, 'Anotha follower of da darkness falls before ya.', 0);
INSERT INTO `quest_offer_reward` VALUES (39231, 0, 0, 0, 0, 0, 0, 0, 0, 'Dat bag of bones be gone again.', 0);
INSERT INTO `quest_offer_reward` VALUES (39232, 0, 0, 0, 0, 0, 0, 0, 0, 'Ya saved Draenor from one of da strongest demons in all creation, Commander. Dey will sing songs of your victory.', 0);
INSERT INTO `quest_offer_reward` VALUES (39233, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная работа, $n! Наверняка ты $gвидел:видела;, что за бардак там устроил Альянс!', 20779);
INSERT INTO `quest_offer_reward` VALUES (39236, 0, 0, 0, 0, 0, 0, 0, 0, 'Обожаю запах свежеспиленных деревьев и смолы! Ну что, начинаем строить?', 20779);
INSERT INTO `quest_offer_reward` VALUES (39241, 0, 0, 0, 0, 0, 0, 0, 0, 'Хех! Мы еще сделаем из тебя настоящего морского волка! Пора приступать к постройке первого корабля!', 20779);
INSERT INTO `quest_offer_reward` VALUES (39242, 0, 0, 0, 0, 0, 0, 0, 0, 'It\'s done. Handsome, not a ship! You owe me an army, and he\'ll take it wherever you want. I\'ll get him ready to go to Tanahan.', 20779);
INSERT INTO `quest_offer_reward` VALUES (39243, 0, 0, 0, 0, 0, 0, 0, 0, 'Ты отлично справляешься, командир! Из тебя выйдет замечательный капитан.', 20779);
INSERT INTO `quest_offer_reward` VALUES (39245, 0, 0, 0, 0, 0, 0, 0, 0, 'Отличная работа! Мы сразу приступим к расширению пристани.', 20779);
INSERT INTO `quest_offer_reward` VALUES (39246, 0, 0, 0, 0, 0, 0, 0, 0, 'Это будет самый мощный флот на Дреноре!', 20886);
INSERT INTO `quest_offer_reward` VALUES (39247, 0, 0, 0, 0, 0, 0, 0, 0, 'What of the others? Did they...?$B$BI see. They gave their lives for a worthy cause - the Twinblades will change the tide of this war, mark my words.', 20994);
INSERT INTO `quest_offer_reward` VALUES (39250, 0, 0, 0, 0, 0, 0, 0, 0, 'We can\'t handle too many more of those fel-corrupted beasts.', 0);
INSERT INTO `quest_offer_reward` VALUES (39253, 0, 0, 0, 0, 0, 0, 0, 0, 'That was some kind of monster, wasn\'t it?', 0);
INSERT INTO `quest_offer_reward` VALUES (39256, 0, 0, 0, 0, 0, 0, 0, 0, 'How does a vindicator fall so far from the light?', 0);
INSERT INTO `quest_offer_reward` VALUES (39259, 0, 0, 0, 0, 0, 0, 0, 0, 'Let\'s hope Mannoroth stays down longer this time.', 0);
INSERT INTO `quest_offer_reward` VALUES (39260, 0, 0, 0, 0, 0, 0, 0, 0, 'You have saved Draenor from one of the strongest demons in all creation, Commander. They will sing songs of your victory.', 0);
INSERT INTO `quest_offer_reward` VALUES (39261, 1, 0, 0, 0, 0, 0, 0, 0, 'Хорошо, что Кор\'вас быстро нашла тебя, $gповелитель:повелительница;.', 20994);
INSERT INTO `quest_offer_reward` VALUES (39262, 5, 0, 0, 0, 0, 0, 0, 0, 'Я так и знал, что в пещере кто-то есть!', 20994);
INSERT INTO `quest_offer_reward` VALUES (39272, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо, |3-6($r). Часть посевов, кажется, уцелела. Надеюсь, что упорный труд и водная магия Джейл сделают свое дело, и мы соберем хоть какой-то урожай.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39276, 0, 0, 0, 0, 0, 0, 0, 0, 'Интересуешься кораблестроением, как я погляжу? Я сделаю все, что смогу, чтобы помочь тебе, командир.', 20444);
INSERT INTO `quest_offer_reward` VALUES (39277, 0, 0, 0, 0, 0, 0, 0, 0, 'It worked out just fine. I\'ll keep that sprayer for now. I\'m sure it\'ll come in handy in the coming battles.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39278, 0, 0, 0, 0, 0, 0, 0, 0, 'Dis... is important! Where you find dis? We been lookin for dis stuff a while.$B$BYou got a dock now? Dat is VERY interesting. Maybe it time we became good friends? Da big fort you have is hard to sneak a spy-- er, forget I said dat.$B$BI gots an idea.', 0);
INSERT INTO `quest_offer_reward` VALUES (39283, 0, 0, 0, 0, 0, 0, 0, 0, 'Wow, look at this thing.$B$BI think maybe I\'ll... actually, I\'m not sure what to do with this. Is it really a fish?', 0);
INSERT INTO `quest_offer_reward` VALUES (39299, 0, 0, 0, 0, 0, 0, 0, 0, 'I am pleased to make your acquaintance, commander. If you ever wish to see the latest wonders that Madame Goya offers, please do not hesitate to speak with me. She sends this as a token of appreciation for your work in retrieving the manifest.$B$BRemember, Madame Goya only accepts bids in person. You must travel to meet her, if you wish to acquire any of her wares.', 0);
INSERT INTO `quest_offer_reward` VALUES (39305, 0, 0, 0, 0, 0, 0, 0, 0, '', 21414);
INSERT INTO `quest_offer_reward` VALUES (39313, 1, 0, 0, 0, 0, 0, 0, 0, 'Отлично! Если вдруг передумаешь – я всегда здесь!', 20574);
INSERT INTO `quest_offer_reward` VALUES (39315, 1, 0, 0, 0, 0, 0, 0, 0, 'Artillery for all tastes! Just like in a fairy tale. $B$BYes, keep in mind - if you change your mind, I\'m always here!', 20779);
INSERT INTO `quest_offer_reward` VALUES (39316, 0, 0, 0, 0, 0, 0, 0, 0, 'Они вернулись относительно целыми и невредимыми. Спасибо тебе, $n.', 21108);
INSERT INTO `quest_offer_reward` VALUES (39318, 15, 0, 0, 0, 0, 0, 0, 0, 'Юлан мертв? Жаль. Он был хорошим шаманом... и очень жизнерадостным. Совсем не походил на этих унылых духостранников.$b$bСегодня вечером мне так будет не хватать его шуток...', 21249);
INSERT INTO `quest_offer_reward` VALUES (39321, 0, 0, 0, 0, 0, 0, 0, 0, 'Отлично. Осталось, как говорится, отсечь змее голову.', 21249);
INSERT INTO `quest_offer_reward` VALUES (39322, 0, 0, 0, 0, 0, 0, 0, 0, 'Позволь засвидетельствовать тебе наше уважение и почет, $p. Нарекаю тебя Небесным другом и даю право свободно путешествовать по нашим землям на твое усмотрение.', 21249);
INSERT INTO `quest_offer_reward` VALUES (39323, 0, 0, 0, 0, 0, 0, 0, 0, '<Muzie chirps, rejoicing at your presence>', 21108);
INSERT INTO `quest_offer_reward` VALUES (39325, 0, 0, 0, 0, 0, 0, 0, 0, 'I trust you were successful in making the potions? This will be the first step in building trust between us.', 0);
INSERT INTO `quest_offer_reward` VALUES (39326, 0, 0, 0, 0, 0, 0, 0, 0, 'You found them! I had the utmost faith, of course. What\'s a few demons or naga to one so dedicated to advancing our craft?', 0);
INSERT INTO `quest_offer_reward` VALUES (39327, 0, 0, 0, 0, 0, 0, 0, 0, 'It seems that twerp of a scribe has his uses after all! Let\'s see what he came up with...', 0);
INSERT INTO `quest_offer_reward` VALUES (39328, 0, 0, 0, 0, 0, 0, 0, 0, 'My poor table!$B$BAll the implements we needed to mix the new potions were on that table. Without them, the new recipes are useless!$B$BFinding replacements will be no small task, but I\'m confident you\'re up to it. If you need guidance, I can suggest places to look for the items we need.$B$BNow, off with you!', 0);
INSERT INTO `quest_offer_reward` VALUES (39329, 0, 0, 0, 0, 0, 0, 0, 0, 'Ah, this burner is exactly what we need for the alchemy table!$B$BWait just a moment... No, no, this will never do!', 0);
INSERT INTO `quest_offer_reward` VALUES (39330, 0, 0, 0, 0, 0, 0, 0, 0, 'You imbued the burner with ley energy, eh? Let\'s have a look.$B$B<Deucus examines the leyflame burner.>$B$BYes, this will do nicely! A crucial step in rebuilding the alchemy table.', 0);
INSERT INTO `quest_offer_reward` VALUES (39331, 0, 0, 0, 0, 0, 0, 0, 0, 'What\'s this? The spoils from another of your adventures, eh? Let\'s have a look.', 0);
INSERT INTO `quest_offer_reward` VALUES (39332, 0, 0, 0, 0, 0, 0, 0, 0, 'These potions have the corks we need for the alchemy table. Another successful endeavor!', 0);
INSERT INTO `quest_offer_reward` VALUES (39333, 0, 0, 0, 0, 0, 0, 0, 0, 'Oh yes, these are fine. Fine indeed. Unfortunately they need to be calibrated in order to for us to get any accurate readings off of them.', 0);
INSERT INTO `quest_offer_reward` VALUES (39334, 0, 0, 0, 0, 0, 0, 0, 0, 'Barm did his work, then? I hoped that tauren would come through!$B$BThis is a very useful addition to my alchemy table.', 0);
INSERT INTO `quest_offer_reward` VALUES (39335, 0, 0, 0, 0, 0, 0, 0, 0, 'You... you found some? I\'m impressed, and that doesn\'t happen easily I\'ll have you know.$B$BNow let\'s see what exactly it was that you found...', 0);
INSERT INTO `quest_offer_reward` VALUES (39336, 0, 0, 0, 0, 0, 0, 0, 0, 'The ghost of Svena appeared again? Seems she doesn\'t take kindly to our attempts to learn these vrykul recipes...$B$BThankfully, the satchel you recovered has all the precipitating powder we require for the alchemy table. Another victory for us!', 0);
INSERT INTO `quest_offer_reward` VALUES (39337, 0, 0, 0, 0, 0, 0, 0, 0, 'This Shaldorei Silk appears to be torn into strips. The elves have apparently been using it as bandages for their fallen warriors.', 0);
INSERT INTO `quest_offer_reward` VALUES (39338, 0, 0, 0, 0, 0, 0, 0, 0, 'This Shaldorei Silk will make an excellent filter for the alchemy table. Nice work getting that nightborne to help us, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (39339, 0, 0, 0, 0, 0, 0, 0, 0, 'You\'ve found something quite intriguing here! Others have brought me fragments of such implements, but never so complete a sample.$B$BThis is going to require a lot of work...', 0);
INSERT INTO `quest_offer_reward` VALUES (39340, 0, 0, 0, 0, 0, 0, 0, 0, 'It looks like you acquired everything we need. Give me a few moments to apply them to the interior.$B$B<Deucus works on the crucible, restoring its lining.>$B$BPerfect! The crucible is almost ready for use.$B$BJust one more tiny detail...', 0);
INSERT INTO `quest_offer_reward` VALUES (39341, 0, 0, 0, 0, 0, 0, 0, 0, 'You did it! I must say, I was a bit concerned for your well-being, but once again you\'ve proven resourceful.$B$BNow that the crucible\'s lining is restored and it has been bathed in demon\'s bile, it\'s ready for use on the alchemy table.$B$BWell done!', 0);
INSERT INTO `quest_offer_reward` VALUES (39342, 0, 0, 0, 0, 0, 0, 0, 0, 'You got the hair?!$B$B<Ms. Xiulan laughs audibly.>$B$BOh, I wish I could have been there to see the look on his face. Nevertheless, a deal\'s a deal.', 0);
INSERT INTO `quest_offer_reward` VALUES (39343, 0, 0, 0, 0, 0, 0, 0, 0, 'What\'s this you have for me, $n? Say, these are some wondrous tubes! I bet I know a way to make them even better.$B$BI\'ll need you to temper these glass tubes in the fire of a dragon\'s breath under the light of a full moon on the equinox, then travel into the Emerald Dream and--$B$BOh, hold on. These tubes are perfect just as they are. Nevermind!$B$BSometimes I get a bit caught up in this whole adventuring business...', 0);
INSERT INTO `quest_offer_reward` VALUES (39344, 0, 0, 0, 0, 0, 0, 0, 0, 'Well, now that we understand one another let\'s see what sort of arrangement we can come to.', 0);
INSERT INTO `quest_offer_reward` VALUES (39345, 0, 0, 0, 0, 0, 0, 0, 0, 'Yes, I\'d say these burettes look well-calibrated.$B$BI knew those three would be meticulous. Never underestimate the value of someone who obsesses over tiny details, $n!', 0);
INSERT INTO `quest_offer_reward` VALUES (39346, 0, 0, 0, 0, 0, 0, 0, 0, 'That\'s quite an armload of potions you made! I think it\'s safe to say that these burettes are working as intended.$B$BI\'ll add them to the alchemy table.', 0);
INSERT INTO `quest_offer_reward` VALUES (39347, 0, 0, 0, 0, 0, 0, 0, 0, 'What\'s this then?$B$B<Alard takes the list and looks it over. His eyes widen as he reaches the bottom>$B$BI take it you don\'t fully grasp what it is that you need, do you? If this is truly what\'s required then we better get started.', 0);
INSERT INTO `quest_offer_reward` VALUES (39348, 0, 0, 0, 0, 0, 0, 0, 0, 'Well done, $n. Once we have everything together, I\'ll get started right away.', 0);
INSERT INTO `quest_offer_reward` VALUES (39349, 0, 0, 0, 0, 0, 0, 0, 0, '<Alard takes the bag from you and peers inside.>$B$BSo this is the stuff? It\'s definitely true what I\'ve heard. It is quite heavy.', 0);
INSERT INTO `quest_offer_reward` VALUES (39350, 0, 0, 0, 0, 0, 0, 0, 0, 'It was there? You found some?$B$B<Alard takes the crate and opens it. He reels back from the smell.>$B$BYep, that\'s the stuff alright. By the light, does that reek.', 0);
INSERT INTO `quest_offer_reward` VALUES (39351, 0, 0, 0, 0, 0, 0, 0, 0, '<You hand the heart over to Alard. He places it within his forge and it begins to burn white hot.>$B$BPerhaps I should refer to you as Dragonslayer $n from now on.$B$BJoking aside, I\'ll begin my forging.', 0);
INSERT INTO `quest_offer_reward` VALUES (39354, 0, 0, 0, 0, 0, 0, 0, 0, 'Спасибо тебе, $gстранник:странница;! Сегодня ты очень $gпомог:помогла; лесу.', 21916);
INSERT INTO `quest_offer_reward` VALUES (39355, 0, 0, 0, 0, 0, 0, 0, 0, 'This is a blueprint for some high tech naval equipment. I should be able to reverse engineer the plans and have this equipment available for you shortly!', 0);
INSERT INTO `quest_offer_reward` VALUES (39356, 0, 0, 0, 0, 0, 0, 0, 0, 'Это чертеж какого-то высокотехнологичного корабельного оборудования. Думаю, я смогу разобраться в нем, и уже скоро это оборудование станет тебе доступно!', 20574);
INSERT INTO `quest_offer_reward` VALUES (39358, 0, 0, 0, 0, 0, 0, 0, 0, 'Это чертеж какого-то высокотехнологичного корабельного оборудования. Думаю, я смогу разобраться в нем, и уже скоро это оборудование станет тебе доступно!', 20886);
INSERT INTO `quest_offer_reward` VALUES (39359, 0, 0, 0, 0, 0, 0, 0, 0, 'Это чертеж какого-то высокотехнологичного корабельного оборудования. Думаю, я смогу разобраться в нем, и уже скоро это оборудование станет тебе доступно!', 20886);
